INSERT INTO `step` (`id`, `recipe_title`, `recipe_id`, `step_no`, `step_text`) VALUES
	(1, '치킨 밀푀유', NULL, 1, '1. 파프리카, 양배추, 슬라이스 햄을 썰어서 볶는다.\n2. 닭가슴살에 소금, 후추를 뿌린 후 팬에 굽고, 잠시 식힌 후 얇게 포를 뜬다.\n3. 포를 뜬 가슴살, 볶은 채소와 햄, 치즈, 토마토소스를 켜켜이 쌓는다.\n*동시에 작은 프라이팬에 크림과 가루치즈를 붓고 약불에 끓여 놓는다\n4. 오븐에 5분간 굽고 홀그레인 머스터드, 크림소스, 구운 바게트를 곁들인다.'),
	(2, '취닭거리', NULL, 1, '1. 살을 발라낸 족발에 버터, 라조장을 넣고 볶는다.\n2. 취두부를 팬에서 익힌다.\n3. 해동한 닭죽과 볶은 족발, 취두부를 함께 곁들인다.'),
	(3, '닭딸', NULL, 1, '1. 복분자를 가열하다가 우유와 레몬즙을 넣고 졸인다.\n2. 바케트는 썰어서 팬에 굽는다.\n3. 소금 후추로 간을 한 닭가슴살을 마늘과 함께 굽는다.\n4. 구운 닭가슴살에 복분자 소스와 바게트를 함께 곁들인다.'),
	(4, '어머니 맛 풍족밥', NULL, 1, '1. 살을 발라낸 치킨, 족발에 두반장, 라조장, 유자청 을 넣고 볶는다.\n2. 어느 정도 볶다가 밀가루(또는 녹말)를 물에 풀어 좀 더 볶아내면 완성.\n3. 뜨거운 밥에 끼얹어 먹는다.'),
	(5, '치킨 요로케', NULL, 1, '1. 얇게 저민 닭가슴살에 소금, 후추를 뿌리고 밀가루를 묻혀 굽는다.\n2. 잘게 썰은 양파와 슬라이스 햄을 함께 볶는다.\n3. 토마토 소스에 구운 닭가슴살, 파마산 치즈, 모짜렐라 치즈를 넣고 가열한다.\n4. 마지막으로 올리브유를 뿌려준다.'),
	(6, '포크 미트볼', NULL, 1, '1. 믹서에 갈은 김치와 달걀 물을 섞어서 스크램블을 만든다.\n2. 갈은 통조림 햄을 넓게 편 후 1을 넣고 말아준다.\n3. 겉에 달걀 물을 뭍이고 팬에서 익혀준다.\n4. 접시에 밥과 함께 곁들여 낸다.'),
	(7, '만두부인 육수터졌네', NULL, 1, '① 뜨거운 팬에 올리브 오일을 두르고 다진 마늘을 볶다가 냉동만두를 굽는다.\n② 겉은 바삭하게 구운 다음 버터를 조금 넣고 사골육수를 넣어 졸인다.\n③ 졸여지면 발사믹 소스와 고추냉이, 토마토를 함께 넣어 볶는다.\n④ 접시에 만두, 토마토 순으로 올리고 프로슈토 햄과 그뤼에르 치즈를 위에 토핑한다.'),
	(8, '심쿵 오믈렛', NULL, 1, '① 달걀을 풀어놓는다.\n② 프라이팬에 오일을 두르고 소금 후추 간을 한 연어를 굽는다.\n③ 양파잼과 다진 브로콜리를 넣고 함께 굽는다.\n④ 버터를 두른 팬에 풀어놓은 계란을 넣어 반숙으로 익힌다.\n(* 스크램블 에그 만들 듯이 약간 저어주며 익힌다)\n⑤ 반숙으로 익힌 오믈렛에 구운 연어와 야채를 넣고 반으로 접는다.\n⑥ 접시에 올리고 케찹을 뿌린다.'),
	(9, '엔초비 파스타', NULL, 1, '① 올리브 오일에 파슬리와 다진 마늘, 찢은 바게트 빵을 섞어 프라이팬에 굽는다.\n② 끓는 물에 파스타면을 삶는다.\n③ 프라이팬에 올리브 오일을 두르고 애호박, 엔초비, 페페로치노, 파슬리를 넣어 볶는다.\n④ 익은 파스타면을 팬으로 옮겨 모든 재료를 함께 볶는다. (기호에 따라 버터를 조금 넣는다.)'),
	(10, '고기와 썸타는 떡국떡 삼합', NULL, 1, '① 맥주와 삼겹살을 한 번에 넣고 삶는다.\n② 들기름 두른 팬에 김치를 볶다가 방울토마토 간 것, 케찹, 고추장을 넣고 볶는다.\n③ 기름 두른 팬에 떡국떡을 굽는다.\n④ 접시에 적당한 크기로 자른 삼겹살과 김치, 구운 떡국떡을 담아낸다.'),
	(11, '와닭', NULL, 1, '① 생닭을 해체한다. (껍질과 뼈는 사용하지 않는다.)\n② 양파를 채 썰고, 마늘을 편 썬다.\n③ 팬에 오일을 두르고 해체한 닭을 굽는다.\n④ 다른 팬에 마늘과 양파를 넣고 볶다가 아티초크를 넣어 함께 볶는다.\n⑤ 닭을 굽던 팬에 토마토 소스와 야채들을 넣어 함께 볶는다.\n⑥ 적포도의 씨를 발라 프라이팬에 볶다가 모든재료를 함께 볶고 사골육수를 조금 넣어 익힌다.'),
	(12, '브로콜리 치킨', NULL, 1, '① 브로콜리를 잘라서 소금 넣은 물에 데친 후 다진다.\r\n② 방울토마토를 반으로 자르고 파프리카를 채썬다.\r\n③ 닭가슴살을 반으로 갈라 소금 후추 간을 한 후 데친 브로콜리와 치즈를 넣고 이쑤시개로 고정해\r\n굽는다.\r\n④ 같은 팬에 파프리카와 방울토마토를 넣어 함께 익힌다.\r\n⑤ 버터를 조금 넣고 물을 약간 부어 뚜껑을 덮어 익힌다.\r\n⑥ 고춧가루를 조금 뿌린다.'),
	(13, '삼겹살 플레이트', NULL, 1, '① 삼겹살을 썰어서 소금후추 간을 해서 굽는다.\n② 다른 팬에 버터와 통마늘, 채 썬 양파를 넣어 볶다가 간장, 미림, 다진생강, 다진마늘, 커피가루를 넣어 끓인다.\n③ 삼겹살에 청주를 넣어 끓이다가 건져 야채와 함께 섞는다\n④ 채 썬 알배추를 듬뿍 넣고 뚜껑을 덮어 숨을 죽인다.\n⑤ 아이스크림 스쿱으로 밥을 담고 익은 고기와 채소를 얹어 낸다.'),
	(14, '강남비빔면', NULL, 1, '① 삼겹살을 잘게 썰어서 기름에 바삭하게 튀긴다\r\n② 고추장과 딸기잼, 참기름, 고추냉이를 섞는다\r\n③ 끓는 물에 칼국수면을 삶는다 (넘칠 때쯤 찬물을 한 번 넣어 쫄깃함을 더한다)\r\n④ 물에 씻은 새우젓을 기름에 튀긴다\r\n⑤ 익은 면을 찬물에 씻고 소스와 버무려 접시에 담은 뒤 튀긴 삼겹살과 새우젓, 오렌지를 토핑한다.'),
	(15, '나 또 브리또', NULL, 1, '① 간장, 미림, 다진마늘, 후추, 어슷 썬 대파, 타이칠리소스, 낫토를 섞은 것에 샤브샤브용 고기를 넣어 재운다.\r\n② 냉동새우볶음밥에 낫토, 버터를 넣어 볶는다.\r\n③ 플레인 요거트와 레몬즙을 섞어 사워크림을 만들고 낫토를 잘게 다져 넣는다.\r\n④ 또띠아를 팬에 굽고 다른 팬에 고기를 익힌다.\r\n⑤ 또띠아 위에 볶음밥과 고기, 크림과 어슷썬 파를 얹어 낸다.'),
	(16, '갸루상 케이크', NULL, 1, '① 달걀의 흰자와 노른자를 분리한다.\n② 노른자 2개와 설탕, 우유, 바닐라 아이스크림, 밀가루를 넣어 섞고 커스터드 크림이 될 때까지 전자렌지에 3분 간격으로 돌린다.\n③ 또띠아를 6등분 해 기름에 튀겨낸다.\n④ 튀긴 또띠아 위에 커스터드 크림을 층층이 올리고 반으로 자른 딸기를 토핑한다.'),
	(17, 'PLETKA', NULL, 1, '① 길게 썬 돼지고기 안심과 베이컨의 윗부분을 이쑤시개로 고정한 후 머리땋 듯 땋고 끝부분도 이쑤시개로 고정해 팬에 굽는다.\r\n② 소주를 부어 잡내를 날린다.\r\n③ 김치, 양파, 당근을 잘게 썰어 같은 팬에 넣고 볶다가 물을 붓고 뚜껑을 덮는다.\r\n④ 접시에 고기를 올리고 밥과 김치를 쌓아서 낸 후 다진 부추로 토핑한다.'),
	(18, '다 줄게라면', NULL, 1, '① 복분자주에 재운 삼겹살을 길게 찢은 김치와 함께 말아 부추로 고정한 후 팬에 굽는다.\r\n② 물과 어묵통조림과 매운미소소스, 큼직하게 썬 알배추를 넣고 끓인다.\r\n③ 면을 넣어 끓인다.\r\n④ 접시에 끓인 라면을 담고 김치말이 삼겹살을 얹어서 낸다.'),
	(19, '우리집에 오.새.요', NULL, 1, '1. 청주, 미림, 양조간장, 국수장국을 끓여 소스를 만든다.\r\n2. 오징어는 간장, 다진 마늘, 청주, 미림에 담가 간을 한다.\r\n3. 채썬 채소와 칵테일 새우, 박력분, 전분, 얼음을 넣어 반죽을 한다.\r\n(* 반죽 후 얼음은 꼭 제거한다.)\r\n4. 달궈진 기름에 반죽을 한국자씩 넣고 튀긴다.\r\n5. 간을 한 오징어에 전분을 묻혀 튀긴다.\r\n6. 그릇에 밥과 어슷썬 대파, 완성된 튀김과 갈은 무, 소스를 올려낸다.'),
	(20, 'Mr.콩chu~♥', NULL, 1, '1. 두부의 물기를 제거한다.\n2. 버터를 녹이고 밀가루, 두유를 넣어 소스를 만든다.\n3. 계피맛 쿠키를 믹서에 갈고 설탕을 섞어둔다\n4. 깍뚝 썬 두부에 전분을 묻혀 튀긴다.\n5. 튀긴 두부에 계피설탕가루를 입힌다.\n6. 그릇에 두유소스와 두부츄러스, 바닐라 아이스크림, 다진 초콜릿을 토핑한다.'),
	(21, 'For덕', NULL, 1, '① 가지를 길게 썰어 올리브유를 두른 팬에 양면을 굽는다.\r\n② 토마토는 열십자로 칼집을 내어 끓는 물에 데쳐 껍질을 벗긴다.\r\n③ 적당한 크기로 썬 토마토와 다진 파프리카, 양파, 사과, 고추장아찌를 냄비에 넣고 으깨 소스를 만든다.\r\n④ 토마토 소스를 넣어 풍미를 더한다.\r\n⑤ 익은 가지를 격자 모양으로 놓고 소금 후추를 뿌린 후, 가운데 계란흰자 통조림을 넣어 감싼다.\r\n(* 비닐을 이용하면 좀 더 쉽게 모양을 잡을 수 있다.)\r\n⑥ 모양이 잡힌 가지를 올리브유를 두른 팬에 한 번 더 익힌다.\r\n⑦ 접시에 소스와 가지를 담아 낸다.'),
	(22, '프로틴 스틱', NULL, 1, '① 계란 흰자 통조림과 닭가슴살, 파프리카, 양파를 깍둑썰기 해 꼬치에 끼운다.\r\n② 꼬치에 소금, 후추를 두르고 그릴팬에 굽는다.\r\n③ 당근, 사과를 강판에 채썰고, 식초, 올리브유, 다진 견과류를 뿌린다.\r\n④ 접시에 샐러드를 깔고 꼬치를 위에 낸다.'),
	(23, '불닭 소시지', NULL, 1, '① 소시지를 끓는 물에 데쳐 기름기를 제거하고 적당한 크기로 썬다.\r\n② 당면라면의 면을 끓는 물에 데쳐 찬물에 헹군 후, 매운라면 소스, 다진 청양고추에 버무린다.\r\n③ 꼬치에 소시지를 꽂고, 양념한 면을 감는다.\r\n④ 접시에 꼬치를 놓고 상추를 곁들여 낸다.'),
	(24, '매우면 참치 & 쏘루아밀크', NULL, 1, '① 식빵을 토스트기에 굽는다.\r\n② 버터를 두른 팬에 기름 뺀 참치와 믹서에 간 토마토, 고춧가루를 넣고 끓인다.\r\n③ 얼음을 담은 잔에 소주, 설탕, 인스턴트 커피 섞은 것을 넣고, 흰우유를 넣어 층을 낸다.\r\n④ 구운 식빵을 적당한 크기로 자르고 참치 양념을 얹어낸다.'),
	(25, '삼층돈탑', NULL, 1, '1. 알배추는 먹기 좋은 크기로 썰어둔다.\r\n2. 대파는 채썰고, 마늘은 편으로 썰어둔다. 돼지고기는 먹기 좋은 크기로 자른다.\r\n3. 전자레인지용 그릇에 알배추 - 채 썬 대파 - 돼지고기를 층층이 쌓고 소금 후추 간을 한다.\r\n4. 랩을 두르고 3분 간격으로 렌지에 돌린다.\r\n5. 다진 고추 장아찌와 국물, 설탕을 두른 소스와 스테이크소스, 올리고당을 섞은 소스를 함께 낸다.'),
	(26, '엠티 오후의 라면', NULL, 1, '1. 끓는 물에 밀가루와 쇠고기 스프, 라면스프를 넣고 끓인다.\r\n2. 채 썬 오징어와 면, 건더기 스프를 넣고 끓인다.\r\n3. 계란 푼 것을 라면에 두른다.\r\n4. 총각김치를 곁들여 낸다.'),
	(27, '봉선아 시집가자미', NULL, 1, '1. 팬 하나에 대파를 잘게 썰어 볶고, 다른 팬에는 양송이 버섯에 소금 후추 간을 해 볶는다.\r\n2. 가자미는 껍질을 벗기고 뼈를 발라낸다.\r\n3. 가자미에 소금 후추 간을 하고 밀가루를 뭍혀 올리브유를 두른 팬에 굽는다.\r\n4. 가자미를 굽는 팬에 화이트와인과 복분자 엑기스를 부어 졸인다.\r\n5. 그릇에 볶은 대파와 버섯을 놓고 그 위에 구운 가자미를 올린다.\r\n6. 가자미 육즙이 남은 팬에 버터를 넣어 소스를 만든다.'),
	(28, '스키니 신', NULL, 1, '1. 감자를 삶는다.\r\n2. 관자는 물기를 제거하고, 올리브유는 냄비에 넣고 데운다.\r\n3. 관자는 얇게 썰어두고, 살짝 감자를 강판에 간다\r\n4. 볼에 다진 양상추, 단감, 고구마를 넣고 샐러드를 만든다.\r\n5. 감자와 다진 양파로 전을 부친다.\r\n6. 달군 올리브유를 관자에 부어 익힌다.\r\n7. 접시에 감자양파전, 샐러드, 관자를 얹어 낸다.'),
	(29, '파티비프', NULL, 1, '1. 청고추에 칼집을 내고 그릴팬에 굽는다.\r\n2. 고기와 양파는 채썰고 양송이 버섯도 편으로 썰어 팬에 볶는다.\r\n3. 2에 고기에 쌈장과 생크림을 넣고 볶는다.\r\n4. 3에 다진 총각김치를 넣고 후추를 뿌린다.\r\n5. 접시에 밥을 놓고 주위에 볶은 고기를 얹는다.\r\n6. 중앙에 총각김치를 올린다.'),
	(30, '아이러브유부', NULL, 1, '1. 삼겹살, 생크림, 달걀 흰자, 마늘 2쪽을 믹서기에 넣고 간다.\r\n2. 간 고기에 잣, 두반장, 굴소스를 넣고 섞는다.\r\n3. 양파와 물에 씻은 고추장아찌를 다져 섞는다.\r\n4. 유부의 속을 채우고 삶는다.\r\n5. 고추 장아찌 국물과 핫소스, 볶은 다진 마늘을 섞어 소스를 만들어 함께 낸다.'),
	(31, '곤약 봉골레 곤봉', NULL, 1, '1. 가스레인지 위에 파프리카를 올리고 표면을 태운다.\r\n2. 곤약을 얇게 국수처럼 썬다. (실곤약 대체 가능)\r\n3. 바지락 반은 육수를 내고 반은 살을 발라둔다.\r\n4. 표면을 태운 파프리카의 껍질을 벗기고 얇게 채썬다.\r\n5. 올리브유에 다진 마늘을 볶다가 바지락과 소금 후추를 뿌리고 볶는다.\r\n6. 채 썬 곤약과 파프리카를 넣어 볶다가 육수를 부어 끓인다.'),
	(32, '렛잇컵', NULL, 1, '1. 찐 고구마의 껍질을 벗기고 으깬 후, 두유를 섞어 농도를 맞춘다.\r\n2. 각종 과일을 먹기 좋은 크기로 썰어 둔다.\r\n3. 와인잔에 찐 고구마와 과일을 겹겹이 쌓는다.\r\n4. 플레인 요거트와 복분자 원액을 넣는다.\r\n5. 생고구마를 썰어 스틱을 만들고 홍시와 플레인 요거트로 소스을 만들어 함께 낸다.'),
	(33, '마이 러블리 튀밥', NULL, 1, '1. 파프리카, 버섯, 양파를 다지고 올리브유를 두른 팬에 볶는다.\n2. 명란젓에 다진 대파를 넣고, 볶은 채소, 즉석밥, 참기름을 넣고 함께 섞는다.\n3. 팬에 올리브유와 마늘, 블랙올리브, 홍고추, 방울토마토, 토마토 소스를 넣어 소스를 만든다.\n4. 밥을 뭉칠 때 속에 치즈를 넣고 밀가루, 달걀물, 빵가루 순으로 옷을 입힌다.\n5. 달군 기름에 뭉친 밥을 넣어 튀긴다.\n6. 토마토소스 위에 튀긴 밥을 올리고 파슬리와 올리브유를 뿌려서 낸다.'),
	(34, '자투리타타', NULL, 1, '1. 파프리카, 양파, 버섯 등의 재료는 잘게 다진다.\r\n2. 명란젓을 넣은 달걀물에 치즈를 갈아 넣는다.\r\n3. 올리브유를 두른 팬에 다진 채소와 방울토마토, 다진 마늘, 팽이버섯을 넣고 볶는다.\r\n4. 채소를 볶던 팬에 달걀물을 붓고 호일을 덮어 익힌다.\r\n5. 베이컨을 잘게 썰어 키친타올 사이에 넣고 렌지에 돌려 바삭하게 만든다.\r\n6. 프리타타 위에 베이컨을 놓고 토치로 표면을 익힌다.'),
	(35, '괜찮아, 목심이야', NULL, 1, '1. 양파, 당근, 사과, 양상추를 가늘게 채 썬다.\r\n2. 오리엔탈 소스에 식초와 설탕을 넣어 끓인 후, 채 썬 양파, 당근과 섞어둔다.\r\n3. 소금, 후추로 밑간을 한 돼지고기 목심을 전분이 고르게 묻도록 섞는다.\r\n4. 끓는 물에 돼지고기 목심을 익힌 후, 건진다.\r\n5. 접시에 고기와 채소를 차례로 얹는다.\r\n6. 채 썬 양상추와 사과를 올리고 소스를 뿌려 완성한다.'),
	(36, '마이 스위트 베이비', NULL, 1, '1. 소금, 후추 간을 한 소고기 등심을 팬에 굽는다.\r\n2. 사과를 자르고 씨를 발라 팬에 굽는다.\r\n3. 소고기 등심을 꺼내 놓고 흑설탕과 물에 구운 사과를 졸인다.\r\n4. 등심을 다시 팬에 올리고 레드와인을 부어 좀 더 익힌다.\r\n5. 졸인 사과와 등심을 함께 낸다'),
	(37, '어란대첩', NULL, 1, '1. 무를 얇게 채 썬다.\r\n2. 채 썬 무를 얼음물에 담가 식감을 살린다.\r\n3. 끓는 물에 파스타면을 넣고 삶는다.\r\n4. 익은 파스타면을 얼음물에 넣어 식힌 후 올리브유, 쯔유, 소금, 후추를 넣어 섞는다.\r\n5. 접시에 파스타면과 채썬 무를 차례로 올리고 어란을 갈아 올린다.'),
	(38, '뉴타운 달팽이', NULL, 1, '1. 팬에 버터, 다진 양파, 소금, 달팽이를 넣고 볶는다.\r\n2. 끓는 물에 매시드 포테이토 분말을 넣고 매시드 포테이토를 만든다.\r\n3. 달팽이를 볶던 팬에 레드와인을 부어 졸인다.\r\n4. 팬에 버터와 시금치를 넣어 볶는다.\r\n5. 접시에 매시드 포테이토를 깔고 달팽이와 양파를 올린 후, 볶은 시금치와 소스를 곁들여 낸다.\r\n6. 마지막으로 다진 홍고추를 올린다.'),
	(39, '삼국회담', NULL, 1, '1. 끓는 물에 쌀국수면을 삶는다.\n2. 후추를 뿌린 양갈비를 손질한 후, 커민으로 양념을 한다.\n3. 올리브유를 두른 팬에 으깬 마늘과 중국 고추소스를 넣어 볶다가 양갈비를 넣어 굽는다.\n4. 믹서기에 코코넛 밀크와 새우젓, 말린 양파를 넣고 갈아 소스를 만든다.\n5. 끓인 쌀국수 면을 건져 얼음물에 헹군 후 피쉬소스, 동치미 국물, 탄산수, 참기름을 넣어 국수를 완성한다.\n6. 접시에 구운 양갈비와 새우젓 소스를 곁들여 낸다.'),
	(40, '로맨티스튜~♡', NULL, 1, '1. 한우 등심을 깍둑썰기 한다.\r\n2. 올리브유를 두른 팬에 밀가루를 묻힌 등심을 볶아 놓는다.\r\n3. 팬에 올리브유와 잘게 썬 베이컨을 넣어 볶는다.\r\n4. 팬에 올리브유와 다진 양파, 토마토를 넣고 볶는다.\r\n5. 토마토가 익으면 생크림, 코코넛 밀크를 넣고 끓인다.\r\n6. 끓인 소스에 볶아둔 한우 등심을 넣고 끓인다.\r\n7. 커리 페이스트를 넣고 소금, 후추로 간을 한다.\r\n8. 베이컨을 볶던 팬에 채 썬 양배추를 넣어 함께 볶는다.\r\n9. 즉석밥을 전자레인지에 돌리고, 베이컨에 레몬즙을 넣는다.\r\n10. 스튜와 베이컨에 고수를 넣어 익힌다.\r\n11. 접시에 스튜와 볶은 베이컨, 양배추를 올린 후 홍고추, 청고추, 레몬을 곁들인다.\r\n12. 밥 위에 홍고추, 고수를 올리고 레몬즙을 뿌려낸다.'),
	(41, '섹시 한 컵', NULL, 1, '1. 통조림 햄을 비닐팩에 넣고 으깬다.\r\n2. 팬에 버터를 두르고 다진 양파와 으깬 햄을 볶는다.\r\n3. 볶던 햄과 양파에 설탕을 넣는다.\r\n4. 슬라이스 치즈를 넣고 함께 볶는다.\r\n5. 밥을 바질페스토에 비벼둔다.\r\n6. 올리브유를 두른 팬에 달걀을 깨트려 넣고 물을 조금 부은 다음 뚜껑을 닫아 반숙으로 익힌다.\r\n7. 컵에 바질페스토에 비빈 밥과 할라피뇨, 볶은 햄과 양파를 차례로 담는다.\r\n8. 컵밥 가장 위에 반숙 달걀 프라이를 올린다.'),
	(42, '가희에게 반하나', NULL, 1, '1. 달걀, 밀가루, 우유, 탄산수를 넣어 반죽을 만든다.\n2. 바나나를 얇게 썬다.\n3. 팬에 반죽을 얇게 부친다.\n4. 익은 반죽 위에 바나나를 올리고 초콜릿 잼을 바른다.\n5. 크레페의 가장자리를 접어 모양을 만든 후, 접시에 올려 다진 견과류와 블루베리를 곁들인다.\n\n* 기호에 따라 다양한 재료를 넣어 식사, 디저트 등으로 즐길 수 있다'),
	(43, '엑소 떡볶이', NULL, 1, '1. 소량의 기름에 떡을 표면만 살짝 튀긴다.\r\n2. 잘게 썬 대파를 볶다가 굴소스, XO소스, 다진 마늘, 다진 생강, 채 썬 양배추를 넣어 소스를 만든다.\r\n3. 소스에 백후추, 청주, 설탕을 넣어 볶는다.\r\n4. 소스에 식초, 양조간장을 넣어 볶는다.\r\n5. 소스에 떡을 넣어 볶는다.\r\n6. 접시에 떡볶이를 담고 볶은 로메인 상추를 올려낸다.'),
	(44, '한 뚝볶이 하실래예?', NULL, 1, '1. 끓는 물에 떡국떡을 살짝 데친다.\r\n2. 감자, 양파, 대파를 잘게 다진다.\r\n3. 다진 채소를 뚝배기에 담고 만두소를 넣는다.\r\n4. 뚝배기에 물을 붓고 끓인다.\r\n5. 된장에 전분, 고춧가루, 청주를 섞는다.\r\n6. 기름을 두른 팬에 다진 마늘과 다진 페페로치노를 넣고 데친 떡을 볶는다.\r\n7. 채소를 끓이던 뚝배기에 된장을 넣고 졸여 강된장을 만든다.\r\n8. 볶은 떡을 소금, 간장으로 간을 한다.\r\n9. 떡에 채 썬 대파를 고명으로 올리고 강된장과 함께 낸다.'),
	(45, '굴로장생', NULL, 1, '1. 끓는 물에 멸치가루를 넣어 육수를 만든다.\r\n2. 해동된 굴비는 다듬어 칼집을 낸다.\r\n3. 고추장과 호두오일, 레몬즙을 섞어 양념을 만든다.\r\n4. 후추를 뿌린 굴비에 고추장 양념을 바른다.\r\n5. 얇게 썬 레몬을 양념한 굴비의 위아래에 깐 다음 오븐에 굽는다.\r\n6. 기름을 두른 팬에 다진 양파, 으깬 마늘을 넣어 볶다가 밥과 멸치 육수를 넣어 볶는다.\r\n7. 팬에 기름을 두르고 편썰기 한 마늘과 느타리버섯을 소금, 후추 간을 해 볶는다.\r\n8. 리소토에 그라나파다노 치즈를 갈아 넣고 버터를 넣어 볶는다.\r\n9. 접시에 리소토와 볶은 버섯을 담고 위에 고추장 굴비를 얹어낸다.'),
	(46, '낙지 호로록', NULL, 1, '1. 낙지는 소금을 뿌려 깨끗하게 씻는다.\r\n2. 돌나물, 채 썬 양파, 얇게 썬 청양고추를 각각 얼음물에 담근다.\r\n3. 끓는 물에 식초와 소금을 넣고 낙지를 데친다.\r\n4. 데친 낙지는 얼음물에 식혀둔다.\r\n5. 레몬청, 얼음, 설탕, 다진마늘, 페페로치노를 믹서에 곱게 갈아 소스를 만든다.\r\n6. 식혀둔 낙지에 다진 마늘과 참기름으로 양념을 한다.\r\n7. 접시에 돌나물, 양념한 낙지, 양파, 청양고추를 차례로 담는다.\r\n8. 깨를 양 손으로 비벼 으깨 넣고 소스를 부어 완성한다.'),
	(47, '달.고.나', NULL, 1, '1. 한 입 크기로 썬 양파와 당근을 볶다가 설탕을 넣는다.\r\n2. 돼지고기 앞다리살을 반으로 가르고 고기망치로 육질을 부드럽게 만든다.\r\n3. 볶던 채소에 물을 부어 끓인다.\r\n4. 고기에 소금 후추로 간을 하고 마살라와 밀가루를 묻혀 기름을 두른 팬에 굽는다.\r\n5. 채소를 끓인 물을 고기에 부어 익힌다.\r\n6. 고기를 다른 팬에 옮기고 남은 육수에 밀가루와 버터를 넣어 소스를 만든다.\r\n7. 쪽파를 굽는다.\r\n8. 접시에 볶은 야채, 고기, 쪽파를 차례로 올리고 소스를 뿌려 완성한다.'),
	(48, '오순도순', NULL, 1, '1. 올리브유를 두른 팬에 다진 오징어 다리, 양파, 대파, 청양고추를 넣어 볶는다.\n2. 밥과 쌈장을 넣고 볶다가 토마토를 갈아 넣는다.\n3. 오징어 몸통의 속을 채운 다음 그릴팬에 굽는다.\n4. 접시에 쌈채소, 토마토, 레몬, 케이퍼를 놓고 가운데 오징어를 놓는다.\n5. 오징어에 칼집을 내고 그라나파다노 치즈를 갈아 오징어 위에 뿌려준다.'),
	(49, '굴링굴링', NULL, 1, '1. 굴에 소금을 많이 뿌려 수분을 뺀다.\r\n2. 식초, 설탕, 간장, 국수장국, 연겨자, 굴소스, 우스터소스를 끓여 소스를 만든다.\r\n3. 수분을 제거한 굴을 물에 담가 남은 염분을 제거한다.\r\n4. 물기를 닦아낸 굴에 전분을 입혀서 튀긴다.\r\n5. 끓인 소스에 물전분을 넣어 걸쭉하게 만든다.\r\n6. 접시에 굴튀김을 담고 채 썬 양파, 다진 고추와 매실장아찌, 소스를 얹는다.\r\n7. 다진 파를 뿌려 완성한다.'),
	(50, '보굴보굴', NULL, 1, '1. 끓는 물에 굴을 넣고 삶아서 육수를 만든다.\n2. 팬에 버터, 밀가루를 넣어서 루를 만든다.\n3. 참기름, 마늘, 불린 미역을 함께 볶다가 굴 육수를 넣고 끓인다.\n4. 루에 우유를 부어 베샤멜 소스를 만든다.\n5. 팬에 다진 양파와 굴을 넣어 볶는다.\n6. 미역과 함께 끓인 굴 육수에 베샤멜 소스를 넣어 함께 끓인다.\n7. 끓인 스프를 믹서기에 넣고 간다.\n8. 스프를 체에 거르고 볶은 굴과 양파를 넣은 뒤 소금, 후추로 간을 한다.\n9. 그릇에 완성된 스프를 담고 참기름을 뿌려 낸다.'),
	(51, '허풍찜닭밥', NULL, 1, '1. 팬에 채 썬 양파를 볶다가 소금, 후추 간을 한다.\r\n2. 다진 양파에 콜라를 붓고, 닭가슴살 통조림, 우스터소스를 넣어 졸인다.\r\n3. 끓는 물에 김치를 잘라 넣는다.\r\n4. 달걀과 카레가루를 함께 푼다.\r\n5. 살짝 익힌 즉석밥을 달걀물에 넣고 섞어 팬에 볶는다.\r\n6. 토마토 간 것과 참치 통조림을 찌개에 부어 함께 끓인다.\r\n7. 졸인 닭가슴살에 전분을 넣어 농도를 조절한다.\r\n8. 찌개에 우스터소스와 설탕을 넣는다.\r\n9. 찌개에는 굴소스를, 닭가슴살에는 레몬즙을 넣어 마무리한다.\r\n10. 볶음밥에 콜라에 졸인 닭가슴살을 곁들이고, 김치찌개를 따로 낸다.'),
	(52, '허달달', NULL, 1, '1. 다시마와 멸치로 육수를 낸다.\r\n2. 팬에 기름을 두르고 대파를 볶는다.\r\n3. 육수에 새송이 버섯, 채 썬 양파, 두부를 넣고 된장을 푼다.\r\n4. 치킨 비프볼과 다진 마늘, 다진 생강, 굴소스를 섞은 다음 대파와 함께 볶는다.\r\n5. 즉석밥과 치킨 비프볼을 함께 볶는다.\r\n6. 달걀물을 부어 함께 볶아 볶음밥을 완성한다.\r\n7. 된장국에 체에 내린 달걀물을 넣어 함께 끓인다.\r\n8. 달걀물에 우유, 설탕을 넣고 소로보빵을 적셔 팬에 굽는다.\r\n9. 볶음밥, 된장국, 프렌치토스트를 따로 담아 낸다.'),
	(53, '파콘 투게더', NULL, 1, '1. 옥수수와 참치를 체에 받쳐 수분과 기름기를 제거한다.\r\n2. 끓는 물에 소금을 넣고 펜네를 삶는다.\r\n3. 방울토마토를 썰어 레몬즙을 뿌리고 올리브유와 소금, 다진 고수를 넣어 섞는다.\r\n4. 채 썬 양파와 잘게 썬 베이컨을 팬에 볶는다.\r\n5. 아보카도를 잘게 썰어 샐러드에 넣고 섞는다.\r\n6. 팬에 칠리파우더, 방울토마토, 면수를 넣어 볶는다.\r\n7. 채소를 볶던 팬에 토마토소스를 넣어 끓인다.\r\n8. 삶은 펜네를 토마토소스에 넣고 함께 볶는다.\r\n9. 파마산 치즈를 뿌려 파스타를 완성한다.\r\n10. 샐러드 위에 옥수수와 참치를 올리고 레몬즙, 다진 고수를 뿌려 파스타와 함께 낸다.'),
	(54, '백 투 더 치킨', NULL, 1, '1. 닭의 뼈를 발라 육수를 낸다.\n2. 닭고기를 올리브유를 두른 팬에 굽는다.\n3. 닭고기에 생강을 갈아 넣고 소금, 후추로 간을 한다.\n4. 양파와 당근을 다져 넣고 함께 볶는다.\n5. 육수에 달걀 흰자를 넣는다.\n6. 육수를 체에 걸러 남은 기름기를 제거한다.\n7. 닭고기를 익히던 팬에 육수를 부어 함께 끓인다.\n8. 스튜에 방울토마토를 갈아 넣는다.\n9. 스튜에 물전분을 넣어 농도를 조절한다.\n10. 즉석밥에 고수를 올리고 완성된 스튜와 함께 낸다.'),
	(55, '멕시코 갓(god) 쌈?!', NULL, 1, '1. 망고스틴, 양파, 방울토마토, 고수를 다져서 섞어 타코 토핑을 만든다.\r\n2. 사워크림과 사과를 섞어 딥소스를 만든다.\r\n3. 잘게 썬 닭고기를 타코시즈닝, 화이트 와인을 넣어 볶는다.\r\n4. 소시지를 길게 잘라 굽는다.\r\n5. 슬라이스 치즈는 길게 잘라 둔다.\r\n6. 가장자리를 자른 식빵을 밀대로 납작하게 펴서 팬에 굽는다.\r\n7. 식빵과 볶은 닭고기, 타코 토핑, 구운 소시지, 치즈, 사과 딥소스를 함께 낸다.'),
	(56, '타코 리턴', NULL, 1, '1. 만두피를 기름에 튀긴다.\r\n2. 다진 소고기에 소금, 후추 간을 해서 볶는다.\r\n3. 볶던 소고기에 타코시즈닝으로 양념을 한다.\r\n4. 고추를 가스레인지 불에 그대로 올려 직화한다.\r\n5. 방울토마토, 양파, 청양고추 섞은 것에 핫소스, 스리라차 소스를 넣는다.\r\n6. 얼린 토마토, 고수, 마늘장아찌, 올리브유, 후추, 레몬즙을 섞는다.\r\n7. 구운 고추를 다져서 넣고 아보카도를 올려 타코 토핑을 완성한다.\r\n8. 튀긴 만두피, 볶은 고기, 타코 토핑, 사워크림, 스리라차 소스를 함께 낸다.'),
	(57, '냉장고탕', NULL, 1, '1. 김치를 물에 씻는다.\r\n2. 김치와 양파를 채 썰어 다진 마늘과 참기름으로 양념한다.\r\n3. 2에 다진 대파를 섞고 냄비에 넣어 볶는다.\r\n4. 3에 물과 청주를 부어 끓인다.\r\n5. 잘게 썬 청양고추를 넣어 끓인다.\r\n6. 기름을 두른 팬에 고등어를 구워서 으깬다.\r\n7. 믹서에 고등어, 뜨거운 물을 넣고 갈아 냄비에 넣는다.\r\n8. 상추를 넣고 된장을 푼 다음 두부를 넣는다.\r\n9. 깨를 갈아 넣고 후추를 뿌린다.\r\n10. 완성된 탕에 대파를 썰어 올리고 밥과 함께 낸다.'),
	(58, '고된 밀푀유', NULL, 1, '1. 원형 틀을 이용해 두부를 동그랗게 자르고 얇게 포를 뜬다.\r\n2. 기름을 두른 팬에 두부를 바삭하게 지진다.\r\n3. 감자는 작게 썰어 끓는 물에 삶는다.\r\n4. 고등어에 된장을 바르고 버터를 두른 팬에 굽는다.\r\n5. 다진 마늘과 다진 생강, 청주, 미림을 부어 익힌다.\r\n6. 버터와 된장을 섞는다.\r\n7. 삶은 감자, 버터, 된장버터를 섞어 퓨레를 만든다.\r\n8. 다진 할라피뇨를 넣는다.\r\n9. 체를 이용해 구운 고등어와 소스를 분리한다.\r\n10. 접시에 감자 퓨레, 고등어, 두부를 차례로 쌓는다.\r\n11. 올리브유와 레몬즙, 유자청, 다진 상추를 섞어 가니쉬를 만든다.\r\n12. 밀푀유 위에 가니쉬를 얹고 소스를 둘러 완성한다.'),
	(59, '안심하드라고~!', NULL, 1, '1. 끓는 물에 소금, 버터를 넣고 감자, 당근, 대파를 삶는다.\r\n2. 소금, 후추 간을 한 안심을 달군 팬에 굽는다.\r\n3. 고기를 굽는 팬에 삶은 대파를 넣어 함께 굽는다.\r\n4. 삶은 당근에 버터, 레몬즙, 설탕을 넣고 볶아준다.\r\n5. 4에 레몬 껍질을 갈아 넣는다.\r\n6. 팬에 레드와인을 붓고 팥 아이스크림과 버터를 넣고 졸인다.\r\n7. 체에 팥 건더기를 거르고 소스만 끓인다.\r\n8. 접시에 안심 스테이크와 감자, 당근, 대파를 올리고 팥소스를 뿌려낸다.'),
	(60, '미숫가루보나라', NULL, 1, '1. 믹서에 우유와 미숫가루를 넣고 간다.\r\n2. 냄비에 1을 붓고 설탕을 넣어 끓인다.\r\n3. 달걀, 박력분, 우유를 넣고 반죽을 만든다.\r\n4. 기름을 두른 팬에 반죽을 부어 굽는다.\r\n5. 미숫가루 소스에 소금 간을 한다.\r\n6. 구워진 반죽을 가늘게 썰어 면을 만든다.\r\n7. 미숫가루 소스에 면을 넣어 끓인다.\r\n8. 접시에 면과 소스를 담고 초콜릿을 뿌려낸다.'),
	(61, '미소볼', NULL, 1, '1. 냄비에 으깬 마늘, 미트볼, 레드와인, 다진 생강을 넣고 끓인다.\r\n2. 토마토 소스에 크림치즈를 넣어 소스를 만든다.\r\n3. 버터를 두른 팬에 옥수수를 넣고 설탕, 간장을 넣어 볶는다.\r\n4. 미트볼에 토마토 소스를 넣고 간장을 넣어 끓인다.\r\n5. 고추, 팽이버섯, 새송이버섯, 대파, 양파를 굽는다.\r\n6. 소금, 후추 간을 하고 레몬 껍질을 갈아 넣는다.\r\n7. 채소에 레몬즙을 넣는다.\r\n8. 미트볼에 파마산 치즈를 뿌린다.\r\n9. 달걀 프라이를 만든다.\r\n10. 그릇에 밥, 미트볼, 달걀 프라이, 옥수수, 채소를 얹어낸다.'),
	(62, '와풍 주니어 버거', NULL, 1, '1. 미트볼, 통조림햄, 달걀을 함께 으깨어 섞는다.\r\n2. 둥글게 패티를 만들어 굽는다.\r\n3. 버터를 두른 팬에 채 썬 양파, 팽이버섯을 볶다가 소금, 후추 간을 한다.\r\n4. 카스테라 두 개를 납작하게 누른다.\r\n5. 패티 위에 슬라이스 치즈를 올리고 뚜껑을 닫아 치즈를 녹인다.\r\n6. 마요네즈, 머스터드, 다진 마늘, 후추를 섞어 소스를 만든다.\r\n7. 카스테라→패티→볶은 채소→소스→카스테라 순으로 얹어 낸다.'),
	(63, '트리퐁크라운', NULL, 1, '1. 냄비 두 개에 우유를 끓인다.\r\n2. 크림치즈를 전자레인지에 넣어 녹인다.\r\n3. 달걀, 설탕, 사과주스, 레몬즙을 섞어서 끓인다.\r\n4. 3에 버터를 넣어 커스터드 크림을 만든다.\r\n5. 체에 거른 커스터드 크림을 냉동실에 넣어 식힌다.\r\n6. 끓인 우유에 초콜릿을 넣어 녹인다.\r\n7. 초콜릿 소스에 호두를 갈아 넣는다.\r\n8. 끓인 우유에 크림치즈, 레몬즙을 넣어 끓인다.\r\n9. 크림치즈 소스에 바닐라 시럽을 넣는다.\r\n10. 깍둑썰기 한 카스테라를 접시에 담고, 크림치즈 소스, 초콜릿 소스, 커스터드 크림을 각각 담아낸다.\r\n11. 시리얼을 잘게 부숴 함께 낸다.'),
	(64, '라벤더숲', NULL, 1, '1. 냄비에 우유, 설탕, 라벤더 티백을 넣어 끓인다.\r\n2. 달걀 노른자에 설탕을 넣고 섞다가 1을 부어 함께 섞는다.\r\n3. 체를 이용해 거품과 티백을 거르고 다시 끓인다.\r\n4. 아사이베리즙에 레몬즙을 넣어 끓인다.\r\n5. 다진 아몬드, 잘게 썬 사과를 완성 그릇에 깔아준다.\r\n6. 카스테라를 얇게 썰어 아몬드와 사과 위에 얹는다.\r\n7. 완성된 라벤더 소스를 카스테라에 붓는다.\r\n8. 다진 초콜릿을 올린다.\r\n9. 쿠키를 올리고 아사이베리 소스를 둘러 완성한다.'),
	(65, '살치는 매운 요리', NULL, 1, '1. 올리브유에 마늘, 페페론치노, 파슬리, 레몬즙을 넣고 끓인다.\r\n2. 감자를 삶는다.\r\n3. 마늘 기름을 두른 팬에 채 썬 양배추를 볶는다.\r\n4. 소금, 후추로 간을 한 살치살을 굽는다.\r\n5. 고추와 콜라비, 아보카도를 함께 구워준다.\r\n6. 살치살과 볶은 양배추를 접시에 담는다.\r\n7. 구운 고추를 올리고 마늘 기름을 둘러 완성한다.'),
	(66, '삼고마비', NULL, 1, '1. 올리브유에 대파를 넣어 끓인다.\r\n2. 1에 고춧가루, 페페론치노를 넣어 함께 끓인다.\r\n3. 얇게 채 썬 콜라비에 소금을 뿌려 숨을 죽인다.\r\n3. 소금, 후추 간을 한 꽃갈비살을 굽는다.\r\n4. 익힌 꽃갈비살을 얇게 썬다.\r\n5. 콜라비에 매실액을 넣는다.\r\n6. 콜라비에 다진 대파와 설탕을 넣어 섞는다.\r\n7. 끓인 기름을 체에 거르고 꿀과 레몬즙을 넣고 섞는다.\r\n8. 접시에 꽃갈비살과 콜라비를 올리고 기름을 두른다.\r\n9. 얇게 썬 청양고추와 레몬 껍질을 갈아 올려서 완성한다.'),
	(67, '가슴이 콩닭콩닭', NULL, 1, '1. 팬에 호일을 깔고 설탕, 파슬리를 가득 뿌린다.\n2. 닭가슴살은 얇게 포를 뜬다.\n3. 올리브유, 고춧가루, 후추, 소금으로 간을 한다.\n4. 그릴에 닭가슴살을 올리고 뚜껑을 닫아 훈제한다.\n5. 올리브유, 소금, 후추로 간을 한 애호박과 양파, 고추를 굽는다.\n6. 동그랗게 파낸 콜라비를 채소와 함께 굽는다.\n8. 호일에 소금을 뿌려 연기를 더한다.\n8. 구운 채소에 올리브유, 발사믹 드레싱을 넣어 섞는다.\n9. 접시에 채소와 훈제 닭가슴살을 올리고 파슬리를 뿌려준다.\n10. 다진 마늘을 섞은 발사믹 드레싱과 구운 콜라비를 올려 완성한다.'),
	(68, '나 혼자 쓰리볼', NULL, 1, '1. 물에 화이트와인을 넣고 끓인다.\r\n2. 닭가슴살과 달걀 흰자, 크림소스, 소금, 후추를 믹서기에 넣고 간다.\r\n3. 닭가슴살을 같은 양으로 3등분한다.\r\n4. 3개로 나눈 닭가슴살에 각각 크림소스를 넣어준다.\r\n5. 첫 번째 볼 : 닭가슴살 + 크림소스 + 바질페스토\r\n6. 두 번째 볼 : 닭가슴살 + 크림소스 + 다진 청양고추, 페페론치노\r\n7. 세 번째 볼 : 닭가슴살 + 크림소스\r\n8. 숟가락을 이용해 닭가슴살을 완자 모양으로 만들어 끓는 물에 삶는다.\r\n9. 마늘과 양파를 다져서 볶다가 완자 삶은 물을 넣어준다.\r\n10. 10에 크림소스를 넣고 후추, 파르메산 치즈를 뿌린다.\r\n11. 익은 완자를 건지고 크림소스 완자에 훈제연어를 두른다.\r\n12. 크림소스 위에 세 종류의 완자를 올린다.\r\n13. 석류와 볶은 현미를 올려 완성한다.'),
	(69, '연양만점', NULL, 1, '1. 채 썬 양배추에 소금으로 간을 한 후 볶아준다.\r\n2. 감자, 대파, 양파를 채 썰어 물기를 뺀다.\r\n3. 훈제연어를 랩에 싼 다음 두드려준다.\r\n4. 플레인 요거트에 레몬즙, 다진 양파를 넣고 섞는다.\r\n5. 발사믹 식초에 설탕을 넣고 졸인다.\r\n6. 연어에 볶은 양배추를 올려 감싼 다음 모양을 잡아준다.\r\n7. 요거트 소스에 레몬 껍질을 갈아 넣는다.\r\n8. 접시에 요거트 소스를 깔고 연어쌈을 올린다.\r\n9. 감자, 대파, 양파를 기름에 튀긴다.\r\n10. 연어쌈 위에 튀긴 채소를 올리고 발사믹 소스를 뿌려준다.\r\n11. 딸기에 발사믹 소스를 뿌려 같이 낸다.'),
	(70, '의기양양', NULL, 1, '1. 즉석밥을 전자레인지에 돌린다.\n2. 양배추를 잘게 썰어 볶아준다.\n3. 채 썬 양파, 파를 넣고 함께 볶는다.\n4. 훈제오리를 잘게 썰어 버터와 함께 볶는다.\n5. 4에 후추, 으깬 마늘을 넣어 볶는다.\n6. 볶던 양배추에 닭가슴살을 넣어 함께 볶는다.\n7. 후추와 쯔유를 넣어준다.\n8. 다진 생강을 넣는다.\n9. 체를 이용해 훈제오리의 기름을 걸러준다.\n10. 백김치 국물을 넣어 훈제오리에 간을 한다.\n11. 볶은 양배추와 닭가슴살에 달걀물을 넣고 살짝 익힌다.\n12. 밥 위에 11을 올리고 훈제오리, 어린잎 채소를 올려 완성한다'),
	(71, '감자봤수란', NULL, 1, '1. 강판을 이용해 감자를 채 썬다.\n2. 채 썬 감자에 소금을 넣어 수분을 뺀다.\n3. 얼음물에 어린잎 채소와 양파를 담가 둔다.\n4. 감자를 손으로 짜 수분을 빼내고 밀가루를 넣어 섞어준다.\n5. 채 썬 감자를 팬에 올려 굽는다.\n6. 감자전에 버터를 넣어 굽는다.\n7. 쯔유, 매실액, 후추, 으깬 깨를 함께 넣고 끓인다.\n8. 끓는 물에 소금과 식초를 넣는다.\n9. 물을 저어 회오리를 만든 후 달걀을 넣어 수란을 만든다.\n10. 소스에 버터와 밀가루를 넣어 함께 끓인다.\n11. 채소를 면포로 싼 다음 크게 휘둘러 물기를 뺀다.\n12. 접시에 감자전, 채소, 수란, 잘게 썬 마늘장아찌를 올린다.\n13. 소스를 따로 담아낸다.'),
	(72, '미치고 팔닭', NULL, 1, '1. 팬에 다진 마늘, 채 썬 양파를 넣어 볶는다.\r\n2. 체를 이용해 인절미의 콩고물을 털어낸다.\r\n3. 기름을 두른 팬에 인절미를 굽는다.\r\n4. 양파에 후추, 간장, 조미료, 닭가슴살 통조림을 넣고 함께 볶는다.\r\n5. 구운 인절미 위에 볶은 재료를 올리고 감싼다.\r\n6. 접시에 구운 인절미를 올리고 콩고물, 꿀을 함께 낸다.'),
	(73, '2연타', NULL, 1, '1. 적당한 크기로 썬 연어에 소금, 후추 간을 한다.\n2. 기름을 두른 팬에 연어의 겉면만 익힌다.\n3. 양배추, 옥수수 통조림, 후추, 매운 오리엔탈 소스를 섞어 샐러드를 만든다.\n4. 샐러드에 레몬즙을 넣어준다.\n5. 양파는 잘게 썬 후 물에 담가 아린 맛을 뺀다.\n6. 고추장아찌, 방울토마토, 연어를 다진다.\n7. 다진 재료에 후추, 핫소스, 올리브유, 마요네즈를 섞어 준다.\n8. 참치, 마요네즈, 크림치즈, 우유를 섞은 후 갈아서 소스를 만든다.\n9. 겉만 익힌 연어를 적당한 크기로 썬다.\n10. 접시에 샐러드를 깔고 연어 타타키를 올린 후 참치 소스를 뿌린다.\n11. 원형 틀을 이용해 타타키를 담고 방울토마토와 청포도를 올려낸다.'),
	(74, '홍런볼', NULL, 1, '1. 연어를 얇게 썬다.\n2. 레몬 껍질, 레몬즙, 소금, 후추를 섞은 소스에 연어를 절인다.\n3. 멸치볶음을 버터에 볶는다.\n4. 즉석밥에 멸치볶음, 다진 고추장아찌, 참기름을 넣어 섞는다.\n5. 주먹밥을 만들어 김자반에 굴린다.\n6. 양배추와 과일을 잘게 썰어 그릇에 담는다.\n7. 유산균 음료, 딸기 요거트, 올리브 오일을 섞어 요거트 소스를 만든다.\n8. 양배추와 과일에 요거트 소스를 섞는다.\n9. 접시에 과일 샐러드를 깔고 주먹밥, 연어 세비체를 올린다.\n10. 요거트 소스를 뿌려 완성한다.'),
	(75, '비어 쉬림프', NULL, 1, '1. 새우는 물기를 제거하고 소금, 후추, 마늘을 넣어 재워둔다.\r\n2. 달걀 노른자와 흰자를 분리해 흰자는 냉장고에 넣어둔다.\r\n3. 노른자에 소금, 밀가루를 섞어 튀김옷을 만든다.\r\n4. 튀김옷에 맥주를 넣고, 레몬껍질을 갈아 넣는다.\r\n5. 양배추, 당근을 채썰고 소금, 올리브유, 식초로 양념해 샐러드를 만든다.\r\n6. 냉장고에서 달걀 흰자를 꺼내 거품을 낸다.\r\n7. 튀김옷에 거품을 낸 달걀 흰자를 넣어 접듯이 섞는다.\r\n8. 새우에 튀김옷을 입혀 튀긴다.\r\n9. 접시에 샐러드를 얹고 새우튀김과 레몬을 올려 완성한다.'),
	(76, '치튀치튀뱅뱅', NULL, 1, '1. 크림치즈를 적당한 크기로 썬다.\r\n2. 크림치즈를 둥글게 빚어 밀가루를 묻힌다.\r\n3. 버터, 딸기, 토마토소스를 끓여 소스를 만든다.\r\n4. 소스를 갈아 체에 거른다.\r\n5. 소스에 딸기잼을 넣는다.\r\n6. 다른 냄비에 오리엔탈 드레싱, 딸기잼을 섞은 후 갈아서 두 번째 소스를 만든다.\r\n7. 밀가루에 물을 넣어 튀김옷을 만든 후 얼음을 넣는다.\r\n8. 크림치즈에 튀김옷을 입혀 튀긴다.\r\n9. 튀김 위에 반죽을 뿌려 눈꽃 모양을 만든다.\r\n10. 청포도를 얇게 썬다.\r\n11. 접시에 두 가지 소스, 크림치즈 튀김, 청포도를 올려 완성한다.'),
	(77, '사우나 육국수', NULL, 1, '1. 물에 으깬 냉동만두, 생강즙, 다진 마늘, 소주를 넣고 끓여 육수를 낸다.\n2. 다진 마늘, 양파, 대파, 고춧가루, 페페론치노를 함께 볶는다.\n3. 2에 소주를 넣고 볶아 다진 양념을 만든다.\n4. 다진 양념에 등심, 양송이버섯을 넣고 볶아준다.\n5. 육수를 체에 거른 후 다진 양념에 부어 끓인다.\n6. 국물에 된장, 다진 마늘을 넣어 끓인다.\n7. 마카다미아를 잘게 부순다.\n8. 넓게 펼친 곶감에 마카다미아를 놓고 말아준 다음 적당한 크기로 썬다.\n9. 바닐라 아이스크림 위에 마카다미아 곶감말이를 올린다.\n10. 소면을 삶은 후 찬물에 헹군다.\n11. 소면 위에 육개장 국물을 붓고 깻잎을 올린 후 밥과 함께 낸다.'),
	(78, '완소짬뽕', NULL, 1, '1. 낙지를 먹기 좋은 크기로 썬다.\n2. 고추, 양송이버섯, 양파, 마늘, 대파 등 채소를 손질해 둔다.\n3. 손질한 새우를 다진다.\n4. 칼국수 면을 삶는다.\n5. 고추와 대파에 간장을 넣어 볶는다.\n6. 나머지 채소, 고춧가루를 넣고 볶다가 면수를 부어준다.\n7. 6에 낙지를 넣어 함께 끓인다.\n8. 새우, 전분, 달걀물을 섞어 반죽한다.\n9. 새우 완자를 만들어 국물에 넣고 끓인다.\n10. 칼국수 면을 건져 찬물→뜨거운물→찬물 순으로 헹구고 접시에 담는다.\n11. 면에 짬뽕 국물을 부어 마무리한다.'),
	(79, '털업 샐러드', NULL, 1, '1. 물에 된장을 풀어 육수를 낸다.\n2. 양파, 당근을 다져서 팬에 볶는다.\n3. 볶던 채소에 된장 육수를 부어 졸인다.\n4. 3에 불려놓은 렌틸콩을 넣어 졸인다.\n5. 페페론치노, 청양고추를 넣어준다.\n6. 버터에 마늘을 다져 넣어 마늘버터를 만든다.\n7. 주꾸미, 새우를 손질해 마늘버터에 넣고 고춧가루로 간을 한다.\n8. 주꾸미와 새우에 레몬즙을 뿌려준다.\n9. 렌틸콩에 파르메산 치즈를 뿌린다.\n10. 요거트에 메이플 시럽, 레몬즙, 레몬껍질을 섞어 소스를 만든다.\n11. 렌틸콩 위에 주꾸미, 새우를 올린 다음 요거트 소스를 담아낸다.'),
	(80, '하우 두 유두?', NULL, 1, '1. 면포를 이용해 두부의 물기를 뺀다.\r\n2. 잘게 썬 소고기 등심에 소금, 후추 간을 하고 믹서에 간다.\r\n3. 2에 양송이버섯을 넣고 함께 간다.\r\n4. 국수장국, 맛술, 식초, 다진 생강을 넣어 소스를 만든다.\r\n5. 소스에 대파를 넣어 끓인다.\r\n6. 소스에 전분물을 풀어 넣는다.\r\n7. 두부, 소고기, 버섯, 달걀을 섞어 반죽한다.\r\n8. 반죽을 동그랗게 빚어 밀가루를 묻힌 후 팬에 굽는다.\r\n9. 다진 양파에 버터를 넣고 볶아준다.\r\n10. 양파에 케첩, 돈가스 소스, 설탕을 넣어 두 번째 소스를 만든다.\r\n11. 레드페퍼를 넣어 끓인다.\r\n12. 두부 스테이크에 간장 소스, 케첩 소스를 뿌려 낸다.'),
	(81, '치즈 듬풍 토스트', NULL, 1, '1. 설탕에 물을 넣고 끓여 시럽을 만든다.\r\n2. 시럽에 호두를 넣어 졸인다.\r\n3. 버터를 두른 팬에 소금을 뿌린 후 식빵을 굽는다.\r\n4. 식빵 위에 체다 치즈 > 모차렐라 치즈 순서로 얹어준다.\r\n5. 치즈 위에 시럽에 졸인 호두를 올린다.\r\n6. 호두 위에 체다 치즈를 올리고, 구운 식빵을 덮어 치즈를 녹인다.\r\n7. 식빵을 반으로 자른 다음 접시에 올려 낸다.'),
	(82, '샘 앤 치즈 그라탱', NULL, 1, '1. 감자를 얇게 썰어 소금을 넣고 삶는다.\r\n2. 마카로니에 소금을 넣고 삶는다.\r\n3. 버터에 밀가루를 넣고 볶다가 우유, 소금을 넣어 끓인다.\r\n4. 3에 모차렐라 치즈와 체다 치즈를 넣어 끓인다.\r\n5. 삶은 감자에 버터, 소금, 후추, 치즈 소스를 넣고 으깬다.\r\n6. 양파를 볶는다.\r\n7. 볶던 양파에 삶은 마카로니, 버터, 후추를 넣고 함께 볶는다.\r\n8. 7에 치즈 소스와 체다 치즈를 넣어준다.\r\n9. 그릇에 볶은 마카로니를 넣고 후추를 뿌려준다.\r\n10. 마카로니 위에 으깬 감자를 얹고 토치로 표면을 익혀 낸다.'),
	(83, '베리베리 러블리걸', NULL, 1, '1. 끓는 물에 마카로니와 소금을 넣어 삶는다.\n2. 블루베리에 설탕을 부어 졸인다.\n3. 우유를 따뜻하게 데운다.\n4. 데운 우유에 설탕, 달걀, 버터를 넣어 섞는다.\n5. 삶은 마카로니를 건져 찬물에 헹군 후, 버터를 넣고 볶는다.\n6. 그릇에 버터를 바른다.\n7. 호두를 잘게 다진다.\n8. 그릇에 마카로니, 다진 호두, 달걀을 섞은 우유를 넣어준다.\n9. 7을 전자레인지에 넣어 익힌다.\n10. 접시에 마카로니, 키위, 블루베리, 석류를 올려서 완성한다.'),
	(84, 'LOL : The love of your life', NULL, 1, '1. 감자를 잘게 자른다.\n2. 끓는 물에 소금을 넣고 감자를 삶는다.\n3. 삶은 감자에 우유와 소금을 넣고 끓인다.\n4. 버터를 두른 팬에 만두소와 불고기를 볶는다.\n5. 식빵의 가장자리를 자르고 밀대로 밀어 얇게 편다.\n6. 식빵에 볶은 고기를 넣고 말아서 팬에 굽는다.\n7. 부추와 올리브오일을 믹서에 넣고 갈아 부추오일을 만든다.\n8. 복분자 원액, 설탕, 석류즙을 넣고 끓여 소스를 만든다.\n9. 만두피를 튀긴다.\n10. 감자에 강황 가루를 넣고 블렌더로 갈아준다.\n11. 10에 우유와 버터를 넣어 함께 간다.\n12. 불고기롤에 복분자 소스를 바른다.\n13. 접시에 감자 퓌레, 불고기롤, 부추오일, 튀긴 만두피를 올려서 완성한다.'),
	(85, '순결한 튀김', NULL, 1, '1. 소고기 안심을 반으로 갈라 넓게 편다.\r\n2. 소고기 안심에 소금, 후추로 간을 한다.\r\n3. 사과를 다진다.\r\n4. 넓게 편 소고기 안심에 사과와 블루 치즈를 넣어 말아준다.\r\n5. 안심롤에 밀가루>달걀물>빵가루 순서로 튀김옷을 입힌다.\r\n6. 안심롤을 기름에 튀긴다.\r\n7. 튀긴 안심롤을 전자레인지에 넣어 속까지 익힌다.\r\n8. 청양고추, 태국고추, 멸치액젓, 식초, 레몬즙, 설탕, 다진 마늘, 다진 파를 섞어 소스를 만든다.\r\n9. 8에 다진 마카다미아와 레몬청을 넣어 소스를 완성한다.\r\n10. 양상추 위에 안심롤을 올리고 후추를 뿌린 후 소스와 함께 낸다.'),
	(86, '고기 차이나', NULL, 1, '1. 닭가슴살을 적당한 크기로 썬다.\r\n2. 감자 전분, 밀가루, 물을 섞어 튀김 반죽을 만든다.\r\n3. 반죽에 달걀물과 식용유를 넣는다.\r\n4. 닭가슴살에 반죽을 입혀 튀긴다.\r\n5. 채끝살을 감자 전분, 달걀물과 섞어 기름에 볶는다.\r\n6. 볶은 채끝살을 체에 받쳐 기름기를 뺀다.\r\n7. 청경채, 파프리카, 양파, 대파, 마늘을 볶는다.\r\n8. 7에 간장과 굴소스를 넣는다.\r\n9. 채소에 볶은 채끝살을 넣어 함께 볶는다.\r\n10. 튀긴 닭가슴살을 체에 받쳐 기름기를 뺀다.\r\n11. 간장, 설탕, 식초, 레몬즙, 다진 마늘을 섞어 소스를 만든다.\r\n12. 소스에 다진 파를 넣어 준다.\r\n13. 양상추, 양파 위에 튀긴 닭가슴살을 얹고 소스와 함께 낸다.'),
	(87, '힘을 내요 슈퍼 나베', NULL, 1, '1. 사골 육수에 된장을 풀어 끓인다\r\n2. 체를 받치고 마늘, 대파, 양지머리를 넣어 고기 육수를 낸다.\r\n3. 국물에 양배추, 송화 버섯, 청경채를 넣어 끓인다.\r\n4. 소면을 삶아 찬물에 헹군다.\r\n5. 국간장으로 간을 한 후 팽이버섯을 넣는다.\r\n6. 얇게 썬 소고기 안심, 깻잎을 넣는다.\r\n7. 소면을 넣는다.\r\n8. 달걀 노른자와 간장을 섞은 소스를 곁들여 낸다.'),
	(88, '소테미너', NULL, 1, '1. 적은 양의 물에 방울 양배추, 콜리플라워, 파프리카, 브로콜리를 넣고 뚜껑을 닫아 익힌다.\n2. 채소에 소금, 버터, 으깬 마늘을 넣어준다.\n3. 양파에 밀가루를 묻혀 튀긴다.\n4. 살치살에 소금, 후추 간을 해서 굽는다.\n5. 구운 살치살, 튀긴 양파, 소테한 채소를 함께 낸다.'),
	(89, '초코 모찌 맛있찌', NULL, 1, '1. 초코 비스킷을 으깨어 버터와 섞은 후 전자레인지에 넣어 녹인다.\r\n2. 녹인 초코 비스킷에 소금을 살짝 넣어준다.\r\n3. 원형 틀 안에 초코 비스킷을 깔고 잘게 썬 쑥떡을 얹어 케이크 시트를 만든다.\r\n4. 케이크 시트를 냉동실에 넣어 둔다.\r\n5. 달걀 노른자에 커피 음료, 믹스커피, 밀가루, 설탕을 넣고 섞는다.\r\n6. 5에 버터를 넣고 끓여 모카 크림을 만든다.\r\n7. 모카 크림에 커피 우유를 넣어준다.\r\n8. 모카 크림을 체에 거른다.\r\n9. 얼음물 위에서 모카 크림을 식힌다.\r\n10. 냉동 블루베리를 설탕에 졸인다.\r\n11. 냉동실에서 케이크 시트를 꺼낸 후 아이스크림을 올린다.\r\n12. 11에 모카 크림을 부어준다.\r\n13. 케이크 위에 딸기를 올리고 블루베리 소스를 뿌려 장식한다.'),
	(90, '수플레가 머랭?', NULL, 1, '1. 초콜릿은 중탕한다.\r\n2. 달걀 흰자를 분리해 설탕을 넣고 거품을 낸다.\r\n3. 수플레 컵에 버터와 설탕을 바른다.\r\n4. 중탕한 초콜릿에 커피 우유를 넣는다.\r\n5. 머랭에 녹인 초콜릿을 섞는다.\r\n6. 수플레 컵에 반죽을 반 정도 담는다.\r\n7. 6에 블루베리를 올리고 나머지 반죽을 담는다.\r\n8. 오븐에 넣고 익힌다.\r\n9. 설탕과 전분을 믹서에 넣고 갈아준다.\r\n10. 수플레 위에 슈거파우더를 뿌려 완성한다.'),
	(91, '그.레.요 (그대를 위한 레토르트 요리)', NULL, 1, '1. 버터를 두른 팬에 양파를 볶는다.\r\n2. 볶던 양파에 즉석밥, 토마토 소스를 넣고 함께 볶는다.\r\n3. 2에 기름을 뺀 참치, 파르메산 치즈를 넣고 볶는다.\r\n4. 버터를 두른 냄비에 건자두를 볶다가 레드 와인, 설탕을 넣어 졸인다.\r\n5. 햄을 으깬 후 달걀, 밀가루와 섞는다.\r\n6. 햄 반죽 안에 참치를 넣어 동그랗게 빚는다.\r\n7. 6에 밀가루>달걀물>빵가루 순으로 튀김옷을 입혀 튀긴다.\r\n8. 물에 옥수수 수프 분말을 넣고 끓이다 파르메산 치즈, 후추를 넣어 소스를 만든다.\r\n9. 토마토 리소토 위에 옥수수 수프 소스를 뿌린다.\r\n10. 9에 햄 튀김을 올리고 건자두, 데친 브로콜리를 올려 완성한다.'),
	(92, '수픈데 오믈렛', NULL, 1, '1. 달걀 노른자와 흰자를 분리한다.\n2. 노른자에 레몬 껍질, 레몬즙을 넣고 소금, 후추 간을 한다.\n3. 노른자를 체에 거른다.\n4. 흰자는 거품을 낸다.\n5. 노른자에 잘게 자른 햄, 흰자 거품을 넣고 섞는다.\n6. 팬에 버터를 녹인 후 오믈렛 재료를 담는다.\n7. 6을 오븐에 넣어 익힌다.\n8. 참치와 데친 브로콜리를 섞어 샐러드를 만든다.\n9. 머스터드 소스, 올리브유, 레몬즙, 설탕을 섞어 소스를 만든 후 샐러드와 섞는다.\n10. 물에 옥수수 수프 분말을 넣고 끓이다 후추 간을 한다.\n11. 옥수수 수프에 스위트 칠리 소스, 레몬즙을 섞어 소스를 만든다.\n12. 접시에 샐러드와 오믈렛을 담고 옥수수 수프 소스를 뿌려 완성한다.'),
	(93, '빠리송로', NULL, 1, '1. 물에 치킨 스톡, 당근을 넣어 끓인다.\n2. 두부의 물기를 제거하고 4등분 한다.\n3. 기름에 두부를 살짝 튀긴다.\n4. 버터, 느타리버섯, 양파를 함께 볶다가 소금, 후추, 다진 생강을 넣는다.\n5. 두부의 속을 파낸다.\n6. 볶은 버섯을 잘게 다진다.\n7. 두부에 다진 버섯, 트러플 소스를 넣어 속을 채운다.\n8. 냄비에 체를 밭쳐 육수를 걸러낸다.\n9. 당근을 핸드 블렌더로 갈아 준다.\n10. 9에 육수, 우유를 넣고 갈아 당근 퓌레를 만든다.\n11. 접시에 당근 퓌레와 속을 채운 두부를 올리고 트러플 소스를 올린다.\n12. 트러플 오일을 뿌려 완성한다.'),
	(94, '머라노', NULL, 1, '1. 표고버섯을 따뜻한 물에 넣어 우린다.\r\n2. 양파, 당근을 큼직하게 썰어 소금을 넣고 볶는다.\r\n3. 2에 물을 붓고 끓여 채소 육수를 낸다.\r\n4. 올리브 오일을 두른 팬에 마늘과 표고버섯, 느타리버섯을 넣고 볶는다.\r\n5. 버섯에 즉석밥을 넣고 함께 볶는다.\r\n6. 5에 표고버섯을 우린 물과 채소 육수를 부어가며 리소토를 만든다.\r\n7. 리소토에 그라나파다노 치즈를 갈아 넣는다.\r\n8. 버터를 두른 팬에 느타리버섯을 볶는다.\r\n9. 접시에 리소토를 담고 치즈를 갈아 올린 후, 볶은 느타리버섯을 올린다.\r\n10. 리소토에 후추를 뿌려 완성한다.'),
	(95, '파프리콘', NULL, 1, '1. 파프리카의 껍질을 태운다.\r\n2. 믹서기에 토마토 통조림, 마늘, 고추, 파슬리, 바질페스토, 소금, 후추, 올리브 오일을 넣고 간다.\r\n3. 페타 치즈와 옥수수를 섞는다.\r\n4. 파프리카의 껍질을 벗긴다.\r\n5. 파프리카에 페타 치즈와 옥수수 섞은 것을 넣어 속을 채운다.\r\n6. 5에 밀가루를 묻혀 굽는다.\r\n7. 파프리카에 토마토 소스를 부어 함께 끓인다.\r\n8. 토마토 소스에 옥수수를 넣고 볶아준다.\r\n9. 접시에 속을 채운 파프리카를 놓고 생 파프리카, 블랙 올리브를 올린다.\r\n10. 옥수수를 넣어 볶은 토마토 소스와 파슬리를 올려 완성한다.'),
	(96, '섬섬옥수수', NULL, 1, '1. 버터를 두른 팬에 으깬 마늘, 다진 양파, 소금을 넣고 볶는다.\r\n2. 볶던 양파에 옥수수를 넣어 함께 볶는다.\r\n3. 우유에 리코타 치즈를 넣고 끓인다.\r\n4. 3에 치킨 스톡을 넣는다.\r\n5. 치킨 스톡과 치즈를 넣고 끓인 우유를 옥수수에 부어 함께 끓인다.\r\n6. 5를 믹서에 넣고 갈아준다.\r\n7. 옥수수 수프에 버터, 소금, 후추를 넣는다.\r\n8. 밀가루, 설탕, 우유, 달걀, 소금을 섞어 튀김 반죽을 만든다.\r\n9. 반죽에 해바라기씨, 옥수수를 넣은 후 트러플 오일을 섞어 준다.\r\n10. 반죽을 한 숟가락씩 덜어 기름에 튀긴다.\r\n11. 옥수수 수프 위에 옥수수 튀김을 올리고 그라나파다노 치즈를 갈아 올린다.'),
	(97, '스푼 파스타', NULL, 1, '1. 물에 밴댕이를 넣고 끓여 육수를 낸다.\r\n2. 파스타면을 펼치지 않고 한데 모아 삶는다.\r\n3. 살짝 익은 면을 찬물에 헹군 뒤 잘게 썬다.\r\n4. 갑오징어 알, 새우, 키조개 관자를 손질한다.\r\n5. 팬에 올리브 오일을 두르고다진 마늘, 갑오징어 알, 새우, 키조개 관자를 함께 볶는다.\r\n6. 5에 올리브유를 한 번 더 두르고, 후추로 간을 한다.\r\n7. 밴댕이 육수를 부어준 다음, 잘게 썬 파스타면을 넣어 익힌다.\r\n8. 잘게 썬 파스타면을 넣어 익힌다.\r\n9. 파스타에 카망베르 치즈를 잘게 썰어 넣는다.\r\n10. 접시에 파스타를 담아낸다.'),
	(98, '연복쌈', NULL, 1, '1. 끓는 물에 전복을 삶는다.\n2. 돼지고기를 가늘게 썬다.\n3. 양파, 애호박을 잘게 썬다.\n4. 대파와 오이를 채 썰어 접시에 담아둔다.\n5. 밀가루에 소금을 넣고 뜨거운 물을 부어가며 춘빙 반죽을 만든다.\n6. 춘빙 반죽을 적당한 크기로 떼어 납작하게 누른 후 한쪽 면에 참기름을 바른다.\n7. 참기름을 바른 쪽이 서로 마주보도록 춘빙 반죽을 두 개 합친 후 밀대로 얇게 민다.\n8. 삶은 전복을 얇게 채 썬다.\n9. 기름을 두른 팬에 손질해 둔 양파, 애호박, 돼지고기, 전복을 넣고 볶는다.\n10. 9에 춘장, 전복소스, 올리브 오일을 넣어 볶는다.\n11. 팬에 춘빙 반죽을 굽는다.\n12. 구운 춘빙 반죽을 바닥에 강하게 내리쳐 두 장으로 분리한다.\n13. 채썬 대파, 오이를 담아 놓은 접시에 춘빙을 접어 낸다.\n14. 다른 접시에 볶은 돼지고기와 전복을 얹어 낸다.'),
	(99, '갈갈이 풍면', NULL, 1, '1. 믹서에 황태채, 새우가루, 홍합가루, 고춧가루를 넣고 간다.\n2. 1에 토마토, 할라피뇨, 매실 장아찌, 마늘 장아찌, 매실액, 올리브 오일을 넣고 간다.\n3. 삼겹살에 후추 간을 해서 굽는다.\n4. 엔젤헤어를 삶는다.\n5. 면을 건져 찬물에 헹군다.\n6. 접시에 적당한 크기로 자른 삼겹살을 얹는다.\n7. 6에 면, 토마토 소스를 올려 완성한다.'),
	(100, '한식 브런찜', NULL, 1, '1. 끓는 물에 된장을 풀고 요리술을 넣어 끓인다.\r\n2. 찜기에 솔잎을 올리고 굴비채를 찐다.\r\n3. 즉석밥을 전자레인지에 넣고 익힌다.\r\n4. 끓는 물에 소금을 넣고 눈개승마를 데친다.\r\n5. 데친 눈개승마를 차가운 물에 담가 놓는다.\r\n6. 다진 마늘, 다진 파, 된장, 아가베 시럽을 섞는다.\r\n7. 6에 고춧가루, 깨를 갈아 넣어 나물 양념을 만든다.\r\n8. 물기를 뺀 눈개승마에 나물 양념과 참기름을 넣고 무친다.\r\n9. 접시에 밥과 나물을 올린다.\r\n10. 대파, 양조간장, 유자청, 아가베 시럽, 물을 섞어 굴비채 양념을 만든다.\r\n11. 찐 굴비채를 접시에 담고 굴비채 양념을 올려 완성한다.'),
	(101, '연복풍 덮밥', NULL, 1, '1. 팬에 식용유와 참기름을 두른 후 다진 마늘, 다진 생강을 넣어 볶는다.\r\n2. 1에 길게 썬 대파를 넣어 볶는다.\r\n3. 간장, 고춧가루, 게맛살을 넣고 함께 볶는다.\r\n4. 고량주를 넣어 볶는다.\r\n5. 팽이버섯을 넣어 볶는다.\r\n6. 전분물을 넣어준다.\r\n7. 달걀 프라이를 만든다.\r\n8. 즉석밥을 전자레인지에 데운다.\r\n9. 달걀 프라이에 물을 조금 붓고 뚜껑을 닫아 익힌다.\r\n10. 접시에 즉석밥을 담고 볶은 재료와 달걀 프라이를 올린다.'),
	(102, '윌나잇까스', NULL, 1, '1. 큼직하게 썬 당근과 양파를 볶는다.\r\n2. 1에 물을 붓고 끓여 채소 육수를 만든다.\r\n3. 소고기 등심 위 아래로 비닐을 깔고 고기를 두드려 얇게 편다.\r\n4. 고기에 칼집을 넣은 다음 소금, 후추 간을 한다.\r\n5. 버터와 밀가루를 볶아 루를 만든다.\r\n6. 다진 양파를 볶다가 레몬즙과 채소 육수를 넣는다.\r\n7. 6에 루를 넣어 레몬 소스를 만든다.\r\n8. 고기에 밀가루-달걀-빵가루 순으로 튀김옷을 입혀 굽는다.\r\n9. 기름 두른 팬에 팽이버섯을 볶는다.\r\n10. 고기와 버섯에 버터를 넣어준다.\r\n11. 고기에 버터를 끼얹으며 굽는다.\r\n12. 접시에 레몬 소스를 붓고 돈가스, 볶은 팽이버섯을 올려 낸다.'),
	(103, '이것이 원윌이닭', NULL, 1, '1. 닭가슴살을 얇게 썬 다음 소금, 후추 간을 한다.\r\n2. 닭가슴살을 비닐팩에 넣어 최대한 공기를 뺀 후 밀봉한다.\r\n3. 비닐팩에 넣은 닭가슴살을 따뜻한 물에 넣어 익힌다.\r\n4. 메밀라면을 익힌다.\r\n5. 익힌 메밀라면은 얼음물에 담가둔다.\r\n6. 알배추, 사과, 깻잎은 채 썰어 얼음물에 담가둔다.\r\n7. 쌈무를 잘게 썰어둔다.\r\n8. 익은 닭가슴살을 잘게 찢는다.\r\n9. 물김치 국물에 사과 주스, 식초, 레몬즙, 설탕, 다진 마늘, 고추, 연겨자, 닭고기 육수를 섞는다.\r\n10. 9에 얼음을 넣는다.\r\n11. 그릇에 메밀라면과 닭고기, 채 썬 채소를 올리고 국물을 붓는다.'),
	(104, '치킨 마요랑깨', NULL, 1, '1. 닭가슴살을 길게 썰어 간장, 후추로 간을 한다.\r\n2. 닭가슴살에 믹서에 간 김치, 으깬 마늘을 넣는다.\r\n3. 깨와 밀가루를 섞어 준비한다.\r\n4. 닭가슴살에 달걀물을 섞은 다음 3을 묻혀 준다.\r\n5. 닭가슴살을 튀긴다.\r\n6. 달걀 노른자에 허니 머스터드, 레몬즙을 넣고 거품기로 섞는다.\r\n7. 올리브 오일을 조금씩 넣어가며 섞는다.\r\n8. 소금을 조금 넣는다.\r\n9. 접시에 튀긴 닭가슴살, 김치, 청양고추를 썰어 넣은 마요네즈를 같이 낸다.'),
	(105, '감동의 더가니', NULL, 1, '1. 새우 껍질을 벗긴다.\r\n2. 새우 등에 칼집을 넣고 소금, 후추 간을 한다.\r\n3. 새우에 밀가루를 묻힌 후 마늘을 볶던 팬에 굽는다.\r\n4. 인스턴트 도가니탕을 끓인다.\r\n5. 냄비에 레드와인을 넣고 졸인다.\r\n6. 도가니탕에 졸인 레드와인, 더치커피를 넣고 끓여 소스를 만든다.\r\n7. 6에 토마토 소스를 넣는다.\r\n8. 설탕을 넣는다.\r\n9. 버터에 밀가루를 묻힌 후 소스에 넣어 점도를 잡는다.\r\n10. 즉석밥을 데운다.\r\n11. 선드라이드 토마토를 잘게 다진 후 즉석밥, 올리브 오일과 섞는다.\r\n12. 새우에 화이트 와인을 뿌린다.\r\n13. 밥에 파슬리를 넣고 동그랗게 모양을 잡는다.\r\n14. 접시에 밥과 새우를 올린다.\r\n15. 소스를 붓고 미나리 잎으로 장식한 후, 레몬껍질을 갈아 올린다.'),
	(106, '치사의 사탑', NULL, 1, '1. 버터를 두른 팬에 동그랗게 썬 사과를 굽는다.\n2. 버터를 두른 팬에 살치살을 굽는다.\n3. 원형틀을 놓고 사과-살치살-사과-살치살 순으로 쌓는다.\n4. 3에 포트 와인과 레드 와인을 붓고 끓인다.\n5. 적당히 졸여진 사과와 살치살을 접시에 옮겨 담는다.\n6. 양파, 소시지, 베이컨, 할라피뇨를 볶는다.\n7. 6에 토마토 소스와 베이크드 빈을 넣어 카술레를 만든다.\n8. 팬에 레드와인을 붓고 다진 마늘, 표고 버섯, 케첩, 스리라차 소스를 넣는다.\n9. 8에 칠리 플레이크, 설탕, 버터를 넣어 레드 와인 소스를 만든다.\n10. 사과와 살치살 위에 레드 와인 소스를 뿌리고 카술레를 따로 담는다.\n11. 파슬리와 할라피뇨로 장식한다.'),
	(107, '와인퐁당', NULL, 1, '1. 스모크 치즈를 잘게 썬다.\r\n2. 냄비에 화이트 와인을 붓고 스모크 치즈를 넣어 끓인다.\r\n3. 2에 카망베르 치즈를 넣고 끓여 치즈 소스를 만든다.\r\n4. 마늘, 애호박, 살치살을 그릴팬에 굽는다.\r\n5. 손질한 새우를 그릴팬에 굽는다.\r\n6. 치즈 소스를 체에 거른다.\r\n7. 접시에 구운 애호박, 새우, 살치살을 담는다.\r\n8. 애호박과 살치살 위에 칠리 플레이크를 뿌린다.\r\n9. 새우 위에 로즈마리를 뿌린다.\r\n10. 치즈 소스에 칠리 플레이크를 뿌려 함께 낸다.'),
	(108, '우와한 양갈비', NULL, 1, '1. 사과와 애호박을 잘게 다진다.\r\n2. 베이크드 빈을 물에 헹군다.\r\n3. 베이컨을 잘게 다진다.\r\n4. 버터를 두른 팬에 다진 재료들과 베이크드 빈, 블랙 올리브를 넣고 볶는다.\r\n5. 양갈비에 소금, 후추 간을 한다.\r\n6. 레드 와인에 다진 표고 버섯, 다진 마늘을 넣고 끓인다.\r\n7. 6에 소금, 설탕을 넣고 끓인다.\r\n8. 재료를 걸러낸 끓인 레드 와인을 볶던 채소에 붓는다.\r\n9. 식용유를 두른 팬에 양갈비를 굽는다.\r\n10. 접시 위에 구운 양갈비를 놓고 레스팅한다.\r\n11. 양갈비를 굽던 팬에 포트 와인을 부어 알코올을 날린다.\r\n12. 끓인 포트 와인에 로즈마리를 넣는다.\r\n13. 12에 버터와 후추를 넣고 졸여 포트 와인 소스를 만든다.\r\n14. 볶은 채소 위에 양갈비를 올린다.\r\n15. 양갈비 위에 포트 와인 소스를 두른다.\r\n16. 파슬리와 칠리 플레이크를 뿌린다.\r\n17. 한 쪽에 양꼬치 분말 소스를 곁들여 낸다.'),
	(109, '이길 만두 하자냐', NULL, 1, '1. 새우 물만두의 만두피와 만두소를 분리한다.\r\n2. 배추김치를 다진다.\r\n3. 버터를 두른 팬에 김치를 볶는다.\r\n4. 방울토마토를 으깨어 넣는다.\r\n5. 만두소를 으깨어 넣는다.\r\n6. 고추장과 설탕을 넣어 볶는다.\r\n7. 찬밥에 우유를 넣고 간다.\r\n8. 7에 양송이 수프 통조림을 섞어 함께 끓인다.\r\n9. 그릇에 볶은 김치>만두피>수프>모차렐라 치즈를 얹고 전자레인지에 돌린다.'),
	(110, '복꽃엔딩', NULL, 1, '1. 팬에 기름을 두르고 한 번 살짝 닦는다.\r\n2. 달걀에 전분, 소금을 섞는다.\r\n3. 달군 팬에 달걀지단을 부친다.\r\n4. 배추김치를 물에 한 번 헹군 뒤 잘게 다진다.\r\n5. 양파, 표고버섯, 돼지고기를 잘게 썬다.\r\n6. 살짝 데친 오징어를 잘게 썬다.\r\n7. 팬에 기름을 두르고 양파와 돼지고기를 볶다가 간장을 넣는다.\r\n8. 7에 김치, 표고버섯, 오징어를 넣어 함께 볶는다.\r\n9. 굴소스를 넣는다.\r\n10. 설탕을 넣는다.\r\n11. 달걀지단에 볶은 재료를 올리고 김밥 말듯이 만다.\r\n12. 11에 밀가루 푼 물을 바른 후 기름에 살짝 튀긴다.\r\n13. 달걀춘권을 썰어 낸다.'),
	(111, '맹모닝', NULL, 1, '1. 꽁치 통조림의 국물과 건더기를 분리한다.\r\n2. 꽁치를 반으로 자르고 오렌지 즙을 뿌려 재워둔다.\r\n3. 꽁치에 레몬 식초를 뿌린다.\r\n4. 재워둔 꽁치를 팬에 넣고 끓인다.\r\n5. 4에 다진 양파와 소금을 넣고 볶는다.\r\n6. 식빵을 토스트기에 넣어 굽는다.\r\n7. 토마토를 얇게 썬다.\r\n8. 꽁치 국물에 양송이 수프 통조림을 넣고 끓인다.\r\n9. 8에 청양고추를 넣고, 우유를 부어 끓인다.\r\n10. 식빵의 가장자리를 자른 후, 수프에 넣어 적신다.\r\n11. 꽁치에 소금, 후추 간을 한다.\r\n12. 꽁치에 다진 마늘을 넣는다.\r\n13. 배추김치와 열무김치를 물에 헹군 후, 잘게 썬다.\r\n14. 김치에 옥수수 통조림, 채 썬 당근, 마요네즈를 함께 넣어 버무린다.\r\n15. 그릇에 김치 콘슬로우를 담고 다진 잣을 올린다.\r\n16. 식빵에 볶은 꽁치를 올린 후, 수프에 적신 식빵을 올린다.\r\n17. 16에 슬라이스 치즈, 토마토, 다진 피클을 얹고 식빵으로 덮는다.\r\n18. 접시에 토스트와 김치 콘슬로우를 함께 낸다.'),
	(112, 'LA 떡다져스', NULL, 1, '1. 소고기를 잘게 썬다.\r\n2. 간장, 설탕, 다진 마늘, 참기름, 깨를 섞어 간장 양념을 만든다.\r\n3. 간장 양념에 다진 파를 넣고 섞는다.\r\n4. 잘게 썬 소고기를 믹서에 간다.\r\n5. 간 소고기와 간장 양념을 섞는다.\r\n6. 떡국떡을 다진다.\r\n7. 양념한 소고기와 다진 떡을 섞은 후, 한 덩이씩 떼어 뭉친다.\r\n8. 동그랗게 반죽한 소고기를 팬에 굽는다.\r\n9. 표고버섯, 관자구이 제품, 양파, 갈릭&차이브 치즈, 대파를 썰어 둔다.\r\n10. 팬에 다진 마늘과 양파, 표고버섯, 대파, 갈릭&차이브 치즈를 볶는다.\r\n11. 10에 맛간장을 넣은 후, 관자구이 제품을 넣어 볶는다.\r\n12. 볶은 재료들을 그릇에 담고, 모차렐라 치즈를 올려 전자레인지에 돌린다.\r\n13. 철판에 떡갈비를 담고 잣을 올린다.\r\n14. 떡갈비에 관자 버섯 볶음을 곁들인 후, 모차렐라 치즈를 올려 낸다.'),
	(113, '혜.품.닭', NULL, 1, '1. 닭가슴살 위아래로 비닐을 깔고 냄비로 두드려 얇게 편다.\r\n2. 얇게 편 닭가슴살에 소금 간을 한다.\r\n3. 리코타 치즈와 크림 치즈, 말린 크랜베리를 섞는다.\r\n4. 3을 닭가슴살 위에 놓고 럭비공 모양으로 말아준다.\r\n5. 찜기에 물과 화이트 와인을 넣고, 말아놓은 닭가슴살을 찐다.\r\n6. 체에 가쓰오부시를 넣고 따뜻한 물을 부어 육수를 낸다.\r\n7. 가쓰오부시 육수에 샤프란을 넣는다.\r\n8. 생크림에 소금을 조금 넣고 끓인다.\r\n9. 생크림에 샤프란을 넣은 육수를 넣고 소금 간을 해 끓인다.\r\n10. 그릇에 찐 닭가슴살을 담고 샤프란 소스를 붓는다.\r\n11. 파슬리를 올려 완성한다.'),
	(114, '커룽지', NULL, 1, '1. 고추, 양파를 잘게 썰어 마늘과 함께 볶는다.\r\n2. 페페론치노와 레드 페퍼를 넣어 볶는다.\r\n3. 커리 가루, 코코넛 밀크, 생크림을 넣어 커리를 끓인다.\r\n4. 방울토마토, 레드와인 식초를 넣는다.\r\n5. 다마리 간장과 진간장을 넣는다.\r\n6. 기름을 두른 팬에 레드 커리 페이스트를 볶는다.\r\n7. 커리에 볶은 커리 페이스트를 넣는다.\r\n8. 설탕, 커리 가루를 넣고 함께 끓인다.\r\n9. 요거트를 넣는다.\r\n10. 끓인 커리를 믹서에 갈아준 뒤, 다시 끓인다.\r\n11. 닭가슴살을 잘게 썬다.\r\n12. 닭가슴살에 소금과 커리 가루를 묻힌 뒤, 커리에 넣어 함께 끓인다.\r\n13. 레몬즙을 넣는다.\r\n14. 그릇에 누룽지를 깔고, 커리를 붓는다.\r\n15. 그라나파다노 치즈를 갈아 올린 뒤, 다진 바질을 올린다.\r\n16. 올리브 오일을 둘러 완성한다.'),
	(115, '아빠손 피자', NULL, 1, '1. 애호박, 파프리카, 느타리 버섯을 손질한다.\n2. 손질한 채소에 올리브 오일, 소금을 넣고 섞는다.\n3. 채소를 그릴팬에 굽는다.\n4. 채소에 소금, 후추로 간을 한다.\n5. 마늘을 올리브 오일에 볶는다.\n6. 볶은 마늘을 우유에 넣고 함께 끓인다.\n7. 6을 믹서에 넣고 간 다음, 리코타 치즈를 넣어 마늘 크림 스프레드를 만든다.\n8. 토르티야를 구운 뒤 마늘 크림 스프레드를 바른다.\n9. 구운 채소를 올린다.\n10. 고르곤졸라 치즈, 생크림, 올리고당을 섞어 소스를 만든다.\n11. 구운 채소 위에 소스를 뿌린 뒤, 다진 양상추를 올린다.\n12. 채 썬 사과와 다진 마카다미아를 올린다.\n13. 말린 크랜베리를 올려 완성한다.'),
	(116, '따라미소', NULL, 1, '1. 달걀 흰자와 노른자를 분리한다.\r\n2. 달걀 흰자를 냉동실에 넣어둔다.\r\n3. 리코타 치즈에 달걀 노른자와 바닐라 오일을 넣는다.\r\n4. 3에 설탕을 넣고 섞는다.\r\n5. 생크림으로 거품을 낸다.\r\n6. 차가워진 달걀 흰자에 설탕을 넣어가며 머랭을 친다.\r\n7. 레이디핑거 쿠키를 썰어 딸기잼과 섞는다.\r\n8. 키위를 얇게 썬다.\r\n9. 달걀 노른자를 섞은 리코타 치즈, 거품을 낸 생크림, 머랭을 섞어 크림을 만든다.\r\n10. 컵에 크림>키위>크림>쿠키를 섞은 딸기잼>크림 순으로 올린다.\r\n11. 표면을 평평하게 만든 후, 코코아 파우더를 뿌린다.\r\n12. 체리, 사과로 장식해서 낸다.'),
	(117, '흥.칩.풍', NULL, 1, '1. 플레인 치즈와 바나나, 설탕을 섞은 뒤 갈아준다.\r\n2. 생크림, 설탕을 섞어 거품을 낸다.\r\n3. 2에 갈아놓은 치즈와 바나나를 섞어 바나나 크림을 만든다.\r\n4. 바나나 크림을 냉동실에 넣어 차갑게 만든다.\r\n5. 라이스 페이퍼를 튀겨 칩을 만든다.\r\n6. 접시에 블루베리 퓌레를 바른다.\r\n7. 퓌레 위에 라이스 페이퍼 칩을 올린다.\r\n8. 칩 위에 바나나 크림, 딸기를 올린다.\r\n9. 설탕과 물을 끓여 시럽을 만든 뒤, 포크로 가늘고 길게 늘어뜨린다.\r\n10. 9를 동그랗게 뭉친 뒤, 바나나 크림 위에 올려 완성한다.'),
	(118, '이롤슈가', NULL, 1, '1. 오븐을 예열한다.\r\n2. 전자레인지에 버터를 넣어 녹인다.\r\n3. 설탕(종이컵 1/2컵)과 커피 리큐어(한 큰술)를 섞는다.\r\n4. 3에 녹인 버터(종이컵 1/2컵)를 넣고 섞는다.\r\n5. 소금과 달걀 두 개를 넣고 섞는다.\r\n6. 박력분(소주 종이컵 2컵)을 넣고 섞는다.\r\n7. 유산지 위에 반죽을 부어 오븐에 넣고 익힌다.\r\n8. 설탕, 생크림을 섞어 거품을 낸다.\r\n9. 구워진 케이크 시트를 네모난 모양으로 자른다.\r\n10. 요거트, 조청을 섞어 케이크 시트에 바른다.\r\n11. 잘게 썬 사과와 딸기를 올린 뒤 생크림을 얹는다.\r\n12. 11을 돌돌 말아준 다음, 한입 크기로 썬다.\r\n13. 접시에 생크림을 깔고 롤케이크를 올린다.\r\n14. 다진 아몬드를 뿌려준다.\r\n15. 설탕과 전분을 믹서에 넣고 갈아 슈거파우더를 만든다.\r\n16. 롤케이크 위에 슈거파우더를 뿌려 완성한다.'),
	(119, '나이스 춘권', NULL, 1, '1. 대파를 썰어 팬에 볶는다.\n2. 차돌박이를 다진 뒤 소금, 후추 간을 한다.\n3. 대파를 볶던 팬에 차돌박이, 표고버섯을 넣고 볶는다.\n4. 3에 죽순을 넣어 함께 볶는다.\n5. 콩나물을 잘게 썰어 넣는다.\n6. 굴소스를 넣는다.\n7. 다진 마늘, 다진 고추를 넣는다.\n8. 설탕, 식초, 맛술을 넣어 볶는다.\n9. 간장을 넣는다.\n10. 레몬즙, 해선장, 다진 고추, 다진 마늘을 섞어 소스를 만든다.\n11. 뜨거운 물에 라이스 페이퍼를 넣고 불린다.\n12. 라이스 페이퍼에 볶은 재료를 놓고 말아준 뒤, 달걀물을 묻혀 고정시킨다.\n13. 12를 기름에 튀긴다.\n14. 접시에 샐러드 채소를 담고, 춘권을 잘라 올린 후 소스를 곁들여 낸다.'),
	(120, '오리감자 너엇~!', NULL, 1, '1. 오이와 토마토를 썰어둔다.\r\n2. 적당한 크기로 썬 감자와 달걀을 삶는다.\r\n3. 밀가루, 어니언 파우더, 물, 소금을 섞어 튀김옷을 만든다.\r\n4. 양파를 동그란 모양으로 썰어 튀김옷을 입힌다.\r\n5. 마요네즈에 다진 양파, 레몬즙, 파슬리를 넣는다.\r\n6. 5에 올리고당을 넣고 섞어 타르타르 소스를 만든다.\r\n7. 훈제오리를 굽는다.\r\n8. 튀김옷을 입힌 양파를 기름에 튀긴다.\r\n9. 구운 훈제오리를 잘게 썬다.\r\n10. 간장, 들기름, 올리브 오일, 파슬리, 레몬즙, 다진 마늘, 식초를 넣어 간장 소스를 만든다.\r\n11. 접시에 토마토와 오이를 깔고 양파튀김을 올린다.\r\n12. 잘게 썬 훈제오리를 타르타르 소스와 섞어 양파튀김 속을 채운다.\r\n13. 파슬리와 올리브 오일, 간장 소스를 뿌린다.\r\n14. 다진 아몬드와 호두를 뿌려 낸다.'),
	(121, '행사의 완자님', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n2. 믹서에 삼겹살, 새우, 달걀 노른자, 소금, 후추를 넣고 간다.\r\n3. 2에 청국장을 넣고 함께 갈아 완자 반죽을 만든다.\r\n4. 완자 반죽에 양배추를 잘게 썰어 넣는다.\r\n5. 물에 헹군 김치를 잘게 썰어 완자 반죽에 넣는다.\r\n6. 번데기 통조림 국물에 번데기와 버터를 넣어 볶는다.\r\n7. 채 썬 파, 씨를 뺀 고추, 채 썬 양배추를 넣는다.\r\n8. 7에 소금, 후추, 고추기름을 넣고 볶는다.\r\n9. 고춧가루를 넣어 함께 볶는다.\r\n10. 전분에 탄산수를 넣어 튀김옷을 만든다.\r\n11. 동그랗게 뭉친 반죽에 전분 > 튀김옷을 입혀 기름에 튀긴다.\r\n12. 마요네즈, 다진 마늘, 핫소스, 유자청, 탄산수를 섞어 소스를 만든다.\r\n13. 접시에 번데기 채소 볶음을 담고 완자를 올린 후 소스를 곁들여 낸다.'),
	(122, '막창 한 번 믿어봐~', NULL, 1, '1. 물에 막창을 삶은 육수를 섞는다.\r\n2. 1에 잘게 썬 막창, 우유를 넣고 끓여 수프를 만든다.\r\n3. 다진 마늘과 식초를 섞는다.\r\n4. 올리브 오일에 버터를 녹인다.\r\n5. 4에 레드 페퍼를 넣어 고추기름을 만든다.\r\n6. 수프에 고추기름을 넣고 끓인다.\r\n7. 그릇에 수프를 담고 대파를 올린다.\r\n8. 마늘 식초를 곁들여낸다.'),
	(123, '김마삼', NULL, 1, '1. 끓는 물에 당면을 삶는다.\r\n2. 명란젓을 물에 헹군다.\r\n3. 껍질을 제거한 명란젓과 갈릭 디핑소스를 섞는다.\r\n4. 삶은 당면을 찬물에 헹군다.\r\n5. 당면에 간장, 소금, 후추를 넣어 섞는다.\r\n6. 슬라이스 체다 치즈와 김밥용 김을 자른다.\r\n7. 첫 번째 김말이 : 당면 + 치즈를 넣고 돌돌 만다.\r\n8. 두 번째 김말이 : 당면 + 새우를 넣고 돌돌 만다.\r\n9. 김말이에 밀가루를 묻힌다.\r\n10. 튀김가루와 물을 섞어 튀김옷을 만든다.\r\n11. 김말이에 튀김옷을 입혀 기름에 튀긴다.\r\n12. 튀김옷을 뿌려 눈꽃모양을 만든다.\r\n13. 접시에 소스를 담고 김말이를 올린다.'),
	(124, '누르삼', NULL, 1, '1. 당근과 양파를 얇게 썰어 올리브 오일에 볶는다.\n2. 1에 적당한 크기로 썬 막창을 넣어 함께 볶는다.\n3. 소금, 후추 간을 한다.\n4. 레드 페퍼, 토마토, 브로콜리를 넣어 함께 볶는다.\n5. 토마토 소스를 넣어 끓인다.\n6. 베이컨을 굽는다.\n7. 베이컨 기름에 달걀 스크램블을 만든다.\n8. 올리브 오일과 참기름을 두른 팬에 명란젓을 굽는다.\n9. 누룽지를 기름에 튀긴다.\n10. 남은 누룽지를 잘게 부숴 기름에 튀긴다.\n11. 10에 설탕을 묻힌 뒤 그릇에 담는다.\n12. 튀긴 누룽지에 파르메산 치즈가루를 묻힌다.\n13. 첫 번째 누룽지 : 치즈 + 달걀 스크램블+베이컨을 올린다.\n14. 두 번째 누룽지 : 치즈 + 토마토 막창 스튜를 올린다.\n15. 세 번째 누룽지 : 구운 명란젓 + 다진 대파 + 울외 장아찌를 올린다.\n16. 세 가지 누룽지를 함께 담아 완성한다.'),
	(125, '만두렀써니', NULL, 1, '1. 우유에 설렁탕 국물, 버터를 넣고 끓여 크림 소스를 만든다.\r\n2. 팬에 소고기 등심을 넣고 소금, 후추를 뿌려 굽는다.\r\n3. 2에 잘게 썬 양파를 넣어 볶는다.\r\n4. 끓는 물에 밀싹, 소금을 넣어 살짝 데친다.\r\n5. 소고기 등심을 볶던 팬에 데친 밀싹을 넣어 함께 볶는다.\r\n6. 5를 핸드 블렌더로 갈아 만두소를 만든다.\r\n7. 만두소에 파르메산 치즈를 갈아 넣는다.\r\n8. 밀가루, 달걀을 섞어 반죽을 만든다.\r\n9. 반죽을 밀대로 얇게 펴서 만두피를 만든다.\r\n10. 만두피를 정사각형으로 자른 뒤, 가장자리에 물을 발라준다.\r\n11. 만두피 위에 만두소를 얹은 뒤, 동그랗게 빚는다.\r\n12. 끓는 물에 만두를 넣어 익힌다.\r\n13. 버터를 두른 팬에 다진 양파를 넣고, 크림 소스를 부어 끓인다.\r\n14. 크림 소스에 만두를 넣은 뒤, 다진 밀싹을 뿌려준다.\r\n15. 만두를 그릇에 옮겨 담고 파르메산 치즈를 갈아 올린다.'),
	(126, '오겹시대', NULL, 1, '1. 마늘, 파, 당근, 양파를 손질한다.\r\n2. 냄비에 기름을 두른 뒤, 채소를 넣고 물을 부어 육수를 낸다.\r\n3. 살라미를 다진다.\r\n4. 마늘, 파, 양배추를 잘게 자른다.\r\n5. 팬에 살라미와 채소를 넣고 볶은 뒤 소금, 후추로 간을 한다.\r\n6. 5에 채소 육수, 레몬즙을 넣어가며 볶는다.\r\n7. 사과즙에 시나몬 설탕을 넣어 졸인다.\r\n8. 볶던 채소에 사과 소스를 넣어준다.\r\n9. 오겹살 비계에 칼집을 낸 뒤 소금, 후추를 뿌려준다.\r\n10. 칼집을 낸 오겹살을 굽는다.\r\n11. 오겹살 비계에 사과 소스를 발라가며 다시 굽는다.\r\n12. 오븐 팬에 채소 볶음을 깔고 오겹살을 올린다.\r\n13. 12에 사과 소스를 뿌린 뒤, 오븐에 넣어 다시 익힌다.\r\n14. 밀싹을 다진다.\r\n15. 접시에 채소 볶음을 올린다.\r\n16. 채소 볶음 위에 사과 소스를 뿌리고, 오겹살을 올린다.\r\n17. 다진 밀싹, 레몬즙을 뿌려 완성한다.'),
	(127, '코드네임 써니', NULL, 1, '1. 냄비에 올리브 오일을 가득 따른 뒤, 레몬 껍질을 넣어준다.\r\n2. 1에 대구를 넣어 중불로 익힌다.\r\n3. 설렁탕 국물에 샤프란을 넣어 끓인다.\r\n4. 3에 잘게 썬 감자, 소금을 넣는다.\r\n5. 살라미를 썰어 전자레인지에 넣고 돌린다.\r\n6. 팬에 파르메산 치즈를 갈아서 넣은 뒤, 잘게 썬 밀싹을 올려 굽는다.\r\n7. 익힌 대구에 레몬 껍질을 갈아 올린다.\r\n8. 레몬즙, 올리브 오일, 꿀, 데이츠 시럽, 데이츠, 소금, 후추를 넣어 소스를 만든다.\r\n9. 접시에 데이츠 소스를 올린다.\r\n10. 샤프란 소스에 졸인 감자를 올린다.\r\n11. 감자 위에 대구를 올린다.\r\n12. 구운 살라미를 얹는다.\r\n13. 데이츠 소스를 뿌린 뒤, 밀싹을 올려 완성한다.'),
	(128, '오시지', NULL, 1, '1. 오징어를 손질한다.\n2. 팬에 버터, 다진 마늘을 넣고 볶는다.\n3. 오징어에 칼집을 낸 뒤 후추, 설탕, 고춧가루를 뿌린다.\n4. 믹서에 케이퍼, 볶은 버터와 다진 마늘, 오징어를 넣어 함께 갈아준다.\n5. 종이호일 위에 오징어 반죽을 올린 뒤 소시지 모양으로 돌돌 말아준다.\n6. 찜기에 소주를 넣은 뒤, 오징어 반죽을 찐다.\n7. 냄비에 맛술, 간장, 오렌지 주스, 다진 생강을 넣어 졸인다.\n8. 7에 전분물을 넣는다.\n9. 오렌지 소스를 체에 거른다.\n10. 오징어 소시지의 종이호일을 벗겨내고 굽는다.\n11. 잘게 썬 양배추를 볶는다.\n12. 달군 팬에 볶은 양배추를 올린 뒤, 오징어 소시지를 올린다.\n13. 오징어 소시지 위에 오렌지 소스를 뿌린다.\n14. 마요네즈를 뿌린다.\n15. 잘게 썬 파를 올리고, 후추를 뿌려 완성한다.'),
	(129, '나이스 투 미튜', NULL, 1, '1. 물에 잘게 부순 면과 분말수프를 넣어 끓인다.\r\n2. 감자칩을 잘게 부순다.\r\n3. 달걀물과 밀가루를 준비한다.\r\n4. 야채참치의 기름을 제거한다.\r\n5. 야채참치에 소금, 후추, 버터를 섞은 후, 동그랗게 뭉친다.\r\n6. 익은 면을 건진 후, 찬물에 헹군다.\r\n7. 참치볼에 면을 감싸 동그랗게 뭉친 후, 밀가루를 묻힌다.\r\n8. 7에 달걀물→감자칩 순으로 튀김옷을 입힌다.\r\n9. 참치볼을 기름에 튀긴다.\r\n10. 면을 기름에 넣어 튀긴다.\r\n11. 접시에 레몬을 잘게 썰어 얹고, 케첩과 마요네즈를 뿌린다.\r\n12. 접시에 참치볼을 얹은 후, 방울토마토로 장식한다.\r\n13. 참치볼과 튀긴 면을 함께 낸다.'),
	(130, '복침개', NULL, 1, '1. 햄을 잘게 썬다.\r\n2. 양파, 대파, 당근을 잘게 썰어 준비한다.\r\n3. 우동면을 기름에 넣어 튀긴다.\r\n4. 튀긴 우동면을 건져낸 후, 다시 기름에 넣어 납작하게 튀긴다.\r\n5. 잘게 썰어 놓은 햄을 튀긴다.\r\n6. 햄을 체에 밭쳐 기름을 뺀다.\r\n7. 기름을 두른 팬에 다진 마늘과 손질한 채소를 넣어 볶는다.\r\n8. 볶던 채소에 우동 액상소스와 물을 넣어 끓인다.\r\n9. 8에 튀긴 햄을 넣어 함께 볶는다.\r\n10. 전분물을 풀어 농도를 맞춘다.\r\n11. 튀긴 우동면을 적당한 크기로 잘라 접시에 담는다.\r\n12. 채소&햄 볶음에 참기름을 넣는다.\r\n13. 튀긴 우동면 위에 채소&햄 볶음을 얹어 낸다.'),
	(131, '토달토달', NULL, 1, '1. 마늘을 으깬다.\r\n2. 팬에 식용유와 참기름을 두른 후, 으깬 마늘을 볶는다.\r\n3. 비닐팩에 방울토마토를 넣고 으깬다.\r\n4. 볶던 마늘에 으깬 방울토마토, 간장, 식초를 넣고 끓인다.\r\n5. 맛술을 넣어 함께 끓인다.\r\n6. 식빵을 깍둑썰기한 후, 팬에 굽는다.\r\n7. 토마토탕에 곰탕라면의 분말수프를 넣고 함께 끓인다.\r\n8. 8에 전분물을 풀어 농도를 맞춰준다.\r\n9. 달걀을 볼에 깨트린 후 거품기로 곱게 푼다.\r\n10. 토마토탕에 달걀물을 천천히 풀어가며 끓인다.\r\n11. 그릇에 토마토 달걀탕을 부은 후, 구운 식빵을 함께 낸다.'),
	(132, '아육동', NULL, 1, '1. 양파, 마늘, 당근, 대파를 손질한다.\r\n2. 기름을 두른 팬에 손질한 채소를 넣고 볶는다.\r\n3. 믹서기에 육포를 넣고 간다.\r\n4. 볶던 채소에 육포가루를 넣고 함께 볶는다.\r\n5. 소주를 부어 함께 볶는다.\r\n6. 5에 뜨거운 물을 붓고 끓여 육수를 낸다.\r\n7. 장조림 통조림의 국물과 건더기를 분리한다.\r\n8. 장조림을 잘게 찢는다.\r\n9. 밀가루에 소금, 물을 넣고 섞어 반죽을 만든다.\r\n10. 끓는 기름에 반죽을 부어 튀긴다.\r\n11. 텐카스를 잘게 부순 후, 그릇에 담는다.\r\n12. 깻잎 장아찌, 참기름, 다진 마늘을 섞은 후, 그릇에 담는다.\r\n13. 대파를 잘게 썰어 물에 담가 아린맛을 뺀 후, 그릇에 담는다.\r\n14. 육수에 장조림 국물과 청양고추를 넣는다.\r\n15. 끓는 물에 우동면을 삶는다.\r\n16. 육수에 설탕, 소금을 넣는다.\r\n17. 그릇에 우동면을 담고 육수를 부은 후, 잘게 찢은 장조림을 얹어낸다.\r\n18. 잘게 썬 대파, 양념한 깻잎 장아찌, 텐카스를 곁들여 낸다.'),
	(133, '뽀빠이롤', NULL, 1, '1. 접시를 냉동실에 넣어둔다.\r\n2. 달걀, 소금, 우유, 탄산수를 섞어준다.\r\n3. 2에 냉동 시금치를 넣고 핸드 블렌더로 갈아준다.\r\n4. 곱게 간 재료들에 밀가루를 섞어 반죽을 만든다.\r\n5. 기름을 두른 팬에 시금치 반죽을 얇게 부친다.\r\n6. 훈제 연어를 으깨듯이 잘게 부순다.\r\n7. 크림 치즈, 페타 치즈, 플레인 요거트를 섞어 치즈 소스를 만든다.\r\n8. 냉동실에서 꺼낸 접시 위에 구운 시금치 반죽을 올려 식힌다.\r\n9. 구운 시금치 반죽에 치즈 소스를 바른다.\r\n10. 9에 훈제 연어살을 올린 후 돌돌 말아준다.\r\n11. 시금치롤을 냉동실에 넣어 식힌다.\r\n12. 플레인 요거트, 마요네즈, 꿀을 섞어 드레싱을 만든 후 샐러드 채소와 버무린다.\r\n13. 접시에 샐러드를 담는다.\r\n14. 시금치롤을 썰어 접시에 올린 후 치즈 소스를 뿌려 완성한다.'),
	(134, '부드러어', NULL, 1, '1. 달걀을 푼 다음 소금, 후추로 간을 한다.\r\n2. 살라미를 적당한 크기로 썬다.\r\n3. 냄비에 올리브 오일, 살라미, 우유를 넣어 함께 끓인다.\r\n4. 끓인 재료들을 핸드 블렌더로 갈아준다.\r\n5. 4를 체에 밭쳐 건더기를 분리한 후, 달걀물과 함께 섞는다.\r\n6. 소금으로 간을 한 후, 버터를 바른 원형틀에 달걀물을 붓는다.\r\n7. 원형틀에 랩을 씌운 후 구멍을 낸다.\r\n8. 7을 찜통에 넣어 찐다.\r\n9. 방울양배추를 찜통에 넣어 함께 찐다.\r\n10. 대구살을 적당한 크기로 썬 다음 소금, 후추로 간을 한다.\r\n11. 대구살에 밀가루를 묻혀 기름을 두른 팬에 굽는다.\r\n12. 11에 화이트 와인을 부어준다.\r\n13. 꿀을 넣는다.\r\n14. 레몬즙을 넣는다.\r\n15. 버터, 잘게 썬 방울토마토를 넣어 조린다.\r\n16. 대파를 채 썬 다음 기름에 튀긴다.\r\n17. 접시에 달걀찜을 올린 후 방울양배추 잎을 곁들인다.\r\n18. 달걀찜 위에 대구살, 방울토마토를 올린 후 소스를 부어준다.\r\n19. 튀긴 대파를 올려 완성한다.'),
	(135, '오린기', NULL, 1, '1. 양파를 채 썰어 얼음물에 담가둔다.\r\n2. 감자 전분과 옥수수 전분을 7:3의 비율로 섞는다.\r\n3. 2에 물을 넣으면서 섞어준다.\r\n4. 식용유를 넣어 섞는다.\r\n5. 달걀을 풀어 반죽에 넣고 섞는다.\r\n6. 물에 불린 반건조 오징어에 칼집을 낸다.\r\n7. 반건조 오징어를 적당한 크기로 썬다.\r\n8. 반건조 오징어에 반죽을 묻힌 후 기름에 튀긴다.\r\n9. 마요네즈, 코코넛 밀크, 레몬즙, 설탕, 고추냉이를 섞어 소스를 만든다.\r\n10. 양파의 물기를 제거한 후 접시에 담는다.\r\n11. 양파 위에 오징어 튀김을 올린 후, 소스를 부어 완성한다.'),
	(136, '허니버터징', NULL, 1, '1. 그릇(오븐용)을 오븐에 넣어 데운다.\r\n2. 반건조 오징어를 잘게 자른다.\r\n3. 끓는 물에 소금을 넣은 후 브로콜리를 데친다.\r\n4. 애호박, 양파, 레몬, 방울양배추, 마늘, 쪽파, 고추 등 채소를 손질한다.\r\n5. 손질한 채소들을 팬에 굽는다.\r\n6. 데운 그릇에 구운 채소들을 담은 후, 오븐에 넣어 다시 굽는다.\r\n7. 끓는 물에 반건조 오징어를 넣고 익힌다.\r\n8. 매실청, 꿀, 쯔유, 후추, 버터, 파슬리를 섞어 소스를 만든다.\r\n9. 익힌 반건조 오징어를 소스에 넣고 끓인다.\r\n10. 오븐에서 그릇을 꺼낸 후 반건조 오징어를 담고 소스를 붓는다.\r\n11. 10을 오븐에 넣고 다시 굽는다.\r\n12. 잘게 썬 대파, 양파를 볶는다.\r\n13. 12에 쯔유, 달걀물을 부은 후 즉석밥을 넣어 함께 볶는다.\r\n14. 후추로 간을 한 후 볶음밥을 컵에 담는다.\r\n15. 오이지의 물기를 뺀 후, 대파를 다져 함께 섞는다.\r\n16. 15에 다진 마늘, 참기름, 깨를 넣어 무친다.\r\n17. 오븐에서 구운 재료들을 꺼낸 후, 볶음밥을 함께 담아낸다.'),
	(137, '납작 탕수육', NULL, 1, '1. 대파의 뿌리를 제거한 후 껍질을 벗긴다.\n2. 식용유에 대파, 고춧가루를 넣고 끓인다.\n3. 등심을 칼등으로 두드려준다.\n4. 전분, 찹쌀가루, 물, 달걀물, 식용유를 섞어 튀김옷을 만든다.\n5. 등심에 튀김옷을 입힌 후 기름에 튀긴다.\n6. 끓인 재료들을 체에 밭쳐 기름만 걸러낸다.\n7. 당근, 양파, 고추를 얇게 썬다.\n8. 마늘을 다지고, 대파를 썰어둔다.\n9. 고추기름에 다진 마늘, 대파, 고추를 넣어 볶는다.\n10. 9에 양파, 당근, 간장을 넣어 함께 볶는다.\n11. 토마토소스를 넣는다.\n12. 물, 설탕, 굴소스를 넣는다.\n13. 레몬즙을 넣은 다음, 전분물을 풀어 농도를 맞춘다.\n14. 튀긴 등심을 먹기 좋은 크기로 썰어 접시에 담는다.\n15. 탕수육 소스를 따로 담아 고기와 함께 낸다.'),
	(138, '샐러드 올리오', NULL, 1, '1. 마늘을 편으로 썬다.\r\n2. 그릴팬에 얇게 썬 애호박을 올리고 후추를 뿌려 굽는다.\r\n3. 토마토를 적당한 크기로 썰어 둔다.\r\n4. 끓는 물에 소금을 넣고 면을 삶는다.\r\n5. 올리브 오일을 두른 팬에 마늘을 넣어 볶는다.\r\n6. 5에 면수를 넣어 볶는다.\r\n7. 레몬즙, 올리브 오일, 설탕을 섞어 레몬 드레싱을 만든다.\r\n8. 팬에 삶은 면을 넣어 함께 볶는다.\r\n9. 8에 구운 호박을 넣어 함께 볶는다.\r\n10. 접시에 파스타를 담는다.\r\n11. 파스타 위에 토마토와 새싹 채소를 올린다.\r\n12. 레몬 드레싱을 뿌린다.\r\n13. 고다 치즈를 갈아서 올린다.\r\n14. 바질을 올려 완성한다.'),
	(139, '소고기 냉부', NULL, 1, '1. 무, 당근을 채 썬다.\r\n2. 다시마로 육수를 낸다.\r\n3. 육수에 소금을 넣고 무, 당근을 넣어 데친다.\r\n4. 애호박, 양배추, 청경채를 손질한다.\r\n5. 손질한 채소를 육수에 넣어 데친다.\r\n6. 참깨, 마요네즈, 간장, 설탕, 식초를 섞는다.\r\n7. 6을 핸드 블렌더로 갈아 참깨 소스를 만든다.\r\n8. 달걀 노른자, 일본 된장, 간장, 통마늘을 섞은 후 핸드 블렌더로 갈아준다.\r\n9. 8에 식초, 다진 마늘, 설탕을 넣어 된장 소스를 만든다.\r\n10. 된장 소스와 참깨 소스를 그릇에 담는다.\r\n11. 데친 채소들을 얼음물에 담가둔다.\r\n12. 소고기를 잘게 썬 후, 육수에 넣어 살짝 익힌다.\r\n13. 익힌 소고기를 얼음물에 담가둔다.\r\n14. 접시에 물기를 뺀 채소들을 올린다.\r\n15. 채소 위에 차갑게 식힌 소고기를 올린다.\r\n16. 소고기 위에 참기름을 두르고, 소스들을 함께 낸다.'),
	(140, '채면차림', NULL, 1, '1. 채끝살을 두드려 얇게 편다.\r\n2. 채끝살의 표면에 소금, 후추, 고춧가루를 묻힌다.\r\n3. 채끝살의 한쪽 면만 살짝 굽는다.\r\n4. 접시에 구운 고기를 올리고 올리브 오일을 뿌린 후, 냉동실에 넣어 식힌다.\r\n5. 두릅 장아찌, 마늘 장아찌, 올리브 오일, 레몬즙을 섞는다.\r\n6. 5를 핸드 블렌더로 갈아 두릅소스를 만든다.\r\n7. 고구마, 당근, 무를 필러로 얇게 썬 후, 반을 접어 면처럼 가늘게 썰어준다.\r\n8. 대파를 가늘게 채 썬다.\r\n9. 채소면을 찬물에 담가둔다.\r\n10. 올리브 오일과 버터를 두른 팬에 잘게 썬 마늘, 양파를 볶는다.\r\n11. 10에 채소면을 넣어 함께 볶는다.\r\n12. 채소면에 소금, 후추, 간장으로 간을 한다.\r\n13. 냉동실에 넣어둔 채끝살을 꺼낸 후, 나머지 한쪽 면을 살짝 구워준다.\r\n14. 접시에 채끝살을 얹은 후, 그 위에 볶은 채소면을 올린다.\r\n15. 레몬&산수유청, 올리브 오일, 레몬즙을 섞어 드레싱을 만든다.\r\n16. 15에 새싹 채소를 버무려 샐러드를 만든다.\r\n17. 채소면 위에 샐러드를 올린다.\r\n18. 두릅 소스를 뿌린 후 두릅 장아찌를 곁들인다.\r\n19. 견과류를 뿌려 완성한다.'),
	(141, '보아 씨, 초면입니다', NULL, 1, '1. 끓는 물에 소면을 삶는다.\r\n2. 익힌 면을 얼음물에 헹군다.\r\n3. 면의 물기를 제거한 뒤, 기름을 두른 팬에 넣고 지진다.\r\n4. 양파, 대파, 청양고추, 표고버섯을 잘게 썬다.\r\n5. 기름을 두른 팬에 대파를 넣어 볶는다.\r\n6. 소고기 안심을 얇게 썰어 넣는다.\r\n7. 소금, 다진 마늘을 넣는다.\r\n8. 얇게 썬 오징어와 손질한 채소를 넣고 함께 볶는다.\r\n9. 소금, 후추, 맛술을 넣는다.\r\n10. 간장과 물을 넣고 끓인다.\r\n11. 밀폐용기에 고춧가루, 으깬 마늘, 기름을 넣는다.\r\n12. 11을 전자레인지에 넣고 돌린다.\r\n13. 1분, 30초, 30초씩 끊어서 돌린다.\r\n14. 미나리를 적당한 크기로 썰어 넣는다.\r\n15. 참기름을 넣는다.\r\n16. 소스에 전분물을 넣어 농도를 맞춘다.\r\n17. 면을 적당한 크기로 썬다.\r\n18. 그릇에 면을 담고 소스를 올린 뒤, 고추기름을 뿌린다.\r\n19. 파채를 올린 뒤, 끓인 기름을 둘러 완성한다.'),
	(142, '최.면.석', NULL, 1, '1. 밀가루와 달걀을 섞어 반죽을 만든다.\r\n2. 반죽을 밀대로 얇게 민다.\r\n3. 얇게 민 반죽을 접은 다음, 가늘게 썰어 면을 만든다.\r\n4. 끓는 물에 면을 넣고 삶는다.\r\n5. 청양고추, 아스파라거스, 마늘을 잘게 썬다.\r\n6. 팬에 손질한 채소를 넣고 볶는다.\r\n7. 6에 새우를 넣은 뒤 소금, 후추, 화이트 와인을 넣어 함께 볶는다.\r\n8. 새우를 볶던 팬에 멸치육수, 카레가루를 넣어 소스를 만든다.\r\n9. 카레소스에 익힌 면을 넣어 함께 볶는다.\r\n10. 올리브 오일을 넣는다.\r\n11. 그릇에 완성된 요리를 담아낸다.'),
	(143, '맛있게 자시오', NULL, 1, '1. 냉면육수에 청양고추, 표고버섯을 넣어 끓인다.\r\n2. 잣을 볶은 다음 핸드 블렌더로 갈아준다.\r\n3. 끓인 재료들을 체에 밭쳐 육수만 걸러낸다.\r\n4. 갈아놓은 잣에 육수를 섞은 뒤, 함께 갈아 잣소스를 만든다.\r\n5. 잣소스를 체에 거른 뒤, 후추로 간을 한다.\r\n6. 소고기 안심을 깍둑썰기 한 뒤, 카레가루를 골고루 묻힌다.\r\n7. 카레가루로 양념한 소고기 안심을 굽는다.\r\n8. 끓는 물에 새우, 레몬을 넣어 데친다.\r\n9. 팬에 데친 새우와 레몬을 넣어 굽는다.\r\n10. 9에 버터, 소금, 후추를 넣어 함께 볶는다.\r\n11. 접시에 잣소스를 바르고 소고기 안심, 새우를 올린 뒤 쑥갓으로 장식한다.\r\n12. 고춧가루를 뿌려 완성한다.'),
	(144, '옥돔이 맛있돔', NULL, 1, '1. 즉석밥에 멸치육수를 붓고 전자레인지에 넣어 데운다.\r\n2. 냄비에 멸치육수를 넣고 끓인다.\r\n3. 옥돔의 살만 발라낸다.\r\n4. 옥돔살에 소금, 후추로 밑간을 한 뒤, 기름을 두른 팬에 굽는다.\r\n5. 양파, 대파, 마늘을 다진다.\r\n6. 옥돔살을 굽던 팬에 다진 채소를 넣은 뒤, 맛술을 넣고 볶는다.\r\n7. 6에 멸치육수를 붓고 끓인다.\r\n8. 멸치육수를 넣어 데운 밥을 넣고 함께 끓인다.\r\n9. 8을 핸드 블렌더로 곱게 간다.\r\n10. 곱게 간 옥돔죽을 냄비에 붓고 끓인다.\r\n11. 북어채를 믹서에 넣고 곱게 갈아 북어보푸라기를 만든다.\r\n12. 북어보푸라기에 부순 참깨를 넣는다.\r\n13. 설탕, 참기름, 소금을 섞은 뒤, 북어보푸라기에 양념이 배도록 무친다.\r\n14. 양념한 북어보푸라기를 동그랗게 뭉친 뒤 접시에 담는다.\r\n15. 고추 장아찌를 썰어 접시에 담는다.\r\n16. 그릇에 옥돔죽을 담고 채 썬 깻잎을 올린다.\r\n17. 옥돔죽에 북어보푸라기, 고추장아찌를 곁들여 낸다.'),
	(145, '키스 버거', NULL, 1, '1. 버터를 두른 팬에 적당한 크기로 썬 베이컨을 굽는다.\n2. 껍질을 벗긴 새우를 팬에 넣고 함께 굽는다.\n3. 2에 바질, 후추, 소금을 뿌린다.\n4. 버터를 두른 팬에 소시지, 소고기 안심을 굽는다.\n5. 소고기 안심에 바질을 뿌려 굽는다.\n6. 사과, 양파, 오이 장아찌, 고추 장아찌를 잘게 다진다.\n7. 마요네즈와 올리브 오일을 섞은 뒤, 다진 재료들을 넣고 섞는다.\n8. 레몬즙을 뿌린다.\n9. 구운 새우, 베이컨, 소시지를 잘게 썰어 넣는다.\n10. 진저 시럽을 넣은 뒤, 골고루 섞어준다.\n11. 훈제 닭가슴살에 버터를 발라 굽는다.\n12. 소고기 안심 > 소스에 버무린 재료들 > 소고기 안심 순으로 올린다.\n13. 닭가슴살 > 소스에 버무린 재료들 > 명이나물 > 닭가슴살 순으로 올린다.'),
	(146, '소시지 달콤하시지', NULL, 1, '1. 소시지를 잘게 다진다.\r\n2. 새우의 껍질을 벗긴 뒤 잘게 다진다.\r\n3. 팬에 다진 소시지와 새우를 넣고 볶는다.\r\n4. 소금과 으깬 마늘을 넣는다.\r\n5. 다진 양파를 넣는다.\r\n6. 만가닥버섯을 잘게 썰어 넣고 함께 볶는다.\r\n7. 양파를 한 꺼풀 벗긴 뒤, 그대로 기름에 튀긴다.\r\n8. 베이컨을 굽는다.\r\n9. 버터에 밀가루를 넣어 볶다가 우유를 조금씩 부어 베샤멜 소스를 만든다.\r\n10. 베샤멜 소스에 구운 베이컨을 다져 넣는다.\r\n11. 10에 소금, 후추, 레몬즙을 넣는다.\r\n12. 튀긴 양파에 볶은 재료를 넣고 돌돌 말아준다.\r\n13. 팬에 양파롤을 올린 뒤, 발사믹 크림을 뿌려 굽는다.\r\n14. 대파를 채썰어 기름에 튀긴다.\r\n15. 그릇에 베샤멜 소스를 담는다.\r\n16. 소스 위에 구운 양파롤을 얹은 뒤, 튀긴 파채를 올린다.\r\n17. 발사믹 크림을 뿌려 완성한다.'),
	(147, '소고기가 살아있네~', NULL, 1, '1. 소고기 안심을 잘게 다진다.\r\n2. 양파와 청양고추를 잘게 다진다.\r\n3. 다진 안심과 채소들을 섞은 뒤 후추를 뿌린다.\r\n4. 3에 소금, 올리브 오일, 핫소스, 케첩, 머스터드, 레몬즙을 넣고 골고루 섞는다.\r\n5. 오이 장아찌를 잘게 다져 넣는다.\r\n6. 육회에 달걀 노른자를 넣어 섞은 뒤, 냉동실에 넣어둔다.\r\n7. 쌈채소를 먹기 좋은 크기로 썬다.\r\n8. 다진 양파와 유자청을 섞는다.\r\n9. 8에 올리브 오일, 발사믹 식초, 후추를 섞어 드레싱을 만든다.\r\n10. 채소와 드레싱을 섞어 샐러드를 만든다.\r\n11. 원형틀을 이용해 육회를 접시에 담고, 샐러드를 곁들인다.\r\n12. 발사믹 크림과 파슬리를 뿌려 완성한다.'),
	(148, '족발 먹을 곤약?', NULL, 1, '1. 곤약을 길게 썬다.\r\n2. 길게 썬 곤약을 끓는 물에 데친다.\r\n3. 고춧가루, 간장, 유자청, 다진 마늘을 섞어 양념장을 만든다.\r\n4. 데친 곤약을 건져서 차가운 물로 식힌다.\r\n5. 꼬치에 곤약과 족발을 번갈아 끼운다.\r\n6. 족발&곤약 꼬치에 양념장을 발라 굽는다.\r\n7. 족발&곤약 꼬치와 상추를 함께 낸다.'),
	(149, '술푸자냐', NULL, 1, '1. 방울토마토를 믹서에 간다.\n2. 불린 미역을 잘게 다진 뒤, 크림치즈와 섞는다.\n3. 2에 소금, 후추로 간을 한다.\n4. 술떡을 얇게 썬다.\n5. 컵에 술떡>미역&크림치즈>간 토마토>술떡>미역&크림치즈>에멘탈 치즈>체다 치즈>간 토마토>미역&크림치즈>술떡>에멘탈 치즈>생모차렐라 치즈 순으로 쌓는다.\n6. 컵에 랩을 씌워 전자레인지에 넣고, 치즈가 녹을 정도로 돌린다.\n7. 술떡라자냐 위에 에멘탈 치즈, 김, 하몬으로 장식한다.\n8. 토치로 표면을 살짝 그을린다.\n9. 파르메산 치즈를 뿌려 완성한다.'),
	(150, '새로운 샐러드와 수란잔', NULL, 1, '1. 방울토마토는 꼭지 부분을 썰어 손질해 놓는다.\r\n2. 불린 미역을 적당한 크기로 썰어 끓는 물에 데친 뒤, 차가운 물에 담가둔다.\r\n3. 끓는 물에 방울토마토를 데친다.\r\n4. 데친 방울토마토의 껍질을 벗긴다.\r\n5. 끓는 물에 식초를 넣은 뒤, 달걀을 깨트려 넣어 수란을 만든다.\r\n6. 올리브 오일, 화이트 와인, 소금, 후추, 레몬즙을 섞어 드레싱을 만든다.\r\n7. 데친 미역에 드레싱을 버무려준다.\r\n8. 올리브 오일에 버터, 다진 마늘, 소금, 후추를 넣고 살짝 태우듯이 볶는다.\r\n9. 생모차렐라 치즈, 방울토마토, 드레싱을 섞어 샐러드를 만든다.\r\n10. 접시에 드레싱에 버무린 미역을 깔고, 샐러드를 올린다.\r\n11. 샐러드 위에 그린 올리브, 다진 견과류, 하몬을 올린다.\r\n12. 수란을 올린다.\r\n13. 뵈르 누아제트를 뿌려 완성한다.'),
	(151, '별미 리소토', NULL, 1, '1. 냄비에 손질한 양파와 당근, 올리브 오일을 넣고 볶는다.\n2. 1에 물을 붓고 끓여 채소 육수를 만든다.\n3. 기름을 두른 팬에 하몬, 으깬 마늘을 넣고 볶는다.\n4. 차돌박이를 잘게 썰어 팬에 넣고 함께 볶는다.\n5. 불린 미역을 잘게 썰어 넣고, 참기름을 더한다.\n6. 볶던 재료에 채소 육수를 넣은 뒤, 블렌더에 담는다.\n7. 6에 파르메산 치즈를 넣고 곱게 갈아준다.\n8. 기름을 두른 팬에 하몬, 차돌박이, 대파, 소금을 넣고 볶는다.\n9. 8에 다진 마늘, 즉석밥을 넣고 함께 볶아 리소토를 만든다.\n10. 리소토에 채소 육수와 갈아놓은 재료들을 넣고 함께 볶는다.\n11. 리소토에 파르메산 치즈를 넣어가며 볶는다.\n12. 잘게 썬 미역을 채소 육수에 넣어 데친다.\n13. 리소토에 버터와 다진 견과류를 넣는다.\n14. 리소토에 데친 미역을 넣고 함께 볶는다.\n15. 다진 방울토마토, 크림치즈, 다진 견과류를 섞어 동그랗게 뭉친다.\n16. 그릇에 리소토를 담고, 15를 올려 완성한다.'),
	(152, '이 참외 비빔면', NULL, 1, '1. 참외를 반으로 잘라 씨를 빼낸 뒤 얇게 썬다.\r\n2. 참외에 소금, 식초, 설탕을 넣고 살짝 절인다.\r\n3. 불린 미역을 잘게 썰어 소금을 넣은 끓는 물에 데친다.\r\n4. 데친 미역을 얼음물에 넣어 헹군다.\r\n5. 감자라면의 면을 익힌 뒤, 얼음물에 넣어 헹군다.\r\n6. 양파, 고추, 대파, 마늘을 손질한다.\r\n7. 고추장, 고춧가루, 설탕, 식초, 다진 마늘, 간장, 생강즙, 참기름을 섞어 양념장을 만든다.\r\n8. 달걀을 풀어 지단을 부친다.\r\n9. 면과 미역의 물기를 짠 뒤, 손질해 놓은 채소와 섞어준다.\r\n10. 절인 참외의 물기를 짠 뒤, 9와 함께 섞는다.\r\n11. 양념장을 넣고 골고루 비빈 뒤, 깨를 뿌린다.\r\n12. 그릇에 완성된 비빔면을 담는다.\r\n13. 비빔면 위에 채 썬 지단을 올려 완성한다.'),
	(153, '넘사복', NULL, 1, '1. 닭가슴살을 얇게 저민 뒤 기름에 튀긴다.\r\n2. 낙지를 끓는 물에 데친 뒤 얼음물에 담가 식힌다.\r\n3. 아스파라거스를 데친 뒤 얼음물에 담가 식힌다.\r\n4. 아스파라거스를 먹기 좋은 크기로 썰고, 오이는 채 썰어 준비한다.\r\n5. 튀긴 닭가슴살을 얇고 긴 모양으로 썬다.\r\n6. 땅콩버터, 물, 굴소스, 두반장, 설탕, 매운 냉면양념을 섞어 소스를 만든다.\r\n7. 닭가슴살, 채소, 낙지를 섞은 뒤 소스를 넣고 버무려 접시에 담는다.\r\n8. 소면을 삶은 뒤 얼음물에 헹군다.\r\n9. 물기를 뺀 소면에 고추기름, 굴소스, 매운 냉면양념을 넣고 버무린다.\r\n10. 접시에 양념한 소면을 올린다.\r\n11. 기름을 두른 팬에 잣을 볶는다.\r\n12. 볶은 잣을 올려 완성한다.'),
	(154, '풍살기', NULL, 1, '1. 냄비에 설탕과 물을 넣고 졸여 시럽을 만든다.\r\n2. 달걀 노른자에 박력분, 설탕을 넣고 섞는다.\r\n3. 2에 우유, 레몬즙을 섞은 뒤 전자레인지에 넣고 돌려 커스터드 크림을 만든다.\r\n4. 커스터드 크림이 뭉치지 않게 풀어준 다음, 다시 전자레인지에 돌리는 과정을 반복한다.\r\n5. 달걀 흰자는 거품을 낸다.\r\n6. 5에 시럽을 부어가며 이탈리안 머랭을 만든다.\r\n7. 달걀에 설탕, 소금을 넣고 곱게 푼다.\r\n8. 식빵을 달걀물에 적신 뒤, 버터를 두른 팬에 굽는다.\r\n9. 이탈리안 머랭에 커스터드 크림을 섞어 시부스트 크림을 만든다.\r\n10. 얼음물에 시부스트 크림이 담긴 볼을 담가 식힌다.\r\n11. 프렌치토스트 위에 시부스트 크림을 얹는다.\r\n12. 블루베리를 올려 완성한다.'),
	(155, '요거닭', NULL, 1, '1. 애호박을 썰어 기름에 튀긴다.\r\n2. 기름을 두른 팬에 훈제 닭가슴살을 굽는다.\r\n3. 달걀물에 모차렐라 치즈, 후추, 소금, 그릭 요거트, 바질, 다진 청양고추를 넣고 섞는다.\r\n4. 구운 닭가슴살에 3을 바른 뒤, 예열된 오븐에 넣어 굽는다.\r\n5. 그릭 요거트와 잘게 썬 딜, 다진 마늘, 소금을 섞어 요거트 소스를 만든다.\r\n6. 샐러드 채소에 올리브 오일, 레몬즙을 넣고 섞은 뒤 접시에 담는다.\r\n7. 원형틀을 이용해 요거트 소스 > 튀긴 애호박 > 요거트 소스 순으로 쌓는다.\r\n8. 잘게 썬 오이와 플레인 요거트를 섞은 뒤 소금으로 간을 한다.\r\n9. 8에 물을 붓고 다진 딜, 다진 마늘을 섞어 타라토르를 만든다.\r\n10. 그릇에 타라토르를 담고 잣, 다진 딜, 올리브 오일을 더한다.\r\n11. 핸드 블렌더에 바질, 올리브 오일, 잣을 넣고 갈아 바질 페스토를 만든다.\r\n12. 요거트 소스 위에 바질 페스토를 뿌린다.\r\n13. 접시에 구운 닭가슴살을 올려 완성한다.'),
	(156, '가지가지 한다', NULL, 1, '1. 가지를 길게 반으로 자른 뒤, 기름을 두른 팬에 굽는다.\r\n2. 구운 가지에 칼집을 낸 뒤 다시 굽는다.\r\n3. 냄비에 홀토마토, 마늘, 양파, 올리브 오일, 앤초비, 바질, 방울토마토를 넣고 끓인다.\r\n4. 끓인 재료들을 믹서에 넣고 갈아 토마토 소스를 만든다.\r\n5. 오븐팬에 구운 가지를 얹고 토마토 소스를 바른다.\r\n6. 말린 토마토와 올리브 오일을 섞은 뒤, 핸드 블렌더로 갈아준다.\r\n7. 6을 토마토 소스 위에 얹는다.\r\n8. 다진 페페론치노, 고르곤졸라 치즈, 모차렐라 치즈를 가지 위에 올린다.\r\n9. 예열된 오븐에 양념한 가지를 넣고 굽는다.\r\n10. 버터를 두른 팬에 불린 포르치니 버섯을 볶는다.\r\n11. 포르치니 버섯에 소금, 후추, 로즈마리를 넣어 함께 볶는다.\r\n12. 샐러드 채소에 레몬즙, 올리브 오일, 소금, 후추를 넣는다.\r\n13. 12에 볶은 포르치니 버섯을 넣고 버무린 뒤, 접시에 담는다.\r\n14. 방울토마토와 아스파라거스를 팬에 굽는다.\r\n15. 샐러드 위에 가지구이를 올린다.\r\n16. 구운 방울토마토와 아스파라거스를 곁들인다.\r\n17. 말린 토마토를 잘게 다져 올린다.'),
	(157, '명란 한주먹', NULL, 1, '1. 즉석밥을 전자레인지에 넣어 데운다.\r\n2. 다진 생강과 간장을 섞는다.\r\n3. 간장에 유자청의 유자를 다져 넣는다.\r\n4. 3에 식초, 다진 마늘, 올리브 오일, 다진 파, 설탕, 레몬즙을 섞어 간장소스를 만든다.\r\n5. 마른 팬에 잔멸치와 간장을 넣어 볶는다.\r\n6. 밥에 볶은 잔멸치를 넣고 섞는다.\r\n7. 6에 참기름, 명란젓을 넣고 섞는다.\r\n8. 채 썬 양파에 전분을 묻힌 뒤, 기름에 튀긴다.\r\n9. 간장소스에 후추와 들기름을 넣는다.\r\n10. 간장소스에 물을 넣는다.\r\n11. 동그랗게 주먹밥을 만든 뒤, 전분을 묻혀 기름에 튀긴다.\r\n12. 그릇에 튀긴 양파, 튀긴 주먹밥을 담는다.\r\n13. 주먹밥 위에 다진 견과류를 뿌린 뒤, 간장소스를 두른다.\r\n14. 채 썬 파를 올린다.\r\n15. 올리브 오일과 후추를 뿌려 완성한다.'),
	(158, '다 이 김', NULL, 1, '1. 김을 잘게 찢어 물에 불린다.\r\n2. 기름을 두른 팬에 다진 마늘, 채 썬 양파를 넣고 볶는다.\r\n3. 잘게 썬 파, 고춧가루, 맛술을 넣고 함께 볶는다.\r\n4. 참치액, 식초, 물에 불린 김과 물을 넣고 함께 끓인다.\r\n5. 참기름을 넣는다.\r\n6. 그릇에 김 수프와 밥을 따로 담아낸다.'),
	(159, '동파면', NULL, 1, '1. 청양고추는 잘게 썰고, 파는 채 썬 뒤 차가운 물에 담가둔다.\r\n2. 기름을 두른 팬에 으깬 마늘을 넣어 볶는다.\r\n3. 동치미에 들어있던 고추, 무를 잘게 썰어 넣고 함께 볶는다.\r\n4. 청양고추, 설탕을 넣어 함께 볶는다.\r\n5. 끓는 물에 소금을 넣은 뒤, 파스타면을 넣어 익힌다.\r\n6. 볶던 재료에 후추, 동치미 국물을 넣어 동치미 소스를 만든다.\r\n7. 찬물에 담가뒀던 파를 넣는다.\r\n8. 버터를 넣는다.\r\n9. 팬에 익힌 면을 넣고, 동치미 소스와 섞는다.\r\n10. 조미료를 넣는다.\r\n11. 그릇에 파스타를 담아 완성한다.'),
	(160, '커리업', NULL, 1, '1. 반건조 우럭은 껍질과 뼈를 제거한 뒤, 먹기 좋은 크기로 썬다.\r\n2. 자두를 전자레인지에 넣어 익힌다.\r\n3. 기름을 두른 팬에 다진 생강, 파, 손질한 우럭을 넣고 볶는다.\r\n4. 카레가루, 고춧가루를 넣고 함께 볶는다.\r\n5. 후추, 청주, 맛술을 넣어 끓인다.\r\n6. 카레에 익힌 자두를 넣는다.\r\n7. 두유를 넣고 함께 끓인다.\r\n8. 즉석밥을 전자레인지에 넣어 데운다.\r\n9. 카레에 버터를 넣는다.\r\n10. 파를 가늘게 채 썬 뒤, 찬물에 헹군다.\r\n11. 그릇에 밥을 담고 카레를 곁들인 뒤, 채 썬 파를 올려 완성한다.'),
	(161, '복면완자', NULL, 1, '1. 돼지안심과 양파를 잘게 썬다.\r\n2. 잘게 썬 돼지안심과 양파를 곱게 다진다.\r\n3. 두부를 으깬다.\r\n4. 끓는 물에 소면을 넣고 삶는다.\r\n5. 다진 돼지안심과 양파, 으깬 두부, 전분, 소금을 섞는다.\r\n6. 5에 달걀물을 넣어 반죽한다.\r\n7. 완자 반죽을 동그랗게 떼어 끓는 물에 넣고 익힌다.\r\n8. 삶은 소면을 찬물에 넣어 헹군다.\r\n9. 물에 고추장을 푼다.\r\n10. 9에 설탕, 우유를 넣어 물회 육수를 만든다.\r\n11. 기름을 두른 팬에 파, 으깬 마늘, 채 썬 당근, 양파를 넣고 볶는다.\r\n12. 그릇에 물기를 뺀 소면을 담고 얼음을 올린다.\r\n13. 소면 위에 익힌 완자를 올린다.\r\n14. 물회 육수를 붓는다.\r\n15. 볶은 채소를 올려 완성한다.'),
	(162, '돈 워리 비 해피', NULL, 1, '1. 식빵을 갈아서 빵가루를 만든다.\r\n2. 돼지안심을 적당한 크기로 썬 뒤, 블렌더로 갈아준다.\r\n3. 간 돼지안심과 리코타 치즈를 섞는다.\r\n4. 3에 소금, 후추를 뿌리고 섞은 뒤, 동그랗게 빚는다.\r\n5. 동그랗게 빚은 고기에 밀가루>달걀물>빵가루 순으로 묻힌다.\r\n6. 5에 스모크 치즈를 갈아 올린 뒤, 빵가루로 덮는다.\r\n7. 버터를 두른 팬에 돈가스를 올려 굽는다.\r\n8. 불린 표고버섯을 다진 뒤, 버터를 두른 팬에 넣고 볶는다.\r\n9. 팬에 다진 적양파, 즉석밥을 넣는다.\r\n10. 9에 얼그레이 티백을 우려낸 물을 넣고 끓인다.\r\n11. 화이트 소스, 스모크 치즈, 소금, 후추를 넣고 볶는다.\r\n12. 팬에 볶은 표고버섯을 넣고 스모크 치즈, 화이트 소스를 넣어 볶는다.\r\n13. 돈가스 위에 12를 얹어준다.\r\n14. 모차렐라 슬라이스 치즈를 얹는다.\r\n15. 리소토에 파르메산 치즈를 넣는다.\r\n16. 토치로 돈가스 위에 올린 치즈의 표면을 그을린다.\r\n17. 치즈 위에 바질을 뿌린다.\r\n18. 접시에 돈가스와 리소토를 올려 완성한다.'),
	(163, '연어가 똠얌꽁냥', NULL, 1, '1. 연어대가리에 소금을 뿌리고 토치로 살짝 익힌다.\r\n2. 끓는 물에 연어대가리를 넣고 삶는다.\r\n3. 끓는 물에 똠얌 페이스트를 넣고 끓인다.\r\n4. 3에 익힌 연어대가리, 소금을 넣고 끓인다.\r\n5. 국물에 카옌페퍼 가루, 연어볼, 다진 레몬그라스, 파프리카를 넣는다.\r\n6. 태국고추를 잘게 썰어 넣는다.\r\n7. 연유를 넣는다.\r\n8. 레몬즙을 넣는다.\r\n9. 적당한 크기로 썬 적양파, 다진 생강, 파를 넣고 끓인다.\r\n10. 연어살에 팟타이 페이스트를 바른 뒤, 버터를 두른 팬에 굽는다.\r\n11. 국물에 필리핀 라면과 수프를 넣고 끓인 뒤, 후추를 뿌린다.\r\n12. 그릇에 국물과 건더기를 담고 후추, 잘게 썬 파를 올려 완성한다.'),
	(164, '삼겹살 팟티', NULL, 1, '1. 불린 당면을 적당한 길이로 썬다.\r\n2. 간장을 넣고 끓인 물에 당면을 넣고 삶는다.\r\n3. 통삼겹살을 필러로 얇게 썬다.\r\n4. 샌드위치용 햄을 잘게 썬다.\r\n5. 당근, 양파를 채 썰고 마늘은 으깬다.\r\n6. 파와 고추를 얇게 채 썬다.\r\n7. 기름을 두른 팬에 똠얌 페이스트, 그린 커리 페이스트, 다진 레몬그라스를 넣고 볶는다.\r\n8. 7에 올리브 오일, 다진 생강을 넣고 볶아 양념을 만든다.\r\n9. 다른 팬에 기름을 두르고 으깬 마늘, 양파, 얇게 썬 삼겹살을 넣고 볶는다.\r\n10. 9에 샌드위치용 햄, 당근, 고추, 파를 넣는다.\r\n11. 그린 커리 페이스트, 볶아둔 양념을 넣고 함께 볶는다.\r\n12. 연유, 물을 붓고 볶는다.\r\n13. 태국고추를 잘게 다져 넣고 볶는다.\r\n14. 삶은 당면을 넣고 볶는다.\r\n15. 채 썬 두부를 넣고 함께 볶는다.'),
	(165, '돔 샤카라카', NULL, 1, '1. 끓는 물에 옥돔대가리, 파, 양파를 넣고 끓인다.\r\n2. 1에 옥돔뼈, 통후추를 넣고 끓여 육수를 낸다.\r\n3. 옥돔을 손질해 살만 발라낸다.\r\n4. 달걀 흰자만 분리해 거품을 낸다.\r\n5. 거품을 낸 달걀 흰자에 양파, 당근, 옥돔살, 토마토를 넣고 섞는다.\r\n6. 냄비에 옥돔 육수, 5를 넣어 함께 끓인다.\r\n7. 다진 마늘, 달걀 노른자, 레몬즙, 올리브 오일, 소금, 후추를 섞는다.\r\n8. 7에 다진 애플망고, 올리고당을 섞어 소스를 만든다.\r\n9. 소금, 후추 간을 한 옥돔살을 기름을 두른 팬에 굽는다.\r\n10. 달걀 흰자와 채소, 옥돔살을 넣고 끓인 육수를 체에 밭쳐 건더기를 거른 뒤, 옥돔뼈로 낸 육수와 합쳐 다시 끓인다.\r\n11. 그릇에 국물을 담고, 구운 옥돔살을 올린다.\r\n12. 잘게 썬 파, 소스를 올려 완성한다.'),
	(166, '베스트 오브 베스튜', NULL, 1, '1. 돼지고기를 큼직하게 썰어 소금, 후추 간을 한다.\r\n2. 돼지고기에 밀가루를 묻힌 뒤, 기름을 두른 팬에 굽는다.\r\n3. 압력 밥솥에 배추김치 국물, 애플망고, 소불고기 양념을 넣어 끓인다.\r\n4. 3에 표고버섯가루, 코코넛 워터를 넣어 끓인다.\r\n5. 돼지고기를 굽던 팬에 유자맛 소주를 넣어 잡내를 날린다.\r\n6. 구운 돼지고기를 압력솥에 넣어 익힌다.\r\n7. 팬에 배추김치 국물, 애플망고, 다진 배추김치를 넣고 끓인다.\r\n8. 7에 소불고기 양념, 후추를 넣고 끓여 소스를 만든다.\r\n9. 소스에 다진 토마토, 코코넛 워터를 넣고 끓인다.\r\n10. 익힌 돼지고기를 팬에 한 번 더 굽는다.\r\n11. 유자맛 소주를 부어 잡내를 날린다.\r\n12. 돼지고기에 소스를 부어 함께 끓인다.\r\n13. 따뜻하게 데운 그릇에 스튜를 담는다.\r\n14. 스튜에 깨를 뿌려 완성한다.'),
	(167, '화채 얼쑤', NULL, 1, '1. 오미자 원액, 칼라만시 원액, 코코넛 워터, 탄산수, 꿀을 섞어 화채국물을 만든다.\r\n2. 화채국물을 냉동실에 넣어둔다.\r\n3. 청포도를 얇게 썰고, 참외는 필러로 얇게 벗겨낸 뒤 채 썬다.\r\n4. 얼음물에 설탕, 레몬즙을 넣고, 손질한 과일을 담가둔다.\r\n5. 애플망고, 복숭아도 채 썰어 얼음물에 담가둔다.\r\n6. 옥수수 전분에 따뜻한 물을 넣고 익반죽을 한다.\r\n7. 반죽을 쟁반 바닥에 얇게 깔리게 부은 뒤, 쟁반을 끓는 물 위에 띄운다.\r\n8. 반죽의 표면에 물기가 없어지면 쟁반째 끓는 물 속에 넣어 완전히 익힌다.\r\n9. 반죽이 투명해지면 쟁반을 꺼내 얼음물에 담근다.\r\n10. 차갑게 식힌 반죽을 채 썰어 면을 만든다.\r\n11. 그릇에 채 썬 과일들을 담는다.\r\n12. 얼음을 갈아 그릇에 담고, 화채국물과 면을 넣는다.\r\n13. 피스타치오를 으깬 뒤 뿌려준다.'),
	(168, '오 마이 갓토', NULL, 1, '1. 끓는 물에 소금을 넣고 파스타면을 삶는다.\r\n2. 기름을 두른 팬에 미니 파프리카를 굽는다.\r\n3. 갈치는 얇게 포를 뜬 뒤, 팬에 넣어 굽는다.\r\n4. 토치로 미니 파프리카의 표면을 태운다.\r\n5. 믹서에 발사믹 식초와 쯔유를 넣는다.\r\n6. 미니 파프리카의 껍질을 벗긴 뒤, 믹서에 넣는다.\r\n7. 믹서에 으깬 마늘, 다진 양파를 넣고 함께 갈아 소스를 만든다.\r\n8. 소스에 참기름을 넣고 다시 한 번 갈아준다.\r\n9. 삶은 파스타면을 찬물에 헹군다.\r\n10. 파스타면과 소스, 잘게 썬 파를 함께 버무린다.\r\n11. 접시에 양념한 파스타면을 담고 구운 갈치를 올린다.\r\n12. 물에 씻은 갓김치와 낫토를 섞은 뒤, 갈치 위에 올린다.\r\n13. 소스를 뿌린다.\r\n14. 잘게 썬 파, 김을 올린다.\r\n15. 달걀 노른자를 올린다.'),
	(169, '갈쌈만두', NULL, 1, '1. 밀가루에 소금, 따뜻한 물을 넣어 익반죽을 한다.\r\n2. 내장을 제거한 갈치를 익힌다.\r\n3. 그린빈을 잘게 다진 뒤, 물기를 제거한다.\r\n4. 표고버섯, 양파, 파를 잘게 다진다.\r\n5. 다진 재료들에 쯔유, 식용유를 넣고 섞는다.\r\n6. 5에 갈치의 살만 발라 넣고 섞어 만두소를 만든다.\r\n7. 반죽을 적당한 크기로 나눈 뒤, 밀대로 밀어 만두피를 만든다.\r\n8. 만두피에 만두소를 넣고 빚는다.\r\n9. 기름을 두른 팬에 만두를 넣고 굽는다.\r\n10. 팬에 물을 붓고 뚜껑을 덮어 만두 속까지 익혀준다.'),
	(170, '판타스틱 어묵', NULL, 1, '1. 오징어를 잘게 썰어 핸드 블렌더에 넣는다.\r\n2. 1에 푸아그라 트러플 파테, 레몬&소금이 들어간 고춧가루, 소금, 후추, 고춧가루, 깨를 넣어 간다.\r\n3. 달걀 껍질을 반으로 가른다.\r\n4. 반으로 가른 달걀 껍질에 어묵반죽을 담는다.\r\n5. 어묵반죽 안에 각각 명란젓, 카망베르 치즈, 푸아그라 트러플 파테를 넣어 속을 채운다.\r\n6. 접시에 밀가루를 깔고, 어묵반죽을 넣은 달걀 껍질을 세운 뒤 전자레인지에 넣고 돌린다.\r\n7. 팬에 기름과 버터를 두른 뒤, 어묵반죽을 넣고 익힌다.\r\n8. 접시에 어묵을 담고 첫 번째 어묵 위에 캐비아, 채 썬 파를 올린다.\r\n9. 두 번째 어묵에는 푸아그라 트러플 파테, 딸기잼을 올린다.\r\n10. 세 번째 어묵에는 트러플과 꿀, 카망베르 치즈를 올린다.'),
	(171, '분짜지용', NULL, 1, '1. 얇게 썬 삼겹살에 고추, 양파, 다진 마늘, 레몬즙, 피시소스를 넣는다.\r\n2. 1에 설탕을 넣고 버무린다.\r\n3. 팬에 설탕을 얇게 깔고 녹여 캐러멜화한다.\r\n4. 3에 양념한 삼겹살을 넣고 굽는다.\r\n5. 볼에 피시소스, 설탕, 레몬즙, 다진 마늘, 다진 양파, 고추를 넣는다.\r\n6. 5에 물을 부어 육수를 만든다.\r\n7. 끓는 물에 메밀 라면을 넣고 삶는다.\r\n8. 찬물에 헹군 면을 접시에 담는다.\r\n9. 면에 구운 삼겹살을 곁들인다.\r\n10. 육수를 따로 담아낸다.'),
	(172, '양싸퀴진', NULL, 1, '1. 장어흑마늘즙을 끓인다.\r\n2. 삼겹살에 소금, 후추 간을 하고 팬에 굽는다.\r\n3. 장어흑마늘즙에 다진 생강, 설탕, 소금, 후추를 넣고 끓여 소스를 만든다.\r\n4. 전복을 손질한다.\r\n5. 끓는 물에 소금을 넣고 전복을 삶는다.\r\n6. 팬에 그린빈과 채 썬 능이버섯을 넣고 함께 굽는다.\r\n7. 소스에 삶은 전복을 넣고, 소스를 끼얹어가며 조린다.\r\n8. 소스에 트러플을 잘게 다져 넣고 함께 끓인다.\r\n9. 접시에 소스를 담고 능이버섯과 그린빈을 올린다.\r\n10. 구운 삼겹살과 전복, 트러플을 올린다.\r\n11. 올리브 오일을 뿌려 완성한다.'),
	(173, '텐미닛 수프', NULL, 1, '1. 꼬리곰탕을 체에 걸러 냄비에 넣고 끓인다.\r\n2. 버터에 밀가루를 넣고 볶아 루를 만든다.\r\n3. 팬에 기름을 두르고 채 썬 양파를 볶는다.\r\n4. 꼬리곰탕에 알감자조림을 넣고 끓인다.\r\n5. 양파를 볶던 팬에 소시지를 넣고, 후추를 뿌려 굽는다.\r\n6. 꼬리곰탕에서 건져낸 알감자를 팬에 넣고 함께 볶는다.\r\n7. 팬에 꼬리곰탕과 크림 치즈를 넣고 끓인다.\r\n8. 7에 루를 넣어 농도를 조절한다.\r\n9. 끓인 재료들을 믹서에 넣고 꼬리곰탕을 조금 부은 뒤 갈아준다.\r\n10. 그릇에 망고푸딩을 담고 잘게 썬 크랜베리를 올린다.\r\n11. 믹서에 간 수프를 체에 밭쳐 곱게 내린다.\r\n12. 따뜻하게 데운 주전자에 수프를 담은 뒤, 망고푸딩과 함께 낸다.'),
	(174, '10분 스테이크 맛있찬오', NULL, 1, '1. 소고기 안심에 소금, 후추 간을 한다.\r\n2. 팬에 올리브 오일을 넉넉하게 두르고 소고기 안심, 대파, 햄을 넣고 굽는다.\r\n3. 다진 마늘, 진간장, 꿀, 홀그레인 머스터드, 참기름을 섞어 소스를 만든다.\r\n4. 알감자조림에 카망베르 치즈, 꿀, 소스를 넣고 섞는다.\r\n5. 그릇에 4를 담는다.\r\n6. 안심 스테이크 위에 구운 파, 소스를 올린다.\r\n7. 그릇에 안심 스테이크를 담고 소스를 뿌려 완성한다.'),
	(175, '영광의 바지락', NULL, 1, '1. 냄비에 올리브 오일을 두르고 통마늘, 대파, 당근, 양파, 감자, 통후추를 넣은 뒤 물을 부어 끓인다.\n2. 팬에 올리브 오일을 두르고 으깬 마늘, 바지락을 넣어 볶는다.\n3. 2에 화이트 와인과 채소육수를 부어 끓인다.\n4. 양파, 감자, 대파를 잘게 다진다.\n5. 기름을 두른 팬에 햄을 굽는다.\n6. 5에 다진 재료들을 넣고 소금, 후추 간을 해서 함께 볶는다.\n7. 구운 햄을 꺼낸다.\n8. 채소를 볶던 팬에 바지락을 넣고 함께 볶는다.\n9. 팬에 버터를 두르고 밀가루를 볶아 루를 만든다.\n10. 루와 채소육수를 섞은 뒤, 채소와 바지락을 볶던 팬에 부어 함께 끓인다.\n11. 10에 우유를 넣고 끓인다.\n12. 마늘과 함께 볶아둔 바지락을 넣은 뒤 더 끓여준다.\n13. 잘게 썬 전복을 굽는다.\n14. 빵을 굽는다.\n15. 그릇에 바지락 차우더를 담고 구운 전복과 빵을 올려 낸다.'),
	(176, '파워 비어 롤', NULL, 1, '1. 소고기 안심을 고기망치로 두드려 얇게 편다.\r\n2. 1에 소금, 후추로 간을 한다.\r\n3. 얇게 펼친 소고기 안심에 카망베르 치즈, 말린 크랜베리, 크림 치즈를 넣고 만다.\r\n4. 안심롤을 이쑤시개로 고정한 후, 기름을 두른 팬에 통마늘과 함께 굽는다.\r\n5. 구운 통마늘을 꺼낸다.\r\n6. 햄을 채 썰어 팬에 볶는다.\r\n7. 팬에 설탕을 뿌린다.\r\n8. 올리브 오일과 밀가루를 넣어 함께 볶는다.\r\n9. 흑맥주를 붓고 끓여 소스를 만든다.\r\n10. 소스에 월계수잎과 꿀을 넣는다.\r\n11. 알감자조림을 전자레인지에 넣어 데운다.\r\n12. 안심롤을 먹기 좋은 크기로 썰어 그릇에 담는다.\r\n13. 소스에 버터를 넣고 녹인 뒤, 안심롤 위에 뿌린다.\r\n14. 데운 알감자조림을 가니쉬로 곁들인다.'),
	(177, '새로 깐풍치킨', NULL, 1, '1. 프라이드 치킨의 튀김옷 부분을 벗겨낸다.\r\n2. 튀김옷을 벗겨낸 프라이드 치킨에 밀가루를 묻혀 다시 튀긴다.\r\n3. 채 썬 양파와 다진 마늘을 섞는다.\r\n4. 3에 설탕, 간장, 물, 식초, 굴소스를 넣어 깐풍소스를 만든다.\r\n5. 깐풍소스에 후추, 레몬즙을 넣는다.\r\n6. 파와 청양고추를 잘게 썰어 기름을 두른 팬에 볶는다.\r\n7. 튀긴 프라이드 치킨의 기름을 뺀다.\r\n8. 그릇에 프라이드 치킨을 담고 볶은 파, 청양고추를 올린다.\r\n9. 프라이드 치킨 위에 깐풍소스를 올려 완성한다.'),
	(178, '피자 말아또', NULL, 1, '1. 다진 파, 다진 마늘, 진간장, 설탕, 참기름을 섞어 양념을 만든다.\r\n2. 불고기를 양념에 버무린 뒤, 오레가노를 넣어 섞는다.\r\n3. 피자조각의 끝부분을 잘라내고 반을 자른 뒤, 네모난 모양으로 합친다.\r\n4. 달걀물에 소금을 넣는다.\r\n5. 버터를 두른 팬에 달걀지단을 부친다.\r\n6. 달걀지단의 한쪽 면만 익힌 뒤, 위에 피자를 올린다.\r\n7. 양파와 파를 잘게 썰어 팬에 넣고 소금, 후추 간을 해서 볶는다.\r\n8. 7에 잘게 썬 살라미, 버터를 넣고 함께 볶는다.\r\n9. 피자 위에 볶은 재료들을 올린다.\r\n10. 불고기를 팬에 볶는다.\r\n11. 볶은 불고기를 피자 위에 올린 뒤, 달걀지단으로 돌돌 말아준다.\r\n12. 피자달걀말이를 그릴팬에 올려 굽는다.\r\n13. 피자달걀말이를 썰어 접시에 담아낸다.'),
	(179, '후룩국', NULL, 1, '1. 끓는 물에 소금을 넣은 뒤, 파르팔레(리본모양 파스타)를 넣고 삶는다.\r\n2. 믹서기에 양파, 파, 소금, 다진 마늘, 다진 생강, 식용유, 고체카레를 넣고 간다.\r\n3. 항정살을 적당한 크기로 썰어 팬에 넣고 볶는다.\r\n4. 믹서에 간 재료들을 항정살과 함께 볶는다.\r\n5. 쇠젓가락에 파를 꽂아 불에 직접 굽는다.\r\n6. 볶던 재료에 물을 부어 함께 끓인다.\r\n7. 6에 삶은 파르팔레를 넣고 소금으로 간을 한다.\r\n8. 라임즙을 넣는다.\r\n9. 그릇에 건더기와 국물을 담고 구운 파를 올려 완성한다.'),
	(180, '고새 한 그릇', NULL, 1, '1. 냄비에 마늘, 파, 다진 생강, 양파를 넣고 볶는다.\r\n2. 다른 냄비에 물과 치킨스톡을 넣고 끓여 육수를 낸다.\r\n3. 볶은 채소에 육수를 넣어 함께 끓인다.\r\n4. 칵테일 새우를 믹서에 넣고 간다.\r\n5. 육수를 체에 걸러 맑은 육수만 받아낸다.\r\n6. 다진 칵테일 새우에 달걀 흰자, 소금, 후추, 전분을 넣어 완자 반죽을 만든다.\r\n7. 완자 반죽을 먹기 좋은 크기로 떼어 육수에 넣고 익힌다.\r\n8. 육수에 칵테일 새우를 넣고 익힌다.\r\n9. 8에 전분물을 넣어 농도를 조절한 뒤, 소금으로 간을 한다.\r\n10. 체에 다진 마늘, 다진 생강, 고춧가루를 올린 뒤 끓인 식용유를 부어 고추기름을 낸다.\r\n11. 국물에 달걀물을 풀어준다.\r\n12. 그릇에 새우완자와 국물을 담고 고추기름, 잘게 썬 파를 올려 낸다.'),
	(181, '야밤엔 복쌈', NULL, 1, '1. 기름을 두른 팬에 잘게 자른 현미국수면을 넣어 튀긴다.\r\n2. 튀긴 면을 체에 밭쳐 기름을 뺀다.\r\n3. 양배추의 심을 잘게 다진다.\r\n4. 채끝살을 잘게 다진다.\r\n5. 고추, 아스파라거스, 파, 마늘, 불린 표고버섯을 잘게 다진다.\r\n6. 팬에 다진 파와 마늘, 채끝살을 넣은 뒤 간장을 넣어 볶는다.\r\n7. 6에 나머지 채소, 쯔유, 해선장을 넣고 볶는다.\r\n8. 참기름을 넣는다.\r\n9. 물전분을 넣은 뒤 후추를 넣는다.\r\n10. 접시에 튀긴 면을 담는다.\r\n11. 면 위에 볶은 재료들을 얹는다.\r\n12. 양배추 잎을 곁들여 낸다.'),
	(182, '나물 버물 파스타', NULL, 1, '1. 물에 소금을 넣고 끓인다.\r\n2. 토마토를 잘게 썬다.\r\n3. 팬에 올리브 오일을 두른 뒤, 편으로 썬 마늘과 소금을 넣고 볶는다.\r\n4. 3에 잘게 썬 새우살을 넣고 볶는다.\r\n5. 미니 파프리카의 겉면을 태운다.\r\n6. 소금을 넣고 끓인 물에 파스타면을 삶는다.\r\n7. 볶던 재료에 화이트 와인을 넣는다.\r\n8. 7에 면수와 잘게 썬 토마토를 넣어 볶는다.\r\n9. 올리브 오일을 두른 팬에 다진 마늘, 삶은 고구마 줄기를 넣고 볶는다.\r\n10. 9에 소금, 아욱을 넣고 함께 볶는다.\r\n11. 겉면을 태운 미니 파프리카의 껍질을 벗긴다.\r\n12. 미니 파프리카를 잘게 썬다.\r\n13. 아스파라거스를 잘게 썰어 끓는 물에 살짝 데친다.\r\n14. 팬에 삶은 면과 데친 아스파라거스, 볶은 나물들을 넣고 볶는다.\r\n15. 잘게 썬 미니 파프리카를 파스타에 넣는다.\r\n16. 파스타에 소금 간을 한다.\r\n17. 파스타에 루꼴라를 넣어 섞는다.\r\n18. 접시에 파스타를 담아낸다.'),
	(183, '스윗 펌킨', NULL, 1, '1. 껍질을 벗긴 단호박을 강판을 이용해 채 썬다.\r\n2. 채 썬 단호박에 올리브 오일을 넣고 볶는다.\r\n3. 다진 호두, 코코넛 오일, 버터, 채 썬 사과, 건포도를 넣고 함께 볶는다.\r\n4. 3에 설탕을 넣어 볶는다.\r\n5. 따뜻한 물에 라이스페이퍼를 담가 부드럽게 만든다.\r\n6. 라이스페이퍼에 볶은 재료를 얹고 모양을 잡아 단호박쌈을 만든다.\r\n7. 단호박쌈 위에 설탕을 뿌린 뒤, 오븐에 넣어 굽는다.\r\n8. 핸드 블렌더에 전분과 설탕을 넣고 갈아 슈거파우더를 만든다.\r\n9. 단호박쌈 위에 슈거파우더를 뿌린다.\r\n10. 각종 베리를 얹어 완성한다.'),
	(184, '해물 달라고 졸라', NULL, 1, '1. 버터를 전자레인지에 넣어 녹인다.\r\n2. 새우의 머리와 껍질을 제거한다.\r\n3. 녹인 버터의 표면에 떠오른 거품과 불순물을 걷어낸다.\r\n4. 정제 버터(맑은 액체 부분)를 팬에 두른 뒤 다진 마늘, 새우 머리를 넣고 볶는다.\r\n5. 새우의 향이 버터에 배면 새우 머리를 꺼낸다.\r\n6. 팬에 가지, 양송이버섯, 새우살, 고르곤졸라 치즈를 넣고 볶는다.\r\n7. 해물모듬을 넣고 볶는다.\r\n8. 화이트 와인, 으깬 마늘, 소금을 넣고 볶는다.\r\n9. 사과는 채 썰고, 토르티야는 적당한 크기로 썬다.\r\n10. 접시에 토르티야와 채 썬 사과를 담고, 볶은 해물은 따로 담아낸다.\r\n11. 볶은 해물에 후추를 뿌려 완성한다.'),
	(185, '낭만 시래기', NULL, 1, '1. 전복 내장에 맛술과 청주를 넣고 데친다.\r\n2. 믹서에 데친 전복 내장, 간장, 식초를 넣고 갈아준다.\r\n3. 으깬 마늘, 채 썬 파, 다진 생강, 고춧가루, 식용유를 섞는다.\r\n4. 3을 전자레인지에 넣고 돌려 고추기름을 만든다.\r\n5. 시래기, 양파, 고추를 잘게 썬다.\r\n6. 5에 다진 마늘을 섞은 뒤, 팬에 넣고 볶는다.\r\n7. 가늘게 채 썬 파와 간장을 넣고 함께 볶는다.\r\n8. 믹서에 간 전복내장소스를 체에 밭쳐 거른다.\r\n9. 볶던 시래기에 간장과 소금을 넣는다.\r\n10. 전복내장소스에 고추기름, 참기름을 넣고 섞는다.\r\n11. 밥 위에 볶은 시래기를 올리고 참깨를 뿌린다\r\n12. 시래기 덮밥과 전복내장소스를 함께 낸다.'),
	(186, '낙엽 두부', NULL, 1, '1. 우유를 끓인다.\r\n2. 찐 고구마의 껍질을 벗기고 으깬 뒤, 우유에 넣는다.\r\n3. 2에 크림치즈를 넣고 함께 끓인다.\r\n4. 끓이던 재료에 소금, 후추, 올리브 오일을 넣은 뒤 믹서에 넣고 간다.\r\n5. 기름을 두른 팬에 길게 썬 두부를 굽는다.\r\n6. 5에 잘게 썬 파와 양파를 넣는다.\r\n7. 팬에 물을 붓고 간장, 고춧가루를 넣는다.\r\n8. 7에 올리고당, 참기름, 다진 마늘을 넣고 조린다.\r\n9. 기름을 두른 팬에 잘게 썬 양파와 파를 넣고 볶는다.\r\n10. 잘게 썬 채끝 등심을 팬에 넣고 함께 볶는다.\r\n11. 잘게 썬 고추를 넣고 후추로 간을 한다.\r\n12. 두부를 조리던 팬에 달걀노른자를 넣어 반숙으로 익힌다.\r\n13. 조린 두부와 볶은 채끝등심을 번갈아 가며 쌓는다.\r\n14. 13의 위에 반숙으로 익힌 달걀노른자를 얹는다.\r\n15. 접시에 고구마크림소스를 바르고 층층이 쌓은 두부와 채끝등심을 얹는다.\r\n16. 두부조림소스를 뿌려 마무리한다.'),
	(187, '쑥테이크', NULL, 1, '1. 볶은 땅콩가루와 크림치즈를 섞는다.\r\n2. 쑥, 잣, 파르메산 치즈, 마늘, 고추, 올리브 오일, 소금, 후추를 함께 갈아 쑥페스토를 만든다.\r\n3. 물에 불린 표고버섯의 기둥을 떼고, 안쪽 부분에 땅콩크림치즈를 넣어 채운다.\r\n4. 땅콩크림치즈 위에 쑥페스토를 바른다.\r\n5. 기름을 두른 팬에 속을 채운 표고버섯을 굽는다.\r\n6. 다른 팬에 기름을 두르고 으깬 마늘, 고춧가루를 넣는다.\r\n7. 6에 소금, 후추 간을 한 채끝등심을 넣고 버터를 끼얹어가며 굽는다.\r\n8. 구운 표고버섯에 파르메산 치즈를 뿌린 뒤, 토치를 이용해 표면을 굽는다.\r\n9. 그릇에 구운 채끝등심과 표고버섯을 담는다.\r\n10. 쑥페스토에 설탕, 파르메산 치즈를 넣고 섞는다.\r\n11. 쑥페스토 소스를 곁들인다.'),
	(188, '최면육회', NULL, 1, '1. 채끝등심을 고기망치로 두드려 얇게 편다.\r\n2. 명란젓을 물에 씻은 뒤 속만 발라낸다.\r\n3. 명란젓과 크림치즈를 섞은 뒤 후추를 뿌린다.\r\n4. 얇게 편 채끝등심을 원형틀을 이용해 동그랗게 찍어낸다.\r\n5. 동그란 채끝등심 위에 명란크림치즈를 올린 뒤 반으로 접어 붙인다.\r\n6. 끓는 물에 소금을 넣고 소면을 삶는다.\r\n7. 땅콩소스, 올리브 오일, 간장을 섞어 소스를 만든다.\r\n8. 삶은 소면을 찬물에 헹군다.\r\n9. 사과를 잘게 채 썬다.\r\n10. 물기를 제거한 소면에 올리브 오일, 후추, 만들어 둔 소스를 넣고 섞는다.\r\n11. 접시에 양념한 면과 채 썬 사과, 명란크림치즈가 들어간 채끝등심을 올린다.'),
	(189, '터치 마이 주꾸미', NULL, 1, '1. 주꾸미에 소금을 뿌리고 문질러 깨끗하게 씻는다.\r\n2. 유자청에 레몬즙을 짜 넣는다.\r\n3. 즙을 짜고 난 레몬을 끓는 물에 넣는다.\r\n4. 레몬을 넣고 끓인 물에 주꾸미를 데친다.\r\n5. 데친 주꾸미를 얼음에 넣고 식힌다.\r\n6. 완두콩 통조림을 체에 밭쳐 물기를 빼고 냄비에 넣어 우유와 함께 끓인다.\r\n7. 시금치 무침을 물에 헹궈 6에 넣고 함께 끓인다.\r\n8. 끓인 우유와 완두콩, 시금치를 핸드블렌더에 넣고 갈아 퓌레를 만든다.\r\n9. 퓌레를 체에 밭쳐 내린다.\r\n10. 레몬즙을 넣은 유자청을 핸드블렌더로 간다.\r\n11. 양파장아찌를 잘게 다진다.\r\n12. 핸드블렌더로 간 유자청에 올리브 오일, 소금, 다진 양파장아찌를 넣어 드레싱을 만든다.\r\n13. 차게 식힌 주꾸미를 적당한 크기로 썰어 꿀, 후추와 함께 버무린다.\r\n14. 접시에 그린 퓌레를 얇게 깐다.\r\n15. 그린 퓌레 위에 주꾸미를 올린다.\r\n16. 주꾸미 위에 드레싱을 뿌리고 레몬껍질을 갈아 올려 완성한다.'),
	(190, '리소토 먹어보라', NULL, 1, '1. 당근, 양파, 대파를 썰어 넣고 채소육수를 만든다.\r\n2. 채소육수에 단호박과 손질한 단호박 껍질을 넣고 끓인다.\r\n3. 팬에 블루베리즙과 설탕을 넣고 졸여 소스를 만든다.\r\n4. 기름을 두른 팬에 다진 양파와 잘게 썬 단호박을 넣고 볶는다.\r\n5. 팬에 채소육수를 부어 끓인다.\r\n6. 채소육수를 불에서 내리고 불린 강낭콩을 데친다.\r\n7. 파, 마늘을 다지고 살치살을 잘게 썬다.\r\n8. 올리브 오일을 두른 팬에 파와 마늘을 볶는다.\r\n9. 파, 마늘을 볶은 팬에 잘게 썬 살치살, 데친 강낭콩, 소금, 후추를 넣고 볶는다.\r\n10. 채소육수를 부어 끓인 양파와 단호박을 핸드블렌더로 갈아 단호박 육수를 만든다.\r\n11. 볶은 살치살과 강낭콩에 블루베리 소스를 넣고 섞는다.\r\n12. 기름을 두른 팬에 으깬 마늘, 잘게 썬 살치살, 즉석밥을 넣고 볶는다.\r\n13. 채소육수에 넣고 삶은 단호박을 잘게 썰어 12에 넣고 단호박 육수를 붓고 끓여 리소토를 만든다.\r\n14. 리소토에 소금간을 하고 파르메산 치즈를 넣는다.\r\n15. 리소토에 버터를 넣는다.\r\n16. 파르메산 치즈를 넣어 농도를 조절한다.\r\n17. 체다&모차렐라 치즈를 넣는다.\r\n18. 그릇에 리소토를 담는다.\r\n19. 리소토 위에 블루베리 소스를 넣은 살치살과 강낭콩을 올려 완성한다.'),
	(191, '기세등등갈비', NULL, 1, '1. 등갈비에 간장, 설탕, 청주, 후추를 넣고 양념한다.\r\n2. 대파는 적당한 크기로 썰고, 마늘은 으깨어 양념한 등갈비와 섞는다.\r\n3. 불린 찹쌀을 체에 밭쳐 물기를 빼고 핸드블렌더로 간다.\r\n4. 핸드블렌더로 간 찹쌀에 소금간을 한다.\r\n5. 4에 고춧가루를 넣고 섞는다.\r\n6. 간을 한 찹쌀에 양념한 등갈비를 넣고 버무린다.\r\n7. 찜기에 찹쌀을 입힌 등갈비를 넣고 찐다.\r\n8. 양파, 오이를 채 썰고 마늘은 다져서 섞는다.\r\n9. 섞은 채소에 물, 식초, 간장, 설탕을 2:2:1:1의 비율로 섞어 소스를 만든다.\r\n10. 소스에 레몬즙과 참기름을 더한다.\r\n11. 10에 후추와 소금을 넣는다.\r\n12. 양념한 채소와 등갈비를 각각 담아낸다.'),
	(192, '등갈빈', NULL, 1, '1. 등갈비에서 살을 반 정도 분리해 손질한다.\r\n2. 떼어낸 등갈비살에 이쑤시개를 꽂아 고정한다.\r\n3. 등갈비에 올리브 오일, 후추, 간장, 소금으로 양념한다.\r\n4. 양념한 등갈비를 그릴팬에 굽는다.\r\n5. 김밥용 햄과 고추, 마늘을 팬에 넣고 함께 굽는다.\r\n6. 강낭콩 통조림을 체에 밭쳐 물기를 뺀다.\r\n7. 물기를 뺀 강낭콩 통조림에 옥수수 통조림을 넣는다.\r\n8. 7에 양파, 부추, 오이장아찌를 다져 넣어 샐러드를 만든다.\r\n9. 샐러드에 등갈비 양념, 케첩, 소금, 후추를 넣어 양념한다.\r\n10. 양념한 샐러드에 구운 고추와 김밥용 햄을 잘게 잘라 넣고 섞는다.\r\n11. 원형틀을 이용해 접시에 샐러드를 담는다.\r\n12. 버터를 전자레인지에 넣고 녹인다.\r\n13. 녹인 버터, 올리브 오일, 레몬즙을 섞어 소스를 만든다.\r\n14. 14에에 핫소스를 넣는다.\r\n15. 접시에 등갈비를 담고 다진 부추와 소스를 곁들인다.'),
	(193, '짱짱면', NULL, 1, '1. 대파, 양파를 잘게 썬다.\r\n2. 냉이를 잘게 썬다.\r\n3. 삼겹살을 잘게 썬다.\r\n4. 창난젓을 잘게 썬다.\r\n5. 기름을 두른 팬에 대파, 다진 마늘, 간장을 넣고 볶는다.\r\n6. 5에 된장과 꿀을 넣고 함께 볶는다.\r\n7. 끓는 물에 탄탄면 면을 넣고 삶는다.\r\n8. 기름을 두른 팬에 잘게 썬 삼겹살, 다진 마늘을 넣고 볶는다.\r\n9. 8에 소주를 넣어 볶는다.\r\n10. 삼겹살을 볶던 팬에 다진 양파, 대파, 창난젓을 넣어 함께 볶는다.\r\n11. 10에 볶아둔 된장을 넣고 섞는다.\r\n12. 잘게 썬 냉이를 넣고 함께 볶는다.\r\n13. 전분물을 넣어 농도를 조절한다.\r\n14. 찬물에 헹군 면을 그릇에 담는다.\r\n15. 면 위에 유니된장짜장 소스를 붓는다.\r\n16. 깨를 뿌려 완성한다.'),
	(194, '리소토 위~아래 곱창', NULL, 1, '1. 삼겹살에 소금 간을 해서 굽는다.\r\n2. 기름을 두른 팬에 양파, 대파를 잘게 썰어 넣고 볶는다.\r\n3. 양념곱창을 물에 헹군 뒤 잘게 썬다.\r\n4. 양파와 대파를 볶던 팬에 잘게 썬 곱창을 넣고 함께 볶는다.\r\n5. 4에 다진 마늘을 넣는다.\r\n6. 볶던 재료에 사골육수를 넣고 끓인다.\r\n7. 6에 데우지 않은 즉석밥을 넣고 함께 끓인다.\r\n8. 소금, 후추로 간을 한다.\r\n9. 삼겹살에 후추를 뿌린 뒤 다진 마늘, 레몬청, 버터를 넣어 굽는다.\r\n10. 버터를 두른 팬에 달걀물을 부어 스크램블 에그를 만든다.\r\n11. 리소토에 잘게 썬 대파를 넣는다.\r\n12. 리소토에 참기름을 넣는다.\r\n13. 원형틀을 이용해 접시에 리소토를 담는다.\r\n14. 리소토 위에 스크램블 에그를 올린다.\r\n15. 스크램블 에그 위에 구운 삼겹살을 올린다.\r\n16. 잘게 썬 대파를 올려 완성한다.'),
	(195, '풍기명란 토스트', NULL, 1, '1. 칼등을 이용해 명란젓의 속만 발라낸다.\r\n2. 팬에 명란젓과 달걀노른자를 넣는다.\r\n3. 2에 밀가루와 우유를 넣고, 뭉치지 않게 저어주며 끓여 명란 커스터드를 만든다.\r\n4. 명란 커스터드에 잘게 다진 양파, 청양고추를 넣고 섞는다.\r\n5. 편으로 썬 마늘을 기름에 튀긴다.\r\n6. 튀긴 마늘을 건져 기름기를 뺀다.\r\n7. 명란 커스터드에 소금으로 간을 한다.\r\n8. 식빵을 토스트기에 넣고 굽는다.\r\n9. 명란 커스터드에 마요네즈를 섞는다.\r\n10. 식빵에 명란마요 커스터드, 튀긴 마늘을 올린다.'),
	(196, '창난하니?', NULL, 1, '1. 삼겹살을 손질한 뒤, 얇게 썬다.\r\n2. 얇게 썬 삼겹살에 소금, 후추 간을 해서 굽는다.\r\n3. 대파를 적당한 크기로 썰어 삼겹살과 함께 굽는다.\r\n4. 창난젓을 물에 살짝 헹군다.\r\n5. 물에 헹군 창난젓을 삼겹살과 함께 볶는다.\r\n6. 5에 다진 마늘, 청주를 넣는다.\r\n7. 볶은 재료들에 물, 간장을 넣고 끓인다.\r\n8. 대파의 흰 부분을 가늘게 채 썰어 물에 담가둔다.\r\n9. 소금을 넣고 끓인 물에 탄탄면 면을 넣고 삶는다.\r\n10. 삶은 면을 얼음물에 넣고 헹군다.\r\n11. 국물에 참기름을 넣는다.\r\n12. 그릇에 면을 담고 국물을 부은 뒤, 채 썬 대파를 올려 완성한다.'),
	(197, '입에 불레리나', NULL, 1, '1. 물에 양파, 레몬, 대파를 넣고 끓여 채소육수를 만든다.\r\n2. 1에 손질한 전복을 넣고 삶는다.\r\n3. 채소육수 파우더를 넣는다.\r\n4. 두부의 물기를 제거하고 으깬 뒤, 마른 팬에 넣고 볶는다.\r\n5. 두부에 강황가루를 넣어 함께 볶는다.\r\n6. 낙지와 새우를 잘게 썬다.\r\n7. 기름을 두른 팬에 다진 마늘과 양파, 낙지, 새우를 넣고 볶는다.\r\n8. 화이트 와인, 채소육수 파우더, 고춧가루를 넣는다.\r\n9. 다진 청양고추, 핫소스를 넣는다.\r\n10. 와사비를 넣어 함께 볶는다.\r\n11. 채소육수와 꿀, 간장, 강황가루를 넣고 볶는다.\r\n12. 달걀물을 넣어 스크램블을 만든 뒤 섞어준다.\r\n13. 접시에 삶은 전복과 볶은 두부를 올린다.\r\n14. 두부 위에 볶은 재료들을 올려 완성한다.'),
	(198, '불새닭', NULL, 1, '1. 기름을 두른 팬에 대파를 넣고 볶는다.\r\n2. 닭가슴살은 포를 떠서 소금으로 밑간을 한다.\r\n3. 대파와 닭가슴살을 함께 볶는다.\r\n4. 3에 새우를 넣고 함께 볶는다.\r\n5. 석쇠에 청양고추를 넣고 굽는다.\r\n6. 양파를 잘게 다진다.\r\n7. 구운 청양고추의 탄 부분을 살짝 벗겨내고 복숭아잼과 섞은 뒤 함께 갈아준다.\r\n8. 7에 마요네즈, 간장, 설탕을 넣고 갈아 소스를 만든다.\r\n9. 다진 양파와 소스를 섞는다.\r\n10. 월남쌈용 채소를 적당한 크기로 썬다.\r\n11. 새우는 잘게 썰고, 닭가슴살은 잘게 찢어 놓는다.\r\n12. 새우와 닭가슴살을 채소와 섞은 뒤, 소스에 버무린다.\r\n13. 올리브 오일을 두른 팬에 빵을 굽는다.\r\n14. 구운 빵 위에 채 썬 양파, 소스에 버무린 재료들을 올린다.\r\n15. 채 썬 깻잎과 청양고추를 올려 완성한다.'),
	(199, '유 캔 플라이롤', NULL, 1, '1. 끓는 물에 소금을 넣고 펜네를 삶는다.\r\n2. 연어를 얇게 썬다.\r\n3. 바나나를 얇게 썰어 팬에 굽는다.\r\n4. 아보카도는 껍질을 벗기고 잘게 썬다.\r\n5. 김발에 비닐을 깔고 연어를 올린 뒤, 잘게 썬 아보카도를 올린다.\r\n6. 케일과 오이를 잘게 썰어 연어 위에 올린다.\r\n7. 구운 바나나를 올리고 후추를 뿌린 뒤, 돌돌 말아 연어롤을 만든다.\r\n8. 연어롤을 냉동실에 넣어둔다.\r\n9. 플레인 요거트, 블루베리잼, 딸기잼, 레몬즙을 섞어 소스를 만든다.\r\n10. 요거트 소스를 체에 밭쳐 곱게 내린다.\r\n11. 접시에 요거트 소스를 올린다.\r\n12. 냉동실에 넣어뒀던 연어롤을 꺼내 먹기 좋은 크기로 썬다.\r\n13. 삶은 펜네를 찬물에 식힌다.\r\n14. 펜네를 얇게 썰어 연어롤 위에 올린다.'),
	(200, '연어 타다익선', NULL, 1, '1. 냄비에 간장, 맛술, 청주, 설탕, 꿀을 넣고 끓여 데리야키 소스를 만든다.\r\n2. 방울토마토의 꼭지를 제거하고 끓는 물에 넣어 데친다.\r\n3. 데친 방울토마토를 얼음물에 담가 식힌다.\r\n4. 잘게 썬 두부를 끓는 물에 넣어 데친 뒤, 얼음물에 담가 식힌다.\r\n5. 양파를 잘게 썰어 찬물에 담가둔다.\r\n6. 아보카도를 반으로 가르고 씨를 빼낸다.\r\n7. 아보카도에 칼집을 넣은 뒤 팬에 올려 굽는다.\r\n8. 올리브 오일, 소금, 식초, 꿀, 와사비, 간장을 섞어 와사비 드레싱을 만든다.\r\n9. 연어에 쇠꼬치를 꽂은 뒤, 소금을 뿌린다.\r\n10. 연어를 직화해 겉면만 살짝 익힌다.\r\n11. 직화한 연어를 얼음물에 담가둔다.\r\n12. 연어의 물기를 제거한다.\r\n13. 두부의 물기를 제거한 뒤 와사비 드레싱과 섞는다.\r\n14. 13에 껍질을 제거한 방울토마토를 잘게 썰어 넣는다.\r\n15. 찬물에 담가둔 양파를 넣는다.\r\n16. 구운 아보카도의 껍질을 벗긴 뒤 잘게 썰어 넣어 샐러드를 완성한다.\r\n17. 연어를 얇게 썰어 접시에 담고 샐러드를 곁들인다.\r\n18. 구운 아보카도에 데리야키 소스를 붓는다.\r\n19. 데리야키 소스를 곁들인 아보카도를 접시에 함께 담아낸다.'),
	(201, '햄복한 밥', NULL, 1, '1. 전자레인지에 즉석밥을 넣고 돌린다.\r\n2. 햄을 넓적하게 썰어 기름에 지진다.\r\n3. 양파, 당근, 대파를 잘게 썬다.\r\n4. 햄을 체에 밭쳐 기름을 뺀 뒤, 잘게 썬다.\r\n5. 기름을 두른 팬에 달걀물, 잘게 썬 채소를 넣고 함께 볶는다.\r\n6. 5에 즉석밥을 넣고 함께 볶는다.\r\n7. 기름을 두른 팬에 소금을 넣은 달걀물을 넣고 넓게 부친다.\r\n8. 볶음밥에 잘게 썬 채소와 햄을 넣어 함께 볶는다.\r\n9. 그릇에 햄볶음밥을 담는다.\r\n10. 햄볶음밥 위에 부친 달걀을 올린다.\r\n11. 양파는 채 썰고 마늘과 파는 잘게 다진 뒤, 팬에 넣고 간장을 더해 볶는다.\r\n12. 볶은 채소에 소시지와 차돌박이를 넣고 함께 볶는다.\r\n13. 볶던 재료에 물을 넣는다.\r\n14. 13에 간장으로 간을 한다.\r\n15. 전분물을 넣어 농도를 조절한다.\r\n16. 달걀 위에 볶은 재료를 부어 완성한다.'),
	(202, '불량식풍', NULL, 1, '1. 핸드블렌더에 즉석밥, 달걀, 밀가루, 전분, 맥주, 소금, 후추, 설탕을 넣고 갈아 반죽을 만든다.\r\n2. 햄을 적당한 크기로 썬다.\r\n3. 주둥이 부분을 잘라낸 패트병에 반죽을 담는다.\r\n4. 햄에 나무젓가락을 꽂는다.\r\n5. 햄에 반죽을 묻힌 뒤 기름에 튀긴다.\r\n6. 핫도그를 건져 기름을 뺀다.\r\n7. 한 번 튀겨낸 핫도그에 밀가루>달걀>참깨 순으로 튀김옷을 입힌다.\r\n8. 튀김옷을 입힌 핫도그를 다시 한 번 기름에 튀긴다.\r\n9. 간장, 마요네즈, 다진 마늘, 후추를 섞어 소스를 만든다.\r\n10. 소스에 연겨자를 넣고 섞는다.\r\n11. 소스와 핫도그를 따로 담는다.\r\n12. 소스에 설탕을 넣고 섞는다.'),
	(203, '3점 슛테이크', NULL, 1, '1. 손질한 양파, 당근으로 채소육수를 끓인다.\n2. 석쇠에 대파를 넣고 겉면을 태우듯이 굽는다.\n3. 올리브 오일을 두른 팬에 당근과 양파를 넣고 소금을 뿌려 볶는다.\n4. 비닐팩에 등심을 넣고 고기망치로 두드려 얇게 편다.\n5. 얇게 편 등심에 소금, 후추 간을 한 뒤 밀가루를 묻힌다.\n6. 올리브 오일을 두른 팬에 밀가루를 묻힌 등심을 굽는다.\n7. 채소육수의 채소만 건진다.\n8. 대파의 탄 겉면을 벗겨낸다.\n9. 접시에 건져낸 채소, 대파를 올리고 파르메산 치즈를 뿌려 전자레인지에 넣고 돌린다.\n10. 볶던 당근과 양파에 채소육수를 넣고, 구워둔 등심을 넣어 조린다.\n11. 10에 버터와 소금을 넣어 소스를 만든다.\n12. 접시에 조린 채소와 소스를 담는다.\n13. 채소 위에 구운 등심을 올리고 소스를 두른다.\n14. 파르메산 치즈를 뿌려 구운 대파를 올리고 후추를 뿌려 완성한다.'),
	(204, '아이스테키', NULL, 1, '1. 끓는 물에 달걀을 넣고 삶는다.\r\n2. 냄비에 올리브 오일, 다진 마늘, 마카다미아 아이스크림을 넣고 끓여 소스를 만든다.\r\n3. 아이스크림 소스에 후추, 소금, 간장을 넣고 끓인다.\r\n4. 기름을 두른 팬에 잘게 썬 대파와 양파를 구운 뒤 꺼내서 기름을 제거한다.\r\n5. 기름을 두른 팬에 채끝살, 다진 마늘을 넣고 소금, 후추 간을 해 굽는다.\r\n6. 삶은 달걀을 건져 얼음물에 담가둔다.\r\n7. 삶은 달걀의 노른자와 흰자를 분리해 각각 체에 밭쳐 곱게 내린다.\r\n8. 접시에 아이스크림 소스를 담는다.\r\n9. 채끝 스테이크 위에 곱게 내린 달걀 흰자와 노른자를 올린다.\r\n10. 9에 잘게 썬 대파를 올린다.\r\n11. 아이스크림 소스 위에 스테이크를 올린다.\r\n12. 구운 대파와 양파를 곁들인다.'),
	(205, '가슴이 심쿵해', NULL, 1, '1. 닭가슴살을 얇게 포를 뜬다.\r\n\r\n2. 얇게 포를 뜬 닭가슴살에 후추, 소금, 올리브 오일로 밑간을 한다.\r\n\r\n3. 팬에 호일을 깔고 파슬리, 설탕을 넣어 연기를 낸다.\r\n\r\n4. 석쇠 위에 밑간을 한 닭가슴살을 올리고 뚜껑을 덮어 훈제를 한다.\r\n\r\n5. 애호박, 양파를 썰어 팬에 볶는다.\r\n\r\n6. 올리브 오일, 발사믹 드레싱, 꿀을 섞는다.\r\n\r\n7. 6에 소금, 후추, 다진 마늘을 넣은 뒤 애호박, 양파를 넣고 버무려 샐러드를 만든다.\r\n\r\n8. 접시에 훈제한 닭가슴살과 샐러드를 담아 완성한다.'),
	(206, '돈달돈달 + 여보 밥 안 안치니?', NULL, 1, '1. 마늘을 다진다.\r\n\r\n2. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n3. 식용유와 참기름을 두른 팬에 다진 마늘을 넣고 볶는다.\r\n\r\n4. 지퍼백에 방울토마토를 넣고 으깬다.\r\n\r\n5. 마늘을 볶던 팬에 으깬 방울토마토를 넣고 함께 볶는다.\r\n\r\n6. 식빵을 썰어 마른 팬에 굽는다.\r\n\r\n7. 볶던 재료에 식초, 맛술을 넣는다.\r\n\r\n8. 7에 물, 간장, 곰탕라면 분말수프를 넣고 끓인다.\r\n\r\n9. 전분, 달걀, 물을 섞는다.\r\n\r\n10. 밥에 멸치볶음을 넣어 주먹밥을 만든다.\r\n\r\n11. 주먹밥에 달걀전분물을 묻혀 기름에 튀긴다.\r\n\r\n12. 토마토탕에 달걀전분물을 넣고 저어준다.\r\n\r\n13. 그릇에 토마토 달걀탕을 부은 뒤, 구운 식빵을 올린다.\r\n\r\n14. 튀긴 주먹밥에 깨를 묻혀 낸다.'),
	(207, '이런 돈장', NULL, 1, '1. 껍질을 벗긴 배를 강판에 간다.\r\n\r\n2. 1에 간 양파, 된장, 꿀, 생강즙, 다진 마늘을 넣고 섞어 양념을 만든다.\r\n\r\n3. 불고기용 돼지고기를 적당한 크기로 썰어 양념에 넣고 잘 버무린다.\r\n\r\n4. 3에 깨를 넣어 섞는다.\r\n\r\n5. 물에 헹군 갓김치에 참기름, 다진 마늘, 깨, 꿀을 넣어 무친다.\r\n\r\n6. 깍둑썰기한 두부를 끓는 물에 넣어 데친다.\r\n\r\n7. 기름을 두른 팬에 양념한 돼지고기를 볶는다.\r\n\r\n8. 7에 후추를 넣는다.\r\n\r\n9. 볶은 돼지고기를 석쇠로 옮긴다.\r\n\r\n10. 달군 팬에 술을 넣고 불을 붙인 뒤, 볶은 돼지고기를 직화한다.\r\n\r\n11. 팬에 남은 고기와 직화한 뒤 남은 육즙을 합친다.\r\n\r\n12. 11에 잘게 썬 부추와 애호박을 넣고 볶아 소스를 만든다.\r\n\r\n13. 접시에 데친 두부와 갓김치, 볶은 돼지고기를 담은 뒤 소스를 뿌린다.\r\n\r\n14. 부추를 올려 완성한다.'),
	(208, '허세우', NULL, 1, '1. 감자를 잘게 썰어 우유, 물과 함께 냄비에 넣고 끓인다.\r\n\r\n2. 1에 북어를 잘게 잘라 넣고 함께 끓인다.\r\n\r\n3. 새우의 머리를 떼어낸다.\r\n\r\n4. 손질한 새우를 반으로 가른다.\r\n\r\n5. 종이호일 사이에 새우를 넣고 밀대로 두드려 얇게 편다.\r\n\r\n6. 라이스 페이퍼 사이에 새우를 넣는다.\r\n\r\n7. 기름을 두른 팬에 라이스 페이퍼를 넣고 튀긴다.\r\n\r\n8. 새우를 끼운 라이스 페이퍼를 기름에 튀긴다.\r\n\r\n9. 채 썬 대파를 기름에 볶아 파기름을 낸다.\r\n\r\n10. 파기름에 새우 머리를 넣고 볶은 뒤, 체에 밭쳐 기름만 걸러낸다.\r\n\r\n11. 우유와 함께 삶은 감자를 핸드블렌더에 넣고 버터, 간장을 넣고 갈아 매시드 포테이토를 만든다.\r\n\r\n12. 11에 우유를 넣고 볶는다.\r\n\r\n13. 접시에 매시드 포테이토를 담는다.\r\n\r\n14. 13에 새우를 끼워 튀긴 라이스 페이퍼를 올린다.\r\n\r\n15. 튀긴 라이스 페이퍼를 부순 뒤, 뿌려준다.\r\n\r\n16. 과일간장, 새우 머리를 볶아 향을 낸 기름을 뿌린다.'),
	(209, '단칼국수', NULL, 1, '1. 기름을 두른 팬에 필리핀식 새우젓과 라유를 넣고 볶는다.\r\n\r\n2. 1에 대파, 다진 마늘, 불고기용 돼지고기를 넣고 함께 볶는다.\r\n\r\n3. 볶던 재료에 고춧가루를 뿌린다.\r\n\r\n4. 소금을 넣은 끓는 물에 칼국수면을 넣고 삶는다.\r\n\r\n5. 파프리카와 새송이버섯을 적당한 크기로 썰어 팬에 넣고 볶는다.\r\n\r\n6. 5에 면수를 넣어 볶는다.\r\n\r\n7. 땅콩버터를 넣는다.\r\n\r\n8. 라유, 소금, 후추를 넣는다.\r\n\r\n9. 부추, 청양고추, 레몬즙을 넣는다.\r\n\r\n10. 삶은 칼국수면을 찬물에 헹궈 식힌다.\r\n\r\n11. 그릇에 면을 담고 볶은 재료들을 올린 뒤, 레몬즙을 둘러 완성한다.'),
	(210, '맛있소면', NULL, 1, '1. 불고기용 돼지고기와 베이컨을 잘게 썰어 기름을 두른 팬에 넣고 볶는다.\r\n\r\n2. 1에 잘게 썬 양파, 새송이버섯을 넣고 함께 볶는다.\r\n\r\n3. 볶던 재료에 후추를 뿌린다.\r\n\r\n4. 끓는 물에 소금을 넣고, 소면을 삶는다.\r\n\r\n5. 물, 간장, 소금, 식초를 섞어 국물을 만든다.\r\n\r\n6. 5에 갈아놓은 방울토마토와 마늘을 넣은 뒤, 설탕을 더한다.\r\n\r\n7. 달걀노른자만 분리해 지단을 부친다.\r\n\r\n8. 달걀지단을 말아서 채 썬다.\r\n\r\n9. 국물에 잘게 썬 부추를 넣고 섞는다.\r\n\r\n10. 파프리카를 채 썰어 팬에 볶는다.\r\n\r\n11. 애호박을 채 썰어 팬에 볶는다.\r\n\r\n12. 삶은 소면을 찬물에 헹군다.\r\n\r\n13. 그릇에 면을 담고, 달걀지단, 파프리카, 부추, 돼지고기와 베이컨을 고명으로 올린다.\r\n\r\n14. 물에 헹군 김치와 애호박을 올린다.\r\n\r\n15. 채 썬 양파를 올린 뒤, 국물을 부어 완성한다.'),
	(211, '갓김치치즈스마일', NULL, 1, '1. 기름을 두른 팬에 달걀물을 부은 뒤, 스크램블 한다.\r\n\r\n2. 스크램블 에그에 소금, 후추를 뿌린다.\r\n\r\n3. 베이컨을 격자 모양으로 엮는다.\r\n\r\n4. 베이컨 위에 스크램블 에그를 올린다.\r\n\r\n5. 4에 에멘탈 치즈, 크림 치즈, 모차렐라 치즈를 올린다.\r\n\r\n6. 치즈 위에 다시 한 번 스크램블 에그를 올린 뒤, 베이컨으로 여며 모양을 고정한다.\r\n\r\n7. 기름을 두른 팬에 베이컨 치즈말이를 넣고 굽는다.\r\n\r\n8. 버터를 두른 냄비에 소시지를 썰어 넣는다.\r\n\r\n9. 8에 갓김치, 생크림, 단팥을 넣고 끓여 소스를 만든다.\r\n\r\n10. 구운 베이컨 치즈말이를 전자레인지에 넣고 치즈가 녹을 때까지 돌린다.\r\n\r\n11. 접시에 베이컨 치즈말이를 담고, 갓김치 소스를 따로 담아 낸다.\r\n\r\n12. 파슬리를 뿌려 완성한다.'),
	(212, '고풍격 디저트', NULL, 1, '1. 냄비에 블루베리즙, 복분자 원액을 넣고 졸인다.\r\n\r\n2. 냄비에 설탕과 물을 넣고 끓여 캐러멜라이징한다.\r\n\r\n3. 볼에 달걀, 설탕, 우유를 넣고 섞어 플랑 반죽을 만든다.\r\n\r\n4. 캐러멜라이징한 설탕을 그릇 바닥에 깔리도록 담는다.\r\n\r\n5. 플랑 반죽을 체에 내려 그릇에 담는다.\r\n\r\n6. 5를 전자레인지에 넣고 돌린다.\r\n\r\n7. 생크림, 장미잼, 설탕을 섞어 크림을 만든다.\r\n\r\n8. 접시를 뒤집어 플랑이 담긴 그릇 위에 올린 뒤, 다시 뒤집는다.\r\n\r\n9. 접시에 블루베리 복분자 소스를 뿌린다.\r\n\r\n10. 장미잼 크림을 곁들인다.\r\n\r\n11. 플랑을 덮고 있던 그릇을 뺀다.'),
	(213, '배추 카르복나라', NULL, 1, '1. 끓는 물에 배추를 넣고 데친다.\r\n\r\n2. 물에 푸실리와 소금을 넣고 삶는다.\r\n\r\n3. 데친 배추를 꺼내 찬물에 식힌 뒤, 먹기 좋은 크기로 썬다.\r\n\r\n4. 팬에 올리브 오일을 두르고 마늘, 대파를 넣어 볶는다.\r\n\r\n5. 4에 우유를 넣고 끓인다.\r\n\r\n6. 생크림과 배추를 넣고 끓인다.\r\n\r\n7. 삶은 푸실리를 팬에 넣은 뒤, 소금 간을 한다.\r\n\r\n8. 올리브 오일을 두른 팬에 잘게 썬 베이컨을 볶은 뒤, 파스타에 넣고 섞는다.\r\n\r\n9. 그릇에 파스타를 담아 완성한다.'),
	(214, '샘(킴)표 탕수육', NULL, 1, '1. 양파, 당근, 대파를 썬다.\r\n\r\n2. 팬에 맛술, 간장, 물을 넣고 끓인다.\r\n\r\n3. 불린 표고버섯의 기둥을 제거한다.\r\n\r\n4. 팬에 표고버섯을 넣고 양념에 조린다.\r\n\r\n5. 조린 표고버섯을 체에 밭쳐 버섯과 양념을 분리한다.\r\n\r\n6. 식용유를 두른 팬에 고추, 양파, 당근, 대파를 넣고 볶는다.\r\n\r\n7. 6에 청경채를 넣고 함께 볶는다.\r\n\r\n8. 볶던 재료에 물을 붓고, 표고버섯 조린 양념을 넣어 소스를 만든다.\r\n\r\n9. 소스에 식초, 설탕을 넣는다.\r\n\r\n10. 밀가루와 전분을 섞은 뒤, 물을 넣고 저어가며 반죽한다.\r\n\r\n11. 반죽을 묻힌 표고버섯을 기름에 튀긴다.\r\n\r\n12. 소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n13. 그릇에 소스를 담은 뒤, 튀긴 표고버섯을 올려 완성한다.'),
	(215, '쓰리스타 풍코스', NULL, 1, '1. 불린 반건조 오징어의 껍질을 벗긴다.\r\n\r\n2. 껍질을 벗긴 반건조 오징어에 칼집을 낸다.\r\n\r\n3. 올리브 오일을 두른 팬에 양파, 베이컨을 넣고 볶는다.\r\n\r\n4. 3에 반건조 오징어 다리, 다진 마늘을 넣고 함께 볶는다.\r\n\r\n5. 4에 소금, 후추로 간을 한 뒤, 양지 육수를 넣고 끓인다.\r\n\r\n6. 잘게 썬 대파를 팬에 넣고 함께 끓인다.\r\n\r\n7. 믹서기에 물기를 뺀 병아리콩과 우유를 넣고 간다.\r\n\r\n8. 7에 들깨가루를 넣는다.\r\n\r\n9. 달군 팬에 버터를 녹인 뒤 달걀물, XO소스를 넣고 스크램블한다.\r\n\r\n10. 스크램블 에그에 소금, 후추, 파슬리를 넣는다.\r\n\r\n11. 게살 통조림을 체에 밭쳐 물기를 뺀다.\r\n\r\n12. 물기를 뺀 게살에 레몬즙을 넣어 섞는다.\r\n\r\n13. 데운 그릇에 원형틀을 이용해 스크램블 에그와 게살을 층층이 올린다.\r\n\r\n14. 13에 올리브 오일을 둘러 에피타이저를 완성한다.\r\n\r\n15. 병아리콩 수프에 후추, 올리브 오일, 우유, 소금을 넣고 섞는다.\r\n\r\n16. 그릇에 병아리콩 수프를 담고, 올리브 오일과 파슬리를 뿌린다.\r\n\r\n17. 올리브 오일을 두른 팬에 불린 반건조 오징어를 굽는다.\r\n\r\n18. 볶던 재료에 구운 반건조 오징어를 넣고, 반건조 오징어를 굽고 남은 기름을 더한다.\r\n\r\n19. 생크림과 설탕을 섞은 뒤, 핸드 블렌더로 갈아 샹티이 크림을 만든다.\r\n\r\n20. 샹티이 크림에 화이트 트러플 오일을 넣고 섞는다.\r\n\r\n21. 접시에 볶은 재료들과 반건조 오징어를 올린 뒤, 파슬리를 뿌려 메인 요리를 완성한다.\r\n\r\n22. 그릇에 잘게 썬 고구마 말랭이를 담고, 샹티이 크림을 올려 디저트를 완성한다.'),
	(216, '미슐랭 쓰리스타는 게뿔', NULL, 1, '1. 끓는 물에 소금, 올리브 오일을 넣고 오르초를 삶는다.\r\n\r\n2. 소고기를 삼각형 모양으로 썬다.\r\n\r\n3. 파슬리와 튀김가루를 섞은 뒤, 소고기에 묻힌다.\r\n\r\n4. 3을 기름에 넣어 튀긴다.\r\n\r\n5. 믹서기에 홀토마토, 티카 마살라 소스를 넣고 간다.\r\n\r\n6. 팬에 물기를 뺀 게살, 오렌지 리큐르, 갈아놓은 소스를 넣고 끓인다.\r\n\r\n7. 작은 냄비에 면수를 담고, 비프 스톡을 넣어 육수를 끓인다.\r\n\r\n8. 삶은 오르초를 체에 밭쳐 물기를 빼고, 소스와 섞는다.\r\n\r\n9. 8에 코코넛 크림, 소금, 후추를 넣는다.\r\n\r\n10. 핸드 블렌더에 라즈베리와 육수를 넣고 간다.\r\n\r\n11. 10에 후추를 뿌린 뒤, 체에 밭쳐 곱게 내린다.\r\n\r\n12. 그릇에 파스타를 담고, 튀긴 소고기를 꽂는다.\r\n\r\n13. 라즈베리 소스를 곁들이고, 파슬리를 뿌려 완성한다.'),
	(217, '로맨티스타 케이크', NULL, 1, '1. 끓는 물에 소금을 넣고 파스타면을 삶는다.\r\n\r\n2. 차돌박이를 다진다.\r\n\r\n3. 베이컨과 팽이버섯을 적당한 크기로 썬다.\r\n\r\n4. 백김치의 국물을 짜낸 뒤, 잘게 썬다.\r\n\r\n5. 달걀물에 크림소스를 넣고 섞는다.\r\n\r\n6. 두 개의 팬에 기름을 두른 뒤, 다진 마늘과 베이컨을 넣고 볶는다.\r\n\r\n7. 팬에 차돌박이와 팽이버섯을 넣고 소금, 후추 간을 한다.\r\n\r\n8. 파스타면을 건져 팬에 넣고 함께 볶는다.\r\n\r\n9. 팬에 면수를 넣고, 잘게 다진 백김치를 넣는다.\r\n\r\n10. 9에 크림소스를 섞은 달걀물을 부어 파스타 케이크의 시트를 만든다.\r\n\r\n11. 접시를 이용해 케이크 시트를 뒤집어 익힌다.\r\n\r\n12. 오징어 젓갈을 잘게 다진다.\r\n\r\n13. 다진 오징어 젓갈과 크림치즈를 섞는다.\r\n\r\n14. 접시에 구운 케이크 시트를 올린다.\r\n\r\n15. 케이크 시트 위에 크림치즈와 섞은 오징어 젓갈을 바른다.\r\n\r\n16. 15 위에 케이크 시트를 한 장 더 올려 파스타 케이크를 완성한다.\r\n\r\n17. 데친 칵테일 새우로 케이크를 장식한다.'),
	(218, '마이 ♥ 케이크', NULL, 1, '1. 인스턴트 카레를 체에 걸러 건더기와 소스를 분리한다.\r\n\r\n2. 카레소스에 돈코츠 라면 수프, 물, 트러플 오일, 후추를 넣고 끓인다.\r\n\r\n3. 달걀, 물, 크림소스, 핫케이크 가루를 섞어 핫케이크 반죽을 만든다.\r\n\r\n4. 브로콜리, 대파, 파프리카, 팽이버섯을 잘게 썬다.\r\n\r\n5. 끓는 물에 칵테일 새우와 잘게 썬 채소를 데친다.\r\n\r\n6. 기름을 두른 팬에 핫케이크 반죽을 넣고 굽는다.\r\n\r\n7. 데친 칵테일 새우와 채소에 트러플 오일, 소금, 후추를 넣고 섞는다.\r\n\r\n8. 구운 핫케이크를 원형틀로 눌러 동그란 모양으로 두 장을 찍어낸다.\r\n\r\n9. 쇠꼬치를 불에 달군 뒤, 핫케이크 표면에 그림을 그린다.\r\n\r\n10. 접시에 카레소스를 담은 뒤, 그림을 그리지 않은 핫케이크를 올린다.\r\n\r\n11. 핫케이크 위에 양념한 칵테일 새우와 채소, 날치알을 올린다.\r\n\r\n12. 11에 트러플 오일을 두른 뒤, 그림을 그린 핫케이크를 올린다.'),
	(219, '크림 속에 비친 파프리카', NULL, 1, '1. 파프리카를 직화한다.\r\n\r\n2. 구워져있는 삼치의 가시를 제거한다.\r\n\r\n3. 팬에 으깬 마늘과 손질한 삼치를 넣은 뒤, 살을 으깨어가며 굽는다.\r\n\r\n4. 콩나물을 잘게 다진다.\r\n\r\n5. 다진 콩나물과 양파를 팬에 넣고 소금, 후추를 뿌려 볶는다.\r\n\r\n6. 5에 간장과 맛술을 넣어 볶는다.\r\n\r\n7. 팽이버섯을 적당한 크기로 썰어 채소를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n8. 즉석밥에 구운 삼치와 날치알을 넣는다.\r\n\r\n9. 8에 참기름, 볶던 채소를 넣고 섞는다.\r\n\r\n10. 팬에 남은 구운 삼치에 화이트 와인을 넣고 볶는다.\r\n\r\n11. 핸드블렌더에 구운 삼치와 물을 넣고 간다.\r\n\r\n12. 갈아낸 삼치에 크림치즈와 물을 넣고 끓여 삼치 크림소스를 만든다.\r\n\r\n13. 12에 레몬즙, 올리고당을 넣는다.\r\n\r\n14. 접시에 삼치 크림소스를 담는다.\r\n\r\n15. 구운 파프리카의 탄 부분을 벗겨낸다.\r\n\r\n16. 길게 펼친 파프리카에 양념한 밥을 넣고 돌돌 만 뒤, 소스 위에 올린다.\r\n\r\n17. 잘게 썬 파를 올리고, 올리브 오일을 뿌린다.\r\n\r\n18. 끓는 물에 달걀을 넣어 수란을 만든 뒤, 파프리카 위에 올린다.\r\n\r\n19. 후추를 뿌려 완성한다.'),
	(220, '사장님 라이스 버거~', NULL, 1, '1. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n2. 차돌박이를 적당한 크기로 썬다.\r\n\r\n3. 장조림 국물, 물, 꿀을 섞어 불고기 양념을 만든다.\r\n\r\n4. 불고기 양념에 다진 파, 다진 마늘, 후추를 넣는다.\r\n\r\n5. 차돌박이에 불고기 양념을 넣어 버무린다.\r\n\r\n6. 팬에 양념한 차돌박이를 넣고 볶는다.\r\n\r\n7. 즉석밥에 깨와 소금을 넣고 섞어 밑간을 한다.\r\n\r\n8. 비닐팩에 밥을 넣은 뒤, 바닥을 제거한 종이그릇 안에 넣고 꾹꾹 눌러 모양을 잡는다.\r\n\r\n9. 기름을 두른 팬에 모양을 잡은 밥을 굽는다.\r\n\r\n10. 양상추를 채 썰어 차가운 물에 담가둔다.\r\n\r\n11. 마요네즈, 된장, 트러플 오일, 참기름, 레몬즙, 꿀을 섞어 된장 아이올리를 만든다.\r\n\r\n12. 양상추에 된장 아이올리를 넣고 버무린다.\r\n\r\n13. 구운 밥 위에 차돌박이 불고기와 체다 치즈를 올린 뒤, 토치로 치즈를 녹인다.\r\n\r\n14. 녹인 체다 치즈 위에 구운 밥을 올린다.\r\n\r\n15. 된장 아이올리에 버무린 양상추를 곁들여 낸다.'),
	(221, '이탈리안 특급 파스타', NULL, 1, '1. 양파를 잘게 다져 냄비에 넣고 볶는다.\r\n\r\n2. 끓는 물에 소금을 넣은 뒤, 파스타면을 넣고 삶는다.\r\n\r\n3. 길게 찢은 새송이버섯, 다진 마늘을 팬에 넣고 볶는다.\r\n\r\n4. 우설을 얇게 썬 뒤, 3에 넣고 볶는다.\r\n\r\n5. 팬에 화이트 와인을 부어 플람베한다.\r\n\r\n6. 볶던 재료에 소금, 후추로 간을 한다.\r\n\r\n7. 냄비에 우유와 레몬즙을 넣고 끓인다.\r\n\r\n8. 볶던 양파에 토마토 소스, 후추를 넣고 끓인다.\r\n\r\n9. 레몬즙을 넣고 끓인 우유를 면보에 걸러 물기를 빼 리코타 치즈를 만든다.\r\n\r\n10. 우설을 볶던 팬에 토마토 소스를 넣는다.\r\n\r\n11. 10에 파스타면을 넣는다.\r\n\r\n12. 파스타에 올리브 오일, 설탕을 넣는다.\r\n\r\n13. 접시에 파스타를 담은 뒤, 리코타 치즈를 올린다.\r\n\r\n14. 토치로 리코타 치즈의 겉면을 살짝 그을린다.\r\n\r\n15. 올리브 오일을 둘러 완성한다.'),
	(222, '당근 체인지업', NULL, 1, '1. 소고기 안심에 소금을 뿌려 밑간을 한다.\r\n\r\n2. 양파를 반으로 자른 뒤, 가운데 심 부분만 빼내어 팬에 굽는다.\r\n\r\n3. 남은 양파와 소고기 안심을 채 썰어 기름을 두른 팬에 볶는다.\r\n\r\n4. 3에 통후추를 으깨어 넣는다.\r\n\r\n5. 소고기 안심과 양파를 볶던 팬에 레드와인을 넣는다.\r\n\r\n6. 5에 꿀, 연겨자를 넣어 소스를 만든다.\r\n\r\n7. 단면을 구운 양파를 접시에 담은 뒤, 전자레인지에 넣고 익힌다.\r\n\r\n8. 당근을 얇게 썬다.\r\n\r\n9. 팬에 얇게 썬 당근, 버터, 불린 누룽지를 넣고 볶는다.\r\n\r\n10. 9에 물을 부어 끓이면서 소금으로 간을 한다.\r\n\r\n11. 10을 핸드블렌더에 넣고 간 뒤, 체에 걸러 퓌레를 만든다.\r\n\r\n12. 기름을 두른 팬에 소금, 후추 간을 한 소고기 안심을 굽는다.\r\n\r\n13. 전자레인지에서 익힌 양파를 꺼내 한 겹씩 분리한다.\r\n\r\n14. 원형틀을 이용해 접시에 당근 퓌레를 담는다.\r\n\r\n15. 소고기 안심 스테이크를 먹기 좋은 크기로 썬다.\r\n\r\n16. 당근 퓌레 위에 썰어놓은 스테이크를 올린 뒤, 양파로 장식한다.\r\n\r\n17. 소스를 체에 밭쳐 건더기를 걸러낸다.\r\n\r\n18. 접시에 소스를 두르고 후추를 뿌려 완성한다.'),
	(223, '입이 떡~갈비', NULL, 1, '1. 볼에 매실고추장, 다진 마늘, 생강즙, 양파장아찌와 장아찌 국물을 함께 넣는다.\r\n\r\n2. 1에 다진 고추장아찌, 맛술, 참기름, 깨, 꿀, 설탕, 다진 베트남 고추를 섞는다.\r\n\r\n3. 핸드블렌더에 잘게 썬 소고기와 양념을 넣고 간다.\r\n\r\n4. 3에 다진 양파, 대파, 고추, 참기름, 전분을 넣어 치댄다.\r\n\r\n5. 새송이버섯을 도톰하게 편으로 썬 뒤, 직사각형 모양으로 썬다.\r\n\r\n6. 직사각형으로 썬 새송이버섯을 양념한 고기로 감싼다.\r\n\r\n7. 6과 편으로 썬 새송이버섯을 팬에 넣고 굽는다.\r\n\r\n8. 떡갈비에 버터를 끼얹어가며 굽는다.\r\n\r\n9. 올리브 오일, 레몬즙을 섞은 뒤 채 썬 사과에 버무린다.\r\n\r\n10. 접시에 떡갈비를 얹는다.\r\n\r\n11. 떡갈비 위에 구운 새송이버섯을 올린다.\r\n\r\n12. 양념한 채 썬 사과, 얇게 썬 밤을 올려 완성한다.'),
	(224, '콩나물 팍팍 무쳤돔', NULL, 1, '1. 옥돔의 뼈를 발라낸 뒤, 살을 손질한다.\r\n\r\n2. 손질한 옥돔살에 칼집을 내고 적당한 크기로 썬다.\r\n\r\n3. 2에 청주를 부어 비린내를 잡는다.\r\n\r\n4. 손질한 낙지를 소금으로 비벼 씻은 뒤, 적당한 크기로 썬다.\r\n\r\n5. 끓는 물에 소금을 넣고 콩나물을 삶는다.\r\n\r\n6. 양파, 대파, 청양고추를 잘게 썬다.\r\n\r\n7. 대파의 흰 부분을 채 썰어 찬물에 담가둔다.\r\n\r\n8. 데친 콩나물을 찬물로 식힌다.\r\n\r\n9. 낙지를 데친 뒤, 얼음물에 담가둔다.\r\n\r\n10. 고춧가루(3), 고추장(1), 간장(2), 소금, 후추를 섞어 양념장을 만든다.\r\n\r\n11. 양념장에 설탕(2), 청주, 다진 마늘, 다진 생강을 넣고 섞는다.\r\n\r\n12. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n13. 기름을 두른 팬에 손질한 채소, 데친 콩나물과 낙지를 넣은 뒤 양념장과 함께 볶는다.\r\n\r\n14. 달걀 노른자, 얼음물, 밀가루를 섞어 튀김옷을 만든다.\r\n\r\n15. 튀김옷을 입힌 옥돔을 기름에 튀긴다.\r\n\r\n16. 튀긴 옥돔을 건져 기름기를 뺀다.\r\n\r\n17. 기름에 날달걀을 넣어 튀긴다.\r\n\r\n18. 접시에 즉석밥을 담는다.\r\n\r\n19. 옥돔 튀김과 낙지 콩나물찜을 올린다.\r\n\r\n20. 날달걀 튀김을 얹어 완성한다.'),
	(225, '토마토 쓴다고 놀리지 말아요', NULL, 1, '1. 냄비에 올리브 오일을 두른 뒤, 손질한 채소와 물을 넣고 끓여 채소 육수를 만든다.\r\n\r\n2. 1에 게딱지를 넣고 함께 끓인다.\r\n\r\n3. 팬에 잣을 넣고 볶는다.\r\n\r\n4. 물기를 제거한 대구살을 핸드블렌더에 넣고 간다.\r\n\r\n5. 4에 볶은 잣, 전분, 파르메산 치즈를 넣고 함께 간다.\r\n\r\n6. 5에 물을 넣은 뒤 동그랗게 뭉친다.\r\n\r\n7. 올리브 오일을 두른 팬에 올리브, 케이퍼, 마늘을 잘게 다져 넣고 볶는다.\r\n\r\n8. 7에 게와 육수를 넣고 함께 볶는다.\r\n\r\n9. 잘게 썬 선드라이 토마토와 올리브 오일을 넣고 함께 볶는다.\r\n\r\n10. 올리브 오일을 두른 팬에 뭉친 대구살을 부친다.\r\n\r\n11. 대구살 완자를 육수에 넣고 살짝 익힌 뒤, 팬에 넣고 마저 익힌다.\r\n\r\n12. 팬에서 게를 건져낸다.\r\n\r\n13. 그릇에 국물과 대구살 완자를 담는다.\r\n\r\n14. 13 위에 부친 대구살을 다져 올린다.'),
	(226, '밖으로 나가버리곤약', NULL, 1, '1. 불고기용 소고기를 칼로 두드려 부드럽게 만든 뒤, 후추와 소금으로 밑간한다.\r\n\r\n2. 고기의 질긴 부분을 제거하고 적당한 크기로 썬다.\r\n\r\n3. 기름을 두른 압력솥에 손질한 채소와 고기를 넣고 볶는다.\r\n\r\n4. 3에 카레가루를 넣고 볶는다.\r\n\r\n5. 팬에 레드와인을 졸인다.\r\n\r\n6. 볶던 재료에 졸인 와인과 돈가스 소스를 넣는다.\r\n\r\n7. 6에 배즙을 넣는다.\r\n\r\n8. 적당한 크기로 썬 배를 압력솥에 넣는다.\r\n\r\n9. 압력솥의 뚜껑을 닫고 익힌다.\r\n\r\n10. 표고버섯은 편으로 썰고, 애호박은 얇게 채 썬다.\r\n\r\n11. 기름을 두른 팬에 표고버섯, 애호박, 후추를 넣고 볶는다.\r\n\r\n12. 11에 물기를 뺀 실곤약과 간장을 넣고 볶은 뒤 접시에 담는다.\r\n\r\n13. 익힌 재료에 버터와 트러플 꿀을 넣는다.\r\n\r\n14. 소금을 넣은 끓는 물에 적당한 크기로 썬 아스파라거스를 데친다.\r\n\r\n15. 실곤약 볶음 위에 익힌 고기와 채소를 올린다.\r\n\r\n16. 졸인 국물을 부어 완성한다.'),
	(227, '안심하그라탱', NULL, 1, '1. 소금, 후추로 밑간을 한 닭안심살을 두드려 부드럽게 만든다.\r\n\r\n2. 닭안심살에 올리브 오일을 뿌린 뒤, 그릴팬에 굽는다.\r\n\r\n3. 생모차렐라 치즈와 흑토마토를 같은 두께로 썬다.\r\n\r\n4. 그라탱 접시에 생모차렐라 치즈, 구운 닭안심살, 흑토마토를 겹겹이 놓는다.\r\n\r\n5. 모차렐라 치즈를 갈아 올리고, 소금으로 간을 한다.\r\n\r\n6. 5를 예열된 오븐에 넣고 굽는다.\r\n\r\n7. 토마토를 돌려 깎아 껍질로 장미꽃 모양을 만든다.\r\n\r\n8. 상추를 잘게 썬다.\r\n\r\n9. 올리브로 토끼 모양을 만든다.\r\n\r\n10. 올리브를 잘게 썬다.\r\n\r\n11. 오븐에서 꺼낸 그라탱 위에 상추, 올리브로 만든 토끼, 토마토로 만든 꽃을 얹는다.\r\n\r\n12. 잘게 썬 올리브와 데친 아스파라거스를 올려 완성한다.'),
	(228, '홍시 밭의 거위', NULL, 1, '1. 홍시는 껍질을 벗기고 심과 씨를 발라낸다.\r\n\r\n2. 홍시를 핸드블렌더에 넣고 곱게 간다.\r\n\r\n3. 2를 체에 밭쳐 곱게 내린 뒤 소금, 후추로 간을 한다.\r\n\r\n4. 찜기에 홍삼정과를 넣고 찐다.\r\n\r\n5. 끓는 물에 소금을 넣고 아스파라거스를 데친다.\r\n\r\n6. 데친 아스파라거스를 적당한 크기로 썬다.\r\n\r\n7. 볼에 트러플 꿀, 트러플 오일, 후추, 데친 아스파라거스를 넣고 섞는다.\r\n\r\n8. 허니 아몬드를 잘게 부순다.\r\n\r\n9. 푸아그라 통조림을 적당한 크기로 썬 다음 트러플 소금을 뿌린다.\r\n\r\n10. 원형틀을 이용해 상추를 동그란 모양으로 찍어낸다.\r\n\r\n11. 찐 홍삼절편에 트러플 오일과 트러플 소금을 뿌린다.\r\n\r\n12. 접시에 홍시 소스를 얇게 펼친다.\r\n\r\n13. 홍시 소스 위에 푸아그라, 홍삼정과, 아스파라거스를 올린다.\r\n\r\n14. 13 위에 동그랗게 손질한 상추를 올리고, 잘게 부순 허니 아몬드를 뿌려 완성한다.'),
	(229, '장어 팔복채', NULL, 1, '1. 끓는 물에 전복을 데친다.\r\n\r\n2. 오징어에 칼집을 내고 적당한 크기로 썬다.\r\n\r\n3. 새우는 머리와 껍질을 제거한 뒤, 등에 칼집을 낸다.\r\n\r\n4. 데친 전복에 칼집을 낸 뒤, 잘게 썬다.\r\n\r\n5. 장어에 칼집을 내고 적당한 크기로 썬다.\r\n\r\n6. 기름에 대파, 고춧가루, 다진 생강을 넣고 끓인다.\r\n\r\n7. 6을 체에 걸러 고추기름만 받아낸다.\r\n\r\n8. 브로콜리, 파프리카, 표고버섯, 배추를 적당한 크기로 썬다.\r\n\r\n9. 끓는 물에 청주를 넣은 뒤, 손질한 해산물과 채소를 넣고 데친다.\r\n\r\n10. 대파, 마늘, 다진 생강, 고추를 손질한다.\r\n\r\n11. 팬에 고추기름을 두르고 대파, 고추, 다진 마늘과 생강을 넣고 볶는다.\r\n\r\n12. 데친 해산물과 채소를 체에 밭쳐 물기를 뺀 뒤, 팬에 넣어 함께 볶는다.\r\n\r\n13. 12에 간장, 장어구이용 소스, 두반장을 넣고 볶는다.\r\n\r\n14. 데친 전복을 넣고 함께 볶는다.\r\n\r\n15. 전분물을 넣어 농도를 맞춘다.'),
	(230, '트러플 메이커', NULL, 1, '1. 볼에 달걀을 푼 뒤, 체에 밭쳐 곱게 내린다.\r\n\r\n2. 팬에 달걀물을 붓고 젓가락으로 살살 휘젓는다.\r\n\r\n3. 아랫면만 익힌 달걀을 팬을 기울여 한 쪽에 몰아놓고, 반대편 바닥을 두드리며 반달모양으로 만든다.\r\n\r\n4. 달걀 사이에 화이트 트러플 페이스트를 넣어 감싸 오믈렛을 만든다.\r\n\r\n5. 곡물빵을 석쇠에 넣고 직화로 굽는다.\r\n\r\n6. 곡물빵에 올리브 오일을 뿌리고 다시 직화로 굽는다.\r\n\r\n7. 버터를 중탕으로 녹여 정제버터를 만든다.\r\n\r\n8. 볼에 달걀 노른자, 나초 치즈를 넣고 섞으며 중탕한다.\r\n\r\n9. 8에 정제버터를 조금씩 넣어가며 섞어 홀랜다이즈 소스를 만든다.\r\n\r\n10. 홀랜다이즈 소스에 식초를 넣고 섞는다.\r\n\r\n11. 접시에 구운 곡물빵을 담고 오믈렛을 올린다.\r\n\r\n12. 오믈렛 위에 홀랜다이즈 소스를 얹는다.\r\n\r\n13. 화이트 트러플을 갈아 올려 완성한다.'),
	(231, '자연풍 주의보', NULL, 1, '1. 파프리카를 직화한다.\r\n\r\n2. 새우의 머리와 껍질을 제거한다.\r\n\r\n3. 들기름을 두른 냄비에 새우 머리를 볶은 뒤, 우유를 부어 끓인다.\r\n\r\n4. 3에 들깨가루를 넣어 들깨소스를 만든다.\r\n\r\n5. 들깨소스에 소금으로 간을 한다.\r\n\r\n6. 버터를 두른 팬에 잘게 썬 새우를 넣어 볶는다.\r\n\r\n7. 6에 소금, 후추로 간을 한 뒤 화이트 와인을 넣는다.\r\n\r\n8. 아보카도는 반으로 갈라 씨를 빼고, 껍질을 벗긴다.\r\n\r\n9. 새우를 볶던 팬에 잘게 썬 아보카도를 넣고 함께 볶는다.\r\n\r\n10. 9에 파르메산 치즈를 갈아 넣는다.\r\n\r\n11. 토치로 파프리카의 겉면을 완전히 태운다.\r\n\r\n12. 파프리카의 탄 부분을 깨끗이 씻어내고 씨를 빼낸다.\r\n\r\n13. 파프리카 속에 치즈를 넣어 볶은 새우와 아보카도를 채워 넣는다.\r\n\r\n14. 끓인 들깨소스를 체에 밭쳐 곱게 내린다.\r\n\r\n15. 그릇에 들깨소스를 붓고 속을 채운 파프리카를 담아 완성한다.'),
	(232, '마성의 해물면', NULL, 1, '1. 마는 껍질을 벗겨 손질한다.\n\n2. 오징어와 새우를 적당한 크기로 썬다.\n\n3. 믹서기에 오징어, 새우, 마를 넣는다.\n\n4. 3에 달걀 흰자, 성게알, 전분, 간장을 넣고 갈아 반죽을 만든다.\n\n5. 전복의 내장을 떼어내고 적당한 크기로 썬다.\n\n6. 마와 애호박을 채 썬다.\n\n7. 손질한 전복, 마, 애호박을 끓는 물에 데친 뒤 얼음물에 담가둔다.\n\n8. 비닐팩에 갈아놓은 반죽을 넣는다.\n\n9. 비닐팩 모서리를 잘라 작게 구멍을 낸다.\n\n10. 끓는 물을 저어 회오리를 만든 뒤, 반죽을 짜 넣는다.\n\n11. 익은 해물면을 건져낸 뒤, 얼음물에 담가둔다.\n\n12. 성게알, 트러플 오일, 소금, 간장을 섞어 성게알 소스를 만든다.\n\n13. 성게알 소스를 체에 밭쳐 곱게 내린다.\n\n14. 볼에 데친 전복과 마, 애호박, 해물면을 담은 뒤 성게알 소스를 넣고 버무린다.\n\n15. 성게알 소스에 버무린 해물면과 전복, 채소를 접시에 담는다.\n\n16. 성게알을 올려 완성한다.'),
	(233, '라자냐는 나자냐', NULL, 1, '1. 끓는 물에 소금을 넣은 뒤, 라자냐를 넣고 삶는다.\r\n\r\n2. 파프리카, 삶은 호박고지, 느타리버섯, 브로콜리, 양파, 가지를 잘게 썬다.\r\n\r\n3. 닭다리살을 잘게 썬다.\r\n\r\n4. 기름을 두른 팬에 으깬 마늘과 닭다리살을 넣고 볶는다.\r\n\r\n5. 4에 소금, 후추로 간을 한 뒤 손질한 채소들을 넣고 함께 볶는다.\r\n\r\n6. 팬에 버터와 밀가루를 넣고 볶아 루를 만든다.\r\n\r\n7. 루에 우유와 파르메산 치즈를 갈아 넣어 베샤멜 소스를 만든다.\r\n\r\n8. 볼에 화이트 와인 식초, 토마토 소스, 설탕, 소금, 올리브 오일을 넣고 섞는다.\r\n\r\n9. 8에 볶은 재료들을 넣고, 파르메산 치즈를 갈아 넣는다.\r\n\r\n10. 오븐용 그릇에 라자냐 > 베샤멜 소스 > 토마토 소스에 버무린 재료들 순으로 켜켜이 쌓는다.\r\n\r\n11. 10에 슬라이스 치즈를 올리고 베샤멜 소스를 뿌린다.\r\n\r\n12. 파르메산 치즈를 갈아 올린 뒤, 예열한 오븐에 넣어 익힌다.\r\n\r\n13. 오븐에서 라자냐를 꺼낸 뒤, 토치로 표면을 마저 익힌다.'),
	(234, '사랑햄', NULL, 1, '1. 사과의 껍질을 깎은 뒤, 잘게 썬다.\r\n\r\n2. 잘게 썬 사과를 냄비에 넣는다.\r\n\r\n3. 사과를 넣은 냄비에 사과주스와 설탕을 넣고 조린다.\r\n\r\n4. 3에 버터를 넣는다.\r\n\r\n5. 냄비에 통조림 햄을 으깨어 넣고 함께 조린다.\r\n\r\n6. 볼에 달걀, 밀가루, 설탕을 넣고 섞는다.\r\n\r\n7. 6에 우유를 넣고 섞어 크레페 반죽을 만든다.\r\n\r\n8. 달군 팬에 크레페 반죽을 부어 얇게 부친다.\r\n\r\n9. 달걀프라이를 만들어 크레페 위에 올린다.\r\n\r\n10. 9에 조린 사과와 햄, 슬라이스 치즈를 올린다.\r\n\r\n11. 크레페를 접어 재료들을 감싼다.\r\n\r\n12. 재료를 감싼 크레페에 달걀물 > 빵가루 순으로 튀김옷을 입힌다.\r\n\r\n13. 튀김옷을 입힌 크레페를 기름에 넣고 튀긴다.\r\n\r\n14. 튀겨낸 크레페를 접시에 담아낸다.'),
	(235, '가지 싸움에 새우등 터진다', NULL, 1, '1. 가지를 편으로 썬다.\r\n\r\n2. 편으로 썬 가지에 밀가루를 묻힌다.\r\n\r\n3. 칵테일 새우의 꼬리를 제거한 뒤, 살을 으깬다.\r\n\r\n4. 적당한 크기로 썬 소고기와 으깬 새우살을 함께 다진다.\r\n\r\n5. 4에 감자 전분, 달걀물, 생강즙을 넣고 치댄다.\r\n\r\n6. 밀가루를 묻힌 가지 사이에 소고기&새우 반죽을 넣는다.\r\n\r\n7. 감자 전분, 옥수수 전분, 달걀물, 물을 섞어 튀김옷을 만든다.\r\n\r\n8. 소고기&새우 반죽을 넣은 가지에 튀김옷을 입힌다.\r\n\r\n9. 8을 기름에 넣고 튀긴다.\r\n\r\n10. 볼에 마늘, 대파, 파프리카를 잘게 다져 넣는다.\r\n\r\n11. 10에 간장, 식초, 물, 설탕(1:1:1:1)을 넣고 섞어 소스를 만든다.\r\n\r\n12. 접시에 가지 튀김을 담고 소스를 뿌려 완성한다.'),
	(236, '대게 맛있는 따로 덮밥', NULL, 1, '1. 기름을 두른 팬에 잘게 썬 양파를 넣고 볶는다.\r\n\r\n2. 1에 삶은 호박고지를 넣고 함께 볶는다.\r\n\r\n3. 잘게 썬 대파와 삶은 고사리, 다진 마늘을 팬에 넣고 함께 볶는다.\r\n\r\n4. 채소를 볶던 팬에 물과 맛간장을 넣어 끓인다.\r\n\r\n5. 4에 맛술과 올리고당을 넣어 함께 끓인다.\r\n\r\n6. 표고버섯과 고추를 썰어 팬에 넣은 뒤, 후추를 뿌린다.\r\n\r\n7. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n8. 잘게 썬 대파를 얼음물에 담가둔다.\r\n\r\n9. 대게살과 삶은 문어의 물기를 제거한다.\r\n\r\n10. 물기를 제거한 대게살과 삶은 문어를 기름에 튀긴다.\r\n\r\n11. 채소를 넣어 끓인 덮밥 소스에 참기름을 넣는다.\r\n\r\n12. 11에 달걀물을 풀어 넣는다.\r\n\r\n13. 볼에 잘게 다진 삶은 시래기, 밥, 참기름을 넣고 섞는다.\r\n\r\n14. 13에 소금으로 간을 한 뒤, 잘 섞어준다.\r\n\r\n15. 원형틀을 이용해 그릇에 시래기밥을 담고 깨를 뿌린다.\r\n\r\n16. 다른 접시에 원형틀을 이용해 덮밥 소스를 담는다.\r\n\r\n17. 덮밥 소스 위에 튀긴 문어와 대게살, 채 썬 파를 올려 완성한다.'),
	(237, '삼치렐라', NULL, 1, '1. 삼치의 가시를 제거하고 껍질을 벗긴다.\r\n\r\n2. 삼치에 소금, 후추, 레몬즙으로 밑간을 한다.\r\n\r\n3. 호두를 잘게 다진다.\r\n\r\n4. 밑간을 한 삼치에 밀가루>달걀물>다진 호두 순으로 튀김옷을 입힌다.\r\n\r\n5. 튀김옷을 입힌 삼치를 기름에 넣고 튀긴다.\r\n\r\n6. 버터를 두른 팬에 브로콜리를 넣고 굽는다.\r\n\r\n7. 6에 편으로 썬 마늘, 물, 소금, 후추를 넣는다.\r\n\r\n8. 파프리카는 윗부분을 잘라내고 씨를 빼낸다.\r\n\r\n9. 다진 토마토와 마늘, 바질 페스토, 올리브 오일, 청양고추를 섞어 샐러드를 만든다.\r\n\r\n10. 토마토 샐러드에 소금 간을 한 뒤, 파프리카 속에 담는다.\r\n\r\n11. 튀긴 삼치의 기름기를 제거한다.\r\n\r\n12. 접시에 토마토 샐러드, 익힌 브로콜리, 튀긴 삼치를 담는다.\r\n\r\n13. 수제 요거트에 다진 실파, 소금, 다진 마늘, 마요네즈를 섞어 소스를 만든다.\r\n\r\n14. 접시에 요거트 소스를 올려 완성한다.'),
	(238, '개천에서 용 났새우', NULL, 1, '1. 브로콜리를 적당한 크기로 썰어 소금을 넣은 끓는 물에 데친다.\r\n\r\n2. 데친 브로콜리를 그릴팬에 굽는다.\r\n\r\n3. 북어채에 기름을 두른 뒤, 전자레인지에 돌려 바삭하게 만든다.\r\n\r\n4. 끓는 물에 라면을 삶는다.\r\n\r\n5. 팬에 올리브 오일을 넉넉히 두른다.\r\n\r\n6. 올리브 오일을 두른 팬에 두껍게 썬 마늘과 파를 넣고 볶아 향을 낸다.\r\n\r\n7. 6에 새우와 다진 청양고추를 넣고 볶는다.\r\n\r\n8. 양파를 잘게 다져 팬에 넣고 함께 볶는다.\r\n\r\n9. 그릇에 구운 브로콜리를 담는다.\r\n\r\n10. 삶은 면을 체에 밭쳐 물기를 뺀다.\r\n\r\n11. 면에 재료들의 향이 밴 기름을 넣고 버무린다.\r\n\r\n12. 면을 그릇에 담는다.\r\n\r\n13. 면 위에 볶은 재료들을 올린다.\r\n\r\n14. 바삭해진 북어채를 핸드블렌더에 넣고 간다.\r\n\r\n15. 곱게 간 북어채를 요리 위에 뿌려 완성한다.'),
	(239, '부드럽끼', NULL, 1, '1. 감자를 반으로 썰어 접시에 담는다.\r\n\r\n2. 물에 적신 키친타올로 감자를 덮은 뒤, 전자레인지에 넣고 익힌다.\r\n\r\n3. 냄비에 우유를 넣은 뒤, 꼬리곰탕을 체에 걸러 넣는다.\r\n\r\n4. 3에 리코타 치즈, 카망베르 치즈를 넣고 끓여 크림소스를 만든다.\r\n\r\n5. 크림소스에 소금, 후추를 넣어 간을 한다.\r\n\r\n6. 크림소스를 핸드블렌더로 곱게 간 뒤, 다시 끓인다.\r\n\r\n7. 팬에 버터를 두르고 잘게 썬 양파를 볶는다.\r\n\r\n8. 양파를 볶던 팬에 크림소스를 부어 끓인다.\r\n\r\n9. 익힌 감자의 껍질을 벗긴다.\r\n\r\n10. 볼에 감자를 넣고 으깬 뒤, 얼음물 위에 얹어 식힌다.\r\n\r\n11. 식힌 감자에 밀가루를 조금씩 넣으며 뇨키 반죽을 만든다.\r\n\r\n12. 뇨키 반죽에 달걀노른자를 섞은 뒤, 길게 늘인다.\r\n\r\n13. 뇨키 반죽을 적당한 크기로 썬 뒤, 포크로 눌러 홈을 만든다.\r\n\r\n14. 끓는 물에 뇨키를 삶는다.\r\n\r\n15. 크림소스에 삶은 뇨키를 넣어 끓인다.\r\n\r\n16. 완성된 뇨키를 접시에 담아낸다.'),
	(240, '유아식망고땡!', NULL, 1, '1. 프라이팬을 냉동실에 넣어 차갑게 만든다.\r\n\r\n2. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n3. 팬에 우유와 꿀을 넣고 끓여 연유처럼 만든다.\r\n\r\n4. 3에 당근을 잘게 썰어 넣는다.\r\n\r\n5. 망고를 곱게 간 뒤, 팬에 넣고 끓여 망고소스를 만든다.\r\n\r\n6. 끓인 우유&꿀에 밥을 넣고, 소금으로 간한다.\r\n\r\n7. 6에 망고소스를 넣고 섞어 망고약밥을 만든다.\r\n\r\n8. 호두를 잘게 다진다.\r\n\r\n9. 다진 호두를 체에 밭쳐 곱게 내린 뒤, 망고약밥과 섞는다.\r\n\r\n10. 차갑게 만든 프라이팬에 망고약밥을 넣고 얇게 편다.\r\n\r\n11. 10을 다시 냉동실에 넣어 식힌다.\r\n\r\n12. 대추와 잣을 잘게 다진다.\r\n\r\n13. 냉동실에서 차게 식힌 망고약밥을 꺼낸다.\r\n\r\n14. 접시에 망고소스를 담는다.\r\n\r\n15. 숟가락으로 망고약밥의 모양을 잡은 뒤, 접시에 담는다.\r\n\r\n16. 다진 잣과 대추를 곁들여 낸다.'),
	(241, '요기조기 탕수', NULL, 1, '1. 조기를 손질한다.\r\n\r\n2. 조기 양쪽에 깊이 칼집을 낸다..\r\n\r\n3. 칼집을 낸 조기에 감자전분을 묻힌다.\r\n\r\n4. 조기 꼬리를 잡고 끓인 기름에 담근 뒤, 국자로 기름을 끼얹는다.\r\n\r\n5. 조기의 모양이 잡히면 기름에 넣고 튀긴다.\r\n\r\n6. 양파, 당근, 파, 청양고추를 채 썬다.\r\n\r\n7. 팬에 채 썬 재료들과 간장, 후추, 물, 식초, 꿀, 불린 표고버섯채를 넣고 끓여 탕수소스를 만든다.\r\n\r\n8. 전분과 물을 섞어 전분물을 만든다.\r\n\r\n9. 튀긴 조기를 건져 기름을 빼고, 접시에 올린다.\r\n\r\n10. 탕수소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n11. 튀긴 조기 위에 탕수소스를 끼얹어 완성한다.'),
	(242, '뒤집었닭', NULL, 1, '1. 반숙란의 노른자만 분리한다.\r\n\r\n2. 핸드블렌더에 반숙란 노른자, 느타리버섯 볶음, 올리브, 소금, 고춧가루, 후추를 넣고 간다.\r\n\r\n3. 닭날개 한 쪽에 칼집을 내 뼈와 살을 분리한 뒤, 살을 뒤집는다.\r\n\r\n4. 뒤집은 닭날개 살 안에 핸드블렌더로 간 재료를 넣고 이쑤시개로 봉한다.\r\n\r\n5. 재료를 채운 닭날개에 소금, 후추로 간을 하고, 기름을 두른 그릴팬에 굽는다.\r\n\r\n6. 삶은 양배추를 채 썬다.\r\n\r\n7. 볼에 채 썬 양배추를 담고, 총각김치 무를 갈아 넣는다.\r\n\r\n8. 7에 다진 총각김치 무청과 올리브오일, 식초를 넣고 버무려 김치 코울슬러를 만든다.\r\n\r\n9. 접시에 김치 코울슬러를 담는다.\r\n\r\n10. 그릴팬에 굽던 닭날개에 맥주를 붓고 끓여 속까지 익힌다.\r\n\r\n11. 닭날개를 고정한 이쑤시개를 뺀다.\r\n\r\n12. 닭날개 뼈에 호일을 감싸고, 접시에 담는다.\r\n\r\n13. 파슬리 가루를 뿌려 완성한다.'),
	(243, '명란 대파주이소', NULL, 1, '1. 청양고추를 잘게 다진다.\r\n\r\n2. 볼에 명란, 참깨, 다진 마늘, 다진 청양고추, 참기름을 넣고 섞는다.\r\n\r\n3. 양념한 명란을 삶은 양배추 위에 놓고 돌돌 만다.\r\n\r\n4. 양배추 명란 말이를 비닐팩에 담아 냉동실에 넣는다.\r\n\r\n5. 대파를 얇게 썬다.\r\n\r\n6. 팬에 올리브 오일을 넉넉히 두르고 얇게 썬 대파와 다진 청양고추를 넣고 파기름을 낸다.\r\n\r\n7. 끓인 파기름을 체에 밭쳐 기름만 걸러낸다.\r\n\r\n8. 채끝등심을 길게 썰고 소금, 후추, 고춧가루로 밑간을 한다.\r\n\r\n9. 팬에 파기름을 두르고 밑간 한 채끝등심을 굽는다.\r\n\r\n10. 대파를 갈라 심을 빼낸 후, 구운 채끝등심을 안에 넣어 감싼다.\r\n\r\n11. 구운 채끝등심을 넣은 대파를 실로 묶어 고정한다.\r\n\r\n12. 마른 팬에 11을 굽는다.\r\n\r\n13. 냉동실에 넣어둔 양배추 명란 말이를 꺼내 적당한 크기로 썬다.\r\n\r\n14. 불린 표고버섯채와 편으로 썬 마늘을 팬에 굽는다.\r\n\r\n15. 채끝등심을 넣은 대파말이가 익으면 실을 제거하고 그릇에 담는다.\r\n\r\n16. 양배추 명란 말이와 구운 표고버섯채를 곁들인다.\r\n\r\n17. 올리브 오일과 후추를 뿌려 완성한다.'),
	(244, '채끝과 당근', NULL, 1, '1. 당근은 얇게 썬다.\r\n\r\n2. 끓는 물에 얇게 썬 당근과 소금을 넣고 삶는다.\r\n\r\n3. 2에 레몬즙을 넣는다.\r\n\r\n4. 삶은 당근을 체에 밭쳐 물기를 뺀다.\r\n\r\n5. 핸드블렌더에 삶은 당근과 버터를 넣고 갈아 퓌레를 만든다.\r\n\r\n6. 당근 퓌레를 냄비에 담아 끓인다.\r\n\r\n7. 삶은 양배추를 채 썰어 당근 퓌레에 넣는다.\r\n\r\n8. 양배추를 넣은 당근 퓌레에 설탕과 레몬껍질을 갈아 넣는다.\r\n\r\n9. 채끝등심을 비닐팩에 넣고 고기망치로 두드려 얇게 편다.\r\n\r\n10. 얇게 편 채끝등심에 소금, 후추로 밑간을 한다.\r\n\r\n11. 밑간을 한 채끝등심에 찹쌀가루를 뿌린다.\r\n\r\n12. 양배추를 넣은 당근 퓌레에 소금으로 간을 한다.\r\n\r\n13. 기름을 두른 팬에 찹쌀가루를 입힌 채끝등심을 굽는다.\r\n\r\n14. 팬 한 쪽에 느타리버섯 볶음을 굽는다.\r\n\r\n15. 고기와 느타리버섯 볶음을 굽던 팬에 레드와인을 붓고 끓인다.\r\n\r\n16. 15에 꿀과 버터를 넣어 레드와인 소스를 만든다.\r\n\r\n17. 팬에 파슬리 가루를 뿌린다.\r\n\r\n18. 접시에 양배추를 넣은 당근 퓌레를 담는다.\r\n\r\n19. 레드와인 소스에 조린 채끝등심과 느타리버섯 볶음을 접시에 담아 완성한다.'),
	(245, '위풍닭닭', NULL, 1, '1. 끓는 물에 호두와 땅콩을 데친다.\r\n\r\n2. 볼에 설탕과 고춧가루를 넣고 섞는다.\r\n\r\n3. 2에 데친 견과류를 넣고 섞는다.\r\n\r\n4. 오븐용 팬에 양념한 견과류를 담아 예열된 오븐에 굽는다.\r\n\r\n5. 견과류를 데친 물에 양파, 당근, 대파를 넣고 끓여 채소육수를 만든다.\r\n\r\n6. 오븐에 구운 견과류를 핸드블렌더에 간다.\r\n\r\n7. 기름을 두른 팬에 양파를 굽고, 소금, 후추로 간한다.\r\n\r\n8. 오븐용 팬에 구운 양파를 담고, 간 견과류를 얹는다.\r\n\r\n9. 팬에 포트와인을 붓고, 잘게 썬 말린 대추를 넣고 끓여 소스를 만든다.\r\n\r\n10. 견과류를 얹은 구운 양파 위에 포트와인 소스를 뿌리고 오븐에 굽는다.\r\n\r\n11. 생닭의 닭다리살만 발라낸다.\r\n\r\n12. 기름을 두른 팬에 닭다리살을 굽고, 소금과 후추로 간한다.\r\n\r\n13. 팬 한 쪽에 적당한 크기로 썬 대파의 흰 부분을 굽는다.\r\n\r\n14. 포트와인 소스에 채소육수를 넣는다.\r\n\r\n15. 포트와인 소스에 잘게 썬 사과, 버터, 꿀을 넣고 조린다.\r\n\r\n16. 접시에 견과류를 올린 구운 양파와 구운 대파를 올린다.\r\n\r\n17. 접시에 구운 닭다리살을 얹고, 포트와인 소스를 곁들여 완성한다.'),
	(246, '미트볼그레', NULL, 1, '1. 양파와 대파를 잘게 썬다.\r\n\r\n2. 돼지고기와 소고기를 적당한 크기로 썬다.\r\n\r\n3. 핸드블렌더에 돼지고기, 소고기, 달걀을 넣고 간다.\r\n\r\n4. 볼에 믹서에 간 고기, 다진 생강, 잘게 썬 양파와 대파, 소금, 간장을 넣는다.\r\n\r\n5. 청주와 달걀을 넣는다.\r\n\r\n6. 볼에 있는 재료들에 전분을 넣고 치대 미트볼 반죽을 만든다.\r\n\r\n7. 미트볼 반죽에 후추를 넣는다.\r\n\r\n8. 동그랗게 뽑아낸 미트볼 반죽을 숟가락으로 하나씩 떼어 기름에 튀긴다.\r\n\r\n9. 사과, 파프리카, 고추, 대파의 흰 부분을 잘게 썬다.\r\n\r\n10. 호두와 잣도 잘게 썬 재료들과 함께 준비한다.\r\n\r\n11. 팬에 포트와인, 맛술, 꿀, 설탕을 넣고 졸여 소스를 만든다.\r\n\r\n12. 튀긴 미트볼을 건져 기름기를 뺀다.\r\n\r\n13. 포트와인 소스에 튀긴 미트볼, 잘게 썬 채소와 견과류를 넣고 섞는다.\r\n\r\n14. 13에 전분물을 넣는다.\r\n\r\n15. 미트볼과 소스를 그릇에 담아낸다.'),
	(247, '장풍덮밥', NULL, 1, '1. 돼지고기와 대파를 적당한 크기로 썬다.\r\n\r\n2. 마늘을 다진다.\r\n\r\n3. 고추를 잘게 썬다.\r\n\r\n4. 기름을 두른 냄비에 돼지고기를 볶는다.\r\n\r\n5. 볶던 돼지고기에 다진 마늘, 고추, 대파, 고춧가루를 넣고 함께 볶는다.\r\n\r\n6. 볶던 재료에 맛술, 간장, 된장을 넣는다.\r\n\r\n7. 볶은 재료에 물을 넣고 끓여 덮밥 소스를 만든다.\r\n\r\n8. 새우살을 적당한 크기로 썰어 덮밥 소스에 넣고 함께 끓인다.\r\n\r\n9. 파프리카를 적당한 크기로 썰어 덮밥 소스에 넣고 함께 끓인다.\r\n\r\n10. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n11. 덮밥 소스에 카레가루를 넣고 끓인다.\r\n\r\n12. 덮밥 소스에 식초를 넣는다.\r\n\r\n13. 기름을 두른 팬에 달걀프라이를 만들다가 물을 넣고 뚜껑을 닫아 윗부분까지 익힌다.\r\n\r\n14. 덮밥 소스에 전분물을 넣는다.\r\n\r\n15. 그릇에 즉석밥을 담고 덮밥 소스를 올린다.\r\n\r\n16. 달걀프라이를 올려 완성한다.'),
	(248, '돼.새.김', NULL, 1, '1. 믹서기에 조미김과 뜨거운 물을 넣고 간다.\r\n\r\n2. 믹서에 간 조미김을 냄비에 넣고 끓인다.\r\n\r\n3. 2에 맛술을 넣고 끓여 김소스를 만든다.\r\n\r\n4. 돼지고기를 잘게 썬다.\r\n\r\n5. 핸드블렌더에 잘게 썬 돼지고기와 생강대추청을 넣는다.\r\n\r\n6. 생강대추청의 생강을 꺼내 칼로 으깬다.\r\n\r\n7. 핸드블렌더에 으깬 생강과 간장, 전분, 달걀 흰자를 넣고 갈아 반죽을 만든다.\r\n\r\n8. 대파를 잘게 다진다.\r\n\r\n9. 새우살을 으깬다.\r\n\r\n10. 새우살과 다진 대파, 핸드블렌더로 간 돼지고기 반죽을 섞는다.\r\n\r\n11. 완자반죽을 동그랗게 떼어 김소스에 넣고 익힌다.\r\n\r\n12. 대파의 흰 부분을 가늘게 채 썰어 찬물에 담가둔다.\r\n\r\n13. 김소스를 팬에 덜어 참기름과 설탕으로 양념한다.\r\n\r\n14. 13에 익힌 완자를 넣고, 간장을 넣어 조린다.\r\n\r\n15. 즉석밥과 완자를 다른 그릇에 담아낸다.\r\n\r\n16. 팬에 남은 김소스에 버터와 전분물을 넣는다.\r\n\r\n17. 완자 위에 김소스를 얹고, 채 썬 대파를 올린다.\r\n\r\n18. 체에 밭쳐 곱게 내린 고춧가루를 올려 완성한다.'),
	(249, '아! 추로스~', NULL, 1, '1. 냄비에 물, 우유, 버터를 넣고 끓인다.\r\n\r\n2. 1에 소금으로 간을 한다.\r\n\r\n3. 냄비 속 재료에 설탕을 더해 함께 끓인다.\r\n\r\n4. 다른 냄비에 블루베리즙과 블루베리, 설탕, 소금, 레몬즙을 넣고 끓여 블루베리 소스를 만든다.\r\n\r\n5. 냄비 속 재료들이 끓으면 밀가루를 넣어가며 익반죽한다.\r\n\r\n6. 얼음물 위에 볼을 놓고 반죽을 넣어 식힌다.\r\n\r\n7. 식힌 반죽에 달걀물을 넣고 섞는다.\r\n\r\n8. 마요네즈 통 윗부분과 끝부분을 자른 비닐팩을 연결해 짤주머니를 만든다.\r\n\r\n9. 짤주머니 안에 반죽을 넣는다.\r\n\r\n10. 끓는 기름에 반죽을 적당한 길이로 잘라 넣어가며 튀긴다.\r\n\r\n11. 튀긴 추로스를 건져 기름기를 제거한다.\r\n\r\n12. 블루베리 소스를 그릇에 담아낸다.\r\n\r\n13. 기름기를 뺀 추로스를 설탕에 버무린다.\r\n\r\n14. 접시에 추로스와 블루베리 소스를 담아낸다.'),
	(250, '퍼펙타르트', NULL, 1, '1. 냄비에 블루베리, 블루베리즙, 설탕을 넣고 끓여 블루베리소스를 만든다.\r\n\r\n2. 기름을 끓여 해시 브라운 포테이토를 튀긴다.\r\n\r\n3. 사과를 얇게 슬라이스 한다.\r\n\r\n4. 슬라이스 한 사과의 물기를 제거하고 그릇에 넓게 편다.\r\n\r\n5. 슬라이스 한 사과를 전자레인지에 넣고 수분을 날려 사과칩을 만든다.\r\n\r\n6. 튀긴 해시 브라운 포테이토를 건져 기름기를 제거한다.\r\n\r\n7. 그릇에 설탕을 얇게 깐 후, 해시 브라운 포테이토를 얹고 위에도 설탕을 뿌린다.\r\n\r\n8. 전자레인지에서 사과칩을 꺼낸다.\r\n\r\n9. 모차렐라 치즈를 볼에 담아 전자레인지에 넣고 녹인다.\r\n\r\n10. 딸기를 먹기 좋은 크기로 썰어 설탕에 버무린다.\r\n\r\n11. 10에 잘게 다진 땅콩을 섞는다.\r\n\r\n12. 랩을 깔고 녹인 모차렐라 치즈를 넓게 편다.\r\n\r\n13. 넓게 편 모차렐라 치즈 위에 설탕에 버무린 딸기와 땅콩을 올리고 돌돌 만다.\r\n\r\n14. 돌돌 만 딸기 치즈말이를 적당한 크기로 썬다.\r\n\r\n15. 블루베리 소스를 체에 밭쳐 내린 후, 접시에 담는다.\r\n\r\n16. 설탕을 입힌 해시 브라운 포테이토 위에 딸기 치즈말이를 올린다.\r\n\r\n17. 블루베리 소스의 블루베리를 올린다.\r\n\r\n18. 블루베리 소스를 담은 접시 위에 플레이팅 한다.\r\n\r\n19. 사과칩을 위에 올려 완성한다.'),
	(251, '한잔비어떡', NULL, 1, '1. 훈제 닭가슴살을 얇게 포 뜬다.\r\n\r\n2. 얇게 포 뜬 훈제 닭가슴살을 잘게 다진다.\r\n\r\n3. 대파를 잘게 썬다.\r\n\r\n4. 밀가루, 소금, 미지근한 물로 반죽을 만든다.\r\n\r\n5. 밀가루 반죽을 밀대로 밀어 넓게 편다.\r\n\r\n6. 넓게 편 반죽에 참기름을 바른다.\r\n\r\n7. 반죽에 소금으로 간을 한다.\r\n\r\n8. 반죽 위에 잘게 썬 닭가슴살과 대파를 골고루 얹는다.\r\n\r\n9. 재료를 넣은 반죽을 돌돌 만다.\r\n\r\n10. 돌돌 말아 길어진 반죽을 동그랗게 다시 한 번 말아준다.\r\n\r\n11. 동그랗게 만 반죽을 밀대로 밀어 편다.\r\n\r\n12. 기름을 두른 팬에 반죽을 넣고 튀긴다.\r\n\r\n13. 반죽을 튀긴 기름을 조금 따라내고 버터를 넣는다.\r\n\r\n14. 다 익으면 먹기 좋게 잘라 그릇에 담아낸다.'),
	(252, '닭고치', NULL, 1, '1. 기름을 두른 팬에 해시 브라운 포테이토를 튀긴다.\r\n\r\n2. 대파와 양파를 잘게 썬다.\r\n\r\n3. 훈제 닭가슴살을 칼 단면으로 내리쳐 결대로 찢는다.\r\n\r\n4. 볼에 고추장, 데리야키 소스, 시럽용 올리고당, 설탕, 고춧가루를 넣어 고추장 양념을 만든다.\r\n\r\n5. 기름을 두른 팬에 잘게 썬 대파와 양파를 볶다가, 고추장 앙념을 넣어 함께 볶는다.\r\n\r\n6. 5에 물을 넣는다.\r\n\r\n7. 결대로 찢은 훈제 닭가슴살을 손으로 더 잘게 찢어 팬에 넣고 볶는다.\r\n\r\n8. 청양고추를 잘게 썰어 볶던 재료들에 넣고 함께 볶는다.\r\n\r\n9. 튀긴 해시 브라운 포테이토를 그릇에 담는다.\r\n\r\n10. 해시 브라운 포테이토 위에 고추장 양념으로 볶은 닭가슴살과 채소를 올린다.\r\n\r\n11. 10 위에 슬라이스 치즈, 모차렐라 치즈, 고추장 양념을 순서대로 얹는다.\r\n\r\n12. 그릇을 전자레인지에 넣고 치즈가 녹을 때까지 익힌다.'),
	(253, '긴밤지새우콩', NULL, 1, '1. 새우는 머리를 제거하고 껍질을 벗긴다.\r\n\r\n2. 감자는 얇게 썰고, 양파는 채 썬다.\r\n\r\n3. 냄비에 버터와 얇게 썬 감자, 채 썬 양파와 완두콩을 넣고 볶는다.\r\n\r\n4. 재료를 볶고 있는 냄비에 소금과 후추를 넣는다.\r\n\r\n5. 볶던 재료에 인스턴트 사골곰탕을 넣고 끓인다.\r\n\r\n6. 5에 고르곤졸라 치즈를 넣는다.\r\n\r\n7. 팬에 버터와 밀가루를 볶아 루를 만든다.\r\n\r\n8. 끓이던 재료에 루를 넣어 끓인다.\r\n\r\n9. 돼지고기의 지방만 분리해 새우살과 함께 후추, 소금으로 간한다.\r\n\r\n10. 핸드블렌더로 새우와 돼지고기 지방을 갈아 완자 반죽을 만든다.\r\n\r\n11. 완자 반죽을 적당한 크기로 동그랗게 떼어내 끓는 물에 삶는다.\r\n\r\n12. 완자를 삶는 물에 쯔유를 넣는다.\r\n\r\n13. 끓이던 재료를 믹서기에 곱게 간다.\r\n\r\n14. 갈아낸 재료를 체에 밭쳐 거르고, 다시 끓인다.\r\n\r\n15. 다 익은 완자를 그릇에 담는다.\r\n\r\n16. 완자를 담은 그릇에 완두콩 스프를 담아 완성한다.'),
	(254, '살빠질톰얌', NULL, 1, '1. 새우는 머리를 제거하고 껍질을 벗긴다.\r\n\r\n2. 채소육수를 만든다.\r\n\r\n3. 채소육수에 새우머리와 껍질을 넣어 함께 끓인다.\r\n\r\n4. 당근과 감자를 잘게 썬다.\r\n\r\n5. 양파를 잘게 다지고 마늘은 편으로 썬다.\r\n\r\n6. 새우를 적당한 크기로 썬다.\r\n\r\n7. 기름을 두른 팬에 새우, 마늘을 넣고 볶는다.\r\n\r\n8. 베트남 고추를 잘게 썰어 팬에 넣고 소금으로 간한다.\r\n\r\n9. 잘게 썬 당근과 감자를 팬에 넣고 함께 볶는다.\r\n\r\n10. 볶던 재료에 톰얌 페이스트를 넣고 볶는다.\r\n\r\n11. 10에 새우채소육수를 넣고 끓인다.\r\n\r\n12. 액상조미료를 넣는다.\r\n\r\n13. 기름을 두른 그릴팬에 토마토, 새우살을 굽다가 소금, 후추로 간한다.\r\n\r\n14. 끓는 물을 저어 회오리를 만들고 달걀을 넣어 수란을 만든다.\r\n\r\n15. 그릇에 톰얌 수프를 담는다.\r\n\r\n16. 대파의 흰 부분을 잘라 그릴팬에 굽는다.\r\n\r\n17. 톰얌 수프 위에 구운 토마토, 새우살, 대파를 얹는다.\r\n\r\n18. 17 위에 수란을 올리고, 잘게 썬 대파를 얹어 마무리 한다.'),
	(255, '한잔하새우', NULL, 1, '1. 자숙새우살을 잘게 다져 두 개의 볼에 나눠 담는다.\r\n\r\n2. 한 쪽 볼에 향쑥리큐어를 넣는다.\r\n\r\n3. 2에 칠리소스를 넣는다.\r\n\r\n4. 양파를 잘게 썰어 양 쪽 볼에 담는다.\r\n\r\n5. 올리브와 할라페뇨를 잘게 썰어 양 쪽 볼에 담는다.\r\n\r\n6. 낙지젓을 잘게 다져 양 쪽 볼에 담는다.\r\n\r\n7. 식빵을 토스터기에 넣고 굽는다.\r\n\r\n8. 소스를 넣지 않은 재료에 머스터드소스, 마요네즈, 소금, 후추, 올리브 오일을 넣고 섞는다.\r\n\r\n9. 두 개의 칵테일 잔에 잘게 썬 알배추를 넣는다.\r\n\r\n10. 한 개의 잔에 머스터드소스&마요네즈에 버무린 재료를 넣는다.\r\n\r\n11. 조미김을 잘라서 재료에 꽂아 장식한다.\r\n\r\n12. 구운 식빵을 적당한 크기로 잘라 재료에 꽂고, 멸치볶음을 얹어 장식한다.\r\n\r\n13. 칠리소스로 양념한 재료에 후추를 넣는다.\r\n\r\n14. 13에 잘게 자른 토마토와 레몬즙을 넣는다.\r\n\r\n15. 알배추를 깐 칵테일 잔에 칠리소스로 양념한 재료를 넣고 구운 식빵으로 장식한다.\r\n\r\n16. 레몬과 올리브로 장식해 완성한다.'),
	(256, '뱅뱅뱅볶음', NULL, 1, '1. 양파를 채 썬다.\r\n\r\n2. 채 썬 양파를 얼음물에 담가둔다.\r\n\r\n3. 볼에 고춧가루, 간장, 설탕, 참기름, 맛술을 섞어 양념을 만든다.\r\n\r\n4. 얼음물에 담가뒀던 양파를 건져 양념에 버무린다.\r\n\r\n5. 식용유와 들기름을 두른 팬에 다진 마늘을 넣고 볶는다.\r\n\r\n6. 알배추와 청양고추를 썬다.\r\n\r\n7. 볶던 다진 마늘에 잘게 썬 청양고추를 넣고 볶다가 간장으로 간한다.\r\n\r\n8. 물기를 뺀 골뱅이 통조림을 적당한 크기로 썰어 팬에 넣고 함께 볶는다.\r\n\r\n9. 잘게 썬 알배추와 들깨가루를 골뱅이를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n10. 접시에 양념에 버무린 양파를 담는다.\r\n\r\n11. 골뱅이와 알배추를 볶던 팬에 인스턴트 사골곰탕을 넣는다.\r\n\r\n12. 접시에 골뱅이 알배추 볶음을 담아 완성한다.'),
	(257, '소풍가는 닭', NULL, 1, '1. 감자는 껍질을 벗기고 얇게 썬다.\r\n\r\n2. 소금을 넣은 끓는 물에 얇게 썬 감자를 넣고 삶는다.\r\n\r\n3. 양배추를 채 썬다.\r\n\r\n4. 기름을 두른 팬에 채 썬 양배추와 다진 파, 데친 브로콜리를 넣고 볶는다.\r\n\r\n5. 볶던 재료에 닭안심살을 넣고 소금과 후추로 간한다.\r\n\r\n6. 5에 시금치를 넣고 함께 볶는다.\r\n\r\n7. 허니버터 브레드를 잘게 썰어 믹서기에 간다.\r\n\r\n8. 보코치니 치즈의 물기를 뺀다.\r\n\r\n9. 물기를 뺀 보코치니 치즈를 적당한 크기로 자른다.\r\n\r\n10. 보코치니 치즈에 밀가루>달걀물>밀가루>달걀물>빵가루 순으로 튀김옷을 입혀 튀긴다.\r\n\r\n11. 튀긴 치즈의 기름을 빼고, 소금 후추로 간한다.\r\n\r\n12. 넓게 편 명이나물 장아찌에 익힌 닭안심살을 넣고 돌돌 말아 적당한 크기로 썬다.\r\n\r\n13. 삶은 감자를 건져 체에 밭쳐 으깬다.\r\n\r\n14. 으깬 감자에 버터, 소금 후추를 넣고 섞는다.\r\n\r\n15. 냄비에 크림소스와 물, 다진 마늘, 명이나물 장아찌국물, 후추를 넣어 명이크림소스를 만든다.\r\n\r\n16. 접시에 으깬 감자를 올리고, 그 위에 볶은 채소를 올린다.\r\n\r\n17. 16위에 명이크림 소스를 올린다.\r\n\r\n18. 명이나물 닭안심말이와 튀긴 치즈를 번갈아 올리고 완성한다.'),
	(258, '함박웃음치~즈', NULL, 1, '1. 냄비에 물, 맛술, 간장, 설탕을 2:1:1:1 비율로 넣고, 청주를 더해 불고기 양념을 끓인다.\r\n\r\n2. 볼에 설탕, 물, 식초를 1:1:1 비율로 넣고 섞어 초절임소스를 만든다.\r\n\r\n3. 동치미와 미니 파프리카를 적당한 크기로 썬다.\r\n\r\n4. 오이의 양면에 칼집을 촘촘하게 낸다.\r\n\r\n5. 칼집을 낸 오이에 소금을 뿌린다.\r\n\r\n6. 칼집 낸 오이, 적당한 크기로 썬 동치미, 미니 파프리카를 초절임소스에 넣고 절인다.\r\n\r\n7. 믹서에 치마살과 달걀을 넣고 간다.\r\n\r\n8. 7에 소금, 후추 간을 한다.\r\n\r\n9. 양파를 잘게 썰어 다진 고기에 넣는다.\r\n\r\n10. 9에 전분을 넣고 치대어 햄버그 스테이크 반죽을 만든다.\r\n\r\n11. 슬라이스 치즈에 버터를 넣어 감싼다.\r\n\r\n12. 패티 반죽에 사이에 버터를 감싼 치즈를 넣고 동그랗게 뭉친다.\r\n\r\n13. 기름을 두른 팬에 패티를 넣고 굽는다.\r\n\r\n14. 패티를 굽는 팬에 청주를 붓고 뚜껑을 닫아 패티 속까지 익힌다.\r\n\r\n15. 익힌 패티에 불고기 양념을 붓고 끼얹어가며 조린다.\r\n\r\n16. 전자레인지에 냉동된 밥을 넣고 돌린다.\r\n\r\n17. 초절임한 오이를 적당한 크기로 썬다.\r\n\r\n18. 접시에 초절임한 오이, 동치미, 미니 파프리카를 올린다.\r\n\r\n19. 멸치볶음과 깨, 데운 밥을 섞고 뭉쳐서 주먹밥을 만들어 접시에 올린다.\r\n\r\n20. 접시에 햄버그 스테이크를 올리고 조린 양념을 뿌려 마무리한다.'),
	(259, '육해공 연볶이', NULL, 1, '1. 대파와 마늘을 적당한 크기로 썬다.\r\n\r\n2. 냄비에 적당하게 썬 대파와 마늘, 닭날개, 고춧가루, 물을 넣고 끓인다.\r\n\r\n3. 가래떡을 적당한 크기로 썬다.\r\n\r\n4. 끓이던 재료에 적당한 크기로 썬 가래떡과 고추장을 넣는다.\r\n\r\n5. 4에 고형카레를 넣는다.\r\n\r\n6. 통조림 햄을 얇게 썰어 기름을 두른 팬에 굽는다.\r\n\r\n7. 애호박을 적당한 크기로 썰어 떡볶이에 넣는다.\r\n\r\n8. 수제비 반죽을 적당한 크기로 떼어 떡볶이에 넣는다.\r\n\r\n9. 떡볶이에 콩나물과 설탕을 넣는다.\r\n\r\n10. 떡볶이에 간장과 문어숙회를 넣는다.\r\n\r\n11. 떡볶이에 설탕과 청주를 넣는다.\r\n\r\n12. 구운 통조림 햄을 적당한 크기로 썰어 떡볶이에 넣고 끓인다.\r\n\r\n13. 모차렐라 치즈를 넣는다.\r\n\r\n14. 대파를 잘게 썬다.\r\n\r\n15. 완성된 떡볶이를 그릇에 담고 잘게 썬 대파를 올려 마무리한다.'),
	(260, '너는 힙합! 나는 햄팝!', NULL, 1, '1. 팬에 설탕과 물을 붓고 끓여 졸인다.\r\n\r\n2. 볼에 얼음, 물, 밀가루를 넣는다.\r\n\r\n3. 2에 전분을 더해 튀김옷을 만든다.\r\n\r\n4. 통조림 햄을 깍둑썰기 한다.\r\n\r\n5. 깍둑썰기 한 통조림 햄에 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n6. 튀긴 통조림 햄을 건져 기름기를 뺀다.\r\n\r\n7. 졸인 설탕에 땅콩버터를 넣고 섞는다.\r\n\r\n8. 7에 튀긴 햄을 넣고 버무린다.\r\n\r\n9. 그릇에 담아낸다.'),
	(261, '쌈삼한 리소토', NULL, 1, '1. 기름을 두른 팬에 잘게 썬 양파와 다진 마늘, 얼린 밥을 넣고 볶는다.\r\n\r\n2. 삼계탕을 체에 밭쳐 건더기와 국물을 분리한다.\r\n\r\n3. 삼계탕 건더기의 국물을 완전히 짜낸 후 잘게 다진다.\r\n\r\n4. 밥을 볶던 팬에 삼계탕 국물을 붓고 끓여 리소토를 만든다.\r\n\r\n5. 리소토에 소금, 후추로 간한다.\r\n\r\n6. 믹서기에 꽃게맛 과자와 치즈맛 과자, 고춧가루를 넣고 간다.\r\n\r\n7. 잘게 다진 삼계탕 건더기를 동그랗게 뭉친 뒤 밀가루>달걀물>과자가루를 입혀 기름에 튀긴다.\r\n\r\n8. 청양고추와 묵은지, 무김치를 잘게 썬다.\r\n\r\n9. 팬에 잘게 썬 김치를 넣고, 깨, 버터, 고춧가루, 삼계탕 국물을 넣고 볶는다.\r\n\r\n10. 튀긴 삼계볼을 건져 기름기를 뺀다.\r\n\r\n11. 마른 팬에 리소토를 옮겨 담고 파르메산 치즈분말을 넣고 볶는다.\r\n\r\n12. 그릇에 리소토를 담고 튀긴 삼계볼을 올린다.\r\n\r\n13. 12 위에 볶은 김치를 올린다.\r\n\r\n14. 깨와 잘게 썬 청양고추를 얹어 마무리한다.'),
	(262, '쌈닭 파스타', NULL, 1, '1. 기름을 두른 냄비에 적당한 크기로 썬 당근과 양파를 넣고 볶는다.\r\n\r\n2. 갈비탕을 체에 밭쳐 국물만 걸러낸다.\r\n\r\n3. 걸러낸 갈비탕 국물을 채소가 담긴 냄비에 넣고 끓여 육수를 낸다.\r\n\r\n4. 끓는 물에 소금을 넣고, 파스타 면을 삶는다.\r\n\r\n5. 마늘을 잘게 다진다.\r\n\r\n6. 기름을 두른 팬에 다진 마늘과 매운 양념숯불치킨을 넣고 함께 볶는다.\r\n\r\n7. 볶던 치킨에 후추, 유자소주, 채소갈비탕육수를 넣고 끓여 치킨갈비탕소스를 만든다.\r\n\r\n8. 익은 파스타면을 건져 치킨갈비탕소스에 넣고 볶는다.\r\n\r\n9. 파스타에 소금으로 간을 한다.\r\n\r\n10. 파스타에 파르메산 치즈분말을 뿌리고 섞는다.\r\n\r\n11. 그릇에 파스타를 담아 완성한다.'),
	(263, '소울 푸드의 고참', NULL, 1, '1. 냄비에 적당한 크기로 썬 당근, 설탕, 물을 넣고 끓여 당근육수를 만든다.\r\n\r\n2. 스위트콘 통조림을 체에 밭쳐 물기를 뺀다.\r\n\r\n3. 물기를 뺀 스위트콘과 버터를 팬에 넣고 볶는다.\r\n\r\n4. 3에 당근육수를 넣고 끓인다.\r\n\r\n5. 즉석밥을 전자레인지에 데운다.\r\n\r\n6. 마늘을 편으로 썰어 기름에 튀긴다.\r\n\r\n7. 볼에 고추참치 통조림과 마요네즈를 넣는다.\r\n\r\n8. 다른 볼에 토마토 소스와 레몬즙을 넣고 섞는다.\r\n\r\n9. 9에 올리브오일, 후추, 소금을 넣고 섞어 소스를 만든다.\r\n\r\n10. 전자레인지에서 즉석밥을 꺼내 고추참치, 마요네즈와 함께 버무린다.\r\n\r\n11. 버무린 밥에 소금, 후추로 간한다.\r\n\r\n12. 당근육수와 함께 끓인 스위트콘을 핸드블렌더로 간다.\r\n\r\n13. 기름에 튀긴 마늘을 건져낸다.\r\n\r\n14. 기름에 튀긴 마늘을 잘게 다진다.\r\n\r\n15. 고추참치에 버무린 밥에 잘게 다진 튀긴 마늘을 넣고 섞는다.\r\n\r\n16. 랩을 펼쳐 양념한 밥을 넣고 동그랗게 말아 기름을 두른 팬에 굽는다.\r\n\r\n17. 다른 팬에 맛술과 간장을 넣어 간장소스를 끓인다.\r\n\r\n18. 구운 주먹밥에 버터와 간장소스를 넣고 조린다.\r\n\r\n19. 채 썬 양파에 전분을 묻혀 기름에 튀긴다.\r\n\r\n20. 튀긴 양파를 레몬즙과 섞은 토마토 소스에 넣고 무친다.\r\n\r\n21. 그릇에 곱게 간 스위트콘을 담은 뒤, 구운 주먹밥을 올린다.\r\n\r\n22. 소스에 무친 튀긴 양파를 얹어 마무리한다.'),
	(264, '옥수스웨그 팬케이크', NULL, 1, '1. 냄비에 물, 인스턴트 체다치즈 수프, 파르메산 치즈분말을 넣고 끓인다.\r\n\r\n2. 매운 양념숯불치킨을 잘게 썰어 수프에 넣고 끓인다.\r\n\r\n3. 스위트콘 통조림을 체에 밭쳐 물기를 뺀다.\r\n\r\n4. 볼에 물기를 뺀 스위트콘과 달걀을 넣는다.\r\n\r\n5. 4에 양파와 오이피클을 잘게 썰어 넣은 뒤 소금 후추로 간한다.\r\n\r\n6. 청양고추를 잘게 썰어 넣는다.\r\n\r\n7. 볼에 담긴 각종 재료를 핸드블렌더로 간다.\r\n\r\n8. 핸드블렌더로 간 재료들에 밀가루를 넣고 팬케이크 반죽을 만든다.\r\n\r\n9. 기름을 두른 팬에 팬케이크 반죽을 넓게 펴고 굽는다.\r\n\r\n10. 팬케이크에 후추를 뿌려 간한다.\r\n\r\n11. 고추참치 통조림을 체에 밭쳐 기름기를 뺀다.\r\n\r\n12. 볼에 고추참치, 잘게 썬 양파, 소금, 후추, 잘게 썬 피클, 토마토 소스를 넣고 섞는다.\r\n\r\n13. 양념한 고추참치를 접시에 담는다.\r\n\r\n14. 스위트콘 팬케이크를 적당한 크기로 썰어 접시에 담고, 치킨치즈수프를 올린다.\r\n\r\n15. 레몬슬라이스를 얹어 마무리한다.'),
	(265, '등심빵빵', NULL, 1, '1. 식빵의 테두리를 잘라내고 4등분한다.\r\n\r\n2. 다른 식빵은 테두리를 잘라내고 잘게 썬다.\r\n\r\n3. 등심을 적당한 크기로 썬다.\r\n\r\n4. 믹서기에 달걀, 버터, 적당한 크기로 썬 등심, 생강즙을 넣고 간다.\r\n\r\n5. 4에 전분, 매실맛 후리카케를 넣고 섞어 완자 반죽을 만든다.\r\n\r\n6. 완자 반죽을 동그랗게 빚어 식빵 사이에 넣는다.\r\n\r\n7. 완자를 끼운 식빵을 기름에 튀긴다.\r\n\r\n8. 완자 반죽을 동그랗게 빚어 잘게 썬 식빵을 묻힌 뒤, 기름에 튀긴다.\r\n\r\n9. 볼에 케첩, 잘게 다진 양파, 레몬즙을 넣는다.\r\n\r\n10. 9에 석류즙을 더해 소스를 만든다.\r\n\r\n11. 튀겨진 등심 식빵 튀김을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n12. 소스를 따로 담아낸다.'),
	(266, '달콤하게 튀겼지우', NULL, 1, '1. 사과는 껍질째 썰고 원형틀을 이용해 도넛 모양으로 만든다.\r\n\r\n2. 브리 치즈는 동그랗게 썰고 원형틀을 이용해 도넛 모양으로 만든다.\r\n\r\n3. 팬에 블루베리, 과일맛 맥주, 설탕을 넣고 졸여 블루베리 소스를 만든다.\r\n\r\n4. 볼에 달걀, 설탕, 밀가루, 스파클링 사과주스를 넣고 섞어 튀김옷을 만든다.\r\n\r\n5. 사과에 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n6. 튀긴 사과를 건져 기름기를 뺀다.\r\n\r\n7. 핸드블렌더에 호두와 아몬드를 넣고 간다.\r\n\r\n8. 갈아낸 호두와 아몬드를 반죽에 섞는다.\r\n\r\n9. 브리 치즈에 호두와 아몬드를 섞은 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n10. 소스볼에 블루베리 소스를 담는다.\r\n\r\n11. 튀긴 브리 치즈를 건져 기름기를 뺀다.\r\n\r\n12. 접시에 사과 튀김과 브리 치즈 튀김을 올리고, 블루베리 소스를 얹어 완성한다.'),
	(267, '여심전복', NULL, 1, '1. 전복을 손질한다.\r\n\r\n2. 손질한 전복을 끓는 물에 삶는다.\r\n\r\n3. 올리브의 씨를 발라낸다.\r\n\r\n4. 핸드블렌더에 올리브, 케이퍼, 하몬, 올리브오일을 넣고 갈아 소스를 만든다.\r\n\r\n5. 배추를 잘게 썬다.\r\n\r\n6. 삶은 전복을 건져 적당한 크기로 썬다.\r\n\r\n7. 전복 껍데기에 잘게 썬 배추를 깔고, 데친 전복을 올린다.\r\n\r\n8. 데친 전복 위에 소스를 얹는다.\r\n\r\n9. 핸드블렌더로 소금과 석류알을 함께 간다.\r\n\r\n10. 9을 소금에 넣고 섞어 석류소금을 만든다.\r\n\r\n11. 접시에 석류소금을 올리고, 레몬껍질을 갈아서 뿌린다.\r\n\r\n12. 11 위에 소스를 얹은 전복을 올린다.\r\n\r\n13. 전복 위에 영양부추와 망고맛 젤리를 올려 완성한다.'),
	(268, '여식공략', NULL, 1, '1. 배추, 영양부추, 콜라비를 잘게 썰어 볼에 담는다.\r\n\r\n2. 1에 반건조 문어다리, 게맛살도 잘게 썰어 넣는다.\r\n\r\n3. 잘게 썬 재료에 올리브오일, 후추를 넣고 섞어 샐러드를 만든다.\r\n\r\n4. 샐러드에 레몬즙을 뿌린다.\r\n\r\n5. 핸드블렌더에 믹스너츠를 넣고 간다.\r\n\r\n6. 크림치즈를 작게 잘라 간 믹스너츠를 입힌다.\r\n\r\n7. 아스파라거스와 두릅을 손질해 소금을 넣은 끓는 물에 데친다.\r\n\r\n8. 데친 아스파라거스와 두릅을 체에 밭쳐 물기를 뺀다.\r\n\r\n9. 올리브를 적당한 크기로 썬다.\r\n\r\n10. 믹서기에 아보카도, 물을 넣고 간 뒤 체에 밭쳐 내린다.\r\n\r\n11. 아보카도 소스에 소금, 후추, 레몬즙을 넣는다.\r\n\r\n12. 접시에 아보카도 소스를 깔고, 샐러드를 올린다.\r\n\r\n13. 발사믹 식초에 올리브오일을 섞는다.\r\n\r\n14. 물기를 뺀 아스파라거스와 두릅에 발사믹오일을 넣고 섞는다.\r\n\r\n15. 샐러드 위에 양념한 아스파라거스와 두릅, 믹스너츠를 묻힌 크림치즈를 얹는다.\r\n\r\n16. 하몬을 돌돌 말아 적당한 크기로 썬다.\r\n\r\n17. 발사믹오일을 뿌리고 적당한 크기로 썬 하몬과 올리브를 얹어 완성한다.'),
	(269, '최후의 소보로', NULL, 1, '1. 식빵을 토스트기에 넣고 굽는다.\r\n\r\n2. 핸드블렌더로 잣을 곱게 간다.\r\n\r\n3. 볼에 잣가루, 밀가루, 설탕, 버터, 달걀노른자를 넣고 치대어 소보로 반죽을 만든다.\r\n\r\n4. 팬에 소보로 반죽을 넣고 굽는다.\r\n\r\n5. 구운 식빵의 한 면에 파인애플&망고 잼을 바르고, 소보로 반죽 위에 얹는다.\r\n\r\n6. 볼에 달걀, 밀가루, 설탕을 넣고 잘 섞어 튀김옷을 만든다.\r\n\r\n7. 튀김옷에 우유와 소금을 넣고 섞는다.\r\n\r\n8. 식빵을 적당한 크기로 잘라 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n9. 튀긴 식빵을 건져 기름기를 빼고, 설탕을 입힌다.\r\n\r\n10. 접시에 소보로 반죽과 함께 구운 식빵과 튀긴 식빵을 함께 담아낸다.'),
	(270, '봉주르마끼', NULL, 1, '1. 식빵의 테두리를 잘라낸다.\r\n\r\n2. 테두리를 잘라낸 식빵을 밀대로 밀어 얇게 만든다.\r\n\r\n3. 잘라낸 식빵 테두리를 기름에 튀긴 뒤, 기름기를 빼고 설탕을 입힌다.\r\n\r\n4. 사과, 양파, 당근을 채 썬다.\r\n\r\n5. 기름을 두른 팬에 채 썬 양파와 당근을 볶는다.\r\n\r\n6. 같은 팬에 베이컨을 굽는다.\r\n\r\n7. 게맛살을 잘게 찢은 후, 마요네즈를 넣어 무친다.\r\n\r\n8. 달걀노른자를 풀어, 밀대로 얇게 민 식빵에 바른다.\r\n\r\n9. 달걀노른자를 바른 식빵에 양념한 게맛살, 구운 베이컨,\r\n볶은 채소와 채 썬 사과를 넣고 말아 롤을 만든다.\r\n\r\n10. 전자레인지로 버터를 녹인다.\r\n\r\n11. 볼에 달걀, 설탕, 밀가루, 소금, 맛술, 우유를 넣는다.\r\n\r\n12. 11에 녹인 버터를 넣어가며 잘 섞어 달걀물을 만든다.\r\n\r\n13. 사각팬에 달걀물을 붓고 식빵롤을 얹어 돌돌 만다.\r\n\r\n14. 달걀물을 계속 부어가며 식빵롤에 달걀옷을 여러 겹으로 입힌다.\r\n\r\n15. 달걀말이롤을 적당한 크기로 썬다.\r\n\r\n16. 접시에 달걀말이롤을 담고, 식빵튀김을 곁들여 낸다.'),
	(271, '어무이라이스', NULL, 1, '1. 마늘, 대파, 당근, 양파를 잘게 썬다.\r\n\r\n2. 전자레인지에 즉석밥을 데운다.\r\n\r\n3. 기름을 두른 팬에 차돌박이를 굽는다.\r\n\r\n4. 구운 차돌박이를 덜어내고 난 기름에 잘게 썬 채소를 볶다가 소금 후추로 간한다.\r\n\r\n5. 볶던 채소에 데운 즉석밥을 넣고 볶는다.\r\n\r\n6. 볶음밥에 구운 차돌박이를 잘게 썰어 넣고 함께 볶는다.\r\n\r\n7. 팬에 맛술과 간장을 넣고 끓여 간장양념을 만든다.\r\n\r\n8. 다른 팬에 기름을 두르고, 잘게 썬 베이컨을 볶는다.\r\n\r\n9. 마늘을 다져 베이컨을 볶던 팬에 넣고 함께 볶는다.\r\n\r\n10. 볶던 재료에 식초를 넣는다.\r\n\r\n11. 10에 간장양념을 넣고 끓여 오므라이스 소스를 만든다.\r\n\r\n12. 전분물을 만들어 오므라이스 소스에 넣는다.\r\n\r\n13. 대파를 가늘게 채 썬다.\r\n\r\n14. 볼에 채 썬 파를 넣고, 식초, 간장, 고춧가루, 설탕, 올리브오일을 넣고 양념한다.\r\n\r\n15. 달걀을 풀어 기름을 두른 팬에 붓고 지단을 부친다.\r\n\r\n16. 지단 한 쪽에 볶음밥을 올리고 반으로 접는다.\r\n\r\n17. 접시에 오므라이스를 옮겨 담는다.\r\n\r\n18. 오므라이스 위에 양념한 파채와 소스를 담고 깨를 뿌려 마무리한다.'),
	(272, '돼새찌개', NULL, 1, '1. 양파와 대파를 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 팬에 양파를 먼저 볶다가, 대파를 넣고 함께 볶는다.\r\n\r\n3. 삼겹살을 적당한 크기로 썰어 팬에 넣고 함께 볶는다.\r\n\r\n4. 3에 다진 마늘을 넣는다.\r\n\r\n5. 볶던 재료에 물을 넣고 끓인다.\r\n\r\n6. 찌개가 끓으면 불순물을 걷어내며 계속해서 끓인다.\r\n\r\n7. 불린 목이버섯을 적당한 크기로 썰어, 찌개에 넣고 함께 끓인다.\r\n\r\n8. 찌개에 새우젓과 간장을 넣어 간한다.\r\n\r\n9. 청양고추와 마늘을 잘게 썬다.\r\n\r\n10. 냄비에 식용유, 잘게 썬 청양고추와 마늘, 고춧가루를 넣고 끓여 고추기름을 낸다.\r\n\r\n11. 전자레인지에 즉석밥을 데운다.\r\n\r\n12. 즉석밥을 밥그릇에 담는다.\r\n\r\n13. 그릇에 찌개를 옮겨 담고, 고추기름을 얹어 낸다.'),
	(273, '못 먹어도 고! 등어', NULL, 1, '1. 고등어의 껍질을 제거한다.\r\n\r\n2. 양파를 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n3. 껍질을 벗긴 고등어에 밀가루를 묻힌다.\r\n\r\n4. 다른 팬에 기름을 두른 뒤, 밀가루를 묻힌 고등어를 굽고 후추를 뿌린다.\r\n\r\n5. 굽던 고등어에 화이트와인을 뿌린다.\r\n\r\n6. 방울토마토를 잘게 썬다.\r\n\r\n7. 알루미늄 포일에 올리브오일을 뿌리고, 볶은 양파와 잘게 썬 방울토마토를 얹는다.\r\n\r\n8. 7에 팬에서 초벌구이한 고등어를 올리고 소금을 뿌린다.\r\n\r\n9. 고등어 위에 잘게 썬 방울토마토를 다시 얹고, 후추를 뿌린다.\r\n\r\n10. 포일을 덮기 전 올리브오일을 뿌리고, 반으로 접은 뒤 테두리를 봉한다.\r\n\r\n11. 올리브오일을 뿌린 오븐용 그릇에 각종 재료가 담긴 포일을 올리고, 예열된 오븐에서 익힌다.\r\n\r\n12. 명란젓의 물기를 제거한다.\r\n\r\n13. 냄비에 버터, 밀가루, 우유, 코코넛 밀크, 소금, 후추, 명란젓을 넣고 끓여 명란크림소스를 만든다.\r\n\r\n14. 오븐에서 재료가 담긴 포일을 꺼내 접시에 올린다.\r\n\r\n15. 소스볼에 명란크림소스를 따로 담아낸다.'),
	(274, '맛있을 고양', NULL, 1, '1. 양파는 밑둥을 잘라내고, 칼집을 여러 개 낸 뒤 손으로 벌려 꽃모양을 만든다.\r\n\r\n2. 볼에 밀가루, 전분, 라면스프를 넣고 섞어 튀김가루를 만든다.\r\n\r\n3. 꽃모양으로 만든 양파에 튀김가루를 골고루 묻힌다.\r\n\r\n4. 볼에 달걀을 푼 뒤, 맥주를 넣고 섞는다.\r\n\r\n5. 튀김가루를 묻힌 양파에 맥주를 넣은 달걀물을 묻히고, 다시 튀김가루를 입힌다.\r\n\r\n6. 5를 기름에 튀긴다.\r\n\r\n7. 고등어를 적당한 크기로 썰어 끓는 물에 익힌다.\r\n\r\n8. 고등어를 익히는 냄비에 맛술을 넣는다.\r\n\r\n9. 믹서기에 고형카레, 익힌 고등어, 우유를 넣고 간다.\r\n\r\n10. 팬에 버터와 밀가루를 넣고 볶아 루를 만든다.\r\n\r\n11. 루에 믹서기에 간 재료들과 소금을 넣고 끓여 고등어소스를 만든다.\r\n\r\n12. 고등어소스에 레몬즙을 넣는다.\r\n\r\n13. 접시에 고등어소스를 뿌려 데커레이션 한다.\r\n\r\n14. 튀긴 양파를 건져 접시에 올린다.\r\n\r\n15. 소스볼에 고등어소스를 따로 담아낸다.'),
	(275, '싹쓸이 팬', NULL, 1, '1. 방울토마토의 꼭지를 떼어낸다.\r\n\r\n2. 양파를 채 썬다.\r\n\r\n3. 풋고추는 꼭지와 씨를 제거하고 잘게 썬다.\r\n\r\n4. 기름을 두른 팬에 채 썬 양파와 잘게 썬 풋고추를 볶는다.\r\n\r\n5. 방울토마토를 적당한 크기로 썰어 팬에 넣고 함께 볶는다.\r\n\r\n6. 볶던 재료에 페타 치즈를 손으로 으깨어 넣는다.\r\n\r\n7. 6에 달걀을 넣고 잘 섞는다.\r\n\r\n8. 후추를 뿌려 마무리 한다.'),
	(276, '설레기 없수', NULL, 1, '1. 종이포일 가운데에 고형카레를 으깨어 바른다.\r\n\r\n2. 훈제 닭가슴살을 슬라이스 해서 고형카레 위에 얹는다.\r\n\r\n3. 종이포일 테두리를 따라 버터를 바른다.\r\n\r\n4. 종이포일을 반으로 접은 후, 테두리를 따라 돌돌 만다.\r\n\r\n5. 재료를 넣은 종이포일 안에 코코넛 밀크를 붓고 완전히 봉한다.\r\n\r\n6. 종이포일이 깔린 팬 위에 재료를 넣고 봉한 종이포일을 넣은 뒤, 뚜껑을 닫고 익힌다.\r\n\r\n7. 말린 바나나를 잘게 썬다.\r\n\r\n8. 아이스 망고바와 아이스 파인애플바, 육포를 잘게 썰어 섞고 소금, 후추로 간한다.\r\n\r\n9. 접시에 잘게 썬 말린 바나나를 올리고, 그 위에 간을 한 망고, 파인애플, 육포를 얹는다.\r\n\r\n10. 종이포일 안에 넣고 익힌 닭가슴살 슬라이스와 소스를 접시에 담는다.\r\n\r\n11. 양념한 망고, 파인애플, 육포를 한 번 더 올려 마무리 한다.'),
	(277, '쌈싸시지', NULL, 1, '1. 볼에 밀가루, 소금, 뜨거운 물, 식용유를 넣고 치대 익반죽한다.\r\n\r\n2. 반죽을 길게 밀어준 뒤, 적당한 크기로 떼어낸다.\r\n\r\n3. 적당한 크기로 떼어낸 반죽을 밀대로 밀어 얇고 동그랗게 펴서 피를 만든다.\r\n\r\n4. 찜기에 넓게 편 피를 찐다.\r\n\r\n5. 소고기 등심을 채 썬다.\r\n\r\n6. 볼에 채 썬 등심, 전분, 달걀물을 넣고 섞는다.\r\n\r\n7. 6을 넉넉한 기름에 튀기듯이 볶는다.\r\n\r\n8. 청양고추는 잘게 썰고, 양파, 당근을 채 썬다.\r\n\r\n9. 볶은 등심은 체에 밭쳐 기름을 빼고, 등심을 볶았던 팬에 채 썬 청양고추를 볶는다.\r\n\r\n10. 대파를 적당한 크기로 썬다.\r\n\r\n11. 팬에 적당한 크기로 썬 대파, 채 썬 양파, 당근을 넣고 함께 볶는다.\r\n\r\n12. 볶던 채소에 간장을 넣고 볶은 등심을 넣어 함께 볶는다.\r\n\r\n13. 기름을 두른 팬에 다진 마늘과 다진 대파를 넣고 볶는다.\r\n\r\n14. 13에 매실맛 술, 땅콩버터, 간장을 넣고 끓여 땅콩소스를 만든다.\r\n\r\n15. 볶던 등심&채소와 땅콩소스에 액상 조미료를 넣는다.\r\n\r\n16. 볶던 등심&채소에 후추를 넣는다.\r\n\r\n17. 어육소시지를 채 썬다\r\n\r\n18. 찜기에 찐 피와 등심&채소볶음을 접시에 따로 담고, 땅콩소스도 소스볼에 담아낸다.\r\n\r\n19. 등심&채소볶음 위에 채 썬 어육소시지를 올려 마무리한다.'),
	(278, '살몬의 지혜', NULL, 1, '1. 연어 통조림의 기름기를 뺀다.\r\n\r\n2. 양파를 잘게 다진다.\r\n\r\n3. 훈제닭가슴살소시지를 잘게 다진다.\r\n\r\n4. 잘게 다진 양파와 훈제닭가슴살소시지를 각각 기름을 두른 팬에 볶는다.\r\n\r\n5. 연어 통조림이 담긴 볼에 볶은 재료를 담고 소금, 후추로 간을 한다.\r\n\r\n6. 볼에 담긴 재료에 마요네즈와 달걀을 넣고 섞는다.\r\n\r\n7. 핸드블랜더로 크루아상을 갈아 빵가루를 만든다.\r\n\r\n8. 양념한 연어 통조림을 동그랗게 빚어 빵가루를 묻힌 뒤, 기름에 튀긴다.\r\n\r\n9. 튀긴 연어볼을 베이컨으로 만 뒤, 꼬치를 꽂아 고정하고 기름을 두른 팬에 굽는다.\r\n\r\n10. 양파를 동그랗게 썰어 기름을 두른 팬에 올리고 소금, 후추를 뿌려 굽는다.\r\n\r\n11. 구운 양파를 접시에 올린다.\r\n\r\n12. 양파 위에 베이컨 연어볼 말이를 얹는다.\r\n\r\n13. 냄비에 사과맛 음용식초를 넣고 끓여 졸인 후, 아로니아액을 넣고 섞는다.\r\n\r\n14. 베이컨 연어 말이 위에 사과아로니아소스를 뿌린다.\r\n\r\n15. 대파의 흰 부분을 채 썰어 전분을 묻힌 뒤, 기름에 튀긴다.\r\n\r\n16. 연어 말이 위에 파채튀김을 올려 마무리한다.'),
	(279, '알콜달콤', NULL, 1, '1. 달걀은 노른자만 분리해 두 개의 볼에 나눠 담는다.\r\n\r\n2. 팬에 복숭아맛 술과 딸기맛 술을 넣고 끓여 졸인다.\r\n\r\n3. 달걀노른자가 담긴 두 개의 볼에 설탕을 넣고 거품기로 섞는다.\r\n\r\n4. 두 개의 볼에 각각 복숭아맛 술과 레몬맛 술을 넣고 섞는다.\r\n\r\n5. 두 개의 볼을 끓는 물 위에서 저어가며 중탕해 크림 상태로 만든다.\r\n\r\n6. 레몬맛 술을 넣은 소스에 레몬즙을 넣는다.\r\n\r\n7. 크루아상을 반으로 잘라 토스트기에 굽는다.\r\n\r\n8. 버터를 두른 팬에 토스트한 크루아상을 다시 한 번 굽는다.\r\n\r\n9. 졸인 복숭아&딸기맛 술을 볼에 담고, 구운 크루아상을 찢어 넣어 적신다.\r\n\r\n10. 졸인 술에 적신 크루아상을 컵에 담는다.\r\n\r\n11. 10에 복숭아맛 술로 만든 소스를 얹는다.\r\n\r\n12. 다른 컵에 구운 크루아상을 담고, 레몬맛 술로 만든 소스를 얹는다.\r\n\r\n13. 레몬맛 소스 위에 레몬 껍질을 갈아서 올린다.\r\n\r\n14. 복숭아맛 소스 위에 딸기 초콜릿을 갈아서 올린다.'),
	(280, '우동 노 미', NULL, 1, '1. 불고기용 소고기에 콜라를 부어 재운다.\r\n\r\n2. 양파는 굵게 채 썰고, 대파는 칼집을 낸 후 적당한 크기로 썬다.\r\n\r\n3. 기름을 두른 팬에 썰어둔 양파와 대파를 굽는다.\r\n\r\n4. 냄비에 콜라, 맛술, 간장, 설탕을 2:2:1:1 비율로 넣고 끓여서 전골 양념을 만든다.\r\n\r\n5. 냄비에 인스턴트 우동을 끓인다.\r\n\r\n6. 인스턴트 우동의 유부를 건져 양념을 씻어내고 적당한 크기로 썬다.\r\n\r\n7. 우동면이 익으면 건져내 양념을 씻어낸다.\r\n\r\n8. 콜라에 재워둔 불고기용 소고기를 석쇠에 넣고 굽는다.\r\n\r\n9. 8의 윗면을 토치로 함께 굽는다.\r\n\r\n10. 팬에 구운 불고기와 전골 양념을 넣고 끓인다.\r\n\r\n11. 10에 당근과 청양고추를 썰어 넣고 함께 끓인다.\r\n\r\n12. 달걀노른자를 분리해 따로 담아낸다.\r\n\r\n13. 구운 양파와 대파를 전골용 냄비에 깔고, 양념과 함께 끓인 불고기를 담아 다시 한 번 끓인다.\r\n\r\n14. 마지막으로 적당한 크기로 썬 유부와 우동면을 올려 완성한다.'),
	(281, '천하젤면', NULL, 1, '1. 볼에 밀가루, 옥수수전분, 감자전분, 물 (2:3:5:10) 을 넣고, 소금을 넣어 반죽을 만든다.\r\n\r\n2. 넓은 접시에 반죽을 붓고 찜기에 찐다.\r\n\r\n3. 돼지목살, 양파, 당근, 애호박, 아스파라거스, 샐러리를 손질한다.\r\n\r\n4. 기름을 두른 팬에 적당한 크기로 썬 대파와 다진 마늘, 고춧가루,\r\n잘게 썬 청양고추, 채 썬 돼지목살을 넣고 볶는다.\r\n\r\n5. 볶던 재료에 간장으로 간한다.\r\n\r\n6. 매콤하게 볶은 재료에 손질한 채소, 굴소스, 맛술, 참기름을 넣고 볶는다.\r\n\r\n7. 찜기에 찐 반죽을 찬물에 담가 식힌다.\r\n\r\n8. 차게 식힌 반죽을 접고 채 썰어 면을 만든다.\r\n\r\n9. 볶던 재료에 면을 넣고 함께 볶는다.\r\n\r\n10. 접시에 볶음면을 담고, 대파 흰 부분을 채 썰어 올려 마무리한다.'),
	(282, '젤 맛있돔', NULL, 1, '1. 냄비에 코코넛워터, 사과주스, 간장을 넣고 끓여 소스를 만든다.\r\n\r\n2. 물에 불려둔 젤라틴의 물기를 짜낸 뒤, 사과간장소스에 넣고 녹인다.\r\n\r\n3. 젤라틴을 넣고 녹인 사과간장소스를 냉동실에 차게 식혀둔 그릇에 나눠 담는다.\r\n\r\n4. 사과간장소스를 담은 접시를 하나는 얼음물 위에, 하나는 냉동실에 두고 굳혀 젤리 형태로 만든다.\r\n\r\n5. 인스턴트 새우딤섬을 찜기에 넣고 찐다.\r\n\r\n6. 달래를 썰어 물에 담가둔다.\r\n\r\n7. 찐 새우딤섬을 적당한 크기로 썰어, 냉동실에서 식힌다.\r\n\r\n8. 끓는 물에 달래와 방울양배추 잎을 데친다.\r\n\r\n9. 데친 방울양배추 잎을 얼음물에 담가둔다.\r\n\r\n10. 방울토마토를 잘게 썬다.\r\n\r\n11. 아보카도의 껍질을 벗겨내고 얇게 썬다.\r\n\r\n12. 차게 식힌 새우 딤섬의 피를 벗기고 소만 볼에 담는다.\r\n\r\n13. 새우딤섬소와 데친 달래, 마요네즈, 후추를 한데 버무린다.\r\n\r\n14. 원형틀을 이용해 접시에 잘게 썬 방울토마토와 양념한 새우딤섬소를 올린다.\r\n\r\n15. 양념한 새우딤섬소 위에 얇게 썬 아보카도를 올리고, 소금을 뿌린다.\r\n\r\n16. 얼음물에 담가 식힌 방울양배추 잎을 올리브오일과 후추에 버무린다.\r\n\r\n17. 접시에 담긴 재료들 위에 차게 굳힌 사과간장젤리를 돔처럼 덮는다.\r\n\r\n18. 젤리 위에 양념한 방울양배추 잎을 올리고, 라임껍질을 갈아 올려 마무리한다.'),
	(283, '컵있슈', NULL, 1, '1. 냄비에 버터를 녹인다.\r\n\r\n2. 다른 냄비에 우유, 설탕을 넣고 끓인다.\r\n\r\n3. 버터를 넣은 냄비와, 우유를 넣은 냄비에 각각 소금을 넣는다.\r\n\r\n4. 버터가 녹으면 밀가루를 넣고 저어 루를 만든다.\r\n\r\n5. 루에 설탕을 넣어 끓인 우유를 넣고 섞는다.\r\n\r\n6. 5에 달걀노른자를 넣고 섞어 베샤멜 소스를 만든다.\r\n\r\n7. 베샤멜 소스에 두유와 파르메산 치즈를 넣는다.\r\n\r\n8. 완성된 베샤멜 소스를 반으로 나눠담고, 한 쪽에는 핫초코 분말을 넣는다.\r\n\r\n9. 컵에 열대과일 통조림을 담는다.\r\n\r\n10. 파인애플과 망고를 적당한 크기로 썰어 컵에 담는다.\r\n\r\n11. 컵에 담긴 과일들 위에, 베샤멜 소스와 초코 베샤멜 소스를 반씩 올린다.\r\n\r\n12. 소프트 아이스크림을 컵 위에 올린다.\r\n\r\n13. 각종 젤리와 체리로 장식해 마무리한다.'),
	(284, '버블버블버블밥!', NULL, 1, '1. 삶은 옥수수의 알갱이를 분리한다.\r\n\r\n2. 아스파라거스를 잘게 썰어 옥수수 알갱이와 함께 볼에 담아둔다.\r\n\r\n3. 아스파라거스, 양파, 파인애플, 옥수수 알갱이, 청양고추를 잘게 썬다.\r\n\r\n4. 기름을 두른 팬에 잘게 썬 채소들과 인스턴트 깍두기 볶음밥, 케첩, 설탕을 넣고 볶는다.\r\n\r\n5. 달걀의 노른자와 흰자를 분리한다.\r\n\r\n6. 기름을 두른 팬에 달걀 노른자물을 부어 지단을 만든다.\r\n\r\n7. 노른자로 만든 지단에 동그랗게 구멍을 뚫는다.\r\n\r\n8. 지단에 낸 동그란 구멍에 달걀 흰자물을 붓고 익힌다.\r\n\r\n9. 잘게 썬 아스파라거스와 옥수수 알갱이에 밀가루와 물을 넣고 섞어 동그랗게 뭉친 뒤 튀긴다.\r\n\r\n10. 달걀 지단 위에 채소를 넣고 볶은 깍두기볶음밥을 올리고 돌돌 만다.\r\n\r\n11. 볶음밥을 넣고 만 지단을 한 입 크기로 썰어 접시에 담는다.\r\n\r\n12. 방울토마토의 꼭지 부분을 썰어내고 끓는 물에 살짝 데친다.\r\n\r\n13. 데친 방울토마토의 껍질을 벗겨 접시에 올린다.\r\n\r\n14. 옥수수아스파라거스 튀김을 건져 기름기를 빼고, 버터를 두른 팬에 살짝 볶은 뒤 접시에 올린다.\r\n\r\n15. 튀김 위에 파르메산 치즈를 갈아 올려 마무리한다.'),
	(285, '어머님이 샘이니', NULL, 1, '1. 감자를 얇게 슬라이스 한다.\r\n\r\n2. 끓는 물에 소금을 넣고, 슬라이스 한 감자를 삶는다.\r\n\r\n3. 양송이버섯은 기둥을 제거하고 적당한 크기로 썬다.\r\n\r\n4. 대하는 머리와 꼬리를 떼어내고, 껍질을 벗겨 살만 발라낸다.\r\n\r\n5. 크림치즈에 레몬즙과 후추를 넣어 레몬크림치즈를 만든다.\r\n\r\n6. 기름을 두른 팬에 손질한 양송이버섯, 대하, 소금, 버터를 넣고 익힌다.\r\n\r\n7. 볶은 재료에 감자 삶은 물을 넣고 핸드블렌더에 간다.\r\n\r\n8. 믹서기로 간 재료에 레몬크림치즈와 후추를 넣고 섞은 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n9. 슬라이스해서 삶은 감자를 기름을 두른 팬에 펼치고 소금, 후추로 간을 해서 굽는다.\r\n\r\n10. 달걀을 삶는다.\r\n\r\n11. 샐러드 채소를 잘게 다진다.\r\n\r\n12. 볼에 올리브오일, 발사믹 드레싱, 소금, 후추, 잘게 다진 샐러드 채소, 적당한 크기로 썬\r\n청포도, 레몬즙을 넣고 섞어 샐러드를 만든다.\r\n\r\n13. 접시에 구운 슬라이스감자를 올린다.\r\n\r\n14. 13 위에 레몬크림치즈로 양념한 대하와 버섯을 올리고 샐러드를 만든다.\r\n\r\n15. 삶은 달걀을 반으로 잘라 위에 얹는다.\r\n\r\n16. 후추와 올리브오일을 뿌려 완성한다.'),
	(286, '버섯닭', NULL, 1, '1. 파프리카는 꼭지와 씨를 제거하고 채 썬다.\r\n\r\n2. 새송이버섯은 채 썰고, 양파는 적당한 크기로 썰어둔다.\r\n\r\n3. 냄비에 기름을 두르고 고춧가루, 다진 마늘과 파를 넣어 볶아 양념을 만든다.\r\n\r\n4. 전자레인지에 즉석밥을 데운다.\r\n\r\n5. 데친 콩나물을 체에 밭친 뒤, 뜨거운 물로 헹구고 물기를 뺀다.\r\n\r\n6. 먼저 볶아둔 양념에 채 썬 파프리카, 새송이버섯, 양파를 넣고 볶다가 간장으로 간한다.\r\n\r\n7. 닭가슴살 치킨을 적당한 크기로 썰어 채소와 함께 볶다가 소금, 후추로 간한다.\r\n\r\n8. 7에 물을 넣는다.\r\n\r\n9. 볶은 재료에 굴소스와 데친 콩나물, 참기름을 넣고 함께 볶는다.\r\n\r\n10. 대파의 흰 부분과 청양고추를 잘게 썰어 넣고 함께 볶는다.\r\n\r\n11. 데운 즉석밥을 밥그릇에 담아낸다.\r\n\r\n12. 볶은 재료에 조미료를 넣는다.\r\n\r\n13. 치킨채소볶음을 그릇에 담는다.\r\n\r\n14. 13 위에 깨와 참기름을 뿌려 완성한다.'),
	(287, '기절초(코)풍', NULL, 1, '1. 냄비에 우유, 설탕, 버터를 넣고 데운다.\r\n\r\n2. 1에 밀가루를 넣고 섞어 익반죽을 한다.\r\n\r\n3. 반죽에 달걀을 넣고 섞는다.\r\n\r\n4. 반죽을 적당한 크기로 떼어 마른 팬에 동그랗게 편다.\r\n\r\n5. 반죽이 잠길 정도로 식용유를 넉넉히 붓고 튀겨 도우를 만든다.\r\n\r\n6. 냄비에 버터와 밀가루를 넣고 루를 만든다.\r\n\r\n7. 루에 우유를 넣고 끓인다.\r\n\r\n8. 7에 크림치즈와 핫초코 분말을 넣고 초코치즈소스를 만든다..\r\n\r\n9. 초코치즈소스에 설탕을 넣고 섞는다.\r\n\r\n10. 냄비에 버터와 밀가루를 볶아 루를 만든 뒤, 우유, 슬라이스 치즈, 설탕을 넣고 치즈소스를 만든다.\r\n\r\n11. 접시에 튀긴 도우를 깔고, 초코치즈소스를 두른다.\r\n\r\n12. 딸기와 청포도를 썰어 초코피자 위에 얹는다.\r\n\r\n13. 11 위에 치즈소스를 올린다.\r\n\r\n14. 피자를 먹기 좋게 잘라 완성한다.'),
	(288, '부티 귀티 싼티', NULL, 1, '1. 기름을 두른 팬에 미트볼을 넣고 익힌다.\r\n\r\n2. 미트볼을 익힌 기름을 따라내고 토마토소스와 물을 넣는다.\r\n\r\n3. 2에 설탕, 후추, 소금을 넣어 간한다.\r\n\r\n4. 껍질을 벗긴 감자와 당근을 얇게 슬라이스 한다.\r\n\r\n5. 저민 감자와 당근, 양파를 채 썬다.\r\n\r\n6. 비닐팩에 채 썬 채소를 넣고 전분, 밀가루, 후추를 넣어 튀김옷을 입힌다.\r\n\r\n7. 튀김옷을 입힌 채소를 조금씩 덜어 기름을 두른 팬에 튀긴다.\r\n\r\n8. 다른 팬에 기름을 두른 뒤, 적당한 크기로 썬 애호박과 달걀을 넣고 익힌다.\r\n\r\n9. 채소 튀김과 달걀 프라이를 꺼내 기름을 뺀다.\r\n\r\n10. 피망을 적당한 크기로 잘라 기름을 두른 팬에 익힌다.\r\n\r\n11. 그릇에 토마토소스를 담고, 미트볼>슬라이스 치즈>채소 튀김 순서로 쌓은 뒤 볶은 피망과 애호박을 올린다.\r\n\r\n12. 미트볼과 슬라이스 치즈를 한 층 더 쌓는다.\r\n\r\n13. 12위에 달걀 프라이와 채소 튀김을 올려 완성한다.'),
	(289, '만두가 머랭?', NULL, 1, '1. 냄비에 인스턴트 소고기무국을 끓인다.\r\n\r\n2. 달걀의 흰자와 노른자를 분리한다.\r\n\r\n3. 게맛살은 두드려서 결대로 찢고, 달걀노른자와 섞는다.\r\n\r\n4. 거품기로 달걀흰자를 쳐서 머랭을 만든다.\r\n\r\n5. 머랭 안에 달걀노른자에 버무린 게맛살을 넣어 감싼다.\r\n\r\n6. 기름에 게맛살을 넣은 머랭을 넣고, 기름을 끼얹어가며 튀긴다.\r\n\r\n7. 인스턴트 소고기무국에 후추를 넣는다.\r\n\r\n8. 후추를 넣은 인스턴트 소고기무국에 튀긴 머랭을 넣고 조린다.\r\n\r\n9. 8에 전분물을 넣는다.\r\n\r\n10. 그릇에 머랭튀김과 국물을 담는다.\r\n\r\n11. 대파를 잘게 썰어 올려 마무리한다.'),
	(290, '계이득', NULL, 1, '1. 끓는 물에 달걀과 소금을 넣고 삶는다.\r\n\r\n2. 찜갈비용 소고기는 뼈를 발라내고 적당한 크기로 썬다.\r\n\r\n3. 핸드블랜더에 적당한 크기로 썬 소고기를 넣고 간다.\r\n\r\n4. 갈아낸 소고기에 강판에 간 마늘과 대파를 넣고 섞는다.\r\n\r\n5. 볼에 간장, 설탕을 섞어 양념을 만든다.\r\n\r\n6. 간 마늘과 대파를 넣은 소고기에 밀가루, 간장설탕양념을 넣고 섞는다.\r\n\r\n7. 반숙으로 삶은 달걀을 얼음물에 담가둔다.\r\n\r\n8. 얼음물에 담가둔 달걀의 껍데기를 벗기고 물기를 제거한 후, 밀가루를 묻힌다.\r\n\r\n9. 양념한 소고기로 밀가루를 묻힌 달걀을 감싼다.\r\n\r\n10. 기름을 넉넉하게 두른 팬에 소고기로 감싼 달걀을 넣고, 기름을 끼얹어가며 익힌다.\r\n\r\n11. 대파와 알배추를 잘게 썬다.\r\n\r\n12. 기름을 두른 팬에 잘게 썬 대파와 알배추를 넣고, 간장, 인스턴트 사골곰탕, 참기름을 넣어 졸인다.\r\n\r\n13. 12에 전분물, 후추을 넣는다.\r\n\r\n14. 소고기달걀튀김을 건져 반으로 자른다.\r\n\r\n15. 접시에 반으로 자른 소고기달걀튀김을 올리고, 사골곰탕에 졸인 채소를 얹는다.\r\n\r\n16. 소금, 후추를 뿌려 마무리한다.'),
	(291, '김흥국', NULL, 1, '1. 냄비에 인스턴트 사골곰탕과 인스턴트 설렁탕을 넣고 끓인다.\r\n\r\n2. 대파, 마늘, 양파, 알배추를 적당한 크기로 썬다.\r\n\r\n3. 찜갈비용 소고기의 뼈를 발라내고, 발라낸 살을 적당한 크기로 썬다.\r\n\r\n4. 대하는 껍질을 벗기고 포 뜨듯이 썬다.\r\n\r\n5. 볼에 소고기살과 대하, 달걀물을 넣고 잘 섞은 뒤, 기름에 튀기듯 볶는다.\r\n\r\n6. 볶은 재료를 체에 밭쳐 기름기을 뺀다.\r\n\r\n7. 팬에 손질한 채소와 간장, 볶은 소고기와 대하를 넣고 볶는다.\r\n\r\n8. 볶던 재료에 청주와 굴소스를 넣고 볶는다.\r\n\r\n9. 끓는 물에 우동면을 삶는다.\r\n\r\n10. 우동면을 끓이는 물에 청양고추를 잘게 썰어 넣어 향을 낸다.\r\n\r\n11. 볶던 재료에 전분물을 넣는다.\r\n\r\n12. 삶은 우동면을 건져 찬물에 헹구고, 그릇에 담는다.\r\n\r\n13. 12 위에 볶은 재료들을 얹고, 끓인 사골곰탕&설렁탕을 넣는다.\r\n\r\n14. 대파의 흰 부분을 잘게 썰어 올려 완성한다.'),
	(292, '앗싸~ 호랑나베!', NULL, 1, '1. 무는 껍질을 벗겨 적당한 크기로 썬다.\r\n\r\n2. 대파와 알배추를 적당한 크기로 썬다.\r\n\r\n3. 끓는 물에 마른 멸치를 넣고 육수를 낸다.\r\n\r\n4. 압력솥에 물, 으깬 마늘, 적당한 크기로 자른 무와 양파를 넣고 끓인다.\r\n\r\n5. 찜갈비용 소고기를 손질해 채소육수에 넣고 함께 끓인다.\r\n\r\n6. 껍질을 벗긴 대하를 핸드블렌더로 간다.\r\n\r\n7. 기름을 두른 팬에 손질한 무, 대파, 알배추를 넣고 볶는다.\r\n\r\n8. 볶던 채소에 멸치육수를 넣고 끓인다.\r\n\r\n9. 갈아놓은 대하살에 전분과 달걀물을 넣고 치대어 새우완자 반죽을 만든다.\r\n\r\n10. 새우완자 반죽을 적당한 크기로 떼어 채소를 넣은 멸치육수에 넣는다.\r\n\r\n11. 10에 소고기채소육수를 넣고 끓인다.\r\n\r\n12. 익은 소고기살을 건져 국물에 넣고 함께 끓인다.\r\n\r\n13. 맛술, 간장, 다진 마늘, 소금을 넣어 간을 한다.\r\n\r\n14. 완성된 요리를 그릇에 담아낸다.'),
	(293, '오필승 파에야', NULL, 1, '1. 바지락을 씻어 냄비에 넣고 끓인다.\r\n\r\n2. 모시조개도 씻어서 바지락이 담긴 냄비에 함께 넣고 끓인다.\r\n\r\n3. 굴, 낙지, 대하, 새우살을 손질한다.\r\n\r\n4. 문어를 끓는 물에 데친다.\r\n\r\n5. 전복을 손질해 적당한 크기로 자른다.\r\n\r\n6. 데친 문어를 적당한 크기로 썬다.\r\n\r\n7. 끓인 바지락과 모시조개를 체에 밭쳐 육수를 따로 담아낸다.\r\n\r\n8. 걸러낸 조개육수에 강황가루를 넣고 섞는다.\r\n\r\n9. 기름을 두른 팬에 잘게 다진 마늘과 손질한 각종 해산물을 넣고 볶다가 소금, 후추로 간한다.\r\n\r\n10. 해산물을 볶는 팬에 화이트와인을 넣는다.\r\n\r\n11. 볶은 해산물에 즉석밥, 강황가루를 넣은 조개육수, 토마토주스를 넣고 볶는다.\r\n\r\n12. 익힌 바지락과 모시조개의 살을 발라낸다.\r\n\r\n13. 파에야 팬을 오븐에서 데운다.\r\n\r\n14. 데운 파에야 팬에 파에야을 담고, 조갯살을 넣어 함께 익힌다.\r\n\r\n15. 파에야에 올리브오일을 두른 뒤, 오븐에 넣어 익힌다.\r\n\r\n16. 그린올리브와 블랙올리브를 적당한 크기로 썬다.\r\n\r\n17. 오븐에서 익힌 파에야를 꺼내, 적당한 크기로 썬 그린&블랙 올리브를 얹는다.\r\n\r\n18. 17을 다시 오븐에 넣어 익힌다.\r\n\r\n19. 익힌 파에야를 팬 채로 낸다.'),
	(294, '레알 만두리드', NULL, 1, '1. 살치살과 돼지고기를 적당한 크기로 썰어 핸드블랜더로 간다.\r\n\r\n2. 양파, 파프리카, 마늘, 블랙올리브를 잘게 썬다.\r\n\r\n3. 기름을 두른 팬에 잘게 썬 재료들을 넣고, 소금, 후추로 간한다.\r\n\r\n4. 3에 핸드블렌더로 간 고기와 토마토 주스를 넣고 볶아 소를 만든다.\r\n\r\n5. 볶은 소를 체에 밭쳐 기름을 빼며 식힌다.\r\n\r\n6. 파프리카를 직화한다.\r\n\r\n7. 핸드블랜더에 밀가루, 소금, 올리브오일, 달걀, 우유를 넣고 반죽을 만든다.\r\n\r\n8. 직화한 파프리카는 물에 씻어 태운 부분을 벗겨 낸다.\r\n\r\n9. 핸드블랜더에 익힌 파프리카, 블랙올리브, 올리브오일, 후추, 토마토주스를 넣고 갈아 소스를 만든다.\r\n\r\n10. 반죽을 넓게 편다.\r\n\r\n11. 넓게 편 반죽을 원형틀을 이용해 동그랗게 찍어낸다.\r\n\r\n12. 동그랗게 찍어낸 반죽에 소를 넣고 달걀물을 묻혀 싼 후, 테두리에 모양을 낸다.\r\n\r\n13. 빚은 만두를 기름에 튀긴 후, 표면에 달걀노른자를 묻혀 오븐에 굽는다.\r\n\r\n14. 그릇에 소스를 담고, 잘게 썬 그린올리브를 얹는다.\r\n\r\n15. 구운 만두를 올린다.\r\n\r\n16. 달래를 잘게 썰어 올린다.\r\n\r\n17. 플레인요거트를 뿌려 마무리 한다.'),
	(295, '아따~ 조기찜 맛있다잉!', NULL, 1, '1. 조기의 비늘을 벗겨내 손질한다.\r\n\r\n2. 손질한 조기에 청주, 소금, 후추, 레몬즙을 뿌려 비린내를 제거한다.\r\n\r\n3. 비린내를 제거한 조기에 양면으로 칼집을 낸다.\r\n\r\n4. 칼집을 낸 조기를 찜기에 넣고 찐다.\r\n\r\n5. 콜리플라워는 기둥을 잘라내고 적당한 크기로 손질한다.\r\n\r\n6. 방울양배추, 레몬, 아스파라거스, 파프리카를 적당한 크기로 썬다.\r\n\r\n7. 손질한 채소를 찜기에 넣고 찐다.\r\n\r\n8. 마른 그릴팬에 칼집을 낸 고추를 굽는다.\r\n\r\n9. 볼에 마늘, 올리브오일, 소금, 식초를 넣고 섞어 양념장을 만든다.\r\n\r\n10. 달래를 잘게 썰어 양념장에 넣고 섞는다.\r\n\r\n11. 구운 고추를 양념장에 넣고 버무린다.\r\n\r\n12. 볼에 간장, 참기름, 태국간장, 깨, 잘게 썬 대파, 설탕에 잰 마늘, 매실액을 넣고 섞어 간장양념장을 만든다.\r\n\r\n13. 찜기에 찐 파프리카, 방울양배추, 아스파라거스, 조기를 접시에 담는다.\r\n\r\n14. 달래와 찜기에 찐 콜리플라워도 접시에 담는다.\r\n\r\n15. 조기 위에 간장양념장을 얹는다.\r\n\r\n16. 작은 접시에 달래양념장에 버무린 구운 고추와 달래무침을 각각 담아낸다.\r\n\r\n17. 전자레인지에 돌린 즉석밥을 밥그릇에 담고 잘게 썬 대파를 올려 마무리한다.'),
	(296, '민어는 축구선수를 좋아해', NULL, 1, '1. 팬에 사골국, 묵은지, 어슷하게 썬 대파를 넣고 끓인다.\r\n\r\n2. 1에 고춧가루, 설탕을 넣고 끓인다.\r\n\r\n3. 적당한 크기로 손질한 낙지를 묵은지를 끓이던 팬에 넣고 함께 끓여 묵은지 소스를 만든다.\r\n\r\n4. 낙지를 넣은 묵은지 소스에 새우젓으로 간한다.\r\n\r\n5. 반건조민어를 손질해 적당한 크기로 썬다\r\n\r\n6. 기름을 두른 팬에 반건조민어를 굽는다.\r\n\r\n7. 민어를 굽는 팬에 설탕에 잰 마늘과 버터를 넣어 함께 익힌다.\r\n\r\n8. 7에 달래무침을 넣고, 민어에 양념을 끼얹어가며 조린다.\r\n\r\n9. 냉이를 적당한 크기로 손질해 함께 조린다.\r\n\r\n10. 인스턴트 곤드레나물밥을 전자레인지에 익히고, 파래무침과 올리브오일을 넣어 섞는다.\r\n\r\n11. 그릇에 묵은지 소스를 담는다.\r\n\r\n12. 묵은지 소스 위에 조린 반건조민어를 올린다.\r\n\r\n13. 냉이를 참기름에 버무린 뒤, 민어 위에 올려 마무리한다.'),
	(297, '다 털었Show!', NULL, 1, '1. 소금을 넣은 끓는 물에 달걀을 삶는다.\r\n\r\n2. 냄비에 바나나맛 우유를 넣고 끓인다.\r\n\r\n3. 핸드블렌더에 기름을 뺀 참치 통조림과, 채 썬 양파를 넣는다.\r\n\r\n4. 3에 후추를 넣고 갈아 참치반죽을 만든다.\r\n\r\n5. 볼에 바나나맛 우유와 전분을 넣고 섞는다.\r\n\r\n6. 바나나맛 우유를 끓이는 냄비에 전분과 섞은 바나나맛 우유를 넣고 잘 섞어 푸딩을 만든다.\r\n\r\n7. 냉동실에서 차게 식힌 그릇에 바나나맛 푸딩을 담고, 다시 냉동실에 넣어 식힌다.\r\n\r\n8. 식빵을 적당한 크기로 썰고 소금과 후추를 뿌린다.\r\n\r\n9. 기름을 두른 팬에 버터를 넣고 적당한 크기로 썬 식빵을 굽는다.\r\n\r\n10. 양상추와 파프리카를 손질해 각각 볼에 담는다.\r\n\r\n11. 손질한 파프리카에 호두드레싱을 넣고 버무린다.\r\n\r\n12. 삶은 달걀의 껍데기를 벗기고 반으로 자른다.\r\n\r\n13. 삶은 달걀의 노른자만 분리해, 참치반죽에 넣고 버무린다.\r\n\r\n14. 참치반죽을 완자모양으로 동그랗게 뭉쳐서 흰자 위에 올리고 접시에 담는다.\r\n\r\n15. 핸드블렌더에 남은 참치반죽과 올리브오일, 레몬즙을 넣고 갈아 참치드레싱을 만든다.\r\n\r\n16. 손질해둔 양상추에 참치드레싱을 넣고 버무려 접시에 올린다.\r\n\r\n17. 16 위에 호두드레싱으로 버무린 파프리카를 얹고, 멸치볶음을 곁들인다.\r\n\r\n18. 샐러드에 마카다미아와 적당하게 썬 금귤을 올린다.\r\n\r\n19. 구운 식빵을 얹는다.\r\n\r\n20. 냉동실에서 식힌 바나나맛 푸딩 위에 딸기를 썰어 올린다.\r\n\r\n21. 마카다미아를 으깨 바나나맛 푸딩에 곁들인다.\r\n\r\n22. 바나나맛 푸딩에 마누카 꿀을 뿌려 마무리한다.'),
	(298, '떡.고.치', NULL, 1, '1. 참치 통조림과 야채참치 통조림의 기름을 뺀다.\r\n\r\n2. 떡볶이 떡을 적당한 크기로 썰어 뜨거운 물에 불린다.\r\n\r\n3. 식빵은 테두리를 잘라내고, 적당한 크기로 썰어 핸드블렌더에 넣고 갈아 빵가루를 만든다.\r\n\r\n4. 볼에 기름을 뺀 참치, 불린 떡볶이 떡, 달걀, 소금, 후추를 넣는다.\r\n\r\n5. 4에 빵가루와 마요네즈를 넣고 치대 참치 패티 반죽을 만든다..\r\n\r\n6. 참치 패티 반죽을 적당한 크기로 떼어내 기름에 튀긴다.\r\n\r\n7. 마늘을 강판에 간다.\r\n\r\n8. 볼에 간 마늘과 고추장, 물엿, 맛술, 고춧가루, 간장을 섞어 양념을 만든다.\r\n\r\n9. 고추와 파프리카의 씨를 발라내고, 적당한 크기로 썬다.\r\n\r\n10. 기름을 두른 팬에 손질한 채소를 볶다가 양념과 청주를 넣고 함께 볶는다.\r\n\r\n11. 참치 패티를 익히는 팬에 으깬 마늘과 대파 흰 부분을 넣어 향을 낸다.\r\n\r\n12. 매콤하게 볶은 채소에 익힌 참치 패티와 참기름을 넣고 한 번 더 볶는다.\r\n\r\n13. 접시에 참치 패티를 담고, 볶은 채소를 올린 뒤 깨를 뿌려 마무리 한다.'),
	(299, '파니니 안파니', NULL, 1, '1. 당근과 양파는 채 썰고, 금귤은 씨를 빼고 얇게 썬다.\r\n\r\n2. 볼에 손질한 재료들과 장조림 통조림, 으깬 맛밤, 물과 밀가루를 넣고 반죽한다.\r\n\r\n3. 반죽에 소금과 후추로 간을 한다.\r\n\r\n4. 반죽을 적당한 크기로 덜어 기름에 튀긴다.\r\n\r\n5. 튀김을 건져 기름기를 뺀다.\r\n\r\n6. 뜯어먹는 식빵을 반으로 잘라 속을 파낸다.\r\n\r\n7. 속을 파낸 식빵에 마요네즈를 바르고, 장조림&채소튀김을 넣은 뒤 다른 식빵으로 덮어 파니니를 만든다.\r\n\r\n8. 마른 그릴팬에 파니니를 올리고 모양을 내며 굽는다.\r\n\r\n9. 양파를 잘게 썰고, 참치 통조림의 기름을 뺀다.\r\n\r\n10. 볼에 잘게 썬 양파, 참치, 마요네즈를 넣고 섞어 참치 샐러드를 만든다.\r\n\r\n11. 속을 파낸 식빵을 밀대로 얇게 민다.\r\n\r\n12. 얇게 편 식빵에 잘게 썬 양상추와 참치샐러드를 넣고 돌돌 만다.\r\n\r\n13. 구운 파니니를 적당한 크기로 잘라 도시락에 담는다.\r\n\r\n14. 참치 샐러드를 넣은 식빵말이를 유산지로 감싸 도시락에 담는다.'),
	(300, '검사외조', NULL, 1, '1. 팬에 버터와 기름을 두른다.\r\n\r\n2. 달걀물을 만든다.\r\n\r\n3. 장조림 통조림을 체에 밭쳐 건더기만 걸러낸 뒤, 고기를 잘게 찢는다.\r\n\r\n4. 버터와 기름을 두른 팬에 달걀물과 장조림, 멸치볶음을 넣고 스크램블을 만든다.\r\n\r\n5. 식빵의 테두리를 잘라내고 밀대로 얇게 민다.\r\n\r\n6. 얇게 민 식빵에 장조림과 멸치볶음을 넣은 스크램블을 넣고 돌돌 만다.\r\n\r\n7. 버터를 두른 팬에 스크램블식빵말이를 굽는다.\r\n\r\n8. 슈크림빵의 슈크림만 덜어내 유산지컵에 담는다.\r\n\r\n9. 꼭지를 잘라낸 딸기를 슈크림 위에 올린 뒤, 마카다미아를 곁들인다.\r\n\r\n10. 케첩과 핫소스를 섞은 뒤, 소스통에 담는다.\r\n\r\n11. 구운 식빵말이를 도시락에 담고, 마카롱을 곁들인다.\r\n\r\n12. 바나나맛 우유를 함께 낸다.'),
	(301, '샤부의 샐러드', NULL, 1, '1. 솜땀 파우더를 따뜻한 물에 갠다.\r\n\r\n2. 솜땀소스에 가쓰오간장, 매운 참깨소스, 명란젓을 넣는다.\r\n\r\n3. 2에 레몬즙을 넣고 섞는다.\r\n\r\n4. 샤부샤부용 소고기의 핏물을 빼고 전분에 무친다.\r\n\r\n5. 전분을 입힌 샤부샤부용 소고기에 달걀물을 넣고 섞은 뒤, 기름에 튀기듯 볶는다.\r\n\r\n6. 볶은 고기를 체에 밭쳐 기름기를 뺀다.\r\n\r\n7. 애호박을 채 썰어 끓는 물에 데친다.\r\n\r\n8. 스노우크랩의 다리살을 발라낸다.\r\n\r\n9. 볼에 전분, 물, 식용유를 넣고 섞어 반죽을 만든 뒤, 스노우크랩에 입혀 기름에 튀긴다.\r\n\r\n10. 양파를 채 썬다.\r\n\r\n11. 채 썬 양파를 그릇에 넓게 편다.\r\n\r\n12. 데친 애호박을 체에 밭쳐 물기를 빼고 차게 식혀 양파채 위에 올린다.\r\n\r\n13. 적당히 썬 상추와 얇게 찢어낸 삶은 죽순을 올린다.\r\n\r\n14. 샐러드 위에 성게알 통조림과 볶은 소고기를 올린다.\r\n\r\n15. 소고기 위에 성게알 통조림을 다시 한 번 얹는다.\r\n\r\n16. 스노우크랩 튀김을 접시에 담아낸다.\r\n\r\n17. 고추를 잘게 썰어 솜땀소스에 섞는다.\r\n\r\n18. 샐러드 위에 솜땀소스를 얹고, 스노우크랩 튀김을 따로 낸다.'),
	(302, '연어를 찾아~ 사바', NULL, 1, '1. 양파, 대파, 애호박을 잘게 썬다.\r\n\r\n2. 기름을 두른 팬에 마늘과 잘게 썬 채소들을 넣고 소금, 후추를 넣어 볶는다.\r\n\r\n3. 연어머리의 살만 발라내어 다진다.\r\n\r\n4. 볶던 채소에 연어머릿살, 팟타이소스를 넣고 볶는다.\r\n\r\n5. 마른 팬에 건새우를 넣고 볶는다.\r\n\r\n6. 물이 담긴 냄비에 소금을 넣고 끓인다.\r\n\r\n7. 핸드블랜더에 밀가루, 달걀을 넣고 돌려 면 반죽을 만든다.\r\n\r\n8. 대파를 얇게 채 썰어 기름에 튀긴다.\r\n\r\n9. 튀긴 대파와 볶던 재료들을 함께 볶는다.\r\n\r\n10. 핸드블랜더에 볶은 건새우를 간다.\r\n\r\n11. 면 반죽을 길게 밀어 적당한 크기로 썬다.\r\n\r\n12. 적당히 썬 반죽을 손가락으로 눌러, 파스타면을 만든다.\r\n\r\n13. 소금을 넣은 끓는 물에 면을 넣고 삶는다.\r\n\r\n14. 삶은 면을 볶던 재료에 넣고 함께 볶는다.\r\n\r\n15. 완성된 파스타를 그릇에 담는다.\r\n\r\n16. 파스타 위에 간 건새우를 얹는다.\r\n\r\n17. 대파 흰 부분을 잘게 썰어 올린다.\r\n\r\n18. 적당한 크기로 썬 레몬을 곁들여 마무리 한다.'),
	(303, '사와디캡 누들', NULL, 1, '1. 볼에 솜땀 파우더, 물, 레몬즙, 매실액을 넣고 잘 섞는다.\r\n\r\n2. 핸드블랜더에 건새우와 페페론치노, 우메보시, 바나나, 꾸지뽕을 넣는다.\r\n\r\n3. 2에 마늘, 흑설탕, 토마토소스를 더해 간다.\r\n\r\n4. 솜땀 소스에 간 재료들을 넣고 섞는다.\r\n\r\n5. 끓는 물에 된장을 푼다.\r\n\r\n6. 삼겹살은 적당한 크기로 썰고, 마늘은 으깬다.\r\n\r\n7. 된장을 푼 끓는 물에 으깬 마늘, 커피가루를 넣고 썰어둔 삼겹살을 넣어 삶는다.\r\n\r\n8. 끓는 물에 비빔라면의 면을 넣고 삶는다.\r\n\r\n9. 대파 흰 부분, 고추, 상추, 양파를 적당한 크기로 썬다.\r\n\r\n10. 삶은 삼겹살을 건져 적당한 크기로 썬다.\r\n\r\n11. 마른 팬에 썰어둔 삶은 삼겹살과 잘게 썬 마늘을 넣고 볶는다.\r\n\r\n12. 삶은 면을 체에 밭쳐 물을 빼내고, 얼음물에 헹군다.\r\n\r\n13. 볼에 면과 적당한 크기로 썬 오렌지, 블루베리, 해바라기씨, 손질한 채소와\r\n삶은 삼겹살, 솜땀소스를 넣고 버무린다.\r\n\r\n14. 완성된 비빔면을 접시에 담아낸다.'),
	(304, '두부딥두부딥', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n\r\n2. 적당히 썬 삼겹살을 냄비에 넣고, 기름을 두른다.\r\n\r\n3. 대파를 적당한 크기로 썰어 팬에 넣고 삼겹살과 함께 볶는다.\r\n\r\n4. 마늘을 으깨 팬에 넣고 함께 볶는다.\r\n\r\n5. 고추를 적당한 크기로 썰어 팬에 넣는다.\r\n\r\n6. 볶던 재료에 가쓰오간장을 넣는다.\r\n\r\n7. 볶은 재료에 물을 붓고, 건새우, 소금, 식초를 넣고 끓여 육수를 만든다.\r\n\r\n8. 팬에 기름을 넉넉히 두르고 대파, 고춧가루를 넣고 끓여 고추기름을 만든다.\r\n\r\n9. 도마에 물을 넉넉히 바른다.\r\n\r\n10. 연두부를 가늘게 채 썬다.\r\n\r\n11. 육수를 끓인 재료들을 체에 밭쳐 건더기를 걸러내고, 육수만 다시 끓인다.\r\n\r\n12. 육수에 전분물을 넣어 농도를 조절한다.\r\n\r\n13. 12를 그릇에 담는다.\r\n\r\n14. 국물에 가늘게 채 썬 연두부와 고추기름을 넣어 마무리한다.'),
	(305, '효도르', NULL, 1, '1. 무, 당근, 애호박을 얇게 슬라이스 한다.\r\n\r\n2. 얇게 슬라이스한 채소를 볼에 담고 소금에 절인다.\r\n\r\n3. 대합살을 씻어 손질한다.\r\n\r\n4. 핸드블렌더에 대합살, 동태포, 두부, 생강즙, 달걀 노른자를 넣고 간다.\r\n\r\n5. 불린 표고버섯은 기둥을 잘라내고, 잘게 다진다.\r\n\r\n6. 잘게 다진 표고버섯에 믹서기로 간 재료들을 넣고 잘 섞는다.\r\n\r\n7. 6을 스푼에 담고, 찜기에 찐다.\r\n\r\n8. 소금에 절인 채소들을 물로 헹구고, 가늘게 채 썬다.\r\n\r\n9. 채 썬 재료들을 볼에 담는다.\r\n\r\n10. 홍고추의 씨를 빼내고, 가늘게 채 썬다.\r\n\r\n11. 채 썬 홍고추도 채소와 함께 볼에 담고, 오코노미야키소스, 식초, 삼발소스를 넣어 버무린다.\r\n\r\n12. 스푼에 찐 해산물찜을 접시에 옮겨 담는다.\r\n\r\n13. 해산물찜 위에 양념에 버무린 채소를 올린다.\r\n\r\n14. 청양고추를 썰어서 스푼마다 하나씩 올려 완성한다.'),
	(306, '아브라카부라무시', NULL, 1, '1. 대구살을 썰어 접시에 담고, 청주와 소금을 뿌려 찜기에 찐다.\r\n\r\n2. 달걀 흰자를 분리해 머랭을 친다.\r\n\r\n3. 무는 껍질을 벗겨낸 뒤 적당한 크기로 썰어 물과 함께 믹서기에 넣고 간다.\r\n\r\n4. 믹서기로 간 무를 김발에 밭쳐 건더기만 걸러낸다.\r\n\r\n5. 표고버섯은 채 썰어 끓는 물에 데친다.\r\n\r\n6. 찐 대구살을 꺼내 식힌다.\r\n\r\n7. 불린 목이버섯을 적당한 크기로 썬다.\r\n\r\n8. 피망과 파프리카를 얇게 채 썰어 끓는 물에 데친다.\r\n\r\n9. 그릇에 찐 대구살, 데친 표고버섯, 목이버섯을 넣는다.\r\n\r\n10. 머랭에 간 무를 넣고 섞는다.\r\n\r\n11. 대구살 위에 무와 함께 섞은 머랭을 얹는다.\r\n\r\n12. 그릇에 구멍을 뚫은 포일을 씌워 찜기에 찐다.\r\n\r\n13. 냄비에 북어포, 적당한 크기로 썬 무와 대파를 넣고 육수를 낸다.\r\n\r\n14. 육수에 소금, 맛술, 간장을 넣고 끓인다.\r\n\r\n15. 육수에 청주를 넣는다.\r\n\r\n16. 팬에 북어육수를 덜어 청주와 전분물을 넣고 끓여 소스를 만든다.\r\n\r\n17. 레몬을 얇게 채 썬다.\r\n\r\n18. 그릇째 쪄낸 대구찜을 꺼내 채 썬 피망과 파프리카를 올린다.\r\n\r\n19. 19 위에 북어소스를 얹는다.\r\n\r\n20. 채 썬 레몬을 얹어 완성한다.'),
	(307, '이 만두는 모닝?', NULL, 1, '1. 양파, 당근, 대파를 손질한다.\r\n\r\n2. 기름을 두른 냄비에 손질한 채소를 넣는다.\r\n\r\n3. 2에 손질한 샐러리와 통마늘을 넣고 볶는다.\r\n\r\n4. 농어의 뼈와 껍질을 분리해 볶던 채소와 함께 볶는다.\r\n\r\n5. 볶던 재료에 뜨거운 물을 붓고 끓여 농어육수를 낸다.\r\n\r\n6. 양파, 표고버섯을 잘게 썰어 기름을 두른 팬에 볶는다.\r\n\r\n7. 농어살을 적당한 크기로 썰어 팬에 넣고 함께 볶는다.\r\n\r\n8. 볶던 재료에 화이트와인을 넣는다.\r\n\r\n9. 볼에 볶은 재료를 덜어내고, 크림치즈를 섞어 만두소를 만든다.\r\n\r\n10. 핸드블렌더에 밀가루, 달걀을 넣고 돌려 만두피 반죽을 만든다.\r\n\r\n11. 반죽을 얇게 밀어 원형틀로 찍어내 만두피를 만든다.\r\n\r\n12. 만두피 안에 만두소를 넣고 달걀물을 묻혀 만두모양을 만든다.\r\n\r\n13. 팬에 볶은 재료에 농어육수, 소금을 넣고 끓여 수프를 만든다.\r\n\r\n14. 끓는 물에 만두를 넣고 삶는다.\r\n\r\n15. 파프리카를 잘게 썰어, 썰어둔 양파, 표고버섯과 함께 볶다가 크림치즈를 넣고 섞는다.\r\n\r\n16. 대파 흰 부분을 잘게 썬다.\r\n\r\n17. 그릇에 크림치즈와 섞은 볶은 채소들을 올리고, 농어 수프를 담는다.\r\n\r\n18. 삶은 만두를 농어수프에 넣는다.\r\n\r\n19. 만두를 건져내 그릇에 담고, 잘게 썬 대파 흰 부분을 올려 마무리한다.'),
	(308, '사랑하지롤♡', NULL, 1, '1. 애호박을 길게 슬라이스 한다.\r\n\r\n2. 볼에 크림치즈, 소금, 후추, 케이퍼를 넣고 섞어 소스를 만든다.\r\n\r\n3. 끓는 물에 브로콜리와 소금을 넣고 데친 뒤, 잘게 썬다.\r\n\r\n4. 크림치즈 소스에 잘게 썬 브로콜리를 넣고 섞는다.\r\n\r\n5. 길게 슬라이스 한 애호박 위에 브로콜리를 넣은 크림치즈 소스와 훈제연어를 올리고 돌돌 만다.\r\n\r\n6. 애호박말이를 꼬치로 고정하고, 기름을 두른 팬에 굽는다.\r\n\r\n7. 팬에 물을 끓이다가 식초를 넣고, 달걀을 넣어 수란을 만든다.\r\n\r\n8. 수란을 건져 물기를 제거하고 접시에 담는다.\r\n\r\n9. 구운 애호박말이의 꼬치를 제거하고 접시에 담는다.\r\n\r\n10. 볼에 달걀노른자와 머스터드소스를 넣고 섞는다.\r\n\r\n11. 팬에 올리브오일, 밀가루를 넣고 끓이다가 달걀머스터드소스를 넣어 섞는다.\r\n\r\n12. 소스에 후추를 뿌린다.\r\n\r\n13. 수란과 애호박말이 위에 달걀머스터드 소스를 뿌리고, 후추를 뿌린다.\r\n\r\n14. 훈제연어를 장미꽃 모양으로 말아 데커레이션한다.'),
	(309, '샐러드 바이블', NULL, 1, '1. 냄비에 레몬즙과 마누카 꿀을 넣고 끓인다.\r\n\r\n2. 1에 전분물, 퓨어 올리브오일을 넣고 섞어 레몬꿀소스를 만든다.\r\n\r\n3. 레몬꿀소스를 두 개의 볼에 나눠 담고, 한 곳에 트러플 페스토를 넣고 섞어 트러플소스를 만든다.\r\n\r\n4. 볼에 발사믹크림, 퓨어 올리브오일, 잘게 다진 양파를 넣고 섞어 발사믹소스를 만든다.\r\n\r\n5. 냄비에 휘핑크림을 넣고 끓인 뒤, 냄비 채 얼음물에 식힌다.\r\n\r\n6. 방울토마토, 딸기, 청포도를 썬다.\r\n\r\n7. 마늘을 잘게 다져 볼에 넣는다.\r\n\r\n8. 다진 마늘에 잘게 다진 이탈리안 파슬리를 더한다.\r\n\r\n9. 8에 퓨어 올리브오일, 올리브오일, 소금을 넣고 섞어 올리브오일소스를 만든다.\r\n\r\n10. 생모차렐라 치즈를 적당한 크기로 손질한다.\r\n\r\n11. 적당한 크기로 자른 생모차렐라 치즈를 식힌 휘핑크림에 넣는다.\r\n\r\n12. 아랍염소치즈를 손질한다\r\n\r\n13. 볼에 휘핑크림, 홍차잼을 넣고 거품기로 저어 홍차크림소스을 만든다.\r\n\r\n14. 다섯 개의 접시에 크림을 묻힌 생모차렐라 치즈와 아랍염소치즈를 각각 담는다.\r\n\r\n15. 크림을 묻힌 생모차렐라 치즈에 올리브오일소스를 얹는다.\r\n\r\n16. 크림을 묻힌 생모차렐라 치즈에 발사믹소스와 잘게 썬 딸기를 얹는다.\r\n\r\n17. 아랍염소치즈 위에 홍차크림소스를 얹고, 얇게 썬 청포도를 올린다.\r\n\r\n18. 올리브오일소스를 얹은 모차렐라치즈 위에 얇게 썬 방울토마토를 얹는다.\r\n\r\n19. 크림을 묻힌 생모차렐라 치즈에 레몬꿀소스를 얹고, 바질을 잘라 올린다.\r\n\r\n20. 아랍염소치즈 위에 트러플소스를 얹는다.\r\n\r\n21. 올리브오일소스 치즈 위에 루콜라를 곁들인다.'),
	(310, '풍미풍미풍미 업', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n\r\n2. 등심 햄을 채 썬다.\r\n\r\n3. 마늘을 다진다.\r\n\r\n4. 양파를 채 썬다.\r\n\r\n5. 기름을 두른 팬에 다진 마늘, 채 썬 양파, 버터, 손질한 삼겹살과 등심 햄, 후추, 고춧가루를 넣는다.\r\n\r\n6. 5에 간장을 넣고 볶는다.\r\n\r\n7. 볶은 재료에 인스턴트 카레가루를 넣고, 물을 부어 끓인다.\r\n\r\n8. 끓는 물에 글루텐프리 파스타면을 삶는다.\r\n\r\n9. 스위트콘 통조림을 체에 밭쳐 물기를 빼내고, 믹서기에 넣는다.\r\n\r\n10. 스위트콘을 넣은 믹서기에 휘핑크림, 후추를 넣고 간다.\r\n\r\n11. 끓이던 카레소스에 치킨스톡을 넣는다.\r\n\r\n12. 삶은 글루텐프리 파스타면을 건져 찬물로 헹궈내고, 그릇에 담는다.\r\n\r\n13. 글루텐프리 파스타면 위에 카레소스를 올린다.\r\n\r\n14. 믹서기에 간 크림옥수수소스를 지퍼백에 넣고 모서리를 잘라 카레면 위에 짜낸다.\r\n\r\n15. 14 위에 바질을 다져 올려 마무리한다.'),
	(311, '채끝 인 더 대파', NULL, 1, '1. 채끝등심을 잘게 썰어 기름을 두른 팬에 넣는다.\r\n\r\n2. 양파를 채 썰어 채끝등심과 함께 볶는다.\r\n\r\n3. 다른 팬에 잘게 썬 대파와 버터를 넣고 볶는다.\r\n\r\n4. 양쪽 팬에 우유를 넣고, 채끝등심을 볶던 팬에는 휘핑크림도 함께 넣는다.\r\n\r\n5. 전자레인지에 즉석밥을 돌린다.\r\n\r\n6. 핸드블렌더에 우유와 함께 끓인 볶은 대파를 넣고 간다.\r\n\r\n7. 6에 적당량의 밥과, 트러플 페스토를 넣고 갈아 대파퓌레를 만든다.\r\n\r\n8. 접시에 대파퓌레를 담는다.\r\n\r\n9. 종이포일로 통후추를 감싼 뒤, 냄비로 두드려 부순다.\r\n\r\n10. 우유&크림에 졸이던 채끝등심에 통후추가루를 넣는다.\r\n\r\n11. 10에 소금으로 간을 한다.\r\n\r\n12. 끓인 재료를 체에 밭쳐 소스만 남긴 뒤, 발사믹크림과 올리브오일을 넣고 섞는다.\r\n\r\n13. 소스에 버터를 넣고 섞은 뒤, 마른 팬에 붓고 끓인다.\r\n\r\n14. 채끝등심의 양면에 소금, 후추를 뿌린다.\r\n\r\n15. 기름을 두른 팬에 채끝등심을 넣고, 버터와 통마늘을 넣어 굽는다.\r\n\r\n16. 구운 채끝등심 스테이크를 꺼내 레스팅한다.\r\n\r\n17. 레스팅한 채끝등심 스테이크를 적당한 크기로 잘라 접시에 올린다.\r\n\r\n18. 스테이크 위에 이탈리안 파슬리를 올린다.\r\n\r\n19. 접시에 후추소스를 얹고, 스테이크에 소금, 후추를 뿌린다.\r\n\r\n20. 후추소스에 마누카 꿀을 넣어 마무리한다.'),
	(312, '퐁네프의 연어들', NULL, 1, '1. 레드향의 껍질을 잘라내고, 과육만 적당한 크기로 썬다.\r\n\r\n2. 접시에 손질한 레드향, 잘게 뜯은 아랍염소치즈, 루콜라를 담아 샐러드를 만든다.\r\n\r\n3. 면보에 석류알을 넣고 즙을 낸 후, 올리브오일, 소금, 후추를 섞어 드레싱을 만든다.\r\n\r\n4. 샐러드 위에 석류드레싱을 뿌려 애피타이저를 완성한다.\r\n\r\n5. 손질된 딸기를 믹서기에 넣고 간 후, 체에 밭쳐 내린다.\r\n\r\n6. 볼에 휘핑크림, 설탕을 넣고 거품기로 쳐서 크림을 만든다.\r\n\r\n7. 그릇에 곱게 간 딸기를 담고, 크림을 올린다.\r\n\r\n8. 7위에 이탈리안 파슬리와 올리브오일을 올려 디저트를 완성한다.\r\n\r\n9. 대파 흰 부분을 잘게 썰어, 기름을 두른 팬에 볶다가 소금, 다진 마늘, 설탕을 넣는다.\r\n\r\n10. 대파를 볶는 팬에 .휘핑크림과 우유를 넣고 끓인다.\r\n\r\n11. 대파를 끓이는 팬에 소금, 후추로 간한다.\r\n\r\n12. 이탈리안 파슬리를 잘게 썰어 대파를 끓이던 팬에 넣고 함께 끓인다.\r\n\r\n13. 껍질을 벗긴 새우등에 칼집을 내 일자로 편다.\r\n\r\n14. 기름을 두른 팬에 손질한 새우를 넣고, 스푼으로 눌러 익힌다.\r\n\r\n15. 연어를 얇게 포 뜬다.\r\n\r\n16. 포 뜬 연어에 구운 새우를 넣고 돌돌 만다.\r\n\r\n17. 새우연어말이를 기름을 두른 팬에 굽는다.\r\n\r\n18. 끓인 대파크림소스를 체에 밭쳐 건더기만 걸러내 다시 팬에 볶는다.\r\n\r\n19. 대파크림소스와, 새우연어말이에 레몬껍질을 갈아 넣는다.\r\n\r\n20. 대파크림소스에 레몬즙을 넣고, 그릇에 담는다.\r\n\r\n21. 대파크림소스 위에 새우연어말이를 올린다.\r\n\r\n22. 새우연어말이 위에 이탈리안 파슬리 잎을 올려 완성한다.'),
	(313, '아뵤~카도', NULL, 1, '1. 끓는 물에 토마토, 마늘, 당근, 양파, 대파, 통후추, 레몬, 소금을 넣어 채소육수를 낸다.\r\n\r\n2. 연어의 껍질을 벗겨내고 채소육수에 연어살을 넣어 데친다.\r\n\r\n3. 핸드블렌더로 블루베리를 갈아 체에 밭쳐 즙만 내린 후,\r\n팬에 옮기고 레몬즙, 설탕을 더해 끓여 블루베리 소스를 만든다.\r\n\r\n4. 끓인 블루베리 소스를 식힌다.\r\n\r\n5. 양파, 케이퍼를 잘게 다진다.\r\n\r\n6. 볼에 잘게 다진 양파와 케이퍼, 홀그레인 머스터드, 올리브오일, 소금을 넣고 섞는다.\r\n\r\n7. 6에 랠리시 소스, 잘게 다진 토마토, 후추를 넣고 섞어 케이퍼 소스를 완성한다.\r\n\r\n8. 볼에 밀가루, 소금, 물을 넣고 반죽을 만든다.\r\n\r\n9. 아보카도를 적당한 크기로 잘라 반죽을 입힌다.\r\n\r\n10. 반죽을 입힌 아보카도를 기름에 튀긴다.\r\n\r\n11. 아보카도 튀김을 건져 기름을 뺀다.\r\n\r\n12. 연어껍질을 기름에 튀긴 뒤, 건져서 기름을 뺀다.\r\n\r\n13. 접시에 케이퍼 소스를 깔고, 아보카도 튀김을 올린다.\r\n\r\n14. 아보카도 튀김 위에 데친 연어를 올린다.\r\n\r\n15. 튀긴 연어껍질을 잘게 썰어 데친 연어 위에 올리고, 케이퍼 소스를 한 번 더 뿌린다.\r\n\r\n16. 블루베리 소스를 뿌려 완성한다.'),
	(314, '불량만쥬', NULL, 1, '1. 볼에 달걀, 밀가루, 찹쌀가루, 베이킹파우더, 소금, 설탕을 넣고 섞는다.\r\n\r\n2. 전자레인지에 버터를 넣고 녹인다.\r\n\r\n3. 반죽에 우유를 넣고 섞는다.\r\n\r\n4. 3에 녹인 버터와 테프를 넣고 섞어 반죽을 만든다.\r\n\r\n5. 기름을 두른 팬에 채 썬 양파, 적당한 크기로 썬 베이컨, 후추, 소금, 식초를 넣고 볶는다.\r\n\r\n6. 끓는 기름에 반죽을 넣고 튀겨 빵을 만든다.\r\n\r\n7. 바나나를 적당한 크기로 잘라 반죽을 입힌 뒤, 기름에 튀긴다.\r\n\r\n8. 튀긴 빵과 바나나튀김을 건져 기름을 빼낸다.\r\n\r\n9. 볼에 땅콩버터, 설탕, 휘핑크림을 넣고 섞어 소스를 만든다.\r\n\r\n10. 부추를 적당한 크기로 잘라 볶던 재료에 넣고 함께 볶는다.\r\n\r\n11. 땅콩버터크림소스를 소스볼에 담는다.\r\n\r\n12. 접시에 빵, 바나나튀김, 소스볼을 올린다.\r\n\r\n13. 튀긴 빵 사이에 볶은 재료들을 넣고 샌드위치를 만들어 완성한다.'),
	(315, '탄복면', NULL, 1, '1. 볼에 밀가루, 소금, 물을 넣고 치대어 면 반죽을 한다.\r\n\r\n2. 면 반죽을 덮어 휴지한다.\r\n\r\n3. 휴지한 면 반죽을 밀대로 얇게 민다.\r\n\r\n4. 얇게 민 면 반죽을 접고 칼로 썰어 면을 만든다.\r\n\r\n5. 만든 면을 삶은 후에 찬물에서 식힌다.\r\n\r\n6. 기름이 담긴 냄비에 대파와 고춧가루를 넣고 끓여 고추기름을 만든다.\r\n\r\n7. 끓는 물에 새우와 오징어를 넣고 익힌다.\r\n\r\n8. 익힌 오징어를 채 썬다.\r\n\r\n9. 익힌 새우의 껍데기를 발라내고, 포를 뜬다.\r\n\r\n10. 볼에 땅콩버터, 뜨거운 물, 두반장, 참깨드레싱, 굴소스를 넣고 섞어 매콤한 땅콩소스를 만든다.\r\n\r\n11. 면의 물기를 짜고, 매콤한 땅콩소스, 채 썬 오징어와 함께 버무린다.\r\n\r\n12. 고추기름을 체에 밭쳐 기름만 내린 후 면과 함께 버무린다.\r\n\r\n13. 버무린 면을 접시에 담는다.\r\n\r\n14. 포 뜬 새우를 면 위에 올린다.\r\n\r\n15. 오이를 채 썰어 면 위에 올려 완성한다.'),
	(316, '녹아내리면', NULL, 1, '1. 녹차맛 생초콜릿을 전자레인지에 돌려 녹인다.\r\n\r\n2. 볼에 밀가루, 소금, 달걀, 물을 넣고 섞어 반죽을 한다.\r\n\r\n3. 녹인 녹차맛 생초콜릿을 면 반죽에 넣고 섞는다.\r\n\r\n4. 반죽을 찜기에 붓고, 구멍을 통해 면을 내려 끓는 물에 바로 익힌다.\r\n\r\n5. 익힌 면을 체에 밭쳐 건져내고, 얼음물에서 식힌다.\r\n\r\n6. 파프리카를 직화한다.\r\n\r\n7. 얼음물에 식힌 면의 물기를 제거한다.\r\n\r\n8. 그라탱 그릇에 면>토마토소스>면 순서로 담는다.\r\n\r\n9. 직화한 파프리카의 탄 부분을 벗기고, 씨를 발라낸 후 믹서기로 간다.\r\n\r\n10. 곱게 간 파프리카를 그라탱 그릇에 올린다.\r\n\r\n11. 그라탱 그릇에 면>바질페스토 순으로 한층 더 쌓는다.\r\n\r\n12. 잣을 잘게 다져 그라탱 그릇에 올린다.\r\n\r\n13. 12 위에 낫토를 올리고, 모차렐라 치즈를 깐다.\r\n\r\n14. 그라탱 그릇에 랩을 덮고, 전자레인지에서 치즈가 녹을 때까지 익힌다.\r\n\r\n15. 청양고추를 잘게 다진다.\r\n\r\n16. 전자레인지에서 치즈를 녹인 그릇을 꺼내 토치로 표면을 익히고 잘게 썬 청양고추를 뿌린다.'),
	(317, '포켓몽', NULL, 1, '1. 냄비에 오렌지주스를 끓인다.\r\n\r\n2. 애호박과 당근을 동그란 모양으로 파낸다.\r\n\r\n3. 동그랗게 손질한 채소를 오렌지주스에 넣고 조린다.\r\n\r\n4. 오렌지주스에 조리던 재료에 소금과 버터를 넣는다.\r\n\r\n5. 닭가슴살을 두드려 얇게 편다.\r\n\r\n6. 얇게 편 닭가슴살에 소금, 후추를 뿌린다.\r\n\r\n7. 슬라이스 치즈, 모차렐라 치즈, 버터를 얇게 편 닭가슴살 안에 넣고 감싼다.\r\n\r\n8. 기름을 두른 팬에 치즈를 넣고 감싼 닭가슴살을 굽는다.\r\n\r\n9. 구운 닭가슴살을 전자레인지에 넣고 속까지 익힌다.\r\n\r\n10. 믹서기에 자몽청, 레몬즙, 레드와인을 넣고 곱게 간다.\r\n\r\n11. 갈아낸 재료를 체에 밭쳐 건더기를 걸러내고, 팬에 끓인다.\r\n\r\n12. 끓이던 재료에 소금, 설탕, 크림파스타소스를 섞는다.\r\n\r\n13. 12에 우유, 버터를 넣고 졸여 소스를 만든다.\r\n\r\n14. 접시에 소스를 넓게 깐다.\r\n\r\n15. 소스 위에 오렌지주스에 조린 애호박과 당근을 올린다.\r\n\r\n16. 익힌 닭가슴살을 접시 중앙에 올리고 소스로 덮어 마무리한다.'),
	(318, '무적 카레레인저', NULL, 1, '1. 기름을 47도로 데운다.\r\n\r\n2. 연어를 적당한 크기로 손질해 데운 기름에 넣고 익힌다.\r\n\r\n3. 대파 흰부분과 양파를 잘게 다진다.\r\n\r\n4. 기름을 두른 팬에 다진 생강, 다진 대파, 다진 양파, 인스턴트 카레,\r\n우유를 넣고 끓여 카레소스를 만든다.\r\n\r\n5. 불린 미역을 잘게 다진다.\r\n\r\n6. 다른 팬에 기름을 두르고, 다진 양파와 잘게 썬 미역, 크림파스타소스, 우유를 넣고 끓인다.\r\n\r\n7. 미역크림소스에 즉석밥, 후추, 소금, 참기름, 간장, 버터를 넣고 볶아 리소토를 만든다.\r\n\r\n8. 익힌 연어를 건져 기름기를 뺀다.\r\n\r\n9. 카레소스에 망고, 사과맛 음용식초를 넣는다.\r\n\r\n10. 연어 위에 소금을 뿌리고, 브로콜리 윗부분만 칼로 긁어내 뿌린다.\r\n\r\n11. 접시에 미역리소토를 담고, 카레소스를 두른다.\r\n\r\n12. 11 위에 연어를 올리고, 고춧가루를 체에 밭쳐 뿌려 완성한다.'),
	(319, '민들레 만들래', NULL, 1, '1. 민들레를 잘게 썬다.\r\n\r\n2. 볼에 잘게 썬 민들레와 달걀, 모차렐라 치즈, 레몬즙을 넣는다.\r\n\r\n3. 2에 후추, 소금, 잘게 썬 애호박, 밀가루를 넣고 섞어 민들레 반죽을 만든다.\r\n\r\n4. 넓은 접시에 밀가루를 깔고, 달걀물을 만든다.\r\n\r\n5. 닭가슴살에 깊게 칼집을 내서 펼친다.\r\n\r\n6. 펼친 닭가슴살 안에 모차렐라 치즈를 넣고 여민 뒤\r\n밀가루>달걀물>밀가루>달걀물 순으로 튀김옷을 입힌다.\r\n\r\n7. 튀김옷을 입힌 닭가슴살을 기름에 넣고 튀긴다.\r\n\r\n8. 민들레 반죽을 적당한 크기로 빚어 밀가루를 묻힌 뒤 튀긴다.\r\n\r\n9. 볼에 손질한 민들레를 담고, 레몬즙, 소금, 후추, 올리브오일을 넣고 버무려 민들레샐러드를 만든다.\r\n\r\n10. 오이를 얇게 슬라이스 해 민들레샐러드를 돌돌 만다.\r\n\r\n11. 오이를 채 썬다.\r\n\r\n12. 토마토를 슬라이스 해 접시에 담는다.\r\n\r\n13. 채 썬 오이에 레몬즙을 뿌린다.\r\n\r\n14. 민들레튀김, 닭가슴살튀김의 기름을 뺀다.\r\n\r\n15. 토마토 슬라이스 위에 민들레튀김을 얹고, 오이채를 올린다.\r\n\r\n16. 닭가슴살튀김과 오이민들레말이를 차례로 접시에 담는다.\r\n\r\n17. 레몬을 곁들여 완성한다.'),
	(320, '자반 방아를 돌려라', NULL, 1, '1. 자반고등어를 손질한다.\r\n\r\n2. 손질한 고등어의 등부분에 올리브오일, 후추를 뿌리고 방아잎을 덮는다.\r\n\r\n3. 기름을 두른 팬에 방아잎을 덮은 자반고등어의 등부분부터 굽는다.\r\n\r\n4. 자반고등어의 한 쪽이 익으면 뒤집고, 레몬껍질을 갈아 넣는다.\r\n\r\n5. 고추를 잘게 썰어 자반고등어와 함께 익힌다.\r\n\r\n6. 양파를 잘게 썬 뒤, 기름을 두른 팬에 넣고 볶는다.\r\n\r\n7. 애호박을 잘게 썰어 양파를 볶는 팬에 넣고 다진 마늘을 더해 함께 볶는다.\r\n\r\n8. 파인애플을 잘게 썰어 팬에 넣고 함께 볶는다.\r\n\r\n9. 오징어를 잘게 썰어 팬에 넣고 함께 볶다가 후추를 뿌린다.\r\n\r\n10. 끓는 물에 톰얌페이스트를 넣고 풀어 톰얌소스를 만든다.\r\n\r\n11. 볶던 재료에 톰얌소스, 간 레몬껍질, 잘게 썬 방아잎을 넣어 함께 볶는다.\r\n\r\n12. 굽던 자반고등어에 레몬즙을 뿌린다.\r\n\r\n13. 그릇에 볶은 재료를 담는다.\r\n\r\n14. 오이를 잘게 채 썰어 레몬즙과 후추에 버무린다.\r\n\r\n15. 볶은 재료 위에 오이채무침과 방아잎을 덮어 구운 자반고등어를 올려 완성한다.'),
	(321, '다말았씨유', NULL, 1, '1. 볼에 밀가루, 소금, 뜨거운 물을 넣고 익반죽을 만든다.\r\n\r\n2. 물에 적신 행주로 익반죽을 덮어 숙성시킨다.\r\n\r\n3. 기름을 두른 팬에 잘게 썬 대파, 양파를 넣는다.\r\n\r\n4. 3에 잘게 썬 핫바, 달걀, 인스턴트 불닭 볶음밥을 넣고 볶는다.\r\n\r\n5. 볶음밥에 케첩으로 간을 한다\r\n\r\n6. 숙성시킨 반죽을 치대고, 적당한 크기로 두 덩이를 떼어내 납작하게 누른다.\r\n\r\n7. 두 개의 반죽에 참기름을 바르고 겹친 뒤, 밀대로 얇게 민다.\r\n\r\n8. 마른 팬에 춘빙 반죽을 넣고 굽는다.\r\n\r\n9. 스트링치즈를 길게 이등분 한다.\r\n\r\n10. 구운 춘빙을 바닥에 강하게 내려쳐 두 장으로 분리한다.\r\n\r\n11. 춘빙 위에 스트링 치즈와 각종 재료와 함께 볶은 인스턴트 볶음밥을 넣고 돌돌 만다.\r\n\r\n12. 재료를 넣어 만 춘빙을 넉넉한 기름에 튀긴다.\r\n\r\n13. 볼에 채 썬 양파와 사과, 잘게 다진 명이나물 장아찌, 참깨&흑임자 드레싱,\r\n스위트칠리소스를 넣고 버무린다.\r\n\r\n14. 튀긴 춘빙을 적당한 크기로 썬다.\r\n\r\n15. 접시에 춘빙을 담고, 명이사과샐러드를 곁들인다.'),
	(322, '닭.된.겨', NULL, 1, '1. 물에 간장을 풀고, 길게 칼집 낸 대파를 넣어 삶는다.\r\n\r\n2. 고추와 마늘을 으깬다.\r\n\r\n3. 기름을 두른 팬에 으깬 고추와 마늘, 된장을 넣고 볶는다.\r\n\r\n4. 볼에 달걀노른자, 간장을 넣는다.\r\n\r\n5. 매콤하게 볶은 된장을 물에 풀고, 체에 밭쳐 국물만 볼에 넣는다.\r\n\r\n6. 볼에 연겨자를 넣는다.\r\n\r\n7. 물이 끓는 냄비 위에 소스 재료를 넣은 볼을 올리고, 설탕과 레몬즙을 넣는다.\r\n\r\n8. 끓는 물 위에서 소스를 저어 된장홀랜다이즈 소스를 만든다.\r\n\r\n9. 데친 대파를 얼음물에 넣고 식힌다.\r\n\r\n10. 훈제닭가슴살을 두드린 후, 결대로 잘게 찢는다.\r\n\r\n11. 된장홀랜다이즈 소스에 레몬껍질을 갈아 넣는다.\r\n\r\n12. 볼에 잘게 찢은 닭가슴살과 적당한 크기로 썬 데친 대파, 된장홀랜다이즈 소스를 넣고 버무린다.\r\n\r\n13. 버무린 재료를 그릇에 담고, 깨를 뿌려 마무리한다.'),
	(323, '애플리케이크', NULL, 1, '1. 사과는 껍질을 깎고, 적당한 크기로 썬다.\r\n\r\n2. 믹서기에 사과, 설탕, 밀가루, 물을 넣고 간다.\r\n\r\n3. 2를 볼에 담고 전자레인지에 넣어 졸인다.\r\n\r\n4. 볼에 달걀흰자와 설탕을 넣고 머랭을 친다.\r\n\r\n5. 건빵을 지퍼백에 담고 두드려서 으깬다.\r\n\r\n6. 5에 버터쿠키를 넣고 함께 으깬다.\r\n\r\n7. 으깬 과자를 핸드블렌더로 한 번 더 간다.\r\n\r\n8. 곱게 간 과자와 머랭을 볼에 담고 섞어 반죽을 만든다.\r\n\r\n9. 반죽을 컵에 담고, 전자레인지에 넣어 익혀 빵처럼 만든다.\r\n\r\n10. 믹서기에 맛밤, 설탕, 우유, 밀가루를 넣고 간다.\r\n\r\n11. 10을 컵에 담고 전자레인지에 넣어 졸인다.\r\n\r\n12. 빵 위에 졸인 사과를 올려 완성한다.'),
	(324, '본능족으로', NULL, 1, '1. 사과를 채 썰어 볼에 담는다.\r\n\r\n2. 스트링 치즈를 길게 찢어 채 썬 사과와 함께 담는다.\r\n\r\n3. 2에 해바라기씨, 갈아낸 레몬껍질, 레몬즙, 후추, 소금,\r\n올리브 오일을 넣고 버무려 샐러드를 만든다.\r\n\r\n4. 지퍼백에 매운맛 감자칩과 건빵을 담고 두드려 으깬다.\r\n\r\n5. 으깬 과자를 접시에 넓게 깐다.\r\n\r\n6. 볼에 감자샐러드를 담는다.\r\n\r\n7. 족발을 잘게 썰어 감자샐러드와 섞는다.\r\n\r\n8. 족발을 넣은 감자샐러드에 레몬 껍질을 갈아 넣고 섞은 뒤, 적당한 크기로 빚는다.\r\n\r\n9. 동그랗게 빚은 족발감자샐러드에 으깬 과자를 입히고, 해바라기씨를 올려 고로케모양을 낸다.\r\n\r\n10. 갈릭디핑소스, 스위트칠리소스, 후추, 소금을 섞어 소스를 만든다.\r\n\r\n11. 그릇에 사과치즈샐러드를 담고, 족발감자고로케를 올린다.\r\n\r\n12. 소스를 따로 담아낸다.'),
	(325, '오빤 채소스타일', NULL, 1, '1. 기름을 두른 팬에 다진 마늘과 느타리버섯, 팽이버섯을 찢어 넣는다.\r\n\r\n2. 양파, 애호박, 당근을 채 썰어 팬에 넣는다.\r\n\r\n3. 채소에 소금, 후추로 간을 해서 볶는다.\r\n\r\n4. 볶은 채소에 복분자 데리야키 소스, 레몬즙을 넣는다.\r\n\r\n5. 볶은 채소를 볼에 담아 한 김 식힌다.\r\n\r\n6. 끓는 물에 소금을 넣고, 냉동 완두콩을 데친다.\r\n\r\n7. 데친 완두콩의 물기를 뺀다.\r\n\r\n8. 당근, 양파를 잘게 썰어 팬에 담는다.\r\n\r\n9. 닭안심살을 적당한 크기로 자른 뒤, 당근, 양파와 함께 볶는다.\r\n\r\n10. 9에 데친 완두콩을 넣고 함께 볶는다.\r\n\r\n11. 볶던 재료에 올리브오일, 뜨거운 물, 소금, 후추를 넣는다.\r\n\r\n12. 뜨거운 물에 적신 라이스페이퍼에 한 김 식혀둔 채소와\r\n채 썬 깻잎을 올리고 돌돌 말아 스프링롤을 만든다.\r\n\r\n13. 토마토를 슬라이스해 접시에 담는다.\r\n\r\n14. 13에 양상추를 채 썰어 올린다.\r\n\r\n15. 볼에 잘게 다진 마늘, 간장, 참기름, 식초, 설탕, 올리브오일,\r\n오리엔탈 드레싱, 잘게 썬 토마토를 넣고 섞어 소스를 만든다.\r\n\r\n16. 토마토 슬라이스 위에 소스를 뿌린다.\r\n\r\n17. 양상추 위에 스프링롤을 얹는다.\r\n\r\n18. 마른 팬에 아몬드 슬라이스를 볶는다.\r\n\r\n19. 볶은 재료를 곁들인다.\r\n\r\n20. 적당한 크기로 썬 오렌지를 올린다.\r\n\r\n21. 볶은 아몬드 슬라이스를 잘게 다져 뿌리고, 소스를 뿌려 마무리한다.'),
	(326, '아임 소바', NULL, 1, '1. 냄비에 무, 밴댕이, 건새우, 다시마를 넣고 끓여 육수를 낸다.\r\n\r\n2. 토마토의 꼭지를 잘라내고, 칼집을 낸다.\r\n\r\n3. 끓는 물에 칼집 낸 토마토를 데치고, 닭안심살을 함께 넣어 삶는다.\r\n\r\n4. 데친 토마토를 건져 껍질을 벗겨내고 얼음물에 담가 식힌다.\r\n\r\n5. 식혀둔 토마토를 슬라이스 해 시판 메밀소스에 담가 냉동실에 보관한다.\r\n\r\n6. 콜라비를 가늘게 채 썰어 얼음물에 담가둔다.\r\n\r\n7. 양상추를 잘게 채 썰어 콜라비와 함께 얼음물에 담가둔다.\r\n\r\n8. 애호박을 얇게 돌려 깎는다.\r\n\r\n9. 얇게 돌려 깎은 애호박을 면처럼 가늘게 채 썬다.\r\n\r\n10. 채 썬 애호박면을 데친 뒤, 얼음물에 식힌다.\r\n\r\n11. 양파를 채 썬다.\r\n\r\n12. 삶은 닭안심살을 꺼내 얼음물에 식힌다.\r\n\r\n13. 끓는 물에 메밀생면을 삶는다.\r\n\r\n14. 닭안심살을 적당한 크기로 찢어 채 썬 양파와 함께 볼에 담고\r\n참깨&흑임자소스에 버무려 차게 식힌다.\r\n\r\n15. 삶은 메밀면을 찬물에 헹구고, 냉동실에 보관한다.\r\n\r\n16. 냄비에 육수, 맛술, 간장, 설탕을 넣고 끓여 소바 국물을 만든다.\r\n\r\n17. 소바 국물을 얼음물 위에서 식히며, 식초와 레몬즙을 넣는다.\r\n\r\n18. 그릇에 메밀면, 애호박면, 채 썬 콜라비&양상추, 소스에 절인 토마토를 담는다.\r\n\r\n19. 레몬껍질을 채 썰어 올린다.\r\n\r\n20. 마지막으로 소바 국물을 넣고 깨, 와사비를 곁들여 완성한다.'),
	(327, '바나나를 튀기면 반하나?', NULL, 1, '1. 밀가루(1컵), 전분(1ts), 탄산수(2컵)을 섞어 튀김반죽을 만든다.\r\n\r\n2. 튀김 반죽을 조금 높은 곳에서 180도의 기름에 붓고 14초간 튀긴다.\r\n\r\n3. 튀긴 텐카스를 건져내 기름기를 뺀다.\r\n\r\n4. 오리엔탈 드레싱과 올리브오일을 4:1 비율로 섞어 소스를 만든다.\r\n\r\n5. 소스에 레몬껍질을 갈아 넣는다.\r\n\r\n6. 바나나의 끝부분을 잘라내고, 2cm 정도 두께로 썰어 밀가루와 전분을 묻힌다.\r\n\r\n7. 밀가루와 전분을 묻힌 바나나에 튀김반죽 > 텐카스 순으로 튀김옷을 입힌다.\r\n\r\n8. 튀김옷을 입힌 바나나를 180도의 기름에 50초 가량 튀긴다.\r\n\r\n9. 바나나튀김을 접시에 담고, 소스를 따로 낸다.'),
	(328, '렌틸콩그레츄레이션', NULL, 1, '1. 불린 렌틸콩을 삶는다. (한 가족 분량 약 1~2컵)\r\n\r\n2. 마른 팬에 베이컨을 굽는다.\r\n\r\n3. 대파(7cm), 당근(반 개), 빨간색 파프리카(한 개)를 잘게 썬다.\r\n\r\n4. 렌틸콩을 삶는 물에 소금(1Ts)을 넣는다.\r\n\r\n5. 토마토에 십(十)자로 칼집을 내고 렌틸콩을 삶는 물에 데친다.\r\n\r\n6. 바싹 구운 베이컨을 건져내 기름기를 뺀다.\r\n\r\n7. 베이컨을 구운 기름을 조금만 남기고 버린다.\r\n\r\n8. 베이컨을 구웠던 팬에 잘게 다진 채소를 넣어 2-3분 정도 볶는다.\r\n\r\n9. 데친 토마토의 껍질을 벗긴다.\r\n\r\n10. 볶던 채소에 렌틸콩 삶은 물을 1국자 넣는다.\r\n\r\n11. 10에 마늘 두 쪽을 잘게 다져 넣는다.\r\n\r\n12. 렌틸콩을 삶던 냄비에서 국물을 덜어내고, 볶은 채소를 넣는다.\r\n\r\n13. 핸드블렌더로 렌틸콩과 채소를 곱게 갈아 수프를 만든다.\r\n\r\n14. 데쳐서 껍질을 벗긴 토마토를 적당한 크기로 썰어 수프에 넣는다.\r\n\r\n15. 기름기를 뺀 베이컨을 잘게 썬다.\r\n\r\n16. 핸드블렌더로 곱게 간 렌틸콩 수프에 소금, 후추로 간 한다.\r\n\r\n17. 고추(한 개)와 마늘(한 쪽)을 잘게 다져 볼에 담고, 식초(5Ts)를 붓는다.\r\n\r\n18. 렌틸콩 수프를 그릇에 담는다.\r\n\r\n19. 수프 위에 잘게 썬 베이컨을 올린다.\r\n\r\n20. 렌틸콩 수프와 마늘&고추 식초를 함께 곁들여낸다.'),
	(329, '오늘도전', NULL, 1, '1. 볼에 밀가루, 전분, 달걀, 소금, 물을 넣고 반죽을 만든다.\r\n\r\n2. 반건조오징어를 잘게 썰어 반죽에 넣는다.\r\n\r\n3. 대파, 햄 통조림, 아스파라거스를 잘게 썰어 반죽에 넣고 섞는다.\r\n\r\n4. 기름을 두른 팬에 반죽을 넓게 펴서 부친다.\r\n\r\n5. 소면을 잘게 부숴 기름에 튀긴 후 건져내 기름기를 뺀다.\r\n\r\n6. 망고는 과육만 적당한 크기로 썬다.\r\n\r\n7. 체리의 꼭지를 떼고, 씨를 발라낸 후 망고 과육과 함께 핸드블렌더로 간다.\r\n\r\n8. 곱게 간 망고&체리에 마요네즈, 레몬즙을 넣고 섞어 과일소스를 만든다.\r\n\r\n9. 전을 부치는 팬에 버터를 넣는다.\r\n\r\n10. 전을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n11. 전 위에 과일소스를 올리고, 튀긴 소면을 뿌려 완성한다.'),
	(330, '풍볶이 말았소면', NULL, 1, '1. 양파, 대파 흰 부분, 베이컨을 적당한 크기로 손질한다.\r\n\r\n2. 기름을 두른 팬에 베이컨을 넣고 볶는다.\r\n\r\n3. 끓는 물에 소면을 삶는다.\r\n\r\n4. 볶던 베이컨에 다진 마늘, 고추장을 넣고 볶는다.\r\n\r\n5. 4에 고춧가루, 간장을 넣고 볶는다.\r\n\r\n6. 볶은 양념에 손질한 양파와 대파, 물을 넣고 끓여 떡볶이 양념을 만든다.\r\n\r\n7. 재료에 물엿, 설탕을 넣고 섞어 소스를 만든다.\r\n\r\n8. 삶은 소면을 체에 밭쳐 물에 헹군다.\r\n\r\n9. 기름을 두른 팬에 삶은 소면을 넓게 깔고 부친다.\r\n\r\n10. 햄 통조림을 적당한 크기로 썰어 삶은 소면으로 돌돌 만다.\r\n\r\n11. 10에 전분을 묻히고 기름에 튀긴다.\r\n\r\n12. 소스에 청경채를 넣는다.\r\n\r\n13. 소스에 전분물을 넣어 농도를 잡는다.\r\n\r\n14. 기름에 부친 소면의 기름기를 제거하고, 접시에 담는다.\r\n\r\n15. 14 위에 튀긴 햄소면볼을 올리고, 소스를 얹어 마무리한다.'),
	(331, '오닭후면', NULL, 1, '1. 끓는 물에 식초를 넣고 달걀을 삶는다.\r\n\r\n2. 베이컨을 적당한 크기로 썰어 냄비에 넣는다.\r\n\r\n3. 대파 흰 부분을 적당한 크기로 썰어 베이컨과 함께 볶는다.\r\n\r\n4. 반건조오징어를 채 썰어 냄비에 넣고 함께 볶는다.\r\n\r\n5. 4에 뜨거운 물, 곰탕라면 스프를 넣고 끓인다.\r\n\r\n6. 끓이던 재료에 간장, 닭가슴살 통조림을 넣고 함께 끓인다.\r\n\r\n7. 청양고추를 잘게 썰어 국물에 넣고 끓인다.\r\n\r\n8. 짬뽕라면의 면을 삶는다.\r\n\r\n9. 끓인 국물에서 베이컨만 건져낸다.\r\n\r\n10. 대파 흰 부분을 잘게 썬다.\r\n\r\n11. 청경채를 적당한 크기로 손질한다.\r\n\r\n12. 끓는 물에 소금을 넣고 손질한 청경채를 데친다.\r\n\r\n13. 삶은 달걀의 껍데기를 벗기고 얼음물에 넣어 식힌다.\r\n\r\n14. 삶은 면을 얼음물에 헹군다.\r\n\r\n15. 씻은 면을 살짝 데친다.\r\n\r\n16. 면을 그릇에 담는다.\r\n\r\n17. 면에 라면국물을 붓는다.\r\n\r\n18. 반숙달걀과 데친 청경채를 올린다.\r\n\r\n19. 조미김을 곁들여 마무리한다.'),
	(332, '짬뽕의 순정', NULL, 1, '1. 끓는 물에 소금, 식초를 넣고 달걀을 삶는다.\r\n\r\n2. 마늘을 슬라이스 한다.\r\n\r\n3. 양파를 채 썬다.\r\n\r\n4. 양배추를 채 썬다.\r\n\r\n5. 청경채, 청양고추, 대파, 베이컨, 소시지를 적당한 크기로 썬다.\r\n\r\n6. 어묵, 고다치즈를 적당한 크기로 썬다.\r\n\r\n7. 골뱅이 통조림을 체에 밭쳐 건더기만 걸러낸다.\r\n\r\n8. 짬뽕라면의 면을 삶는다.\r\n\r\n9. 기름을 두른 팬에 손질한 재료와 고춧가루, 간장, 청양고추, 짬뽕라면 스프를 넣고 볶는다.\r\n\r\n10. 볶던 재료에 우유를 넣고 끓여 국물을 만든다.\r\n\r\n11. 국물에 후추, 골뱅이, 소금을 넣고 끓인다.\r\n\r\n12. 끓이던 국물에 짬뽕라면의 건더기스프와 소금을 넣는다.\r\n\r\n13. 12에 고다치즈를 넣고 섞어 크림짬뽕국물을 만든다.\r\n\r\n14. 삶은 면을 얼음물에 넣고 식힌다.\r\n\r\n15. 삶은 달걀의 껍데기를 벗겨내고 반으로 자른다.\r\n\r\n16. 그릇에 면을 담는다.\r\n\r\n17. 면 위에 크림짬뽕국물을 담는다.\r\n\r\n18. 반숙달걀을 올려 마무리한다.'),
	(333, '터미네미트볼', NULL, 1, '1. 끓는 물에 채칼로 얇게 저민 감자를 넣고 익힌다.\r\n\r\n2. 애호박을 채칼로 길고 얇게 저민다.\r\n\r\n3. 양파를 잘게 썰어 기름을 두른 팬에 넣고 소금, 후추를 뿌려 볶는다.\r\n\r\n4. 익힌 감자를 건져 우유에 넣고 끓인다.\r\n\r\n5. 소고기 등심을 적당한 크기로 썰어 핸드블랜더에 넣고 곱게 간다.\r\n\r\n6. 곱게 간 소고기 등심에 익힌 양파를 넣고 소금, 후추로 간 해 미트볼 반죽을 만든다.\r\n\r\n7. 미트볼을 만들어 밀가루에 묻힌 뒤, 기름을 두른 팬에 굽는다.\r\n\r\n8. 기름을 두른 그릴팬에 애호박을 올리고 소금, 후추를 뿌려 굽는다.\r\n\r\n9. 구운 미트볼을 조금 남겨두고 건져낸다.\r\n\r\n10. 팬에 남은 미트볼에 화이트와인을 넣고 볶는다.\r\n\r\n11. 볶은 미트볼에 잘게 양파를 더해 함께 볶는다.\r\n\r\n12. 끓인 우유와 감자를 핸드블랜더로 곱게 간다.\r\n\r\n13. 볶은 미트볼과 양파에 곱게 간 감자와 우유를 부어 끓이다가 구운 미트볼을 넣는다.\r\n\r\n14. 13에 카망베르치즈를 넣는다.\r\n\r\n15. 마른 팬에 빵가루, 올리브오일, 버터를 넣고 볶는다.\r\n\r\n16. 접시에 구운 애호박을 올린다.\r\n\r\n17. 볼에 발사믹 드레싱, 올리브오일, 레몬즙을 넣어 발사믹소스를 만든다.\r\n\r\n18. 접시에 졸인 미트볼을 소스를 함께 얹는다.\r\n\r\n19. 마지막으로 발사믹소스와 볶은 빵가루를 올려 마무리한다.'),
	(334, '트랜스두부', NULL, 1, '1. 두부는 적당한 크기로 썰어 볼에 담아둔다.\r\n\r\n2. 오이를 길게 잘라 씨를 발라낸 뒤, 얇게 썬다.\r\n\r\n3. 얇게 썬 오이에 소금을 뿌려 절여둔다.\r\n\r\n4. 당근, 양파, 대파를 잘게 썬다.\r\n\r\n5. 마늘을 으깬다.\r\n\r\n6. 적당한 크기로 썬 두부에 전분을 묻혀 튀긴다.\r\n\r\n7. 튀긴 두부의 기름기를 뺀다.\r\n\r\n8. 기름을 두른 팬에 으깬 마늘, 다진 소고기 등심을 넣고 볶는다.\r\n\r\n9. 볶던 재료에 손질한 채소, 굴소스, 스위트 칠리소스를 넣고 볶는다.\r\n\r\n10. 9에 물을 넣고 끓여 소스를 만든다.\r\n\r\n11. 끓인 소스에 전분물을 넣는다.\r\n\r\n12. 기름기를 뺀 두부를 다시 한 번 기름에 넣고 튀긴다.\r\n\r\n13. 튀긴 두부를 건져 기름기를 뺀다.\r\n\r\n14. 기름기를 뺀 두부튀김을 소스에 넣고 볶는다.\r\n\r\n15. 볶은 두부탕수를 그릇에 담는다.\r\n\r\n16. 레몬껍질을 잘게 썰어 두부탕수 위에 올린다.\r\n\r\n17. 소금에 절인 오이의 물기를 짜고 두부탕수 위에 뿌려 마무리한다.'),
	(335, '오트밀 맛있삼', NULL, 1, '1. 우유를 끓인다.\r\n\r\n2. 오트밀을 끓는 물에 데친 후, 우유에 넣고 끓인다.\r\n\r\n3. 오트밀을 끓이는 우유에 건포도, 소금, 후추, 연유를 넣고 끓여 오트밀 수프를 만든다.\r\n\r\n4. 마른 팬에 삼겹살을 굽는다.\r\n\r\n5. 구운 삼겹살의 기름을 뺀다.\r\n\r\n6. 각종 견과류를 지퍼백에 담고, 잘게 부순 뒤 넓은 접시에 깐다.\r\n\r\n7. 고추마늘장아찌에서 마늘쫑만 잘게 썬 뒤, 부순 견과류, 깨와 함께 섞어 토핑을 만든다.\r\n\r\n8. 곶감을 적당한 크기로 썬다.\r\n\r\n9. 볼에 고추마늘장아찌의 국물만 덜어낸 뒤, 맛술을 넣고 섞어 소스를 만든다.\r\n\r\n10. 구운 삼겹살을 적당한 크기로 잘라 소스에 골고루 묻힌다.\r\n\r\n11. 10에 토핑을 골고루 묻힌다.\r\n\r\n12. 그릇에 오트밀수프를 담고, 손질한 곶감>토핑을 묻힌 삼겹살을 차례로 올린다.\r\n\r\n13. 마지막으로 레몬껍질을 갈아 올려 완성한다.'),
	(336, '미카옐로', NULL, 1, '1. 감자는 씻어서 껍질을 벗긴다.\r\n\r\n2. 껍질을 벗긴 감자와 당근, 애호박을 적당한 크기로 썬다.\r\n\r\n3. 소금을 넣은 끓는 물에, 손질한 감자와 당근을 넣고 끓인다.\r\n\r\n4. 다진 돼지고기에 소금과 후추를 뿌린다.\r\n\r\n5. 4에 달걀노른자, 즉석밥을 넣고 미트볼 반죽을 만든다.\r\n\r\n6. 반죽에 밀가루를 묻혀가며 미트볼을 만든다.\r\n\r\n7. 감자와 당근을 넣고 끓이던 물에 미트볼을 넣고 끓인다.\r\n\r\n8. 사골국을 체에 밭쳐 건더기를 걸러내고 국물만 남긴다.\r\n\r\n9. 미트볼과 채소를 끓이던 냄비에 걸러낸 사골국물을 넣고 함께 끓인다.\r\n\r\n10. 미트볼국에 잘게 썬 양파와 손질한 애호박을 넣는다.\r\n\r\n11. 달걀의 노른자만 분리한다.\r\n\r\n12. 달걀노른자에 레몬즙, 우유를 넣고 핸드블랜더로 섞는다.\r\n\r\n13. 12에 후추로 간을 하고, 미트볼국의 국물을 조금씩 넣어가며 핸드블랜더로 섞는다.\r\n\r\n14. 끓이던 국을 저어가며 국물을 넣고 섞은 달걀노른자를 넣는다.\r\n\r\n15. 소스 종지에 소금, 레몬, 잘게 썬 청양고추를 각각 담는다.\r\n\r\n16. 미트볼국에 버터를 넣는다.\r\n\r\n17. 대파를 잘게 썬다.\r\n\r\n18. 그릇에 미트볼국을 담고, 잘게 썬 대파를 올려 완성한다.'),
	(337, '하트비트', NULL, 1, '1. 기름을 두른 팬에 채 썬 양파를 넣는다.\r\n\r\n2. 돼지염통을 잘게 썰어 양파가 담긴 팬에 넣는다.\r\n\r\n3. 고추는 씨를 빼고 적당한 크기로 썰어 팬에 넣고 다른 재료들과 함께 볶다가 소금, 후추로 간한다.\r\n\r\n4. 돼지간을 적당한 크기로 썰고, 레몬즙을 뿌린다.\r\n\r\n5. 달걀물을 푼다.\r\n\r\n6. 돼지간에 밀가루>달걀물 순서로 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n7. 볶던 재료에 레드와인을 넣는다.\r\n\r\n8. 핸드블랜더로 방울토마토를 간다.\r\n\r\n9. 곱게 간 방울토마토를 재료를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n10. 볶던 재료에 고춧가루를 넣는다.\r\n\r\n11. 튀긴 간을 건져 기름을 빼낸다.\r\n\r\n12. 마늘과 대파를 얇게 썰어 마늘은 볶던 재료에 넣는다.\r\n\r\n13. 레몬을 슬라이스 해 모양을 낸다.\r\n\r\n14. 접시에 볶은 재료를 담고, 튀긴 간, 레몬, 얇게 썬 대파, 레몬즙을 곁들여 완성한다.'),
	(338, '내장풍 냉채', NULL, 1, '1. 끓는 물에 돼지염통, 돼지허파, 맛술을 넣고 삶는다.\r\n\r\n2. 기름이 담긴 냄비에 대파 흰 부분을 적당한 크기로 잘라 넣고 파기름을 낸다.\r\n\r\n3. 마늘을 다진다.\r\n\r\n4. 파기름에 고춧가루, 다진 생강을 넣고 끓여 고추기름을 만든다.\r\n\r\n5. 볼에 다진 마늘을 담고, 맛술, 간장, 설탕, 식초를 1:1:1:1 비율로 넣어 냉채소스를 만든다.\r\n\r\n6. 비닐팩에 오이를 넣고 두드려 으깬다.\r\n\r\n7. 볼에 밀가루, 전분, 소금, 물을 넣고 섞어 반죽을 만든다.\r\n\r\n8. 반죽에 식용유를 넣는다.\r\n\r\n9. 마른 팬에 반죽을 얇게 둘러 굽는다.\r\n\r\n10. 삶은 염통과 허파를 건져내 물로 헹구고, 적당한 크기로 썬다.\r\n\r\n11. 접시에 손질한 염통과 허파를 올리고, 으깬 오이를 얹는다.\r\n\r\n12. 키친타월을 깐 체에 끓인 고추기름을 부어 곱게 걸러낸다.\r\n\r\n13. 접시에 담긴 재료 위에 적당한 크기로 썬 상추를 올리고 냉채소스를 끼얹는다.\r\n\r\n14. 고추기름을 뿌려 마무리한다.\r\n\r\n15. 얇게 구운 반죽을 따로 곁들여 낸다.'),
	(339, '염통이 바샤샤샤~', NULL, 1, '1. 양파를 슬라이스 한 뒤, 석쇠에 올려 직화한다.\r\n\r\n2. 냄비에 간장, 맛술, 설탕, 청주를 넣고 끓여 간장소스를 만든다.\r\n\r\n3. 구운 양파를 간장소스에 넣고 졸인다.\r\n\r\n4. 볼에 밀가루, 전분을 넣고 섞어 튀김가루를 만든다.\r\n\r\n5. 튀김가루를 다른 볼에 덜어낸 뒤, 물을 넣고 섞어 튀김반죽을 만든다.\r\n\r\n6. 파를 얇게 썰어 튀김가루에 묻힌 뒤, 기름에 튀긴다.\r\n\r\n7. 돼지염통을 적당한 두께로 썰어 튀김가루를 묻힌다.\r\n\r\n8. 튀김반죽을 기름에 튀겨 텐카스를 만든다.\r\n\r\n9. 튀김가루를 입힌 돼지염통에 한 번 더 반죽을 입힌 뒤 기름에 넣고, 텐카스가 묻어나도록 튀긴다.\r\n\r\n10. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n11. 볼에 새우맛 후리카게, 밥, 참기름을 넣고 섞는다.\r\n\r\n12. 그릇에 양념한 밥을 담고, 파 튀김, 염통 튀김을 얹는다.\r\n\r\n13. 간장소스를 뿌려 마무리한다.'),
	(340, '초밥 베이베', NULL, 1, '1. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n2. 식초, 소금, 설탕을 3:1:2 비율로 넣고 섞어 단촛물을 만든다.\r\n\r\n3. 밥에 단촛물을 넣고 비벼 양념밥을 만든다.\r\n\r\n4. 냄비에 맛술, 청주, 간장, 설탕을 넣고 끓여 간장소스를 만든다.\r\n\r\n5. 김, 청포도, 닭가슴살 소시지를 적당한 크기로 썬다.\r\n\r\n6. 볼에 밀가루, 물을 넣고 섞어 튀김반죽을 만든다.\r\n\r\n7. 손질한 닭가슴살 소시지에 튀김반죽을 묻혀 튀긴다.\r\n\r\n8. 튀긴 닭가슴살 소시지를 건져내 기름을 뺀다.\r\n\r\n9. 마른팬에 불고기용 소고기를 펼치고 설탕을 뿌린 뒤, 토치로 직화한다.\r\n\r\n10. 손에 물을 묻히고 양념밥을 적당한 크기로 뭉친 뒤 직화한 소고기를 올린다.\r\n\r\n11. 양념밥 위에 닭가슴살 소시지 튀김을 올린 뒤 김으로 말아준다.\r\n\r\n12. 닭가슴살 소시지 튀김 위에 손질한 청포도를 올린다.\r\n\r\n13. 멸치볶음과 잘게 다진 열무김치, 들기름을 섞는다.\r\n\r\n14. 양념밥을 적당한 크기로 뭉쳐 김으로 말아준 뒤, 위에 양념한 멸치김치를 올린다.\r\n\r\n15. 닭가슴살 소시지 초밥에 케첩을 올린다.\r\n\r\n16. 소고기 초밥과 닭가슴살 소시지 초밥에 간장소스를 발라 완성한다.'),
	(341, '크레이지 크레페', NULL, 1, '1. 팬에 버터를 녹인다.\r\n\r\n2. 밀가루를 체에 밭쳐 곱게 친다.\r\n\r\n3. 핸드블랜더에 우유, 설탕, 달걀, 체 친 밀가루를 넣는다.\r\n\r\n4. 3에 녹인 버터를 넣고 갈아 크레페 반죽을 만든다.\r\n\r\n5. 냄비에 버터, 밀가루, 두유, 고구마 아이스크림을 넣고 졸여 고구마소스를 만든다.\r\n\r\n6. 마른 팬에 오렌지즙을 넣는다.\r\n\r\n7. 오렌지즙에 설탕, 오렌지 과육과 오렌지 껍질을 갈아 넣고 끓여 오렌지소스를 만든다.\r\n\r\n8. 적당한 크기로 썬 바나나 위에 설탕을 뿌린 뒤, 토치로 직화한다.\r\n\r\n9. 직화한 바나나 위에 오렌지 껍질을 갈아서 뿌린다.\r\n\r\n10. 버터를 녹인 팬에 반죽을 넣고 크레페를 굽는다.\r\n\r\n11. 접시에 고구마소스를 넓게 깐다.\r\n\r\n12. 크레페를 적당한 크기로 잘라 고구마소스 위에 얹는다.\r\n\r\n13. 크레페 위에 오렌지소스를 올린다.\r\n\r\n14. 오렌지소스 위에 직화한 바나나를 올리고 오렌지 껍질을 갈아서 뿌려 완성한다.'),
	(342, '술이 깰랑말랑 몽블랑', NULL, 1, '1. 접시를 냉동실에 보관한다.\r\n\r\n2. 볼에 체 친 밀가루, 설탕, 버터를 넣는다.\r\n\r\n3. 핸드블랜더로 견과류를 곱게 간다.\r\n\r\n4. 밀가루, 설탕, 버터가 담긴 볼에 곱게 간 견과류를 넣고 섞어 크럼 반죽을 만든다.\r\n\r\n5. 오븐용 그릇에 반죽을 깔고 오븐에 굽는다.\r\n\r\n6. 핸드블랜더에 설탕, 전분을 넣고 갈아 슈거파우더를 만든다.\r\n\r\n7. 믹서기에 얼음을 간다.\r\n\r\n8. 간 얼음에 소금을 넣고 섞은 뒤, 냉동실에 보관한다.\r\n\r\n9. 오븐에서 구운 크럼을 냉동실에 보관한다.\r\n\r\n10. 믹서기에 맛밤, 커피우유를 넣고 갈아 밤페이스트를 만든다.\r\n\r\n11. 밤페이스트를 체에 밭쳐 내린다.\r\n\r\n12. 밤페이스트를 비닐팩에 담고, 끝을 잘라 짤주머니를 만든 뒤 냉동실에 보관한다.\r\n\r\n13. 식빵을 토스트한다.\r\n\r\n14. 그릇에 소금얼음을 담고, 그 위에 접시를 올린다.\r\n\r\n15. 접시에 크럼과 적당한 크기로 썬 청포도, 적포도를 올린다.\r\n\r\n16. 접시에 녹차 아이스크림을 담는다.\r\n\r\n17. 토스트를 찢어 녹차 아이스크림에 곁들인다.\r\n\r\n18. 녹차 아이스크림 위에 밤페이스트를 올린다.\r\n\r\n19. 슈거파우더를 뿌려 완성한다.'),
	(343, '복차오루는 쌈', NULL, 1, '1. 잡채용 돼지고기 등심에 전분, 달걀물을 넣고 버무린다.\r\n\r\n2. 전분과 달걀물에 버무린 돼지고기 등심을 넉넉한 기름에 튀기듯이 볶는다.\r\n\r\n3. 볶아낸 등심을 체에 밭쳐 기름기를 뺀다.\r\n\r\n4. 대파 흰 부분을 채 썰어 찬물에 담가둔다.\r\n\r\n5. 대파, 당근, 양파, 고추, 불린 표고버섯채, 마늘을 잘게 다진다.\r\n\r\n6. 깍두기를 얇게 썬다.\r\n\r\n7. 팬에 등심을 볶았던 기름을 두르고, 손질한 채소와 고춧가루를 넣고 볶는다.\r\n\r\n8. 볶은 재료에 두반장, 중국식 다진 고추소스를 넣고 볶는다.\r\n\r\n9. 7에 간장, 설탕, 굴소스를 넣고 볶아 소스를 만든다.\r\n\r\n10. 두부피를 적당한 크기로 잘라, 뜨거운 물에 넣고 불린다.\r\n\r\n11. 중국 찹쌀떡을 끓는 물에 데친다.\r\n\r\n12. 소스에 볶은 돼지고기 등심을 넣고 섞는다.\r\n\r\n13. 12에 얇게 썬 깍두기를 넣는다.\r\n\r\n14. 그릇에 불려놓은 흰목이버섯을 깔고, 소스에 볶은 돼지고기 등심을 올린다.\r\n\r\n15. 찬물에 담가뒀던 파채와 적당한 크기로 썬 고수를 올린다.\r\n\r\n16. 불린 두부피를 접시에 따로 담아낸다.\r\n\r\n17. 데친 중국 찹쌀떡을 그릇에 담고 설탕을 뿌려낸다.'),
	(344, '일거양두', NULL, 1, '1. 핸드블랜더에 설탕, 굴소스, 간장, 다진 마늘, 청주를 넣고 갈아 불고기 양념을 만든다.\r\n\r\n2. 볼에 양고기를 담고, 적당한 크기로 썬 대파, 불고기 양념을 넣고 버무린다.\r\n\r\n3. 냄비에 양념한 양고기, 손질한 양파와 불려둔 건두부, 표고버섯채, 흰목이버섯, 목이버섯을 넣는다.\r\n\r\n4. 표고버섯 불린 물을 냄비에 붓고 끓인다.\r\n\r\n5. 끓이던 전골에 간장으로 간을 한다.\r\n\r\n6. 믹서기에 라조장, 자차이, 물을 넣고 간다.\r\n\r\n7. 6을 볼에 담고, 물, 라조장, 다진 마늘, 설탕을 넣고 섞어 소스를 만든다.\r\n\r\n8. 적당한 크기로 썬 고수와 잘게 썬 고추, 자차이소스를 종지에 각각 담아낸다.\r\n\r\n9. 완성된 전골과 세 개의 종지를 따로 낸다.'),
	(345, '나 혼자 먹게 낫토', NULL, 1, '1. 닭다리의 뼈를 발라내고 살만 넓게 편 뒤, 칼집을 낸다.\r\n\r\n2. 칼집 낸 닭다리살 양면에 소금을 뿌리고 기름을 두른 팬에서 굽는다.\r\n\r\n3. 청양고추를 손으로 두드린 뒤, 닭다리살을 굽는 팬에 넣고 함께 굽는다.\r\n\r\n4. 볼에 으깬 마늘, 식용유, 고춧가루를 넣고 랩을 씌운 뒤, 전자레인지에 돌려 고추기름을 만든다.\r\n\r\n5. 쪽파와 양파를 잘게 썰어 볼에 담는다.\r\n\r\n6. 강판에 무를 갈아 즙을 짜내고 건더기만 볼에 담는다.\r\n\r\n7. 다른 볼에 간장, 식초, 레몬즙, 설탕을 넣고 간장소스를 만든다.\r\n\r\n8. 팬에 청주를 끓인다.\r\n\r\n9. 끓인 청주를 간장소스에 넣고 섞는다.\r\n\r\n10. 잘게 썬 채소에 간장소스, 와사비, 마요네즈를 넣고 섞어 소스를 만든다.\r\n\r\n11. 볼에 검은콩 낫토와 낫토소스를 넣고 섞는다.\r\n\r\n12. 낫토에 만들어둔 소스와 참기름을 넣고 다시 한 번 섞는다.\r\n\r\n13. 즉석밥을 전자레인지에 돌린다.\r\n\r\n14. 닭가슴살을 굽는 팬의 뚜껑을 닫고 속까지 익힌다.\r\n\r\n15. 전자레인지에 돌린 고추기름을 체에 밭쳐 거른다.\r\n\r\n16. 쪽파, 파프리카를 채 썬다.\r\n\r\n17. 기름을 두른 팬에 채 썬 쪽파와 파프리카, 간장소스를 넣고 볶는다.\r\n\r\n18. 그릇에 밥을 담는다.\r\n\r\n19. 구운 닭다리살을 적당한 크기로 썬다.\r\n\r\n20. 밥 위에 구운 닭다리살, 양념한 낫토, 볶은 채소를 올리고 고추기름을 뿌려 완성한다.'),
	(346, '닭날두', NULL, 1, '1. 닭날개에 칼집을 내고, 뼈를 발라낸다.\r\n\r\n2. 닭가슴살을 적당한 크기로 썬다.\r\n\r\n3. 삼겹살을 적당한 크기로 썬다.\r\n\r\n4. 핸드블랜더에 닭가슴살, 불린 표고버섯, 채 썬 양파, 채 썬 적양파, 삼겹살을 넣고 간다.\r\n\r\n5. 곱게 간 재료에 달걀, 소금, 잘게 썬 쪽파를 넣고 만두소를 만든다.\r\n\r\n6. 뼈를 발라낸 닭날개 안에 만두소와 모차렐라 치즈를 채워 넣는다.\r\n\r\n7. 볼에 밀가루와 물을 넣고 섞어 튀김옷을 만든다.\r\n\r\n8. 닭날개만두에 밀가루를 묻힌 뒤, 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n9. 가지, 방울토마토, 파프리카를 적당한 크기로 썬다.\r\n\r\n10. 기름을 두른 팬에 채 썬 적양파와 가지, 방울토마토, 파프리카를 넣고 볶는다.\r\n\r\n11. 볶던 재료에 불린 표고버섯, 소금, 후추, 토마토소스, 설탕을 넣고 볶아 소스를 만든다.\r\n\r\n12. 고추를 잘게 썰어 소스에 넣고 함께 볶는다.\r\n\r\n13. 튀긴 닭날개만두를 소스에 넣어 졸인다.\r\n\r\n14. 소스에 졸인 닭날개만두를 먹기 좋게 잘라 접시에 담아낸다.'),
	(347, '쉑쉑 파스타', NULL, 1, '1. 끓는 물에 소금을 넣고 파스타면을 삶는다.\r\n\r\n2. 게맛살을 가늘게 찢는다.\r\n\r\n3. 양배추, 적양파, 깻잎, 파프리카를 채 썬다.\r\n\r\n4. 볼에 가늘게 찢은 게맛살, 채 썬 채소, 타르타르소스, 레몬즙, 소금을 넣고 섞어 샐러드를 만든다.\r\n\r\n5. 끓는 물에 게살을 데쳐 샐러드에 넣는다.\r\n\r\n6. 물에 적신 라이스페이퍼에 샐러드를 넣고 돌돌 만다.\r\n\r\n7. 핸드블랜더에 땅콩버터, 매운 굴소스, 견과류, 파스타 삶은 물을 넣고 간다.\r\n\r\n8. 7에 고추를 넣고 다시 갈아 소스를 만든다.\r\n\r\n9. 삶은 소라살을 적당한 크기로 손질한다.\r\n\r\n10. 볼에 손질한 소라살, 새우살, 손질한 문어를 넣는다.\r\n\r\n11. 삶은 파스타면을 건져 물기를 뺀다.\r\n\r\n12. 기름을 두른 팬에 으깬 마늘, 잘게 썬 양파, 손질한 해산물을 넣고 볶는다.\r\n\r\n13. 볶던 재료에 소스를 넣고 볶는다.\r\n\r\n14. 삶은 파스타면을 팬에 넣고 재료들과 함께 볶다가 소금, 후추로 간한다.\r\n\r\n15. 해산물 파스타에 파스타 삶은 물과 버터를 넣는다.\r\n\r\n16. 낙지를 적당한 크기로 손질해 파스타에 넣고 함께 볶는다.\r\n\r\n17. 접시에 완성된 파스타를 담는다.\r\n\r\n18. 파스타 위에 라이스페이퍼롤을 올려 완성한다.'),
	(348, '바로빵', NULL, 1, '1. 버터를 전자레인지에 넣고 돌려 녹인다.\r\n\r\n2. 볼에 밀가루, 달걀, 수제 요거트, 소금, 녹인 버터를 넣고 반죽한다.\r\n\r\n3. 반죽을 밀대로 얇게 민 후, 원형으로 자른다.\r\n\r\n4. 마른 팬에 반죽을 굽는다.\r\n\r\n5. 볼에 수제 요거트, 마요네즈, 다진 마늘을 넣고 섞어 요거트소스를 만든다.\r\n\r\n6. 토마토를 반으로 자르고 꼭지를 제거한다.\r\n\r\n7. 토마토와 양상추를 채 썬다.\r\n\r\n8. 채 썬 양상추에 레몬즙을 뿌린다.\r\n\r\n9. 적양파를 채 썰어 후추와 고춧가루로 양념한다.\r\n\r\n10. 훈제연어를 적당한 크기로 썬다.\r\n\r\n11. 쪽파를 적당한 크기로 썬다.\r\n\r\n12. 접시에 구운 빵을 올리고 요거트소스>훈제연어>손질한 채소 순으로 담은 후, 반으로 접는다.\r\n\r\n13. 종이호일로 먹기 좋게 감싸 완성한다.'),
	(349, '카우복이', NULL, 1, '1. 감자, 브로콜리를 적당한 크기로 썰어 끓는 물에 넣고 데친다.\r\n\r\n2. 데친 감자와 브로콜리를 건져낸 후, 체에 밭쳐 물기를 뺀다.\r\n\r\n3. 콜리플라워, 양파, 애호박, 고추를 적당한 크기로 썬다.\r\n\r\n4. 마늘을 편으로 썬다.\r\n\r\n5. 대파 흰 부분을 어슷 썬다.\r\n\r\n6. 누룽지를 기름에 튀긴다.\r\n\r\n7. 소갈비의 살을 발라내 손질한다.\r\n\r\n8. 살을 발라낸 갈비에 전분, 간장, 달걀물을 넣고 버무린다.\r\n\r\n9. 버무린 갈비를 넉넉한 기름에 튀기듯이 볶는다.\r\n\r\n10. 튀긴 누룽지를 건져내, 체에 밭쳐 기름기를 뺀다.\r\n\r\n11. 볶던 갈비에 보리굴비살을 넣고 함께 볶는다.\r\n\r\n12. 볶은 갈비와 보리굴비살을 체에 밭쳐 기름기를 뺀다.\r\n\r\n13. 기름을 두른 팬에 손질한 마늘과 대파를 볶다가, 고춧가루를 넣고 볶는다.\r\n\r\n14. 볶던 재료에 손질한 채소들을 함께 넣고 볶는다.\r\n\r\n15. 간장, 핫소스, 굴소스, 설탕을 넣고 볶는다.\r\n\r\n16. 볶던 재료에 기름기를 뺀 갈비와 보리굴비살, 참기름을 넣고 함께 볶는다.\r\n\r\n17. 볶던 재료에 전분물을 넣어 농도를 조절한다.\r\n\r\n18. 접시에 튀긴 누룽지를 깔고, 볶은 재료를 올려 완성한다.'),
	(350, '오! 가닉~ 고등어', NULL, 1, '1. 채소육수를 낸다.\r\n\r\n2. 채소육수에 토마토를 으깨어 넣고 함께 끓인다.\r\n\r\n3. 강낭콩 통조림을 체에 밭쳐 물기를 뺀다.\r\n\r\n4. 가지는 껍질을 벗기고 적당한 크기로 썬다.\r\n\r\n5. 감자, 애호박, 양파를 적당한 크기로 썬다.\r\n\r\n6. 베이컨을 잘게 썰어 마른 팬에서 볶는다.\r\n\r\n7. 볶은 베이컨을 다른 팬에 덜어내고, 손질한 채소, 강낭콩을 함께 볶는다.\r\n\r\n8. 볶던 재료에 후추를 뿌리고, 베이컨 기름을 넉넉하게 부어 볶는다.\r\n\r\n9. 껍질을 벗겨낸 통감자를 원형틀로 찍어낸다.\r\n\r\n10. 볶은 베이컨을 볼에 담고, 잘게 썬 보리굴비살과 레몬즙, 마요네즈,\r\n발사믹크림, 토마토소스를 넣고 섞어 보리굴비소스를 만든다.\r\n\r\n11. 팬에서 볶던 재료에 채소육수, 토마토소스를 넣고 졸여 강낭콩소스를 만든다.\r\n\r\n12. 손질한 감자, 애호박, 양파에 밀가루를 묻혀 기름에 튀긴다.\r\n\r\n13. 기름을 두른 팬에 고등어를 굽는다.\r\n\r\n14. 원형틀로 찍어낸 감자를 얇게 슬라이스 한 뒤, 겹쳐 굽는다.\r\n\r\n15. 튀긴 채소를 건져, 기름기를 빼고 보리굴비소스에 섞는다.\r\n\r\n16. 졸인 강낭콩소스를 그릇에 담는다.\r\n\r\n17. 강낭콩소스 위에 채소와 섞은 보리굴비소스를 얹는다.\r\n\r\n18. 구운 고등어에 레몬즙을 뿌린다.\r\n\r\n19. 소스 위에 구운 고등어를 담고, 구운 감자를 올린 후 후추를 뿌려 완성한다.'),
	(351, '홀롤서기', NULL, 1, '1. 즉석밥을 전자레인지에 돌린다.\r\n\r\n2. 감자는 껍질을 벗기고, 채칼로 가늘게 채 썬다.\r\n\r\n3. 채 썬 감자는 물에 헹군 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n4. 기름이 담긴 팬에 누룽지를 튀긴다.\r\n\r\n5. 팬에 기름을 두르고, 다진 마늘과 채 썬 감자를 볶는다.\r\n\r\n6. 볼에 밥과 명란젓을 담는다.\r\n\r\n7. 6에 잘게 썬 고추장아찌와 참기름을 넣고 섞는다.\r\n\r\n8. 튀긴 누룽지를 건져 기름기를 빼고, 곱게 빻는다.\r\n\r\n9. 곱게 빻은 튀긴 누룽지를 양념한 밥에 넣고 섞는다.\r\n\r\n10. 누룽지를 넣어 섞은 양념한 밥을 김밥용 김에 넓게 펴서 깐다.\r\n\r\n11. 감자볶음에 후추를 넣고 섞는다.\r\n\r\n12. 얇게 편 밥 위에 감자볶음을 얹는다.\r\n\r\n13. 재료를 넣은 롤을 돌돌 만다.\r\n\r\n14. 마른 팬에 바닐라 아이스크림을 넣고 녹여 바닐라소스를 만든다.\r\n\r\n15. 그릇 가운데에 곱게 빻은 튀긴 누룽지를 담는다.\r\n\r\n16. 롤을 적당한 크기로 썰어 그릇에 올린다.\r\n\r\n17. 튀긴 누룽지 위에 바닐라소스를 얹는다.\r\n\r\n18. 아보카도는 껍질과 씨를 제거하고 얇게 썬다.\r\n\r\n19. 그릇에 얇게 썬 아보카도와 고추냉이, 간장을 올려 마무리한다.'),
	(352, '눈물회밥', NULL, 1, '1. 믹서기에 양파, 고추장, 된장, 맛술, 고춧가루, 설탕, 식초를 넣고 간다.\r\n\r\n2. 1에 통마늘, 물을 넣고 갈아 물회육수를 만든다.\r\n\r\n3. 물회육수에 얼음을 넣고 간다.\r\n\r\n4. 상추, 로메인상추, 쑥갓을 적당한 크기로 썬다.\r\n\r\n5. 사과는 껍질을 벗겨내고 얇게 썬다.\r\n\r\n6. 참외는 껍질을 벗겨내고 반으로 잘라 씨를 빼내고, 얇게 썬다.\r\n\r\n7. 불고기용 소고기를 끓는 물에 데친다.\r\n\r\n8. 데친 불고기용 소고기를 찬물에서 헹군 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n9. 즉석밥을 전자레인지에 돌린다.\r\n\r\n10. 그릇에 손질한 채소를 담는다.\r\n\r\n11. 채소 위에 데친 불고기용 소고기를 얹는다.\r\n\r\n12. 얇게 썬 사과와 참외를 올린다.\r\n\r\n13. 물회육수에 참기름을 넣어 섞은 뒤, 각종 재료 위에 붓는다.\r\n\r\n14. 불고기 물회 위에 얼음을 얹는다.\r\n\r\n15. 밥을 따로 담아낸다.\r\n\r\n16. 불고기 물회 위에 깨를 뿌려 마무리한다.'),
	(353, '후덜덜이 김밥', NULL, 1, '1. 전자레인지에 즉석밥을 넣고 돌린다.\r\n\r\n2. 마른 팬에 베이컨을 굽는다.\r\n\r\n3. 통조림 햄을 길게 썰어 베이컨과 함께 굽는다.\r\n\r\n4. 꽃등심에 소금, 후추를 뿌리고 마른 팬에 굽는다.\r\n\r\n5. 통마늘을 으깨서 등심을 굽던 팬에 넣는다.\r\n\r\n6. 연어를 잘게 썰어 볼에 담고 갈릭소스, 라임즙, 소금, 마요네즈, 후추를 넣고 섞는다.\r\n\r\n7. 전자레인지에 돌린 즉석밥을 볼에 담는다.\r\n\r\n8. 굽던 꽃등심과 밥에 버터를 넣는다.\r\n\r\n9. 버터밥에 소금, 으깬 깨, 참기름을 넣어 양념한다.\r\n\r\n10. 케이퍼를 기름에 튀긴다.\r\n\r\n11. 볼에 고수를 넣고 치포틀레 핫소스, 라임즙, 소금, 마요네즈를 넣고 섞는다.\r\n\r\n12. 구운 꽃등심을 길게 썬다.\r\n\r\n13. 김밥김 위에 양념한 밥 > 꽃등심 > 통조림 햄 > 베이컨을 넣고 말아 고기 김밥을 만든다.\r\n\r\n14. 김밥김 위에 양념한 밥 > 양념한 고수 > 양념한 연어 > 케이퍼 튀김을 넣고 말이 연어 김밥을 만든다.\r\n\r\n15. 김밥김 위에 양념한 밥 > 체다치즈 > 모차렐라치즈 > 카옌페퍼 > 고기김밥 > 연어 김밥을 넣고 돌돌 만다.\r\n\r\n16. 김밥에 참기름을 바르고, 깨를 뿌린다.\r\n\r\n17. 김밥을 먹기 좋은 크기로 썬다.\r\n\r\n18. 김밥을 접시에 담고, 위에 깨를 뿌려 마무리한다.'),
	(354, '칼로이스', NULL, 1, '1. 베이컨을 적당한 크기로 썰어 마른 팬에서 굽는다.\r\n\r\n2. 통조림 햄을 적당한 크기로 썰어 베이컨과 함께 굽는다.\r\n\r\n3. 양파를 잘게 다져 볼에 담고 마요네즈와 섞는다.\r\n\r\n4. 3에 아보카도의 과육만 발라내 함께 담는다.\r\n\r\n5. 양파, 마요네즈, 아보카도가 담긴 볼에 소금, 후추, 치포틀레 핫소스를 넣는다.\r\n\r\n6. 훈제닭가슴살을 비닐팩에 넣고 두드린 후 결대로 찢는다.\r\n\r\n7. 양념한 아보카도에 훈제닭가슴살과 적당한 크기로 썬 고수를 넣고 섞어 과카몰레를 만든다.\r\n\r\n8. 마른 팬에 커민을 볶는다.\r\n\r\n9. 볶은 커민을 빻아서 과카몰레에 넣는다.\r\n\r\n10. 마른 팬에 토르티야를 굽는다.\r\n\r\n11. 구운 토르티야에 초콜릿 잼을 바르고, 구운 베이컨과 통조림 햄을 잘게 썰어 올린다.\r\n\r\n12. 11 위에 모차렐라 치즈, 딸기잼을 얹는다.\r\n\r\n13. 다른 토르티야에 딸기잼을 바르고, 뚜껑처럼 덮는다.\r\n\r\n14. 토르티야 샌드 위에 과카몰레를 올리고, 소금, 치포틀레 핫소스, 카옌페퍼, 모차렐라 치즈를 얹는다.\r\n\r\n15. 다른 토르티야에 땅콩버터, 초콜릿 잼을 바르고, 뚜껑처럼 덮는다.\r\n\r\n16. 토르티야 샌드를 먹기 좋게 잘라 접시에 담는다.\r\n\r\n17. 콜라를 곁들여 낸다.'),
	(355, '중복냉면', NULL, 1, '1. 당근, 양파를 채 썬다.\r\n\r\n2. 달걀을 풀어 소금으로 간하고, 지단을 부친다.\r\n\r\n3. 지단을 채 썰고, 로메인상추를 잘게 썬다.\r\n\r\n4. 배는 껍질을 벗기고 채 썬다.\r\n\r\n5. 볼에 밀가루, 소금, 물을 넣고 반죽한다.\r\n\r\n6. 반죽을 밀대로 얇게 밀고, 가늘게 채 썰어 면을 만든다.\r\n\r\n7. 끓는 물에 면을 삶는다.\r\n\r\n8. 얼음을 믹서기에 넣고 곱게 간다.\r\n\r\n9. 볼에 곱게 간 얼음과 물, 쯔유, 레몬즙, 설탕을 넣고 섞어 냉면 국물을 만든다.\r\n\r\n10. 볼에 땅콩버터, 뜨거운 물, 소금, 연겨자를 넣고 섞어 땅콩겨자소스를 만든다.\r\n\r\n11. 삶은 면을 건져내 얼음물에서 식힌다.\r\n\r\n12. 훈제닭가슴살을 채 썬다.\r\n\r\n13. 그릇에 면을 담고, 채 썬 재료들을 올린다.\r\n\r\n14. 냉면 국물을 담아내고, 땅콩소스를 곁들여 완성한다.'),
	(356, '풍양냉면', NULL, 1, '1. 끓는 물에 식초, 달걀, 소금을 넣고 삶는다.\r\n\r\n2. 인스턴트 곰탕을 면보로 걸러 국물만 냄비에 넣고 끓인다.\r\n\r\n3. 양파를 적당한 크기로 썰어 곰탕에 넣고 끓인다.\r\n\r\n4. 끓이던 곰탕에 조미료를 넣는다.\r\n\r\n5. 조미료를 넣은 곰탕에 설탕과 식초를 넣고 함께 끓여 냉면 국물을 만든다.\r\n\r\n6. 살치살을 얇게 포 뜬 뒤, 곰탕에 넣고 익힌다.\r\n\r\n7. 끓는 물에 메밀면을 넣고 삶는다.\r\n\r\n8. 냉면 국물에 물을 부어가며 간을 맞춘다.\r\n\r\n9. 그릇에 얼음을 담는다.\r\n\r\n10. 익힌 살치살을 건져내 식힌다.\r\n\r\n11. 끓인 국물을 면보에 내려 국물만 얼음과 섞는다.\r\n\r\n12. 삶은 달걀을 찬물에 식힌다.\r\n\r\n13. 삶은 메밀면을 체에 밭치고, 얼음물에 헹궈 식힌다.\r\n\r\n14. 삶은 달걀의 껍데기를 벗긴다.\r\n\r\n15. 냉면 국물에 삶은 메밀면을 넣고, 살치살을 올린다.\r\n\r\n16. 삶은 달걀을 곁들인다.\r\n\r\n17. 배를 곁들여 완성한다.'),
	(357, '고기밀라노~', NULL, 1, '1. 소고기 등심을 고기망치로 두드려 얇게 편다.\r\n\r\n2. 비엔나소시지를 핸드블렌더에 넣고 간다.\r\n\r\n3. 곱게 간 비엔나소시지를 얇게 편 소고기 등심 위에 얹는다.\r\n\r\n4. 비엔나소시지를 얹은 소고기 등심을 다시 고기망치로 두드린다.\r\n\r\n5. 베이컨용 삼겹살을 등심위에 올리고 소금, 후추를 뿌린 뒤 고기망치로 두드린다.\r\n\r\n6. 고기망치로 두드린 얇게 편 고기에 밀가루를 묻힌다.\r\n\r\n7. 볼에 발사믹글레이즈, 레몬즙, 올리브오일을 넣고 섞어 샐러드드레싱을 만든다.\r\n\r\n8. 다진 양파와 잘게 썬 천도복숭아를 샐러드드레싱에 섞는다.\r\n\r\n9. 양상추와 흑토마토를 적당한 크기로 썰어 접시에 담는다.\r\n\r\n10. 기름을 두른 팬에 살팀보카를 굽는다.\r\n\r\n11. 살팀보카에 화이트와인, 설탕, 버터를 넣고 계속해서 저어주며 조린다.\r\n\r\n12. 소스에 파슬리가루를 뿌린다.\r\n\r\n13. 접시에 살팀보카를 올리고, 위에 소스를 끼얹는다.\r\n\r\n14. 양상추와 흑토마토 위에 샐러드드레싱을 얹어 마무리한다.'),
	(358, '볼로네제는 처음이제?', NULL, 1, '1. 기름을 두른 팬에 으깬 통마늘과 국거리용 소고기를 넣고 볶는다.\r\n\r\n2. 애호박을 채칼로 얇게 슬라이스 한다.\r\n\r\n3. 기름을 두른 그릴팬에 손질한 양파, 대파, 애호박, 고추를 넣고 굽는다.\r\n\r\n4. 볶던 소고기에 화이트와인, 물을 넣고 끓인다.\r\n\r\n5. 끓는 물에 소금을 넣고, 파스타면을 삶는다.\r\n\r\n6. 핸드블렌더에 적당한 크기로 썬 소고기 등심, 베이컨용 삼겹살을 넣고 곱게 간다.\r\n\r\n7. 기름을 두른 팬에 슬라이스 마늘을 굽다가, 곱게 간 고기를 넣어 함께 볶는다.\r\n\r\n8. 볶던 고기와 굽던 채소에 소금간을 한다.\r\n\r\n9. 볶던 고기에 후추를 뿌린다.\r\n\r\n10. 소고기를 끓이던 팬에 토마토소스를 넣어 파스타소스를 만든다.\r\n\r\n11. 채소를 굽던 그릴팬에 당귀와 양상추를 넣고 함께 굽는다.\r\n\r\n12. 볶던 고기에 화이트와인을 넣는다.\r\n\r\n13. 볶던 고기와 파스타소스에 파슬리가루를 넣는다.\r\n\r\n14. 볶던 고기에 적당한 크기로 썬 흑토마토를 넣는다.\r\n\r\n15. 볼에 파슬리가루, 레몬즙, 굴소스, 올리브오일을 넣고 섞어 샐러드드레싱을 만든다.\r\n\r\n16. 접시에 구운 채소를 담고, 샐러드드레싱을 뿌린다.\r\n\r\n17. 적당한 크기로 썬 흑토마토를 곁들인다.\r\n\r\n18. 익힌 파스타면을 건져 볶은 고기, 파스타 소스와 함께 볶는다.\r\n\r\n19. 접시에 면을 담고, 파스타소스를 얹는다.\r\n\r\n20. 파스타 위에 파슬리가루를 뿌려 마무리한다.'),
	(359, '웃음꼬치 피었습니다', NULL, 1, '1. 소고기 등심을 적당한 크기로 썬 뒤, 고기망치로 두드려 얇게 편다.\r\n\r\n2. 얇게 편 소고기 등심에 소금, 후추로 간을 하고, 홀스래디시 소스를 바른다.\r\n\r\n3. 양념한 소고기 등심을 베이컨용 삼겹살 위에 놓고 돌돌 만다.\r\n\r\n4. 흑토마토를 적당한 크기로 썬다.\r\n\r\n5. 꼬치에 삼겹살로 만 소고기 등심과 흑토마토를 번갈아 끼운 뒤 기름을 두른 그릴팬에서 굽는다.\r\n\r\n6. 비엔나소시지에 칼집을 넣고, 크림치즈를 끼운 뒤, 베이컨용 삼겹살로 돌돌 만다.\r\n\r\n7. 6을 꼬치에 끼운 뒤 기름을 두른 그릴팬에서 굽는다.\r\n\r\n8. 훈제오리와 모과청을 함께 베이컨용 삼겹살로 돌돌 말아 꼬치에 끼운 뒤 기름을 두른 그릴팬에서 굽는다.\r\n\r\n9. 볼에 크림치즈, 후추, 파슬리가루, 다진 마늘과 흑토마토 속을 넣고 섞는다.\r\n\r\n10. 속을 파낸 흑토마토에 양념한 크림치즈를 채워 넣는다.\r\n\r\n11. 볼에 적당한 크기로 썬 상추와 올리브오일, 식초를 넣는다.\r\n\r\n12. 오이는 껍질을 벗겨내고 얇게 썰어 볼에 함께 담는다.\r\n\r\n13. 양념한 채소에 진미채 무침을 함께 넣고 섞어 샐러드를 만든다.\r\n\r\n14. 양파를 반으로 썰어 접시에 올리고, 양파에 세 가지 꼬치를 꽂아 플레이팅 한다.\r\n\r\n15. 꼬치에 샐러드와 크림치즈를 채운 흑토마토를 곁들인다.\r\n\r\n16. 레몬즙을 뿌려 마무리한다.'),
	(360, '연장전', NULL, 1, '1. 양파와 대파 흰 부분을 적당한 크기로 썰어 핸드블랜더에 넣는다.\r\n\r\n2. 대파 흰 부분, 마늘, 양파, 애호박을 손질한다.\r\n\r\n3. 불린 표고버섯채는 물기를 짜내 손질한 채소와 함께 둔다.\r\n\r\n4. 불린 표고버섯채, 적당한 크기로 썬 찌개용 돼지고기와 훈제오리, 달걀을 핸드블랜더에 넣고 간다.\r\n\r\n5. 간 재료에 콩비지를 더해 다시 한 번 갈아 반죽을 만든다.\r\n\r\n6. 반죽에 전분, 간장, 밀가루를 넣고 섞는다.\r\n\r\n7. 반죽을 적당한 크기로 떼어 기름을 두른 팬에 굽는다.\r\n\r\n8. 종지에 마요네즈, 간장, 후추, 잘게 다진 청양고추를 넣어 소스를 만든다.\r\n\r\n9. 끓는 물에 해물다시팩을 넣고 끓여 육수를 낸다.\r\n\r\n10. 기름을 두른 팬에 손질한 채소를 넣고 볶다가, 해물육수,\r\n간장, 소금, 청주, 굴소스를 넣고 끓여 난자완스 소스를 만든다.\r\n\r\n11. 구운 콩비지전을 건져 기름기를 빼고, 그릇에 담는다.\r\n\r\n12. 난자완스 소스에 전분물과 참기름을 넣고 잘 섞는다.\r\n\r\n13. 구운 콩비지전과 마요네즈 소스, 난자완스 소스를 따로 담아낸다.'),
	(361, '발라버린닭', NULL, 1, '1. 닭의 살을 발라낸다.\r\n\r\n2. 살을 발라낸 닭고기를 비닐로 덮고, 고기망치로 두드려 얇게 편다.\r\n\r\n3. 얇게 편 닭고기에 으깬 마늘과 고추를 올리고 비닐로 덮은 뒤, 다시 한 번 고기망치로 두드린다.\r\n\r\n4. 수박을 먹기 좋은 크기로 자른다.\r\n\r\n5. 적양파를 슬라이스 한다.\r\n\r\n6. 숯불 그릴에 올리브오일을 바른다.\r\n\r\n7. 손질한 채소와 과일에 올리브오일을 발라 그릴 위에서 굽는다.\r\n\r\n8. 그릴 위에 굽는 채소와 과일에 소금, 후추로 간을 한다.\r\n\r\n9. 숯불 그릴에 올리브오일을 바르고, 닭고기에도 올리브오일을 발라 굽는다.\r\n\r\n10. 닭고기에 소금, 후추로 간 한다.\r\n\r\n11. 버섯에 올리브오일을 발라 굽는다.\r\n\r\n12. 구운 버섯과 대파를 접시에 담는다.\r\n\r\n13. 구운 적양파에 올리브오일, 레몬즙, 후추를 넣고 버무린다.\r\n\r\n14. 구운 수박과 닭고기를 접시에 담고, 양념한 적양파를 올린다.\r\n\r\n15. 홀그레인 머스터드를 곁들여 완성한다.'),
	(362, '포크볼', NULL, 1, '1. 양파를 반으로 잘라 한 겹씩 분리한다.\r\n\r\n2. 삼겹살에 소금, 후추로 밑간을 하고, 대파를 문질러 파향을 입힌다.\r\n\r\n3. 밑간한 삼겹살을 적당한 크기로 썰고, 한 겹씩 벗겨낸 양파로 감싼 뒤 꼬치로 고정한다.\r\n\r\n4. 숯불 안에 고기를 채운 양파를 넣고 익힌다.\r\n\r\n5. 양파, 대파 흰 부분, 고추를 가늘게 채 썬다.\r\n\r\n6. 설탕, 간장, 참기름, 고춧가루, 다진 마늘, 식초를 3:3:3:1:1:8 비율로 섞어 양념장을 만든다.\r\n\r\n7. 양파를 잘게 다진다.\r\n\r\n8. 잘게 다진 양파에 홀그레인 머스터드, 레몬즙, 다진 마늘,\r\n후추, 올리브오일, 간장을 섞어 고기 소스를 만든다.\r\n\r\n9. 골뱅이 통조림을 적당한 크기로 썬다.\r\n\r\n10. 채 썬 채소와 손질한 골뱅이에 양념장을 넣고 무친다.\r\n\r\n11. 골뱅이무침을 접시에 담는다.\r\n\r\n12. 양파 속에서 익히던 고기를 꺼내 석쇠에 올려 다시 한 번 익힌다.\r\n\r\n13. 고기 소스를 종지에 담아낸다.\r\n\r\n14. 접시에 구운 고기를 담아 완성한다.'),
	(363, '긴급사테! 수박주의보!', NULL, 1, '1. 닭가슴살에 칼집을 내고, 적당한 크기로 썬다.\r\n\r\n2. 볼에 카레가루, 코코넛워터, 아몬드두유, 굴소스, 조미료를 넣고 섞어 양념을 만든다.\r\n\r\n3. 양념에 손질한 닭가슴살을 넣고 버무린다.\r\n\r\n4. 3에 유자청, 설탕, 다진 고추, 후추를 넣고 섞는다.\r\n\r\n5. 양념에 버무린 닭가슴살을 꼬치에 끼운 뒤, 기름을 두른 팬에서 굽는다.\r\n\r\n6. 꼬치를 굽던 팬에 코코넛워터를 붓고, 뚜껑을 덮어 속까지 익힌다.\r\n\r\n7. 적양파를 채 썬다.\r\n\r\n8. 채 썬 적양파에 올리브오일과 레몬즙, 설탕을 넣고 버무린다.\r\n\r\n9. 수박을 삼각형 모양으로 썰어 접시에 올린다.\r\n\r\n10. 수박을 깍둑썰기해 양념에 버무린 적양파와 함께 섞어 수박샐러드를 만든다.\r\n\r\n11. 썰어둔 수박 위에 수박샐러드를 얹고, 고르곤졸라 치즈를 적당한 크기로 떼어 그 위에 올린다.\r\n\r\n12. 볼에 간장, 깨, 조미료, 땅콩버터, 유자청을 넣고 섞어 소스를 만든다.\r\n\r\n13. 닭가슴살 꼬치를 접시에 담는다.\r\n\r\n14. 소스를 따로 담아 곁들여낸다.'),
	(364, '불타는 등심폭탄', NULL, 1, '1. 소고기 등심을 적당한 크기로 썬다.\r\n\r\n2. 적당한 크기로 썬 등심을 볼에 담고, 소금을 밑간을 한다.\r\n\r\n3. 비닐팩에 통후추를 넣고, 으깬다.\r\n\r\n4. 밑간을 한 등심에 으깬 통후추, 다진 마늘, 굴소스를 넣고 버무려 양념한다.\r\n\r\n5. 종이포일을 물에 적시고, 양념한 등심을 감싼다.\r\n\r\n6. 물에 적신 종이포일에 싼 양념한 등심을 알루미늄포일로 다시 한 번 감싼다.\r\n\r\n7. 포일 채로 불 위에 얹고 굽는다.\r\n\r\n8. 볼에 레드와인과 콜라를 1:1 비율로 넣어 칼리모초 칵테일을 만든다.\r\n\r\n9. 칼리모초에 오렌지즙을 넣는다.\r\n\r\n10. 수박을 깍둑썰기로 썰어, 칼리모초에 넣는다.\r\n\r\n11. 칼리모초에 파인애플 통조림 국물을 넣는다.\r\n\r\n12. 칼리모초에 적당한 크기로 썬 파인애플 과육과 얼음을 넣는다.\r\n\r\n13. 컵에 칼리모초를 담는다.\r\n\r\n14. 마른 냄비에 포일 째 구운 양념한 등심과 보드카를 담고, 불을 붙여 낸다.'),
	(365, '조전박살', NULL, 1, '1. 조기는 내장을 빼내고 손질한다.\r\n\r\n2. 조기에 칼집을 낸다.\r\n\r\n3. 끓는 물에 전복을 데친다.\r\n\r\n4. 칼집을 낸 조기에 전분을 묻힌다.\r\n\r\n5. 전분을 묻힌 조기를 기름에 튀긴다.\r\n\r\n6. 고추와 대파를 잘게 썬다.\r\n\r\n7. 기름을 두른 팬에 잘게 썬 고추와 대파, 바지락, 화이트와인, 설탕, 후추를 넣고 볶는다.\r\n\r\n8. 볼에 딸기잼, 잘게 썬 샐러리, 소금, 레몬즙을 넣고 섞어 딸기잼소스를 만든다.\r\n\r\n9. 대파 흰 부분을 잘게 썬다.\r\n\r\n10. 잘게 썬 대파에 다진 마늘, 간장, 설탕, 맛술, 참기름을 넣어 간장소스를 만든다.\r\n\r\n11. 데친 전복을 찬물에 식히고 살을 발라낸다.\r\n\r\n12. 전복에 칼집을 내고, 먹기 좋게 반으로 썬 뒤 전복 껍데기에 올린다.\r\n\r\n13. 튀긴 조기를 건져내고 접시에 담는다.\r\n\r\n14. 볶던 바지락에 전분물을 넣고 다시 볶아낸 뒤, 접시에 담는다.\r\n\r\n15. 조기 튀김 위에 딸기잼소스를 끼얹는다.\r\n\r\n16. 데친 전복을 접시에 담고, 간장소스를 얹어낸다.'),
	(366, '도미 살을 바를래', NULL, 1, '1. 도미의 뼈와 살을 분리한다.\r\n\r\n2. 분리한 도미살의 가운데 뼈를 발라내고 등 부분과 배 부분으로 나눈다.\r\n\r\n3. 도미 등 부분의 절반만 껍질을 벗긴다.\r\n\r\n4. 도미뱃살은 껍질에 칼집을 낸다.\r\n\r\n5. 도미를 손질하고 남은 자투리 부분을 끓는 물에 넣고 육수를 우린다.\r\n\r\n6. 도미 육수에 적당한 크기로 썬 배춧잎과 된장, 맛술, 간장, 바지락, 어슷 썬 대파를 넣고 끓인다.\r\n\r\n7. 껍질에 칼집을 낸 도미뱃살에 뜨거운 물을 끼얹어 껍질부분만 익힌다.\r\n\r\n8. 껍질을 익힌 도미뱃살을 얼음물에 넣어 식힌다.\r\n\r\n9. 껍질을 벗기지 않은 도미 등에 꼬치를 끼우고 소금으로 간한다.\r\n\r\n10. 그릴에 대파를 굽는다.\r\n\r\n11. 꼬치에 끼운 도미 등을 그릴에 닿지 않게 고정하고 껍질 쪽부터 굽는다.\r\n\r\n12. 껍질을 벗긴 도미 등에 남은 비늘을 제거한다.\r\n\r\n13. 그릴에 굽던 도미를 팬에서 다시 굽는다.\r\n\r\n14. 비늘을 제거한 도미 등을 회 뜬다.\r\n\r\n15. 껍질만 익힌 도미뱃살을 회 뜬다.\r\n\r\n16. 도미구이를 접시에 담고, 구운 대파와 레몬을 곁들인다.\r\n\r\n17. 접시에 도미 회를 담고, 고추냉이를 곁들인다.\r\n\r\n18. 껍질만 익힌 도미뱃살을 된장국에 넣어 살짝 익힌다.\r\n\r\n19. 도미된장국을 그릇에 담아낸다.'),
	(367, '우아한 프랑스튜', NULL, 1, '1. 냄비에 으깬 통마늘과 올리브오일을 넣고 익힌다.\r\n\r\n2. 도미의 뼈와 살을 분리한다.\r\n\r\n3. 도미살 가운데의 뼈를 발라내고, 적당한 크기로 썬다.\r\n\r\n4. 가리비의 살을 발라낸다.\r\n\r\n5. 마늘을 익히던 냄비에 바지락과 모시조개를 넣고 볶는다.\r\n\r\n6. 5에 화이트와인을 넣고 뚜껑을 닫아 익힌다.\r\n\r\n7. 토마토를 적당한 크기로 썰어 냄비에 넣고, 물을 부어 끓인다.\r\n\r\n8. 볼에 올리브오일, 대파 흰 부분, 슬라이스한 토마토, 소금, 후추를 넣고 버무린다.\r\n\r\n9. 8을 그릴에 굽는다.\r\n\r\n10. 볼에 레몬즙, 잘게 썬 마늘, 올리브오일, 소금, 후추,\r\n잘게 썬 토마토를 넣고 섞어 토마토레몬소스를 만든다.\r\n\r\n11. 끓이던 스튜에 레몬즙을 넣는다.\r\n\r\n12. 손질한 도미에 소금으로 간하고, 껍질 부분에 칼집을 낸다.\r\n\r\n13. 기름을 두른 팬에 손질한 도미를 구운 후, 접시에 담아낸다.\r\n\r\n14. 기름을 두른 팬에 새우를 구우며 후추로 간 한다.\r\n\r\n15. 팬에 가리비를 함께 굽는다.\r\n\r\n16. 익힌 모시조개를 접시에 담고, 구운 대파를 곁들인다.\r\n\r\n17. 스튜 국물을 접시에 담는다.\r\n\r\n18. 구운 가리비와 새우, 도미, 토마토를 접시에 담는다.\r\n\r\n19. 대파를 잘게 썰어 뿌리고, 토마토레몬소스를 곁들여 마무리한다.'),
	(368, '나랑 너랑 게랑', NULL, 1, '1. 그릴에 파프리카를 올리고, 포일로 덮어 직화한다.\r\n\r\n2. 게의 다리를 뜯어낸다.\r\n\r\n3. 게의 몸통 부분을 끓는 물에 넣고 익힌다.\r\n\r\n4. 포일을 넓게 펼치고 샐러리, 통마늘, 통후추, 소금, 버터를 올린다.\r\n\r\n5. 관절을 제거한 게 다리를 포일 위에 올린다.\r\n\r\n6. 포일 안에 바지락과 새우살, 화이트와인을 넣고 밀봉한다.\r\n\r\n7. 포일에 밀봉한 해산물을 그릴 위에서 익힌다.\r\n\r\n8. 적당한 크기로 썬 당근과 샐러리, 대파를 게 몸통을 익히던 냄비에 넣는다.\r\n\r\n9. 토마토를 잘게 썰어 냄비에 넣고 함께 끓인다.\r\n\r\n10. 익힌 게 몸통을 꺼내 살과 껍데기를 분리한다.\r\n\r\n11. 접시에 배춧잎을 깔고, 깨끗하게 씻은 게 등 껍데기를 그릇처럼 올린다.\r\n\r\n12. 볼에 잘게 썬 마늘, 올리브오일, 소금, 식초를 넣고 섞어 드레싱을 만든다.\r\n\r\n13. 직화한 파프리카를 흐르는 물에 씻어 탄 껍질을 벗겨내고, 적당한 크기로 썬다.\r\n\r\n14. 손질한 구운 파프리카를 마늘드레싱에 넣고 섞어 파프리카 샐러드를 만든다.\r\n\r\n15. 파프리카를 넓게 썰어, 접시에 데커레이션 한다.\r\n\r\n16. 포일에 넣고 익힌 해산물을 꺼내, 육수만 접시에 따라낸다.\r\n\r\n17. 익힌 새우살과 바지락살만 골라 접시에 담는다.\r\n\r\n18. 해산물 육수와 새우살, 바지락살을 게 등 껍데기에 담는다.\r\n\r\n19. 배춧잎 위에 파프리카 샐러드를 담는다.\r\n\r\n20. 익힌 게 다리의 살과 집게다리, 새우살을 게 등 껍데기에 담는다.\r\n\r\n21. 익힌 게 집게다리와 새우살을 얹는다.\r\n\r\n22. 잘게 썬 구운 파프리카를 해산물에 곁들인다.\r\n\r\n23. 대파를 잘게 썰어 위에 뿌려낸다.'),
	(369, '소스 하나면 돼', NULL, 1, '1. 끓는 물에 소금, 삶은 강낭콩을 넣고 데친다.\r\n\r\n2. 파프리카와 애호박을 적당한 크기로 썰어 올리브오일에 버무린다.\r\n\r\n3. 올리브오일에 버무린 채소를 그릴팬에 굽는다.\r\n\r\n4. 마늘을 잘게 다져, 올리브오일이 남은 볼에 담는다.\r\n\r\n5. 4에 그린 올리브를 잘게 썰어 넣는다.\r\n\r\n6. 잘게 썬 마늘과 그린 올리브에 레몬즙을 넣어 올리브소스를 만든다.\r\n\r\n7. 데친 관자와 데친 소라를 적당한 크기로 썬다.\r\n\r\n8. 천도복숭아를 슬라이스해 그릴팬에 굽는다.\r\n\r\n9. 구운 파프리카를 잘게 다져 올리브 소스에 넣는다.\r\n\r\n10. 올리브소스에 올리브오일을 더해가며 잘 저어준 뒤, 후추와 소금으로 간 한다.\r\n\r\n11. 올리브소스에 데친 관자와 소라, 데친 강낭콩을 넣고 잘 섞는다.\r\n\r\n12. 기름을 두른 팬에 고등어를 올리고 소금, 후추로 간을 해 굽는다.\r\n\r\n13. 올리브소스에 버무린 강낭콩을 접시에 담는다.\r\n\r\n14. 그릴팬에 구운 애호박을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n15. 올리브소스에 버무린 관자와 소라를 곁들인다.\r\n\r\n16. 핸드블랜더에 고수와 올리브오일을 넣고 곱게 간다.\r\n\r\n17. 16에 레몬즙을 넣고, 접시에 담는다.\r\n\r\n18. 구운 천도복숭아와 고등어를 올린다.\r\n\r\n19. 레몬즙과 후추를 뿌려 마무리한다.'),
	(370, '전병 말아요~', NULL, 1, '1. 볼에 밀가루, 소금, 물을 넣고 섞는다.\r\n\r\n2. 밀가루 반죽을 체에 밭쳐 곱게 걸러 전병 반죽을 만든다.\r\n\r\n3. 당근을 가늘게 채 썬다.\r\n\r\n4. 오이껍질을 얇게 돌려 깍은 후, 채 썬다.\r\n\r\n5. 적양배추를 채 썬다.\r\n\r\n6. 오이지를 적당한 크기로 썰고, 물에 담가 소금기를 뺀다.\r\n\r\n7. 볼에 고기육수, 잣, 깨, 물을 넣는다.\r\n\r\n8. 7을 믹서기에 넣는다.\r\n\r\n9. 기름을 두른 팬에 전병 반죽을 붓고 전병을 부친다.\r\n\r\n10. 소면을 삶는다.\r\n\r\n11. 부친 전병을 꺼내 식힌다.\r\n\r\n12. 기름을 두른 팬에 채 썬 당근을 소금 간을 해서 볶는다.\r\n\r\n13. 채 썬 오이, 채 썬 적양배추도 소금으로 간 하며 볶는다.\r\n\r\n14. 전병에 볶은 채소들을 올리고 돌돌 만다.\r\n\r\n15. 믹서기에 넣은 잣, 깨, 고기육수를 곱게 갈아 소스를 만든다.\r\n\r\n16. 삶은 소면을 건져 찬물에 헹군다.\r\n\r\n17. 믹서기로 간 소스를 체에 밭쳐 곱게 거른다.\r\n\r\n18. 채소전병을 적당한 크기로 썬다.\r\n\r\n19. 그릇에 소면을 담고, 채소전병, 오이지를 올린다.\r\n\r\n20. 19에 소스를 부어 마무리한다.'),
	(371, '톰얌킹', NULL, 1, '1. 뜨거운 물에 찌개용 게를 넣은 뒤, 레몬껍질을 갈아 넣고 끓여 게 육수를 낸다.\r\n\r\n2. 생강을 으깨서 게 육수에 넣고 끓인다.\r\n\r\n3. 끓이던 재료에 얇게 썬 천도복숭아와 토마토소스, 사테소스를 넣고 톰얌 국물을 끓인다.\r\n\r\n4. 새우, 데친 관자, 데친 소라를 손질한다.\r\n\r\n5. 포토벨로 버섯과 참타리 버섯을 적당한 크기로 썬다.\r\n\r\n6. 프릭키누를 잘게 썬다.\r\n\r\n7. 기름을 두른 냄비에 잘게 썬 프릭키누와 크러시드 칠리를 넣고 볶다가, 톰얌 국물을 넣고 볶는다.\r\n\r\n8. 냄비에 손질한 새우, 톰얌 국물, 토마토주스를 넣고 끓이다가 손질한 버섯을 넣는다.\r\n\r\n9. 톰얌 국물을 면보에 걸러 국물만 남긴다.\r\n\r\n10. 볶던 재료에 걸러낸 톰얌 국물을 붓고, 베트남 매운 간장을 넣어 끓인다.\r\n\r\n11. 톰얌쿵에 아몬드우유와 소금을 넣는다.\r\n\r\n12. 톰얌쿵에 레몬즙, 피시소스, 데친 관자와 소라를 넣는다.\r\n\r\n13. 그릇에 톰얌쿵을 담은 뒤, 고수를 올려 마무리한다.'),
	(372, '톰얌의 고수', NULL, 1, '1. 기름을 두른 팬에 다진 마늘과 창난젓을 볶는다.\r\n\r\n2. 볶은 다진 마늘과 창난젓에 소주를 넣고 함께 볶는다.\r\n\r\n3. 2에 황태포와 해물다시팩을 넣고 뜨거운 물을 부어 끓여 육수를 낸다.\r\n\r\n4. 즉석밥에 레몬껍질과 생강즙을 넣고 전자레인지에 돌린다.\r\n\r\n5. 마늘은 으깨고, 양파는 채 썬다.\r\n\r\n6. 대파 흰 부분을 반으로 가른 후, 적당한 크기로 썬다.\r\n\r\n7. 베이컨을 잘게 썰어 마른 팬에 볶는다.\r\n\r\n8. 베이컨을 볶던 팬에 손질한 채소들과 사테소스를 넣는다.\r\n\r\n9. 볶던 재료에 레몬껍질과 베트남 매운 간장, 프릭키누를 넣는다.\r\n\r\n10. 9에 손질한 애호박과 찌개용 게, 새우, 포토벨로 버섯, 레몬즙을 넣고 볶는다.\r\n\r\n11. 볶은 재료에 육수를 넣고 톰얌쿵을 끓인다.\r\n\r\n12. 톰얌쿵에 육수를 낸 황태포를 건져 넣는다.\r\n\r\n13. 데친 소라를 적당한 크기로 썰어 관자와 함께 톰얌쿵에 넣고 끓인다.\r\n\r\n14. 톰얌쿵에 레몬즙과 된장을 넣고 끓인다.\r\n\r\n15. 고수를 잘게 썰어 톰얌쿵에 넣고 끓인다.\r\n\r\n16. 그릇에 톰얌쿵을 담고, 레몬을 올린다.\r\n\r\n17. 레몬생강밥을 그릇에 담아 따로 낸다.'),
	(373, '바쥐락펴락', NULL, 1, '1. 끓는 물이 담긴 냄비에 바지락을 넣고 뚜껑을 덮어 익힌다.\r\n\r\n2. 볼에 전분, 라면스프를 넣고 섞어 튀김옷을 만든다.\r\n\r\n3. 익힌 바지락을 체에 밭쳐 거른 뒤, 찬물에 헹군다.\r\n\r\n4. 익힌 바지락의 살만 발라내 튀김옷을 입힌다.\r\n\r\n5. 튀김옷을 입힌 바지락을 기름에 넣고 튀긴다.\r\n\r\n6. 튀긴 바지락을 건져 기름기를 뺀다.\r\n\r\n7. 기름기를 뺀 바지락튀김을 다시 한 번 튀긴다.\r\n\r\n8. 바지락튀김을 건져내 기름기를 뺀다.\r\n\r\n9. 볼에 마요네즈, 달걀노른자를 넣고 저어 마요네즈 소스를 만든다.\r\n\r\n10. 바지락튀김을 그릇에 담는다.\r\n\r\n11. 마요네즈 소스를 소스볼에 담고, 레몬즙을 뿌려 마무리한다.'),
	(374, '일탈 닭돼지락', NULL, 1, '1. 냄비에 바지락, 청주, 뜨거운 물을 넣고 끓인다.\r\n\r\n2. 대파 흰부분의 가운데 심을 제거하고 적당한 크기로 썰어 기름을 두른 냄비에 담는다.\r\n\r\n3. 삼겹살을 가늘게 채 썰어 대파와 함께 볶는다.\r\n\r\n4. 통마늘과 다진 생강을 냄비에 넣고 함께 볶는다.\r\n\r\n5. 프라이드치킨의 튀김옷을 벗겨내고 치킨 살을 두드려 편 뒤, 잘게 찢어 볶던 재료에 넣는다.\r\n\r\n6. 볶던 재료에 바지락 육수를 넣고 끓인다.\r\n\r\n7. 익힌 바지락을 비닐팩에 넣고 흔들어 살만 발라낸다.\r\n\r\n8. 발라낸 바지락살을 끓이던 국에 넣는다.\r\n\r\n9. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n10. 국에 간장으로 간을 한다.\r\n\r\n11. 간을 한 국에 콩나물과 바지락 껍데기를 넣고 끓인다.\r\n\r\n12. 그릇에 김가루와 즉석밥을 각각 담아낸다.\r\n\r\n13. 그릇에 완성된 국을 담고, 청양고추와 레몬을 썰어 올려 마무리한다.'),
	(375, '에그머니나', NULL, 1, '1. 팬에 물, 바지락, 소금, 화이트와인을 넣고 뚜껑을 덮어 익힌다.\r\n\r\n2. 끓는 물 위에 볼을 올리고, 버터를 넣어 녹인다.\r\n\r\n3. 다른 볼에 달걀, 소금, 우유를 넣고 섞는다.\r\n\r\n4. 버터를 녹인 볼에 달걀물을 붓고 저어가며 스크램블을 만든다.\r\n\r\n5. 고추참치를 체에 밭쳐 기름기를 뺀다.\r\n\r\n6. 핸드블렌더에 기름기를 뺀 고추참치, 적당한 크기로 썬 양파와\r\n마늘, 버터를 넣고 곱게 갈아 스프레드를 만든다.\r\n\r\n7. 접시에 원형틀을 놓고 참치스프레드 > 달걀 스크램블\r\n> 참치스프레드 > 달걀 스크램블 순으로 쌓는다.\r\n\r\n8. 7 위에 럭비공 모양으로 만든 참치스프레드를 올린다.\r\n\r\n9. 올리브오일을 두른 팬에 버터를 녹이다가, 고춧가루를 섞어 소스를 만든다.\r\n\r\n10. 익힌 바지락을 접시에 올린다.\r\n\r\n11. 바지락 위에 소스를 뿌린다.\r\n\r\n12. 대파를 잘게 썰어 얹고, 후추를 뿌려 마무리한다.'),
	(376, '달걀의 대시', NULL, 1, '1. 팬에 으깬 마늘을 넣고 기름을 두른 뒤, 바지락을 넣는다.\r\n\r\n2. 바지락을 넣은 팬에 청주, 뜨거운 물, 맛술, 간장, 소금을 넣고 끓인다.\r\n\r\n3. 볼에 달걀, 전분, 설탕을 넣고 섞는다.\r\n\r\n4. 기름이 끓는 냄비 위에 두 개의 체를 겹쳐 밭치고, 달걀물을 내려 면처럼 튀긴다.\r\n\r\n5. 튀긴 달걀면을 건져내 체에 밭친 뒤, 뜨거운 물로 기름기를 뺀다.\r\n\r\n6. 익힌 바지락을 체에 밭쳐 거르고, 살만 빼내 뚝배기에 담는다.\r\n\r\n7. 바지락살을 넣은 뚝배기에 바지락육수를 붓고 끓인다.\r\n\r\n8. 달걀면을 물에 헹군 뒤, 뚝배기에 넣고 끓인다.\r\n\r\n9. 대파 흰 부분을 어슷 썰어 달걀면 위에 올리고, 김가루를 뿌려 마무리한다.'),
	(377, '데워먹고 대어 낚는 낚시요리', NULL, 1, '1. 끓는 물에 새우젓, 건새우, 멸치를 넣고 끓인다.\r\n\r\n2. 1에 무를 적당한 크기로 썰어 함께 끓여 육수를 낸다.\r\n\r\n3. 둥근 애호박을 얇게 썰어 전골냄비에 담는다.\r\n\r\n4. 돼지고기를 적당한 크기로 썰어 볼에 담고, 소금, 후추로 간 한 뒤 전골냄비에 담는다.\r\n\r\n5. 소 안심을 적당한 크기로 썰어 볼에 담은 뒤, 아로니아액,\r\n후추, 설탕, 참기름으로 양념해 전골냄비에 담는다.\r\n\r\n6. 표고버섯, 햄 통조림을 적당한 크기로 썰어 전골냄비에 담는다.\r\n\r\n7. 재료가 담긴 전골냄비에 육수를 넣는다.\r\n\r\n8. 양파를 다진 뒤 참치 통조림과 함께 핸드블렌더로 간다.\r\n\r\n9. 8을 전처럼 빚어 달걀흰자>밀가루>달걀물을 입히고 기름을 두른 팬에 굽는다.\r\n\r\n10. 전복을 손질해 적당한 크기로 썬 뒤 전골냄비에 담는다.\r\n\r\n11. 각종 재료가 잠길 만큼 육수를 붓고 끓인다.\r\n\r\n12. 떡갈비를 적당한 크기로 뭉쳐 전분을 묻힌다.\r\n\r\n13. 전분을 묻힌 떡갈비를 끓인 육수에 넣고 익힌다.\r\n\r\n14. 데친 얼갈이의 물기를 짜낸 뒤, 적당한 크기로 썰어 전골냄비에 담는다.\r\n\r\n15. 육수에서 익힌 떡갈비를 건져 전골냄비에 담는다.\r\n\r\n16. 파를 적당한 크기로 썰어 전골냄비에 넣는다.\r\n\r\n17. 끓이던 전골에 참치전과 후추를 넣어 마무리한다.'),
	(378, '낚았찌개', NULL, 1, '1. 토마토를 잘게 썬다.\r\n\r\n2. 잘게 썬 토마토를 비닐팩에 담는다.\r\n\r\n3. 2에 고추장, 카레가루를 넣고 주물러 잘 섞는다.\r\n\r\n4. 대파 흰 부분을 적당한 크기로 썰어 기름을 두른 팬에 넣는다.\r\n\r\n5. 돼지고기를 적당한 크기로 썰어 팬에 담고, 다진 마늘, 고춧가루, 간장을 넣어 볶는다.\r\n\r\n6. 팬에 볶던 재료에 고추장, 카레가루와 버무린 토마토를 넣고 끓인다.\r\n\r\n7. 6에 건새우를 넣는다.\r\n\r\n8. 즉석밥을 전자레인지에 돌린다.\r\n\r\n9. 끓이던 재료에 물을 더해 끓인다.\r\n\r\n10. 김을 잘게 찢어 넣는다.\r\n\r\n11. 둥근 애호박을 적당한 크기로 썰어 넣는다.\r\n\r\n12. 끓이던 재료에 달걀을 넣는다.\r\n\r\n13. 즉석밥을 그릇에 담는다.'),
	(379, '할아버지 최고닭!', NULL, 1, '1. 닭다리의 살과 뼈를 분리한다.\r\n\r\n2. 냄비에 닭다리 뼈와 뜨거운 물을 넣고 끓여 육수를 낸다.\r\n\r\n3. 기름을 두른 팬에 닭다리살을 구우며 소금, 후추로 간 한다.\r\n\r\n4. 가지, 감자를 적당한 크기로 썰어 기름을 두른 그릴팬에 굽는다.\r\n\r\n5. 미니 파프리카도 반으로 썰어 그릴 팬에 굽는다.\r\n\r\n6. 그릴팬에서 굽는 채소에 소금, 후추로 간 한다.\r\n\r\n7. 팬에 버터와 밀가루를 넣고 볶아 루를 만든 뒤 볼에 담아 둔다.\r\n\r\n8. 레몬 껍질만 갈아 볼에 담아둔다.\r\n\r\n9. 다른 볼에 레몬즙, 케이퍼 국물을 담아둔다.\r\n\r\n10. 케이퍼, 양파, 마늘을 각각 잘게 다진다.\r\n\r\n11. 기름을 두른 팬에 껍질을 벗긴 새우를 굽는다.\r\n\r\n12. 굽던 새우에 다진 마늘, 소금, 후추, 다진 양파와 케이퍼, 닭육수,\r\n레몬즙&케이퍼국물, 루, 버터, 곱게 간 레몬 껍질을 넣고 함께 익힌다.\r\n\r\n13. 접시에 구운 감자> 가지> 파프리카> 닭다리살을 순서대로 올린다.\r\n\r\n14. 익힌 새우와 소스를 접시에 담는다.\r\n\r\n15. 얇게 썬 대파 흰 부분을 뿌려 마무리한다.'),
	(380, '그랜드파더 젠틀롤', NULL, 1, '1. 토마토에 칼집을 내고 끓는 물에 데친다.\r\n\r\n2. 사과, 미니 파프리카를 채 썬다.\r\n\r\n3. 감자의 껍질을 벗겨낸 후 채칼로 얇게 썬 뒤, 가늘게 채 썰어 볼에 담아둔다.\r\n\r\n4. 데친 토마토를 건져 얼음물에 담가 식히고, 껍질을 벗긴다.\r\n\r\n5. 데친 토마토의 꼭지와 씨 부분을 발라내고, 과육 부분만 적당한 크기로 썬다.\r\n\r\n6. 채끝살을 얇게 포 떠서 펼친다.\r\n\r\n7. 펼친 채끝살에 칼집을 넣어 부드럽게 만든다.\r\n\r\n8. 채끝살 위에 삶은 숙주나물, 채 썬 사과와 파프리카, 데친 토마토를 올리고 돌돌 만다.\r\n\r\n9. 팬에 버터와 채끝롤을 넣고 버터를 끼얹어가며 굽는다.\r\n\r\n10. 우엉을 연필 깎듯 돌려가며 가늘게 썬 뒤, 감자채와 함께 볼에 담는다.\r\n\r\n11. 감자&우엉채에 전분을 넣고 섞는다.\r\n\r\n12. 두 개의 체 사이에 전분을 묻힌 감자&우엉채를 담아 바구니 모양으로 고정한 뒤, 기름에 튀긴다.\r\n\r\n13. 감자&우엉채 튀김의 체를 빼내고 앞뒤로 골고루 튀긴다.\r\n\r\n14. 레드와인, 간장, 설탕, 맛술을 섞어 소스를 만든다.\r\n\r\n15. 구운 롤스테이크에 소스를 넣고 조린다.\r\n\r\n16. 데친 토마토의 꼭지를 잘라내고, 적당한 크기로 썰어 접시에 담는다.\r\n\r\n17. 바구니 모양의 감자&우엉채 튀김을 접시에 올린다.\r\n\r\n18. 롤스테이크를 먹기 좋은 크기로 썰어 감자&우엉채 튀김 안에 담고, 소스를 뿌린다.\r\n\r\n19. 롤스테이크 위에 잣을 뿌려 마무리한다.'),
	(381, '제비 몰러 나간 알', NULL, 1, '1. 옥돔을 손질해 살만 발라낸다.\r\n\r\n2. 새우살을 칼 단면으로 내리쳐 으깬다.\r\n\r\n3. 옥돔살과 새우살을 함께 다진다.\r\n\r\n4. 골뱅이를 잘게 썬 뒤, 다진 옥돔살, 새우살과 함께 볼에 담아둔다.\r\n\r\n5. 볼에 담긴 해산물에 생강즙과 녹인 버터를 넣는다.\r\n\r\n6. 5에 전분과 달걀물을 넣고 치대 반죽을 만든다.\r\n\r\n7. 반죽을 동그랗게 떼어 찜기에 넣고 찐다.\r\n\r\n8. 사과, 복숭아, 양파를 잘게 다진다.\r\n\r\n9. 냄비에 물을 넣고 끓이다가 레몬즙을 넣는다.\r\n\r\n10. 9에 설탕을 넣고, 잘게 다진 사과, 복숭아, 양파를 넣고 졸인다.\r\n\r\n11. 파파야, 오이, 양파, 당근을 가늘게 채 썬다.\r\n\r\n12. 채 썬 파파야와 채소들을 그릇에 담는다.\r\n\r\n13. 쪄낸 완자를 그릇에 담고 제비집을 얹는다.\r\n\r\n14. 볼에 간장, 식초, 설탕, 잘게 다진 청양고추, 레몬즙, 굴소스를 넣고 섞어 샐러드드레싱을 만든다.\r\n\r\n15. 샐러드에 드레싱을 뿌린다.\r\n\r\n16. 조린 과일에 전분물을 넣어 농도를 조절한다.\r\n\r\n17. 제비집 완자찜 위에 과일소스를 부어 완성한다.'),
	(382, '타임 투 새우굿', NULL, 1, '1. 양파와 마늘을 잘게 썬다.\r\n\r\n2. 기름을 두른 팬에 손질한 양파와 마늘을 넣고 볶는다.\r\n\r\n3. 불고기용 소고기에 후추를 뿌리고 키친타월을 이용해 물기를 뺀다.\r\n\r\n4. 볶은 양파와 마늘을 볼에 담고 불고기용 소고기와 함께 섞는다.\r\n\r\n5. 소고기 안에 모차렐라 치즈를 넣고 뭉쳐 볼을 만든다.\r\n\r\n6. 소고기볼에 칵테일새우를 꽂는다.\r\n\r\n7. 기름을 두른 팬에 소고기새우볼을 넣고 뚜껑을 덮어 익힌다.\r\n\r\n8. 블랙올리브, 할라피뇨를 적당한 크기로 썰어 볼에 담아둔다.\r\n\r\n9. 자두, 양파를 적당한 크기로 썰어 블랙올리브, 할라피뇨와 함께 기름을 두른 팬에 볶는다.\r\n\r\n10. 재료를 볶는 팬에 굴소스, 후추를 넣어 양념한 뒤 접시에 담는다.\r\n\r\n11. 소고기새우볼을 굽던 팬에 화이트와인을 붓고 뚜껑을 덮어 속까지 익힌다.\r\n\r\n12. 소고기새우볼 위에 모차렐라치즈를 올리고 뚜껑을 덮어 익힌다.\r\n\r\n13. 대파 흰 부분을 가늘게 채 썰어 밀가루를 묻힌 뒤, 기름에 튀긴다.\r\n\r\n14. 파채튀김을 건져내 기름기를 뺀다.\r\n\r\n15. 자두채소볶음 위에 소고기새우볼을 올린다.\r\n\r\n16. 접시에 소고기새우볼을 익힌 와인소스를 뿌린 뒤, 파채튀김을 얹어 마무리한다.'),
	(383, '묵은지 뚝배기', NULL, 1, '1. 묵은지, 소고기, 베이컨을 잘게 썰고, 양파는 가늘게 채 썬다.\r\n\r\n2. 마른 팬에 소고기, 베이컨, 양파를 넣고 볶는다.\r\n\r\n3. 볶던 재료에 후추, 잘게 썬 묵은지를 넣고 함께 볶는다.\r\n\r\n4. 당근을 가늘게 채 썰어 팬에 넣고 함께 볶는다.\r\n\r\n5. 감자, 불린 표고버섯을 가늘게 채 썰어 함께 볶는다.\r\n\r\n6. 애호박을 가늘게 채 썰어 팬에 넣고 다른 재료들과 볶다가\r\n뜨거운 물을 부어 김치보르시를 끓인다.\r\n\r\n7. 김치보르시에 소금과 후추로 간 한다.\r\n\r\n8. 볼에 레몬즙, 알프레도소스를 넣고 섞어 소스를 만든다.\r\n\r\n9. 소스에 달걀노른자를 넣고 섞는다.\r\n\r\n10. 10에 후추, 고춧가루를 넣고 섞는다.\r\n\r\n11. 양배추를 채 썰어 김치보르시에 넣고 끓인다.\r\n\r\n12. 소스에 김치보르시 국물만 넣고 섞는다.\r\n\r\n13. 12를 김치보르시에 붓고 끓인다.\r\n\r\n14. 김치보르시를 뚝배기에 옮겨 담고 끓이다가 잘게 썬 대파를 올려 마무리한다.'),
	(384, '부자 나초', NULL, 1, '1. 양파를 잘게 썬다.\r\n\r\n2. 김치를 잘게 썰고 국물을 짜낸다.\r\n\r\n3. 기름을 두른 팬에 잘게 썬 양파와 김치를 볶는다.\r\n\r\n4. 떡갈비를 잘게 다져 팬에 넣고 후추, 화이트와인을 더해 볶는다.\r\n\r\n5. 냄비에 토마토소스, 피치망고잼, 스테이크소스, 고형카레를 넣고 소스를 끓인다.\r\n\r\n6. 소스에 뜨거운 물을 붓고 끓이다 볶은 재료에 부어 김치토마토소스를 만든다.\r\n\r\n7. 김치토마토소스에 핫소스를 넣는다.\r\n\r\n8. 대파, 청양고추를 잘게 썬다.\r\n\r\n9. 전자레인지에 즉석밥을 돌린다.\r\n\r\n10. 얼음물 위에 스테인레스 볼을 올리고, 밥을 담아 식힌다.\r\n\r\n11. 식힌 밥에 볶은 김치토마토소스을 넣고 섞는다.\r\n\r\n12. 유부 안에 양념한 밥을 채워 넣고 김치유부초밥을 만든다.\r\n\r\n13. 오븐용 그릇에 나초를 넓게 깐다.\r\n\r\n14. 나초 위에 김치토마토소스, 모차렐라 치즈, 슬라이스 체다치즈를 올린다.\r\n\r\n15. 예열한 오븐에 소스를 얹은 나초를 넣고 치즈가 녹을 때까지 익힌다.\r\n\r\n16. 오븐에서 익힌 나초를 꺼내 위에 잘게 썬 대파와 청양고추, 블랙올리브를 올려 완성한다.'),
	(385, '튀튀빵빵', NULL, 1, '1. 밀가루를 체에 친다.\r\n\r\n2. 체 친 밀가루에 소금과 설탕을 넣는다.\r\n\r\n3. 2에 이스트를 넣고, 뜨거운 물을 부어가며 반죽한 후 충분히 발효시킨다.\r\n\r\n4. 사과와 자두, 오렌지를 손질해 플레이팅한다.\r\n\r\n5. 냄비에 기름을 끓이다가, 코코넛오일을 넣어 함께 끓인다.\r\n\r\n6. 발효시킨 반죽을 적당한 양으로 떼어 기름에 튀긴다.\r\n\r\n7. 튀긴 빵을 건져 기름을 뺀다.\r\n\r\n8. 과일이 담긴 접시 위에 튀긴 빵을 올리고 연유를 뿌린다.\r\n\r\n9. 소스용 접시에 연유와 꿀을 따로 낸다.'),
	(386, '두밥두밥 두부두밥♬', NULL, 1, '1. 두부를 적당한 두께로 썰어 키친타올로 감싸 물기를 제거한다.\r\n\r\n2. 아보카도는 씨를 빼고, 껍질을 벗긴다.\r\n\r\n3. 불고기용 소고기를 적당한 크기로 썰어 볼에 담아둔다.\r\n\r\n4. 마늘을 잘게 다진다.\r\n\r\n5. 손질한 불고기용 소고기에 청주, 맛술, 간장, 다진 마늘, 채 썬 적양파를 넣고 버무린다.\r\n\r\n6. 비닐팩에 전분, 밀가루를 넣고 섞는다.\r\n\r\n7. 두부 사이에 밥을 끼워 샌드를 만들고, 섞어둔 밀가루&전분을 묻힌다.\r\n\r\n8. 튀김옷을 입힌 두부샌드를 기름에 튀긴다.\r\n\r\n9. 손질한 아보카도를 적당한 두께로 썰어 사이에 밥을 넣어 샌드를 만든 뒤,\r\n섞어둔 밀가루&전분을 묻혀 기름에 튀긴다.\r\n\r\n10. 애호박과 표고버섯을 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n11. 배의 껍질을 깎고, 적당한 크기로 썰어 믹서기에 담는다.\r\n\r\n12. 믹서기에 김밥용 우엉을 넣고 배와 함께 갈아 소스를 만든다.\r\n\r\n13. 볶은 애호박과 표고버섯을 볼에 담아둔다.\r\n\r\n14. 마른 팬에 양념한 불고기를 볶는다.\r\n\r\n15. 믹서로 간 소스를 체에 밭쳐 내린다.\r\n\r\n16. 볶던 불고기에 소스, 전분물, 뜨거운 물, 간장, 설탕, 매실액을 넣는다.\r\n\r\n17. 불고기를 볶은 팬에 볶아뒀던 채소를 넣고 다시 한 번 볶는다.\r\n\r\n18. 두부샌드와 아보카도샌드를 적당한 크기로 썰어 접시에 담는다.\r\n\r\n19. 18 위에 불고기를 얹는다.\r\n\r\n20. 매운 수제양념장을 곁들여 낸다.'),
	(387, '소주 한 잔 참치마', NULL, 1, '1. 마른 팬에 참치 통조림을 넣고 볶는다.\r\n\r\n2. 청양고추의 씨를 뺀다.\r\n\r\n3. 씨를 뺀 청양고추와 마늘을 잘게 다져 팬에 넣고 참치와 함께 볶는다.\r\n\r\n4. 볶던 재료에 간장, 고춧가루, 물을 넣고 끓인다.\r\n\r\n5. 떡볶이 떡, 대파를 적당한 크기로 썰어 팬에 넣고 다른 재료들과 함께 조린다.\r\n\r\n6. 볼에 간장, 식초, 라임즙, 다진 마늘, 설탕, 다진 대파를 넣고 섞어 소스를 만든다.\r\n\r\n7. 적양파를 가늘게 채 썰어 밀가루와 전분에 묻힌다.\r\n\r\n8. 청양고추를 길게 썬 뒤, 밀가루와 전분에 묻힌다.\r\n\r\n9. 튀김옷을 입힌 적양파와 청양고추를 기름에 튀긴다.\r\n\r\n10. 기름을 끓이던 팬에 고춧가루를 넣고 끓인 뒤, 체에 밭쳐 걸러 고추기름을 만든다.\r\n\r\n11. 그릇에 조린 참치를 담는다.\r\n\r\n12. 아보카도를 얇게 썰어 참치 위에 올리고 소금, 잘게 썬 청양고추, 고추기름을 뿌린다.\r\n\r\n13. 적양파튀김과 청양고추튀김을 건져 기름을 뺀 뒤, 그릇에 담고 소스를 뿌린다.\r\n\r\n14. 대파를 가늘게 채 썰어 올린 뒤, 깨를 뿌려 마무리한다.'),
	(388, '두부세요~ 나야♬', NULL, 1, '1. 마늘은 으깬 뒤 잘게 다진다.\r\n\r\n2. 대파 흰 부분, 청양고추를 적당한 크기로 썬다.\r\n\r\n3. 마른 팬에 으깬 마늘, 적당한 크기로 썬 대파와 청양고추를 볶는다.\r\n\r\n4. 적양파를 잘게 썰어 팬에 넣고 함께 볶는다.\r\n\r\n5. 채소를 볶던 팬에 기름을 넉넉히 두르고 고춧가루를 넣어 볶는다.\r\n\r\n6. 표고버섯을 잘게 썰어 팬에 넣는다.\r\n\r\n7. 불고기용 소고기를 잘게 다져 팬에 넣고 다른 재료들과 함께 볶는다.\r\n\r\n8. 새우젓을 으깬 뒤 볶던 재료에 넣어 간한다.\r\n\r\n9. 볶던 재료에 굴소스와 맛술을 넣는다.\r\n\r\n10. 볶은 재료에 뜨거운 물을 부어 끓인다.\r\n\r\n11. 11에 식초, 잘게 찢은 태국고추를 넣는다.\r\n\r\n12. 떡볶이 떡을 잘게 썰어 팬에 넣고 함께 끓인다.\r\n\r\n13. 두부를 깍둑썰기한 뒤, 팬에 넣고 다른 재료들과 함께 끓인다.\r\n\r\n14. 굴소스와 소금으로 간 한다.\r\n\r\n15. 부추를 적당한 크기로 썰어 팬에 넣고 끓인다.\r\n\r\n16. 후추를 뿌려 마무리 한 뒤, 완성된 요리를 그릇에 담아낸다.'),
	(389, '가위로 오리면', NULL, 1, '1. 볼에 밀가루, 소금, 물을 넣고 치대어 면 반죽을 만든다.\r\n\r\n2. 다른 볼로 반죽을 덮어놓고 숙성시킨다.\r\n\r\n3. 적양파를 가늘게 채 썬다.\r\n\r\n4. 기름을 두른 팬에 슬라이스 된 훈제오리살을 넣고 지진다.\r\n\r\n5. 마늘을 으깨고, 대파 흰 부분을 적당한 크기로 썰어 함께 볼에 담아둔다.\r\n\r\n6. 아스파라거스와 표고버섯을 적당한 크기로 썰어 채 썬 적양파와 함께 섞어둔다.\r\n\r\n7. 연근의 껍질을 벗겨내고 적당한 크기로 썰어 채소들과 함께 둔다.\r\n\r\n8. 지진 훈제오리살을 체에 밭쳐 기름기를 뺀다.\r\n\r\n9. 오리를 지졌던 팬에 으깬 마늘과 손질한 대파를 넣고 볶는다.\r\n\r\n10. 손질한 채소들도 함께 넣고 볶는다.\r\n\r\n11. 숙성시킨 면 반죽을, 적당한 크기로 잘라 끓는 물에 넣고 삶는다.\r\n\r\n12. 볶던 채소에 간장, 돼지갈비 양념, 귤즙, 소금, 후추, 맛술을 넣는다.\r\n\r\n13. 삶은 가위면을 체에 밭치고 찬물에 헹궈 식힌다.\r\n\r\n14. 식힌 가위면을 볶던 채소에 넣고 함께 볶는다.\r\n\r\n15. 볶은 면과 채소에 지진 훈제오리살을 넣고 함께 볶는다.\r\n\r\n16. 완성된 요리를 그릇에 담아 마무리한다.'),
	(390, '오늘부터 오리는♬', NULL, 1, '1. 아스파라거스를 적당한 크기로 잘라, 소금을 뿌린 뜨거운 물에 넣고 살짝 데친다.\r\n\r\n2. 적양파를 가늘게 채 썬다.\r\n\r\n3. 마른 팬에 채 썬 적양파를 넣고 볶는다.\r\n\r\n4. 오리고기의 껍질과 살을 분리해 절반의 오리살을 핸드블렌더로 갈아 볼에 담아둔다.\r\n\r\n5. 남은 절반의 오리살을 칼로 잘게 다진다.\r\n\r\n6. 간 오리살과 다진 오리살을 섞어 패티반죽을 만든다..\r\n\r\n7. 패티반죽을 적당한 크기로 뭉쳐 패티를 만들고, 소금, 후추,\r\n올리브오일을 뿌린 뒤 골고루 발라준다.\r\n\r\n8. 기름을 두른 그릴팬에 오리패티를 굽는다.\r\n\r\n9. 8에 소금, 후추로 간 한다.\r\n\r\n10. 데친 아스파라거스를 그릴팬 한 쪽에서 굽는다.\r\n\r\n11. 복숭아를 적당한 크기로 잘라 껍질을 벗기고 그릴팬에 굽는다.\r\n\r\n12. 볶던 적양파에 레드와인, 물, 소금을 넣고 볶아 어니언 페이스트를 만든다.\r\n\r\n13. 접시에 구운 아스파라거스를 담고, 어니언 페이스트를 올린다.\r\n\r\n14. 귤을 반으로 썰어 즙만 짜낸다.\r\n\r\n15. 냄비에 귤즙, 레몬즙, 소금, 버터를 넣고 귤소스를 만든다.\r\n\r\n16. 귤소스를 넓은 팬에 옮겨 담고, 버터, 밀가루를 넣어 졸인다.\r\n\r\n17. 어니언 페이스트 위에 구운 오리패티를 올리고 구운 복숭아를 얹는다.\r\n\r\n18. 귤소스를 뿌려 마무리한다.'),
	(391, '고등어가 놀라유', NULL, 1, '1. 무쇠냄비에 카놀라유를 끓인다.\r\n\r\n2. 고등어의 잔가시를 제거한다.\r\n\r\n3. 고등어 등부분에 칼집을 내고 청주, 생강즙에 담가둔다.\r\n\r\n4. 강판에 마늘을 갈아 볼에 담고, 다진 생강, 다진 고추, 적당한 크기로 썬 대파 흰 부분을 섞는다.\r\n\r\n5. 4에 간장, 맛술, 뜨거운 물을 넣고 양념장을 만든다.\r\n\r\n6. 고등어를 내열접시에 옮겨 담고, 양념장, 매운 고춧가루를 뿌린 뒤 그릇 채로 찜기에 넣는다.\r\n\r\n7. 양파를 잘게 썰어 고등어를 담은 내열접시에 올리고 찜기 뚜껑을 닫은 후 찐다.\r\n\r\n8. 즉석밥을 전자레인지에 돌린다.\r\n\r\n9. 아이스군고구마를 적당한 크기로 자른 뒤, 기름을 두른 팬에 익힌다.\r\n\r\n10. 익힌 군고구마를 체에 밭쳐 기름기를 뺀다.\r\n\r\n11. 군고구마를 익혔던 팬에 설탕, 물엿을 넣고 끓여 맛탕소스를 만든다.\r\n\r\n12. 익힌 군고구마를 맛탕소스에 넣고 섞는다.\r\n\r\n13. 접시에 고구마 맛탕을 담아낸다.\r\n\r\n14. 그릇에 밥을 담는다.\r\n\r\n15. 고등어 찜 위에 끓인 카놀라유를 뿌린다.\r\n\r\n16. 고등어 찜의 기름기를 걷어내고 마무리한다.'),
	(392, '고등어 하나만 허락해주소서♬', NULL, 1, '1. 고등어를 청주에 담가 비린내를 제거하고, 잔가시를 제거한다.\r\n\r\n2. 고등어 등 부분에 칼집을 낸다.\r\n\r\n3. 마른 팬에 고등어를 살 부분부터 익힌다.\r\n\r\n4. 고등어를 익히는 팬에 물, 청주, 맛술, 설탕, 생강즙을 넣어 끓이다 거품을 걷어낸다.\r\n\r\n5. 고등어를 익히는 팬에 간장을 넣고 고등어에 양념을 끼얹어가며 조린다.\r\n\r\n6. 즉석밥을 전자레인지에 돌린다.\r\n\r\n7. 감자를 적당한 크기로 썰어 뜨거운 물에 삶는다.\r\n\r\n8. 볼에 명란젓을 담고 마요네즈와 섞는다.\r\n\r\n9. 옥수수 통조림의 내용물을 체에 밭쳐 건더기만 건져 볼에 담는다.\r\n\r\n10. 삶은 감자를 체에 밭쳐 물기를 제거하고, 볼에 담아 재료들과 함께 으깬다.\r\n\r\n11. 10에 소금으로 간한다.\r\n\r\n12. 버터를 두른 팬에 적당한 크기로 뭉친 명란감자를 굽는다.\r\n\r\n13. 아스파라거스를 적당한 크기로 썰고, 고등어조림에 넣어 익힌다.\r\n\r\n14. 접시에 완성된 명란감자를 담는다.\r\n\r\n15. 볼에 밥을 담고, 깨, 참기름을 넣고 섞은 뒤 적당한 크기로 뭉쳐 접시에 담는다.\r\n\r\n16. 명란감자 위에 고등어조림, 조림양념, 아스파라거스를 얹어 마무리한다.'),
	(393, '블링토볼', NULL, 1, '1. 토마토를 잘게 썰어 볼에 담아둔다.\r\n\r\n2. 양파를 잘게 썰어 토마토와 함께 볼에 담아둔다.\r\n\r\n3. 잘게 썬 토마토와 양파에 소금, 식초, 설탕을 넣고 섞는다.\r\n\r\n4. 3에 후추, 올리브오일을 넣고 섞어 토마토살사를 만든다.\r\n\r\n5. 적당한 크기로 썬 양파, 마늘, 고추, 대파를 핸드블렌더로 간다.\r\n\r\n6. 5에 적당한 크기로 썬 살치살을 넣고 다시 한 번 간다.\r\n\r\n7. 핸드블렌더로 간 재료에 식용유를 넣는다.\r\n\r\n8. 핸드블렌더 속 재료들에 조미료를 넣은 뒤, 다시 갈아준다.\r\n\r\n9. 마른 팬에 간 재료들을 넣고 볶는다.\r\n\r\n10. 끓인 물에 달걀을 넣고 반숙으로 익힌다.\r\n\r\n11. 즉석밥을 전자레인지에 돌린다.\r\n\r\n12. 샐러드용 채소를 적당한 크기로 썬다.\r\n\r\n13. 볶던 고기에 후추를 넣는다.\r\n\r\n14. 접시에 밥을 담는다.\r\n\r\n15. 달걀을 익힌 냄비의 불을 끈다.\r\n\r\n16. 밥 위에 볶은 고기, 샐러드용 채소, 토마토살사를 얹는다.\r\n\r\n17. 반숙으로 익힌 달걀을 올려 마무리한다.'),
	(394, '입맛다시징', NULL, 1, '1. 오징어의 껍질을 벗긴다.\r\n\r\n2. 껍질을 벗긴 오징어에 촘촘히 칼집을 낸다.\r\n\r\n3. 칼집을 낸 오징어를 적당한 크기로 썬다.\r\n\r\n4. 적당한 크기로 썬 오징어에 뜨거운 물을 부어 데친다\r\n\r\n5. 전자레인지에 즉석밥을 돌린다.\r\n\r\n6. 대파 흰 부분, 양파, 청양고추, 태국고추를 적당한 크기로 썬다.\r\n\r\n7. 볼에 뜨거운 물을 담고, 조미료를 푼다.\r\n\r\n8. 기름을 두른 팬에 다진 마늘, 적당한 크기로 썬 청양고추와 태국고추를 넣고 볶는다.\r\n\r\n9. 8에 적당한 크기로 썬 양파, 간장, 조미료를 푼 물을 넣고 볶는다.\r\n\r\n10. 볶던 채소에 물을 넣어 간을 맞춘다.\r\n\r\n11. 볶던 채소에 전분물, 참기름을 넣는다.\r\n\r\n12. 11에 칼집을 내 데친 오징어와 적당한 크기로 썬 엔다이브를 넣고 함께 볶는다.\r\n\r\n13. 접시에 오징어채소볶음을 담는다.\r\n\r\n14. 오징어채소볶음 위에 참기름을 뿌린다.\r\n\r\n15. 접시에 밥을 따로 담아낸다.'),
	(395, '야들야들한~♬ 고추완자', NULL, 1, '1. 양파를 잘게 썰어 볼에 담아둔다.\r\n\r\n2. 매실장아찌, 청양고추를 잘게 썰어 양파와 함께 볼에 담는다.\r\n\r\n3. 살치살과 닭가슴살을 적당한 크기로 썬 후, 칼로 곱게 다져 섞는다.\r\n\r\n4. 팬에 버터를 넣고 녹인다.\r\n\r\n5. 곱게 다진 살치살과 닭가슴살을 잘게 썰어둔 채소들과 함께 담고,\r\n후추, 달걀, 소금, 전분, 녹인 버터를 넣고 섞어 완자반죽을 만든다.\r\n\r\n6. 완자반죽을 적당한 크기로 떼어 넉넉하게 기름을 두른 팬에 튀긴다.\r\n\r\n7. 애플망고 과육, 설탕, 레몬즙을 핸드블렌더로 곱게 간다.\r\n\r\n8. 7에 마요네즈를 넣고 다시 갈아 애플망고소스를 만든다.\r\n\r\n9. 아스파라거스를 손질한 뒤, 끓는 물에 넣어 데친다.\r\n\r\n10. 데친 아스파라거스를 찬물에 헹궈 식히고, 잘게 썬다.\r\n\r\n11. 튀긴 완자를 체에 밭쳐 기름기를 뺀다.\r\n\r\n12. 그릇에 애플망고소스를 담는다.\r\n\r\n13. 소스 위에 튀긴 완자를 올린다.\r\n\r\n14. 잘게 썬 데친 아스파라거스를 올려 완성한다.'),
	(396, '오징어 카니발♬', NULL, 1, '1. 오징어의 껍질을 벗긴다.\r\n\r\n2. 껍질을 벗긴 오징어를 적당한 크기로 썰어 핸드블렌더에 넣는다.\r\n\r\n3. 오징어가 담긴 핸드블렌더에 전분, 달걀흰자, 설탕, 소금을 넣고 곱게 갈아 오징어반죽을 만든다.\r\n\r\n4. 오징어반죽을 완자 크기로 떼어 끓는 물에 넣고 삶는다.\r\n\r\n5. 버터를 볼에 담아 전자레인지에 돌려 녹인다.\r\n\r\n6. 다른 볼에 달걀, 설탕, 녹인 버터를 넣고 잘 섞어 달걀물을 만든다.\r\n\r\n7. 팬에 달걀물을 붓고 익힌다.\r\n\r\n8. 오징어반죽을 비닐팩에 담고 짤주머니를 만든 뒤, 달걀지단 위에 짜낸다.\r\n\r\n9. 달걀지단을 돌돌 말아 오징어달걀말이를 만든다.\r\n\r\n10. 오징어달걀말이를 김발로 말고, 고무줄로 고정시켜 모양을 잡는다.\r\n\r\n11. 마늘을 잘게 썬다.\r\n\r\n12. 팬에 잘게 썬 마늘과 식용유, 고춧가루, 잘게 찢은 태국고추, 잘게 썬 청양고추를 넣는다.\r\n\r\n13. 12에 잘게 썬 대파 흰 부분을 넣고 함께 볶는다.\r\n\r\n14. 볶은 재료에 오징어완자를 삶은 물과 케첩, 설탕을 넣고 졸여 칠리소스를 만든다.\r\n\r\n15. 삶은 오징어완자를 칠리소스에 넣고 섞는다.\r\n\r\n16. 김발로 고정해뒀던 오징어달걀말이를 적당한 크기로 썬다.\r\n\r\n17. 접시에 칠리 오징어완자를 담고, 잘게 썬 청양고추를 올린다.\r\n\r\n18. 오징어달걀말이를 곁들여 완성한다.'),
	(397, '짐승롤', NULL, 1, '1. 냄비에 각각 우유와 휘핑크림을 붓고 끓인다.\r\n\r\n2. 돼지고기, LA갈비, 표고버섯, 대파, 양파를 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n3. 2에 소금으로 간한다.\r\n\r\n4. 볶던 재료와 끓이는 우유, 휘핑크림에 후추를 넣는다.\r\n\r\n5. 끓이던 우유에 레몬즙을 넣고, 끓이던 휘핑크림에 크림치즈를 넣어 크림소스를 만든다.\r\n\r\n6. 볶은 재료를 핸드블렌더에 넣고 간다.\r\n\r\n7. 핸드블렌더에 밀가루와 달걀을 넣고 돌려 반죽을 만든다.\r\n\r\n8. 반죽을 밀어 면을 만든다.\r\n\r\n9. 면을 넓은 네모모양으로 잘라 끓는 물에 삶는다.\r\n\r\n10. 레몬즙을 넣어 끓인 우유를 면보에 걸러 리코타치즈를 만든다.\r\n\r\n11. 마른 팬에 토마토소스를 넣고 끓인다.\r\n\r\n12. 삶은 면을 건져 키친타월에 올린 뒤 물기를 제거한다.\r\n\r\n13. 볶아서 갈아둔 고기에 리코타치즈를 넣고 섞어 소를 만든다.\r\n\r\n14. 넓은 면 위에 소를 올리고 돌돌 말아 파이프모양을 만든다.\r\n\r\n15. 그릇에 크림소스를 붓고 파이프면을 적당한 크기로 잘라 올린 뒤, 다시 크림소스를 끼얹는다.\r\n\r\n16. 끓인 토마토소스를 올려 마무리한다.'),
	(398, '스바라시멘', NULL, 1, '1. 컵라면의 면에 끓는 물을 부어 익힌다.\r\n\r\n2. 끓는 물에 냉면면을 넣고 익힌 뒤, 체에 밭쳐 건지고 찬물에 식힌다.\r\n\r\n3. 익은 컵라면을 찬물에 식힌 뒤, 다시 한 번 뜨거운 물을 부어 익힌다.\r\n\r\n4. 볼에 달걀흰자를 넣고 거품기로 친다.\r\n\r\n5. 달걀흰자에 전분을 넣는다.\r\n\r\n6. 익힌 컵라면면을 다시 한 번 찬물에 식힌 뒤, 전분을 넣은 달걀흰자와 함께 버무린다.\r\n\r\n7. 돼지고기 위에 깻잎, 청포도를 올리고 돌돌 만다.\r\n\r\n8. 종이포일 위에 달걀흰자에 버무린 컵라면 면을 깔고 청포도를 넣은 돼지고기롤을 올려 돌돌 만다.\r\n\r\n9. 종이포일로 감싼 롤을 쿠킹포일로 다시 한 번 감싼다.\r\n\r\n10. 포일로 감싼 롤을 꼬치로 찔러 구멍을 내고, 기름에 넣어 튀긴다.\r\n\r\n11. 핸드블렌더에 깻잎, 표고버섯, 갓김치, 견과류, 올리브오일을 넣고 간다.\r\n\r\n12. 간 재료에 소금, 간장, 맛술을 넣고 섞어 갓김치페스토를 만든다.\r\n\r\n13. 믹서기에 멸치볶음을 넣고 곱게 간다.\r\n\r\n14. 튀긴 롤을 꺼내 포일을 벗기고 팬에 올린 뒤 올리브오일을 뿌려 굽는다.\r\n\r\n15. 표고버섯을 얇게 썰어 기름을 두른 팬에서 굽는다.\r\n\r\n16. 냉면면의 물기를 짜낸 뒤, 구운 표고버섯, 갓김치페스토, 소금, 후추와 함께 섞는다.\r\n\r\n17. 접시에 갓김치페스토에 버무린 면을 올린다.\r\n\r\n18. 팬에서 다시 한 번 익힌 롤을 적당한 크기로 썰어 접시 위에 올린다.\r\n\r\n19. 명란젓의 속만 꺼내 마요네즈를 섞어 롤 위에 올린다.\r\n\r\n20. 애호박을 얇게 썰어 플레이팅한다.\r\n\r\n21. 곱게 간 멸치볶음을 접시 위에 흩뿌린다.\r\n\r\n22. 다른 롤 위에 갓김치페스토를 올리고, 명란마요네즈를 올린 롤 위에 고춧가루를 뿌린다.'),
	(399, '단결! 곰탕이지말입니다', NULL, 1, '1. 양파, 애호박을 슬라이스 한다.\r\n\r\n2. 기름을 두른 그릴팬에 슬라이스한 양파와 애호박, 손질한 미니파프리카를 굽는다.\r\n\r\n3. 냄비에 인스턴트 곰탕을 넣고 끓인다.\r\n\r\n4. 볼에 고춧가루, 고추장, 참기름, 간장, 다진 마늘, 물엿을 넣는다.\r\n\r\n5. 4에 맛술, 후추, 불고기용 돼지고기를 넣고 버무린다.\r\n\r\n6. 팬에 통후추와 잘게 썬 양파를 넣고, 화이트와인을 부어 끓인다.\r\n\r\n7. 그릴팬에 굽던 채소에 소금으로 간한다.\r\n\r\n8. 구운 채소를 잘게 썰고, 볼에 담아 식힌다.\r\n\r\n9. 통오징어의 껍질을 벗긴다.\r\n\r\n10. 마른 팬에 양념한 돼지고기를 넣고, 후추를 뿌려 익힌다.\r\n\r\n11. 볶은 고추장불고기와 구운 채소를 통오징어 안에 채워 넣고 꼬치로 봉한다.\r\n\r\n12. 기름을 두른 그릴팬에 속을 채운 통오징어를 굽는다.\r\n\r\n13. 양파를 넣고 끓이던 화이트와인에 레몬즙, 끓인 인스턴트 곰탕, 버터를 넣고 곰탕소스를 만든다.\r\n\r\n14. 고추와 깻잎을 잘게 썬다.\r\n\r\n15. 접시에 구운 채소를 담는다.\r\n\r\n16. 채소 위에 고추장불고기를 채워 구운 통오징어를 올리고, 잘게 썬 깻잎과 고추를 얹는다.\r\n\r\n17. 곰탕소스를 끼얹어 마무리한다.'),
	(400, 'I\'ll be back♬', NULL, 1, '1. 감자를 적당한 크기로 썬다.\r\n\r\n2. 끓는 물에 적당한 크기로 썬 감자와 소금을 넣고 삶는다.\r\n\r\n3. 돼지목살, 훈제오리, 수비드치킨을 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 팬에 손질한 돼지목살과 가늘게 채 썬 양파를 넣고 볶는다.\r\n\r\n5. 손질한 미니파프리카와 애호박, 훈제오리를 팬에 넣고 함께 볶는다.\r\n\r\n6. 청양고추, 마늘을 잘게 다져 팬에 넣고 함께 볶는다.\r\n\r\n7. 볶던 재료에 고춧가루를 넣는다.\r\n\r\n8. 그린올리브, 방울토마토를 잘게 썬다.\r\n\r\n9. 볶던 재료에 휘핑크림을 붓고 끓인다.\r\n\r\n10. 9에 인스턴트 곰탕, 손질한 수비드치킨, 토마토소스, 손질한 방울토마토, 소금을 넣고 끓인다.\r\n\r\n11. 삶은 감자를 체에 밭쳐 물기를 빼고, 다른 재료들과 함께 끓인다.\r\n\r\n12. 끓인 재료를 뚝배기에 옮겨 담고 모차렐라&체다 치즈를 올린 뒤, 예열한 오븐에 넣어 익힌다.\r\n\r\n13. 잘게 썬 올리브를 올려 마무리한다.'),
	(401, '하룻강아지 복 무서운 줄 알고 오삼', NULL, 1, '1. 핸드블렌더에 적당한 크기로 썬 삼겹살, 달걀물, 소금, 후추를 넣고 갈아 볼에 담아둔다.\r\n\r\n2. 양파를 잘게 다져, 갈아둔 삼겹살과 함께 담아둔다.\r\n\r\n3. 오징어의 껍질을 벗기고, 촘촘하게 칼집을 낸다.\r\n\r\n4. 칼집을 낸 오징어를 적당한 크기로 썰어 밀가루를 묻힌다.\r\n\r\n5. 볼에 밀가루, 전분, 달걀물, 물, 식용유를 넣고 반죽해 튀김옷을 만든다.\r\n\r\n6. 손질한 오징어 > 명란젓 > 다진 삼겹살을 켜켜이 쌓아 접시에 담는다.\r\n\r\n7. 6에 튀김옷을 뿌려 모양이 흐트러지지 않게 한다.\r\n\r\n8. 튀김옷을 입힌 오징어삼겹살을 기름에 튀긴다.\r\n\r\n9. 기름이 담긴 팬에 손질한 대파와 고춧가루를 넣고 끓여 고추기름을 만든다.\r\n\r\n10. 끓인 고추기름을 체에 밭쳐 건더기를 걸러낸다.\r\n\r\n11. 마늘을 다진 뒤, 고추기름, 허니머스터드, 케첩을 넣고 섞어 칠리소스를 만든다.\r\n\r\n12. 오징어삼겹살튀김을 접시에 담는다.\r\n\r\n13. 칠리소스를 따로 담아 완성한다.'),
	(402, '황금탕수육', NULL, 1, '1. 돼지목살을 칼 옆면과 칼등으로 두드려 부드럽게 만든다.\r\n\r\n2. 부드럽게 만든 돼지목살을 길쭉하게 썬다.\r\n\r\n3. 견과류를 으깬 뒤, 볼에 담아둔다.\r\n\r\n4. 볼에 밀가루:전분:으깬 견과류(2:5:3)를 담는다.\r\n\r\n5. 4에 물과 식용유를 넣어 반죽해 튀김옷을 만든다.\r\n\r\n6. 손질한 돼지목살에 튀김옷을 입힌 뒤, 기름에 튀긴다.\r\n\r\n7. 손질한 양파, 레몬, 대파, 고추, 사과를 기름을 두른 팬에 넣고,\r\n뜨거운 물을 부어 채소과일육수를 끓인다.\r\n\r\n8. 양파를 가늘게 채 썰고, 청포도를 잘게 자른다.\r\n\r\n9. 채 썬 양파와 잘게 썬 청포도를 마른 팬에 볶는다.\r\n\r\n10. 끓이던 채소과일육수에 생강즙, 복분자즙을 넣는다.\r\n\r\n11. 간장:식초:설탕:채소과일육수(1:2:2:5)를 섞어 소스를 만든다.\r\n\r\n12. 볶은 양파와 청포도를 볼에 담아 식힌다.\r\n\r\n13. 소스를 끓이다가 전분물을 넣어 농도를 조절한다.\r\n\r\n14. 탕수육을 접시에 담고, 볶은 청포도와 양파를 얹은 뒤 소스를 따로 담아낸다.'),
	(403, '청고마비', NULL, 1, '1. 대파를 적당한 크기로 썬 뒤, 소금간을 한다.\r\n\r\n2. 기름을 두른 팬에 소금으로 간 한 대파를 굽는다.\r\n\r\n3. 볼에 사과주스, 생강즙, 다진 생강, 레몬즙을 넣은 뒤, 면보에 걸러 끓인다.\r\n\r\n4. 3에 레몬껍질을 갈아 넣고, 버터를 넣어 사과생강소스를 만든다.\r\n\r\n5. 고등어의 잔가시를 제거하고 껍질을 벗긴 뒤, 소금과 후추로 간 한다.\r\n\r\n6. 간을 한 고등어에 밀가루를 묻히고 기름을 두른 팬에 굽는다.\r\n\r\n7. 청포도를 반으로 썰고 얇게 슬라이스한다.\r\n\r\n8. 구운 고등어 위에 얇게 슬라이스한 청포도를 비늘처럼 덮는다.\r\n\r\n9. 접시에 구운 대파를 담는다.\r\n\r\n10. 구운 대파 위에 청포도를 올린 구운 고등어 올린다.\r\n\r\n11. 사과생강소스를 따로 담아낸다.'),
	(404, '나비효과', NULL, 1, '1. 마른 팬에 으깬 통마늘과 채 썬 양파, 잘게 썬 대파를 넣고 볶는다.\r\n\r\n2. 전복을 손질해 적당한 크기로 썬다.\r\n\r\n3. 적당한 크기로 썬 전복과 명란젓을 채소를 볶던 팬에 넣고, 화이트와인을 부어 함께 볶는다.\r\n\r\n4. 볶던 재료에 인스턴트 맛조개국과 물을 넣고 끓인다.\r\n\r\n5. 4에 즉석밥을 넣고 끓여 전복명란리소토를 만든다.\r\n\r\n6. 볼에 밀가루, 전분, 잘게 다진 멸치볶음, 조미김을 넣는다.\r\n\r\n7. 6에 올리브오일과 물을 넣고 섞어 튀일반죽을 만든다.\r\n\r\n8. 마른 팬에 튀일반죽을 얇게 붓고 굽는다.\r\n\r\n9. 핸드블렌더에 적당한 크기로 손질한 오징어와 두부, 소금, 후추,\r\n고추냉이, 양파, 깻잎장아찌를 넣고 갈아 튀김반죽을 만든다.\r\n\r\n10. 튀김반죽에 밀가루를 넣고 섞은 뒤, 적당한 크기로 뭉쳐 겉에 밀가루를 입힌다.\r\n\r\n11. 밀가루를 입힌 튀김반죽에 달걀물>잘게 부순 라면 순으로 튀김옷을 입힌다.\r\n\r\n12. 오징어라면튀김을 끓는 기름에 튀겨낸 뒤, 건져서 오븐에서 다시 한 번 굽는다.\r\n\r\n13. 구운 튀일을 꺼내 한 김 식힌다.\r\n\r\n14. 접시에 전복명란리소토를 담는다.\r\n\r\n15. 리소토 위에 오징어라면튀김을 올린다.\r\n\r\n16. 튀김 사이에 튀일을 꽂아 나비 날개 모양을 만든다.'),
	(405, '샘통이다', NULL, 1, '1. 소고기 양지를 적당한 크기로 썬다.\r\n\r\n2. 적당한 크기로 썬 소고기 양지와 대파 흰 부분을 냄비에 넣고 끓여 소고기육수를 낸다.\r\n\r\n3. 감자의 껍질을 벗겨내고 적당한 크기로 썰어 끓는 물에 익힌다.\r\n\r\n4. 마른 팬에 적당한 크기로 썬 소고기 양지를 넣고 볶다가, 소금, 참기름, 으깬 마늘을 넣는다.\r\n\r\n5. 무를 적당한 크기로 썰어 넣고 소고기 양지와 함께 볶는다.\r\n\r\n6. 볶던 재료에 소고기육수와 간장을 넣고 소고기뭇국을 끓인다.\r\n\r\n7. 애호박은 얇게 썰고, 송이버섯은 잘게 찢어 접시에 담아둔다.\r\n\r\n8. 낙지를 적당한 크기로 썰어 담아둔다.\r\n\r\n9. 기름을 두른 팬에 얇게 썬 애호박을 굽는다.\r\n\r\n10. 애호박을 굽는 팬에 적당한 크기로 썬 낙지를 넣고 익히다가, 후추로 간 한다.\r\n\r\n11. 통오징어를 적당한 크기로 썰어 팬에 넣고 함께 익힌다.\r\n\r\n12. 재료를 익히는 팬에 잘게 찢은 송이버섯을 넣고 함께 볶는다.\r\n\r\n13. 골뱅이를 잘게 다진 뒤, 허니머스터드, 잘게 썬 양파,\r\n마요네즈, 레몬즙, 올리브오일, 후추를 넣고 섞는다.\r\n\r\n14. 익힌 감자를 건져내고 파르메산 치즈가루, 후추를 뿌려 으깬다.\r\n\r\n15. 볶은 애호박, 낙지, 송이버섯을 그릇에 담는다.\r\n\r\n16. 볶은 재료들 위에 익힌 오징어를 얹고, 안에 으깬 감자를 채운다.\r\n\r\n17. 소고기뭇국 국물을 담는다.\r\n\r\n18. 송이버섯을 잘게 썰어 올린다.\r\n\r\n19. 대파 흰 부분을 잘게 썰어 올린 뒤, 양념한 골뱅이를 얹고 후추를 뿌려 마무리한다.'),
	(406, '낙장불입', NULL, 1, '1. 양파를 채 썬다.\r\n\r\n2. 마늘을 편으로 썬다.\r\n\r\n3. 기름을 두른 냄비에 손질한 양파와 마늘, 장어를 넣고 익힌다.\r\n\r\n4. 낙지를 적당한 크기로 썰어 냄비에 넣고 다른 재료와 함께 익힌다.\r\n\r\n5. 냄비 속 재료에 위스키를 뿌린 뒤, 불을 붙여 비린내를 제거한다.\r\n\r\n6. 재료가 담긴 냄비에 인삼음료, 물, 인삼뿌리를 넣고 끓여 장어육수를 낸다.\r\n\r\n7. 끓이던 장어육수에 영양부추를 넣고 뚜껑을 닫아 푹 끓인다.\r\n\r\n8. 볼에 밀가루, 물, 식용유를 넣고 섞어 튀김반죽을 만든다.\r\n\r\n9. 튀김반죽에 마요네즈를 넣고 섞는다.\r\n\r\n10. 적당한 크기로 썬 낙지에 고추냉이와 소금을 넣고 버무린다.\r\n\r\n11. 기름을 두른 팬에 채 썬 양파, 즉석밥을 넣고 볶는다.\r\n\r\n12. 볶던 밥에 장어육수를 넣고 조려 리소토를 만든다.\r\n\r\n13. 육수를 낸 장어를 건져 잘게 썬다.\r\n\r\n14. 잘게 썬 장어를 리소토에 넣고 장어리소토를 만든다.\r\n\r\n15. 장어리소토에 소금, 파르메산 치즈가루를 넣고 섞는다.\r\n\r\n16. 고추냉이에 버무린 낙지에 튀김옷을 입히고, 기름에 넣어 튀긴다.\r\n\r\n17. 한 번 튀겨낸 낙지에 튀김옷을 입혀 다시 한 번 튀긴다.\r\n\r\n18. 장어리소토를 접시에 담고, 위에 낙지튀김을 얹는다.\r\n\r\n19. 소스볼에 파르메산 치즈가루와 고춧가루를 섞어 따로 낸다.'),
	(407, '깨지겠탕', NULL, 1, '1. 소고기 양지를 적당한 크기로 썬다.\r\n\r\n2. 핸드블렌더에 소고기 양지와 물에 불린 황태채를 넣고 간다.\r\n\r\n3. 기름을 넉넉히 두른 팬에 곱게 간 재료를 넣고 튀기듯이 볶는다.\r\n\r\n4. 마늘을 다져 팬에 넣고 다른 재료와 함께 볶는다.\r\n\r\n5. 볶던 재료에 참기름과 굴소스, 들깨가루를 넣고 볶는다.\r\n\r\n6. 볶은 재료에 뜨거운 물을 붓고 소고기 들깨탕을 끓인다.\r\n\r\n7. 소고기 들깨탕에 팽이버섯, 손질한 쌈추를 넣는다.\r\n\r\n8. 감자를 가늘게 채 썬다.\r\n\r\n9. 채 썬 감자를 찬물에 씻어 전분기를 빼고 물기를 제거해 볼에 담아둔다.\r\n\r\n10. 청양고추를 적당한 크기로 썰어 감자채와 함께 볼에 담아둔다.\r\n\r\n11. 전자레인지에 즉석밥을 넣고 돌린다.\r\n\r\n12. 마른 팬에 감자채와 청양고추를 넣고 볶다가 소금, 식초를 넣고 볶는다.\r\n\r\n13. 부추를 적당한 크기로 썰어 소고기 들깨탕에 넣는다.\r\n\r\n14. 소고기 들깨탕을 그릇에 담는다.\r\n\r\n15. 감자볶음을 접시에 따로 담아낸다.\r\n\r\n16. 밥을 따로 내 마무리한다.'),
	(408, '콩닭거렸쌈', NULL, 1, '1. 냄비에 인삼음료를 넣고 끓인다.\r\n\r\n2. 끓이던 인삼음료에 설탕, 물, 물엿, 마누카 꿀을 넣고 끓인다.\r\n\r\n3. 끓이던 재료에 꿀에 절인 인삼을 넣어 인삼소스를 만든다.\r\n\r\n4. 기름을 두른 팬에 송편을 굽는다.\r\n\r\n5. 전자레인지에 즉석밥을 넣고 돌린다.\r\n\r\n6. 구운 송편을 볼에 담아 식힌다.\r\n\r\n7. 송편을 구웠던 팬에 적당한 크기로 자른 곶감과 으깬 땅콩을 넣고 볶는다.\r\n\r\n8. 기름을 두른 팬에 인스턴트 닭발을 볶다가 적당한 크기로 자른 뒤, 레몬즙을 넣는다.\r\n\r\n9. 콩잎장아찌를 물에 헹군 뒤, 물기를 제거한다.\r\n\r\n10. 끓이던 인삼소스에 볶은 곶감과 땅콩을 넣고 졸여 강정소스를 만든다.\r\n\r\n11. 구운 송편을 접시에 담고, 강정소스를 뿌려 송편 강정을 만든다.\r\n\r\n12. 볼에 밥, 영양부추, 참기름, 깨, 볶은 닭발을 넣고 버무린다.\r\n\r\n13. 12를 적당한 크기로 뭉친 뒤, 콩잎장아찌로 감싸 접시에 담는다.\r\n\r\n14. 칵테일 셰이커에 증류소주, 능이버섯 달인 물, 레몬즙을 넣고 섞는다.\r\n\r\n15. 유리잔에 레몬즙과 소금을 차례로 묻힌다.\r\n\r\n16. 볼에 흑마늘액, 겨자를 넣고 섞어 흑마늘소스를 만든 뒤, 콩잎쌈밥에 곁들인다.\r\n\r\n17. 소금을 묻힌 유리잔에 능이버섯주를 담아 마무리한다.'),
	(409, '오마이갓코스', NULL, 1, '1. 웨하스 초콜릿을 잘게 부순 뒤, 중탕으로 녹인다.\r\n\r\n2. 레몬즙을 낸다.\r\n\r\n3. 핸드블렌더에 레몬즙, 유자청, 간장을 넣고 곱게 갈아 레몬유자소스를 만든다.\r\n\r\n4. 레몬유자소스를 체에 밭쳐 곱게 거른 뒤, 올리브오일을 넣는다.\r\n\r\n5. 양파를 채칼로 얇게 썰어 얼음물에 담가둔다.\r\n\r\n6. 마른 팬에 적당한 크기로 썬 소고기를 넣고 한 면만 익힌다.\r\n\r\n7. 한 면만 익힌 소고기를 얇게 썰어 접시에 담는다.\r\n\r\n8. 소고기 위에 레몬유자소스를 뿌린 뒤, 얼음물에 담아둔 양파채의 물기를 빼고 올린다.\r\n\r\n9. 대파 흰 부분을 잘게 썰어, 버터를 두른 팬에 볶는다.\r\n\r\n10. 핸드블렌더에 아몬드를 넣고 간다.\r\n\r\n11. 수비드 닭가슴살에 밀가루>달걀물>아몬드가루를 입혀 기름에 튀긴다.\r\n\r\n12. 튀긴 아몬드닭가슴살에 볶은 대파와 체다치즈를 올리고 전자레인지에 돌려 치즈를 녹인다.\r\n\r\n13. 당근을 적당한 크기로 썬 뒤, 핸드블렌더로 간다.\r\n\r\n14. 냄비에 버터를 녹이다가, 곱게 간 당근과 크림치즈, 밀가루,\r\n우유, 굴소스, 소금을 넣고 끓여 당근수프를 만든다.\r\n\r\n15. 믹서기에 당근수프를 넣고 간다.\r\n\r\n16. 체다치즈를 올려서 녹인 아몬드닭가슴살튀김을 접시에 담는다.\r\n\r\n17. 중탕한 웨하스 초콜릿을 그릇에 담고, 바닐라 아이스크림을 올린 뒤,\r\n웨하스 초콜릿을 곱게 갈아 뿌린다.\r\n\r\n18. 그릇에 당근수프를 담아낸다.'),
	(410, '미식풀코스', NULL, 1, '1. 초콜릿을 중탕해서 녹인다.\r\n\r\n2. 깍두기를 물에 헹궈 양념을 씻어낸다.\r\n\r\n3. 양념을 씻어낸 깍두기와 당근을 그레이터를 이용해 얇게 썬다.\r\n\r\n4. 대파를 잘게 썰어 손질한 깍두기, 당근과 함께 담아둔다\r\n\r\n5. 4에 레몬즙, 올리브오일, 식초, 소금, 후추를 넣고 버무려 깍두기샐러드를 만든다.\r\n\r\n6. 접시에 깍두기샐러드를 담는다.\r\n\r\n7. 달걀 흰자를 쳐서 머랭을 만든다.\r\n\r\n8. 핸드블렌더에 아몬드를 넣고 간다.\r\n\r\n9. 머랭에 곱게 간 아몬드를 넣고 섞는다.\r\n\r\n10. 기름을 두른 팬에 아몬드머랭을 넣고 구워 머랭케이크를 만든다.\r\n\r\n11. 양파, 당근을 잘게 썰어 기름을 두른 팬에 넣고 볶는다.\r\n\r\n12. 볶은 재료에 양배추즙과 끓는 물을 넣고 끓인다.\r\n\r\n13. 수비드 닭가슴살을 적당한 크기로 썬 뒤, 끓이던 재료에 함께 넣고 수프를 끓인다.\r\n\r\n14. 짬뽕라면 면을 잘게 부숴, 수프에 넣고 함께 끓인다.\r\n\r\n15. 볼에 달걀, 레몬즙을 넣고 섞은 뒤, 체에 걸러 수프에 넣고 끓인다.\r\n\r\n16. 그릇에 닭가슴살 수프를 담고, 대파를 잘게 썰어 올린다.\r\n\r\n17. 접시에 녹인 중탕으로 초콜릿을 바르고, 머랭케이크를 원형틀로 모양을 내서 올린다.\r\n\r\n18. 바닐라 아이스크림을 적당한 크기로 썰어, 머랭케이크 위에 올린다.\r\n\r\n19. 초콜릿>머랭케이크>초콜릿 순서로 아이스크림 위에 올린 뒤,\r\n초콜릿으로 접시에 데커레이션을 한다.\r\n\r\n20. 초콜릿을 갈아 머랭케이크 위에 뿌린다.\r\n\r\n21. 곱게 간 아몬드를 뿌려 마무리한다.'),
	(411, '잭슨 앤 치즈스테이크', NULL, 1, '1. 수비드 닭가슴살을 잘게 썬다.\r\n\r\n2. 소고기 등심을 잘게 썰고, 기름을 두른 팬에 손질한 닭가슴살과\r\n소고기를 볶다가 소금, 후추로 간 한다.\r\n\r\n3. 마른 팬에 밀가루와 버터를 볶아 루를 만든다.\r\n\r\n4. 양파를 동그랗게 썰어 기름을 두른 팬에 굽다가 소금, 후추로 간 한다.\r\n\r\n5. 루에 우유와 체다치즈를 넣고 끓여 체다치즈소스를 만든다.\r\n\r\n6. 체다치즈소스에 소금, 후추로 간한다.\r\n\r\n7. 볼에 크림치즈와 잘게 부순 아몬드, 후추, 소금을 넣고 섞는다.\r\n\r\n8. 아몬드크림치즈에 레몬즙을 넣는다.\r\n\r\n9. 볶던 닭가슴살과 소고기에 체다치즈소스를 넣고 조린다.\r\n\r\n10. 9를 양파를 굽던 팬에 넣는다.\r\n\r\n11. 접시에 구운 양파와 체다치즈소스에 조린 닭가슴살과\r\n소고기를 담고, 아몬드크림치즈를 얹는다.\r\n\r\n12. 소고기 등심을 적당한 크기로 썰고, 소금, 후추로 간 한 뒤 기름을 두른 팬에 굽는다.\r\n\r\n13. 접시에 체다치즈소스를 뿌린다.\r\n\r\n14. 스테이크를 올린 뒤, 아몬드크림치즈를 얹고, 후추를 뿌려 마무리한다.'),
	(412, '소울을 느끼소', NULL, 1, '1. 소고기 등심을 잘게 썬다.\n\n2. 양파를 채 썬다.\n\n3. 기름을 두른 팬에 손질한 소고기와 양파를 볶는다.\n\n4. 볶던 재료에 레드와인을 넣고 조린다.\n\n5. 마른 팬에 버터와 밀가루를 넣고 볶아 루를 만든다.\n\n6. 소고기 등심을 고기망치로 두드려 얇게 편 뒤, 적당한 크기로 썬다.\n\n7. 적당한 크기로 썬 소고기에 소금을 뿌리고 밀가루를 입힌다.\n\n8. 밀가루를 입힌 소고기와 채 썬 양파와 우유를 루에 넣고 끓여 조린다.\n\n9. 기름을 두른 팬에 잘게 썬 대파 흰 부분과 즉석밥을 넣고 볶는다.\n\n10. 루에 조리던 소고기와 대파밥에 각각 간장으로 간 한다.\n\n11. 간장으로 간 한 대파밥에 버터를 넣고 볶다가, 달걀을 넣어 섞는다.\n\n12. 그릇에 달걀버터간장밥을 담고, 소금으로 간 한다.\n\n13. 루에 조린 소고기에 크림치즈를 넣고 녹인 후, 달걀버터간장밥 위에 얹는다.\n\n14. 소고기를 건져낸 크림치즈소스를 잘 저어 크림치즈를 완전히 녹인다.\n\n15. 소고기와 양파를 조린 레드와인소스에 버터와 겨자를 넣는다.\n\n16. 크림치즈소스를 체에 거른 뒤, 우유를 부어 다시 한 번 끓인다.\n\n17. 소고기 위에 크림치즈소스를 뿌린다.\n\n18. 레드와인에 조리던 소고기와 양파를 체에 밭쳐 걸러내고 레드와인소스만 남긴다.\n\n19. 완성된 음식 위에 레드와인소스를 뿌려 마무리한다.'),
	(413, '만두에 대한 경례', NULL, 1, '1. 파프리카를 직화한다.\r\n\r\n2. 마른 팬에 버터를 넣고 녹인다.\r\n\r\n3. 적당한 크기로 썬 삼겹살과 굴소스, 뜨거운 물, 맛술,\r\n생강즙, 간장을 핸드블렌더로 갈아 만두소를 만든다.\r\n\r\n4. 곱게 간 만두소를 볼에 담고 녹인 버터를 섞는다.\r\n\r\n5. 만두소에 후추, 잘게 다진 양파를 넣는다.\r\n\r\n6. 대파 흰 부분을 잘게 다져 볼에 덜어두고, 만두소에도 넣는다.\r\n\r\n7. 채소를 넣은 만두소에 참기름을 넣고 섞는다.\r\n\r\n8. 팽이버섯을 잘게 썰어 만두소에 넣는다.\r\n\r\n9. 볼에 밀가루와 뜨거운 물을 넣고 치대어 익반죽한다.\r\n\r\n10. 반죽을 적당한 크기로 떼어내고 밀대로 밀어 만두피를 만든다.\r\n\r\n11. 만두피에 만두소를 넣고 빚는다.\r\n\r\n12. 기름을 두른 팬에 만두를 넣고 익히다가 물을 부은 뒤, 뚜껑을 닫아 증기로 속까지 익힌다.\r\n\r\n13. 다진 대파에 레몬즙, 으깬 마늘, 굴소스, 맛술, 설탕, 간장,\r\n잘게 다진 구운 파프리카를 섞어 소스를 만든다.\r\n\r\n14. 소스를 소스 종지에 담아낸다.\r\n\r\n15. 접시에 구운 만두를 담아낸다.'),
	(414, '복 잡으러 왔단닭', NULL, 1, '1. 끓는 물에 표고버섯을 삶는다.\r\n\r\n2. 삼겹살을 적당한 크기로 썬다.\r\n\r\n3. 적당한 크기로 썬 삼겹살과 고추맛 닭가슴살볼을 핸드블렌더에 넣고 간다.\r\n\r\n4. 곱게 간 삼겹살과 고추맛 닭가슴살볼에 굴소스, 청주, 달걀흰자를 넣고 치대어 소를 만든다.\r\n\r\n5. 삶은 표고버섯을 건져내 찬물에 헹군 뒤, 기둥과 윗부분을 잘라낸다.\r\n\r\n6. 손질한 표고버섯 안쪽에 격자로 촘촘하게 칼집을 낸다.\r\n\r\n7. 표고버섯 윗부분에 소를 올리고 감싼 뒤, 이쑤시개로 고정한다.\r\n\r\n8. 소를 채운 표고버섯을 기름에 튀긴다.\r\n\r\n9. 볼에 물, 케첩, 레몬즙, 설탕, 식초를 섞어 케첩소스를 만든다.\r\n\r\n10. 양파를 잘게 다져 볼에 담아둔다.\r\n\r\n11. 다진 양파에 굴소스를 넣고 마른 팬에 볶는다.\r\n\r\n12. 마른 팬에 케첩소스를 넣고 졸인다.\r\n\r\n13. 굴소스에 볶은 양파를 접시에 깐다.\r\n\r\n14. 볶은 양파 위에 표고버섯 튀김을 올린다.\r\n\r\n15. 표고버섯 튀김 위에 케첩소스를 뿌려 마무리한다.'),
	(415, '풍절판', NULL, 1, '1. 끓는 물에 소금을 넣고, 브로콜리를 데친다.\r\n\r\n2. 볼에 밀가루, 소금, 물을 넣고 섞어 밀전병 반죽을 만든다.\r\n\r\n3. 삼겹살을 적당한 크기로 썬다.\r\n\r\n4. 마른 팬에 적당한 크기로 썬 삼겹살을 볶다가 설탕, 맛술,\r\n다진 마늘, 다진 생강을 넣어 볶는다.\r\n\r\n5. 믹서기에 깨, 간장, 설탕, 식초를 넣고 갈아 소스를 만든다.\r\n\r\n6. 감자를 채 썰어 기름을 두른 팬에 볶다가 소금으로 간 한다.\r\n\r\n7. 삶은 브로콜리를 건져낸 뒤, 브로콜리를 삶던 물에 파프리카를 삶는다.\r\n\r\n8. 볶던 감자채에 식초를 넣는다.\r\n\r\n9. 완성된 감자볶음을 그릇에 담는다.\r\n\r\n10. 기름을 두른 팬에 가늘게 채 썬 당근을 볶다가 소금으로 간 한다.\r\n\r\n11. 삶은 브로콜리를 적당한 크기로 썰어 그릇에 담는다.\r\n\r\n12. 볶은 당근채를 그릇에 담는다.\r\n\r\n13. 양파를 가늘게 채 썰어 기름을 두른 팬에 볶다가 소금으로 간 한다.\r\n\r\n14. 볶은 삼겹살을 그릇에 담는다.\r\n\r\n15. 볶은 양파를 그릇에 담는다.\r\n\r\n16. 새송이버섯을 길게 썰어 기름을 두른 팬에 볶다가 굴소스로 간한다.\r\n\r\n17. 기름을 두른 팬에 밀전병 반죽을 얇게 부친다.\r\n\r\n18. 삶은 파프리카를 건져 채 썬 뒤 그릇에 담는다.\r\n\r\n19. 볶은 새송이버섯채를 그릇에 담는다.\r\n\r\n20. 기름을 두른 팬에 팽이버섯을 볶다가 소금으로 간 한다.\r\n\r\n21. 밀전병을 그릇에 담는다.\r\n\r\n22. 소스에 설탕, 참기름을 넣고 간다.\r\n\r\n23. 소스를 그릇에 담는다.\r\n\r\n24. 볶은 팽이버섯을 그릇에 담는다.'),
	(416, '으르렁 으르렁 돼지불백♬', NULL, 1, '1. 삼겹살에 칼집을 낸 뒤, 적당한 크기로 썰어 마른 팬에 볶는다.\n\n2. 볼에 다진 마늘, 간장, 설탕, 물, 물엿, 맛술, 굴소스를 넣고 섞어 불고기양념을 만든다.\n\n3. 볶은 삼겹살에 불고기양념을 넣고 조린다.\n\n4. 전자레인지에 즉석밥을 넣고 돌린다.\n\n5. 대파 흰 부분의 심지는 따로 빼놓은 뒤, 대파를 가늘게 채 썰어 찬물에 담가둔다.\n\n6. 당근을 가늘게 채 썰어 대파 심지 부분과 함께 둔다.\n\n7. 불고기 양념에 조린 삼겹살을 석쇠에 얹고, 토치로 직화한다.\n\n8. 삼겹살을 조리던 불고기양념에 대파 심지, 당근채, 직화로 구운 삼겹살을 넣고 볶는다.\n\n9. 마늘을 편으로 썰어 팬에 넣고 함께 볶는다.\n\n10. 끓는 물에 감자, 양파를 적당한 크기로 썰어 넣고 끓인다.\n\n11. 10에 굴소스, 맛술, 간장, 소금, 후추를 넣고 국을 끓인다.\n\n12. 볼에 설탕, 간장, 식초를 넣고 섞어 소스를 만든다.\n\n13. 소스에 찬물에 담가뒀던 파채와 참기름, 깨를 넣고 무친다.\n\n14. 그릇에 밥을 담고 돼지불고기>파무침>달걀노른자 순으로 올린다.\n\n15. 끓이던 국에 달걀물을 풀어 넣고 달걀감자국을 만든다.\n\n16. 달걀감자국을 그릇에 담아 마무리한다.'),
	(417, '오빠 한번 믿어봐♬', NULL, 1, '1. 사과를 가늘게 채 썬다.\r\n\r\n2. 마른 팬에 채 썬 사과, 견과류, 건크랜베리, 버터, 설탕, 발사믹소스, 딸기잼을 넣고 볶는다.\r\n\r\n3. 볼에 밀가루, 달걀, 우유, 소금을 넣고 반죽한다.\r\n\r\n4. 반죽을 밀대로 밀고, 원형틀로 찍어 만두피를 만든다.\r\n\r\n5. 만두피에 볶은 사과소를 올리고 반으로 접어 포크로 모양을 내 만두를 만든다.\r\n\r\n6. 사과만두를 끓는 물에 삶는다.\r\n\r\n7. 마른 팬에 오미자 원액, 전분을 넣고 끓여 소스를 만든다.\r\n\r\n8. 접시에 오미자 소스를 넓게 깐다.\r\n\r\n9. 삶은 만두를 건져내 물기를 제거하고, 오미자소스 위에 올린다.\r\n\r\n10. 아몬드를 갈아 뿌린다.\r\n\r\n11. 오미자소스 위에 딸기맛 요거트를 올리고 모양을 내 완성한다.'),
	(418, '머랭둥둥', NULL, 1, '1. 달걀흰자에 설탕을 넣고 거품기로 쳐 머랭을 만든다.\n\n2. 냄비에 우유를 끓인다.\n\n3. 머랭에 레몬즙을 넣고 친다.\n\n4. 각기 다른 두 개의 냄비에 우유를 끓인다.\n\n5. 끓인 우유에 크림치즈와 체다치즈, 설탕을 넣고 함께 끓여 치즈소스를 만든다.\n\n6. 다른 끓인 우유에 코코아가루를 넣고 함께 끓여 초콜릿소스를 만든다.\n\n7. 넓은 냄비에 끓인 우유에 머랭을 띄워 익히다가 뒤집어 익힌다.\n\n8. 치즈소스를 잘 섞은 뒤, 그릇에 붓는다.\n\n9. 치즈소스 위에 익힌 머랭을 띄운다.\n\n10. 머랭 위에 초콜릿소스를 뿌린다.\n\n11. 자몽을 적당한 크기로 썰어 올려 완성한다.'),
	(419, '식스센스', NULL, 1, '1. 바게트를 적당한 크기로 썬다.\n\n2. 올리브오일을 두른 팬에 적당한 크기로 썬 바게트를 굽는다.\n\n3. 두 개의 팬에 기름을 두르고 각각 잘게 다진 자숙새우와 통자숙새우를 굽다가 후추로 간 한다.\n\n4. 구운 통자숙새우는 접시에 담아둔다.\n\n5. 잘게 다진 자숙새우에 버터, 밀가루, 우유를 넣고 볶아 크림새우를 만들어 접시에 담아둔다.\n\n6. 레드와인식초에 올리브오일을 섞어 소스를 만든 뒤, 구운 통자숙새우를 담가 놓는다.\n\n7. 마른 팬에 불고기용 소고기를 굽다가 소금, 후추로 간 한 뒤 꺼내놓는다.\n\n8. 아보카도를 깍둑썰어 팬에 넣고, 전복장 국물을 부어 조리다가 소금으로 간 한다.\n\n9. 칼집을 낸 토마토에 뜨거운 물을 부어 껍질을 벗긴다.\n\n10. 껍질을 벗긴 토마토를 적당한 크기로 썰어 올리브오일과 섞은 뒤 소금으로 간한다.\n\n11. 구운 바게트 위에 루콜라 > 소스에 담가뒀던 통자숙새우 > 적당한 크기로 썬 자몽 순으로 올린다.\n\n12. 구운 소고기를 발사믹소스에 무친 뒤, 구운 바게트 > 루콜라 > 소고기 순으로 올린다.\n\n13. 구운 바게트 위에 크림치즈 > 전복장 국물에 조린 아보카도 순으로 올린다.\n\n14. 마른 팬에 주꾸미를 올리고, 소금, 후추로 간 한 뒤 올리브오일을 뿌려 볶는다.\n\n15. 구운 바게트 위에 루콜라 > 볶은 주꾸미 > 레몬껍질 순으로 올린다.\n\n16. 구운 바게트 위에 크림새우를 올린다.\n\n17. 구운 바게트 위에 루콜라 > 오일에 절인 토마토 순으로 올린다.\n\n18. 아보카도 브루스케타 위에 마요네즈를 뿌린다.\n\n19. 크림새우 브루스케타 위에 토마토소스를 얹는다.\n\n20. 소고기 브루스케타 위에 잘게 다진 사과를 올린다.'),
	(420, '리듬채소', NULL, 1, '1. 양파, 당근, 파프리카, 대파를 통째로 기름에 튀긴다.\n\n2. 튀긴 파프리카를 꺼내 토치로 직화한다.\n\n3. 팬에 마늘을 갈아 넣고, 설탕, 맛술, 청주, 간장을 넣어 졸인다.\n\n4. 튀긴 대파를 꺼내, 겉 부분은 졸이던 간장소스에 넣고 속 부분은 다시 튀긴다.\n\n5. 핸드블렌더에 아보카도, 마요네즈, 복숭아맛 요거트, 설탕을 넣고 갈아 아보카도소스를 만든다.\n\n6. 간 마늘과 올리브오일을 섞어 마늘오일을 만든다.\n\n7. 핸드블렌더에 직화해서 껍질을 벗긴 파프리카, 마늘오일, 발사믹소스, 케첩을 넣고 간다.\n\n8. 7에 미숫가루, 올리브오일을 넣고 갈아 로메스코소스를 만든다.\n\n9. 기름을 두른 팬에 양배추를 굽는다.\n\n10. 튀긴 양파를 건져낸 뒤, 겉 부분은 간장소스에 넣고 속 부분은\n적당한 크기로 썰어 양배추와 함께 굽는다.\n\n11. 튀긴 당근을 건져내 겉 부분을 벗겨낸다.\n\n12. 불고기용 소고기를 간장소스에 버무린 뒤, 기름을 두른 팬에 굽는다.\n\n13. 굽던 양배추와 양파에 간장소스를 바른다.\n\n14. 접시에 아보카도소스와 로메스코소스를 번갈아가며 깐다.\n\n15. 튀긴 당근과 대파를 적당한 크기로 썰어 소스 위에 올린다.\n\n16. 구운 양파와 양배추를 얹는다.\n\n17. 간장소스에 버무려 구운 소고기를 얹는다.\n\n18. 루콜라에 레드와인식초, 올리브오일, 소금을 뿌려 버무린 뒤, 접시에 올린다.\n\n19. 간장소스에 버터를 넣고 녹인다.\n\n20. 샐러드 위에 버터간장소스를 뿌린다.\n\n21. 미숫가루를 뿌려 마무리한다.'),
	(421, '쉿테이크', NULL, 1, '1. 두부를 면보에 싼 뒤, 물기를 짠다.\r\n\r\n2. 당근, 양파, 새송이버섯을 잘게 썰어 기름을 두른 팬에 담는다.\r\n\r\n3. 잘게 썬 아스파라거스를 팬에 함께 넣고, 소금, 후추로 간을 해 볶는다.\r\n\r\n4. 3에 완두콩을 넣고 함께 볶는다.\r\n\r\n5. 핸드블렌더에 돼지목살을 곱게 간다.\r\n\r\n6. 새우를 잘게 다져 곱게 간 돼지목살, 물기를 짜낸 두부와 함께 섞는다.\r\n\r\n7. 6에 볶은 채소, 달걀, 밀가루, 참기름, 간장을 넣고 섞어 패티를 만든다.\r\n\r\n8. 마른 팬에 적당한 크기로 썬 대파, 알배추를 굽다가 명이나물 장아찌국물을 넣는다.\r\n\r\n9. 명이나물 장아찌를 잘게 다진다.\r\n\r\n10. 볼에 요거트, 레몬즙, 잘게 썬 오이, 다진 명이나물 장아찌를 넣고 섞는다.\r\n\r\n11. 기름을 두른 팬에 패티를 적당한 크기로 뭉쳐 굽는다.\r\n\r\n12. 접시에 구운 대파와 알배추를 올리고, 두부스테이크를 얹은 뒤, 명이요거트소스를 올린다.\r\n\r\n13. 대파와 알배추를 구운 명이나물 소스를 뿌려 마무리한다.'),
	(422, '꼭꼭 숨겼디야', NULL, 1, '1. 두부를 길게 썰어 소금을 뿌린 뒤, 키친타월로 감싸 물기를 뺀다.\r\n\r\n2. 양파, 당근을 가늘게 채 썰어 기름을 두른 팬에 볶는다.\r\n\r\n3. 돼지목살을 잘게 다져 채소와 함께 볶다가 소금, 후추, 살사소스를 넣고 볶는다.\r\n\r\n4. 볶던 재료에 모차렐라치즈, 손질한 아스파라거스를 넣고 볶는다.\r\n\r\n5. 양상추를 가늘게 채 썬다.\r\n\r\n6. 물기를 뺀 두부를 기름에 튀긴다.\r\n\r\n7. 토르티야를 긁어 얇게 만든다.\r\n\r\n8. 얇게 만든 토르티야를 마른 팬에 굽다가, 슬라이스 치즈를 올린다.\r\n\r\n9. 치즈를 넣어 구운 토르티야 위에 얇게 썬 아보카도, 방울토마토,\r\n볶은 고기와 채소, 튀긴 두부를 얹는다.\r\n\r\n10. 채 썬 양상추에 요거트, 소금, 후추를 넣고 버무린다.\r\n\r\n11. 양념한 양상추를 토르티야 위에 얹고 반으로 접어 케사디야를 만든다.\r\n\r\n12. 양상추샐러드를 접시에 담는다.\r\n\r\n13. 케사디야를 적당한 크기로 잘라 담아 마무리한다.'),
	(423, '복불복', NULL, 1, '1. 양파, 아스파라거스, 느타리버섯, 표고버섯, 대파를 잘게 썰어 둔다.\r\n\r\n2. 복어를 얇게 포 뜬 뒤, 접시에 올린다.\r\n\r\n3. 볼에 밀가루, 전분, 물, 식용유, 달걀물을 넣고 섞어 튀김옷을 만든다.\r\n\r\n4. 포 뜬 복어살로 새우를 돌돌 만 뒤, 튀김옷을 입혀 튀긴다.\r\n\r\n5. 기름을 두른 팬에 잘게 썬 채소들을 볶다가, 간장, 맛술,\r\n굴소스, 참기름을 넣어 채소소스를 만든다.\r\n\r\n6. 접시에 올려 둔 포 뜬 복어를 토치로 직화한다.\r\n\r\n7. 새우복어튀김을 건져 그릇에 담는다.\r\n\r\n8. 직화한 복어 위에 채소소스를 얹는다.\r\n\r\n9. 새우복어튀김 위에 채소소스를 얹어 마무리한다.'),
	(424, '복어~싶다♬', NULL, 1, '1. 게를 적당한 크기로 손질한 뒤, 끓는 물에 넣고 끓여 게 육수를 낸다.\r\n\r\n2. 대파, 아스파라거스를 적당한 크기로 썬 뒤, 대파는 끓이고, 아스파라거스는 체에 밭쳐 데친다.\r\n\r\n3. 양파, 알배추를 적당한 크기로 썰어 얼음물에 담가둔다.\r\n\r\n4. 전자레인지에 즉석밥을 돌린다.\r\n\r\n5. 볼에 간장, 식초, 설탕, 맛술을 넣고 섞어 초간장소스를 만든다.\r\n\r\n6. 밥을 찬물에 헹궈 식힌 뒤, 물에 담가 불린다.\r\n\r\n7. 복어의 살을 발라내고, 뼈는 대파와 함께 끓여 복어육수를 낸다.\r\n\r\n8. 전분을 묻힌 손질한 복어살과, 전분을 묻히지 않은 껍질을 끓는 물에 데친다.\r\n\r\n9. 데친 복어살과 대파를 함께 건져내 얼음물에 식힌다.\r\n\r\n10. 복어육수에 알배추를 넣고 끓인다.\r\n\r\n11. 볼에 간장, 청주, 맛술을 넣고 섞어 간장소스를 만든다.\r\n\r\n12. 손질한 복어살에 달걀물, 간장소스, 전분을 버무린 뒤 기름에 튀긴다.\r\n\r\n13. 복어튀김을 건져 한 김 식힌다.\r\n\r\n14. 빈 냄비에 복어육수, 게살, 복어, 불린 밥, 소금, 청주, 간장을 넣고 복죽을 끓인다.\r\n\r\n15. 데친 복어살에 양파, 알배추, 초간장소스, 깨, 고춧가루,\r\n복껍질을 넣고 버무려 복어무침을 만든다.\r\n\r\n16. 식힌 복어튀김을 다시 한 번 튀긴다.\r\n\r\n17. 복어무침을 그릇에 담는다.\r\n\r\n18. 복어튀김을 접시에 담고, 데친 아스파라거스로 데커레이션한다.\r\n\r\n19. 복죽에 달걀물을 풀고, 끓인다.\r\n\r\n20. 복죽을 그릇에 담고, 초간장소스를 곁들여낸다.'),
	(425, '고르고 고른 파스타', NULL, 1, '1. 끓는 물에 호두를 데친 뒤, 설탕에 버무린다.\r\n\r\n2. 예열된 오븐에 설탕에 버무린 호두와 적당한 크기로 썬 무화과를 넣고 익힌다.\r\n\r\n3. 볼에 밀가루, 달걀을 넣고 섞어 반죽을 만든다.\r\n\r\n4. 반죽을 밀대로 얇게 민다.\r\n\r\n5. 얇게 민 반죽을 적당한 두께로 썰어 파스타면을 만든다.\r\n\r\n6. 기름을 두른 팬에 다진 마늘을 볶다가 적당한 크기로 썬 양송이버섯,\r\n새송이버섯, 느타리버섯, 표고버섯을 넣고 볶는다.\r\n\r\n7. 볶던 버섯에 소금, 후추, 버터, 화이트와인을 넣고 볶는다.\r\n\r\n8. 호두를 데친 물에 소금을 넣는다.\r\n\r\n9. 볶던 버섯에 호두 데친 물을 넣는다.\r\n\r\n10. 볶은 버섯에 생크림과 고르곤졸라 치즈를 넣고 끓여 버섯크림소스를 만든다.\r\n\r\n11. 호두 데친 물에 면을 넣고 삶는다.\r\n\r\n12. 삶은 면을 건져 버섯크림소스에 넣고 볶는다.\r\n\r\n13. 버섯크림파스타를 접시에 담는다.\r\n\r\n14. 파스타 위에 오븐에서 익힌 무화과를 올리고, 설탕에 버무려 익힌 호두를 잘게 다져 얹는다.\r\n\r\n15. 파스타 위에 올리브오일을 뿌려 마무리한다.'),
	(426, '조선의 파스타자', NULL, 1, '1. 끓는 물에 바지락을 넣고 끓여 육수를 낸다.\r\n\r\n2. 시금치의 뿌리를 잘라내고, 끓는 물에 데친다.\r\n\r\n3. 데친 시금치를 찬물에 헹궈 식힌 뒤, 잘게 썬다.\r\n\r\n4. 믹서기에 잘게 썬 시금치와 달걀을 넣고 간다.\r\n\r\n5. 다른 믹서기에 달걀과 밀가루를 넣고 갈아 반죽을 만든다.\r\n\r\n6. 곱게 간 시금치에 밀가루를 넣고 갈아 시금치 반죽을 만든다.\r\n\r\n7. 달걀반죽과 시금치반죽을 밀대로 얇게 민 뒤, 적당한 두께로 썰어 두 가지 색의 파스타면을 만든다.\r\n\r\n8. 두 가지 색의 파스타면을 끓는 물에 삶는다.\r\n\r\n9. 익힌 바지락을 체에 밭쳐 육수만 걸러낸다.\r\n\r\n10. 관자, 전복, 새우는 적당한 크기로 손질하고, 고추와 마늘은 잘게 썬다.\r\n\r\n11. 기름을 두른 팬에 손질한 재료들을 넣고 볶다가 소금, 후추로 간 한다.\r\n\r\n12. 볶은 해산물에 화이트 와인을 넣는다.\r\n\r\n13. 삶은 면을 건져 해산물을 볶던 팬에 넣고 함께 볶는다.\r\n\r\n14. 볶던 해산물파스타에 바지락육수와 올리브오일을 넣어가며 볶는다.\r\n\r\n15. 해산물파스타를 접시에 담고 후추를 뿌려 마무리한다.'),
	(427, '마이 무따아이가', NULL, 1, '1. 핸드블렌더에 구이용 소고기와 불고기용 소고기를 넣고 함께 간다.\r\n\r\n2. 곱게 간 소고기에 잘게 썬 적양파, 소금, 후추, 맥주를 넣고 다시 간다.\r\n\r\n3. 2에 달걀물을 넣고 섞는다.\r\n\r\n4. 곱게 간 소고기를 넓게 펴고, 얇게 썬 무화과를 올린다.\r\n\r\n5. 4에 모차렐라 치즈와 고르곤졸라 치즈를 얹고, 반으로 접는다.\r\n\r\n6. 기름을 두른 그릴팬에 무화와과 치즈로 속을 채운 소고기를 굽는다.\r\n\r\n7. 감자를 가늘게 채 썬 뒤, 소금과 후추로 간 한다.\r\n\r\n8. 기름을 두른 팬에 양념한 감자채를 넓게 펴서 부친다.\r\n\r\n9. 팬에 설탕, 레몬청, 물을 넣고 끓인 뒤, 무화과를 넣어 익힌다.\r\n\r\n10. 그릴팬에 구운 소고기를 전자레인지에 넣고 돌려 속까지 익힌다.\r\n\r\n11. 감자채부침을 접시에 올린다.\r\n\r\n12. 팬에 생크림을 끓인다.\r\n\r\n13. 끓이던 생크림에 머스터드소스, 후추, 소금을 넣고 섞어 크림머스터드소스를 만든다.\r\n\r\n14. 감자채부침 위에 속까지 익힌 소고기를 올린다.\r\n\r\n15. 14 위에 크림머스터드소스를 얹고, 익힌 무화과를 올려 마무리한다.'),
	(428, '모시 돼진디?', NULL, 1, '1. 적양파는 잘게 썰고, 대파는 적당한 크기로 썬다.\r\n\r\n2. 항정살과 삼겹살을 적당한 크기로 썰어 팬에 넣고 볶는다.\r\n\r\n3. 3에 적양파와 대파를 넣고 함께 볶는다.\r\n\r\n4. 달래를 적당한 길이로 썬다.\r\n\r\n5. 끓인 기름에 고춧가루, 다진 마늘을 넣고 끓인다.\r\n\r\n6. 5를 체에 밭쳐 고추기름만 걸러낸다.\r\n\r\n7. 적양파, 마늘, 고추를 잘게 썰어 고추기름에 넣고 함께 끓인다.\r\n\r\n8. 고추기름에 모시조개, 동죽, 바지락, 화이트와인을 넣고 함께 볶는다.\r\n\r\n9. 볶은 돼지고기에 굴소스를 넣고 함께 볶는다.\r\n\r\n10. 9에 고추기름에 볶은 각종 조개를 넣고 볶은 뒤, 맥주를 넣는다.\r\n\r\n11. 볶던 재료에 달래를 넣고 함께 볶은 뒤, 접시에 담는다.\r\n\r\n12. 적당한 크기로 썬 레몬을 곁들여 마무리한다.'),
	(429, '풍비박산', NULL, 1, '1. 두부를 적당한 크기로 썰어 기름에 튀긴다.\r\n\r\n2. 애호박, 소시지, 양파를 적당한 크기로 썬다.\r\n\r\n3. 미니새송이버섯을 적당한 크기로 썰고, 마늘을 으깬다.\r\n\r\n4. 대파 흰 부분을 적당한 크기로 썰고, 양파는 가늘게 채 썬다.\r\n\r\n5. 오징어 몸통부분에 촘촘하게 칼집을 내고, 적당한 크기로 썬다.\r\n\r\n6. 미니파프리카와 꽈리고추를 적당한 크기로 썬다.\r\n\r\n7. 튀긴 두부를 체에 밭쳐 기름을 뺀다.\r\n\r\n8. 베이컨용 삼겹살을 적당한 크기로 썰어 팬에 볶는다.\r\n\r\n9. 볶던 삼겹살에 손질한 미니파프리카, 꽈리고추, 대파, 미니새송이버섯, 으깬 마늘을 넣고 볶는다.\r\n\r\n10. 다른 팬에 기름을 두르고, 손질한 애호박, 소시지, 양파를 볶는다.\r\n\r\n11. 10에 케첩, 고추장, 깨, 전분물을 넣고 볶는다.\r\n\r\n12. 볶던 삼겹살&채소에 손질한 오징어, 양파채를 넣고 볶다가, 유자후추와 일본간장을 넣는다.\r\n\r\n13. 그릇에 소시지채소볶음을 담는다.\r\n\r\n14. 볶던 삼겹살과 오징어에 전분물을 넣고 볶은 뒤, 소시지채소볶음 위에 얹는다.\r\n\r\n15. 대파 흰 부분을 적당한 크기로 썰고, 마늘을 으깬다.\r\n\r\n16. 게맛살은 적당한 크기로 찢는다.\r\n\r\n17. 기름을 두른 팬에 대파 흰 부분과 으깬 마늘을 볶다가 물을 넣고 끓인다.\r\n\r\n18. 17에 손질한 게맛살, 게살장, 잘게 썬 팽이버섯을 넣고 볶다가 전분물을 넣는다.\r\n\r\n19. 달걀흰자로 머랭을 친다.\r\n\r\n20. 끓이던 게소스에 머랭을 넣고 잘 섞는다.\r\n\r\n21. 두 가지 음식이 담긴 그릇 위에 머랭을 섞은 게소스와 튀긴 두부를 올려 마무리한다.'),
	(430, '치즈듬풍만두', NULL, 1, '1. 달걀물에 밀가루, 전분, 물을 넣고 섞어 튀김반죽을 만든다.\r\n\r\n2. 튀김반죽에 식용유를 넣는다.\r\n\r\n3. 볼에 크림치즈, 리코타치즈, 버터, 모차렐라치즈, 고다치즈를 넣고 섞은 뒤\r\n새우젓, 후추, 잘게 썬 팽이버섯을 넣고 섞어 소를 만든다.\r\n\r\n4. 베이컨용 삼겹살을 십(十)자로 펴고, 만들어둔 소를 넣어 감싼다.\r\n\r\n5. 소를 감싼 삼겹살에 튀김반죽을 입힌 뒤, 기름에 튀긴다.\r\n\r\n6. 볼에 딸기잼, 다진 양파, 마요네즈를 넣고 섞는다.\r\n\r\n7. 청양고추와 마늘을 으깬 뒤, 레몬즙과 간장을 섞는다.\r\n\r\n8. 7을 딸기마요소스와 섞는다.\r\n\r\n9. 튀긴 만두를 접시에 담는다.\r\n\r\n10. 소스를 소스종지에 담아 함께 낸다.'),
	(431, '낫띵배럴~♬', NULL, 1, '1. 양파, 대파, 고추를 잘게 썬다.\r\n\r\n2. 베이컨용 삼겹살을 적당한 크기로 썰어 팬에 볶다가, 다진 마늘, 후추를 넣는다.\r\n\r\n3. 2에 손질한 양파, 대파, 다진 총각김치, 고춧가루를 넣고 볶는다.\r\n\r\n4. 카망베르치즈와 체다치즈를 전자레인지에 돌려 녹인다.\r\n\r\n5. 낫토를 으깬 뒤, 된장과 섞는다.\r\n\r\n6. 낫토된장에 녹인 치즈, 다진 마늘, 고춧가루를 넣고 섞어 청국장을 만든다.\r\n\r\n7. 팬에 볶은 삼겹살과 채소에 인스턴트 육개장을 붓고 끓인다,\r\n\r\n8. 7에 뜨거운 물에 갠 청국장을 넣고 끓이다가, 후추를 넣는다.\r\n\r\n9. 전자레인지에 즉석밥을 넣고 돌린다.\r\n\r\n10. 끓는 물에 라면을 삶는다.\r\n\r\n11. 청국장에 전분물을 넣는다.\r\n\r\n12. 그릇에 밥을 담고, 달걀노른자와 잘게 부순 깨, 참기름, 잘게 썬 대파를 올린다.\r\n\r\n13. 삶은 라면을 그릇에 담는다.\r\n\r\n14. 라면 위에 청국장소스를 올린다.\r\n\r\n15. 밥을 곁들여낸다.'),
	(432, '연게우동', NULL, 1, '1. 끓는 물에 우동면을 삶는다.\r\n\r\n2. 연근 껍질을 벗겨내고 반은 강판에 곱게 갈고, 반은 잘게 다진다.\r\n\r\n3. 곱게 간 연근과 잘게 다진 연근을 함께 팬에 볶다가 소금, 전분, 설탕을 넣는다.\r\n\r\n4. 랩 위에 볶은 연근을 올리고 크림치즈와 게살장을 안에 넣어\r\n동그랗게 감싼 뒤, 얼음물에 담가 차갑게 식힌다.\r\n\r\n5. 연근을 얇게 슬라이스 해 물에 담가 전분기를 뺀다.\r\n\r\n6. 오징어에 촘촘하게 칼집을 낸다.\r\n\r\n7. 애호박을 가늘게 채 썬다.\r\n\r\n8. 끓는 물에 육수용 티백, 후추를 넣고 우린다.\r\n\r\n9. 육수에 게살통조림과 게맛살, 맛술, 청주, 일본간장, 게살장을 넣고 끓여 게소스를 만든다.\r\n\r\n10. 크림치즈와 게살장을 넣은 연근만두에 전분을 입힌 뒤, 기름에 튀긴다.\r\n\r\n11. 물에 담가뒀던 연근 슬라이스를 기름에 튀긴다.\r\n\r\n12. 익은 우동면을 건져내 찬물에 식힌 뒤, 그릇에 담는다.\r\n\r\n13. 게소스에 파채와 전분물, 유자후추를 넣는다.\r\n\r\n14. 연근만두와 연근슬라이스튀김을 체에 밭쳐 기름기를 뺀다.\r\n\r\n15. 우동면 위에 연근만두를 올리고, 게소스를 얹는다.\r\n\r\n16. 연근슬라이스튀김을 올려 마무리한다.'),
	(433, '아돔케어', NULL, 1, '1. 돔 모양 틀에 버터를 바른 랩을 깐다.\r\n\r\n2. 아보카도를 얇게 슬라이스 한 뒤, 틀 안에 담는다.\r\n\r\n3. 갈치의 비늘을 벗겨낸 뒤, 내장을 제거하고 원형틀로 모양을 낸다.\r\n\r\n4. 기름을 두른 팬에 모양 낸 갈치와 자숙새우를 굽다가, 후추를 뿌린다.\r\n\r\n5. 아보카도 돔 안에 구운 갈치>명란젓>구운 자숙새우 순으로\r\n채워 넣고 랩으로 감싸 모양을 고정한 뒤, 찜기에 찐다.\r\n\r\n6. 냄비에 시금치딥, 우유를 넣고 끓인다.\r\n\r\n7. 끓는 물에 시금치와 브로콜리를 데친다.\r\n\r\n8. 끓이던 시금치딥을 넣은 우유에 데친 시금치와 브로콜리, 버터를 넣고 끓인 뒤,\r\n믹서기에 갈아 시금치소스를 만든다.\r\n\r\n9. 핸드블렌더에 블랙올리브, 황도를 넣고 간다.\r\n\r\n10. 9에 올리브오일, 소금, 후추를 넣고 다시 한 번 갈아준 뒤,\r\n체에 곱게 걸러 올리브황도퓌레를 만든다.\r\n\r\n11. 접시에 시금치소스를 담는다.\r\n\r\n12. 시금치소스 위에 익힌 아보카도 돔을 올린다.\r\n\r\n13. 올리브황도퓌레로 시금치소스 위에 모양을 낸다.\r\n\r\n14. 얇게 썬 레몬을 아보카도 돔 위에 얹어 마무리한다.'),
	(434, '아보카도롤모델', NULL, 1, '1. 끓는 물에 소금을 넣는다.\r\n\r\n2. 안창살에 소금, 후추를 뿌린다.\r\n\r\n3. 아보카도를 얇게 썬다.\r\n\r\n4. 양파를 얇게 채 썬 뒤, 얼음물에 담가 아린 맛을 뺀다.\r\n\r\n5. 랩 위에 얇게 썬 아보카도를 넓게 깐 뒤, 채 썬 양파와 자숙새우,\r\n훈제연어를 넣고 돌돌 말아 롤을 만든다.\r\n\r\n6. 아보카도롤을 적당한 크기로 썬다.\r\n\r\n7. 팬에 반으로 썬 파프리카와 식용유를 듬뿍 넣은 뒤, 후추와 발사믹식초를 넣고 토치로 직화한다.\r\n\r\n8. 직화한 파프리카를 뒤집어 발사믹식초를 듬뿍 부은 뒤, 토치로 직화한다.\r\n\r\n9. 케일을 데친 뒤, 얼음물에 식힌다.\r\n\r\n10. 핸드블렌더에 데친 케일과 얼음, 케일주스와 시금치주스를 넣고 갈아 시금치케일소스를 만든다.\r\n\r\n11. 버터를 녹인 팬에 으깬 마늘을 넣고, 밑간 한 안창살을 굽는다.\r\n\r\n12. 시금치케일소스에 된장과 고추장을 넣고 섞는다.\r\n\r\n13. 접시에 시금치케일소스를 담고, 구운 안창살을 올린다.\r\n\r\n14. 레몬즙을 바른 아보카도롤을 접시에 올린다.\r\n\r\n15. 구운 파프리카의 껍질을 벗겨내고 얇게 슬라이스 해 접시에 올린다.\r\n\r\n16. 슬라이스 한 블랙올리브를 안창살 위에 올린다.\r\n\r\n17. 사과를 얇게 썬 뒤, 애플비네거에 절여 아보카도롤 위에 올린다.\r\n\r\n18. 시금치케일소스 위에 스리라차소스를 뿌려 마무리한다.'),
	(435, '귯갈~', NULL, 1, '1. 냄비에 애플비네거, 올리브오일, 통후추를 넣고 끓인다.\r\n\r\n2. 1에 슬라이스한 마늘을 함께 넣고 끓이다가, 소금, 꿀을 넣고 함께 끓인다.\r\n\r\n3. 갈치의 비늘을 벗겨낸 뒤, 살만 발라낸다.\r\n\r\n4. 손질한 갈치살을 끓인 애플비네거오일에 담가 숙성시키며 익힌다.\r\n\r\n5. 적양파, 파프리카, 양파, 자숙새우, 훈제연어를 잘게 썰어 볼에 담고 올리브 오일을 뿌린다.\r\n\r\n6. 5에 잘게 썬 케이퍼와 블랙올리브, 고춧가루, 후추, 레몬즙,\r\n당근, 마늘도 넣고 한데 섞어 샐러드를 만든다.\r\n\r\n7. 접시에 샐러드와 훈제연어, 파프리카를 담는다.\r\n\r\n8. 애플비네거오일로 익힌 갈치살을 샐러드 위에 올린다.\r\n\r\n9. 아보카도와 레몬으로 데커레이션 하고, 꿀과 고춧가루를 뿌려 마무리한다.'),
	(436, '갈팡치팡', NULL, 1, '1. 끓는 물에 청주, 양파, 레몬을 넣고 끓인다.\r\n\r\n2. 갈치의 비늘을 벗겨낸 뒤, 살만 발라낸다.\r\n\r\n3. 핸드블렌더에 갈치살, 달걀, 전분을 넣고 간다.\r\n\r\n4. 곱게 간 갈치살을 숟가락으로 모양을 잡은 뒤, 채소 끓인 물에 넣고 익혀 크넬을 만든다.\r\n\r\n5. 코코넛워터와 황도를 핸드블렌더로 갈아 황도소스를 만든다.\r\n\r\n6. 대파 흰 부분과 적양파를 잘게 썬다.\r\n\r\n7. 기름을 두른 팬에 잘게 썬 대파와 적양파, 다진 생강을 넣고 볶는다.\r\n\r\n8. 볶던 재료에 잘게 썬 자숙새우와 카레가루, 황도소스를 넣고 볶아 황도카레소스를 만든다.\r\n\r\n9. 소금을 넣은 끓는 물에 시금치를 데친 뒤, 물기를 뺀다.\r\n\r\n10. 황도카레소스에 간장, 데친 시금치, 스리라차소스를 넣고 볶는다.\r\n\r\n11. 기름을 두른 팬에 갈치 크넬을 넣고 익힌다.\r\n\r\n12. 접시에 황도카레소스를 담는다.\r\n\r\n13. 볶던 갈치 크넬에 화이트와인을 넣는다.\r\n\r\n14. 황도카레소스 위에 갈치 크넬을 올리고 다시 한 번 황도카레소스를 얹어 마무리한다.'),
	(437, '똑같은 쌈을 강요하지마♬', NULL, 1, '1. 버터를 전자레인지에 돌려 녹인다.\n\n2. 밀가루와 전분에 뜨거운 물을 넣고 치대어 익반죽한다.\n\n3. 고등어 통조림을 체에 밭쳐 건더기만 걸러낸다.\n\n4. 마른 팬에 건져낸 고등어통조림 건더기와 다진 마늘을 넣고 볶는다.\n\n5. 볶던 재료에 우유를 넣고 끓인다.\n\n6. 끓이던 재료에 크림치즈, 슬라이스 체다치즈를 넣고 녹인다.\n\n7. 6에 잘게 썬 표고버섯과 소금을 넣고 끓여 크림치즈고등어소스를 만든다.\n\n8. 도마에 밀가루를 뿌린 뒤, 적당한 크기로 떼어낸 반죽을 밀대로 밀어 춘빙피를 만든다.\n\n9. 얇게 민 춘빙피에 녹인 버터를 바르고, 다른 춘빙피를 겹쳐 밀대로 민다.\n\n10. 마른 팬에 춘빙을 굽는다.\n\n11. 크림치즈고등어소스에 잘게 썬 청양고추를 넣는다.\n\n12. 구운 춘빙을 바닥에 내려쳐 두 장으로 분리한다.\n\n13. 접시에 춘빙을 담고, 그릇에 크림치즈고등어소스를 담아 함께 낸다.'),
	(438, '만두 널 사랑해~♬', NULL, 1, '1. 밀가루에 물을 넣고 저어 반죽을 만든다.\r\n\r\n2. 기름에 반죽을 흩뿌리듯 튀겨, 덴카스를 만든다.\r\n\r\n3. 튀긴 덴카스를 건져 기름기를 뺀다.\r\n\r\n4. 핸드블렌더에 인스턴트 만두의 소만 빼서 넣고, 적당한 크기로 썬\r\n양파와 방울양배추, 표고버섯, 달걀을 더해 곱게 간다.\r\n\r\n5. 곱게 간 재료에 전분을 넣고 섞어 고로케소를 만든다.\r\n\r\n6. 고로케소에 슬라이스 체다치즈를 넣고 동그랗게 빚어 치즈고로케소를 만든다.\r\n\r\n7. 고로케소에 잘게 썬 청양고추와 카레가루를 넣고 섞어 카레고로케소를 만든다.\r\n\r\n8. 카레고로케소를 동그랗게 빚는다.\r\n\r\n9. 우유에 달걀, 밀가루를 넣고 저어 반죽을 만든다.\r\n\r\n10. 치즈고로케소와 카레고로케소에 반죽을 입힌 뒤, 덴카스를 묻혀 튀긴다.\r\n\r\n11. 기름을 두른 팬에 슬라이스한 마늘과 케첩, 물, 설탕, 레몬즙,\r\n훠궈 홍탕 소스를 넣고 끓여 칠리소스를 만든다.\r\n\r\n12. 칠리소스에 잘게 썬 청양고추를 넣는다.\r\n\r\n13. 접시에 칠리소스를 담은 뒤, 치즈고로케와 카레고로케를 올려 마무리한다.'),
	(439, '리소토가 차이나', NULL, 1, '1. 팬에 버터, 다진 마늘, 다진 양파를 넣고 볶는다.\r\n\r\n2. 1에 밀가루를 넣고 볶아 루를 만들고, 우유를 넣고 끓여 크림소스를 만든다.\r\n\r\n3. 크림소스에 크림치즈와 잘게 썬 표고버섯을 넣고 끓인다.\r\n\r\n4. 대파, 마늘, 양파를 잘게 다져 기름을 두른 팬에 볶는다.\r\n\r\n5. 핸드블렌더로 마라소고기를 곱게 간다.\r\n\r\n6. 볶던 채소에 곱게 간 마라소고기를 넣고 함께 볶는다.\r\n\r\n7. 6에 훠궈 홍탕 소스와 모둠 해물을 넣고 함께 볶는다.\r\n\r\n8. 볶던 재료에 밥을 넣고 볶아 마라리소토를 만든다.\r\n\r\n9. 면근을 체에 밭쳐 건더기를 걸러내고, 국물에 식초, 맛술,\r\n간장, 잘게 썬 청양고추를 넣어 간장소스를 만든다.\r\n\r\n10. 양파, 청경채를 가늘게 채 썰어 그릇에 담는다.\r\n\r\n11. 채소샐러드 위에 면근을 올린다.\r\n\r\n12. 크림소스에 후추를 넣는다.\r\n\r\n13. 마라리소토에 잘게 썬 팽이버섯을 넣고 볶는다.\r\n\r\n14. 그릇에 크림소스를 담고, 마라리소토를 올린다.\r\n\r\n15. 면근샐러드 위에 간장소스를 올려 마무리한다.'),
	(440, '샘차이나', NULL, 1, '1. 팬에 버터, 밀가루를 넣고 볶아 루를 만든다.\r\n\r\n2. 루에 우유를 넣고 끓여 크림소스를 만든다.\r\n\r\n3. 끓는 물에 훠궈 버섯탕 수프를 넣고 끓인다.\r\n\r\n4. 버섯탕 국물에 청경채를 데친 뒤, 건져내 물기를 뺀다.\r\n\r\n5. 표고버섯과 방울양배추를 손질한 뒤, 버섯탕 국물에 데친다.\r\n\r\n6. 기름을 두른 팬에 으깬 마늘, 마라갈비, 다진 양파를 넣고 볶는다.\r\n\r\n7. 볶던 재료에 버섯탕 국물을 넣고 졸인다.\r\n\r\n8. 데친 방울양배추를 건져 식힌다.\r\n\r\n9. 버섯탕 국물에 졸인 마라갈비에 라조장을 넣는다.\r\n\r\n10. 9에 버터, 밀가루를 넣는다.\r\n\r\n11. 크림소스에 크림치즈를 넣고 녹인다.\r\n\r\n12. 다진 양파에 연어통조림, 타르타르소스, 마요네즈, 후추를 넣고 섞어 연어샐러드를 만든다.\r\n\r\n13. 핸드블렌더에 블랙올리브, 올리브오일을 넣고 갈아 올리브페스토를 만든다.\r\n\r\n14. 기름을 두른 팬에 데친 방울양배추와, 표고버섯을 넣고 볶다가 소금, 후추로 간 한다.\r\n\r\n15. 데친 청경채 위에 올리브페스토>연어샐러드>케이퍼>크림치즈소스를 순서대로 올린다.\r\n\r\n16. 16을 토치로 직화한다.\r\n\r\n17. 방울양배추와 표고버섯을 볶던 팬에 각종 허브를 붓고,\r\n토치로 불을 붙인 뒤 뚜껑을 닫아 향을 입힌다.\r\n\r\n18. 접시에 마라갈비소스를 담고, 허브로 훈제한 방울양배추와\r\n표고버섯을 올린 뒤, 크림치즈청경채를 올린다.\r\n\r\n19. 크림치즈청경채 위에 올리브페스토>마라갈비소스>올리브오일을 올려 마무리한다.'),
	(441, '풍당풍당면', NULL, 1, '1. 핸드블렌더에 적당한 크기로 썬 돼지고기 목심과 새우젓을 넣는다.\r\n\r\n2. 1에 명란젓, 새우를 함께 넣고 곱게 간다.\r\n\r\n3. 끓는 물에 당면을 삶는다.\r\n\r\n4. 기름을 넉넉하게 두른 팬에 새우머리를 넣고 새우기름을 낸다.\r\n\r\n5. 새우기름에 잘게 썬 대파, 으깬 마늘, 고춧가루를 넣고 끓인다.\r\n\r\n6. 양념한 새우기름에 젓갈과 함께 간 돼지고기를 넣고 끓인다.\r\n\r\n7. 6에 식초, 손질한 양파, 표고버섯, 죽순을 넣고 끓인다.\r\n\r\n8. 끓이던 재료에 삶은 당면을 넣고 함께 끓인다.\r\n\r\n9. 끓이던 재료에서 새우머리만 건져낸다.\r\n\r\n10. 전분물을 넣는다.\r\n\r\n11. 접시에 담아 마무리한다.'),
	(442, '언더 더 씨♬', NULL, 1, '1. 청양고추를 잘게 썬다.\r\n\r\n2. 문어를 적당한 크기로 썬다.\r\n\r\n3. 감성돔의 살만 발라내 적당한 크기로 썬다.\r\n\r\n4. 전복의 내장을 떼어내고, 살만 적당한 크기로 썬다.\r\n\r\n5. 기름을 두른 냄비에 잘게 다진 마늘과 양파를 볶다가, 새우머리와 우유를 넣고 수프를 끓인다.\r\n\r\n6. 끓이던 수프에서 새우머리만 건져낸다.\r\n\r\n7. 새우머리를 건져낸 수프에 버터를 넣어 버터포치수프를 만든다.\r\n\r\n8. 버터포치수프에 후추와 소금을 넣는다.\r\n\r\n9. 적당한 크기로 자른 당면을 기름에 튀긴 뒤, 건져내 기름기를 뺀다.\r\n\r\n10. 불린 귀리를 데친 뒤, 기름에 튀긴다.\r\n\r\n11. 버터포치수프에 손질한 새우와 전복을 데치다가, 전복만 건져낸다.\r\n\r\n12. 끓는 물에 손질한 문어와 감성돔을 데친 뒤, 버터포치수프에 넣고 끓인다.\r\n\r\n13. 새송이버섯을 적당한 크기로 썬 뒤, 버터포치수프에 넣는다.\r\n\r\n14. 버터포치수프에 데친 해산물과 새송이버섯을 건져낸 뒤, 핸드블랜더로 수프를 곱게 간다.\r\n\r\n15. 버터포치수프를 병에 담고, 잘게 썬 청양고추를 넣는다.\r\n\r\n16. 접시에 튀긴 귀리와 당면을 담는다.\r\n\r\n17. 버터포치수프에 데친 각종 해산물을 올린 뒤, 잘게 썬 청양고추를 뿌린다.\r\n\r\n18. 해산물시리얼과 버터포치수프를 함께 낸다.'),
	(443, '완도풀 파티', NULL, 1, '1. 오징어몸통의 껍질을 벗겨내고, 후추로 밑간한다.\r\n\r\n2. 기름을 두른 그릴팬에 손질한 오징어몸통을 올리고, 무거운 것으로 눌러 굽는다.\r\n\r\n3. 문어다리를 고기망치로 두드려 부드럽게 만들고 후추로 밑간 한 뒤, 그릴팬에 올려 함께 굽는다.\r\n\r\n4. 애호박, 당근, 미니파프리카를 적당한 크기로 썰어, 기름을 두른 그릴팬에 굽는다.\r\n\r\n5. 해산물을 굽던 그릴팬에 새우도 함께 굽는다.\r\n\r\n6. 볼에 올리브오일, 레몬즙, 소금, 후추, 잘게 다진 고구마줄기무침과\r\n할라피뇨를 넣고 섞어 드레싱을 만든다.\r\n\r\n7. 구운 오징어를 적당한 크기로 썬 뒤, 고구마줄기드레싱에 무친다.\r\n\r\n8. 양파를 잘게 썰어 오징어를 넣은 고구마줄기드레싱에 함께 무친다.\r\n\r\n9. 구운 문어다리도 다른 재료들과 함께 고구마줄기드레싱에 무친 뒤, 접시에 담는다.\r\n\r\n10. 구운 새우를 곁들인다.\r\n\r\n11. 구운 각종채소를 볼에 담고, 다진 마늘, 올리브오일, 후추, 식초를 넣고 버무려 샐러드를 만든다.\r\n\r\n12. 11을 접시에 담는다.\r\n\r\n13. 석류알을 뿌린다.\r\n\r\n14. 고춧가루를 뿌려 마무리한다.'),
	(444, '롤 is my everything♬', NULL, 1, '1. 서대를 얇게 포 뜨고 껍질을 벗긴다.\r\n\r\n2. 핸드블렌더에 손질한 새우살과 소금, 참기름, 후추, 달걀흰자, 돼지고기 기름을 넣고 간다.\r\n\r\n3. 핸드블렌더로 간 새우살을 적당한 크기로 뭉친 뒤, 전분을 묻힌 서대포로 감싸듯 롤을 만든다.\r\n\r\n4. 물이 끓는 찜기에 청주를 넣고, 서대새우롤을 찐다.\r\n\r\n5. 끓는 물에 두릅을 데친다.\r\n\r\n6. 데친 두릅을 건지고 체에 밭쳐 물기를 뺀다.\r\n\r\n7. 냄비에 오렌지주스를 끓인다.\r\n\r\n8. 팬에 아로니아즙과 오렌지주스를 끓인다.\r\n\r\n9. 끓인 오렌지주스에 삶은 죽순, 레몬즙, 데친 두릅을 넣고 조린다.\r\n\r\n10. 오렌지주스에 조린 죽순과 두릅을 접시에 담는다.\r\n\r\n11. 기름을 두른 팬에 찐 서대새우롤을 굽는다.\r\n\r\n12. 팬에 끓인 아로니아즙과 오렌지주스에 버터를 넣고 녹여 아로니아소스를 만든다.\r\n\r\n13. 아로니아소스에 두릅과 죽순을 조린 오렌지주스를 넣는다.\r\n\r\n14. 죽순과 두릅 위에 서대새우롤을 올린다.\r\n\r\n15. 아로니아소스를 뿌려 마무리한다.'),
	(445, '완소 두부', NULL, 1, '1. 대파와 마늘은 잘게 다지고, 둥근 호박과 양파, 두부는 적당한 크기로 썬다.\r\n\r\n2. 소고기등심을 잘게 다진다.\r\n\r\n3. 핸드블렌더에 손질한 새우, 버터, 달걀물, 전분, 생강즙을 넣고 간다.\r\n\r\n4. 핸드블렌더로 간 새우에 소금으로 간 한다.\r\n\r\n5. 4에 잘게 다진 골뱅이를 넣고 치대어 완자반죽을 만든다.\r\n\r\n6. 완자반죽을 적당한 크기로 떼어내 찜기에 찐다.\r\n\r\n7. 기름에 누룽지를 튀겨낸 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n8. 기름을 두른 팬에 손질한 대파와 다진 소고기등심, 고춧가루를 넣고 볶는다.\r\n\r\n9. 8에 청주, 간장, 매실엑기스, 손질한 마늘, 둥근 호박, 양파,\r\n두부, 청양고추, 고추장을 넣어 마파두부를 만든다.\r\n\r\n10. 마파두부에 사골곰탕을 넣고 볶는다.\r\n\r\n11. 볶던 재료에 참기름, 전분물을 넣는다.\r\n\r\n12. 그릇에 튀긴 누룽지와 새우골뱅이완자를 담는다.\r\n\r\n13. 그릇에 마파두부를 담아 마무리한다.'),
	(446, '1등 가자~미!', NULL, 1, '1. 채소육수를 만든다.\r\n\r\n2. 채소육수에 사골곰탕을 넣고 끓인다.\r\n\r\n3. 기름을 두른 팬에 적당한 두께로 썬 애호박을 굽다가 소금, 후추로 간 한다.\r\n\r\n4. 애호박에 잘게 다진 마늘, 식초, 곰탕채소육수, 버터를 넣고 볶는다.\r\n\r\n5. 감자를 원형틀로 찍어낸 뒤, 적당한 두께로 썬다.\r\n\r\n6. 기름을 두른 팬에 감자를 굽다가 소금, 후추로 간 한다.\r\n\r\n7. 감자에 잘게 다진 마늘, 곰탕채소육수, 식초를 넣고 볶는다.\r\n\r\n8. 가자미를 포 뜬 뒤, 버터를 바르고 레몬껍질을 올린다.\r\n\r\n9. 가자미포를 반으로 접어 실로 묶는다.\r\n\r\n10. 기름을 두른 팬에 대파 흰 부분과 가자미를 굽는다.\r\n\r\n11. 종이포일을 말아 그릇을 만든 뒤, 볶은 애호박과 감자를 넣고 오븐에 굽는다.\r\n\r\n12. 가자미에 후추로 간 한다.\r\n\r\n13. 접시에 구운 대파와 가자미를 담는다.\r\n\r\n14. 가자미를 굽던 팬에 레몬즙, 화이트와인, 곰탕채소육수, 버터를 넣고 끓여 소스를 만든다.\r\n\r\n15. 대파와 가자미가 담긴 접시에 생선소스를 부은 뒤, 전자레인지에 돌린다.\r\n\r\n16. 채소가 담긴 종이포일 안에 가자미, 구운 대파, 소스를 넣고, 후추와 올리브오일을 뿌려 마무리한다.'),
	(447, '플라이 투 더 전복', NULL, 1, '1. 전복에 뜨거운 물을 부어 데친다.\r\n\r\n2. 데친 전복의 내장과 살을 분리한다.\r\n\r\n3. 닭가슴살을 적당한 크기로 썰어 소금과 후추로 간 한 뒤, 기름을 두른 팬에 굽는다.\r\n\r\n4. 양파를 잘게 썰어 닭가슴살과 함께 굽는다.\r\n\r\n5. 4에 불린 누룽지와 파프리카를 넣고 볶는다.\r\n\r\n6. 전복살을 적당한 크기로 썬다.\r\n\r\n7. 기름을 두른 팬에 채 썬 양파와 전복내장, 청주, 맛술을 넣고 볶는다.\r\n\r\n8. 볼에 고춧가루, 으깬 마늘, 잘게 썬 파, 식용유를 넣고 전자레인지에 돌려 고추기름을 낸다.\r\n\r\n9. 핸드블렌더에 전복내장과 볶은 양파와 홍삼엑기스를 넣고 간 뒤, 참기름을 넣는다.\r\n\r\n10. 9에 고추기름을 넣고 갈아 전복내장소스를 만든다.\r\n\r\n11. 전복내장소스에 간장을 섞는다.\r\n\r\n12. 볶던 누룽지에 전복내장소스와 전복살을 넣고 소금, 후추를 뿌린 뒤 볶아 전복잠발라야를 만든다.\r\n\r\n13. 전복잠발라야를 접시에 담는다.\r\n\r\n14. 대파 흰 부분을 가늘게 채 썬 뒤, 찬물에 헹궈 아린 맛을 뺀다.\r\n\r\n15. 전복잠발라야 위에 파채를 올려 마무리한다.'),
	(448, '전복 미씽유♬', NULL, 1, '1. 끓는 물에 전복을 삶는다.\r\n\r\n2. 달걀을 80도의 물에 10분간 담가 온천달걀을 만든다.\r\n\r\n3. 김을 가늘게 채 썬다.\r\n\r\n4. 양파, 마늘, 새우, 둥근 호박, 부추를 적당한 크기로 손질한다.\r\n\r\n5. 삶은 전복은 내장과 살을 분리한다.\r\n\r\n6. 핸드블렌더에 전복내장과 청주를 넣고 갈아 전복내장소스를 만든다.\r\n\r\n7. 전복살을 적당한 크기로 썬다.\r\n\r\n8. 소금을 넣은 끓는 물에 파스타면을 삶는다.\r\n\r\n9. 기름을 두른 팬에 손질한 전복, 새우, 둥근 호박, 양파, 소금, 후추를 넣고 볶는다.\r\n\r\n10. 볶던 재료에 전복내장소스와 간장을 넣고 볶는다.\r\n\r\n11. 삶은 파스타면을 재료를 볶던 팬에 넣고 함께 볶는다\r\n\r\n12. 전복파스타에 조미료와 버터를 넣고 볶는다.\r\n\r\n13. 전복파스타에 부추를 넣고 볶는다.\r\n\r\n14. 전복파스타를 접시에 담고, 채 썬 김을 올린다.\r\n\r\n15. 전복파스타 위에 온천달걀을 올려 마무리한다.'),
	(449, '복수전', NULL, 1, '1. 옥수수 통조림을 체에 밭쳐 건더기만 걸러낸다.\r\n\r\n2. 옥수수에 달걀, 맛술, 소금을 넣고 섞는다.\r\n\r\n3. 기름을 두른 팬에 링 모양으로 썬 양파를 올리고, 옥수수달걀물을 채워 전을 굽는다.\r\n\r\n4. 3 안에 작은 링 모양의 양파를 넣고 굽는다.\r\n\r\n5. 옥수수양파전을 뒤집어 양면으로 굽는다.\r\n\r\n6. 토마토에 뜨거운 물을 부어 데친 뒤, 껍질을 벗겨내고 잘게 다진다.\r\n\r\n7. 양파, 마늘, 대파를 잘게 다진다.\r\n\r\n8. 기름을 두른 팬에 잘게 다진 토마토, 양파, 마늘, 대파를 넣고 볶는다.\r\n\r\n9. 사과는 껍질을 벗기고 핸드블렌더로 간다.\r\n\r\n10. 곱게 간 사과를 팬에 넣고, 볶던 재료와 함께 볶는다.\r\n\r\n11. 볶던 재료에 매실액, 케첩, 레몬즙을 넣어 토마토사과소스를 만든다.\r\n\r\n12. 옥수수를 곱게 간다.\r\n\r\n13. 곱게 간 옥수수에 옥수수알갱이와 전분을 넣고 치대 반죽을 만든다.\r\n\r\n14. 옥수수반죽으로 완자를 빚어 기름에 튀긴다.\r\n\r\n15. 옥수수완자튀김을 체에 밭쳐 기름기를 뺀다.\r\n\r\n16. 마른 팬에 설탕을 녹인다.\r\n\r\n17. 녹인 설탕에 옥수수완자튀김을 버무려 옥수수맛탕을 만든다.\r\n\r\n18. 토마토사과소스에 전분물, 소금, 후추를 넣는다.\r\n\r\n19. 토마토사과소스를 접시에 담고 옥수수양파전을 적당한 크기로 잘라 올린다.\r\n\r\n20. 다른 접시에 옥수수맛탕을 담고 차가운 물을 곁들여낸다.'),
	(450, '신데렐라 피자', NULL, 1, '1. 단호박을 반으로 잘라 씨를 빼내고 껍질을 벗겨낸다.\r\n\r\n2. 단호박 속을 가늘게 채 썬다.\r\n\r\n3. 채 썬 단호박에 즉석밥을 넣고 섞는다.\r\n\r\n4. 기름을 두른 팬에 단호박밥을 눌러 굽다가 전분을 뿌리고,\r\n동그랗게 모양을 잡아 단호박도우를 만든다.\r\n\r\n5. 끓는 기름에 소금을 넣고, 적당한 크기로 썬 삼겹살을 튀긴다.\r\n\r\n6. 튀긴 삼겹살을 체에 밭쳐 기름기를 뺀다.\r\n\r\n7. 토마토, 사과를 깍둑썰기 한다.\r\n\r\n8. 딸기를 적당한 크기로 썬다.\r\n\r\n9. 손질한 토마토, 사과, 딸기에 소금, 시리얼, 요거트를 넣고 섞어 과일샐러드를 만든다.\r\n\r\n10. 기름을 두른 팬에 고추, 느타리버섯, 옥수수 통조림을 볶다가 크림수프분말을 넣는다.\r\n\r\n11. 10에 물을 부어 가며 끓여 크림소스를 만든다.\r\n\r\n12. 단호박도우를 접시에 담는다.\r\n\r\n13. 기름을 두른 팬에 적당한 크기로 썬 토마토를 굽는다.\r\n\r\n14. 단호박도우 위에 크림소스>구운 토마토>크림소스를 차례로 올린다.\r\n\r\n15. 14를 토치로 직화한다.\r\n\r\n16. 과일샐러드를 곁들인다.'),
	(451, '불량고기', NULL, 1, '1. 기름을 두른 그릴팬에 적당한 크기로 썬 양파와 파프리카, 느타리버섯을 굽는다.\r\n\r\n2. 간장, 설탕, 맛술, 다진 마늘, 깨, 참기름을 섞어 불고기양념을 만든다.\r\n\r\n3. 불고기양념에 적당한 크기로 썬 대파와 양파를 버무린다.\r\n\r\n4. 불고기양념에 매실액, 소금, 후추를 넣고, 돼지고기 뒷다리살을 재운다.\r\n\r\n5. 방울토마토를 반으로 잘라 올리브오일을 뿌리고 소금, 후추로 간한다.\r\n\r\n6. 소금, 후추로 간을 한 방울토마토를 예열한 오븐에 굽는다.\r\n\r\n7. 잘게 다진 마늘에 간장, 레몬즙, 설탕, 들기름, 올리브오일을 섞어 드레싱을 만든다.\r\n\r\n8. 기름을 두른 팬에 재워둔 불고기를 볶는다.\r\n\r\n9. 즉석밥 위에 토마토를 얹고 올리브오일을 뿌린 뒤, 랩으로 감싸 전자레인지에 돌린다.\r\n\r\n10. 오븐에 구운 방울토마토를 토치로 직화한다.\r\n\r\n11. 토마토를 얹고 익힌 즉석밥에 올리브오일을 뿌린 뒤,\r\n구운 방울토마토, 불고기 양념을 넣고 섞는다.\r\n\r\n12. 그릇에 토마토밥, 구운 채소를 담고 드레싱을 뿌린다.\r\n\r\n13. 그릇에 불고기를 담고 드레싱을 뿌린 뒤, 얇게 썬 대파를 올린다.\r\n\r\n14. 후추와 깨를 뿌려 마무리한다.'),
	(452, '밥피자 심쿵해♬', NULL, 1, '1. 마른 팬에 즉석밥을 눌러 누룽지를 굽는다.\r\n\r\n2. 팬에 두유, 고추냉이, 설탕, 버터를 넣고 끓여 두유고추냉이소스를 만든다.\r\n\r\n3. 적당한 크기로 썬 돼지목심에 유자청을 넣고 섞는다.\r\n\r\n4. 3에 다진 마늘, 다진 생강, 된장, 맛술, 간장, 참기름을 넣고 버무린다.\r\n\r\n5. 양파를 얇게 썬 뒤, 찬물에 담가둔다.\r\n\r\n6. 파프리카, 대파를 잘게 썰어 올리브오일, 레몬즙, 후추를 넣고 드레싱을 만든다.\r\n\r\n7. 기름을 두른 팬에 양념한 돼지목심을 볶는다.\r\n\r\n8. 누룽지를 뒤집어 양면을 바삭하게 굽는다.\r\n\r\n9. 토마토를 얇게 썬 뒤, 돌돌 말아 꽃모양을 만든다.\r\n\r\n10. 누룽지를 4등분 한 뒤, 접시에 담는다.\r\n\r\n11. 누룽지 위에 찬물에 담가뒀던 양파를 올리고 두유고추냉이 소스를 뿌린다.\r\n\r\n12. 11 위에 볶은 돼지고기> 토마토장미> 후추> 두유고추냉이소스를 뿌려 완성한다.'),
	(453, '뚝닭뚝닭', NULL, 1, '1. 마늘은 슬라이스 하고, 대파는 적당한 크기로 썬다.\n\n2. 양파와 깻잎을 채 썬다.\n\n3. 고추와 파프리카를 적당한 크기로 썬다.\n\n4. 마늘을 으깬다.\n\n5. 닭안심을 얇게 슬라이스 한다.\n\n6. 슬라이스한 닭안심에 굴소스, 생강즙, 청주를 섞어 재운다.\n\n7. 기름을 두른 팬에 으깬 마늘과 대파 흰 부분을 넣고 볶는다.\n\n8. 볶던 재료에 간장과 청주, 손질한 양파와 파프리카, 굴소스를 넣고 볶는다.\n\n9. 달궈둔 뚝배기에 볶은 채소, 양념한 닭안심, 채 썬 깻잎을 순서대로 담는다.\n\n10. 끓는 기름에 슬라이스한 마늘과 대파, 고추를 넣고 끓인다.\n\n11. 10에 베트남고추를 넣고 끓인다.\n\n12. 채소와 닭안심이 담긴 뚝배기에 끓는 채소기름을 넣고 뚜껑을 덮어 익힌다.\n\n13. 다진 마늘, 간장, 식초, 굴소스를 섞어 양념장을 만들어 따로 낸다.'),
	(454, '복된 짜장', NULL, 1, '1. 밀가루에 물을 넣고 치대어 면 반죽을 한다.\r\n\r\n2. 면 반죽을 휴지시킨다.\r\n\r\n3. 양파를 적당한 크기로 썬다.\r\n\r\n4. 냉동만두소를 꺼내 잘게 다진다.\r\n\r\n5. 고추를 잘게 썬다.\r\n\r\n6. 오이소박이를 물에 씻어 가늘게 채 썬다.\r\n\r\n7. 채 썬 오이소박이에 고추기름, 식초, 설탕을 넣고 섞어 자차이를 만든다.\r\n\r\n8. 기름에 된장을 볶는다.\r\n\r\n9. 기름을 두른 팬에 만두소, 다진 생강, 고량주, 손질한 양파, 잘게 썬 고추를 넣고 볶는다.\r\n\r\n10. 볶던 재료에 기름에 볶은 된장과 굴소스를 넣고 볶아 된장짜장소스를 만든다.\r\n\r\n11. 휴지시킨 면 반죽을 치대다가 밀대로 얇게 민다.\r\n\r\n12. 면을 적당한 두께로 썰어 끓는 물에 삶는다.\r\n\r\n13. 볶던 된장짜장소스에 설탕을 넣는다.\r\n\r\n14. 그릇에 된장짜장소스를 담는다.\r\n\r\n15. 삶은 면을 찬물에 헹구고 다시 한 번 뜨거운 물에 담갔다가 따로 담아낸다.'),
	(455, '명풍튀김', NULL, 1, '1. 잘게 다진 대파에 다진 마늘, 땅콩버터, 고추기름을 넣고 섞는다.\r\n\r\n2. 1에 콜라, 물엿, 마요네즈, 식초, 간장을 넣고 소스를 만든다.\r\n\r\n3. 볼에 참치 통조림, 밀가루, 전분을 넣고 반죽한다.\r\n\r\n4. 명란젓을 적당한 크기로 썬다.\r\n\r\n5. 기름을 두른 팬에 달걀프라이를 만든다.\r\n\r\n6. 참치반죽 안에 명란젓을 넣고 감싸 동그랗게 모양을 잡는다.\r\n\r\n7. 6을 기름에 튀겨 명란참치튀김을 만든다.\r\n\r\n8. 명란참치튀김을 건져 기름기를 뺀다.\r\n\r\n9. 그릇에 상추와 깻잎을 담는다.\r\n\r\n10. 접시에 달걀프라이, 명란참치튀김, 소스를 담아낸다.'),
	(456, '냉볶이', NULL, 1, '1. 밀가루에 뜨거운 물을 넣고 익반죽한다.\r\n\r\n2. 밀가루반죽을 길게 밀어 떡모양을 만든 뒤, 뜨거운 물에 삶는다.\r\n\r\n3. 해물볶음고추장에 레몬즙, 굴소스, 참기름, 설탕을 넣고 섞어 떡볶이소스를 만든다.\r\n\r\n4. 끓는 물에 양념게장을 넣고 끓인다.\r\n\r\n5. 게장국에 달걀물을 넣는다.\r\n\r\n6. 볼에 밀가루, 얇게 썬 대파, 물에 씻은 새우젓, 물을 넣고 섞어 새우젓튀김반죽을 만든다.\r\n\r\n7. 새우젓튀김반죽을 적당한 크기로 덜어 기름에 튀긴다.\r\n\r\n8. 깻잎을 가늘게 채 썬다.\r\n\r\n9. 익힌 떡을 얼음물에 담가 식힌다.\r\n\r\n10. 볼에 잘게 찢은 상추, 떡, 떡볶이소스, 참기름을 넣고 비빈다.\r\n\r\n11. 접시에 떡볶이와 새우젓튀김을 담은 뒤, 깻잎채를 올린다.\r\n\r\n12. 게장국에 후추를 넣은 뒤, 그릇에 담아낸다.'),
	(457, '부굴부굴', NULL, 1, '1. 끓는 물에 건새우, 청양고추를 넣고 끓여 육수를 낸다.\r\n\r\n2. 두부를 적당한 크기로 썰어 속을 동그랗게 파낸다.\r\n\r\n3. 채끝등심, 새우, 바지락살, 전분, 소불고기양념, 달걀물을 핸드블렌더로 갈아 완자반죽을 만든다.\r\n\r\n4. 완자반죽을 동그랗게 빚어 속을 파낸 두부에 넣는다.\r\n\r\n5. 4를 찜기에 넣고 찐다.\r\n\r\n6. 달걀물에 전분을 넣고 섞는다.\r\n\r\n7. 기름을 두른 팬에 전분을 넣은 달걀물을 넣고 익혀 달걀피를 만든다.\r\n\r\n8. 굴에 밀가루를 묻혀 달걀피 위에 올리고 반으로 접어 굴만두를 만든다.\r\n\r\n9. 액상조미료를 육수에 넣고 끓인다.\r\n\r\n10. 재료를 넣고 끓이던 육수를 체에 밭쳐 건더기를 분리한 뒤, 소금을 넣어 끓인다.\r\n\r\n11. 애호박과 새송이버섯을 채 썰어 육수에 넣고 끓인다.\r\n\r\n12. 두부완자찜과 굴만두를 그릇에 담는다.\r\n\r\n13. 12에 애호박과 새송이버섯을 넣고 끓인 육수를 붓고, 잘게 썬 대파를 올려낸다.'),
	(458, '월드스탕', NULL, 1, '1. 끓는 물에 무, 건새우, 대파, 새우머리를 넣고 끓여 육수를 낸다.\r\n\r\n2. 전자레인지에 데운 즉석밥을 마른 팬에 넓게 펴서 구워 누룽지를 만든다.\r\n\r\n3. 건두부피를 가늘게 채 썬다.\r\n\r\n4. 기름을 두른 냄비에 채 썬 건두부피, 트러플 버터, 간장을 넣고 볶는다.\r\n\r\n5. 건두부피를 볶던 냄비에 새우, 굴을 넣고 함께 볶는다.\r\n\r\n6. 볶은 재료에 육수를 넣고 해물탕을 끓인다.\r\n\r\n7. 핸드블렌더에 적당한 크기로 썬 시금치와 물을 넣고 곱게 간다.\r\n\r\n8. 간 시금치를 체에 밭쳐 즙만 해물탕에 넣고 끓인다.\r\n\r\n9. 시금치해물탕에 액상조미료를 넣고 끓인다.\r\n\r\n10. 시금치해물탕에 전분물을 풀어 농도를 조절한다.\r\n\r\n11. 누룽지를 별모양 틀로 찍어 모양을 만든다.\r\n\r\n12. 별모양으로 만든 누룽지를 기름에 튀긴다.\r\n\r\n13. 그릇에 시금치해물탕을 담고 별모양으로 튀긴 누룽지를 따로 담아낸다.'),
	(459, 'I\'m 가리비 a bad boy♬', NULL, 1, '1. 핸드블렌더에 참깨와 오리엔탈드레싱을 넣고 곱게 간다.\r\n\r\n2. 1에 마요네즈와 후추를 넣고 다시 갈아 참깨소스를 만든다.\r\n\r\n3. 오징어는 껍질을 벗겨내고, 잘게 다진다.\r\n\r\n4. 기름을 두른 팬에 적당한 크기로 썬 쪽파와 시금치를 각각 굽다가 소금, 후추로 간 한다.\r\n\r\n5. 기름을 두른 팬에 잘게 다진 오징어를 볶다가 소금, 베이키드빈스,\r\n다진 마늘, 식초, 트러플 꿀, 버터를 넣고 볶는다.\r\n\r\n6. 구운 시금치를 원형틀로 모양을 잡아 접시 위에 올린다.\r\n\r\n7. 구운 쪽파를 적당한 크기로 썰어 데커레이션 한다.\r\n\r\n8. 구운 시금치 위에 오징어볶음을 얹는다.\r\n\r\n9. 기름을 두른 팬에 가리비관자를 굽다가, 소금, 후추로 간 한다.\r\n\r\n10. 접시에 참깨소스를 올린다.\r\n\r\n11. 구운 쪽파 위에 가리비관자구이를 올린다.\r\n\r\n12. 잘게 다진 부추를 올려 마무리한다.'),
	(460, '두부로 피하고 싶어서♬', NULL, 1, '1. 마늘은 얇게 슬라이스하고, 오징어는 촘촘하게 칼집을 낸 뒤 채 썬다.\r\n\r\n2. 새송이버섯, 파프리카, 양파, 애호박도 채 썬다.\r\n\r\n3. 밀가루를 물에 개어 튀김반죽을 만든다.\r\n\r\n4. 채끝등심을 채 썬 뒤, 전분과 달걀물을 묻혀 기름에 튀기듯 볶는다.\r\n\r\n5. 기름에 볶은 채끝등심을 체에 밭쳐 기름기를 뺀다.\r\n\r\n6. 기름을 두른 팬에 손질한 채소들과 오징어, 채끝등심, 설탕, 간장, 소불고기 양념을 넣고 볶는다.\r\n\r\n7. 볶던 재료에 후추를 뿌려 중국식잡채를 완성한다.\r\n\r\n8. 건두부피에 중국식잡채를 넣고 튀김반죽을 묻혀 돌돌 만다.\r\n\r\n9. 건두부피롤에 튀김반죽을 골고루 입혀 기름에 튀긴다.\r\n\r\n10. 접시에 중국식잡채를 담는다.\r\n\r\n11. 뜨거운 물에 건두부피를 데친다.\r\n\r\n12. 데친 건두부피를 접시에 담는다.\r\n\r\n13. 건두부피롤튀김을 적당한 크기로 썰어 접시에 담은 뒤, 트러플 꿀을 뿌려 마무리한다.'),
	(461, '알렉스테이크', NULL, 1, '1. 손질한 양파, 당근, 대파, 아스파라거스를 끓는 물에 넣고 채소육수를 낸다.\r\n\r\n2. 양파를 가늘게 채 썬다.\r\n\r\n3. 기름을 두른 팬에 채 썬 양파와 발사믹소스를 넣고 볶는다.\r\n\r\n4. 살치살에 올리브오일, 소금, 후추를 뿌린다.\r\n\r\n5. 기름을 두른 팬에 간을 한 살치살을 굽는다.\r\n\r\n6. 살치살을 굽는 팬에 버터를 넣고 고기에 끼얹어가며 익힌다.\r\n\r\n7. 볼에 살치살 스테이크와 로즈마리를 넣고 토치로 로즈마리에 불을 붙인 뒤,\r\n랩을 씌워 살치살 스테이크를 훈연한다.\r\n\r\n8. 팬에 양상추와 발사믹소스를 넣고 볶다가 채소육수를 넣는다.\r\n\r\n9. 8에 버터, 후추, 바닐라 아이스크림을 넣고 졸인다.\r\n\r\n10. 훈연한 살치살 스테이크에 발사믹소스에 볶은 양파와 모차렐라치즈를 올린 뒤, 오븐에 익힌다.\r\n\r\n11. 기름을 두른 팬에 적당한 크기로 썬 배와 아스파라거스를 굽는다.\r\n\r\n12. 11에 소금, 버터, 후추를 넣고, 위스키를 넣어 불을 붙인다.\r\n\r\n13. 접시에 구운 아스파라거스와 배를 올리고, 소스에 볶은 양상추를 담는다.\r\n\r\n14. 양상추 위에 살치살스테이크를 올린다.\r\n\r\n15. 소스를 두르고, 레몬껍질과 후추를 뿌려 마무리한다.'),
	(462, '둘이서 한 덮밥', NULL, 1, '1. 대파, 마늘, 양파를 핸드블렌더로 간다.\n\n2. 양배추를 가늘게 채 썬다.\n\n3. 돼지목살을 적당한 크기로 썬 뒤, 기름에 튀기듯 볶는다.\n\n4. 볶은 돼지목살을 체에 밭쳐 기름기를 뺀다.\n\n5. 돼지목살을 볶았던 기름에 다진 마늘과 채 썬 양배추, 간장, 식초를 넣고 볶는다.\n\n6. 볶은 양배추를 꺼내둔다.\n\n7. 기름을 두른 팬에 곱게 간 대파, 마늘, 양파와 고춧가루를 넣고 볶아 소스를 만든다.\n\n8. 전자레인지에 즉석밥을 돌린다.\n\n9. 볶던 소스에 돼지목살, 청주를 넣고 함께 볶는다.\n\n10. 재료를 볶던 팬에 채 썬 파프리카와 맛술을 넣고 함께 볶는다.\n\n11. 접시에 밥을 얹고, 양배추볶음을 올린 뒤, 소스와 함께 볶은 돼지목살을 올려 마무리한다.'),
	(463, '파티가 오레가노', NULL, 1, '1. 파스타면을 잘게 부순다.\r\n\r\n2. 끓는 물에 잘게 부순 파스타면을 넣고 삶는다.\r\n\r\n3. 아스파라거스를 손질한 뒤, 끓는 물에 데친다.\r\n\r\n4. 기름을 두른 팬에 데친 아스파라거스를 굽는다.\r\n\r\n5. 토마토를 적당한 크기로 썬다.\r\n\r\n6. 양상추를 채 썬다.\r\n\r\n7. 구운 아스파라거스를 적당한 크기로 썬다.\r\n\r\n8. 관자에 소금, 후추로 간 한다.\r\n\r\n9. 기름을 두른 팬에 관자를 구운 뒤, 냉동실에서 식힌다.\r\n\r\n10. 베이컨은 잘게 썰고, 마늘은 으깬다.\r\n\r\n11. 팬에 손질한 베이컨과 마늘을 넣고 볶다가, 소금으로 간 한다.\r\n\r\n12. 볶은 베이컨과 마늘을 체에 밭쳐 기름기를 뺀 뒤,\r\n레몬즙과 올리브오일, 오레가노를 넣고 소스를 만든다.\r\n\r\n13. 소스에 구운 아스파라거스, 식힌 관자를 넣는다.\r\n\r\n14. 삶은 파스타면을 체에 밭쳐 거른 뒤, 얼음물에서 식힌다.\r\n\r\n15. 파스타면을 소스에 넣는다.\r\n\r\n16. 손질한 토마토와 베이컨을 넣고 섞는다.\r\n\r\n17. 16에 액상조미료를 넣는다.\r\n\r\n18. 섞은 냉파스타를 그릇에 담고, 채 썬 양상추를 올려 마무리한다.'),
	(464, '팬타스틱', NULL, 1, '1. 아스파라거스를 적당한 크기로 손질한다.\n\n2. 양파, 베이컨을 적당한 크기로 썰어 팬에 볶는다.\n\n3. 다른 팬에 적당한 크기로 썬 소시지와 마늘을 볶다가, 맥주를 붓고 끓인다.\n\n4. 3에 손질한 아스파라거스를 넣는다.\n\n5. 맥주, 연겨자, 달걀, 팬케이크가루를 섞어 팬케이크반죽을 만든다.\n\n6. 팬케이크반죽에 모차렐라치즈를 넣는다.\n\n7. 유리냄비에 버터를 묻히고, 볶은 양파와 베이컨, 팬케이크반죽을 넣고 전자레인지에서 익힌다.\n\n8. 볶던 양파와 베이컨에 버터와 올리브오일을 넣고 다시 한 번 볶는다.\n\n9. 8에 밀가루, 맥주를 넣고 끓이다가, 액상조미료를 넣고 끓인 육수를 부어 함께 끓인다.\n\n10. 끓이던 재료에 후추를 넣어 소스를 만든다.\n\n11. 전자레인지에 익힌 팬케이크반죽을 꺼내 맥주에 끓인 소시지와\n아스파라거스를 넣고, 다시 한 번 익혀 낸다.\n\n12. 소스를 유리냄비에 부어 따로 낸다.'),
	(465, '200% 게살케이크', NULL, 1, '1. 핸드블렌더에 새우살, 버터, 소금, 후추를 넣고 곱게 간다.\r\n\r\n2. 간 새우살에 물기를 짠 대게살을 넣고 섞는다.\r\n\r\n3. 원형틀 안쪽에 버터를 바르고 새우대게살을 채운 후, 찜기에 찐다.\r\n\r\n4. 날치알을 체에 밭쳐 찬물에 헹군다.\r\n\r\n5. 당근을 럭비공모양으로 다듬어 끓는 물에 삶은 뒤 체에 밭쳐 둔다.\r\n\r\n6. 냄비에 딸기주스, 삶은 당근, 버터를 넣고 끓여 조린다.\r\n\r\n7. 냄비에 버터와 밀가루를 볶아 루를 만들고, 우유, 후추를 넣고 끓여 베샤멜소스를 만든다.\r\n\r\n8. 찜기에서 찐 새우대게살 케이크를 기름을 두른 팬에 굽는다.\r\n\r\n9. 새우대게살 케이크에 베샤멜소스를 부어 코팅한 뒤 접시에 올린다.\r\n\r\n10. 딸기주스에 조린 당근을 곁들인다.\r\n\r\n11. 새우대게살 케이크 위에 날치알을 올려 마무리한다.'),
	(466, '갈치 너비아니', NULL, 1, '1. 대파, 양파, 더덕을 석쇠에 굽는다.\r\n\r\n2. 등갈비에 칼집을 내고 전분을 묻혀 기름에 튀기다가 설탕을 넣는다.\r\n\r\n3. 냄비에 설탕, 명이나물장아찌국물, 석쇠에 구운 채소를 넣고 끓인 뒤, 체에 밭쳐 국물만 걸러낸다.\r\n\r\n4. 걸러낸 국물을 다시 한 번 끓여 명이나물소스를 만든다.\r\n\r\n5. 갈치를 포 뜬 뒤, 전분을 묻힌다.\r\n\r\n6. 통조림 햄을 얇게 썰어 전분을 묻힌다.\r\n\r\n7. 튀긴 등갈비를 꺼내 살만 바른 뒤, 잘게 다진다.\r\n\r\n8. 잘게 다진 등갈비살에 전분을 뿌린다.\r\n\r\n9. 갈치포와 통조림 햄 사이에 등갈비살을 넣고 샌드해 너비아니를 만든다.\r\n\r\n10. 기름을 두른 팬에 너비아니를 굽는다.\r\n\r\n11. 믹서기에 참나물, 올리브오일, 케일사과주스를 넣고 갈아 참나물소스를 만든다.\r\n\r\n12. 핸드블렌더에 배, 고추장, 도라지배즙을 넣고 갈아 고추장소스를 만든다.\r\n\r\n13. 너비아니에 고추장소스를 바른다.\r\n\r\n14. 콩비지를 전자레인지에 데운 뒤, 접시에 올린다.\r\n\r\n15. 너비아니를 접시에 얹는다.\r\n\r\n16. 참나물소스와 명이나물소스를 뿌린다.\r\n\r\n17. 참나물을 얹은 뒤, 올리브오일을 뿌려 마무리한다.'),
	(467, '케이밥스타', NULL, 1, '1. 오징어와 새우살을 잘게 다진 뒤, 기름을 두른 팬에 볶는다.\r\n\r\n2. 밥에 수제양념장과 볶은 해산물을 넣고 섞는다.\r\n\r\n3. 양념한 밥으로 주먹밥을 만든 뒤, 명이나물장아찌로 감싼다.\r\n\r\n4. 전분, 식용유, 물을 섞어 튀김옷을 만든다.\r\n\r\n5. 명이나물주먹밥에 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n6. 견과류와 건크랜베리를 잘게 부순 뒤, 크림치즈와 섞는다.\r\n\r\n7. 팬에 발사믹소스, 딸기주스, 물을 넣고 끓여 딸기발사믹소스를 만든다.\r\n\r\n8. 끓는 물에 콘수프분말을 녹인다.\r\n\r\n9. 콘수프에 후추, 파르메산치즈, 달걀노른자를 넣고 섞어 콘수프소스를 만든다.\r\n\r\n10. 접시에 콘수프소스를 올리고, 튀긴 명이나물주먹밥을 올린다.\r\n\r\n11. 명이나물주먹밥 위에 견과류와 섞은 크림치즈를 올린다.\r\n\r\n12. 딸기발사믹소스와 콘수프소스를 뿌려 마무리한다.'),
	(468, '레이면인건가♬', NULL, 1, '1. 기름을 두른 팬에 채 썬 양파를 볶는다.\r\n\r\n2. 끓는 물에 라면과 다시마를 넣고 삶는다.\r\n\r\n3. 볶던 양파에 손질한 오징어, 레몬즙, 잘게 부순 해시브라운, 소금, 후추, 자숙새우를 넣고 볶는다.\r\n\r\n4. 삶은 라면을 체에 밭쳐 물기를 뺀 뒤 라면수프, 전분을 넣고 섞는다.\r\n\r\n5. 기름을 두른 팬에 양념한 라면을 넓게 펴서 오코노미야키를 굽는다.\r\n\r\n6. 카망베르치즈를 적당한 크기로 썬 뒤, 굽던 오코노미야키 위에 올린다.\r\n\r\n7. 6 위에 볶은 해산물>양념한 라면>달걀을 올려 굽다가, 뒤집어가며 굽는다.\r\n\r\n8. 오코노미야키의 달걀을 올린 부분에 굴소스와 케첩을 섞은 소스를 바르고,\r\n파르메산 치즈를 뿌려 굽다가 뒤집는다.\r\n\r\n9. 구운 오코노미야키을 접시에 담은 뒤, 달걀을 올리지 않은 부분에\r\n케첩&굴소스를 바르고 잘게 다진 영양부추를 뿌린다.\r\n\r\n10. 오코노미야키 위에 마요네즈를 바르고, 가쓰오부시를 뿌린다.\r\n\r\n11. 10 위에 달걀노른자를 올린 뒤, 토치로 직화한다.\r\n\r\n12. 잘게 다진 영양부추를 뿌려 마무리한다.'),
	(469, '시금치 나무에서 애벌레 쇼를♬', NULL, 1, '1. 전자레인지에 즉석밥을 돌린다.\r\n\r\n2. 양파를 채 썬다.\r\n\r\n3. 밥에 굴소스를 넣는다.\r\n\r\n4. 기름을 두른 팬에 다진 마늘, 채 썬 양파, 시금치, 소금, 물을 넣고 끓이다가\r\n핸드블렌더로 곱게 갈아 시금치소스를 만든다.\r\n\r\n5. 시금치소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n6. 시금치소스에 치킨스톡을 넣는다.\r\n\r\n7. 굴소스를 넣은 밥에 딸기고추장, 마요네즈를 넣고 섞는다.\r\n\r\n8. 베이컨용 삼겹살 위에 양념한 밥을 올리고 돌돌 만다.\r\n\r\n9. 마른 팬에 삼겹살말이를 올린 뒤, 뚜껑을 닫은 채로 굽는다.\r\n\r\n10. 삼겹살말이를 토치로 직화한다.\r\n\r\n11. 기름을 두른 팬에 두반장, 굴소스, 채 썬 양파, 숙주나물, 식초를 넣고 볶는다.\r\n\r\n12. 시금치소스를 체에 밭쳐 곱게 거른다.\r\n\r\n13. 접시에 시금치소스로 나무를 그린다.\r\n\r\n14. 시금치 나무 위에 삼겹살말이를 올린다.\r\n\r\n15. 그릇에 숙주볶음을 담고, 삼겹살말이를 올려 마무리한다.'),
	(470, '신의 물고기', NULL, 1, '1. 식빵테두리를 잘라내고 적당한 크기로 썬 뒤, 핸드블렌더로 곱게 갈아 빵가루를 만들어 튀긴다.\r\n\r\n2. 마늘을 다진다.\r\n\r\n3. 다진 마늘을 튀긴다.\r\n\r\n4. 기름기를 뺀 튀긴 빵가루와 튀긴 마늘에 후추, 우스터소스를 섞는다.\r\n\r\n5. 삼치를 손질해 전분을 묻힌다.\r\n\r\n6. 냄비에 청주와 진을 넣고, 전분을 묻힌 삼치 절반을 넣은 뒤 불을 붙여 익힌다.\r\n\r\n7. 전분을 묻힌 삼치 남은 절반을 튀긴다.\r\n\r\n8. 튀긴 삼치와 술에 익힌 삼치를 각각 건져내 한 김 식힌다.\r\n\r\n9. 볼에 간장, 설탕, 레몬즙, 물을 넣고 섞어 초절임소스를 만든다.\r\n\r\n10. 오이와 셀러리를 가늘게 채 썰어 그릇에 담고, 초절임소스를 붓는다.\r\n\r\n11. 접시에 튀긴 삼치와 술에 익힌 삼치를 접시에 담는다.\r\n\r\n12. 술에 익힌 삼치 위에 명란젓을 올리고, 튀긴 마늘빵가루를 뿌린다.\r\n\r\n13. 튀긴 삼치 위에 튀긴 마늘빵가루를 뿌린다.\r\n\r\n14. 잘게 부순 감자칩을 올려 마무리한다.'),
	(471, '시금치~즈', NULL, 1, '1. 소금을 넣은 끓는 물에 시금치를 데친다.\r\n\r\n2. 소고기등심을 두드려 얇게 편 뒤, 적당한 크기로 잘라 소금으로 간 한다.\r\n\r\n3. 기름을 두른 팬에 손질한 소고기등심과 채 썬 양파를 볶는다.\r\n\r\n4. 데친 시금치를 체에 밭쳐 물기를 뺀다.\r\n\r\n5. 냄비에 적당한 크기로 썬 시금치, 크림치즈, 체다치즈, 소금, 후추, 우유를 넣고 끓인다.\r\n\r\n6. 달걀과 밀가루, 우유를 섞어 크레이프 반죽을 만든다.\r\n\r\n7. 팬에 반죽을 얇게 부어 크레이프를 굽는다.\r\n\r\n8. 적당한 크기로 썬 로메인상추에 레몬즙, 오이, 올리브오일, 소금을 섞어 샐러드를 만든다.\r\n\r\n9. 두 개의 접시에 각각 샐러드를 담는다.\r\n\r\n10. 크레이프 위에 크림치즈, 양파와 함께 볶은 등심을 올려 감싼다.\r\n\r\n11. 10 위에 모차렐라치즈를 올린 뒤, 오븐에 익힌다.\r\n\r\n12. 구운 크레이프에 치즈와 함께 끓인 시금치를 올려 감싸고 사과로 고정한다.\r\n\r\n13. 샐러드가 담긴 접시 위에 시금치 치즈 크레이프를 올린다.\r\n\r\n14. 샐러드가 담긴 다른 접시에 등심 치즈 크레이프와 레몬을 올려 마무리한다.'),
	(472, '미소 밀푀유', NULL, 1, '1. 냄비에 우유, 옥수수를 넣고 끓인다.\r\n\r\n2. 손질한 양배추에 소금을 뿌린 뒤, 팬에 굽는다.\r\n\r\n3. 소고기등심을 반으로 갈라 넓게 편 뒤, 소금, 후추로 간 하고 기름을 두른 팬에 굽는다.\r\n\r\n4. 구운 양배추를 한 김 식힌다.\r\n\r\n5. 믹서기에 우유에 끓인 옥수수, 크림치즈를 넣고 갈아 퓌레를 만든다.\r\n\r\n6. 간 퓌레를 체에 밭쳐 곱게 거른 뒤, 적미소를 넣고 섞는다.\r\n\r\n7. 감자칼로 셀러리를 얇게 벗겨 얼음물에 담가둔다.\r\n\r\n8. 기름을 두른 팬에 잘게 다진 양파를 볶다가 흑초를 넣고 끓여 흑초소스를 만든다.\r\n\r\n9. 다른 팬에 버터를 녹인 뒤, 밀가루를 넣고, 흑초소스를 부어 끓인다.\r\n\r\n10. 구운 등심>미소옥수수퓌레>구운 양배추>구운 등심>미소옥수수퓌레>구운 양배추 순으로 쌓는다.\r\n\r\n11. 10을 꼬지로 고정한 뒤, 적당한 크기로 썰어 접시에 담는다.\r\n\r\n12. 미소 밀푀유 위에 셀러리를 올린다.\r\n\r\n13. 흑초소스를 한 김 식힌 뒤, 씨겨자, 올리브오일, 소금, 후추를 넣고 섞는다.\r\n\r\n14. 미소 밀푀유에 흑초소스를 뿌려 마무리한다.'),
	(473, 'everyday I 스테이크♬', NULL, 1, '1. 간장, 청주, 맛술, 설탕을 1:1:1:0.8 비율로 섞어 고기양념을 만든다.\r\n\r\n2. 소고기등심을 고기양념에 재워둔다.\r\n\r\n3. 끓는 물에 치킨스톡, 멸치액젓을 넣고 끓여 국물을 만든다.\r\n\r\n4. 끓이던 국물에 게맛살을 잘게 찢어 넣고, 후추를 뿌린다.\r\n\r\n5. 4에 달걀물과 전분물을 넣는다.\r\n\r\n6. 팬에 올리브오일과 식용유를 뿌린 뒤, 채 썬 양파를 볶다가 소금, 후추로 간 한다.\r\n\r\n7. 팬 한 편에서 양념에 재워둔 소고기를 굽는다.\r\n\r\n8. 7에 고기양념을 적당량 붓고, 졸인다.\r\n\r\n9. 볶은 양파를 건져낸다.\r\n\r\n10. 즉석밥을 전자레인지에 돌린다.\r\n\r\n11. 사과는 껍질을 깎고 잘게 썬다.\r\n\r\n12. 밥에 잘게 썬 사과를 넣고 섞는다.\r\n\r\n13. 사과밥에 레몬즙을 넣는다.\r\n\r\n14. 구운 소고기스테이크를 석쇠 위에 올리고, 토치로 직화한다.\r\n\r\n15. 그릇에 사과밥과 볶은 양파를 담고, 크림치즈를 올린다.\r\n\r\n16. 적당한 크기로 썬 소고기스테이크를 얹고, 레몬껍질을 갈아 올린다.\r\n\r\n17. 다른 그릇에 게살수프를 담아 함께 낸다.'),
	(474, '숲 속의 완자님', NULL, 1, '1. 양파, 대파, 당근을 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 냄비에 적당한 크기로 썬 채소와 버터를 넣고 볶는다.\r\n\r\n3. 볶던 채소에 뜨거운 물을 붓고 끓이다가, 적당한 크기로 썬 통조림 햄을 넣는다.\r\n\r\n4. 파스타면을 적당한 크기로 잘라 넣고 끓인다.\r\n\r\n5. 끓이던 재료에 치킨스톡, 파슬리가루, 후추를 넣고 끓여 햄누들수프를 만든다.\r\n\r\n6. 인스턴트 만두의 소만 발라낸다.\r\n\r\n7. 핸드블렌더로 만두소와 두부를 곱게 간다.\r\n\r\n8. 5를 적당한 크기로 뭉쳐 완자를 빚고, 전분을 묻힌다.\r\n\r\n9. 완자를 기름에 튀긴 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n10. 기름기를 뺀 튀김완자를 다시 한 번 튀긴 뒤, 건져서 기름기를 뺀다.\r\n\r\n11. 땅콩버터, 매실청, 스위트칠리소스, 레몬즙을 섞어 땅콩칠리소스를 만든다.\r\n\r\n12. 튀김완자에 땅콩칠리소스를 묻힌다.\r\n\r\n13. 끓이던 햄누들수프에 레몬즙을 뿌리고, 그릇에 담는다.\r\n\r\n14. 햄누들수프 위에 튀김완자를 올려 마무리한다.'),
	(475, '미식이 오믈렛', NULL, 1, '1. 기름을 두른 팬에 적당한 크기로 썬 삼겹살을 볶는다.\r\n\r\n2. 핸드블렌더에 달걀, 소금, 두부를 넣고 곱게 간다.\r\n\r\n3. 기름을 두른 팬에 2를 붓고, 뚜껑을 닫아 익혀 달걀지단을 만든다.\r\n\r\n4. 양파를 가늘게 채 썬다.\r\n\r\n5. 볶던 삼겹살에 양파채, 후추, 스테이크시즈닝을 넣고 함께 볶는다.\r\n\r\n6. 5에 레드와인, 고추장을 넣고 볶는다.\r\n\r\n7. 볼에 으깬 마늘과 간장을 넣고, 적당한 크기로 썬 두부를 담가 재워둔다.\r\n\r\n8. 믹서기로 견과류를 곱게 간다.\r\n\r\n9. 견과류가루와 파르메산치즈가루를 섞어둔다.\r\n\r\n10. 볶던 삼겹살에 파슬리가루를 넣는다.\r\n\r\n11. 10에 적당한 크기로 썬 사과와 치즈견과가루를 넣는다.\r\n\r\n12. 간장에 재워둔 두부에 밀가루>달걀물>치즈견과가루를 차례로 묻혀 기름에 튀긴다.\r\n\r\n13. 달걀지단을 깐 그릇에 양념해 볶은 삼겹살을 담고, 접시에 뒤집어 올려 오믈렛 모양을 낸다.\r\n\r\n14. 오믈렛이 담긴 접시에 두부튀김을 올린다.\r\n\r\n15. 알타리무김치를 곁들이고, 치즈견과가루를 뿌려 마무리한다.'),
	(476, '꿀먹었삼', NULL, 1, '1. 끓는 기름에 고구마를 통째로 튀긴다.\r\n\r\n2. 소금을 넣은 끓는 물에 양배추를 삶는다.\r\n\r\n3. 즉석밥을 전자레인지에 돌린다.\r\n\r\n4. 삼겹살에 촘촘하게 칼집을 낸다.\r\n\r\n5. 마른 팬에 칼집 낸 삼겹살을 굽는다.\r\n\r\n6. 핸드블렌더에 꿀생강청, 굴소스, 인스턴트커피, 사과를 넣고 갈아 꿀사과소스를 만든다.\r\n\r\n7. 구운 삼겹살을 꺼내서 한 쪽 면에 꿀사과소스를 바른다.\r\n\r\n8. 꿀사과소스를 바른 삼겹살을 다시 한 번 굽는다.\r\n\r\n9. 튀긴 고구마를 석쇠 위에 올리고, 토치로 직화한다.\r\n\r\n10. 군고구마의 껍질을 벗겨 한 김 식힌다.\r\n\r\n11. 알타리무장아찌를 강판에 간다.\r\n\r\n12. 김발 위에 삶은 양배추> 밥>슬라이스한 군고구마>곱게 간 알타리무장아찌를 올리고 돌돌 만다.\r\n\r\n13. 돌돌 만 김발을 고정한 뒤, 찜기에 찐다.\r\n\r\n14. 꿀사과소스와 곱게 간 알타리무장아찌, 굴소스, 설탕, 식초를 섞어 알타리무소스를 만든다.\r\n\r\n15. 찜기에 찐 양배추롤을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n16. 양배추롤 위에 꿀사과삼겹살을 올린다.\r\n\r\n17. 알타리무소스를 뿌려 마무리한다.'),
	(477, '에라 모르겠닭♬', NULL, 1, '1. 팬에 기름을 넉넉히 두르고, 다진 마늘을 볶는다.\r\n\r\n2. 다진 마늘을 볶던 팬에 대파, 양파를 잘라 넣는다.\r\n\r\n3. 볶던 채소에 된장과 고체카레, 고춧가루, 돈가스소스를 넣고 볶는다.\r\n\r\n4. 3에 굴소스를 넣고 볶는다.\r\n\r\n5. 훈제 닭가슴살을 두드려 결대로 찢는다.\r\n\r\n6. 볶던 재료에 결대로 찢은 훈제 닭가슴살을 넣고 볶다가, 달걀을 넣어 섞는다.\r\n\r\n7. 볶던 재료에 끓는 물을 붓는다.\r\n\r\n8. 접시에 밥을 담고, 닭카레를 올려 마무리한다.'),
	(478, '쉬워서 오야코', NULL, 1, '1. 김밥햄과 대파를 적당한 크기로 자른다\r\n\r\n2. 양파를 적당한 크기로 자른다.\r\n\r\n3. 팬에 올리브오일을 넉넉히 두르고, 건고추와 손질한 대파, 양파, 통마늘을 넣고 끓여 고추기름을 낸다.\r\n\r\n4. 고추기름과 건고추를 덜어내고, 남은 채소에 손질한 김밥햄과 소금, 후추를 넣고 볶는다.\r\n\r\n5. 볶던 재료에 간장, 맛술, 물, 설탕, 굴소스를 넣고 끓인다.\r\n\r\n6. 그릇에 달걀노른자와 밥을 넣고 잘 섞어준 뒤, 참기름, 깨, 고추기름을 넣는다.\r\n\r\n7. 끓이던 재료에 간장, 달걀을 넣고 섞는다.\r\n\r\n8. 7을 달걀밥 위에 올린 뒤, 고추기름과 깨를 뿌린다.\r\n\r\n9. 대파를 잘게 썰어 올려 마무리한다.'),
	(479, '브레드 피그', NULL, 1, '1. 팬에 버터를 녹인다.\r\n\r\n2. 두 개의 볼에 밀가루와 소금을 넣고, 하나는 뜨거운 물을 부어 익반죽 한다.\r\n\r\n3. 또 다른 하나에는 뜨거운 물과 녹인 버터를 넣어 익반죽한다.\r\n\r\n4. 물반죽과 기름반죽을 숙성시킨다.\r\n\r\n5. 물반죽과 기름반죽을 겹쳐 여러 번 접고, 밀대로 미는 과정을 반복한다.\r\n\r\n6. 마른 팬에 적당한 크기로 자른 반죽을 올리고 뚜껑을 닫아 굽는다.\r\n\r\n7. 삼겹살을 편으로 썬다.\r\n\r\n8. 편으로 썬 삼겹살에 전분, 달걀물을 묻힌다.\r\n\r\n9. 넉넉한 기름을 두른 팬에 손질한 삼겹살을 넣고 튀기듯 볶는다.\r\n\r\n10. 양파, 대파, 청양고추를 적당한 크기로 썬다.\r\n\r\n11. 볶은 삼겹살을 건져 기름기를 뺀다.\r\n\r\n12. 삼겹살을 볶던 팬에 다진 마늘, 양파, 대파, 청양고추를 볶다가 간장, 후추를 넣는다.\r\n\r\n13. 채소를 볶던 팬에 볶은 삼겹살을 더해 함께 볶는다.\r\n\r\n14. 구운 빵을 반으로 갈라 접시에 담는다.\r\n\r\n15. 다른 접시에 회과육을 담아낸다.'),
	(480, '햄카츄~ 야키소바츄~♬', NULL, 1, '1. 믹서기에 시리얼을 곱게 간다.\r\n\r\n2. 라면 면을 삶는다.\r\n\r\n3. 양파, 당근, 김밥햄, 청양고추를 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 팬에 손질한 채소와 김밥햄을 볶다가, 게맛살을 넣고 함께 볶는다.\r\n\r\n5. 삶은 면을 찬물에 헹군 뒤, 재료를 볶던 팬에 넣고 함께 볶아 야키소바를 만든다.\r\n\r\n6. 야키소바에 전분, 돈가스소스, 굴소스를 넣어 양념한다.\r\n\r\n7. 비닐백에 마요네즈, 돈가스소스, 케첩을 넣고 모서리에 구멍을 낸다.\r\n\r\n8. 밀가루와 달걀물을 섞어 튀김옷을 만든다.\r\n\r\n9. 적당한 크기로 썬 김밥햄에 전분을 묻힌 뒤, 튀김옷을 입힌다.\r\n\r\n10. 튀김옷을 입힌 김밥햄에 시리얼가루를 묻힌다.\r\n\r\n11. 10을 기름에 튀겨 햄카츠를 만든 뒤, 기름기를 뺀다.\r\n\r\n12. 접시에 야키소바를 담는다.\r\n\r\n13. 팬에 버터를 녹인 뒤, 달걀물을 부어 오믈렛을 만든다.\r\n\r\n14. 야키소바 위에 오믈렛을 올린다.\r\n\r\n15. 오믈렛 위에 돈가스소스>마요네즈>케첩을 순서대로 뿌린다.\r\n\r\n16. 햄카츠를 곁들인 뒤, 케첩을 올려 마무리한다.'),
	(481, '비상식난', NULL, 1, '1. 끓는 물에 소금, 밀가루를 붓고 잘 섞어가며 익반죽 한다.\r\n\r\n2. 다른 팬에 통조림 참치, 통조림 햄을 넣고 으깬다.\r\n\r\n3. 2에 통조림 옥수수를 넣고 볶는다.\r\n\r\n4. 볶던 재료에 설탕을 넣고, 물을 붓고 끓여 소스를 만든다.\r\n\r\n5. 마른 팬에 밀가루를 두르고, 적당한 크기로 떼어낸 반죽을 납작하게 굽는다.\r\n\r\n6. 끓이던 소스에 밀가루물을 넣어 농도를 맞춘다.\r\n\r\n7. 소스를 그릇에 담는다.\r\n\r\n8. 접시에 난을 담아 마무리한다.'),
	(482, '죽지 않아~!', NULL, 1, '1. 쌀을 곱게 으깬다.\r\n\r\n2. 끓는 물에 김, 으깬 쌀을 넣고 죽을 끓인다.\r\n\r\n3. 끓이던 죽에 통조림 옥수수를 넣는다.\r\n\r\n4. 마른 팬에 통조림 번데기를 볶은 뒤, 으깬다.\r\n\r\n5. 으깬 번데기를 죽에 넣고 끓인다.\r\n\r\n6. 통조림 햄을 적당한 크기로 썬다.\r\n\r\n7. 마른 팬에 손질한 햄을 굽다가, 위스키를 넣어 플람베한다.\r\n\r\n8. 익힌 햄에 골뱅이 통조림의 국물만 넣어 조린다.\r\n\r\n9. 끓이던 죽에 참기름을 넣는다.\r\n\r\n10. 햄장조림을 그릇에 담아낸다.\r\n\r\n11. 죽을 함께 낸다.'),
	(483, '굿럭탕', NULL, 1, '1. 끓는 물에 대추, 장뇌삼, 수삼을 우려낸다.\r\n\r\n2. 다른 냄비에 전복을 삶는다.\r\n\r\n3. 우럭을 손질한 뒤, 살만 적당한 크기로 썬다.\r\n\r\n4. 핸드블렌더에 우럭살, 달걀흰자, 전분, 올리브오일, 청주, 소금을 넣고 곱게 간다.\r\n\r\n5. 양파, 당근을 잘게 썬다.\r\n\r\n6. 잘게 썬 채소에 곱게 간 우럭살을 섞은 뒤, 완자를 빚어 끓는 물에 익힌다.\r\n\r\n7. 삶은 전복, 불린 표고버섯과 대파를 잘게 썬 뒤, 달걀과 소금을 넣고 섞어 달걀전병 반죽을 만든다.\r\n\r\n8. 팬에 참기름을 두른 뒤, 달걀전병 반죽을 넣고 익혀 전복달걀전병을 만든다.\r\n\r\n9. 익히던 완자에 약재를 우려낸 물을 넣고 끓인다.\r\n\r\n10. 참기름을 두른 팬에 데친 시래기를 볶다가 국간장으로 간한다.\r\n\r\n11. 그릇에 볶은 시래기를 담는다.\r\n\r\n12. 전복달걀전병을 적당한 크기로 썬 뒤 그릇에 함께 담는다.\r\n\r\n13. 익힌 완자를 건져 그릇에 함께 담는다.\r\n\r\n14. 완자를 건져낸 약재 우린 물에 소금으로 간 하고, 그릇에 부어 마무리한다.'),
	(484, '솥태미나', NULL, 1, '1. 끓는 물에 겨우살이와 상황버섯을 넣고 끓여 밥물을 만든다.\r\n\r\n2. 우려낸 밥물을 돌솥에 넣고 불린 쌀, 데친 시래기, 불린 연꽃씨앗을 넣은 뒤 뚜껑을 닫고 끓인다.\r\n\r\n3. 냄비에 들기름을 두르고 잘게 다진 마늘과 미역을 넣고 볶는다.\r\n\r\n4. 볶던 미역에 물을 붓고 끓인다.\r\n\r\n5. 끓는 물에 우엉과 헛개를 우리다가 불린 표고버섯, 설탕,\r\n솔잎올리고당, 국간장을 넣고 끓여 표고버섯 장아찌를 만든다.\r\n\r\n6. 전복은 손질 해 살만 적당한 크기로 썬다.\r\n\r\n7. 삶은 문어를 적당한 크기로 썬다.\r\n\r\n8. 돌솥밥에 문어>전복살>대추, 장뇌삼을 넣고 다시 뚜껑을 닫아 끓인다.\r\n\r\n9. 대파, 마늘을 잘게 다진다.\r\n\r\n10. 잘게 다진 대파와 마늘에 생강청, 고추장, 식초, 고춧가루, 깨를 넣고 불린 수삼을 넣어 무친다.\r\n\r\n11. 표고버섯 장아찌 국물에 설탕, 참기름, 깨를 넣고 소스를 만들어 종지에 담아낸다.\r\n\r\n12. 미역국에 참기름과 국간장을 넣는다.\r\n\r\n13. 미역국, 표고버섯장아찌, 수삼무침을 각각 따로 담아낸다.\r\n\r\n14. 돌솥밥을 따로 낸다.'),
	(485, '사랑의 초밥', NULL, 1, '1. 전복을 데친다.\r\n\r\n2. 식초, 소금, 설탕을 섞어 단촛물을 만든 뒤, 밥과 섞어 양념밥을 만든다.\r\n\r\n3. 달걀, 설탕, 전분을 섞어 달걀물을 만든 뒤, 체에 밭쳐 곱게 거른다.\r\n\r\n4. 기름을 두른 팬에 체에 거른 달걀물을 붓고 지단을 부친다.\r\n\r\n5. 달걀지단을 잘게 다진다.\r\n\r\n6. 김을 굽는다.\r\n\r\n7. 데친 전복을 건져 껍데기와 살을 분리하고, 찬물에 헹궈 식힌다.\r\n\r\n8. 전복을 포 뜬 뒤, 격자로 칼집을 넣는다.\r\n\r\n9. 훈제연어를 적당한 크기로 썰어 칼집을 넣는다.\r\n\r\n10. 냄비에 간장, 청주, 맛술, 설탕을 넣고 끓여 간장소스를 만든다.\r\n\r\n11. 잘게 썬 배추김치를 물에 헹군 뒤, 깨, 참기름, 구운 김을 찢어 넣고 섞는다.\r\n\r\n12. 랩 위에 달걀지단, 훈제연어, 데친 전복, 양념김치를 각각 올린다.\r\n\r\n13. 각 재료 위에 양념밥을 올린 뒤, 랩으로 감싸 동그랗게 모양을 잡는다.\r\n\r\n14. 초밥을 감싼 랩을 벗기고 접시 위에 올린다.\r\n\r\n15. 전복초밥위에 간장소스를 바른다.\r\n\r\n16. 훈제연어초밥을 토치로 직화한 뒤, 마요네즈를 올린다.\r\n\r\n17. 달걀초밥 위에 케첩을 올린다.\r\n\r\n18. 훈제연어초밥 위에 잘게 썬 청양고추를 올린다.'),
	(486, '전복의 후예', NULL, 1, '1. 치즈빵 속의 앙금을 빼내고, 적당한 크기로 썰어 오븐에 굽는다.\r\n\r\n2. 전복의 껍데기와 살을 분리하고, 깨끗이 씻어 격자로 칼집을 낸 뒤 적당한 크기로 썬다.\r\n\r\n3. 대파를 가늘게 채 썰어 찬물에 담가둔다.\r\n\r\n4. 대파를 적당한 크기로 썬다.\r\n\r\n5. 마늘은 편 썰고 양파는 가늘게 채 썬다.\r\n\r\n6. 김을 굽는다.\r\n\r\n7. 기름을 두른 팬에 손질한 대파와 마늘을 볶다가 간장, 쌈장, 손질한 양파를 넣고 함께 볶는다.\r\n\r\n8. 구운 김을 잘게 썬다.\r\n\r\n9. 볶던 재료에 전복을 넣고 함께 볶는다.\r\n\r\n10. 치즈빵을 마른 팬에 굽는다.\r\n\r\n11. 구운 치즈빵 위에 볶은 전복과 채소를 올린다.\r\n\r\n12. 토사전복 위에 파채와 자몽청, 구운 김을 올려 마무리한다.'),
	(487, '깐초이면', NULL, 1, '1. 볼에 대파, 고춧가루, 다진 마늘과 끓인 기름을 넣고 전자레인지에 돌려 고추기름을 낸다.\r\n\r\n2. 소갈빗살을 적당한 크기로 썬 뒤, 핸드블렌더에 넣고 간다.\r\n\r\n3. 간 소갈빗살에 전분, 후추, 소금, 간장을 넣는다.\r\n\r\n4. 두부를 면보로 감싸 물기를 짜낸다.\r\n\r\n5. 물기 짠 두부를 양념한 소갈빗살과 섞어 완자를 만든다.\r\n\r\n6. 갈비두부완자에 달걀흰자와 전분을 차례로 입혀 기름에 튀긴다.\r\n\r\n7. 튀긴 갈비두부완자를 건져 기름기를 뺀다.\r\n\r\n8. 마늘은 으깨고, 청양고추는 잘게 썬다.\r\n\r\n9. 끓는 물에 칼국수면을 삶는다.\r\n\r\n10. 고추기름을 체에 밭쳐 거른다.\r\n\r\n11. 기름을 두른 팬에 고추기름, 손질한 마늘과 청양고추, 간장, 인스턴트 꼬리곰탕 국물을 넣고 끓인다.\r\n\r\n12. 끓이던 재료에 조미료, 고추장을 넣어 깐풍소스를 만든다.\r\n\r\n13. 깐풍소스에 완자튀김을 넣는다.\r\n\r\n14. 삶은 칼국수면을 찬물에 헹군다.\r\n\r\n15. 완자튀김을 넣은 깐풍소스에 삶은 칼국수면과 고추기름을 넣고 볶는다.\r\n\r\n16. 그릇에 깐풍완자볶음면을 담는다.\r\n\r\n17. 대파 흰 부분을 가늘게 채 썬 뒤, 찬물에 헹궈 깐풍완자볶음면 위에 얹는다.\r\n\r\n18. 고추기름을 뿌려 마무리한다.'),
	(488, '갈크러쉬', NULL, 1, '1. 기름을 두른 팬에 된장, 설탕, 다진 마늘을 넣고 볶다가 간장을 넣고 함께 볶는다.\r\n\r\n2. 볶던 재료에 물을 넣고 끓여 된장소스를 만든다.\r\n\r\n3. 양파와 대파 흰 부분을 적당한 크기로 썬 뒤, 밀가루를 묻혀 기름에 튀긴다.\r\n\r\n4. 튀긴 양파와 대파를 건져내 기름기를 뺀다.\r\n\r\n5. 밀가루에 물을 넣고 치대어 면반죽을 만든 뒤, 밀대로 밀어 생면을 만든다.\r\n\r\n6. 생면을 썰어 끓는 물에 넣고 익힌다.\r\n\r\n7. 기름을 두른 팬에 으깬 마늘, 잘게 썬 청양고추와 대파를 볶다가,\r\n잘게 썬 소갈빗살과 밀가루를 묻힌 양파를 넣고 볶는다.\r\n\r\n8. 볶던 재료에 된장소스와 잘게 썬 대파, 후추를 넣고 끓여 된장갈비소스를 만든다.\r\n\r\n9. 된장갈비소스에 고춧가루, 후추를 넣는다.\r\n\r\n10. 된장갈비소스에 삶은 면을 넣고 볶는다.\r\n\r\n11. 볶던 면에 조미료를 넣고 함께 볶는다.\r\n\r\n12. 된장갈비볶음면을 접시에 담은 뒤, 위에 양파&대파튀김을 얹는다.\r\n\r\n13. 잘게 썬 청양고추를 올려 마무리한다.'),
	(489, '고기만두 먹으러 왜 안 왔어?', NULL, 1, '1. 핸드블렌더에 적당한 크기로 썬 부챗살과 삼겹살, 버터를 넣는다.\r\n\r\n2. 1에 후추, 생강즙, 청주, 굴소스를 넣고 간다.\r\n\r\n3. 양파, 대파를 잘게 썰어 갈아놓은 고기와 섞어 만두소를 만든다.\r\n\r\n4. 목살을 고기망치로 두드려 얇고 넓게 편 뒤, 소금과 후추를 뿌린다.\r\n\r\n5. 넓게 편 목살에 만두소를 넣고 돌돌 말아 전분을 묻힌 뒤, 기름에 튀긴다.\r\n\r\n6. 만두를 튀기는 냄비의 뚜껑을 닫는다.\r\n\r\n7. 볼에 고춧가루와 적당한 크기로 썬 대파를 담고, 끓인 기름을 넣어 고추기름을 낸다.\r\n\r\n8. 양송이버섯을 적당한 크기로 썰고, 양파, 당근, 애호박은 채 썬다.\r\n\r\n9. 마늘은 으깨고 대파는 적당한 크기로 썬다.\r\n\r\n10. 팬에 고추기름을 두른 뒤, 손질한 채소들을 한데 넣고 볶다가\r\n간장, 물, 굴소스, XO소스를 넣고 끓여 소스를 만든다.\r\n\r\n11. 만두를 튀기던 냄비의 기름을 따라낸 뒤, 물을 넣고 뚜껑을 덮어 속까지 익힌다.\r\n\r\n12. 채소볶음소스에 전분물을 넣는다.\r\n\r\n13. 그릇에 고기만두를 담고, 채소볶음소스를 올려 마무리한다.'),
	(490, '안심펀치 쓰리강냉이', NULL, 1, '1. 소고기 안심에 소금, 후추를 뿌린 뒤, 베이컨으로 감싸 조리용 실로 묶는다.\r\n\r\n2. 중탕하는 기름에 트러플오일을 넣고, 베이컨으로 감싼 안심을 데친다.\r\n\r\n3. 기름을 두른 팬에 으깬 마늘, 기름에 데친 안심베이컨말이, 버터를 넣고 굽는다.\r\n\r\n4. 냄비에 청주, 채 썬 양파, 으깬 마늘, 휘핑크림, 푸아그라를 넣고 끓여 크림 소스를 만든다.\r\n\r\n5. 크림소스에 버터와 소금을 넣는다.\r\n\r\n6. 끓이던 크림소스에 후추, 파르메산 치즈를 넣고, 핸드블렌더로 곱게 간 뒤, 끓인다.\r\n\r\n7. 굽던 안심베이컨말이를 꺼내 조리용 실을 벗겨낸다.\r\n\r\n8. 가지는 껍질을 벗긴 뒤, 깊게 칼집을 내고 기름에 튀긴다.\r\n\r\n9. 튀긴 가지를 적당한 크기로 썰어 마른 팬에 볶다가 스테이크시즈닝을 뿌린다.\r\n\r\n10. 양파, 마늘을 잘게 썰어 가지와 함께 볶다가, 파르메산 치즈를 갈아 넣는다.\r\n\r\n11. 접시에 안심베이컨말이을 담고, 튀긴 가지볶음을 올린 뒤, 크림소스를 담아낸다.\r\n\r\n12. 대파를 얇게 썰어 올려 마무리한다.'),
	(491, '긴풍면', NULL, 1, '1. 핸드블렌더에 밀가루와 물을 넣고 돌려 면 반죽을 만든다.\r\n\r\n2. 핸드블렌더에 적당한 크기로 썬 대파와 소고기 안심, 무, 들기름을 넣고 간다.\r\n\r\n3. 기름을 두른 팬에 곱게 간 재료들을 볶다가,\r\n피시소스, 굴소스, 맛술, 식초, 뜨거운 물을 붓고 끓인다.\r\n\r\n4. 기름이 넉넉하게 담긴 팬에 고춧가루, 대파, 다진 마늘을 넣고 끓여 고추기름을 낸다.\r\n\r\n5. 면 반죽을 길고 가늘게 늘여 면을 뽑는다.\r\n\r\n6. 면을 삶는다.\r\n\r\n7. 그릇에 면을 담는다.\r\n\r\n8. 끓인 국물을 체에 밭쳐 걸러 면이 담긴 그릇에 붓는다.\r\n\r\n9. 고추기름을 올려 마무리한다.'),
	(492, '우동 튀튀♬', NULL, 1, '1. 핸드블렌더에 밀가루와 물, 소금을 넣고 돌려 면 반죽을 만든다.\r\n\r\n2. 면 반죽을 숙성시킨다.\r\n\r\n3. 냄비에 맛술, 간장, 설탕을 1:3:1 비율로 넣고 끓인다.\r\n\r\n4. 끓인 간장소스에 물을 섞고 냉동실에 넣어 차게 식힌다.\r\n\r\n5. 면 반죽을 밀대로 얇게 민다.\r\n\r\n6. 얇게 민 면 반죽을 썰어 생면을 만든다.\r\n\r\n7. 끓는 물에 식초를 넣은 뒤, 생면을 삶는다.\r\n\r\n8. 삶은 면을 얼음물에 식힌 뒤, 그릇에 담는다.\r\n\r\n9. 애호박, 가지, 당근, 만가닥버섯은 채 썰고 관자는 적당한 크기로 썬다.\r\n\r\n10. 손질한 채소와 관자에 밀가루, 물을 넣고 섞어 튀김옷을 입힌 뒤, 기름에 튀긴다.\r\n\r\n11. 채소관자튀김을 건져 기름기를 뺀다.\r\n\r\n12. 밀가루반죽을 기름에 튀겨 덴카스를 만든다.\r\n\r\n13. 면 위에 강판에 간 무, 레몬, 잘게 썬 쪽파와 덴카스를 올린다.\r\n\r\n14. 채소관자튀김을 반으로 썰어 다른 접시에 담아낸다.\r\n\r\n15. 차갑게 식힌 간장소스를 면과 함께 담아낸다.'),
	(493, '산해딘미', NULL, 1, '1. 식빵은 테두리를 잘라내고, 적당한 크기로 썰어 핸드블렌더에 갈아 빵가루를 만든다.\r\n\r\n2. 오징어는 적당한 크기로 썰고, 새우살은 씻어서 핸드블렌더에 넣는다.\r\n\r\n3. 황민어의 살만 발라낸다.\r\n\r\n4. 오징어와 새우살을 넣은 핸드블렌더에 황민어살도 함께 넣고 곱게 간다.\r\n\r\n5. 4에 달걀, 소금, 밀가루를 넣고 다시 한 번 간다.\r\n\r\n6. 곱게 간 해산물반죽에 밀가루를 넣고 치대어 완자반죽을 만든다.\r\n\r\n7. 완자반죽을 적당한 크기로 떼어 동그랗게 빚은 후, 빵가루를 묻혀 기름에 튀긴다.\r\n\r\n8. 튀긴 해산물완자를 전자레인지에 한 번 더 익힌다.\r\n\r\n9. 기름을 두른 팬에 옥수수, 버터, 설탕을 넣고 볶은 뒤, 핸드블렌더로 곱게 간다.\r\n\r\n10. 마른 팬에 곱게 간 옥수수를 넣고, 밀가루와 버터, 슬라이스 모차렐라치즈를 넣어 잘 섞어준다.\r\n\r\n11. 강판에 사과를 갈고, 청국장, 올리브오일, 요구르트, 마요네즈를 넣고 섞어 청국장드레싱을 만든다.\r\n\r\n12. 그릇에 옥수수소스를 담고, 부추를 잘게 썰어 올린다.\r\n\r\n13. 청국장드레싱에 레몬즙을 넣는다.\r\n\r\n14. 부추 위에 청국장드레싱을 올린 뒤, 잘게 썬 부추를 흩뿌린다.\r\n\r\n15. 해산물완자를 다시 한 번 튀긴 뒤, 그릇에 올리고 청국장드레싱을 끼얹어 마무리한다.'),
	(494, '딘토불이 떡볶이', NULL, 1, '1. 매생이를 다진다.\n\n2. 밀가루에 물, 다진 매생이를 넣고 잘 저어 매생이 반죽을 만든다.\n\n3. 기름을 두른 팬에 매생이 반죽을 두른 뒤, 매생이 전병을 만들어 기름기를 뺀다.\n\n4. 민어의 살만 발라낸다.\n\n5. 즉석밥을 핸드블렌더에 넣고 간다.\n\n6. 5에 뜨거운 물, 찹쌀가루, 밀가루를 넣고 치대어 떡 반죽을 만든다.\n\n7. 떡 반죽을 길게 민 뒤, 적당한 크기로 썰어 끓는 물에 삶는다.\n\n8. 채끝살에 소금, 후추를 뿌린 뒤 기름을 두른 팬에 구워 한 김 식힌다.\n\n9. 핸드블렌더에 채끝살, 새우살, 민어살을 넣고 곱게 간다.\n\n10. 매생이 전병에 밀가루를 묻힌 뒤, 곱게 간 재료들을 넣어 돌돌 말고, 튀김옷을 입혀 기름에 튀긴다.\n\n11. 불린 가죽나물의 물기를 짜내고, 기름을 두른 팬에 볶는다.\n\n12. 볶던 가죽나물에 양파채와 깻잎장아찌 국물, 고추장아찌 국물,\n설탕, 식초, 사골육수, 장조림 국물을 넣고 끓인다.\n\n13. 익힌 떡을 건져 찬물에 담가 식힌 뒤, 재료를 끓이던 팬에 넣고 함께 끓인다.\n\n14. 구운 채끝살을 적당한 크기로 썰어 떡볶이에 넣고 함께 끓인다.\n\n15. 끓이던 떡볶이에 전분물을 넣는다.\n\n16. 매생이 전병튀김을 적당한 크기로 썰어 떡볶이에 함께 넣고 끓인다.\n\n17. 떡볶이에 잘게 다진 매생이를 넣고 끓인다.\n\n18. 떡볶이에 설탕, 후추를 넣는다.\n\n19. 그릇에 매생이 떡볶이를 담는다.'),
	(495, 'Caesar Salad with Deep fried oyster', NULL, 1, '1. 식빵의 테두리를 잘라내고, 깍둑썰기 한다.\r\n\r\n2. 버터를 녹인 팬에 깍둑썰기한 식빵을 굽는다.\r\n\r\n3. 적당한 크기로 썬 식빵을 핸드블렌더에 넣고 갈아 빵가루를 만든다.\r\n\r\n4. 핸드블렌더에 새우젓, 멸치액젓, 올리브오일을 넣고 갈아 젓갈소스를 만든다.\r\n\r\n5. 알배추를 적당한 크기로 썬다.\r\n\r\n6. 달걀물을 만든다.\r\n\r\n7. 굴에 후추>밀가루>달걀물>빵가루 순으로 튀김옷을 입혀 기름에 튀긴 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n8. 베이컨을 잘게 썰어 마른 팬에 굽는다.\r\n\r\n9. 구운 베이컨을 체에 밭쳐 기름기를 뺀다.\r\n\r\n10. 식용유와 올리브오일을 섞는다.\r\n\r\n11. 허니머스터드에 달걀노른자, 젓갈소스를 더해 섞다가,\r\n식용유+올리브오일을 조금씩 부어가며 계속해서 섞어준다.\r\n\r\n12. 11에 레몬즙을 넣어 시저 드레싱을 만든다.\r\n\r\n13. 시저 드레싱에 다진 마늘, 파르메산 치즈가루, 구운 베이컨, 크루통,\r\n샐러드채소와 알배추를 넣고 버무린다.\r\n\r\n14. 접시에 시저 샐러드를 담는다.\r\n\r\n15. 굴튀김을 올려 마무리한다.'),
	(496, '돈코츠 드소', NULL, 1, '1. 마른 팬에 토르티야를 굽는다.\r\n\r\n2. 버터에 마늘을 갈아 넣고, 전자레인지에 돌려 녹인다.\r\n\r\n3. 녹인 버터를 구운 토르티야에 바르고 다시 한 번 굽는다.\r\n\r\n4. 버터를 발라 구운 토르티야를 전자레인지에 돌린다.\r\n\r\n5. 양파, 대파를 잘게 다진다.\r\n\r\n6. 기름을 두른 팬에 잘게 다진 양파와 대파, 다진 돼지고기,\r\n베이컨, 사골육수, 된장을 넣고 끓인다.\r\n\r\n7. 끓이던 재료에 슬라이스 모차렐라치즈를 넣고 끓여 돈코츠 치즈소스를 만든다.\r\n\r\n8. 안창살, 양파, 브로콜리를 적당한 크기로 손질한다.\r\n\r\n9. 기름을 두른 팬에 잘게 썬 안창살과 채 썬 양파를 볶다가 후추를 뿌린다.\r\n\r\n10. 볶던 재료에 잘게 썬 브로콜리, 슬라이스 체다치즈와\r\n슬라이스 모차렐라치즈를 넣고 뚜껑을 덮어 익힌다.\r\n\r\n11. 토르티야 위에 치즈를 얹은 안창살볶음을 올리고 돌돌 말아 롤을 만든 뒤, 적당한 크기로 썬다.\r\n\r\n12. 접시에 토르티야 롤을 담는다.\r\n\r\n13. 그릇에 돈코츠 치즈소스를 담고, 잘게 다진 브로콜리를 올린다.\r\n\r\n14. 구운 토르티야를 따로 내 마무리한다.'),
	(497, '장어튀겼쌈', NULL, 1, '1. 양파를 채 썰어 찬물에 담가 둔다.\r\n\r\n2. 무를 채 썬 뒤 소금에 절여놓는다.\r\n\r\n3. 밀가루, 전분, 물, 달걀, 식용유를 섞어 튀김반죽을 만든다.\r\n\r\n4. 장어를 손질해 튀김반죽을 입힌 뒤 기름에 튀긴다.\r\n\r\n5. 핸드블렌더에 밀가루, 물, 소금을 넣고 갈아 물반죽을 만든다.\r\n\r\n6. 마른 팬에 물반죽을 얇게 부쳐 전병을 두 장 만든다.\r\n\r\n7. 장어튀김을 체에 밭쳐 기름기를 뺀다.\r\n\r\n8. 전병 한 장을 기름에 튀긴다.\r\n\r\n9. 명란젓과 마요네즈를 섞어 명란마요소스를 만든다.\r\n\r\n10. 케첩에 다진 마늘, 설탕, 레몬즙을 섞어 칠리소스를 만든다.\r\n\r\n11. 튀긴 전병을 건져 기름기를 뺀다.\r\n\r\n12. 오징어다리에 밀가루를 묻혀 기름에 튀긴다.\r\n\r\n13. 구운 전병에 칠리소스를 바르고 절인 무채, 채 썬 양파를 올린다\r\n\r\n14. 13 위에 장어튀김을 얹고 명란마요소스를 바른 뒤 쌈 싼다.\r\n\r\n15. 접시에 절인 무채, 채 썬 양파를 올리고, 장어전병쌈을 함께 낸다.\r\n\r\n16. 무&양파채 위에 칠리소스를 뿌린다.\r\n\r\n17. 오징어다리튀김을 곁들인다.\r\n\r\n18. 장어전병쌈 안에 튀긴 전병을 부숴 넣어 마무리한다.'),
	(498, '육회한 파튀', NULL, 1, '1. 튀김가루에 물을 넣고 섞어 튀김반죽을 만든다.\r\n\r\n2. 파의 겉면을 벗겨낸 뒤 적당한 크기로 썬다.\r\n\r\n3. 무를 적당한 크기로 썬다.\r\n\r\n4. 손질한 파와 무에 튀김반죽을 입힌다.\r\n\r\n5. 튀김반죽을 입힌 파와 무를 기름에 튀긴다.\r\n\r\n6. 고춧가루, 후추, 간장, 생강청, 설탕, 고추장, 식초, 참기름, 다진 마늘을 섞어 육회양념을 만든다.\r\n\r\n7. 채끝등심을 얇게 썰어 육회양념에 버무린다.\r\n\r\n8. 핸드블렌더에 머스터드소스와 마요네즈, 견과류를 넣고 곱게 간다.\r\n\r\n9. 접시에 무튀김과 파튀김을 올린다.\r\n\r\n10. 튀김 위에 양념에 버무린 육회를 올리고, 견과머스터드소스를 따로 낸다.'),
	(499, '피자하면 LA야~ LA야♬', NULL, 1, '1. 밀가루에 뜨거운 물과 소금을 넣고 치대어 익반죽한다.\r\n\r\n2. 핸드블렌더에 삼겹살, 채끝등심, 로즈마리, 소금을 넣고 간다.\r\n\r\n3. 양송이버섯, 파프리카, 양파, 마늘을 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 팬에 마늘을 볶다가, 양송이버섯과 양파를 넣고 소금, 후추를 뿌려 볶는다.\r\n\r\n5. 곱게 간 삼겹살과 채끝등심을 채소와 함께 볶는다.\r\n\r\n6. 도우 반죽을 밀대로 얇게 민 뒤 동그랗게 모양을 잡는다.\r\n\r\n7. 마른 팬에 도우 반죽을 굽는다.\r\n\r\n8. 베이컨을 썰어 팬에 넣고 다른 재료들과 함께 볶는다.\r\n\r\n9. 파프리카를 팬에 넣고 함께 볶은 뒤, 꺼내둔다.\r\n\r\n10. 구운 도우를 토치로 직화한다.\r\n\r\n11. 도우를 뒤집어 토마토소스를 바르고 파르메산치즈 가루를 뿌린 뒤, 토치로 직화한다.\r\n\r\n12. 도우 위에 볶은 재료들과 모차렐라치즈, 파슬리를 뿌린다.\r\n\r\n13. 피자를 토치로 직화한다.\r\n\r\n14. 모차렐라치즈를 전자레인지에 녹인 뒤, 피자 위에 올려 마무리한다.'),
	(500, '역시 피자는 시카고야~ 시카고야♬', NULL, 1, '1. 밀가루, 달걀노른자, 올리브오일, 뜨거운 물, 파르메산치즈 가루,\r\n로즈마리, 소금을 넣고 치대어 익반죽한다.\r\n\r\n2. 도우 반죽을 밀대로 밀어 넓게 펴고 동그랗게 자른다.\r\n\r\n3. 버터를 바른 내열접시 위에 도우반죽을 올리고 깊은 그릇 모양으로 만든 뒤, 바닥에 포크로 구멍을 낸다.\r\n\r\n4. 도우반죽을 전자레인지에 넣고 익힌다.\r\n\r\n5. 마늘, 슬라이스 햄, 베이컨, 양송이버섯을 잘게 썬다.\r\n\r\n6. 기름을 두른 팬에 손질한 재료들을 볶다가 후추를 뿌린다.\r\n\r\n7. 전자레인지에 익힌 도우를 기름에 튀긴다.\r\n\r\n8. 볶은 재료에 토마토소스, 모차렐라치즈를 넣고 섞는다.\r\n\r\n9. 8을 도우에 가득 채운다.\r\n\r\n10. 슬라이스 체다치즈와 슬라이스 에멘탈치즈를 올린다.\r\n\r\n11. 피자를 전자레인지에 넣고 익힌다.\r\n\r\n12. 전자레인지에서 익힌 피자를 꺼내 토치로 직화한다.\r\n\r\n13. 피자 위에 파르메산치즈 가루와 파슬리를 뿌려 마무리한다.'),
	(501, '어차피 우승은 나자냐~', NULL, 1, '1. 소금을 넣은 끓는 물에 전복을 삶는다.\r\n\r\n2. 새우는 껍질을 벗기고 내장을 빼낸다.\r\n\r\n3. 삶은 전복은 내장을 제거한 뒤, 살만 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 팬에 전복살과 새우를 볶는다.\r\n\r\n5. 냄비에 버터와 밀가루를 볶아 루를 만든 뒤, 우유를 넣고 끓여 크림소스를 만든다.\r\n\r\n6. 볶던 전복과 새우에 크림소스, 카망베르치즈, 우유, 모차렐라치즈를 넣어 볶는다.\r\n\r\n7. 믹서기에 달걀, 우유, 밀가루를 넣고 갈아 반죽을 만든 뒤, 체에 밭쳐 거른다.\r\n\r\n8. 기름을 두른 팬에 밀가루 반죽을 얇게 부쳐 크레이프를 만든다.\r\n\r\n9. 볶던 해산물에 후추를 뿌리고, 취장아찌를 넣어 볶는다.\r\n\r\n10. 크레이프를 그릇에 펼친 뒤, 크림해산물장아찌볶음을 채우고 양념명란젓을 올린다.\r\n\r\n11. 10 위에 잘게 썬 마늘을 올리고 참기름을 뿌린다.\r\n\r\n12. 크레이프로 내용물을 완전히 감싼 뒤, 전자레인지에서 익힌다.\r\n\r\n13. 볼에 밀가루, 물, 톳가루를 넣어 물반죽한 뒤, 기름에 튀겨 톳덴카스를 만든다.\r\n\r\n14. 전자레인지에 익힌 명란해산물라자냐를 꺼내 접시에 올린다.\r\n\r\n15. 명란해산물라자냐 위에 크림소스를 올린다.\r\n\r\n16. 라자냐 위에 톳덴카스를 올려 마무리한다.'),
	(502, '크로켓 맛있찌', NULL, 1, '1. 양상추를 잘게 썬다.\r\n\r\n2. 잘게 썬 양상추에 총각김치를 갈아 넣고, 소금, 올리브오일, 후추, 식초를 넣고 무쳐 샐러드를 만든다.\r\n\r\n3. 끓는 물에 브로콜리, 콜리플라워, 아스파라거스를 데친 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n4. 데친 브로콜리와 콜리플라워를 잘게 다진다.\r\n\r\n5. 달걀물에 다진 채소, 양념명란젓, 밀가루, 크림치즈를 넣고 반죽한다.\r\n\r\n6. 크로켓 반죽을 동그랗게 뭉친 뒤, 밀가루를 묻혀 기름에 튀긴다.\r\n\r\n7. 베이컨을 가늘게 썬다.\r\n\r\n8. 기름을 두른 팬에 채 썬 베이컨과 후추를 넣고 볶는다.\r\n\r\n9. 볶던 베이컨에 간 토마토와 편마늘을 넣고 함께 볶는다.\r\n\r\n10. 볶던 재료에 잘게 다진 울외장아찌와 청양고추, 후추, 잘게 다진 취장아찌를 넣고 볶는다.\r\n\r\n11. 볶던 재료에 마늘을 갈아 넣고, 고춧가루, 설탕을 뿌린다.\r\n\r\n12. 채소크로켓의 기름기를 뺀다.\r\n\r\n13. 베이컨장아찌볶음을 접시에 넓게 깐다.\r\n\r\n14. 베이컨장아찌볶음 위에 채소크로켓을 올린 뒤, 파르메산치즈를 갈아 올리고\r\n데친 아스파라거스를 곁들인다.\r\n\r\n15. 총각김치샐러드를 함께 낸다.'),
	(503, '제육과의 동침', NULL, 1, '1. 무를 나박썰기 한 뒤, 소금에 절여둔다.\n\n2. 볼에 물, 소금, 다진 마늘, 설탕, 식초를 넣고 섞어 동치미국물을 만든다.\n\n3. 사과는 껍질을 벗기고 적당한 크기로 썰어 동치미국물에 담근다.\n\n4. 양상추, 방울토마토, 양파를 적당한 크기로 동치미국물에 담근다.\n\n5. 핸드블렌더에 사과와 물을 넣고 갈아 사과즙을 만든다.\n\n6. 볼에 적당한 크기로 썬 삼겹살에 손질한 양파, 대파, 아스파라거스, 콜리플라워를 담는다.\n\n7. 6에에 토마토소스와 라면수프, 사과즙, 다진 마늘을 넣어 버무린다.\n\n8. 마른 팬에 양념한 삼겹살을 볶는다.\n\n9. 볶던 재료에 후추를 넣는다.\n\n10. 전자레인지에 즉석밥을 돌린 뒤, 그릇에 담는다.\n\n11. 소금에 절인 무를 물에 헹궈내고, 동치미와 함께 섞어 그릇에 담아낸다.\n\n12. 제육볶음을 접시에 담은 뒤, 고추를 잘게 썰어 올린다.\n\n13. 제육볶음, 동치미, 밥을 함께 낸다.'),
	(504, '보쌈하새우', NULL, 1, '1. 끓는 물에 간장과 설탕을 붓고, 게 등껍질, 새우, 게의 알을 넣고 끓인다.\r\n\r\n2. 양파를 채 썬 뒤, 찜기에 넣는다.\r\n\r\n3. 뜨거운 기름에 대파, 고추, 삼겹살을 넣어 데치듯이 튀긴 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n4. 튀긴 삼겹살과 채소를 찜기에 넣는다.\r\n\r\n5. 삼겹살과 채소 위에 더치커피와 맛술을 뿌린 뒤, 뚜껑을 덮어 찐다.\r\n\r\n6. 소금물을 끓여 알배추와 아스파라거스를 데친다.\r\n\r\n7. 가늘게 채 썬 무에 소금, 고춧가루, 식초를 넣고 버무려 무생채를 만든다.\r\n\r\n8. 데친 알배추와 아스파라거스를 얼음물에 담가 식힌다.\r\n\r\n9. 강판에 간 무와 토마토소스, 고춧가루, 식초, 깻잎, 다진 대파에 무생채,\r\n삶은 아스파라거스를 넣고 버무려 김치소를 만든다.\r\n\r\n10. 전자레인지에 즉석밥을 돌린 뒤, 그릇에 담는다.\r\n\r\n11. 간장물에 끓인 게와 새우를 건져 식힌다.\r\n\r\n12. 11에 발사믹식초와 식초를 넣고 섞는다.\r\n\r\n13. 알배추를 펼쳐 김치소를 넣고 감싼 뒤, 먹기 좋게 잘라 그릇 위에 올린다.\r\n\r\n14. 김치 위에 김치소 국물을 뿌린다.\r\n\r\n15. 접시에 수육을 담는다.\r\n\r\n16. 새우장아찌의 껍질을 깐 뒤, 국물과 함께 접시에 담아낸다.\r\n\r\n17. 수육, 보쌈김치, 새우장아찌와 밥을 함께 낸다.'),
	(505, '돔비지마', NULL, 1, '1. 감성돔 살을 적당한 크기로 썬다.\r\n\r\n2. 핸드블렌더에 적당한 크기로 썬 감성돔 살, 자숙새우, 전복을 넣고 간다.\r\n\r\n3. 간 해산물에 밀가루, 소금, 후추, 비지를 넣고 치대어 스테이크 반죽을 만든다.\r\n\r\n4. 스테이크 반죽을 적당한 크기로 뭉쳐 기름을 두른 팬에 굽는다.\r\n\r\n5. 올리브오일을 두른 접시에 돔 스테이크를 올린 뒤, 장어구이소스를 바른다.\r\n\r\n6. 장어구이소스를 바른 돔 스테이크를 오븐에 넣고 익힌다.\r\n\r\n7. 기름을 두른 팬에 적당한 크기로 썬 양파와 대파를 볶다가 뜨거운 물을 넣고 끓인다.\r\n\r\n8. 끓인 재료와 고추를 핸드블렌더로 곱게 갈아 대파 퓌레를 만들어 체에 밭쳐 거른다.\r\n\r\n9. 체에 거른 대파 퓌레에 소금과 후추를 뿌려 끓인다.\r\n\r\n10. 대파 퓌레에 고추와 버터를 넣고 끓인다.\r\n\r\n11. 인절미를 밀대로 얇게 민다.\r\n\r\n12. 얇게 민 인절미를 기름에 튀긴 뒤, 건져서 기름기를 뺀다.\r\n\r\n13. 양파, 대파, 마늘쫑 장아찌, 삶은 달걀을 잘게 다져 볼에 담고, 마요네즈와 섞어 타르타르소스를 만든다.\r\n\r\n14. 접시에 대파 퓌레를 깔고, 오븐에서 익힌 돔 스테이크를 올린 뒤 타르타르소스를 얹는다.\r\n\r\n15. 13 위에 인절미튀김을 올리고, 레몬껍질을 갈아서 뿌려 마무리한다.'),
	(506, '마파 돔돔돔돔♬', NULL, 1, '1. 감성돔의 살을 발라낸다.\r\n\r\n2. 살을 발라낸 감성돔 뼈에 전분을 묻혀 기름에 튀긴다.\r\n\r\n3. 핸드블렌더에 적당한 크기로 썬 감성돔 살과 오징어,\r\n전분, 청주, 달걀노른자를 넣고 갈아 완자 반죽을 만든다.\r\n\r\n4. 자숙새우와 전복을 잘게 썬다.\r\n\r\n5. 잘게 썬 자숙새우와 전복, 완자 반죽과 전분을 넣고 치댄다.\r\n\r\n6. 두부를 동그란 모양으로 파내 전분을 묻혀 튀긴다.\r\n\r\n7. 튀긴 두부를 건져 기름기를 뺀다.\r\n\r\n8. 완자 반죽을 동그랗게 떼어 기름에 튀긴다.\r\n\r\n9. 전자레인지에 즉석밥을 넣고 데운다.\r\n\r\n10. 핸드블렌더에 항정살, 마늘, 대파, 양파를 넣고 간다.\r\n\r\n11. 고추기름과 식용유를 두른 팬에 채소와 함께 간 항정살, 두반장,\r\n굴소스, 설탕, 맛술, 물을 넣고 끓여 마파소스를 만든다.\r\n\r\n12. 기름에 튀긴 감성돔 뼈를 건져 기름기를 뺀다.\r\n\r\n13. 완자튀김을 체에 밭쳐 기름기를 뺀다.\r\n\r\n14. 마파소스에 간장을 넣는다.\r\n\r\n15. 전자레인지에서 데운 즉석밥을 그릇에 담는다.\r\n\r\n16. 마파소스에 전분물을 부어 농도를 조절한다.\r\n\r\n17. 두부튀김과 완자튀김을 마파소스에 버무린다.\r\n\r\n18. 접시에 감성돔 뼈 튀김을 올리고, 마파소스에 버무린 두부튀김과 완자튀김을 담는다.\r\n\r\n19. 대파 흰 부분을 채썰어 올려 완성한다.'),
	(507, '짜장과 정열을 그대에게', NULL, 1, '1. 두부를 적당한 크기로 썰어 튀긴다.\r\n\r\n2. 볼에 밀가루, 소금, 물을 넣고 치대어 면 반죽을 만든다.\r\n\r\n3. 끓인 기름에 춘장을 튀기듯이 볶는다.\r\n\r\n4. 마늘은 다지고, 대파는 적당한 크기로 썬다.\r\n\r\n5. 양파, 애호박, 항정살을 잘게 썬다.\r\n\r\n6. 기름을 넉넉히 두른 팬에 손질한 마늘, 대파를 볶다가 항정살을 넣고 볶는다.\r\n\r\n7. 볶던 재료에 간장, 손질한 채소, 튀기듯 볶은 춘장, 설탕, 굴소스를 넣고 볶아 짜장소스를 만든다.\r\n\r\n8. 면 반죽을 밀대로 얇게 밀고, 적당한 두께로 썰어 삶는다.\r\n\r\n9. 짜장소스에 전분물을 넣는다.\r\n\r\n10. 튀긴 두부를 건져 짜장소스에 넣고 함께 볶는다.\r\n\r\n11. 삶은 면을 찬물에 담가 식힌다.\r\n\r\n12. 식힌 면을 다시 한 번 뜨거운 물에 담갔다가 그릇에 담는다.\r\n\r\n13. 면과 짜장소스를 따로 담아낸다.'),
	(508, '낙지엄지척♬', NULL, 1, '1. 냄비에 적당한 크기로 썬 무와 인스턴트 재첩국을 넣고 끓인다.\r\n\r\n2. 핸드블렌더에 마늘을 넣고 간다.\r\n\r\n3. 기름을 두른 팬에 손질한 무와 대파, 간 마늘을 넣고 볶는다.\r\n\r\n4. 적당한 크기로 썬 소고기 등심과 손질한 낙지를 팬에 넣고 함께 볶는다..\r\n\r\n5. 볶던 재료를 간장과 고춧가루로 양념한다.\r\n\r\n6. 무와 애호박을 채 썬다.\r\n\r\n7. 마른 팬에 채 썬 무와 애호박, 간 마늘, 소금, 후추를 넣고 볶는다.\r\n\r\n8. 잘게 다진 조개젓에 식초, 참기름, 다진 대파를 넣고 섞는다.\r\n\r\n9. 8에 볶은 무와 애호박, 올리브오일을 넣고 섞는다.\r\n\r\n10. 볶은 낙지와 채소에 뜨거운 물을 붓고 끓인다.\r\n\r\n11. 끓이던 낙지탕에 적당한 크기로 썬 전복과 무와 함께 끓인 재첩국을 넣고 함께 끓인다.\r\n\r\n12. 그릇에 낙지탕을 담은 뒤, 애호박조개젓무침과 잘게 썬 대파를 올린다.\r\n\r\n13. 데운 즉석밥을 그릇에 담고 잘게 썬 대파를 곁들여낸다.'),
	(509, '무빙스타', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n\r\n2. 적당한 크기로 썬 삼겹살을 기름에 튀기듯이 볶는다.\r\n\r\n3. 볶은 삼겹살을 체에 밭쳐 기름을 뺀다.\r\n\r\n4. 무를 가늘게 채 썬다.\r\n\r\n5. 무채에 소금으로 간 한다.\r\n\r\n6. 소금으로 간 한 무채를 팬에 볶는다.\r\n\r\n7. 볶던 무채에 잘게 썬 대파와 으깬 마늘, 삼겹살을 볶았던 기름을 넣고 볶는다.\r\n\r\n8. 볶은 무채에 간장으로 간 한다.\r\n\r\n9. 핸드블렌더에 고추장, 밀가루, 물을 넣고 갈아 빙떡반죽을 만든다.\r\n\r\n10. 기름을 두른 팬에 반죽을 얇게 부쳐 고추장빙떡을 만든다.\r\n\r\n11. 고추장빙떡을 뒤집어 부친다.\r\n\r\n12. 핸드블렌더에 아몬드, 통조림옥수수, 마요네즈를 넣고 갈아 소스를 만든다.\r\n\r\n13. 고추장빙떡에 볶은 무채와 삼겹살을 넣고 돌돌 만다.\r\n\r\n14. 고추장빙떡말이를 적당한 크기로 썰어 접시에 올린다.\r\n\r\n15. 소스를 얹어 마무리한다.'),
	(510, '퍼펙트 스튜', NULL, 1, '1. 감자는 껍질을 벗겨 슬라이스 한 뒤, 소금을 넣은 끓는 물에 삶는다.\r\n\r\n2. 기름을 두른 팬에 간 소고기를 볶는다.\r\n\r\n3. 양파, 당근, 마늘을 잘게 썰어 간 소고기와 함께 볶는다.\r\n\r\n4. 볶던 재료에 밀가루, 레드와인, 버터, 고추장을 넣고 볶아 소고기고추장스튜를 만든다.\r\n\r\n5. 삶은 감자를 체에 밭쳐 물기를 뺀 뒤, 후추, 소금, 버터를 넣고 으깨 감자퓌레를 만든다.\r\n\r\n6. 그릇에 소고기고추장스튜를 담는다.\r\n\r\n7. 소고기고추장스튜 위에 감자퓌레를 올린다.\r\n\r\n8. 적당한 크기로 썬 고추를 뿌려 마무리한다.'),
	(511, '소면이 술술', NULL, 1, '1. 무, 대파, 애호박, 고추 장아찌, 양파를 적당한 크기로 썰어 볼에 담는다.\r\n\r\n2. 손질한 채소에 간장, 소금, 고춧가루를 넣고 버무려 양념한다.\r\n\r\n3. 냄비에 적당한 크기로 썬 대파 흰 부분과 으깬 마늘을 넣고 볶는다.\r\n4. 대파와 마늘을 볶은 냄비에 인스턴트 갈비탕을 체에 밭쳐 국물만 넣고 끓인다.\r\n\r\n5. 4에 양념한 채소와 물을 넣고 함께 끓인다.\r\n\r\n6. 살얼은 소고기 등심을 면처럼 길게 썬다.\r\n\r\n7. 소고기면을 소금, 전분, 후추, 고추장아찌 국물로 양념한다.\r\n\r\n8. 양념한 소고기면을 찜기에 찐다.\r\n\r\n9. 냉이에 전분을 묻힌다.\r\n\r\n10. 전분물을 만들어 전분을 묻힌 냉이 위에 한 번 더 입혀서 기름에 튀긴다.\r\n\r\n11. 튀긴 냉이를 체에 밭쳐 기름기를 빼고, 소금을 뿌린다.\r\n\r\n12. 전자레인지에 즉석밥을 돌린다.\r\n\r\n13. 찐 소고기면을 건져 끓이던 국물에 넣고, 잘게 썬 고추를 넣어 육개장을 만든다.\r\n\r\n14. 육개장에 고춧가루, 잘게 썬 시금치를 넣고 끓인다.\r\n\r\n15. 그릇에 밥과 냉이튀김을 각각 담아낸다.\r\n\r\n16. 뚝배기에 육개장을 담아 끓이다가 고춧가루를 뿌려 마무리한다.'),
	(512, '박살면', NULL, 1, '1. 어포를 직화로 굽고, 핸드블렌더로 간다.\r\n\r\n2. 볼에 어포가루, 밀가루, 뜨거운 물을 넣고 치대어 익반죽한 뒤, 숙성시킨다.\r\n\r\n3. 마늘은 다지고, 대파, 애호박, 양파, 고추를 잘게 썬다.\r\n\r\n4. 삼겹살의 살코기와 비계를 분리한 뒤, 비계만 핸드블렌더로 간다.\r\n\r\n5. 마른 팬에 간 비계를 볶는다.\r\n\r\n6. 삼겹살 살코기와 오징어를 잘게 썰어 비계를 볶던 팬에 함께 볶는다.\r\n\r\n7. 6에 간장, 손질한 채소, 멸치육수를 넣고 볶아 오징어삼겹살볶음을 만든다.\r\n\r\n8. 맛술, 멸치액젓, 액상조미료, 후추, 잘게 썬 고추와 대파, 설탕을 섞어 소스를 만든다.\r\n\r\n9. 오징어삼겹살볶음에 소스를 넣고 볶는다.\r\n\r\n10. 숙성시킨 어포면반죽을 밀대로 얇게 민 뒤, 가늘게 썰어 어포면을 만든다.\r\n\r\n11. 어포면을 체 사이에 끼워 그릇모양으로 만든 뒤, 기름에 튀긴다.\r\n\r\n12. 튀긴 면을 건져 기름기를 뺀다.\r\n\r\n13. 그릇에 오징어삼겹살볶음을 담는다.\r\n\r\n14. 대파 흰 부분을 가늘게 채 썰어 오징어삼겹살볶음 위에 올린다.\r\n\r\n15. 14 위에 튀긴 면을 올려 마무리한다.'),
	(513, '아빠의 바다', NULL, 1, '1. 끓는 물에 전복을 삶는다.\r\n\r\n2. 대파 흰 부분을 적당한 크기로 썰어 기름에 튀긴다.\r\n\r\n3. 2에 고춧가루, 양파를 넣고 튀겨 고추기름을 낸다.\r\n\r\n4. 고추기름을 체에 밭쳐 기름만 빼낸다.\r\n\r\n5. 양파를 적당한 크기로 썰어 한 겹씩 분리한다.\r\n\r\n6. 양파, 대파, 마늘을 잘게 썬다.\r\n\r\n7. 팬에 고추기름을 두른 뒤, 손질한 채소와 케첩, 물, 메이플시럽, 굴소스를 넣고 졸여 칠리소스를 만든다.\r\n\r\n8. 오징어를 손질해 끓는 물에 데친다.\r\n\r\n9. 삶은 전복과 데친 오징어를 찬물에 헹군다.\r\n\r\n10. 삶은 전복은 살을 분리해 칼집을 낸다.\r\n\r\n11. 칼집 낸 전복살을 칠리소스에 넣고 조린다.\r\n\r\n12. 양파와 파프리카를 채 썬다.\r\n\r\n13. 데친 오징어의 껍질을 벗겨내고 격자로 촘촘하게 칼집을 낸 뒤, 길게 썬다.\r\n\r\n14. 삶은 전복을 적당한 크기로 썬다.\r\n\r\n15. 손질한 채소, 오징어, 전복에 고추장, 참기름, 굴소스, 메이플시럽을 넣고 버무려 냉채를 만든다.\r\n\r\n16. 한 겹씩 분리한 양파 위에 양념에 버무린 전복&오징어냉채를 올려 접시에 담는다.\r\n\r\n17. 전복껍데기 위에 칠리전복을 올려 접시에 담는다.\r\n\r\n18. 잘게 썬 고추를 전복냉채와 칠리전복 위에 얹어 마무리한다.'),
	(514, '엄마의 바다', NULL, 1, '1. 양념돼지갈비에 잘게 칼집을 낸 뒤, 적당한 크기로 썬다.\r\n\r\n2. 끓는 물에 손질한 양념돼지갈비와 소갈빗살, 설탕, 간장, 대파,\r\n양파, 씨를 발라낸 대추, 당근을 넣고 익힌다.\r\n\r\n3. 전복의 살과 껍데기를 분리한다.\r\n\r\n4. 갈비찜에 전복 껍데기를 넣고 끓인다.\r\n\r\n5. 전복살은 칼집을 낸 뒤, 갈비찜에 넣고 끓인다.\r\n\r\n6. 갈비찜에 통마늘을 .넣고 함께 끓인다.\r\n\r\n7. 갈비찜에 매실원액을 넣고 끓인다.\r\n\r\n8. 적당한 크기로 썬 전복살에 밀가루와 물을 섞은 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n9. 튀긴 전복을 건진 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n10. 설탕, 식초, 깨를 밥에 섞는다.\r\n\r\n11. 김발 위에 배추김치를 펴고 양념한 밥 > 깻잎장아찌 > 전복튀김 순으로 올려 반으로 접어 감싼다.\r\n\r\n12. 김치전복말이를 적당한 크기로 썰어 접시에 담는다.\r\n\r\n13. 전복갈비찜에 전분물을 푼다.\r\n\r\n14. 전복갈비찜을 그릇에 담아낸다.'),
	(515, '믿어주길 바라♬', NULL, 1, '1. 소금을 넣은 끓는 물에 달걀을 삶는다.\r\n\r\n2. 뜨거운 물에 옥수수수프 분말을 갠 뒤, 얼음물 위에 놓고 우유를 섞어 크림소스를 만든다.\r\n\r\n3. 포도, 천혜향을 손질한 뒤 올리브오일, 석류식초, 후추를 넣고 절여둔다.\r\n\r\n4. 팬케이크 가루에 물을 섞어 반죽을 만든다.\r\n\r\n5. 팬케이크 반죽을 체에 내려 기름에 튀긴다.\r\n\r\n6. 튀긴 팬케이크 반죽을 체에 밭쳐 기름기를 뺀다.\r\n\r\n7. 절인 천혜향에 설탕을 뿌린 뒤 토치로 직화한다.\r\n\r\n8. 직화한 천혜향을 냉동실에 넣어 차게 만든다.\r\n\r\n9. 채끝등심에 후추, 소금으로 간을 한 뒤 기름을 두른 팬에 굽는다.\r\n\r\n10. 블랙올리브 페이스트, 된장, 올리브오일, 설탕, 식초, 다진 마늘,\r\n잘게 썬 무장아찌와 고추장아찌를 섞어 된장소스를 만든다.\r\n\r\n11. 해시 브라운 포테이토를 원형틀로 찍어낸 뒤 기름에 튀긴다.\r\n\r\n12. 튀긴 해시 브라운 포테이토를 체에 밭쳐 기름기를 뺀다.\r\n\r\n13. 삶은 달걀을 으깬 뒤 마요네즈, 소금, 후추를 섞어 소스를 만든다.\r\n\r\n14. 접시에 차게 식힌 크림소스를 두르고, 튀긴 해시 브라운 포테이토를 올린다.\r\n\r\n15. 14에 달걀마요네즈소스를 올린 뒤 절인 포도를 곁들인다.\r\n\r\n16. 15 위에 캐비아와 잘게 썬 대파를 올린다.\r\n\r\n17. 차게 만든 천혜향에 식힌 크림소스를 붓는다.\r\n\r\n18. 17 위에 튀긴 팬케이크 반죽, 말린 과일칩을 올린다.\r\n\r\n19. 채 썬 양파, 대파를 된장소스에 넣고 버무린다.\r\n\r\n20. 적당한 크기로 썬 채끝등심을 접시에 담고, 된장소스에 버무린 채소를 올린다.\r\n\r\n21. 스테이크 위에 얇게 썬 양파를 올린다.\r\n\r\n22. 스테이크 위에 참기름을 뿌려 마무리한다.'),
	(516, '아임 쇼 맨♬', NULL, 1, '1. 연시에 물과 마누카 꿀을 넣고, 얼음물 위에 올려 차게 만든다.\r\n\r\n2. 황태포, 해시 브라운 포테이토를 전자레인지에 넣고 돌린다.\r\n\r\n3. 잘게 다진 양파에 레몬즙, 마요네즈를 넣고 섞는다.\r\n\r\n4. 해시 브라운 포테이토를 으깬 뒤, 마요네즈 소스를 넣고 섞는다.\r\n\r\n5. 황태포를 믹서기에 간 뒤, 양념한 해시 브라운 포테이토와 함께 섞는다.\r\n\r\n6. 굴비를 손질한다.\r\n\r\n7. 손질한 굴비에 밀가루를 묻힌 뒤, 기름을 두른 팬에 굽다가 청주를 붓는다.\r\n\r\n8. 삼겹살을 적당한 크기로 썰어 냄비에 넣고 굽는다.\r\n\r\n9. 굽던 삼겹살에 손질한 마늘, 고추, 대파를 넣고 볶는다.\r\n\r\n10. 볶던 재료에 고춧가루와 구운 굴비, 간장, 맛술, 물, 설탕을 넣고 조린다.\r\n\r\n11. 파프리카는 껍질을 벗겨내고 잘게 다진다.\r\n\r\n12. 잘게 다진 파프리카에 소금, 매실원액을 넣고 섞는다.\r\n\r\n13. 접시에 양념한 해시 브라운 포테이토를 담고, 매실원액과 섞은 파프리카를 곁들인다.\r\n\r\n14. 양념한 해시 브라운 포테이토에 캐비아를 올린다.\r\n\r\n15. 달걀노른자만 분리한 뒤 전자레인지에 돌려 익힌다.\r\n\r\n16. 꿀에 절인 연시, 잘게 다진 참외를 그릇에 담는다.\r\n\r\n17. 매실원액에 연유를 섞은 뒤, 연시 위에 뿌려준다.\r\n\r\n18. 굴비삼겹살조림을 접시에 담는다.\r\n\r\n19. 익힌 달걀노른자를 잘게 부순 뒤 캐비아 위에 올린다.'),
	(517, '골뱅이 완전히 새됐어♬', NULL, 1, '1. 핸드블렌더에 밀가루, 뜨거운 물을 넣고 돌려 익반죽한다.\r\n\r\n2. 반죽을 치댄 뒤, 숙성시킨다.\r\n\r\n3. 숙성한 반죽을 밀대로 얇게 민 뒤, 원형틀로 찍어 만두피를 만든다.\r\n\r\n4. 핸드블랜더에 골뱅이국물과 녹인 버터, 적당한 크기로 썬 삼겹살을 넣고 간다.\r\n\r\n5. 간 삼겹살에 적당한 크기로 썬 골뱅이와 씻은 김치, 설탕, 고춧가루, 간장을 넣고 섞어 만두소를 만든다.\r\n\r\n6. 만두피에 골뱅이만두소를 넣고 모양을 빚는다.\r\n\r\n7. 기름을 두른 팬에 골뱅이만두를 굽는다.\r\n\r\n8. 골뱅이만두를 굽는 팬에 전분물을 붓는다.\r\n\r\n9. 분홍소시지를 적당한 크기로 썬 뒤, 골뱅이날개만두에 곁들이고 뚜껑을 덮어 굽는다.\r\n\r\n10. 식초, 설탕, 간장, 잘게 썬 청양고추를 섞어 초간장을 만든다.\r\n\r\n11. 골뱅이날개만두를 접시에 담고 초간장을 곁들여 낸다.'),
	(518, '골뱅이 프린세스', NULL, 1, '1. 골뱅이와 비엔나소시지를 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 팬에 골뱅이와 비엔나소시지를 볶는다.\r\n\r\n3. 볶던 재료에 버터, 편으로 썬 마늘, 소금, 후추를 넣고 볶는다.\r\n\r\n4. 밀가루, 버터, 플레인요거트를 넣고 치대어 반죽을 만든 뒤, 밀대로 얇게 민다.\r\n\r\n5. 볶던 재료에 화이트와인, 간장, 전분물을 넣고 끓인다.\r\n\r\n6. 그릇에 골뱅이볶음을 담고, 크림치즈를 올린다.\r\n\r\n7. 골뱅이볶음이 담긴 그릇 위에 얇게 민 반죽을 씌운 뒤, 모차렐라치즈를 얹는다.\r\n\r\n8. 7을 오븐에 굽는다.\r\n\r\n9. 빵 반죽을 덮어 오븐에 구운 골뱅이볶음을 꺼낸 뒤, 빵 위에 버터를 바른다.\r\n\r\n10. 청양고추를 어슷하게 썰어 빵 위에 올려 마무리한다.'),
	(519, '맵단세트', NULL, 1, '1. 버터를 두른 팬에 적당한 크기로 썬 양파와 대파를 볶는다.\r\n\r\n2. 볶던 채소에 고춧가루, 고추장, 간장을 넣고 끓여 떡볶이 양념을 만든다.\r\n\r\n3. 떡볶이 양념에 불린 떡국떡과 물을 넣고 끓인다.\r\n\r\n4. 통삼겹살을 적당한 크기로 썬다.\r\n\r\n5. 적당한 크기로 썬 통삼겹살에 맛소금을 뿌린 뒤, 전분을 골고루 입힌다.\r\n\r\n6. 전분을 입힌 통삼겹살을 기름에 튀긴다.\r\n\r\n7. 사과는 껍질을 벗겨내고, 적당한 크기로 썰어 핸드블랜더로 곱게 간다.\r\n\r\n8. 삼겹살튀김을 체에 밭쳐 기름을 빼낸다.\r\n\r\n9. 삼겹살튀김에 곱게 간 사과를 넣고 볶는다.\r\n\r\n10. 껍질을 벗긴 사과를 얇게 썰어 팬에 넣고 함께 볶는다.\r\n\r\n11. 10에 설탕과 계피가루를 넣고 볶는다.\r\n\r\n12. 끓이던 떡볶이에 맛소금을 넣는다.\r\n\r\n13. 끓이던 떡볶이에 짜장라면수프를 넣는다.\r\n\r\n14. 끓이던 떡볶이에 설탕을 넣는다.\r\n\r\n15. 그릇에 삼겹살사과볶음과 떡볶이를 각각 담는다.'),
	(520, '니가 젓소', NULL, 1, '1. 전분, 밀가루와 달걀, 물을 섞어 튀김옷을 만든다.\r\n\r\n2. 분홍소시지를 적당한 크기로 썰어 튀김옷을 입힌다.\r\n\r\n3. 떡볶이 떡을 반으로 자른다.\r\n\r\n4. 기름을 두른 팬에 오징어젓, 고춧가루, 설탕을 넣고 볶다가 떡볶이 떡과 물을 넣고 볶는다.\r\n\r\n5. 채소수프분말을 체에 밭쳐 채소건더기를 걸러낸다.\r\n\r\n6. 끓는 물에 채소수프 분말을 풀어 끓인다.\r\n\r\n7. 튀김옷을 입힌 분홍소시지를 기름에 넣고 튀긴다.\r\n\r\n8. 케첩, 설탕, 다진 마늘을 섞은 뒤, 기름을 두른 팬에 볶아 강정소스를 만든다.\r\n\r\n9. 강정소스에 채 썬 양파와 잘게 다진 견과류멸치볶음을 넣는다.\r\n\r\n10. 강정소스에 분홍소시지튀김, 물엿, 참기름을 넣고 섞어 소시지강정을 만든다.\r\n\r\n11. 잘게 썬 고추와 낙지젓을 떡볶이에 넣고 볶는다.\r\n\r\n12. 그릇에 소시지강정과 젓갈떡볶이를 각각 담고, 소시지강정 위에 잘게 썬 대파를 올린다.\r\n\r\n13. 끓이던 수프에 모차렐라치즈를 넣어 크림치즈소스를 만든다.\r\n\r\n14. 크림치즈소스를 그릇에 담고, 고춧가루를 뿌려 마무리한다.'),
	(521, '오늘 밤 안주는 나야 나♬', NULL, 1, '1. 냄비에 설탕과 물을 넣고 끓여 시럽을 만든다.\r\n\r\n2. 핸드블렌더에 달걀노른자, 밀가루, 플레인 요거트, 소금, 설탕,\r\n버터를 넣고 갈아 타르트 반죽을 만든다.\r\n\r\n3. 달걀흰자에 레몬즙을 넣고 거품기로 저어 머랭을 친다.\r\n\r\n4. 타르트 반죽에 밀가루를 더해 치댄다.\r\n\r\n5. 타르트 반죽을 적당량 떼어낸 뒤, 마른 팬에 넓게 펼쳐 굽는다.\r\n\r\n6. 레몬즙을 넣은 달걀흰자에 끓인 시럽을 부어가며 저어 이탈리안 머랭을 만든다.\r\n\r\n7. 핸드블렌더에 손질한 아보카도, 마스카르포네 치즈, 레몬즙, 설탕을 넣고 갈아 아보카도 크림을 만든다.\r\n\r\n8. 접시에 구운 타르트 반죽을 올린다.\r\n\r\n9. 아보카도 크림에 이탈리안 머랭을 넣고 섞는다.\r\n\r\n10. 이탈리안 머랭을 섞은 아보카도 크림을 구운 타르트 위에 얹는다.\r\n\r\n11. 10 위에 이탈리안 머랭을 얹고 토치로 직화한다.\r\n\r\n12. 끓인 시럽을 포크로 가늘고 길게 늘어뜨려 엔젤헤어를 만든다.\r\n\r\n13. 엔젤헤어를 동그랗게 뭉친 뒤, 구운 이탈리안 머랭 위에 올려 완성한다.'),
	(522, '안심하고 드세요', NULL, 1, '1. 비닐팩에 올리브 오일을 뿌리고, 안심을 넣는다.\r\n\r\n2. 비닐팩에 넣은 안심을 고기망치로 두드려 얇게 편다.\r\n\r\n3. 고기망치로 편 고기를 칼등으로 두드리고 후추로 간을 한다.\r\n\r\n4. 손질한 안심을 적당한 크기로 썰어 소금으로 간을 한다.\r\n\r\n5. 허브크림치즈, 트러플 치즈소스, 과일치즈, 고다치즈를 넣고 돌돌 말아 고정한다.\r\n\r\n6. 핸드블렌더에 카사바칩을 넣고 간다.\r\n\r\n7. 달걀을 풀어 달걀물을 만든다.\r\n\r\n8. 안심치즈말이에 밀가루>달걀물>카사바칩 가루 순으로 튀김옷을 입힌 뒤, 기름에 튀긴다.\r\n\r\n9. 안심치즈말이를 튀기는 기름에 타임을 넣고 함께 튀겨 향을 입힌다.\r\n\r\n10. 튀긴 타임을 접시에 깔고 플레인 요거트와 블루베리 퓌레, 발사믹 식초를 곁들인다.\r\n\r\n11. 접시에 안심치즈말이를 올리고 레몬즙을 뿌린다.\r\n\r\n12. 안심치즈 말이 위에 칼라만시원액을 뿌려 완성한다.'),
	(523, '울면 안 돼♬', NULL, 1, '1. 자차이를 물에 담가 짠기를 뺀다.\n\n2. 항정살을 적당한 크기로 썰어 끓는 물에 데친다.\n\n3. 데친 항정살을 다른 냄비로 옮겨 육수를 내고, 적당한 크기로 썬 관자를 끓는 물에 데친다.\n\n4. 핸드블렌더에 새우, 버터, 전분, 달걀흰자를 넣고 갈아 완자 반죽을 만든다.\n\n5. 물에 담가뒀던 자차이를 건져 물기를 짜고 잘게 썬다.\n\n6. 완자 반죽에 잘게 썬 자차이를 넣고 섞는다.\n\n7. 완자 반죽을 동그랗게 떼어 기름에 튀긴다.\n\n8. 인스턴트 쌀국수면을 뜨거운 물에 불린다.\n\n9. 항정살 육수에 소금으로 간을 한다.\n\n10. 간을 한 육수에 적당한 크기로 썬 아스파라거스와 양파를 넣고 끓여 울면 국물을 만든다.\n\n11. 데친 관자를 체에 밭쳐 물기를 뺀다.\n\n12. 불린 쌀국수면을 찬물에 헹군 뒤 물기를 짜고 그릇에 담는다.\n\n13. 튀긴 새우완자와 데친 관자를 쌀국수면 위에 올린다.\n\n14. 울면 국물에 전분물을 조금씩 넣어가며 점도를 조절한다.\n\n15. 울면 국물에 달걀물을 풀어 넣는다.\n\n16. 해물 쌀국수 위에 울면 국물을 부어 완성한다.'),
	(524, '수프 원샷', NULL, 1, '1. 당근을 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n2. 적당한 크기로 썬 사과를 팬에 넣고 당근과 함께 볶는다.\r\n\r\n3. 핸드블렌더에 손질한 아보카도, 마스카르포네 치즈, 페퍼민트, 레몬즙을 넣는다.\r\n\r\n4. 3에 애플망고 요거트, 청양고추, 소금, 후추, 물을 더해 갈아 차가운 아보카도수프를 만든다.\r\n\r\n5. 볶던 당근과 사과에 물을 붓고 끓여 당근사과수프를 만든다.\r\n\r\n6. 당근사과수프에 후추, 생강청, 소금을 넣어 간한다.\r\n\r\n7. 간을 한 당근사과수프에 고구마죽과 레몬즙을 넣어 함께 끓인다.\r\n\r\n8. 라임, 레몬, 아보카도를 적당한 크기로 썬다.\r\n\r\n9. 썰어놓은 아보카도에 레몬즙과 트러플 소금을 뿌린다.\r\n\r\n10. 샷잔에 차가운 아보카도수프를 담는다.\r\n\r\n11. 아보카도수프 위에 레몬조각과 트러플 소금을 얹는다.\r\n\r\n12. 적당한 크기로 썬 대파를 당근사과수프에 넣고 함께 끓인다.\r\n\r\n13. 끓인 당근사과수프를 핸드블렌더로 간다.\r\n\r\n14. 당근사과수프에 얼음을 넣어 식힌다.\r\n\r\n15. 샷잔에 당근사과수프를 담는다.\r\n\r\n16. 당근사과수프 위에 레몬즙을 뿌린 아보카도를 올린다.\r\n\r\n17. 로즈마리, 페퍼민트, 타임, 라임으로 데커레이션 한다.'),
	(525, '에너지닭', NULL, 1, '1. 닭을 발골한다.\r\n\r\n2. 발골한 닭 위에 소금, 후추를 뿌린 뒤 고기망치로 얇게 펴고 밀가루를 묻힌다.\r\n\r\n3. 기름을 두른 팬에 닭을 올리고 종이포일을 덮은 뒤, 무거운 것으로 눌러 편평하게 굽는다.\r\n\r\n4. 양파를 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n5. 대파, 애호박, 파프리카를 적당한 크기로 썬 뒤 양파와 함께 볶는다.\r\n\r\n6. 채소를 볶던 팬에 적당한 크기로 썬 베이컨을 넣고 함께 볶는다.\r\n\r\n7. 6에 떡갈비와 잘게 썬 통조림 햄, 다진 마늘을 넣고 함께 볶는다.\r\n\r\n8. 닭을 뒤집어 바삭하게 굽는다.\r\n\r\n9. 냄비에 버터, 밀가루, 우유, 슬라이스 체다치즈를 넣고 끓여 치즈소스를 만든다.\r\n\r\n10. 양배추를 가늘게 채 썬다.\r\n\r\n11. 핸드블렌더로 피클을 간다.\r\n\r\n12. 볼에 마요네즈, 양배추채, 곱게 간 피클을 넣고 버무리다가 소금을 뿌려 코울슬로를 만든다.\r\n\r\n13. 구운 닭을 포를 떠서 껍질부분과 살부분을 나눈다.\r\n\r\n14. 그릇에 닭 살코기 > 치즈소스 > 고기채소볶음 > 치즈소스 > 구운 닭껍질 순으로 켜켜이 쌓는다.\r\n\r\n15. 구운 닭 껍질을 올린다.\r\n\r\n16. 마요네즈, 쌈장, 머스터드소스, 식초, 케첩, 후추, 설탕, 레몬즙을 섞어 특제소스를 만든다.\r\n\r\n17. 특제소스와 양배추 코울슬로를 각각 접시에 담아낸다.\r\n\r\n18. 닭 라자냐 위에 모차렐라치즈를 뿌린 뒤 토치로 직화한다.'),
	(526, '오삼포테이토', NULL, 1, '1. 양파를 채 썬다.\r\n\r\n2. 감자는 껍질을 벗겨낸 뒤, 적당한 크기로 썬다.\r\n\r\n3. 볼에 감자, 뜨거운 물, 통마늘을 넣은 뒤 랩으로 감싸 전자레인지에 돌려 익힌다.\r\n\r\n4. 고추를 잘게 썬다.\r\n\r\n5. 기름을 두른 팬에 손질한 양파와 고추, 오렌지즙, 소금을 넣고 끓인다.\r\n\r\n6. 삼겹살은 비계를 떼어내고, 살코기에 소금과 후추를 뿌린 뒤 밀가루를 묻힌다.\r\n\r\n7. 기름을 넉넉하게 두른 팬에 삼겹살을 튀기듯이 굽는다.\r\n\r\n8. 오렌지소스에 구운 삼겹살을 넣고 조린다.\r\n\r\n9. 익힌 감자와 우유를 냄비에 넣고, 으깨면서 익힌다.\r\n\r\n10. 매시드 포테이토에 슬라이스 체다치즈와 버터를 넣고 으깨며 섞는다.\r\n\r\n11. 매시드 포테이토를 체에 내린다.\r\n\r\n12. 기름을 두른 팬에 베이컨을 바삭하게 구운 뒤, 잘게 썬다.\r\n\r\n13. 매시드포테이토에 구운 베이컨과 소금을 넣고 섞는다.\r\n\r\n14. 13에 잘게 썬 대파를 넣고 섞는다.\r\n\r\n15. 베이컨포테이토를 접시에 담는다.\r\n\r\n16. 베이컨을 구운 팬에 방울토마토를 굽는다.\r\n\r\n17. 베이컨포테이토 위에 오렌지삼겹살을 올린 뒤, 오렌지소스를 붓는다.\r\n\r\n18. 접시에 구운 방울토마토를 올리고, 잘게 썬 대파를 뿌려 마무리한다.'),
	(527, 'shake it♬ 버거', NULL, 1, '1. 핸드블렌더에 떡갈비와 삼겹살을 3:1 비율로 넣고 간다.\r\n\r\n2. 감자를 적당한 크기로 썬 뒤, 기름에 튀긴다.\r\n\r\n3. 마요네즈, 머스터드소스, 잘게 썬 피클, 레몬즙, 후추를 넣고 섞어 타르타르소스를 만든다.\r\n\r\n4. 슬라이스 체다치즈를 볼에 담은 뒤, 전자레인지에 돌려 녹인다.\r\n\r\n5. 기름을 두른 팬에 잘게 다진 양파를 볶는다.\r\n\r\n6. 핸드블렌더로 간 고기에 볶은 양파, 후추를 넣고 섞어 패티 반죽을 만든다.\r\n\r\n7. 양상추와 방울토마토를 잘게 썬다.\r\n\r\n8. 잘게 썬 양상추와 방울토마토를 타르타르소스와 섞어 샐러드를 만든다.\r\n\r\n9. 튀긴 감자를 건져낸 뒤, 냉동실에 넣고 차게 식힌다.\r\n\r\n10. 기름을 두른 팬에 적당한 크기로 썬 베이컨을 굽는다.\r\n\r\n11. 베이컨을 굽는 팬에 채 썬 양파를 굽는다.\r\n\r\n12. 패티 반죽을 적당한 크기로 빚어 팬에 넣고 함께 굽는다.\r\n\r\n13. 냉동실에서 식힌 감자튀김을 다시 한 번 튀긴다.\r\n\r\n14. 구운 베이컨과 양파를 건져 기름기를 뺀다.\r\n\r\n15. 패티 위에 슬라이스 체다치즈를 올려 치즈를 녹인다.\r\n\r\n16. 모닝빵을 반으로 잘라 안쪽만 굽는다.\r\n\r\n17. 감자튀김을 건져 소금으로 간하고 접시에 담는다.\r\n\r\n18. 구운 모닝빵 위에 샐러드 > 치즈를 올려 구운 패티 > 구운 베이컨&양파를 차례로 올리고 빵으로 덮어 버거를 만든다.\r\n\r\n19. 감자튀김 위에 녹인 체다치즈를 뿌려 마무리한다.'),
	(528, '무~보이♬', NULL, 1, '1. 방울토마토를 끓는 물에 데친 뒤, 찬물에 담가 식힌다.\r\n\r\n2. 국거리용 소고기와 베이컨을 잘게 썬 뒤, 기름을 두른 팬에 볶는다.\r\n\r\n3. 양파, 대파, 당근을 잘게 썰어 볶던 고기와 함께 볶다가 후추로 간 한다.\r\n\r\n4. 무와 대파 흰 부분을 얇고 가늘게 썬다.\r\n\r\n5. 파스타면을 삶는다.\r\n\r\n6. 볶던 재료에 레드와인, 면수를 넣고 끓인다.\r\n\r\n7. 6에 데친 방울토마토의 껍질을 벗기고 으깨어 넣어 볼로네제 소스를 만든다.\r\n\r\n8. 기름을 두른 팬에 으깬 마늘을 볶다가 채 썬 무와 대파를 넣고 소금을 뿌려 볶는다.\r\n\r\n9. 접시 위에 원형틀을 올린 뒤, 볶은 무&대파면을 넣는다.\r\n\r\n10. 끓는 물을 한 방향으로 저은 뒤, 가운데 달걀을 깨뜨려 넣어 수란을 만든다.\r\n\r\n11. 수란에 소금으로 간을 한다.\r\n\r\n12. 삶은 파스타면에 올리브오일, 볼로네제소스를 넣고 버무린다.\r\n\r\n13. 원형틀에 담긴 무&대파면 위에 볼로네제소스에 버무린 파스타면을 넣는다.\r\n\r\n14. 볼로네제소스를 접시에 듬뿍 담는다.\r\n\r\n15. 수란을 건져 파스타 위에 올린다.\r\n\r\n16. 원형틀을 빼내어 마무리한다.'),
	(529, '미스동파육', NULL, 1, '1. 기름을 두른 팬에 적당한 크기로 썬 삼겹살과 대파를 넣고 볶는다.\r\n\r\n2. 볶던 재료에 간장, 청주를 넣고 청양고추를 썰어 넣는다.\r\n\r\n3. 2에 다진 마늘, 생강즙, 물, 굴소스, 후추, 설탕을 넣고 끓인다.\r\n\r\n4. 끓인 재료를 압력솥에 넣고 찐다.\r\n\r\n5. 바나나는 껍질을 벗기고 적당한 크기로 썰어 밀가루를 묻힌다.\r\n\r\n6. 밀가루를 묻힌 바나나에 물을 뿌려준 뒤, 다시 한 번 밀가루를 묻힌다.\r\n\r\n7. 밀가루를 묻힌 바나나를 기름에 튀긴다.\r\n\r\n8. 쟁반에 들기름을 묻힌다.\r\n\r\n9. 마른 팬에 설탕을 녹인다.\r\n\r\n10. 기름에 튀긴 바나나를 체에 밭쳐 기름기를 뺀다.\r\n\r\n11. 녹인 설탕에 바나나튀김을 버무린 뒤, 들기름을 묻힌 쟁반에 부어 식힌다.\r\n\r\n12. 접시에 바나나맛탕을 담는다.\r\n\r\n13. 쌈추를 적당한 크기로 썰어 데친 뒤, 접시에 담는다.\r\n\r\n14. 쌈추를 담은 접시에 동파육과 육수를 담아낸다.'),
	(530, '미트코리아 진', NULL, 1, '1. 마른 팬에 모차렐라치즈를 넓게 펴서 굽다가, 잘게 썬 청양고추를 올린다.\r\n\r\n2. 핸드블렌더에 적당한 크기로 썬 삼겹살과 살치살을 넣고 곱게 갈아 반죽을 만든다.\r\n\r\n3. 적당한 크기로 썬 식빵에 우유를 부어 불린 뒤, 고기반죽과 함께 섞는다.\r\n\r\n4. 3에 소금, 후추로 간 하고 네모나게 모양을 잡는다.\r\n\r\n5. 기름을 두른 팬에 모양을 잡은 고기반죽을 구워 미트로프를 만든다.\r\n\r\n6. 기름을 두른 팬에 채 썬 양파와 으깬 마늘을 볶는다.\r\n\r\n7. 볶던 채소에 토마토소스, 물, 크림소스, 우유를 넣고 끓여 로제소스를 만든다.\r\n\r\n8. 로제소스를 체에 밭쳐 건더기를 걸러내고 다시 한 번 끓인다.\r\n\r\n9. 넓게 펴서 구운 모차렐라치즈 위에 미트로프를 올려 감싼다.\r\n\r\n10. 치즈로 감싼 미트로프를 뚜껑을 덮어 굽는다.\r\n\r\n11. 튀김과자를 잘게 부순다.\r\n\r\n12. 사과를 얇게 썬 뒤, 설탕을 뿌리고 밀가루를 묻힌다.\r\n\r\n13. 밀가루를 묻힌 사과에 달걀물을 묻히고 튀김과자가루를 입혀 기름에 튀긴다.\r\n\r\n14. 접시에 로제소스를 깔고 치즈로 감싼 미트로프를 올린다.\r\n\r\n15. 다른 접시에 사과튀김을 담아낸다.'),
	(531, '가지에 빠져빠져♬', NULL, 1, '1. 가지를 반으로 갈라 속을 파낸다.\r\n\r\n2. 속을 파낸 가지에 밀가루를 묻힌 뒤 기름에 튀긴다.\r\n\r\n3. 애호박과 가지를 적당한 크기로 썬 뒤, 밀가루를 묻혀 기름에 튀긴다.\r\n\r\n4. 기름을 두른 팬에 으깬 마늘, 잘게 썬 애호박과 새송이버섯을 볶는다.\r\n\r\n5. 볶던 채소에 양배추를 채썰어 넣고 소금, 후추를 뿌려 볶는다.\r\n\r\n6. 즉석밥을 전자레인지에 넣고 돌린다.\r\n\r\n7. 랩 위에 훈제연어를 넓게 펴고, 크림치즈를 올린다.\r\n\r\n8. 볼에 참기름, 즉석밥, 볶은 애호박, 새송이버섯, 양배추, 소금, 후리카케를 넣고 섞는다.\r\n\r\n9. 크림치즈를 올린 훈제연어 위에 양념한 밥을 올리고 돌돌 만다.\r\n\r\n10. 튀긴 가지와 애호박을 건져 기름기를 뺀다.\r\n\r\n11. 토마토소스, 식초, 올리브오일, 레몬즙, 설탕을 섞어 소스를 만든다.\r\n\r\n12. 튀긴 채소에 소금으로 간을 하고, 소스에 버무린다.\r\n\r\n13. 튀긴 가지를 건져낸 뒤 소금, 후추를 뿌린다.\r\n\r\n14. 접시에 튀긴 가지를 깔고, 소스에 버무린 튀긴 채소를 올린다.\r\n\r\n15. 14 위에 훈제연어롤을 올리고 소스에 버무린 튀긴 채소를 곁들인다.\r\n\r\n16. 요거트에 레몬즙, 후추를 뿌려 요거트소스를 만든다.\r\n\r\n17. 훈제연어롤 위에 요거트소스를 뿌려 마무리한다.'),
	(532, '난 이제 더 이상 찌개가 아니에요♬', NULL, 1, '1. 끓는 물에 소금을 넣고, 양배추를 데친다.\r\n\r\n2. 데친 양배추를 얼음물에 담가 식힌다.\r\n\r\n3. 기름을 두른 팬에 다진 마늘과 적당한 크기로 썬 삼겹살을 넣고 볶는다.\r\n\r\n4. 삼겹살에 잘게 썬 김치를 넣고 함께 볶다가 물을 붓고 끓인다.\r\n\r\n5. 닭가슴살을 얇게 펴고 칼집을 낸다.\r\n\r\n6. 핸드블렌더에 적당한 크기로 썬 채끝살, 대파 흰 부분, 굴소스, 청주, 달걀을 넣고 곱게 간다.\r\n\r\n7. 데친 양배추를 넓게 편 뒤 칼집을 낸 닭가슴살, 곱게 간 채끝살, 떡볶이 떡을 넣고 돌돌 만다.\r\n\r\n8. 양배추롤을 그릇에 담은 뒤, 김치찌개 국물을 넣고 전자레인지에 돌린다.\r\n\r\n9. 믹서기에 김치찌개를 넣고 곱게 갈아 김치찌개소스를 만든다.\r\n\r\n10. 김치찌개소스에 설탕을 넣고 끓인다.\r\n\r\n11. 끓는 물에 전복을 데친다.\r\n\r\n12. 데친 전복을 잘게 썰어 김치찌개소스에 넣는다.\r\n\r\n13. 양배추롤을 전자레인지에서 꺼내 적당한 크기로 썬다.\r\n\r\n14. 적당한 크기로 썬 양배추롤을 김치찌개소스에 넣고 조린다.\r\n\r\n15. 그릇에 김치찌개소스를 담고, 양배추롤을 올려 마무리한다.'),
	(533, '미식이볼 맛있당께', NULL, 1, '1. 달걀을 삶는다.\r\n\r\n2. 그릴팬에 청양고추를 굽는다.\r\n\r\n3. 소고기 양지, 삼겹살을 핸드블렌더에 곱게 간다.\r\n\r\n4. 곱게 간 고기에 소금, 후추로 간 한다.\r\n\r\n5. 간을 한 고기에 잘게 썬 양파를 넣는다.\r\n\r\n6. 식빵을 물에 불린 뒤, 물기를 짠다.\r\n\r\n7. 간 고기, 잘게 썬 양파, 불린 식빵을 잘 섞어준다.\r\n\r\n8. 7에 달걀과 밀가루를 넣고 치대 미트볼 반죽을 만든다.\r\n\r\n9. 미트볼 반죽을 적당한 크기로 빚은 뒤, 밀가루를 묻히고 기름을 두른 팬에 굽는다.\r\n\r\n10. 구운 청양고추에 식초, 올리브오일, 소금, 편마늘을 넣고 버무린다.\r\n\r\n11. 대파, 오이, 상추를 잘게 썰어 볼에 담고, 식초, 소금, 올리브오일에 버무려 샐러드를 만든다.\r\n\r\n12. 접시에 샐러드를 담고, 삶은 달걀을 반으로 잘라 곁들인다.\r\n\r\n13. 다른 접시에 구워서 오일에 버무린 청양고추를 담는다.\r\n\r\n14. 토마토를 적당한 크기로 썰어 샐러드에 곁들인다.\r\n\r\n15. 샐러드와 함께 미트볼을 담아낸다.\r\n\r\n16. 미트볼 위에 후추를 뿌려 마무리한다.'),
	(534, '이런 안주 파쌈?', NULL, 1, '1. 인스턴트 물만두의 피와 소를 분리한다.\r\n\r\n2. 적당한 크기로 썬 항정살과 인스턴트 고기구이, 다진 마늘, 물만두소, 두부를 핸드블렌더에 넣고 갈아 고기반죽을 만든다.\r\n\r\n3. 김 위에 깻잎을 깔고 고기반죽을 올려 돌돌 만다.\r\n\r\n4. 밀가루, 달걀, 소금을 섞어 튀김옷을 만든다.\r\n\r\n5. 김말이에 튀김옷을 입혀 기름 에 튀긴다.\r\n\r\n6. 마른 팬에 적당한 크기로 썬 차돌박이를 볶는다.\r\n\r\n7. 대파 흰 부분, 양파, 새송이버섯을 잘게 썰어 차돌박이와 함께 볶는다.\r\n\r\n8. 볶던 재료에 다진 마늘, 고춧가루, 고추장, 잘게 다진 청양고추, 된장을 넣고 볶아 차돌박이 강된장을 만든다.\r\n\r\n9. 대파 파란 부분을 적당한 크기로 썰어 그릇에 담는다.\r\n\r\n10. 대파 흰 부분을 가늘게 썰어 찬물에 담가둔다.\r\n\r\n11. 김말이튀김을 건진 뒤, 적당한 크기로 썰어 그릇에 담는다.\r\n\r\n12. 김말이튀김 위에 차돌박이 강된장을 얹는다.\r\n\r\n13. 찬물에 담가둔 대파를 올려 마무리한다.'),
	(535, '국밥 맞고등?', NULL, 1, '1. 적당한 크기로 썬 고등어를 기름에 튀긴다.\r\n\r\n2. 끓는 물에 튀긴 고등어와 들깻가루를 넣고 끓여 육수를 낸다.\r\n\r\n3. 대파, 차돌박이, 항정살을 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 팬에 손질한 차돌박이와 항정살, 대파, 다진 마늘을 넣고 볶는다.\r\n\r\n5. 볶던 재료에 고춧가루, 멸치액젓, 적당한 크기로 썬 새송이버섯을 넣는다.\r\n\r\n6. 5에 고등어육수를 체에 밭쳐 육수만 넣고 끓인다.\r\n\r\n7. 순대를 적당한 크기로 썰어 끓이던 재료에 넣는다.\r\n\r\n8. 육수를 낸 튀긴 고등어를 핸드블렌더로 곱게 간다.\r\n\r\n9. 곱게 간 고등어와 생강즙을 끓이던 재료에 넣어 고등엇국을 만든다.\r\n\r\n10. 전자레인지에 즉석밥을 돌린다.\r\n\r\n11. 오이를 적당한 크기로 썰어 고등엇국에 넣는다.\r\n\r\n12. 즉석밥을 가열된 뚝배기에 넣고, 고등엇국을 부어 끓인다.\r\n\r\n13. 고등어국밥과 청양고추, 고추장을 함께 낸다.'),
	(536, '소박한 국밥', NULL, 1, '1. 끓는 물에 인스턴트 양념닭발을 데친다.\r\n\r\n2. 항정살을 기름에 튀긴다.\r\n\r\n3. 데친 양념닭발을 체에 밭쳐 양념을 씻어내고, 튀긴 항정살과 함께 물에 넣고 끓여 고기육수를 낸다.\r\n\r\n4. 오이에 칼집을 내고 소금물에 끓여 절인다.\r\n\r\n5. 참기름을 두른 팬에 적당한 크기로 썬 양파와 대파, 으깬 마늘을 넣고 볶는다.\r\n\r\n6. 볶던 채소에 고기육수를 조금 붓고 끓인 뒤, 고기육수와 섞어 함께 끓인다.\r\n\r\n7. 순대를 적당한 크기로 썰어 넣는다.\r\n\r\n8. 끓이던 재료에 들깻가루, 새우젓, 잘게 썬 대파와 청양고추를 넣고 끓여 순댓국을 만든다.\r\n\r\n9. 소금물에 절인 오이를 얼음물에 담가 식힌다.\r\n\r\n10. 잘게 다진 갓김치에 김칫소, 식초를 넣고 섞어 김치 양념을 만든 뒤, 절인 오이를 버무린다.\r\n\r\n11. 전자레인지에 즉석밥을 돌린다.\r\n\r\n12. 김칫소, 고춧가루, 맛술, 후추, 다진 마늘, 잘게 썬 대파를 섞어 양념장을 만든다.\r\n\r\n13. 순댓국을 가열된 뚝배기에 옮겨 담고 끓이다가 소금으로 간 한다.\r\n\r\n14. 즉석밥을 순댓국에 넣고 끓인다.\r\n\r\n15. 오이소박이를 접시에 담는다.\r\n\r\n16. 다른 접시에 청양고추와 편으로 썬 마늘을 담는다.\r\n\r\n17. 순댓국에 들깻가루를 뿌린다.\r\n\r\n18. 순대국밥, 오이소박이, 청양고추와 마늘, 양념장, 새우젓을 함께 낸다.'),
	(537, '숙모닝', NULL, 1, '1. 두 개의 팬에 버터를 녹인다.\r\n\r\n2. 브리치즈를 반으로 가른 뒤, 물을 묻힌다.\r\n\r\n3. 물을 묻힌 브리치즈에 밀가루와 달걀물을 차례로 입히고 버터를 녹인 팬에 굽는다.\r\n\r\n4. 달걀물에 설탕, 소금, 밀가루를 넣고 섞어 팬케이크 반죽을 만든다.\r\n\r\n5. 팬케이크 반죽에 우유를 부어가며 섞는다.\r\n\r\n6. 버터를 녹인 팬에 팬케이크를 굽는다.\r\n\r\n7. 마른 팬에 설탕을 녹인다.\r\n\r\n8. 녹인 설탕에 뜨거운 물, 버터를 넣고 끓여 캐러멜소스를 만든다.\r\n\r\n9. 구운 팬케이크와 구운 브리치즈의 기름기를 뺀다.\r\n\r\n10. 모과청과 꿀을 섞어 모과소스를 만든다.\r\n\r\n11. 접시에 팬케이크>모과소스>구운 브리치즈>팬케이크를 차례로 쌓는다.\r\n\r\n12. 캐러멜소스에 레몬즙을 넣는다.\r\n\r\n13. 쌓은 팬케이크 위에 캐러멜소스를 바르고 구운 브리치즈>팬케이크를 덮는다.\r\n\r\n14. 팬케이크 위에 캐러멜소스를 뿌린다.\r\n\r\n15. 접시에 모과소스를 흩뿌려 마무리한다.'),
	(538, '숙나잇', NULL, 1, '1. 끓는 물에 당면을 삶는다.\r\n\r\n2. 인스턴트 만두의 피와 소를 분리한다.\r\n\r\n3. 기름을 두른 팬에 다진 마늘과 만두소를 볶다가, 달걀을 넣고 함께 볶는다.\r\n\r\n4. 볶던 재료에 콜라를 붓고 끓이다가 간장을 넣는다.\r\n\r\n5. 끓이던 재료에 물과 고추장을 넣고 끓여 팟타이소스를 만든다.\r\n\r\n6. 양파, 대파, 오이, 당근을 채 썬다.\r\n\r\n7. 어묵은 채 썰고, 청양고추는 잘게 썬다.\r\n\r\n8. 볼에 올리브오일, 칼라만시식초, 설탕, 다진 마늘, 소금, 후추, 잘게 썬 청양고추를 넣고 섞어 솜땀소스를 만든다.\r\n\r\n9. 게맛살을 잘게 찢는다.\r\n\r\n10. 볼에 잘게 찢은 게맛살과 채 썬 재료들을 넣고, 오징어포과자를 부숴 넣는다.\r\n\r\n11. 10에 솜땀소스를 붓고 섞는다.\r\n\r\n12. 솜땀을 접시에 담는다.\r\n\r\n13. 삶은 당면을 체에 밭쳐 거른 뒤, 찬물에 헹군다.\r\n\r\n14. 팟타이소스에 당면을 넣고 볶는다.\r\n\r\n15. 팟타이에 칼라만시식초를 넣는다.\r\n\r\n16. 팟타이를 접시에 담고 깨를 뿌려 완성한다.'),
	(539, '이길 만두 하지? 당근!', NULL, 1, '1. 당근을 적당한 크기로 썬다.\r\n\r\n2. 믹서기에 적당한 크기로 썬 당근을 넣고 간다.\r\n\r\n3. 곱게 간 당근을 면보에 짜서 당근즙과 밀가루를 섞어 만두피 반죽을 만든다.\r\n\r\n4. 핸드블렌더에 어묵, 버터, 게맛살을 넣고 곱게 간다.\r\n\r\n5. 곱게 간 당근에 잘게 썬 당근을 넣고 섞어 당근 만두소를 만든다.\r\n\r\n6. 오징어포과자를 곱게 간 뒤, 당근만두소에 넣고 섞는다.\r\n\r\n7. 당근만두피 반죽을 적당량 떼어낸 뒤, 밀대로 밀어 동그랗게 만든다.\r\n\r\n8. 당근만두피에 당근만두소를 넣고 만두를 빚는다.\r\n\r\n9. 끓는 물에 당근만두를 넣고 삶는다.\r\n\r\n10. 당근, 오이, 양파를 적당한 크기로 썬다.\r\n\r\n11. 팬에 물, 간장, 설탕, 칼라만시식초, 손질한 채소를 넣고 끓여 탕수소스를 만든다.\r\n\r\n12. 삶은 당근만두를 건져내 물기를 뺀다.\r\n\r\n13. 당근을 모양내 당근꽃을 만든다.\r\n\r\n14. 대파를 가늘게 채 썰어 그릇에 담고, 위에 당근꽃을 올린다.\r\n\r\n15. 당근만두를 그릇에 담는다.\r\n\r\n16. 탕수소스에 전분물을 부어 농도를 조절한다.\r\n\r\n17. 당근만두 위에 탕수소스를 부어 마무리한다.'),
	(540, '오늘 나~ 이길 거 맞지♬', NULL, 1, '1. 당근을 채 썬다.\r\n\r\n2. 팬에 버터, 채 썬 당근, 설탕을 넣고 볶는다.\r\n\r\n3. 다른 팬에 버터를 녹인 뒤, 다진 마늘을 볶아 갈릭버터를 만든다.\r\n\r\n4. 핸드블렌더에 밤식빵을 넣고 간다.\r\n\r\n5. 갈릭버터에 밤식빵가루를 넣고 볶는다.\r\n\r\n6. 냄비에 버터를 녹인 뒤, 밀가루를 볶아 루를 만든다.\r\n\r\n7. 루에 우유를 붓고 끓여 베샤멜소스를 만든다.\r\n\r\n8. 베샤멜소스에 크림치즈, 소금을 넣는다.\r\n\r\n9. 볼에 크림소스를 적당량 덜어낸 뒤, 남은 크림소스에 채 썬 당근과 게맛살, 우유를 넣는다.\r\n\r\n10. 9를 핸드블렌더로 곱게 갈아 당근수프를 만든다.\r\n\r\n11. 볶은 밤식빵가루를 다시 한 번 곱게 간다.\r\n\r\n12. 접시에 밤식빵가루를 담는다.\r\n\r\n13. 접시에 당근수프를 담는다.\r\n\r\n14. 밤식빵가루와 당근수프의 경계에 크림소스를 올린다.\r\n\r\n15. 설탕에 볶은 당근을 밤식빵가루 위에 얹는다.'),
	(541, '해산물이 맛있셧업♬', NULL, 1, '1. 기름을 두른 냄비에 마늘, 양파, 당근, 백합를 넣는다.\n\n2. 1에 화이트와인과 뜨거운 물을 붓고 끓여 채소조개육수를 낸다.\n\n3. 볼에 올리브오일, 적당한 크기로 썬 가지와 파프리카를 넣고 소금, 후추를 뿌려 버무린다.\n\n4. 양념한 가지와 파프리카를 그릴팬에 굽는다.\n\n5. 기름을 두른 팬에 잘게 다진 양파와 불린 렌틸콩을 넣고 소금으로 간한 뒤, 채소조개육수를 부어 끓인다.\n\n6. 데친 문어는 물결모양으로 썰고, 낙지는 채소조개육수에 데쳐 적당한 크기로 썬 뒤 올리브오일, 소금, 후추에 버무린다.\n\n7. 미나리에 올리브오일, 소금, 후추를 넣고 버무린다.\n\n8. 양념한 미나리를 구워 적당한 크기로 썬다.\n\n9. 레몬즙, 올리브오일, 설탕을 섞어 레몬오일소스를 만든다.\n\n10. 구운 파프리카를 잘게 썬다.\n\n11. 10을 레몬오일소스에 넣고 허브시즈닝을 뿌린 뒤, 올리브오일을 더해 파프리카허브소스를 만든다.\n\n12. 백합의 살을 발라낸 뒤 잘게 다진다.\n\n13. 조개육수에 끓이던 렌틸콩에 백합살, 레몬즙을 넣고 끓인다.\n\n14. 볼에 구운 가지와 미나리를 담은 뒤, 포일에 허브시즈닝을 담아 불을 붙이고 뚜껑을 닫아 훈연한다.\n\n15. 그릴팬에 양념한 문어와 낙지를 굽는다.\n\n16. 구운 문어를 접시에 담고, 렌틸콩을 올린다.\n\n17. 훈연한 가지와 미나리를 올리고, 파프리카허브소스를 얹은 뒤, 후추를 뿌려 마무리한다.'),
	(542, '잉글리쉿', NULL, 1, '1. 대파는 적당한 크기로 썰고 마늘은 으깬 뒤, 마른 냄비에 넣고 볶는다.\r\n\r\n2. 손질한 양파와 고추, 으깬 통후추를 냄비에 넣고 함께 볶는다.\r\n\r\n3. 볶던 재료에 화이트와인과 백합, 바지락살을 넣고 뚜껑을 닫아 끓인다.\r\n\r\n4. 3에 뜨거운 물을 넣고 끓인다.\r\n\r\n5. 조개육수를 체에 밭쳐 건더기와 육수를 분리한다.\r\n\r\n6. 백합의 살을 발라낸다.\r\n\r\n7. 끓는 물에 소금을 넣고, 불린 렌틸콩을 삶는다.\r\n\r\n8. 양파, 당근, 마늘을 잘게 썰어 기름을 두른 팬에 볶는다.\r\n\r\n9. 삶은 렌틸콩을 체에 밭쳐 물기를 뺀 뒤, 채소를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n10. 볶던 재료에 소금과 후추로 간을 한다.\r\n\r\n11. 양파, 당근, 파프리카, 애호박, 마늘을 적당한 크기로 썬다.\r\n\r\n12. 기름을 두른 팬에 손질한 채소를 볶는다.\r\n\r\n13. 손질한 채소를 볶던 팬에 불린 표고버섯도 적당한 크기로 썰어 넣고 함께 볶다가, 백합살과 바지락살을 넣고 볶는다.\r\n\r\n14. 볶던 재료에 조개육수를 부어가며 끓인다.\r\n\r\n15. 14에 허브시즈닝, 강황가루, 버터를 넣고 끓여 프리카세를 만든다.\r\n\r\n16. 볶은 렌틸콩을 접시에 담아낸다.\r\n\r\n17. 그릇에 프리카세를 담는다.\r\n\r\n18. 마른 팬에 감태를 구운 뒤, 잘게 찢어 프리카세 위에 올려 마무리한다.'),
	(543, '지천명 반상', NULL, 1, '1. 디포리를 토치로 직화한다.\r\n\r\n2. 끓는 물에 건새우와 직화한 디포리를 넣고 육수를 낸다.\r\n\r\n3. 냄비에 참기름을 두르고, 적당한 크기로 썬 불린 표고버섯, 대파, 양파, 으깬 마늘을 넣고 볶는다.\r\n\r\n4. 3에 낙지를 넣고 볶는다.\r\n\r\n5. 육수를 체에 밭쳐 건더기를 걸러내고, 볶던 재료에 붓고 끓여 연포탕을 만든다.\r\n\r\n6. 연포탕에 어간장을 넣는다.\r\n\r\n7. 연포탕에 적당한 크기로 썬 알배추와 고추를 넣은 뒤 뚜껑을 닫고 끓인다.\r\n\r\n8. 즉석밥을 전자레인지에 돌린다.\r\n\r\n9. 끓는 물에 소금을 넣고 불린 완두콩과 브로콜리를 데친다.\r\n\r\n10. 볼에 밀가루, 달걀노른자, 물을 넣고 섞어 달걀반죽을 만든다.\r\n\r\n11. 원형틀로 애호박의 속을 파내고, 밀가루를 묻힌 뒤 속에 명란젓을 끼워 적당한 크기로 썬다.\r\n\r\n12. 명란젓을 끼운 애호박에 밀가루와 달걀반죽을 순서대로 입힌 뒤 기름을 두른 팬에 굽는다.\r\n\r\n13. 석쇠에 식초를 바른 뒤, 참기름을 바른 가자미를 올리고 굽는다.\r\n\r\n14. 잘게 다진 명이나물장아찌, 명이나물장아찌 국물, 고춧가루, 깨, 참기름을 섞어 간장소스를 만든 뒤, 종지에 담아낸다.\r\n\r\n15. 뚝배기에 밥을 넣고 데친 완두콩과 브로콜리를 올린 뒤, 구운 가자미를 얹는다.\r\n\r\n16. 접시에 명란애호박전을 담는다.\r\n\r\n17. 연포탕에 감태를 풀어준 뒤 그릇에 담는다.\r\n\r\n18. 감태연포탕과 가자미솥밥, 명란애호박전을 함께 낸다.'),
	(544, '따르릉~ 내가 니 문어야♬', NULL, 1, '1. 압력솥에 물, 표고버섯 우린 물, 간장, 맛술, 청주, 설탕을 넣은 뒤, 데친 문어를 넣고 뚜껑을 닫아 조린다.\r\n\r\n2. 오이에 촘촘하게 칼집을 낸다.\r\n\r\n3. 소금물에 칼집 낸 오이를 절여둔다.\r\n\r\n4. 데친 문어를 물결무늬로 썬 뒤, 접시에 담는다.\r\n\r\n5. 채 썬 양파를 찬물에 헹군 뒤, 접시에 담는다.\r\n\r\n6. 끓는 물에 소금을 넣고 불린 미역을 데친다.\r\n\r\n7. 데친 미역을 찬물에 담가 식힌 뒤, 접시에 담는다.\r\n\r\n8. 즉석밥을 전자레인지에 돌린다.\r\n\r\n9. 볼에 식초, 설탕, 물을 넣고 섞어 초절임소스를 만든다.\r\n\r\n10. 소금에 절인 오이를 초절임소스에 담가둔다.\r\n\r\n11. 가지와 문어를 적당한 크기로 썬다.\r\n\r\n12. 밀가루와 물을 섞어 튀김옷을 만든 뒤, 적당한 크기로 썬 가지와 문어에 입혀 튀긴다.\r\n\r\n13. 가지튀김과 문어튀김을 건져낸 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n14. 식초, 설탕, 맛술, 간장, 레몬즙을 섞어 초회소스를 만든다.\r\n\r\n15. 초회소스에 마키베리분말을 넣고 섞은 뒤, 종지에 담아낸다.\r\n\r\n16. 초절임한 오이를 적당한 크기로 썰어 문어초회 접시에 담는다.\r\n\r\n17. 밥, 가지튀김, 문어튀김, 잘게 썬 미나리를 한데 넣는다.\r\n\r\n18. 압력솥에서 조린 문어조림을 적당한 크기로 썬 뒤 밥에 넣고, 조림양념도 함께 넣어 섞어준다.\r\n\r\n19. 문어조림밥을 그릇에 담아낸다.\r\n\r\n20. 문어초회와 문어조림밥을 함께 낸다.'),
	(545, 'M.S.G', NULL, 1, '1. 팬에 버터를 녹인다.\r\n\r\n2. 버터를 녹인 팬에 느타리버섯과 팽이버섯을 볶는다.\r\n\r\n3. 기름을 두른 팬에 잘게 썬 비엔나소시지를 굽는다.\r\n\r\n4. 기름을 두른 팬에 적당한 크기로 썬 소고기 등심을 볶는다.\r\n\r\n5. 채 썬 양파에 소금을 뿌려 섞은 뒤, 등심과 함께 볶는다.\r\n\r\n6. 볶은 등심과 양파를 체에 밭쳐 기름기를 뺀 뒤, 다시 볶는다.\r\n\r\n7. 볶던 버섯에 체에 밭쳐 내린 밀가루를 더해 함께 볶는다.\r\n\r\n8. 밀가루를 뿌려 볶은 버섯에 인스턴트 샤부샤부육수와 레드와인을 넣고 졸인다.\r\n\r\n9. 8에 물을 붓고 끓여 소스를 만든다.\r\n\r\n10. 소스에 구운 비엔나소시지를 넣고 함께 끓인다.\r\n\r\n11. 등심볶음에 모차렐라치즈를 넣고 섞는다.\r\n\r\n12. 접시에 원형틀을 올리고, 치즈등심볶음을 담는다.\r\n\r\n13. 통조림알감자를 적당한 크기로 썰어 소스에 넣고 끓인다.\r\n\r\n14. 치즈등심볶음 위에 소스를 붓는다.\r\n\r\n15. 파채를 얹어 마무리한다.'),
	(546, '샘희태닭', NULL, 1, '1. 닭을 손질한다.\r\n\r\n2. 표고버섯, 느타리버섯을 적당한 크기로 썬다.\r\n\r\n3. 닭뼈를 우려 닭육수를 낸다.\r\n\r\n4. 닭육수에 액상조미료를 넣는다.\r\n\r\n5. 손질한 닭에 칼집을 내고 후추와 소금을 뿌린다.\r\n\r\n6. 기름을 두른 팬에 닭을 굽는다.\r\n\r\n7. 기름을 두른 그릴 팬에 파김치와 대파 흰 부분을 굽는다.\r\n\r\n8. 양파를 잘게 썰어 닭을 굽는 팬에서 볶는다.\r\n\r\n9. 굽던 닭을 그릴팬으로 옮겨 굽는다.\r\n\r\n10. 볶던 양파에 손질한 표고버섯과 느타리버섯을 넣고 소금, 후추를 뿌려 볶는다.\r\n\r\n11. 볶던 버섯에 닭육수를 붓고 끓인다.\r\n\r\n12. 11에 구운 닭과 버터를 넣고 조린다.\r\n\r\n13. 닭을 조리는 소스에 통조림옥수수의 국물만 넣는다.\r\n\r\n14. 구운 파김치와 대파를 잘게 썬 뒤, 올리브오일과 후추를 뿌려 버무린다.\r\n\r\n15. 접시에 조린 닭을 올린다.\r\n\r\n16. 닭을 건져내고 팬에 남은 소스에 밀가루를 넣어 농도를 조절한다.\r\n\r\n17. 닭 위에 소스를 붓고 구운 파김치와 파를 올린다.'),
	(547, '복면라왕', NULL, 1, '1. 냄비에 인스턴트 사골곰탕을 붓고, 모시조개를 넣어 끓인다.\r\n\r\n2. 닭을 발골한다.\r\n\r\n3. 전분, 물, 달걀물, 식용유를 섞어 튀김옷을 만든다.\r\n\r\n4. 닭다리살에 튀김옷을 입혀 기름에 튀긴다.\r\n\r\n5. 생칼국수면을 삶는다.\r\n\r\n6. 핸드블렌더에 적당한 크기로 썬 닭가슴살과 전분, 버터, 잘게 썬 양파, 소금, 액상조미료, 달걀물을 넣고 갈아 완자반죽을 만든다.\r\n\r\n7. 칼국수면을 건져 찬물에 식힌다.\r\n\r\n8. 완자 반죽을 적당한 크기로 떼어 면수에 넣고 삶는다.\r\n\r\n9. 채 썬 양파에 물엿, 식초, 간장, 맛술, 잘게 썬 대파와 고추, 물, 참기름을 넣고 섞어 완자소스를 만든다.\r\n\r\n10. 삶은 닭가슴살 완자를 건져 접시에 담는다.\r\n\r\n11. 즉석밥을 전자레인지에 돌린다.\r\n\r\n12. 찬물에 식힌 칼국수면을 뜨거운 면수에 담가둔다.\r\n\r\n13. 대파, 마늘, 홍고추를 잘게 썰고, 절인 배추를 적당한 크기로 썬다.\r\n\r\n14. 기름을 두른 팬에 손질한 채소들과 고춧가루, 적당한 크기로 썬 애호박과 조개곰탕육수를 넣고 끓여 라조기소스를 만든다.\r\n\r\n15. 닭가슴살 완자 위에 완자소스를 붓는다.\r\n\r\n16. 칼국수면을 그릇에 담고, 조개곰탕육수를 붓는다.\r\n\r\n17. 튀긴 닭다리살을 라조기소스에 넣고 볶는다.\r\n\r\n18. 칼국수 위에 라조기를 부어 라조기탕면을 완성한다.'),
	(548, '렛츠고 짬짜면', NULL, 1, '1. 끓는 물에 소금을 넣은 뒤, 생칼국수면을 삶는다.\r\n\r\n2. 볼에 고춧가루, 적당한 크기로 썬 대파 흰 부분과 생강을 넣고 끓는 기름을 부어 고추기름을 낸다.\r\n\r\n3. 고추기름을 체에 밭쳐 건더기를 걸러낸 뒤, 팬에 두른다.\r\n\r\n4. 양파, 대파, 당근을 적당한 크기로 썬 뒤, 고추기름을 두른 팬에 넣고 볶는다.\r\n\r\n5. 볶던 채소에 생강을 넣고 볶는다.\r\n\r\n6. 표고버섯을 적당한 크기로 썰어 채소를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n7. 볶던 채소에 간장, 맛술, 설탕, 액상조미료를 넣는다.\r\n\r\n8. 즉석밥을 전자레인지에 돌린다.\r\n\r\n9. 팬에 고추기름을 두른 뒤, 칵테일새우, 바지락살, 낙지다리를 넣고 볶는다.\r\n\r\n10. 볶은 해산물을 채소를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n11. 삶은 칼국수면을 찬물에 헹군 뒤, 그릇에 담는다.\r\n\r\n12. 밥을 그릇에 담은 뒤, 깨를 올린다.\r\n\r\n13. 해산물채소볶음에 물, 후추, 전분물을 넣고 끓여 물짜장소스를 만든다.\r\n\r\n14. 면 위에 물짜장소스를 붓고, 파채와 깨를 올려 마무리한다.'),
	(549, '떡이라자냐', NULL, 1, '1. 끓는 물에 올리브오일을 뿌리고 라자냐면을 삶는다.\r\n\r\n2. 양파, 파프리카를 잘게 썬다.\r\n\r\n3. 기름을 두른 팬에 잘게 썬 양파, 다진 마늘, 잘게 썬 파프리카를 넣고 볶는다.\r\n\r\n4. 채소를 볶던 팬에 잘게 다진 수제돈가스와 후추, 적당한 크기로 썬 표고버섯을 넣고 볶는다.\r\n\r\n5. 볶던 재료에 토마토소스, 고춧가루, 크림소스를 넣고 볶는다.\r\n\r\n6. 핸드블렌더에 즉석밥, 옥수수통조림, 뜨거운 물, 소금을 넣고 갈아 옥수수떡반죽을 만든다.\r\n\r\n7. 꼬치에 가래떡을 꽂고 올리브오일을 바른 뒤, 석쇠에서 굽는다.\r\n\r\n8. 쟁반에 올리브오일을 두르고 라자냐면을 건져 놓는다.\r\n\r\n9. 아스파라거스를 데친 뒤 찬물에 담가둔다.\r\n\r\n10. 구운 가래떡을 적당한 크기로 썬다.\r\n\r\n11. 라자냐면을 원형틀로 찍어 모양을 낸다.\r\n\r\n12. 원형틀 안에 라자냐면>토마토돈가스볶음>라자냐면>옥수수떡반죽&구운 가래떡>슬라이스 체다치즈를 겹겹이 쌓은 뒤, 위에 라클렛치즈를 올린다.\r\n\r\n13. 12를 오븐에 굽는다.\r\n\r\n14. 달걀노른자와 바질페스토를 섞어 소스를 만든 뒤, 접시에 뿌린다.\r\n\r\n15. 블랙올리브를 곱게 갈아 접시에 올린다.\r\n\r\n16. 참외, 파프리카, 데친 아스파라거스를 잘게 썬다.\r\n\r\n17. 쌀국수면을 기름에 튀긴 뒤, 건져서 기름기를 뺀다.\r\n\r\n18. 오븐에서 라자냐를 꺼내 접시에 담는다.\r\n\r\n19. 데친 아스파라거스와, 잘게 썬 참외, 파프리카, 아스파라거스를 곁들인다.\r\n\r\n20. 튀긴 쌀국수면을 올려 마무리한다.'),
	(550, '육떡진 롤', NULL, 1, '1. 냄비 안에 고춧가루와 나무젓가락을 넣고, 나무젓가락에 불을 붙인 뒤 뚜껑을 덮어 고춧가루를 훈연한다.\r\n\r\n2. 가지에 칼집을 내고 토치로 직화한다.\r\n\r\n3. 기름을 두른 팬에 적당한 크기로 썬 양파와 파프리카를 볶는다.\r\n\r\n4. 다른 팬에 기름을 두른 뒤, 다진 돼지고기를 볶는다.\r\n\r\n5. 볶던 돼지고기에 레드와인을 붓고 볶는다.\r\n\r\n6. 볶던 채소와 돼지고기에 후추를 뿌린다.\r\n\r\n7. 팬에 버터를 녹인 뒤, 떡국떡을 볶는다.\r\n\r\n8. 볶은 채소와 돼지고기를 한 팬에 담고, 스테이크소스, 다진 마늘, 훈연한 고춧가루를 넣고 볶는다.\r\n\r\n9. 8에 버터에 볶은 떡, 마요네즈, 슬라이스 체다치즈, 라클렛치즈를 넣고 볶는다.\r\n\r\n10. 알배추의 밑둥을 밀대로 밀어 펴고, 볼에 담아 랩으로 감싼 뒤, 전자레인지에 돌린다.\r\n\r\n11. 핸드블렌더에 블랙올리브와 잣을 넣고 곱게 간다.\r\n\r\n12. 전자레인지에서 익힌 알배추를 김발 위에 편 뒤, 잣을 넣고 간 블랙올리브와 돼지고기 떡볶음을 넣고 돌돌 만다.\r\n\r\n13. 직화한 가지를 물에 씻어 탄 껍질을 벗겨낸다.\r\n\r\n14. 껍질을 벗긴 가지를 핸드블렌더로 곱게 간다.\r\n\r\n15. 사워크림, 곱게 간 가지, 홀그레인머스터드를 섞어 가지소스를 만든다\r\n\r\n16. 돼지고기떡롤을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n17. 가지소스에 후추, 발사믹크림, 레몬즙, 설탕을 넣고 섞는다.\r\n\r\n18. 돼지고기떡롤에 가지소스를 곁들이고 얇게 썬 대파를 뿌린다.\r\n\r\n19. 잣을 올려 마무리한다.'),
	(551, '조동아리 샌드', NULL, 1, '1. 데친 우거지에 수제막장, 된장, 다진 마늘을 넣고 무친다.\r\n\r\n2. 누룽지를 기름에 튀긴다.\r\n\r\n3. 곤약을 적당한 크기로 썰어 물에 데친다.\r\n\r\n4. 튀긴 누룽지를 건져 기름기를 뺀다.\r\n\r\n5. 데친 곤약에 올리브오일, 다진 마늘을 더해 볶는다.\r\n\r\n6. 5에 굴소스와 뜨거운 물을 붓고 조린다.\r\n\r\n7. 두부를 적당한 크기로 썰어 기름에 튀긴다.\r\n\r\n8. 씻어서 잘게 썬 총각김치와 채 썬 당근에 참기름을 넣고 버무린다.\r\n\r\n9. 8에 잘게 썬 명이나물장아찌와 식초를 넣고 버무려 총각김치무침을 만든다.\r\n\r\n10. 두부튀김을 건져 기름기를 뺀다.\r\n\r\n11. 기름을 두른 팬에 우거지무침, 뜨거운 물, 멸치를 넣고 끓여 조린다.\r\n\r\n12. 핸드블렌더에 슬라이스아몬드를 넣고 간다.\r\n\r\n13. 조린 우거지무침에 아몬드가루를 넣고 볶는다.\r\n\r\n14. 튀긴 누룽지, 곤약조림, 두부튀김, 총각김치무침, 우거지볶음을 각각 그릇에 담아낸다.'),
	(552, '김치브레이커♬', NULL, 1, '1. 김치를 물로 헹군 뒤, 길게 찢는다.\r\n\r\n2. 김밥용 김을 길게 자른다.\r\n\r\n3. 삼치를 얇게 포 뜬다.\r\n\r\n4. 김 위에 삼치>김치>삼겹살을 순서대로 올려 돌돌 만다.\r\n\r\n5. 밀가루와 물을 섞어 튀김옷을 만든다.\r\n\r\n6. 김치삼치고기말이에 튀김옷을 입혀 기름에 튀겨낸 후, 건져서 기름기를 뺀다.\r\n\r\n7. 당면을 삶는다.\r\n\r\n8. 뜨거운 물에 가쓰오부시를 넣고 우려서 육수를 낸다.\r\n\r\n9. 삶은 당면을 차가운 물에 헹군 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n10. 기름을 두른 팬에 편마늘, 적당한 크기로 썬 삼겹살, 대파, 김치를 넣고 볶는다.\r\n\r\n11. 9에 채 썬 양파, 표고버섯, 파프리카, 애호박과 두반장, 굴소스, 설탕을 넣고 볶는다.\r\n\r\n12. 볶던 재료에 삶은 당면과 가쓰오부시육수를 넣고 볶아 중화풍 잡채를 만든다.\r\n\r\n13. 잡채에 다진 마늘과 맛술을 넣고 볶는다.\r\n\r\n14. 식초, 설탕, 물, 간장, 전분물을 끓여 탕수소스를 만든다.\r\n\r\n15. 김치삼치고기말이를 탕수소스에 버무린다.\r\n\r\n16. 접시에 잡채를 담고 탕수소스에 버무린 김치삼치고기말이를 적당한 크기로 잘라 곁들인다.'),
	(553, '복면닭호', NULL, 1, '1. 밀가루, 소금, 물을 섞어 면 반죽을 만든다.\r\n\r\n2. 면 반죽을 비닐로 감싸 숙성시킨다.\r\n\r\n3. 채끝살을 적당한 크기로 썰어 핸드블렌더에 곱게 간다.\r\n\r\n4. 닭다리를 발골 한 뒤 먹기 좋은 크기로 썬다.\r\n\r\n5. 어묵을 먹기 좋은 크기로 썬다.\r\n\r\n6. 간 채끝살, 닭다리살, 어묵에 참기름과 고추장을 넣어 버무린다.\r\n\r\n7. 면 반죽을 밀대로 얇게 민 뒤, 적당한 크기로 썰어 면을 만든다.\r\n\r\n8. 끓는 물에 면을 넣고 삶은 뒤 찬물에 담가 식힌다.\r\n\r\n9. 기름을 두른 팬에 잘게 썬 마늘, 새송이버섯, 양파, 양념한 고기와 어묵을 넣어 함께 볶는다.\r\n\r\n10. 찬물에 식힌 면을 뜨거운 물에 데친 뒤, 볶던 재료에 넣고 함께 볶는다.\r\n\r\n11. 접시에 볶음면을 담은 뒤 잘게 썬 쪽파를 올려 마무리한다.'),
	(554, '면들에게 물어봐♬', NULL, 1, '1. 핸드블렌더에 밀가루와 달걀노른자를 넣고 섞어 면 반죽을 만든다.\r\n\r\n2. 면 반죽을 치댄 뒤, 밀가루를 뿌려 숙성시킨다.\r\n\r\n3. 고등어를 손질한 뒤 적당한 크기로 썬다.\r\n\r\n4. 기름을 두른 냄비에 마늘을 볶다가 모시조개, 화이트와인을 넣고 뚜껑을 덮어 익힌다.\r\n\r\n5. 면 반죽을 밀대로 얇게 민 뒤 적당한 크기로 썰어 면을 만든다.\r\n\r\n6. 기름을 두른 팬에 다진 마늘, 다진 페페론치노를 넣고 볶는다.\r\n\r\n7. 6에 익힌 모시조개의 살, 고등어를 넣고 함께 볶는다.\r\n\r\n8. 볶던 재료에 소금, 화이트와인, 물, 조개육수, 잘게 썬 대파를 넣고 끓인다.\r\n\r\n9. 소금을 넣은 끓는 물에 면을 익힌다.\r\n\r\n10. 볶던 재료에 면을 넣고 함께 볶아 고등어파스타를 만든다.\r\n\r\n11. 고등어파스타에 올리브오일을 둘러가며 볶는다.\r\n\r\n12. 접시에 고등어파스타를 담은 뒤 올리브오일을 뿌려 마무리한다.'),
	(555, '외할머니의 집으로~ 이경규가 간닭!', NULL, 1, '1. 압력밥솥에 불린 쌀과 물, 들기름, 소금을 넣어 밥을 안친다.\r\n\r\n2. 토종닭을 먹기 좋게 손질한다.\r\n\r\n3. 손질한 토종닭을 고기망치로 두드린다.\r\n\r\n4. 토종닭을 끓는 물에 데친 뒤 들기름, 소금, 후추, 잘게 썬 대파와 함께 버무린다.\r\n\r\n5. 양념한 토종닭을 끓는 물에 넣고 익혀 닭곰탕을 만든다.\r\n\r\n6. 끓는 물에 쪽파를 데친다.\r\n\r\n7. 끓이던 닭곰탕에 인스턴트 사골곰탕을 넣고 끓인다.\r\n\r\n8. 데친 쪽파를 얼음물에 담가 차게 식힌다.\r\n\r\n9. 김치 국물, 고춧가루, 다진 마늘, 간장을 섞어 양념장을 만든다.\r\n\r\n10. 물기를 제거한 쪽파에 양념장, 식초를 넣고 버무려 파김치를 만든다.\r\n\r\n11. 파김치를 먹기 좋게 썰어 접시에 담는다.\r\n\r\n12. 접시에 콩잎장아찌와 후추를 섞은 소금을 각각 담는다.\r\n\r\n13. 닭곰탕의 국물은 면포에 거르고, 익힌 닭은 건져낸다.\r\n\r\n14. 면포에 거른 국물에 대파를 넣어 끓인다.\r\n\r\n15. 익힌 닭의 살을 먹기 좋은 크기로 찢는다.\r\n\r\n16. 익힌 닭에 소금과 후추로 간 한다.\r\n\r\n17. 간을 한 닭을 닭곰탕에 넣어 함께 끓인다.\r\n\r\n18. 그릇에 밥을 담는다.\r\n\r\n19. 양은냄비에 닭곰탕을 담아낸다.'),
	(556, '꼬꼬탕', NULL, 1, '1. 끓는 물에 대파, 마늘, 양파를 넣고 끓여 채소육수를 낸다.\r\n\r\n2. 토종닭을 손질한 뒤 채소육수에 넣어 익힌다.\r\n\r\n3. 양파와 대파를 채 썬 뒤 찬물에 담가 아린 맛을 뺀다.\r\n\r\n4. 설탕, 식초, 맛술, 간장, 고추냉이를 섞어 소스를 만든다.\r\n\r\n5. 닭육수에 소스, 땅콩버터를 섞어 초계탕 국물을 만든다.\r\n\r\n6. 초계탕 국물을 냉동실에 넣어 차게 식힌다.\r\n\r\n7. 익힌 닭의 살을 잘게 찢는다.\r\n\r\n8. 닭고기에 쌈장, 참기름을 넣고 버무린다.\r\n\r\n9. 마늘종장아찌를 잘게 썬다.\r\n\r\n10. 달걀물을 만든 뒤 기름을 두른 팬에 부어 달걀지단을 만든다.\r\n\r\n11. 달걀지단 위에 채 썬 양파와 대파, 잘게 썬 마늘종장아찌, 양념한 닭고기, 다진 오이피클, 후추를 올린다.\r\n\r\n12. 각종 재료를 올린 달걀지단을 접시에 뒤집어 담은 뒤 깨를 뿌린다.\r\n\r\n13. 유리포트를 얼음물에 담가 차게 만든다.\r\n\r\n14. 차게 식힌 초계탕 국물을 골고루 섞이도록 잘 저어준다.\r\n\r\n15. 유리포트에 초계탕 국물을 담아낸다.'),
	(557, '이건 닭도 아니고 빵도 아니여', NULL, 1, '1. 볼에 밀가루, 소금, 설탕, 뜨거운 물을 넣고 치대어 익반죽한다.\r\n\r\n2. 반죽을 밀대로 얇게 민 뒤, 버터를 발라가며 접는 과정을 반복한다.\r\n\r\n3. 접은 반죽을 다시 한 번 밀대로 얇게 민다.\r\n\r\n4. 반죽을 네모낳게 자르고, 모서리에 버터를 올린 뒤 접어서 닭모양을 만든다.\r\n\r\n5. 닭다리 부분에 포일을 감싼 뒤, 기름에 튀긴다.\r\n\r\n6. 기름을 두른 팬에 다진 마늘, 식초, 간장, 고춧가루를 넣고 볶는다.\r\n\r\n7. 6에 통조림 햄을 부숴 넣는다.\r\n\r\n8. 볶던 재료에 인스턴트 카레를 넣고 끓여 카레소스를 만든다.\r\n\r\n9. 튀긴 닭빵의 포일을 벗겨 낸다.\r\n\r\n10. 카레소스에 전분물과 물을 넣는다.\r\n\r\n11. 그릇에 카레소스를 담은 뒤, 닭빵을 올려 마무리한다.'),
	(558, '공갈협빵', NULL, 1, '1. 핸드블렌더에 기름을 뺀 연어 통조림과 달걀, 통조림 햄, 전분을 넣고 갈아 빵 반죽을 만든다.\r\n\r\n2. 청양고추를 적당한 크기로 썬 뒤, 핸드블렌더에 곱게 간다.\r\n\r\n3. 대파 흰 부분을 잘게 썬다.\r\n\r\n4. 통조림 햄을 슬라이서로 간다.\r\n\r\n5. 기름을 두른 팬에 손질한 대파, 통조림햄을 함께 볶는다.\r\n\r\n6. 볶던 재료에 곱게 간 청양고추를 넣고 함께 볶아 소를 만든다.\r\n\r\n7. 식용유를 바른 종이포일 사이에 빵 반죽을 넣고 기름에 튀기다가 종이포일을 빼낸다.\r\n\r\n8. 튀긴 빵에 설탕을 묻힌다.\r\n\r\n9. 볶은 소를 짤주머니에 넣은 뒤, 튀긴 빵에 구멍을 내고 안에 소를 채운다.\r\n\r\n10. 소를 채운 빵을 그릇에 담는다.\r\n\r\n11. 빵 반죽을 넓게 편 뒤, 길게 썰어 기름에 튀긴다.\r\n\r\n12. 길게 튀긴 빵에 설탕을 묻혀 그릇에 담는다.'),
	(559, '어젯밤 시킨 닭', NULL, 1, '1. 치킨을 뜨거운 물에 담가 불린다.\r\n\r\n2. 핸드블렌더에 깨, 마요네즈, 간장, 설탕, 식초, 청양고추를 넣고 갈아 참깨소스를 만든다.\r\n\r\n3. 뜨거운 물에 불린 치킨살을 먹기 좋게 찢는다.\r\n\r\n4. 참깨소스에 고춧가루, 우엉을 불린 물, 적당한 크기로 썬 청양고추를 넣고 섞는다.\r\n\r\n5. 밀가루와 물을 섞어 튀김옷을 만든다.\r\n\r\n6. 불린 우엉과 불린 치킨에 튀김옷을 입힌다.\r\n\r\n7. 6을 기름에 튀겨 우엉치킨을 만든다.\r\n\r\n8. 인스턴트 어묵탕을 체에 밭쳐 어묵과 국물을 분리한다.\r\n\r\n9. 어묵을 가늘게 채 썬다.\r\n\r\n10. 어묵국물을 끓인다.\r\n\r\n11. 라면의 면만 삶는다.\r\n\r\n12. 삶은 라면을 찬물에 헹군다.\r\n\r\n13. 우엉치킨을 건져낸 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n14. 팬에 청주, 맛술, 설탕, 간장을 넣고 끓여 데리야키소스를 만든다.\r\n\r\n15. 끓이던 어묵국물에 달걀물을 풀어 어묵달걀국을 만든다.\r\n\r\n16. 어묵달걀국을 그릇에 담은 뒤, 후추를 뿌린다.\r\n\r\n17. 우엉치킨을 데리야키소스에 버무린 뒤, 잘게 썬 청양고추를 뿌린다.\r\n\r\n18. 데리야키 우엉치킨을 접시에 담고, 깨를 뿌린다.\r\n\r\n19. 삶은 라면을 접시에 담은 뒤, 잘게 찢어놓은 치킨살을 올리고 참깨소스와 레몬즙, 깨를 뿌려 마무리한다.'),
	(560, '1닭 2일', NULL, 1, '1. 양파, 마늘, 당근을 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 팬에 손질한 채소를 볶는다.\r\n\r\n3. 튀김옷을 벗긴 치킨살만 채소를 볶던 팬에 넣고 함께 볶는다.\r\n\r\n4. 볶던 재료에 위스키를 부은 뒤, 불을 붙여 잡내를 날린다.\r\n\r\n5. 볶던 재료에 버터와 밀가루를 넣고 볶는다.\r\n\r\n6. 6에 적당한 크기로 썬 청양고추, 후추, 물, 토마토소스를 넣고 끓여 치킨토마토스튜를 만든다.\r\n\r\n7. 김을 잘게 부순 뒤, 물로 기름기를 헹궈낸다.\r\n\r\n8. 김에 식초, 설탕, 물, 간장을 넣고 섞어 김냉국을 만든다.\r\n\r\n9. 치킨무를 잘게 썰어 김냉국에 넣는다.\r\n\r\n10. 김냉국에 잘게 썬 대파 흰 부분과 얼음을 넣는다.\r\n\r\n11. 즉석밥, 인스턴트 볶음김치, 전분을 섞은 뒤, 기름을 두른 팬에 구워 밥전을 만든다.\r\n\r\n12. 통조림 햄을 적당한 크기로 썰어 기름에 튀긴다.\r\n\r\n13. 그릇에 치킨토마토스튜를 담는다.\r\n\r\n14. 잘게 다진 청양고추를 넣은 김냉국을 그릇에 담는다.\r\n\r\n15. 김냉국에 레몬즙을 뿌린다.\r\n\r\n16. 접시에 밥전을 담고, 튀긴 햄을 올려 마무리한다.'),
	(561, '소미양~ 스테이크양', NULL, 1, '1. 알감자를 적당한 크기로 썰어 끓는 물에 넣고 삶는다.\r\n\r\n2. 양갈비에 소금, 요거트를 넣고 재운다.\r\n\r\n3. 끓는 물에 월계수잎, 바질 가루, 고수홀, 핑크페퍼, 이탈리안 허브시즈닝, 올리브오일, 소금, 으깬 마늘을 넣고 끓여 향을 낸다.\r\n\r\n4. 향신료를 우린 물에 레드와인을 넣는다.\r\n\r\n5. 4에 얼음을 넣어 온도를 내린 뒤, 양갈비에 부어준다.\r\n\r\n6. 잘게 썬 마늘과 요거트, 소금, 마요네즈를 섞어 요거트소스를 만든다.\r\n\r\n7. 요거트소스에 딜 가루를 넣고 섞은 뒤 그릇에 담는다.\r\n\r\n8. 재워뒀던 양갈비에 밀가루를 입힌다.\r\n\r\n9. 기름을 두른 팬에 밀가루를 입힌 양갈비를 튀기듯 굽는다.\r\n\r\n10. 버터를 녹인 팬에 삶은 감자, 당근, 소금을 넣고 볶는다.\r\n\r\n11. 볶던 채소에 딜 가루를 뿌린 뒤 접시에 담는다.\r\n\r\n12. 11 위에 구운 양갈비를 올리고, 슬라이스한 마늘을 곁들여 완성한다.'),
	(562, '케사디양', NULL, 1, '1. 양파를 채 썰어 기름을 두른 팬에 볶는다.\r\n\r\n2. 양갈비는 살만 발라내어 적당한 크기로 썬다.\r\n\r\n3. 기름을 두른 팬에 통마늘과 로즈마리를 볶아 향을 낸 뒤 양갈비살을 넣고 볶는다.\r\n\r\n4. 볶던 양갈비살에 마늘소금, 허브믹스, 소금, 후추를 넣어 함께 볶는다.\r\n\r\n5. 마른 팬에 토르티야를 굽는다.\r\n\r\n6. 구운 토르티야를 반으로 자른다.\r\n\r\n7. 6에 홀그레인머스터드를 바른 뒤 볶은 양파, 양갈비살을 올린다.\r\n\r\n8. 아보카도, 레몬즙, 소금, 후추, 물엿을 핸드블렌더로 갈아 아보카도무스를 만든다.\r\n\r\n9. 양갈비살에 아보카도무스를 올린다.\r\n\r\n10. 설탕, 맛술, 간장을 섞은 양념에 할라피뇨를 재운다.\r\n\r\n\r\n11. 양념에 재운 할라피뇨를 잘게 다지고, 양배추는 가늘게 채 썬다.\r\n\r\n12. 아보카도무스 위에 채 썬 양배추와 할라피뇨를 올린다.\r\n\r\n13. 12에 요거트를 올린다.\r\n\r\n14. 요거트에 후추를 뿌린 뒤 토르티야를 반으로 접어 케사디야를 만든다.\r\n\r\n15. 기름을 두른 팬에 수박을 굽는다.\r\n\r\n16. 굽던 수박에 소금, 후추로 간을 한다.\r\n\r\n17. 접시에 구운 수박을 올린 뒤 크림치즈, 바질페스토, 블루베리, 병아리콩, 레몬껍질을 곁들인다.\r\n\r\n18. 접시에 케사디야를 올려 완성한다.'),
	(563, '오늘 밤 상남자는 나야 나♬', NULL, 1, '1. 사과 껍질을 벗긴다.\r\n\r\n2. 껍질을 벗긴 사과를 핸드블렌더로 간다.\r\n\r\n3. 마른 팬에 간 사과와 꿀을 넣고 졸여 사과소스를 만든다.\r\n\r\n4. 핸드블렌더에 옥수수알, 밀가루, 달걀을 넣고 간다.\r\n\r\n5. 나무꼬치를 쿠킹포일로 감싸 막대를 만든 뒤 옥수수반죽을 둘러 튀긴다.\r\n\r\n6. 마른 냄비에 고다치즈, 체다치즈, 우유, 파르메산치즈를 넣고 끓여 치즈소스를 만든다.\r\n\r\n7. 핸드블렌더에 적당한 크기로 썬 척아이롤, 허브믹스, 소금을 넣고 간다.\r\n\r\n8. 쿠킹포일에 밀가루를 뿌린 뒤 간 척아이롤을 넓게 펼친다.\r\n\r\n9. 튀긴 옥수수반죽을 척아이롤로 감싼다.\r\n\r\n10. 핫도그를 감싼 쿠킹포일에 구멍을 낸 뒤 기름에 튀긴다.\r\n\r\n11. 졸인 사과소스에 레몬즙을 넣는다.\r\n\r\n12. 핫케이크 가루와 우유를 섞어 반죽을 만든다.\r\n\r\n13. 핫케이크 반죽을 기름에 튀긴다.\r\n\r\n14. 튀긴 핫케이크를 그릇에 담고 치즈소스를 붓는다.\r\n\r\n15. 13 위에 핫도그를 올리고 사과소스를 곁들여 낸다.'),
	(564, '마초세트', NULL, 1, '1. 베이컨을 전자레인지에 넣어 익힌다.\r\n\r\n2. 양파를 채 썬다.\r\n\r\n3. 끓는 물에 소금과 제멜리면을 넣고 삶는다.\r\n\r\n4. 기름을 두른 냄비에 채 썬 양파, 으깬 마늘을 넣고 볶는다.\r\n\r\n5. 4에 적당한 크기로 썬 치킨 너겟을 넣고 함께 볶는다.\r\n\r\n6. 청양고추를 잘게 썰어 냄비에 넣고 함께 볶는다.\r\n\r\n7. 옥수수알과 버터. 밀가루, 물을 냄비에 넣고 끓인다.\r\n\r\n8. 7에 치킨스톡, 통조림 토마토수프, 살사소스, 고수 가루를 넣고 끓여 치킨토마토수프를 만든다.\r\n\r\n9. 냄비에 으깬 마늘, 버터, 밀가루, 우유, 고다치즈, 체다치즈, 페퍼잭치즈, 파르메산치즈를 넣고 끓여 치즈소스를 만든다.\r\n\r\n10. 치즈소스에 소금으로 간을 한다.\r\n\r\n11. 팬에 버터를 두르고 식빵을 굽는다.\r\n\r\n12. 아보카도를 적당한 크기로 썰어둔다.\r\n\r\n13. 파프리카와 대파를 잘게 썬다.\r\n\r\n14. 삶은 제멜리면을 치즈소스, 베이컨과 섞어 맥 앤 치즈를 만든다.\r\n\r\n15. 구운 식빵 사이에 맥 앤 치즈를 넣어 토스트를 만든다.\r\n\r\n16. 그릇에 치킨토마토수프를 담고 아보카도, 페퍼잭치즈를 올린다.\r\n\r\n17. 맥 앤 치즈 토스트를 적당한 크기로 썰어 접시에 담고, 파르메산 치즈를 뿌려 완성한다.'),
	(565, '참돔이시여', NULL, 1, '1. 끓는 기름에 대파, 고춧가루를 넣고 끓여 고추기름을 낸다.\r\n\r\n2. 참돔 살에 칼집을 낸 뒤, 소금으로 간을 하고 전분을 입힌다.\r\n\r\n3. 전분을 입힌 참돔을 기름에 튀긴다.\r\n\r\n4. 고추기름을 체에 밭쳐 기름만 빼낸다.\r\n\r\n5. 마늘, 대파, 양파, 표고버섯, 사과를 잘게 썬다.\r\n\r\n6. 고추기름을 두른 팬에 손질한 채소를 넣고 볶다가 매실주와 멸치육수, 굴소스를 넣고 끓인다.\r\n\r\n7. 6에 손질한 사과와 전분물을 넣어 매콤한 소스를 만든다.\r\n\r\n8. 마늘종장아찌를 잘게 썰고, 대파는 가늘게 채 썬다.\r\n\r\n9. 접시에 참돔튀김을 담은 뒤, 소스를 붓는다.\r\n\r\n10. 9 위에 잘게 썬 마늘종장아찌와 파채를 얹어 마무리한다.'),
	(566, '잘 구운 참돔 하나', NULL, 1, '1. 기름을 두른 팬에 양파를 굽는다.\r\n\r\n2. 냄비에 양파와 참돔 뼈, 뜨거운 물을 넣고 끓여 생선육수를 낸다.\r\n\r\n3. 참돔 살을 키친타월로 감싸 물기를 뺀다.\r\n\r\n4. 구운 양파에 발사믹소스, 올리브오일, 후추를 더해 섞는다.\r\n\r\n5. 기름을 두른 팬에 슬라이스한 마늘을 굽다가, 소금, 올리브오일, 레몬즙을 뿌린다.\r\n\r\n6. 기름을 두른 팬에 다진 마늘, 적당한 크기로 썬 표고버섯, 소금, 후추, 식초, 생선육수를 넣고 끓여 졸인다.\r\n\r\n7. 기름을 두른 팬에 물기를 뺀 참돔을 굽다가, 소금, 후추로 간 한다.\r\n\r\n8. 생선육수로 졸인 소스에 버터를 넣는다.\r\n\r\n9. 핸드블렌더에 그린올리브와 올리브오일을 넣고 곱게 갈아 소스를 만든다.\r\n\r\n10. 곱게 간 올리브에 소금, 후추로 간 한다.\r\n\r\n11. 접시에 구운 마늘과 마늘기름을 넓게 펼친 뒤, 발사믹소스에 버무린 구운 양파를 올린다.\r\n\r\n12. 11 위에 생선육수소스를 얹는다.\r\n\r\n13. 구운 참돔을 올린다.\r\n\r\n14. 참돔 위에 올리브소스를 뿌린다.\r\n\r\n15. 참돔 위에 후추를 뿌려 마무리한다.'),
	(567, '월하조어탕수', NULL, 1, '1. 오징어의 껍질을 벗긴다.\r\n\r\n2. 핸드블랜더에 적당한 크기로 썬 오징어와 골뱅이를 넣고 간다.\r\n\r\n3. 곱게 간 오징어와 골뱅이에 생강즙, 버터, 전분, 소금, 후추를 넣고 섞어 소를 만든다.\r\n\r\n4. 3을 찜기에 넣고 찐다.\r\n\r\n5. 양파, 오이, 당근, 대파, 표고버섯을 적당한 크기로 썬 뒤 기름을 두른 팬에 볶는다.\r\n\r\n6. 볶던 채소에 누룽지, 인삼주, 인스턴트 삼계탕 국물, 볶음고추장, 식초, 설탕, 맛술, 깨를 넣고 끓여 소스를 만든다.\r\n\r\n7. 통오징어 안에 찐 오징어골뱅이소를 채워 넣은 뒤, 꼬치로 고정한다.\r\n\r\n8. 통오징어에 칼집을 낸 뒤, 밀가루를 묻힌다.\r\n\r\n9. 물, 전분, 오징어먹물을 섞어 먹물튀김옷을 만든다.\r\n\r\n10. 밀가루를 묻힌 통오징어에 먹물튀김옷을 입힌 뒤, 기름에 튀긴다.\r\n\r\n11. 먹물오징어튀김을 건져 다시 한 번 먹물튀김옷을 입힌 뒤, 기름에 튀긴다.\r\n\r\n12. 먹물오징어튀김을 건진 뒤, 체에 밭쳐 기름기를 뺀다.\r\n\r\n13. 소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n14. 접시에 소스를 담은 뒤, 먹기 좋게 썬 먹물오징어튀김을 얹어 마무리한다.'),
	(568, '면 때무늬야♬', NULL, 1, '1. 오징어의 껍질을 벗긴다.\r\n\r\n2. 핸드블렌더에 적당한 크기로 썬 오징어몸통과 양파, 대파, 표고버섯, 전분, 달걀흰자, 굴소스, 청주를 넣고 갈아 만두소를 만든다.\r\n\r\n3. 오징어만두소에 잘게 썬 오징어다리를 넣고 섞는다.\r\n\r\n4. 오징어를 가늘게 채 썰어 만두피를 만든다.\r\n\r\n5. 오징어만두소를 동그랗게 뭉친 뒤, 오징어채 만두피를 붙인다.\r\n\r\n6. 찜기에 오징어만두를 찐다.\r\n\r\n7. 냄비에 물, 양파, 대파, 표고버섯, 쯔유, 맛술, 설탕, 소금, 간장을 넣고 끓여 육수를 낸다.\r\n\r\n8. 끓인 육수를 냉동실에 넣어 차게 식힌다.\r\n\r\n9. 오징어에 촘촘하게 칼집을 넣은 뒤, 가늘게 채 썰어 오징어면을 만든다.\r\n\r\n10. 오징어면을 찬물에 헹군 뒤, 소금으로 간을 한다.\r\n\r\n11. 라임을 얇게 슬라이스 한다.\r\n\r\n12. 차게 식힌 육수에 라임즙을 넣는다.\r\n\r\n13. 그릇에 오징어면을 담고 슬라이스한 라임을 얹은 뒤, 육수를 부어 오징어냉국수를 만든다.\r\n\r\n14. 접시에 오징어만두를 올린 뒤, 우메보시를 얹고 볶음고추장을 곁들여 낸다.'),
	(569, '깜짝놀랄게닭', NULL, 1, '1. 순살프라이드치킨을 끓는 물에 데쳐 기름기를 뺀다.\r\n\r\n2. 메밀소바면을 삶는다.\r\n\r\n3. 데친 순살프라이드치킨을 물로 씻어 튀김옷을 벗겨낸 뒤, 끓는 물에 넣고 삶는다.\r\n\r\n4. 삶은 메밀소바면을 찬물에 헹군 뒤, 체에 밭쳐 물기를 뺀다.\r\n\r\n5. 볼에 적당한 크기로 썬 감자와 약간의 물을 담고 전자레인지에 돌려 익힌다.\r\n\r\n6. 참깨소스, 굴소스, 라조장을 1:1:1로 섞고, 설탕과 다진 마늘을 넣어 찜닭소스를 만든다.\r\n\r\n7. 돼지안심을 적당한 크기로 썬 뒤, 치킨을 삶던 냄비에 넣고 함께 끓인다.\r\n\r\n8. 7에 찜닭소스를 넣고 끓이다가, 국물을 덜어내고 조린다.\r\n\r\n9. 찜닭에 익힌 감자와 적당한 크기로 썬 파프리카, 깻잎장아찌, 양파를 넣고 조린다.\r\n\r\n10. 먹기 좋은 크기로 자른 게에 밀가루를 묻힌다.\r\n\r\n11. 게를 튀긴다.\r\n\r\n12. 튀긴 게를 건져내 밀가루를 묻힌다.\r\n\r\n13. 튀긴 게와 땅콩을 함께 튀긴다.\r\n\r\n14. 볼에 대게내장을 담은 뒤, 매운치킨소스, 물엿, 곱게 다진 깨, 참깨소스를 섞어 게튀김소스를 만든다.\r\n\r\n15. 메밀소바면에 참기름을 넣고 잘 섞은 뒤, 접시에 담는다.\r\n\r\n16. 튀긴 게를 건져 게튀김소스에 버무린다.\r\n\r\n17. 찜닭에 전분물을 넣어 농도를 조절한다.\r\n\r\n18. 접시에 찜닭을 담고, 게튀김범벅을 얹어 마무리한다.'),
	(570, '거북전', NULL, 1, '1. 감자를 얇게 슬라이스한다.\r\n\r\n2. 기름을 두른 팬에 감자슬라이스를 넓게 펼친 뒤, 굽는다.\r\n\r\n3. 강판에 감자를 간다.\r\n\r\n4. 곱게 간 감자에 달걀노른자, 전분을 넣고 섞는다.\r\n\r\n5. 감자반죽에 소금과 후추로 간 한다.\r\n\r\n6. 감자슬라이스 위에 감자반죽을 붓고 굽는다.\r\n\r\n7. 끓는 물에 반으로 자른 레몬, 게를 넣고 끓인다.\r\n\r\n8. 7에 낙지를 살짝 데쳐낸 뒤, 적당한 크기로 자른다.\r\n\r\n9. 감자전 위에 데친 낙지와 적당한 크기로 자른 새우, 슬라이스치즈를 얹는다.\r\n\r\n10. 삶은 게를 밀대로 밀어 살만 빼낸 뒤, 잘게 썬 청양고추와 섞어 감자전 위에 얹는다.\r\n\r\n11. 감자전을 뒤집은 뒤, 버터를 넣고 녹인다.\r\n\r\n12. 마늘고추간장장아찌 국물과 마늘식초장아찌의 국물을 섞는다.\r\n\r\n13. 12에 적당한 크기로 썬 양파를 넣고 절인 뒤 종지에 담는다.\r\n\r\n14. 갈빗살을 얇게 편 뒤, 칼집을 내고 소금, 밀가루, 후추를 뿌린다.\r\n\r\n15. 갈빗살을 감자슬라이스로 감싸고 전분을 뿌린 뒤, 반으로 자른다.\r\n\r\n16. 15에 달걀물을 입힌 뒤, 기름을 두른 팬에 구워 육전을 만든다.\r\n\r\n17. 접시에 감자전을 담고 먹기 좋은 크기로 자른다.\r\n\r\n18. 육전을 얹어 마무리한다.'),
	(571, '대세는 낙.돼.새', NULL, 1, '1. 채칼로 사과와 당근을 채썬다.\n2. 채 썬 사과와 당근에 간장, 채 썬 양파, 다진 마늘, 고춧가루, 식초, 설탕을 넣고 섞어 사과당근샐러드를 만든다.\n\n3. 돼지 목살의 기름기를 제거하고 적당한 크기로 썬다.\n\n4. 적당한 크기로 썬 돼지목살에 칼집을 낸다.\n\n5. 낙지를 적당한 크기로 썬다.\n\n6. 마른 철판에 돼지 목살을 볶다가, 기름을 두르고 볶는다.\n\n7. 돼지 목살을 볶던 철판에 손질한 새우를 넣고 함께 볶다가 적당한 크기로 썬다.\n\n8. 재료를 볶던 철판에 굴소스, 알배추, 맛술, 대파, 소금을 넣고 볶는다.\n\n9. 즉석밥을 전자레인지에 돌린다.\n\n10. 볶던 재료에 손질한 낙지와 들깻가루를 넣고 볶는다.\n\n11. 즉석밥을 볼에 담은 뒤, 들기름과 고추장을 넣고 섞는다.\n\n12. 낙지돼지새우볶음을 볼에 옮겨 담고, 철판에 고추장밥을 펼쳐서 누룽지를 만든다.\n\n13. 김을 적당한 크기로 자른 뒤, 접시에 담는다.\n\n14. 사과당근샐러드를 그릇에 담는다.\n\n15. 접시에 낙지돼지새우볶음을 담는다.\n\n16. 고추장누룽지를 접시에 함께 담아 마무리한다.'),
	(572, '오코노미~야키를 사랑해♬', NULL, 1, '1. 라면 면을 삶는다.\r\n\r\n2. 양배추를 채 썬다.\r\n\r\n3. 돼지 안심을 적당한 크기로 썬 뒤, 칼집을 낸다.\r\n\r\n4. 삶은 라면을 찬 물에 헹궈 식힌 뒤, 마른 팬에 케첩과 함께 볶는다.\r\n\r\n5. 밀가루와 물을 섞어 반죽을 만든다.\r\n\r\n6. 기름을 두른 철판에 밀가루반죽을 두른 뒤, 양배추채를 올린다.\r\n\r\n7. 양배추 채 위에 돼지 안심을 올리고, 후추를 뿌린 뒤 뒤집어 굽는다.\r\n\r\n8. 새우를 손질한 뒤, 후추를 뿌려 굽는다.\r\n\r\n9. 철판에 양파슬라이스로 탑을 쌓고 구운 새우를 올린 뒤, 고량주를 붓고 불을 붙인다.\r\n\r\n10. 철판 한 쪽에 케첩에 볶은 라면을 펼친 뒤, 오코노미야키를 얹는다.\r\n\r\n11. 철판 한 쪽에 달걀프라이를 만들고, 그 위에 오코노미야키를 얹는다.\r\n\r\n12. 마요네즈와 스위트칠리소스를 섞어 칠리마요를 만든다.\r\n\r\n13. 직화한 새우에 칠리마요를 묻힌다.\r\n\r\n14. 오코노미야키를 접시에 담고, 먹기 좋은 크기로 자른다.\r\n\r\n15. 핸드블랜더에 잔멸치를 곱게 간 뒤, 오코노미야키 위에 뿌린다.\r\n\r\n16. 오코노미야키 위에 돈가스소스와 마요네즈를 뿌린다.\r\n\r\n17. 칠리마요새우를 얹어 마무리한다.'),
	(573, '이겨도 돼지?', NULL, 1, '1. 냄비에 설탕, 물, 체리를 넣고 데운다.\r\n\r\n2. 데운 체리시럽에 돼지목살을 담가 마리네이드 한다.\r\n\r\n3. 핸드블렌더에 표고버섯대, 마늘을 넣고 간다.\r\n\r\n4. 마른 팬에 간 표고버섯대와 마늘을 볶다가 소금, 후추, 버터를 넣고 볶아 표고마늘볶음을 만든다.\r\n\r\n5. 표고마늘볶음을 볼에 옮겨 식힌다.\r\n\r\n6. 마리네이드 한 목살의 물기를 제거하고 칼집을 넣는다.\r\n\r\n7. 칼집을 낸 목살 속을 표고마늘볶음으로 채우고 이쑤시개로 고정한다.\r\n\r\n8. 속을 채운 목살에 올리브오일, 소금, 후추으로 양념 한다.\r\n\r\n9. 핸드블렌더에 깻잎, 마늘, 올리브오일, 레몬즙, 소금을 넣고 곱게 갈아 깻잎소\r\n스를 만든다.\r\n\r\n10. 기름을 두른 그릴팬에 파프리카와 양념한 목살을 굽는다.\r\n\r\n11. 기름을 두른 팬에 다진 마늘과 식초를 넣고 볶다가 체리시럽을 붓고 끓인다.\r\n\r\n12. 11에 굴소스, 물을 넣고 끓여 스테이크소스를 만든다.\r\n\r\n13. 기름을 두른 팬에 적당한 크기로 썬 브로콜리를 볶는다.\r\n\r\n14. 구운 파프리카를 잘게 다져 스테이크소스에 넣고 함께 끓인다.\r\n\r\n15. 목살을 굽던 팬에 버터를 넣는다.\r\n\r\n16. 스테이크소스에 버터를 넣어 농도를 조절한다.\r\n\r\n17. 접시에 스테이크소스를 담은 뒤, 볶은 브로콜리를 올린다.\r\n\r\n18. 접시에 목살스테이크를 담은 뒤, 깻잎소스를 올려 마무리 한다.'),
	(574, '오~ 쌈젤리제♬', NULL, 1, '1. 끓는 물에 표고버섯대와 양배추, 브로콜리를 넣고 삶는다.\r\n\r\n2. 1에 펜네면을 넣고 삶는다.\r\n\r\n3. 기름을 두른 팬에 채 썬 양파와 표고버섯볶음을 넣고 볶는다.\r\n\r\n4. 볶은 재료를 핸드블렌더로 갈아 표고버섯뒥셀을 만든다.\r\n\r\n5. 기름을 두른 팬에 돼지목살을 굽다가 소금으로 간 한다.\r\n\r\n6. 구운 목살을 적당한 크기로 자른 뒤, 핸드블렌더로 간다.\r\n\r\n7. 삶은 양배추를 찬물에 담가 식힌 뒤, 위에 깻잎, 슬라이스치즈, 표고버섯뒥셀과 돼지목살, 연겨자를 차례로 얹고, 돌돌 말아 고정한다.\r\n\r\n8. 기름을 두른 팬에 적당한 크기로 썬 양파, 통마늘, 손질한 새우, 바지락살을 넣고 볶는다.\r\n\r\n9. 8에 토마토소스와 채소육수, 고추장, 굴소스, 청양고추를 넣고 끓여 해산물고추장소스를 만든다.\r\n\r\n10. 돼지목살을 넣은 양배추쌈을 해산물고추장소스에 넣고 끓인다.\r\n\r\n11. 삶은 펜네면을 건져내 해산물고추장소스에 넣고 끓인다.\r\n\r\n12. 해산물고추장소스에 새우가루를 넣고 끓인다.\r\n\r\n13. 그릇에 펜네면과 해산물고추장소스를 담는다.\r\n\r\n14. 해산물고추장소스 위에 익힌 양배추쌈을 올린 뒤, 다시 한 번 해산물고추장소스를 끼얹어 마무리한다.'),
	(575, '명백한 짬뽕', NULL, 1, '1. 밀가루에 적당한 크기로 썬 명란젓과 물을 넣고 치대어 명란면 반죽을 만든다.\r\n\r\n2. 명란면 반죽을 숙성시킨다.\r\n\r\n3. 끓는 물에 적당한 크기로 썬 청양고추, 새우가루를 넣고 끓여 새우육수를 낸다.\r\n\r\n4. 알배추, 양파, 애호박, 브로콜리, 마늘, 대파를 적당한 크기로 썬다.\r\n\r\n5. 핸드블렌더에 손질한 새우살, 전분, 달걀흰자, 명란젓을 넣고 갈아 명란새우완자반죽을 만든다.\r\n\r\n6. 오징어에 칼집을 내고 적당한 크기로 썬다.\r\n\r\n7. 기름을 두른 팬에 손질한 채소들을 넣고 볶는다.\r\n\r\n8. 명란면 반죽을 밀대로 얇게 민 뒤, 썰어서 면을 뽑는다.\r\n\r\n9. 명란면을 끓는 물에 삶는다.\r\n\r\n10. 볶던 채소에 새우육수를 붓고, 바지락살, 오징어, 굴소스를 넣고 끓여 백짬뽕 국물을 만든다.\r\n\r\n11. 명란새우완자반죽을 적당한 크기로 떼어 백짬뽕 국물에 넣고 함께 끓인다.\r\n\r\n12. 백짬뽕 국물에 콩나물을 넣고 함께 끓인다.\r\n\r\n13. 삶은 명란면을 건져 찬물에 헹군 뒤, 다시 면수에 담가 토렴한다.\r\n\r\n14. 명란면을 그릇에 담는다.\r\n\r\n15. 그릇에 백짬뽕 국물을 부어 마무리한다.'),
	(576, '명카엘', NULL, 1, '1. 기름을 두른 팬에 명란젓을 볶는다.\r\n\r\n2. 끓는 물에 콩나물을 데친다.\r\n\r\n3. 오징어의 껍질을 벗겨낸다.\r\n\r\n4. 볼에 잘게 썬 아보카도, 파프리카, 마늘과 모차렐라치즈, 후추를 넣는다.\r\n\r\n5. 4에 볶은 명란젓, 적당한 크기로 썬 스트링치즈, 고춧가루를 넣고 버무려 소를 만든다.\r\n\r\n6. 껍질을 벗긴 오징어 안에 소를 채워 넣고 꼬치로 고정한다.\r\n\r\n7. 속을 채운 오징어에 올리브오일을 바르고 기름을 두른 그릴팬에 굽는다.\r\n\r\n8. 데친 콩나물을 건져 식초에 버무린 뒤, 한 번 씻어내고 얼음물에 담가 차게 식힌다.\r\n\r\n9. 깻잎을 가늘게 채 썬다.\r\n\r\n10. 차게 식힌 콩나물을 체에 밭쳐 물기를 빼고, 채 썬 깻잎과 당근, 올리브오일, 소금, 레몬즙과 섞어 샐러드를 만든다.\r\n\r\n11. 10에 채 썬 양파를 넣고 버무려 콩나물깻잎 샐러드를 만든다.\r\n\r\n12. 접시에 콩나물깻잎 샐러드를 담는다.\r\n\r\n13. 속을 채워 구운 오징어를 적당한 크기로 썬다.\r\n\r\n14. 접시에 적당한 크기로 썬 오징어를 담는다.\r\n\r\n15. 오징어 사이에 레몬슬라이스와 잘게 썬 청양고추를 곁들인다.\r\n\r\n16. 오징어 위에 후추와 파르메산치즈를 뿌려 마무리한다.'),
	(577, '복정식', NULL, 1, '1. 달궈놓은 돌솥에 씻은 쌀과 물을 넣고 끓여 돌솥밥을 만든다.\r\n\r\n2. 달걀에 잘게 다진 통조림 햄, 대파, 물, 후추를 넣고 섞는다.\r\n\r\n3. 2를 달궈놓은 뚝배기에 붓고 익혀 달걀찜을 만든다.\r\n\r\n4. 자반고등어를 적당한 크기로 썬 뒤, 밀가루를 입힌다.\r\n\r\n5. 밀가루를 입힌 자반고등어를 기름에 튀긴다.\r\n\r\n6. 양파, 당근, 대파, 마늘, 청양고추, 훈제 닭가슴살을 적당한 크기로 썬다.\r\n\r\n7. 기름을 두른 팬에 손질한 채소와 고춧가루, 고추장, 간장, 물을 넣고 볶는다.\r\n\r\n8. 볶던 재료에 손질한 훈제 닭가슴살을 넣고 함께 볶아 닭볶음탕을 만든다.\r\n\r\n9. 닭볶음탕에 설탕을 넣는다.\r\n\r\n10. 닭볶음탕에 라면수프를 넣는다.\r\n\r\n11. 닭볶음탕에 믹스채소를 넣는다.\r\n\r\n12. 닭볶음탕에 찐고구마와 참기름을 넣는다.\r\n\r\n13. 접시에 고등어튀김을 담는다.\r\n\r\n14. 접시에 닭볶음탕을 담는다.\r\n\r\n15. 돌솥밥, 고등어튀김, 닭볶음탕, 달걀찜을 함께 낸다.'),
	(578, '덕윤백반', NULL, 1, '1. 불려놓은 쌀과 물을 냄비에 넣고 끓여 냄비밥을 만든다.\r\n\r\n2. 압력솥에 적당한 크기로 썬 대파, 당근, 통마늘, 훈제 닭가슴살, 삶은 닭가슴살과 물을 넣고 끓여 닭국을 만든다.\r\n\r\n3. 냄비에 채 썬 양파, 식용유, 자반고등어, 청주를 넣고 익힌다.\r\n\r\n4. 다진 생강, 다진 마늘, 고춧가루, 간장, 배즙, 설탕, 맛술을 섞어 조림양념을 만든다.\r\n\r\n5. 고등어를 익히던 냄비에 통마늘과 조림양념, 적당한 크기로 썬 청고추와 홍고추, 대파를 넣고 뚜껑을 닫은 뒤, 조린다.\r\n\r\n6. 고추를 적당한 크기로 썬다.\r\n\r\n7. 된장, 배즙, 물엿, 참기름, 고춧가루, 깨를 섞어 된장양념을 만든다.\r\n\r\n8. 손질한 고추에 된장양념을 넣고 버무려 고추된장무침을 만든다.\r\n\r\n9. 달걀물에 소금, 잘게 썬 대파를 넣고 섞는다.\r\n\r\n10. 기름을 두른 팬에 달걀물을 부은 뒤, 조미김을 올린다.\r\n\r\n11. 달걀지단을 돌돌 말아 달걀말이를 만든다.\r\n\r\n12. 압력솥에 끓이던 닭국에 소금, 다진 마늘을 넣어 간 한다.\r\n\r\n13. 그릇에 닭국을 담은 뒤, 잘게 썬 대파를 올리고 후추를 뿌린다.\r\n\r\n14. 접시에 고추된장무침을 담는다.\r\n\r\n15. 고등어조림을 접시에 담는다.\r\n\r\n16. 달걀말이를 먹기 좋은 크기로 썰어 접시에 담는다.\r\n\r\n17. 냄비밥, 닭국, 고등어조림, 달걀말이, 고추된장무침을 함께 낸다.'),
	(579, '할매 라면', NULL, 1, '1. 끓는 물에 설탕과 간장을 붓고 끓여 장아찌국물을 만든다.\r\n\r\n2. 양파에 칼집을 낸다.\r\n\r\n3. 장아찌 국물에 손질한 양파와 청고추, 홍고추를 넣고 끓인다.\r\n\r\n4. 대파를 적당한 크기로 자른 뒤, 토치로 직화한다.\r\n\r\n5. 끓는 물에 직화한 대파와 가쓰오부시를 넣고 끓여 육수를 낸다.\r\n\r\n6. 훈제 닭가슴살과 군고구마, 통조림 햄을 핸드블렌더에 넣고 곱게 간다.\r\n\r\n7. 볼에 달걀물, 곱게 간 재료, 버터를 넣고 섞는다.\r\n\r\n8. 7에 타코야키가루를 섞어 닭완자반죽을 만든다.\r\n\r\n9. 양파장아찌를 얼음 위에서 식힌다.\r\n\r\n10. 가쓰오부시육수를 체에 밭쳐 국물만 걸러낸다.\r\n\r\n11. 냄비에 적당한 크기로 썬 대파와 가쓰오부시육수를 넣고 끓인다.\r\n\r\n12. 11에 체에 거른 된장과 고추장을 풀어 라면 국물을 만든다.\r\n\r\n13. 라면 면을 삶는다.\r\n\r\n14. 덴카스를 잘게 부순다.\r\n\r\n15. 당근, 양파를 채 썬 뒤 닭완자반죽에 넣고 섞는다.\r\n\r\n16. 닭완자반죽을 짤주머니로 짜서 새우모양을 만든 뒤, 타코야키가루를 묻힌다.\r\n\r\n17. 새우모양 닭완자에 덴카스를 입혀 기름에 튀긴다.\r\n\r\n18. 그릇에 양파장아찌를 담고 잘게 썬 청고추와 홍고추를 올린다.\r\n\r\n19. 라면국물에 전분물을 풀어 농도를 조절한다.\r\n\r\n20. 삶은 면에 파래가루를 뿌려 버무린 뒤, 그릇에 담는다.\r\n\r\n21. 면 위에 국물을 붓는다.\r\n\r\n22. 닭완자튀김을 올려 마무리한다.'),
	(580, '중국식 냉면냉면냉면♬', NULL, 1, '1. 라면 면을 삶는다.\r\n\r\n2. 가쓰오부시육수를 낸다.\r\n\r\n3. 양파, 당근, 대파 흰 부분을 채 썰어 찬물에 담가둔다.\r\n\r\n4. 통조림 햄을 채 썬다.\r\n\r\n5. 양파, 당근, 대파 흰 부분, 통조림 햄을 잘게 다진다.\r\n\r\n6. 볼에 달걀, 잘게 다진 채소와 통조림 햄을 넣고 섞는다.\r\n\r\n7. 6에 통조림 참치와 타코야키가루, 물을 넣고 섞어 반죽을 만든다.\r\n\r\n8. 타코야키팬에 반죽을 붓고 굽는다.\r\n\r\n9. 간장, 설탕, 맛술, 식초를 섞어 국물 베이스를 만든다.\r\n\r\n10. 삶은 면을 찬물에 헹군다.\r\n\r\n11. 달걀지단을 만들어 채 썬다.\r\n\r\n12. 국물 베이스에 가쓰오부시육수를 더한 후, 얼음과 함께 믹서기에 넣고 간다.\r\n\r\n13. 12에 칼라만시 원액, 깨를 넣고 섞는다.\r\n\r\n14. 접시에 차갑게 식힌 면을 담은 뒤, 채 썬 재료들과 가쓰오부시, 연겨자를 올린다.\r\n\r\n15. 접시에 참치타코야키를 담고, 마요네즈와 타코야키소스를 뿌린 뒤 가쓰오부시를 올린다.\r\n\r\n16. 면 위에 국물을 부어 완성한다.'),
	(581, '피치 못할 타르트', NULL, 1, '1. 핸드블렌더에 설탕, 곱게 간 레몬껍질을 넣고 돌려 잘 섞어준다.\r\n\r\n2. 레몬설탕에 달걀을 한 알씩 넣으며 갈아 레몬커드를 만든다.\r\n\r\n3. 끓는 물이 담긴 냄비 위에 레몬커드를 중탕하며 잘 저어준다.\r\n\r\n4. 레몬커드에 레몬껍질을 갈아 넣는다.\r\n\r\n5. 핸드블렌더에 밀가루, 버터, 달걀을 넣고 갈아 타르트 반죽을 만든다.\r\n\r\n6. 5를 기름에 튀겨 타르트 가루를 만든다.\r\n\r\n7. 타르트 가루를 건져 기름기를 뺀다.\r\n\r\n8. 천도복숭아와 자두를 적당한 크기로 썬 뒤, 설탕, 올리브오일을 넣어 버무린다.\r\n\r\n9. 설탕에 버무린 과일을 오븐에 익힌다.\r\n\r\n10. 레몬커드를 얼음물 위에 올려 차갑게 식히며, 버터와 레몬즙을 넣고 섞어준다.\r\n\r\n11. 익힌 천도복숭아와 자두를 얼음물 위에 올려 차갑게 식힌다.\r\n\r\n12. 컵에 잘게 썬 천도복숭아와 자두, 타르트 가루를 넣는다.\r\n\r\n13. 컵에 레몬커드를 가득 부은 뒤, 타르트 가루와 익힌 천도복숭아와 자두를 올린다.\r\n\r\n14. 올리브오일을 뿌려 마무리한다.'),
	(582, '최애 컵케이크', NULL, 1, '1. 우유를 끓인다.\r\n\r\n2. 끓이는 우유에 버터를 넣는다.\r\n\r\n3. 브리치즈를 전자레인지에 돌려 부드럽게 만든다.\r\n\r\n4. 브리치즈를 뜯어 부드러운 속 부분만 버터우유에 넣고 잘 섞어준다.\r\n\r\n5. 달걀노른자를 풀어준 뒤, 설탕, 밀가루, 전분을 넣고 잘 저어준다.\r\n\r\n6. 5에 끓인 치즈우유를 부어가며 저어 치즈커스터드크림을 만든다.\r\n\r\n7. 사과를 잘게 썬다.\r\n\r\n8. 기름을 두른 팬에 잘게 썬 사과와 버터, 밀가루를 넣고 볶는다.\r\n\r\n9. 8에 화이트와인, 사과잼, 레몬즙을 넣고 끓인다.\r\n\r\n10. 끓이던 사과소스에 적당한 크기로 썬 식빵을 넣고 조린다.\r\n\r\n11. 조린 사과식빵을 식혀둔다.\r\n\r\n12. 치즈커스터드크림을 체에 밭쳐 곱게 거른다.\r\n\r\n13. 컵에 브라우니를 잘게 부숴 넣는다.\r\n\r\n14. 브라우니 위에 사과식빵을 올린다.\r\n\r\n15. 사과식빵 위에 치즈커스터드크림을 붓고, 모차렐라치즈와 얇게 썬 스모크치즈를 올린다.\r\n\r\n16. 15 위에 브라우니를 부숴 올리고, 사과식빵과 치즈커스터드크림을 차례로 넣는다.\r\n\r\n17. 브라우니를 갈아 올리고, 체리를 얹어 마무리한다.'),
	(583, '쓰촨으로 갈 찬스', NULL, 1, '1. 홍고추를 적당한 크기로 썬다.\r\n\r\n2. 끓는 기름에 홍고추와 통후추를 넣고 끓여 매운 기름을 낸다.\r\n\r\n3. 차돌박이에 소금, 달걀흰자, 전분을 넣고 섞은 뒤 재워둔다.\r\n\r\n4. 기름에 끓인 홍고추와 통후추를 꺼내 식힌다.\r\n\r\n5. 마늘은 으깨고, 대파와 샐러리는 두드린 후 적당한 크기로 썰어둔다.\r\n\r\n6. 팬에 매운 기름을 두른 뒤, 다진 생강과 손질한 채소를 넣고 볶는다.\r\n\r\n7. 볶던 채소에 적당한 크기로 썬 미나리와 소금을 넣고 볶는다.\r\n\r\n8. 볶은 채소를 그릇에 담는다.\r\n\r\n9. 팬에 매운 기름을 두른 뒤, 굴소스와 간장을 넣고 끓인다.\r\n\r\n10. 9에 손질한 대파와 으깬 마늘, 방울토마토, 소금, 물, 다진 생강을 넣고 끓인다.\r\n\r\n11. 끓이던 재료에 양념해 둔 차돌박이를 한 점씩 넣어 익힌다.\r\n\r\n12. 차돌박이를 익히는 기름에 칼라만시액을 넣는다.\r\n\r\n13. 식혀둔 홍고추와 통후추를 잘게 다진다.\r\n\r\n14. 채소가 담긴 그릇에 끓인 채소와 차돌박이를 부은 뒤, 잘게 다진 홍고추와 통후추를 올린다.\r\n\r\n15. 14 위에 끓고 있는 매운 기름을 부어 마무리한다.'),
	(584, '어디에스파냐?', NULL, 1, '1. 기름을 두른 팬에 소고기 등심을 굽는다.\r\n\r\n2. 핸드블렌더에 구운 등심을 넣고 곱게 간다.\r\n\r\n3. 곱게 간 등심에 식빵을 넣고 다시 한 번 갈아 파이반죽을 만든다.\r\n\r\n4. 브리치즈, 스모크치즈, 살라미, 견과류, 천도복숭아를 적당한 크기로 썰어둔다.\r\n\r\n5. 양파를 잘게 썬 뒤, 파이반죽에 섞는다.\r\n\r\n6. 오븐틀에 파이반죽을 넣고 살라미>차돌박이>각종 치즈를 층층이 쌓는다.\r\n\r\n7. 6 위에 달걀을 올린 뒤 전자레인지에 넣고 익힌다.\r\n\r\n8. 핸드블렌더에 방울토마토, 올리브오일, 스리라차소스, 후추, 마늘, 양파를 넣고 곱게 갈아 가스파초를 만든다.\r\n\r\n9. 가스파초에 레몬즙을 넣은 뒤, 냉동실에 넣고 식힌다.\r\n\r\n10. 기름을 두른 팬에 채 썬 양파와 통마늘을 넣고 볶는다.\r\n\r\n11. 볶던 채소에 버터를 넣는다.\r\n\r\n12. 11에 끓인 우유, 설탕, 후추를 넣고 끓여 갈릭소스를 만든다.\r\n\r\n13. 갈릭소스를 핸드블렌더로 곱게 간다.\r\n\r\n14. 그릇에 갈릭소스를 담고, 올리브오일을 뿌린다.\r\n\r\n15. 식힌 가스파초를 꺼내 소금과 후추로 간 한다.\r\n\r\n16. 그릇에 가스파초를 담은 뒤, 잘게 썬 각종 치즈와 견과류, 천도복숭아를 올린다.\r\n\r\n17. 전자레인지에서 고기파이를 꺼낸 뒤, 접시에 담는다.\r\n\r\n18. 고기파이 위에 레몬껍질을 갈아 올리고, 올리브오일과 후추를 뿌린다.\r\n\r\n19. 고추를 갈아 올려 마무리한다.'),
	(585, '햄버거 떠머거', NULL, 1, '1. 기름을 두른 팬과 냄비에 각각 채 썬 양파를 볶는다.\r\n\r\n2. 핸드블렌더에 적당한 크기로 썬 등심을 넣고 간다.\r\n\r\n3. 양파를 볶던 팬에 베이컨을 굽는다.\r\n\r\n4. 구운 베이컨을 기름에 튀긴다.\r\n\r\n5. 곱게 간 등심에 돼지갈비 햄버그스테이크를 섞어 양파를 볶던 팬에 넣고 볶는다.\r\n\r\n6. 튀긴 베이컨을 건져 기름기를 뺀다.\r\n\r\n7. 고기와 양파를 볶던 팬에 소금, 후추, 소불고기양념, 맥주, 빵가루를 넣고 볶아 햄버거 패티를 만든다.\r\n\r\n8. 양파를 볶던 냄비에 버터, 밀가루, 우유, 케첩, 소금을 넣어 케첩소스를 만든다.\r\n\r\n9. 오븐용 그릇에 햄버거패티를 담고, 케첩소스, 슬라이스 모차렐라치즈를 차례로 올린다.\r\n\r\n10. 9 위에 적당한 크기로 썬 베이컨튀김을 올린 뒤, 오븐에 굽는다.\r\n\r\n11. 채 썬 감자에 전분을 묻혀 기름에 튀긴다.\r\n\r\n12. 감자튀김을 건져 기름기를 뺀 뒤, 소금과 후추를 뿌린다.\r\n\r\n13. 오븐에 구운 떠먹는 햄버거 위에 감자튀김을 올린다.\r\n\r\n14. 감자튀김 위에 케첩과 허니머스터드소스를 뿌려 마무리한다.'),
	(586, '오븐 백반 피자', NULL, 1, '1. 핸드블렌더에 즉석밥, 달걀, 소금, 빵가루를 넣고 갈아 반죽을 만든다.\r\n\r\n2. 오븐용 팬에 반죽을 깐 뒤, 오븐에 넣고 굽는다.\r\n\r\n3. 불고기용 소고기를 적당한 크기로 썬 뒤, 칼로 다져 부드럽게 한다.\r\n\r\n4. 손질한 불고기용 소고기에 간장, 다진 마늘, 다진 생강, 전분을 넣고 버무린다.\r\n\r\n5. 양념한 불고기용 소고기에 달걀흰자를 넣고 버무린다.\r\n\r\n6. 마른 팬에 양념한 불고기용 소고기를 볶는다.\r\n\r\n7. 적당한 크기로 썬 김치를 기름을 두른 팬에 볶는다.\r\n\r\n8. 볶던 김치에 고추장과 설탕을 넣고 함께 볶는다.\r\n\r\n9. 오븐에 구운 밥 위에 양배추채를 올린다.\r\n\r\n10. 양배추채 위에 볶은 불고기와 슬라이스 체다치즈와 슬라이스 모차렐라치즈를 올린다.\r\n\r\n11. 10을 다시 오븐에 굽는다.\r\n\r\n12. 김치볶음에 물을 넣고 졸여 김치소스를 만든다.\r\n\r\n13. 김치소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n14. 요거트, 다진 마늘, 달걀노른자, 마요네즈, 소금, 칼라만시 원액을 섞어 요거트소스를 만든다.\r\n\r\n15. 오븐에 구운 떠먹는 피자 위에 김치소스를 올린다.\r\n\r\n16. 요거트소스를 따로 담아낸다.'),
	(587, '브런치 도둑님', NULL, 1, '1. 소금을 넣은 끓는 물에 감자를 데친다.\r\n\r\n2. 잘게 썬 포도와 요거트, 마요네즈, 칼라만시 원액, 소금, 후추, 올리브오일을 섞어 샐러드드레싱을 만든다.\r\n\r\n3. 당근, 양배추를 가늘게 채 썬다.\r\n\r\n4. 채 썬 채소에 샐러드드레싱을 넣고 버무린다.\r\n\r\n5. 버터를 전자레인지에 돌려 녹인다.\r\n\r\n6. 데친 감자를 건져내 물로 헹군 뒤, 채 썬다.\r\n\r\n7. 기름을 두른 팬에 채 썬 감자를 올리고 소금, 후추를 뿌린 뒤 구워 해시브라운을 두 개 만든다.\r\n\r\n8. 달걀노른자, 소금, 레몬즙, 녹인 버터를 섞은 뒤, 끓는 물 위에서 중탕하며 저어 홀랜다이즈소스를 만든다.\r\n\r\n9. 식빵 사이에 슬라이스 체다치즈를 넣은 뒤, 녹인 버터를 두른 팬에 구워 치즈토스트를 만든다.\r\n\r\n10. 끓는 물에 달걀을 넣어 수란을 만든다.\r\n\r\n11. 해시브라운 사이에 슬라이스 체다치즈를 넣고 굽는다.\r\n\r\n12. 치즈토스트 위에 해시브라운치즈샌드를 올린다.\r\n\r\n13. 접시에 샐러드를 담고, 해시브라운 토스트를 올린 뒤 수란을 얹는다.\r\n\r\n14. 13 위에 홀랜다이즈소스를 뿌리고 레몬을 곁들인다.\r\n\r\n15. 후추를 뿌려 마무리한다.'),
	(588, '불런치', NULL, 1, '1. 감자를 채 썬다.\r\n\r\n2. 채 썬 감자를 낮은 온도의 기름에 튀긴다.\r\n\r\n3. 소고기 등심을 길게 썬다.\r\n\r\n4. 손질한 소고기 등심에 잘게 썬 마늘, 후추, 간장을 넣고 버무린다.\r\n\r\n5. 레몬을 썰어 접시에 올린다.\r\n\r\n6. 채 썬 소고기 등심에 밀가루를 묻힌 뒤, 달걀물을 입혀 낮은 온도의 기름에 튀긴다.\r\n\r\n7. 감자튀김을 높은 온도의 기름에 다시 한 번 튀긴 뒤, 건져내 기름기를 빼고 소금을 뿌린다.\r\n\r\n8. 등심튀김을 높은 온도의 기름에 다시 한 번 튀긴다.\r\n\r\n9. 소스종지에 케첩과 허니머스터드소스를 각각 담는다.\r\n\r\n10. 등심튀김을 건져 기름기를 뺀다.\r\n\r\n11. 접시에 감자튀김과 등심튀김을 담은 뒤, 레몬즙을 뿌려 마무리한다.'),
	(589, '귀한 만찬', NULL, 1, '1. 핸드블렌더에 밀가루, 물, 소금을 넣고 갈아 면 반죽을 만든다.\r\n\r\n2. 면 반죽을 치댄 뒤, 숙성시킨다.\r\n\r\n3. 기름에 적당한 크기로 썬 대파와 양파, 고춧가루를 넣고 끓여 고추기름을 낸다.\r\n\r\n4. 끓는 물에 낙지를 데친다.\r\n\r\n5. 양파, 애호박, 꽈리고추, 파프리카, 양배추를 적당한 크기로 썰어둔다.\r\n\r\n6. 면 반죽을 밀대로 넓게 밀고 적당한 크기로 썬 뒤, 손가락으로 눌러 고양이 귀면을 만든다.\r\n\r\n7. 삼겹살을 적당한 크기로 썬 뒤, 전분과 달걀물에 버무린다.\r\n\r\n8. 기름을 넉넉히 두른 팬에 삼겹살을 튀기듯 볶는다.\r\n\r\n9. 데친 낙지를 건져내고, 낙지를 데치던 물에 고양이 귀면을 삶는다.\r\n\r\n10. 으깬 마늘과 적당한 크기로 썬 대파를 삼겹살과 함께 볶은 후 볼에 옮긴다.\r\n\r\n11. 고추기름을 체에 거른다.\r\n\r\n12. 고추기름을 두른 팬에 손질한 각종 채소와 볶은 삼겹살을 넣고 함께 볶는다.\r\n\r\n13. 핸드블렌더에 매실장아찌를 넣고 간다.\r\n\r\n14. 곱게 간 매실장아찌를 볶던 재료에 넣고 함께 볶는다.\r\n\r\n15. 볶던 재료에 간장을 넣는다.\r\n\r\n16. 데친 고양이 귀면을 찬물에 헹군다.\r\n\r\n17. 볶던 재료에 데친 낙지와 고양이 귀면을 넣고 함께 볶는다.\r\n\r\n18. 고양이귀면 낙지삼겹볶음에 설탕을 넣는다.\r\n\r\n19. 그릇에 고양이귀면 낙지삼겹볶음을 담아낸다.'),
	(590, '데리만족', NULL, 1, '1. 마늘, 간장, 맛술, 청주, 설탕, 적당한 크기로 썬 청양고추, 양파를 함께 끓여 청양데리야키소스를 만든다.\r\n\r\n2. 핸드블렌더에 양파, 껍질을 벗긴 아보카도, 마요네즈, 통마늘을 넣고 갈아 아보카도소스를 만든다.\r\n\r\n3. 아보카도소스에 청양고추를 넣고 간다.\r\n\r\n4. 3에 소금, 설탕, 레몬즙을 넣고 섞어준다.\r\n\r\n5. 식빵은 가장자리를 잘라내고, 믹서기에 갈아 빵가루를 만든다.\r\n\r\n6. 소고기 안심을 고기망치로 두드려 얇게 편 뒤, 소금, 후추로 간 하고 전분을 묻힌다.\r\n\r\n7. 전분을 묻힌 안심에 달걀물을 입힌 뒤, 빵가루를 묻힌다.\r\n\r\n8. 튀김옷을 입힌 안심을 튀겨 규카츠를 만든다.\r\n\r\n9. 양파, 삼겹살, 닭안심, 파프리카, 아보카도, 청양고추를 적당한 크기로 썬다.\r\n\r\n10. 기름을 두른 팬에 삼겹살과 닭안심을 볶다가 편마늘과 손질한 채소들, 떡을 넣고 볶는다.\r\n\r\n11. 볶던 재료에 청양데리야키소스를 넣고 볶는다.\r\n\r\n12. 튀긴 규카츠를 건져 기름기를 뺀 뒤, 먹기 좋게 자른다.\r\n\r\n13. 접시에 청양데리야키 삼겹닭볶음과 규카츠를 담는다.\r\n\r\n14. 아보카도소스를 곁들인 뒤, 고춧가루를 뿌려 마무리한다.'),
	(591, '장금이 브런치', NULL, 1, '1. 오이를 슬라이스한다.\r\n\r\n2. 오이를 소금물에 절여둔다.\r\n\r\n3. 소고기 안심을 얇게 포 뜬 뒤, 고기망치로 두드려 얇게 편다.\r\n\r\n4. 참기름과 마늘즙에 안심을 버무린 뒤, 소금으로 간 한다.\r\n\r\n5. 강판에 감자를 간다.\r\n\r\n6. 밀가루, 강판에 간 감자, 전분을 섞어 감자전 반죽을 만든다.\r\n\r\n7. 기름을 두른 팬에 감자전 반죽을 올려 감자전을 굽는다.\r\n\r\n8. 밑간을 한 안심에 전분을 묻힌 뒤, 감자전 위에 올려 감자육전을 굽는다.\r\n\r\n9. 김발 위에 절인 오이를 넓게 펴고, 양상추, 된장, 채 썬 파프리카를 올리고 돌돌 만 뒤, 랩으로 감싸 모양을 고정한다.\r\n\r\n10. 전자레인지에 즉석밥을 돌린다.\r\n\r\n11. 잘게 다진 꽈리고추찜에 밥, 깨, 참기름을 넣고 비벼 꽈리고추밥을 만든다.\r\n\r\n12. 자숙문어 다리에 칼집을 낸 뒤, 전분을 입혀 튀긴다.\r\n\r\n13. 핸드블렌더에 고추장, 매실장아찌, 채소피클국물, 참기름, 깨, 설탕을 넣고 갈아 매실고추장소스를 만든다.\r\n\r\n14. 팬에 설탕과 물을 넣고 끓여 시럽을 만든다.\r\n\r\n15. 카스텔라를 적당한 크기로 자른 뒤, 시럽을 골고루 바른다.\r\n\r\n16. 접시에 꽈리고추밥을 담고, 깨를 뿌린다.\r\n\r\n17. 감자육전을 적당한 크기로 썰어 접시에 담는다.\r\n\r\n18. 접시에 매실고추장소스를 올린 뒤, 문어 튀김을 얹는다.\r\n\r\n19. 랩으로 고정해 둔 오이선을 적당한 크기로 썬 뒤, 접시에 담고 연근피클로 데커레이션 한다.\r\n\r\n20. 접시에 시럽을 입힌 카스텔라를 담고, 콩가루를 뿌려 인절미케이크를 만든다.'),
	(592, '거침없이 불고기', NULL, 1, '1. 가지를 적당한 크기로 썬 뒤, 십자로 칼집을 낸다.\r\n\r\n2. 가지에 소금을 뿌린 뒤, 전자레인지에 돌린다.\r\n\r\n3. 냄비에 식초, 설탕, 소금을 넣고 끓인다.\r\n\r\n4. 무를 슬라이스 한 뒤, 끓인 단촛물을 부어 절인다.\r\n\r\n5. 핸드블렌더에 불고기용 소고기를 곱게 간다.\r\n\r\n6. 핸드블렌더에 다진 마늘, 맛술, 청주, 설탕, 간장, 참기름을 넣고 곱게 갈아 불고기 양념을 만든다.\r\n\r\n7. 곱게 간 불고기용 소고기에 불고기 양념과 잘게 썬 대파를 넣고 버무린 뒤, 기름을 두른 팬에 구워 불고기를 만든다.\r\n\r\n8. 전자레인지에서 익힌 가지 사이에 양념한 불고기용 소고기를 채워 넣고 냄비에 넣는다.\r\n\r\n9. 8에 불고기와 물을 넣고, 뚜껑을 덮어 익힌다.\r\n\r\n10. 볼에 즉석밥, 불고기, 깨, 콩나물, 불고기 양념을 넣고 랩을 덮은 후 전자레인지에 돌려 불고기콩나물밥을 만든다.\r\n\r\n11. 불고기 양념과 다진 청고추, 홍고추, 대파를 넣어 불고기콩나물밥 양념장을 만든다.\r\n\r\n12. 불고기 가지선에 국물을 끼얹어가며 익힌다.\r\n\r\n13. 당근과 오이를 가늘게 채 썬 뒤, 소금을 뿌려둔다.\r\n\r\n14. 기름을 두른 팬에 소금을 뿌린 당근과 오이를 볶는다.\r\n\r\n15. 팽이버섯을 불고기 양념에 버무린 뒤, 볶아준다.\r\n\r\n16. 접시에 볶은 당근, 오이, 팽이버섯과 절인 무, 불고기를 담는다.\r\n\r\n17. 달걀노른자를 분리해 기름을 두른 팬에 붓고 지단을 부친다.\r\n\r\n18. 접시에 불고기가지선을 담는다.\r\n\r\n19. 그릇에 불고기콩나물밥을 담는다.\r\n\r\n20. 달걀노른자지단을 채 썬 뒤, 불고기무쌈과 함께 담아낸다.\r\n\r\n21. 불고기가지선 위에 달걀노른자지단과 가늘게 채 썬 홍고추를 올려 마무리한다.'),
	(593, '젓갈볶음밥을 잘 만드는 남자♬', NULL, 1, '1. 양파를 채 썬다.\r\n\r\n2. 으깬 마늘을 채 썬 양파와 함께 담아둔다.\r\n\r\n3. 레몬즙과 레몬을 넣고 끓인 물에 소금을 뿌린 뒤, 손질한 새우살을 데친다.\r\n\r\n4. 데친 새우살을 얼음물에 담가 차게 식힌다.\r\n\r\n5. 랍스터살을 레몬소금물에 데친 뒤, 얼음물에 담가 차게 식힌다.\r\n\r\n6. 으깬 마늘과 양파채가 담긴 볼에 새우살과 랍스터살을 넣고 레몬즙을 뿌려 섞는다.\r\n\r\n7. 6에 깻잎채를 넣고 버무려 새우세비체를 만든다.\r\n\r\n8. 기름을 두른 팬에 잘게 썬 대파와 고추를 넣고 볶는다.\r\n\r\n9. 자숙피꼬막을 레몬소금물에 데친 뒤, 얼음물에 담가 차게 식힌다.\r\n\r\n10. 볶던 대파와 고추에 순태젓, 가리비젓, 맛술을 넣고 볶는다.\r\n\r\n11. 볶던 젓갈에 굴을 넣고 함께 볶는다.\r\n\r\n12. 데친 자숙피꼬막을 넣고 함께 볶는다.\r\n\r\n13. 볶던 재료에 채 썬 깻잎과 즉석밥을 넣고 볶아 젓갈볶음밥을 만든다.\r\n\r\n14. 접시에 젓갈볶음밥을 담는다.\r\n\r\n15. 젓갈볶음밥에서 피꼬막만 골라내 잘게 다진 뒤, 산초가루를 뿌려 마른 팬에 볶다가 들기름을 뿌린다.\r\n\r\n16. 젓갈볶음밥 위에 깨를 뿌린 뒤, 볶은 피꼬막을 올린다.\r\n\r\n17. 새우세비체에 후추를 뿌리고, 포도, 소금, 올리브오일을 넣고 버무린다.\r\n\r\n18. 접시에 새우세비체를 담아낸다.'),
	(594, '갈치 많이 먹어도 배 안 나오는 남자♬', NULL, 1, '1. 전복을 삶는다.\r\n\r\n2. 갈치를 포 뜬 뒤, 청주에 담가둔다.\r\n\r\n3. 갈치뼈에 전분을 묻혀 튀긴 뒤, 소금을 뿌린다.\r\n\r\n4. 랍스터의 살을 발라 적당한 크기로 썬다.\r\n\r\n5. 깻잎과 갈치살에 전분을 묻힌다.\r\n\r\n6. 박력분과 물, 얼음을 섞어 튀김반죽을 만든다.\r\n\r\n7. 튀김반죽에 성게알을 넣고 섞는다.\r\n\r\n8. 전분을 묻힌 갈치살과 깻잎에 성게튀김반죽을 입혀 기름에 튀긴다.\r\n\r\n9. 새우, 양파, 홍고추를 손질한다.\r\n\r\n10. 삶은 전복의 내장을 떼어내고 전복살을 적당한 크기로 썬다.\r\n\r\n11. 기름을 두른 팬에 편마늘과 양파를 볶다가, 손질한 각종 해산물을 넣고 함께 볶는다.\r\n\r\n12. 11에 소금과 후추로 간 한 뒤, 버터를 넣는다.\r\n\r\n13. 핸드블렌더에 전복내장을 곱게 간다.\r\n\r\n14. 볶던 해산물에 전복내장, 맛간장을 넣고 볶아 전복내장 해산물버터볶음을 만든다.\r\n\r\n15. 성게알, 달걀노른자, 맛간장, 소금을 넣고 섞어 성게소스를 만든 뒤, 접시에 담는다.\r\n\r\n16. 성게소스가 담긴 접시에 갈치뼈튀김과 갈치튀김, 깻잎튀김을 담는다.\r\n\r\n17. 전복내장 해산물버터볶음에 손질한 홍고추와 참기름, 소금을 넣고 볶는다.\r\n\r\n18. 전복내장 해산물버터볶음을 접시에 담아낸다.'),
	(595, '바람은 널 살치게 하네♬', NULL, 1, '1. 살치살에 칼집을 낸다.\r\n\r\n2. 찜기에 손질한 삼겹살을 넣고 산초가루와 나무젓가락에 불을 붙여 훈연한다.\r\n\r\n3. 끓는 물에 적당한 크기로 썬 양파와 대파를 넣고 끓인다.\r\n\r\n4. 3에 설탕, 도라지청, 적당한 크기로 썬 도라지정과, 간장을 넣고 끓여 도라지소스를 만든다.\r\n\r\n5. 가늘게 채 썬 무에 복분자즙, 소금, 식초를 넣고 버무려 복분자무생채를 만든다.\r\n\r\n6. 랩 위에 양상추, 복분자무생채를 올리고 동그랗게 말아 모양을 잡는다.\r\n\r\n7. 훈연한 살치살을 팬에 굽다가 산초가루, 소금, 후추를 뿌린다.\r\n\r\n8. 살치살에 도라지소스를 발라가며 굽는다.\r\n\r\n9. 깻순나물무침을 잘게 다진 뒤, 전자레인지에 돌린 즉석밥과 산초가루를 섞어 깻순나물밥을 만든다.\r\n\r\n10. 김발 위에 구운 살치살>깻순나물밥>파김치를 순서대로 올리고 돌돌 말아 살치살롤을 만든다.\r\n\r\n11. 살치살롤을 랩으로 감싸 모양을 잡는다.\r\n\r\n12. 적당한 크기로 썬 도토리묵에 동그란 구멍을 낸 뒤, 다진고추고기절임을 넣고 접시에 담는다.\r\n\r\n13. 살치살롤을 적당한 크기로 썬 뒤, 랩을 벗겨내고 접시에 담는다.\r\n\r\n14. 접시에 복분자무생채 양상추쌈을 올린다.\r\n\r\n15. 산초장아찌와 오디, 산초가루를 곁들인다.\r\n\r\n16. 도라지소스를 뿌려 마무리한다.'),
	(596, '난 돼지고기가 좋더라♬', NULL, 1, '1. 대파를 반으로 가른 뒤 두드린다.\r\n\r\n2. 비닐백에 손질한 대파와 삼겹살, 으깬 마늘과 적당한 크기로 썬 양파를 넣고 주무른다.\r\n\r\n3. 2에 간장을 넣고 잘 주무른 뒤, 찜기에 넣고 찐다.\r\n\r\n4. 대파를 반으로 길게 썰어 찜기에 넣고 함께 찐다.\r\n\r\n5. 핸드블렌더에 설탕, 식초, 간장, 맛술, 들기름, 연겨자, 레몬즙을 넣고 갈아 간장겨자소스를 만든다.\r\n\r\n6. 무와 양파를 강판에 곱게 간 뒤, 물기를 짜낸다.\r\n\r\n7. 기름에 대파, 고춧가루, 마늘을 넣고 끓여 고추기름을 낸 뒤, 체에 거른다.\r\n\r\n8. 홍고추, 청양고추, 마늘을 잘게 썬 뒤, 산초장아찌와 섞는다.\r\n\r\n9. 8을 고추기름과 함께 팬에 볶다가, 산초가루와 간장겨자소스를 넣어 산초고추기름을 만든다.\r\n\r\n10. 대파찜을 적당한 크기로 썰어 접시에 담고, 간장겨자소스를 뿌린다.\r\n\r\n11. 삼겹살찜을 적당한 크기로 썬 뒤, 접시에 담는다.\r\n\r\n12. 삼겹살찜 위에 강판에 간 무와 양파를 올리고 간장겨자소스를 뿌린다.\r\n\r\n13. 12 위에 산초장아찌를 얹는다.\r\n\r\n14. 산초고추기름을 듬뿍 얹고 간장겨자소스를 뿌려 마무리한다.'),
	(597, '채소를 말아요♬', NULL, 1, '1. 핸드블렌더에 밀가루와 달걀을 넣고 갈아 면 반죽을 만든다.\r\n\r\n2. 레몬즙, 올리브오일, 설탕을 섞어 레몬오일소스를 만든 뒤, 적당한 크기로 썬 토마토를 넣고 절여둔다.\r\n\r\n3. 핸드블렌더에 블랙올리브, 올리브오일, 레몬즙, 코코넛잼을 넣고 갈아 올리브코코넛소스를 만든다.\r\n\r\n4. 올리브코코넛소스에 설탕을 넣고 다시 한 번 곱게 간다.\r\n\r\n5. 올리브코코넛소스에 적당한 크기로 썬 마와 토마토를 넣고 버무려 샐러드를 만든다.\r\n\r\n6. 면 반죽을 얇고 넓게 밀어 적당한 크기로 썰어 끓는 물에 넣고 익힌다.\r\n\r\n7. 기름을 두른 팬에 채 썬 당근을 볶다가 소금, 후추로 간 한다.\r\n\r\n8. 볶던 당근에 간장과 참기름을 넣고 볶은 뒤, 식힌다.\r\n\r\n9. 기름을 두른 팬에 느타리버섯, 팽이버섯, 다진 마늘과 소금, 레몬즙, 후추를 넣고 볶은 뒤, 식힌다.\r\n\r\n10. 삶은 면, 볶은 당근, 볶은 버섯을 한 데 넣고 볶아준다.\r\n\r\n11. 볶은 면을 넓게 편 뒤, 볶은 채소와 레몬오일소스에 절인 토마토를 넣고 돌돌 만다.\r\n\r\n12. 접시에 샐러드를 담는다.\r\n\r\n13. 접시에 채소롤을 올린 뒤, 올리브코코넛소스를 뿌린다.\r\n\r\n14. 샐러드와 채소롤 위에 레몬오일소스와 레몬즙, 후추를 뿌려 마무리한다.'),
	(598, '채소퀸', NULL, 1, '1. 버터를 녹인 팬에 잘게 썬 느타리버섯과 표고버섯을 넣고 볶다가 간장으로 간 한다.\r\n\r\n2. 토마토는 꼭지부분과 밑동을 평평하게 잘라낸 뒤, 속을 파낸다.\r\n\r\n3. 파낸 토마토속을 잘게 다져 버섯과 함께 볶는다.\r\n\r\n4. 마늘을 잘게 다져 다른 재료들과 함께 볶는다.\r\n\r\n5. 잘게 다진 청양고추소박이와 청양고추소박이국물을 다른 재료들과 함께 볶는다.\r\n\r\n6. 5에 즉석밥과 잘게 썬 블랙올리브, 생모차렐라치즈를 넣고 볶아 채소볶음밥을 만든다.\r\n\r\n7. 채소볶음밥에 소금과 후추로 간 한다.\r\n\r\n8. 속을 파낸 토마토 안에 채소볶음밥을 채워 넣는다.\r\n\r\n9. 채소볶음밥을 채운 토마토 위에 생모차렐라치즈를 올린 뒤, 예열된 오븐에 굽는다.\r\n\r\n10. 접시에 토마토, 깻잎, 오이김치, 블랙올리브, 생모차렐라치즈로 데커레이션한다.\r\n\r\n11. 채소볶음밥을 채운 토마토를 오븐에서 꺼낸 뒤, 토치로 직화한다.\r\n\r\n12. 데커레이션 한 접시 위에 채소볶음밥을 채운 토마토를 올려 마무리한다.'),
	(599, '완선이는 이걸 먹고 웃지', NULL, 1, '1. 소갈빗살을 적당한 크기로 썬다.\r\n\r\n2. 키위의 껍질을 벗겨내고 핸드블렌더로 간다.\r\n\r\n3. 소갈빗살에 곱게 간 키위, 적당한 크기로 썬 대파, 생강즙, 간장, 설탕, 손질한 새송이버섯, 후추, 맛술을 넣고 잘 버무린다.\r\n\r\n4. 3에 으깬 마늘을 넣고 기름을 두른 냄비에 볶는다.\r\n\r\n5. 칼집을 낸 대구살을 체에 밭친 뒤, 뜨거운 물을 부어 겉을 익힌다.\r\n\r\n6. 겉을 익힌 대구살을 그릇에 담은 뒤, 생강즙과 청주, 소금을 뿌리고 찜기에 넣어 찐다.\r\n\r\n7. 6에 잘게 다진 청양고추를 넣고 찐다.\r\n\r\n8. 라면 면을 삶는다.\r\n\r\n9. 대파를 가늘게 채 썬다.\r\n\r\n10. 간장, 맛술, 설탕을 섞어 간장소스를 만든다.\r\n\r\n11. 스리라차소스, 간장, 식초를 섞어 양념을 만든다.\r\n\r\n12. 삶은 면을 체에 밭쳐 찬물에 헹군다.\r\n\r\n13. 면을 삶았던 물에 간장, 굴소스볶음소스, 된장을 넣고 끓인다.\r\n\r\n14. 핸드블렌더에 청양고추를 넣고 곱게 간다.\r\n\r\n15. 굴소스육수에 곱게 간 청양고추를 넣고 끓인다.\r\n\r\n16. 찬물에 식힌 면을 굴소스육수에 토렴한 뒤, 그릇에 담는다.\r\n\r\n17. 16 위에 볶은 소갈빗살과 채소를 올리고, 굴소스육수를 부어 소고기탕면을 만든다.\r\n\r\n18. 기름을 두른 팬에 대구살찜을 올린 뒤, 간장소스를 붓고 파채를 얹는다.\r\n\r\n19. 18 위에 끓는 기름을 부어 익힌다.\r\n\r\n20. 대구살찜 위에 스리라차양념을 얹어 마무리한다.'),
	(600, '김완섬', NULL, 1, '1. 핸드블렌더에 불린 쌀, 밀가루, 전분, 물을 넣고 갈아 딤섬피 반죽을 만든다.\r\n\r\n2. 끓는 기름에 대파, 고춧가루를 넣고 끓여 고추기름을 낸다.\r\n\r\n3. 기름을 두른 팬에 간장, 설탕을 넣고 태운다.\r\n\r\n4. 3에 물을 부어 간장소스를 만든다.\r\n\r\n5. 간장소스를 체에 거른다.\r\n\r\n6. 간장소스에 잘게 썬 청양고추와 대파, 으깬 마늘을 넣는다.\r\n\r\n7. 대구살을 적당한 크기로 썬다.\r\n\r\n8. 기름을 두른 팬에 적당한 크기로 썬 대구살과 소금, 후추, 다진 생강, 으깬 마늘을 넣고 볶는다.\r\n\r\n9. 8에 맛술과 적당한 크기로 썬 팽이버섯을 넣고 볶는다.\r\n\r\n10. 마른 팬에 딤섬피 반죽을 붓고, 뚜껑을 덮어 익힌다.\r\n\r\n11. 딤섬피 위에 볶은 대구살과 팽이버섯을 올리고 돌돌 만다.\r\n\r\n12. 11을 그릇에 담는다.\r\n\r\n13. 고추기름을 체에 밭쳐 거른다.\r\n\r\n14. 대구팽이버섯딤섬 위에 간장소스를 끼얹는다.\r\n\r\n15. 14 위에 고추기름을 뿌려 마무리한다.'),
	(601, '낙낙낙낙 낙지호롱♬', NULL, 1, '1. 냄비에 물, 설탕, 청주, 맛술, 간장을 1:1:1:1:1 비율로 넣고 끓여 간장소스를 만든다.\n\n2. 간장소스에 으깬 마늘을 넣는다.\n\n3. 낙지 머리의 내장을 빼고 손질한다.\n\n4. 핸드블렌더에 대패삼겹살과 대파 흰 부분을 넣고 곱게 간다.\n\n5. 손질한 낙지 머리 안에 곱게 간 대패삼겹살을 채워 넣고 이쑤시개로 고정시켜 낙지만두를 만든다.\n\n6. 낙지만두를 간장소스 안에 넣고 조린다.\n\n7. 낙지 머리를 나무젓가락에 끼운 뒤, 낙지 다리를 나무젓가락에 돌돌 말아 낙지호롱을 만든다.\n\n8. 낙지호롱에 대패삼겹살을 돌돌 만다.\n\n9. 마른 그릴팬에 낙지호롱을 굽다가 청주를 붓는다.\n\n10. 낙지호롱에 간장소스를 부어가며 굽는다.\n\n11. 맛술, 물, 식초, 설탕, 간장, 달걀노른자를 섞는다.\n\n12. 11을 끓는 물 위에서 중탕하며 계속 저어 기미스소스를 만든다.\n\n13. 기미스소스에 가츠오부시를 넣는다.\n\n14. 기미스소스를 체에 밭쳐 곱게 거른다.\n\n15. 접시에 기미스소스를 담는다.\n\n16. 기미스소스 위에 낙지호롱과 낙지만두를 올린다.\n\n17. 고춧가루를 뿌려 마무리한다.'),
	(602, '유러피안 낙지', NULL, 1, '1. 끓는 물에 낙지를 삶는다.\r\n\r\n2. 낙지를 삶는 물에 콜라를 넣는다.\r\n\r\n3. 통후추, 허브솔트, 바질가루, 화이트와인, 와인코르크마개를 넣고 삶는다.\r\n\r\n4. 으깬 마늘과 자몽 껍질도 넣고 삶는다.\r\n\r\n5. 양배추를 가늘게 채 썬다.\r\n\r\n6. 자몽을 적당한 크기로 썰고, 자몽즙을 내 볼에 담아둔다.\r\n\r\n7. 파프리카, 당근, 애호박을 채 썬다.\r\n\r\n8. 손질한 채소에 소금, 자몽즙, 올리브오일, 후추를 넣고 버무려 자몽채소샐러드를 만든다.\r\n\r\n9. 식빵은 테두리부분을 잘라내고, 깍둑썰기 한다.\r\n\r\n10. 식빵 위에 허브솔트와 버터를 뿌린 뒤, 오븐에 구워 크루통을 만든다.\r\n\r\n11. 아몬드를 잘게 다져 마른 팬에 볶는다.\r\n\r\n12. 버터를 녹인 그릴 팬에 손질한 새우를 넣고 볶는다.\r\n\r\n13. 12에 느타리버섯과 소금을 넣고 볶는다.\r\n\r\n14. 크루통에 고춧가루를 뿌린다.\r\n\r\n15. 삶은 낙지를 그릴팬에 굽는다.\r\n\r\n16. 접시에 자몽채소샐러드를 담은 뒤, 볶은 새우와 느타리버섯을 곁들인다.\r\n\r\n17. 잘게 썬 마늘쫑, 귤, 볶은 아몬드를 곁들인다.\r\n\r\n18. 삶아서 구운 낙지를 적당한 크기로 썰어 올리고, 크루통을 뿌려 마무리한다.'),
	(603, '복사부일체', NULL, 1, '1. 대파 흰 부분을 적당한 크기로 썰어 끓는 기름에 넣고 끓인다.\r\n\r\n2. 파기름에 고춧가루를 넣어 고추기름을 낸다.\r\n\r\n3. 기름을 두른 팬에 잘게 다진 호두와 잣을 넣고 볶아 견과류기름을 낸다.\r\n\r\n4. 견과류기름을 체에 밭쳐 기름만 고추기름에 넣는다.\r\n\r\n5. 가지를 적당한 두께로 썰어 그릴팬에 굽는다.\r\n\r\n6. 말린 새송이버섯을 물에 불린 후, 물기를 짠다.\r\n\r\n7. 파프리카, 양파, 애호박을 가늘게 채 썬다.\r\n\r\n8. 오징어는 손질해서 껍질을 벗긴다.\r\n\r\n9. 새우의 껍질을 벗기고 편으로 썬다.\r\n\r\n10. 끓는 물에 손질한 오징어와 새우를 데친 뒤, 찬물에 헹궈둔다.\r\n\r\n11. 고추기름을 체에 밭쳐 건더기를 걸러낸다.\r\n\r\n12. 데친 오징어와 대파 흰 부분을 적당한 크기로 썰고, 마늘은 으깬다.\r\n\r\n13. 고추기름을 두른 팬에 손질한 채소들을 넣고 볶다가 간장, 후추, 굴소스, 맛술, 식초, 물을 넣고 볶는다.\r\n\r\n14. 볶던 채소에 손질한 오징어와 새우를 넣고 볶는다.\r\n\r\n15. 소면을 삶는다.\r\n\r\n16. 접시에 구운 가지를 담는다.\r\n\r\n17. 삶은 소면을 찬물에 헹군 뒤, 참기름을 넣고 버무린다.\r\n\r\n18. 구운 가지 위에 참기름에 버무린 소면을 올린다.\r\n\r\n19. 달걀흰자로 머랭을 친다.\r\n\r\n20. 고추기름을 두른 팬에 대파 흰 부분과 데친 새우를 넣고 볶다가 물을 붓는다.\r\n\r\n21. 접시에 해산물채소볶음을 얹는다.\r\n\r\n22. 볶던 새우에 머랭, 볶은 견과류를 넣고 함께 볶는다.\r\n\r\n23. 해산물볶음 위에 머랭새우볶음을 얹어 마무리한다.'),
	(604, '넌 학생이고 난 샘이야', NULL, 1, '1. 껍질을 벗긴 마를 얇게 슬라이스 한 뒤, 삶는다.\r\n\r\n2. 양파, 애호박, 가지, 파프리카를 잘게 썬다.\r\n\r\n3. 마른 팬에 손질한 채소를 넣고 밀가루, 소금, 후추를 뿌려 볶는다.\r\n\r\n4. 전자레인지에 황태채를 넣고 돌린다.\r\n\r\n5. 마른 팬에 버터, 밀가루를 볶아 루를 만든 뒤, 두유를 넣고 끓여 두유크림소스를 만든다.\r\n\r\n6. 전자레인지에 돌린 황태채를 핸드블렌더로 곱게 간다.\r\n\r\n7. 냄비에 삶은 마를 넣고 으깬 뒤, 소금, 황태채가루를 넣고 볶는다.\r\n\r\n8. 7을 두유소스에 넣고 끓여 황태크림소스를 만든다.\r\n\r\n9. 발사믹드레싱, 레몬즙, 올리브오일을 섞은 뒤, 볶은 채소와 바질가루를 넣고 절인다.\r\n\r\n10. 채소절임에 다진 잣을 넣고 섞는다.\r\n\r\n11. 식빵은 테두리부분을 잘라낸 뒤, 밀대로 얇게 밀어 마른 팬에 굽는다.\r\n\r\n12. 기름을 두른 팬에 새우를 굽다가, 소금, 후추로 간 한다.\r\n\r\n13. 접시에 황태크림소스를 담은 뒤, 채소절임을 올리고 레몬조각을 곁들인다.\r\n\r\n14. 구운 식빵을 적당한 크기로 썰어 식빵칩을 만든다.\r\n\r\n15. 접시에 식빵칩을 얹은 뒤, 구운 새우를 올린다.\r\n\r\n16. 후추를 뿌려 마무리한다.'),
	(605, '조기 이즈 베리 임폴턴트', NULL, 1, '1. 조기는 머리를 잘라내고 지느러미와 가시를 발라 넓게 펼친다.\r\n\r\n2. 손질한 조기에 후추를 뿌리고 마늘꿀을 바른다.\r\n\r\n3. 기름을 두른 팬에 양념한 조기를 굽다가 맛술을 뿌린다.\r\n\r\n4. 대파를 넓게 펼쳐 조기와 함께 굽는다.\r\n\r\n5. 달궈진 찜기에 녹차잎을 넣은 뒤, 구운 조기를 훈연한다.\r\n\r\n6. 훈연한 조기를 구운 대파로 감싸 돌돌 만 뒤, 다시 한 번 훈연한다.\r\n\r\n7. 잘게 썬 청양고추, 명란젓, 고추냉이, 마요네즈, 간장을 섞어 명란마요소스를 만든다.\r\n\r\n8. 명란마요소스에 달걀노른자를 넣고 섞은 뒤, 소스종지에 담아낸다.\r\n\r\n9. 기름을 두른 팬에 즉석밥을 넣고, 아몬드, 잣, 크랜베리를 넣어 볶는다.\r\n\r\n10. 견과류볶음밥에 물, 버터, 소금을 넣고 볶는다.\r\n\r\n11. 접시에 견과류볶음밥을 담는다.\r\n\r\n12. 가지를 적당한 크기로 썬다.\r\n\r\n13. 기름을 두른 팬에 가지를 굽는다.\r\n\r\n14. 견과류볶음밥 위에 훈연한 조기를 올린다.\r\n\r\n15. 구운 가지에 오리엔탈드레싱을 넣고 버무린다.\r\n\r\n16. 견과류볶음밥과 조기가 담긴 접시에 구운 가지를 곁들여 마무리한다.'),
	(606, '돈테크만', NULL, 1, '1. 삽겹살에 칼집을 내고 소금으로 간을 한다.\r\n\r\n2. 기름을 두른 냄비에 칼집을 낸 삼겹살과 대파를 넣고 익힌다.\r\n\r\n3. 오징어의 껍질을 벗겨내고 가늘게 채 썬 뒤, 소금으로 간을 한다.\r\n\r\n4. 팬에 발사믹식초, 설탕, 딸기잼을 넣고 끓여 발사믹소스를 만든다.\r\n\r\n5. 삼겹살을 익히는 기름에 청주를 붓고 올라오는 김에 불을 붙여 오징어를 익힌다.\r\n\r\n6. 마른 팬에 잣을 볶은 뒤 핸드블렌더에 넣고 곱게 간다.\r\n\r\n7. 곱게 간 잣에 우유를 붓고 다시 한 번 갈아 잣소스를 만든다.\r\n\r\n8. 잣소스에 쯔유, 연겨자를 넣고 섞은 뒤, 익힌 오징어를 넣고 버무린다.\r\n\r\n9. 접시에 잣소스에 버무린 오징어를 담는다.\r\n\r\n10. 대파 흰 부분을 어슷하게 채 썬 뒤, 얼음물에 담가둔다.\r\n\r\n11. 간장, 식초, 맛술, 설탕을 섞어 간장소스를 만든다.\r\n\r\n12. 채 썬 대파의 물기를 빼고 간장소스에 버무린 뒤, 고춧가루를 뿌린다.\r\n\r\n13. 익힌 삼겹살을 적당한 크기로 썬 뒤, 다시 한 번 파기름에 익힌다.\r\n\r\n14. 발사믹소스에 홍삼진액, 물, 버터를 넣고 졸여 발사믹홍삼소스를 만든다.\r\n\r\n15. 접시에 발사믹홍삼소스를 뿌린다.\r\n\r\n16. 잣소스에 버무린 오징어 위에 익힌 삼겹살과 파채무침을 올려 마무리한다.'),
	(607, '출발! 짜글이 여행', NULL, 1, '1. 들기름을 두른 팬에 된장을 볶는다.\r\n\r\n2. 무는 껍질을 벗기고 채 썬다.\r\n\r\n3. 볶은 된장에 채 썬 무와 새우젓을 넣고 볶는다.\r\n\r\n4. 무를 볶던 팬에 게맛살을 찢어 넣는다.\r\n\r\n5. 4에 고춧가루와 채 썬 새송이버섯을 넣고 볶는다.\r\n\r\n6. 명란젓을 손질해 팬에 넣고 다른 재료들과 함께 볶는다.\r\n\r\n7. 볶던 재료에 강판에 간 무를 넣는다.\r\n\r\n8. 볶던 재료에 물, 쯔유, 식초, 적당한 크기로 썬 대파를 넣고 끓여 짜글이를 만든다.\r\n\r\n9. 즉석밥을 전자레인지에 돌린다.\r\n\r\n10. 짜글이에 으깬 마늘을 넣고 끓인다.\r\n\r\n11. 밥그릇에 밥을 담는다.\r\n\r\n12. 양은냄비에 짜글이를 옮겨 담아 마무리한다.'),
	(608, '명불허전 그뤠잇!', NULL, 1, '1. 끓는 기름에 대파를 넣고 끓여 파기름을 낸다.\r\n\r\n2. 삼겹살을 적당한 크기로 썬다.\r\n\r\n3. 끓는 물에 맥주를 부은 뒤, 손질한 삼겹살을 넣고 익힌다.\r\n\r\n4. 맥주에 익힌 삼겹살을 체에 밭쳐 물기를 뺀다.\r\n\r\n5. 양파를 강판에 곱게 간다.\r\n\r\n6. 간 양파와 양파장아찌, 매운볶음소스, 고춧가루, 깨, 매실액, 참치액을 섞어 돼지불백 양념을 만든다.\r\n\r\n7. 양파, 대파, 새송이버섯, 홍고추, 청양고추를 적당한 크기로 썬다.\r\n\r\n8. 팬에 파기름을 두른 뒤 데친 삼겹살을 볶는다.\r\n\r\n9. 볶은 삼겹살에 돼지불백 양념과 손질한 채소들을 넣고 볶다가, 설탕을 넣는다.\r\n\r\n10. 낫토와 명란젓을 섞은 뒤, 김에 싼다.\r\n\r\n11. 10에 밀가루>튀김반죽을 순서대로 입혀 기름에 튀긴 뒤 기름기를 뺀다.\r\n\r\n12. 즉석밥을 전자레인지에 돌린다.\r\n\r\n13. 돼지불백에 후추를 넣는다.\r\n\r\n14. 돼지불백에 채 썬 새송이버섯과 잘게 찢은 게맛살을 넣고 볶는다.\r\n\r\n15. 돼지불백을 팬에 옮겨 담는다.\r\n\r\n16. 밥그릇에 밥을 담는다.\r\n\r\n17. 돼지불백 위에 명란낫토튀김을 올려 마무리한다.'),
	(609, 'HOT한 갈비찜', NULL, 1, '1. 인스턴트 소갈비탕의 소갈비와 국물을 분리한다.\r\n\r\n2. 양파, 새송이버섯, 대파를 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\r\n\r\n3. 인스턴트 소갈비탕 국물에 다진 마늘, 다진 생강, 간장, 설탕, 참기름을 넣고 섞어 육수를 만든다.\r\n\r\n4. 볶던 채소에 육수를 넣고 졸인다.\r\n\r\n5. 육수를 부어 끓이던 채소에 콩나물, 쌈장, 청양고추, 맛술, 소갈비, 볶음고추장, 고춧가루를 넣고 졸여 갈비찜을 만든다.\r\n\r\n6. 믹서기에 감자칩과자, 감자스틱과자, 김과자, 새우맛 누룽지과자를 넣고 곱게 간다.\r\n\r\n7. 곱게 간 과자가루에 인스턴트 소갈비탕 국물, 달걀노른자, 전분, 볶음고추장을 섞어 장떡반죽을 만든다.\r\n\r\n8. 기름을 두른 팬에 장떡반죽을 구워 장떡을 만든다.\r\n\r\n9. 다진 마늘을 체에 밭쳐 물에 헹구고 물기를 짠 뒤, 갈비찜에 넣는다.\r\n\r\n10. 접시에 장떡을 담고, 들기름을 바른다.\r\n\r\n11. 그릇에 갈비찜을 담고, 잘게 썬 대파를 올려 마무리한다.'),
	(610, '입덕볶이', NULL, 1, '1. 끓는 물에 간장, 맛술을 넣고 끓인다.\r\n\r\n2. 새송이버섯을 떡 모양으로 길쭉하게 썬다.\r\n\r\n3. 손질한 새송이버섯을 간장물에 넣고 익힌다.\r\n\r\n4. 전분, 밀가루, 달걀흰자를 섞어 반죽한다.\r\n\r\n5. 익힌 새송이버섯에 반죽을 입힌 뒤, 다시 간장물에 넣고 삶는다.\r\n\r\n6. 마른 팬에 두드려서 적당한 크기로 썬 대파, 으깬 통조림 햄, 비빔냉면수프, 고추장, 고춧가루, 간장을 넣고 볶아 떡볶이 양념을 만든다.\r\n\r\n7. 떡볶이 양념에 마늘을 으깨 넣는다.\r\n\r\n8. 떡볶이 양념에 인스턴트 짜장, 콩나물, 뜨거운 물, 설탕, 적당한 크기로 썬 양파를 넣고 끓인다.\r\n\r\n9. 끓는 기름에 인스턴트 냉면을 넣고 튀긴다.\r\n\r\n10. 그릇에 튀긴 냉면을 부숴 넣는다.\r\n\r\n11. 떡볶이 양념에 전분물을 넣어 농도를 조절한다.\r\n\r\n12. 반죽을 입혀 삶은 새송이버섯을 떡볶이 양념에 넣고 졸인다.\r\n\r\n13. 냉면튀김 위에 버섯떡볶이를 올린다.'),
	(611, '소생소사', NULL, 1, '1. 소고기 등심은 힘줄을 제거하고 적당한 크기로 자른다.\r\n\r\n2. 아스파라거스와 새송이버섯을 적당한 크기로 자른다.\r\n\r\n3. 꼬치에 손질한 소고기등심, 새송이버섯, 아스파라거스를 끼운다.\r\n\r\n4. 간장, 맛술, 청주, 설탕, 물, 회과육소스를 섞어 양념을 만든 뒤, 소고기 꼬치에 재운다.\r\n\r\n5. 뜨거운 물, 땅콩버터, 설탕, 맛술, 연겨자, 식초, 마요네즈를 섞어 땅콩버터소스를 만든다.\r\n\r\n6. 그릴팬에 양념에 재워둔 소고기 꼬치를 구워 소고기 사테를 만든다.\r\n\r\n7. 굽던 소고기 사테에 후추를 뿌린다.\r\n\r\n8. 끓는 물에 적당한 크기로 썬 홍고추와 청양고추, 식초, 양파, 마늘을 넣고 끓인다.\r\n\r\n9. 8과 새우과자를 핸드블렌더에 넣고 갈아 삼발소스를 만든다.\r\n\r\n10. 삼발소스에 설탕, 식초를 넣고 간다.\r\n\r\n11. 종지에 땅콩버터소스를 담는다.\r\n\r\n12. 마른 팬에 마카다미아를 볶은 뒤, 잘게 다져 땅콩버터소스 위에 올린다.\r\n\r\n13. 접시에 소고기 사테를 담는다.\r\n\r\n14. 삼발소스에 회과육소스를 넣는다.\r\n\r\n15. 14에 고춧가루를 넣은 뒤 핸드블렌더로 곱게 간다.\r\n\r\n16. 삼발소스에 고추장을 넣고 곱게 간다.\r\n\r\n17. 삼발소스를 종지에 담은 뒤, 레몬즙을 뿌린다.\r\n\r\n18. 소고기 사테, 땅콩버터소스, 삼발소스를 함께 낸다.'),
	(612, '닭스키스', NULL, 1, '1. 닭다리를 발골한다.\r\n\r\n2. 적당한 크기로 자른 닭다리살에 소금으로 간 한 뒤, 전분을 입혀 기름에 튀긴다.\r\n\r\n3. 끓는 기름에 으깬 마늘, 대파, 고춧가루를 끓여 고추기름을 낸다.\r\n\r\n4. 홍고추를 토치로 직화해 껍질을 태운다.\r\n\r\n5. 태운 홍고추의 껍질을 벗겨낸 뒤, 적당한 크기로 썬다.\r\n\r\n6. 핸드블렌더에 홍고추, 참기름, 설탕, 체에 거른 고추기름, 콜라를 넣고 갈아 칠리소스를 만든다.\r\n\r\n7. 땅콩버터, 고추장, 칠리소스, 마요네즈, 간장을 섞어 칠리땅콩버터소스를 만든다.\r\n\r\n8. 대파 흰 부분, 홍고추, 청양고추를 적당한 크기로 썬다.\r\n\r\n9. 기름을 두른 팬에 손질한 채소를 볶다가 맥주를 붓고 끓인다.\r\n\r\n10. 9에 오렌지즙, 설탕을 넣고 끓여 오렌지맥주소스를 만든다.\r\n\r\n11. 종지에 칠리땅콩버터소스를 담아낸다.\r\n\r\n12. 오렌지맥주소스에 전분물을 넣어 농도를 조절한다.\r\n\r\n13. 볼에 오렌지맥주소스, 튀긴 닭다리살을 넣고 오렌지껍질을 갈아 넣어 잘 섞어준다.\r\n\r\n14. 비닐백에 곡물시리얼을 넣고 잘게 부순 뒤 오렌지맥주소스에 버무린 닭다리살 튀김에 더한다.\r\n\r\n15. 오렌지맥주소스에 버무린 닭다리살튀김을 접시에 담아낸다.'),
	(613, '삼겹살~ 왜 내 맘을 흔드는 건데♬', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 팬에 손질한 삼겹살을 볶다가 소금, 후추로 간 한다.\r\n\r\n3. 볶은 삼겹살을 덜어두고, 같은 팬에 적당한 크기로 썬 마늘과 대파, 양파를 볶는다.\r\n\r\n4. 볼에 기름, 고춧가루를 넣고 전자레인지에 돌려 고추기름을 낸 뒤, 체에 밭쳐 건더기를 거른다.\r\n\r\n5. 간장, 물, 굴소스, 식초, 설탕, 다진 마늘을 섞어 간장소스를 만든다.\r\n\r\n6. 기름을 두른 그릴팬에 대파, 고추, 홍고추를 굽는다.\r\n\r\n7. 볶던 채소에 잘게 썬 홍고추와 볶아둔 삼겹살을 넣고 함께 볶는다.\r\n\r\n8. 굽던 채소에 소금을 뿌린다.\r\n\r\n9. 볶던 재료에 간장소스를 넣고 볶아 간장삼겹살볶음을 만든다.\r\n\r\n10. 간장삼겹살볶음에 설탕을 넣는다.\r\n\r\n11. 간장삼겹살볶음에 구운 대파를 넣어 간장소스를 입힌다.\r\n\r\n12. 구운 고추와 홍고추를 적당한 크기로 썬 뒤, 올리브오일, 레몬즙, 소금, 후추를 뿌린다.\r\n\r\n13. 접시에 구운 대파를 담은 뒤, 간장삼겹살볶음을 올린다.\r\n\r\n14. 13 위에 잘게 썬 대파를 올린다.\r\n\r\n15. 간장삼겹살볶음 위에 올리브오일에 절인 구운 고추를 얹는다.\r\n\r\n16. 깨, 고추기름, 참기름을 차례로 뿌려 마무리한다.'),
	(614, '삼겹살DNA', NULL, 1, '1. 삼겹살을 적당한 크기로 썬다.\r\n\r\n2. 기름을 두른 팬에 통마늘을 볶는다.\r\n\r\n3. 통마늘을 볶던 팬에 소시지, 햄을 적당한 크기로 썰어 넣고 함께 볶는다.\r\n\r\n4. 양파, 당근, 대파를 적당한 크기로 썬다.\r\n\r\n5. 볶던 재료에 손질한 채소를 넣고 함께 볶는다.\r\n\r\n6. 올리브오일, 식초, 레몬즙, 칠리페퍼소스를 섞어 칠리소스를 만든다.\r\n\r\n7. 적당한 크기로 썬 양파와 홍고추에 칠리소스, 소금, 후추, 잘게 썬 대파를 버무린 뒤, 종지에 담는다.\r\n\r\n8. 기름을 두른 팬에 다진 마늘, 버터를 넣고 볶다가 달걀물을 넣고 에그스크램블을 만든다.\r\n\r\n9. 에그스크램블에 즉석밥을 넣고 볶아 달걀밥을 만든다.\r\n\r\n10. 달걀밥에 소금, 후추로 간 한다.\r\n\r\n11. 볶던 재료에 레드와인, 뜨거운 물, 검은콩 두유를 넣고 끓여 페이조아다를 만든다.\r\n\r\n12. 페이조아다에 후추를 뿌린다.\r\n\r\n13. 페이조아다에 소금, 적당한 크기로 썬 대파와 청양고추를 넣고 끓인다.\r\n\r\n14. 접시에 달걀밥을 담는다.\r\n\r\n15. 달걀밥과 함께 페이조아다를 담아낸다.'),
	(615, '골풍기', NULL, 1, '1. 양파를 채 썰어 얼음물에 담가둔다.\r\n\r\n2. 끓는 기름에 적당한 크기로 썬 대파와 양파를 넣고 끓이다가, 고춧가루를 넣어 고추기름을 낸다.\r\n\r\n3. 골뱅이를 적당한 크기로 썬다.\r\n\r\n4. 밀가루, 물을 섞어 튀김반죽을 만든다.\r\n\r\n5. 골뱅이에 튀김반죽을 입혀 기름에 튀긴다.\r\n\r\n6. 골뱅이 튀김을 건져 기름기를 뺀다.\r\n\r\n7. 냄비에 버터를 녹인다.\r\n\r\n8. 녹인 버터에 잘게 썬 양파, 밀가루, 우유를 넣고 끓여 베샤멜소스를 만든다.\r\n\r\n9. 베샤멜소스에 소금, 굴소스를 넣고 끓인다.\r\n\r\n10. 9에 적당한 크기로 썬 골뱅이와 가래떡을 넣고 끓여 골뱅이 크림수프를 만든다.\r\n\r\n11. 골뱅이 크림수프에 땅콩버터를 넣는다.\r\n\r\n12. 골뱅이 크림수프에 모차렐라치즈를 넣는다.\r\n\r\n13. 핸드블렌더에 견과류를 곱게 간다.\r\n\r\n14. 대파, 양파, 당근, 청양고추는 잘게 썰고, 마늘은 으깬다.\r\n\r\n15. 고추기름을 체에 밭쳐 건더기를 건져낸다.\r\n\r\n16. 팬에 고추기름을 두른 뒤, 손질한 채소, 간장, 맛술, 식초, 설탕, 굴소스, 후추를 넣고 볶아 깐풍소스를 만든다.\r\n\r\n17. 깐풍소스에 골뱅이 튀김을 넣고 볶는다.\r\n\r\n18. 얼음물에 담가뒀던 양파채를 접시에 깐 뒤, 골뱅이 깐풍기를 올린다.\r\n\r\n19. 골뱅이 깐풍기 위에 곱게 간 견과류를 뿌린다.\r\n\r\n20. 골뱅이 깐풍기와 골뱅이 크림수프를 따로 낸다.'),
	(616, '김치 곱빼기', NULL, 1, '1. 끓는 물에 배추김치와 사골국을 넣고 끓인다.\r\n\r\n2. 기름을 두른 팬에 적당한 크기로 썬 양파, 대파를 볶다가 간 돼지고기와 인스턴트 양념곱창을 넣고 볶는다.\r\n\r\n3. 기름을 두른 팬에 적당한 크기로 썬 두부를 굽는다.\r\n\r\n4. 생두부를 으깬 뒤 볶던 재료에 넣는다.\r\n\r\n5. 4를 끓이던 김칫국에 넣고 끓여 김치전골을 만든다.\r\n\r\n6. 무를 얇게 썬 뒤, 소금물에 절여둔다.\r\n\r\n7. 절인 무에 고추장, 참기름, 설탕을 버무려 기름을 두른 팬에 굽는다.\r\n\r\n8. 배추김치를 물에 씻은 뒤, 가늘게 채 썬다.\r\n\r\n9. 적당한 크기로 썬 데친 참나물, 고구마순김치와 다진 깨를 버무려 참나물 김치무침을 만든다.\r\n\r\n10. 핸드블렌더로 명이나물장아찌를 곱게 간다.\r\n\r\n11. 참나물 김치무침에 곱게 간 명이나물장아찌와 참기름, 식초를 넣고 버무린다.\r\n\r\n12. 접시에 참나물 김치무침을 담는다.\r\n\r\n13. 끓이던 김치전골에 식초, 설탕, 고춧가루를 넣는다.\r\n\r\n14. 김치전골에 적당한 크기로 썬 홍고추, 표고버섯, 고추, 마늘즙을 넣고 끓인다.\r\n\r\n15. 그릇에 밥을 담는다.\r\n\r\n16. 김치전골 위에 무구이와 구운 두부, 팽이버섯을 올린다.\r\n\r\n17. 잘게 썬 대파를 올려 마무리한다.'),
	(617, '레이먼킴치', NULL, 1, '1. 배추김치를 물에 씻는다.\n\n2. 씻은 배추김치와 사과, 당근, 청양고추를 가늘게 채 썬다.\n\n3. 채 썬 재료에 마요네즈, 요거트를 넣고 섞어 김치샐러드를 만든다.\n\n4. 세 개의 팬에 돼지목살의 비계를 구워 돼지기름을 낸다.\n\n5. 각 팬에 올리브오일을 두른다.\n\n6. 적당한 크기로 썬 고구마순김치와 밥, 달걀노른자, 부순 조미김, 전분을 섞어 밥전반죽을 만든다.\n\n7. 기름을 두른 팬에 밥전반죽을 부쳐 고구마순김치밥전을 만든다.\n\n8. 기름을 두른 팬에 적당한 크기로 썬 배추김치를 볶는다.\n\n9. 8에 채 썬 양파와 적당한 크기로 썬 대파 흰 부분, 구운 돼지목살 비계를 넣고 볶는다.\n\n10. 볶던 재료에 맛술, 밀가루, 버터를 넣는다.\n\n11. 볶던 재료에 사골국을 넣고 끓여 김치소스를 만든다.\n\n12. 돼지등심 스테이크의 뼈를 제거한 뒤, 돼지기름을 기름을 두른 팬에 굽는다.\n\n13. 김치소스를 체에 밭쳐 국물만 걸러내 끓인다.\n\n14. 김치소스를 종지에 담아낸다.\n\n15. 고구마순김치밥전을 적당한 크기로 잘라 접시에 올린다.\n\n16. 고구마순김치밥전 위에 적당한 크기로 썬 돼지등심 스테이크를 올린 뒤, 고구마순김치밥전으로 덮는다.\n\n17. 16 위에 김치샐러드를 올린다.\n\n18. 김치소스 건더기를 잘게 다져 곁들인다.'),
	(618, '김치 곱빼기', NULL, 2, '1. 끓는 물에 배추김치와 사골국을 넣고 끓인다.\n\n2. 기름을 두른 팬에 적당한 크기로 썬 양파, 대파를 볶다가 간 돼지고기와 인스턴트 양념곱창을 넣고 볶는다.\n\n3. 기름을 두른 팬에 적당한 크기로 썬 두부를 굽는다.\n\n4. 생두부를 으깬 뒤 볶던 재료에 넣는다.\n\n5. 4를 끓이던 김칫국에 넣고 끓여 김치전골을 만든다.\n\n6. 무를 얇게 썬 뒤, 소금물에 절여둔다.\n\n7. 절인 무에 고추장, 참기름, 설탕을 버무려 기름을 두른 팬에 굽는다.\n\n8. 배추김치를 물에 씻은 뒤, 가늘게 채 썬다.\n\n9. 적당한 크기로 썬 데친 참나물, 고구마순김치와 다진 깨를 버무려 참나물 김치무침을 만든다.\n\n10. 핸드블렌더로 명이나물장아찌를 곱게 간다.\n\n11. 참나물 김치무침에 곱게 간 명이나물장아찌와 참기름, 식초를 넣고 버무린다.\n\n12. 접시에 참나물 김치무침을 담는다.\n\n13. 끓이던 김치전골에 식초, 설탕, 고춧가루를 넣는다.\n\n14. 김치전골에 적당한 크기로 썬 홍고추, 표고버섯, 고추, 마늘즙을 넣고 끓인다.\n\n15. 그릇에 밥을 담는다.\n\n16. 김치전골 위에 무구이와 구운 두부, 팽이버섯을 올린다.\n\n17. 잘게 썬 대파를 올려 마무리한다.'),
	(619, '복스테이크', NULL, 1, '1. 돼지목살에 올리브오일을 버무린다.\n\n2. 그릴팬에 올리브오일에 버무린 돼지목살을 굽는다.\n\n3. 마늘을 편으로 썬다.\n\n4. 끓는 기름에 편으로 썬 마늘을 튀겨 마늘칩을 만든다.\n\n5. 마늘칩을 건져낸 기름에 적당한 크기로 썬 대파와 고춧가루를 끓여 고추기름을 낸다.\n\n6. 애호박, 단호박, 양파, 청양고추, 대파, 더덕을 적당한 크기로 썬다.\n\n7. 고추기름을 체에 밭쳐 건더기를 걸러낸다.\n\n8. 고추기름을 두른 팬에 손질한 채소를 볶는다.\n\n9. 볶던 재료에 적당한 크기로 썬 표고버섯을 넣고 볶는다.\n\n10. 간장, 매실액, 칼라만시 원액, 설탕, 맛술, 후추를 섞어 간장소스를 만든다.\n\n11. 핸드블렌더에 배를 곱게 간 뒤, 간장소스에 섞는다.\n\n12. 구운 돼지목살을 적당한 크기로 썬다.\n\n13. 적당한 크기로 썬 돼지목살을 간장소스에 버무린다.\n\n14. 간장소스에 버무린 돼지목살을 고추기름에 볶은 채소와 함께 볶아 찹스테이크를 만든다.\n\n15. 즉석밥을 전자레인지에 돌린다.\n\n16. 대파와 당근을 잘게 다진다.\n\n17. 기름을 두른 팬에 잘게 다진 대파와 당근을 넣고, 달걀을 풀어 볶는다.\n\n18. 17에 밥을 넣고 함께 볶다가 간장, 굴소스로 간을 한다.\n\n19. 볶음밥에 잘게 썬 청양고추를 넣는다.\n\n20. 달궈놓은 그릴팬에 볶음밥을 담는다.\n\n21. 달걀프라이를 만든다.\n\n22. 볶음밥과 함께 찹스테이크를 담는다.\n\n23. 볶음밥과 찹스테이크 위에 달걀프라이와 마늘칩을 올려 마무리한다.'),
	(620, '펌킨 치킨', NULL, 1, '1. 끓는 물에 껍질 벗긴 단호박을 삶는다.\n\n2. 기름을 두른 팬에 닭다리와 닭날개을 굽다가, 소금, 후추로 간 한다.\n\n3. 당근, 표고버섯, 비엔나소시지, 팽이버섯, 마늘, 양파를 적당한 크기로 썬다.\n\n4. 닭을 굽던 팬에 손질한 채소와 비엔나소시지를 넣고 볶는다.\n\n5. 4에 맥주를 붓고 끓여 스튜를 만든다.\n\n6. 삶은 단호박을 체에 밭쳐 건져낸 뒤, 냄비에 넣고 후추, 소금으로 간 한다.\n\n7. 6을 핸드블렌더로 갈아 단호박퓌레를 만든다.\n\n8. 할라피뇨를 잘게 썬다.\n\n9. 치킨스튜에 간장, 토마토소스, 버터를 넣는다.\n\n10. 접시에 단호박퓌레를 담는다.\n\n11. 치킨스튜에 잘게 썬 할라피뇨와 전분물을 넣는다.\n\n12. 접시에 치킨스튜를 담아 마무리한다.'),
	(621, '칼로리 듬풍 샌드위치', NULL, 1, '1. 핸드블렌더에 적당한 크기로 썬 양파와 유자청을 넣고 간다.\n\n2. 녹인 버터에 간 양파와 유자청, 물을 넣고 끓여 양파소스를 만든다.\n\n3. 달걀노른자와 흰자를 분리한다.\n\n4. 달걀흰자에 밀가루, 맥주, 소금을 넣고 섞어 튀김반죽을 만든다.\n\n5. 통조림햄을 슬라이스한다.\n\n6. 식빵 > 통조림햄 & 훈제 닭가슴살 슬라이스 > 식빵을 차례로 쌓는다.\n\n7. 샌드위치의 테두리를 잘라낸 뒤, 꼬치로 고정시킨다.\n\n8. 샌드위치에 튀김반죽을 입혀 기름에 튀긴다.\n\n9. 팬에 달걀노른자, 쇠고기수프분말, 소금, 다진 마늘, 물을 넣고 끓인다.\n\n10. 6에 짜장라면 면을 잘게 부숴 넣고 끓여 맥앤뽀글이를 만든다.\n\n11. 맥앤뽀글이에 전분물을 넣는다.\n\n12. 맥앤뽀글이에 잘게 썬 청양고추와 후추를 넣고 끓인다.\n\n13. 그릇에 맥앤뽀글이를 담는다.\n\n14. 접시에 튀긴 샌드위치를 올리고, 양파소스를 곁들여 낸다.'),
	(622, '슈퍼헤비급 플래터', NULL, 1, '1. 옥수수를 전자레인지에 돌린다.\n\n2. 냄비에 버터를 넣고 녹인다.\n\n3. 마른 팬에 잘게 썬 양파와 으깬 마늘을 볶다가 녹인 버터, 버터피칸\n아이스크림을 넣고 끓인다.\n\n4. 3에 물과 소금을 넣고 끓이다가 핸드블렌더로 곱게 갈아 크림소스를 만든다.\n\n5. 그릴팬에 돼지등심 스테이크와 양파, 고추를 굽는다.\n\n6. 그릴팬에 굽던 고추를 직화한다.\n\n7. 그릴팬에 닭가슴살소시지를 굽는다.\n\n8. 옥수수에 버터를 발라 그릴팬에 굽는다.\n\n9. 비닐팩에 훈제 닭가슴살 슬라이스와 녹인 버터를 넣고 전자레인지에 돌린다\n\n10. 구운 옥수수의 옥수수알을 발라낸 뒤, 크림소스에 넣고 끓여 콘크림소스를 만든다.\n\n11. 구운 옥수수에 막대를 꽂는다.\n\n12. 잘게 부순 옥수수과자와 라면수프를 섞어 양념을 만든다.\n\n13. 옥수수에 콘크림소스를 바르고 만들어둔 양념을 버무린다.\n\n14. 핫소스에 잘게 썬 구운 고추를 넣고 끓여 고추소스를 만든다.\n\n15. 접시에 콘크림수프를 깔고 고추소스를 곁들여 낸 뒤, 구운 돼지등심 스테이크, 닭가슴살 소시지를 담는다.\n\n16. 구운 양파와 마약옥수수를 함께 낸다.'),
	(623, '아모르팟타이♬', NULL, 1, '1. 핸드블렌더에 밀가루와 달걀을 넣고 돌려 면 반죽을 만든다.\n\n2. 양파, 대파를 채 썬다.\n\n3. 채 썬 양파와 대파에 전분을 묻혀 기름에 튀긴다.\n\n4. 튀긴 양파와 대파를 건져 기름기를 뺀다.\n\n5. 기름을 두른 팬에 채 썬 양파와 당근을 볶는다.\n\n6. 돼지등심 스테이크의 뼈를 제거하고 잘게 다진다.\n\n7. 다진 돼지등심 스테이크, 으깬 마늘, 소금, 후추, 잘게 썬 청양고추를 채소를 볶던 팬에 넣고 함께 볶는다.\n\n8. 돼지등심 스테이크에 올리브오일을 바르고 소금, 후추로 간 한다.\n\n9. 양념한 돼지등심 스테이크를 석쇠에 올려 직화한다.\n\n10. 면 반죽을 밀대로 넓게 민 뒤, 썰어 팟타이 면을 만든다.\n\n11. 핸드블렌더에 아몬드를 넣고 간다.\n\n12. 홍고추에 칼집을 내고, 석쇠에 올려 직화한다.\n\n13. 볶던 재료에 팟타이소스와 잘게 썬 대파를 넣고 볶아 팟타이양념을 만든다.\n\n14. 팟타이 면을 삶는다.\n\n15. 삶은 팟타이 면을 건져 팟타이양념에 볶는다.\n\n16. 그릇에 팟타이를 담고, 직화한 돼지등심 스테이크를 적당한 크기로 썰어 올린다.\n\n17. 직화한 홍고추를 적당한 크기로 썰어 올리고, 오리엔탈 드레싱을 뿌린다.\n\n18. 레몬즙을 뿌리고 튀긴 양파와 대파를 올린 뒤, 곱게 간 아몬드를 뿌려 마무리한다.'),
	(624, '카츠우동♬', NULL, 1, '1. 핸드블렌더에 밀가루, 소금, 물을 넣고 돌려 면 반죽을 만든다.\n\n2. 테두리를 잘라낸 식빵을 믹서기에 갈아 빵가루를 만든다.\n\n3. 돼지등심 스테이크의 뼈를 제거한 뒤, 칼집을 내 부드럽게 만든다.\n\n4. 달걀물, 밀가루, 물을 섞어 튀김옷을 만든다.\n\n5. 칼집을 낸 돼지등심 스테이크에 밀가루>튀김옷>빵가루를 순서대로 입힌다.\n\n6. 면 반죽을 밀대로 넓게 민 뒤, 썰어 우동 면을 만든다.\n\n7. 핸드블렌더에 적당한 크기로 썬 양파, 대파, 마늘, 돼지등심 스테이크, 치즈 비엔나 소시지, 통조림 햄을 넣고 곱게 간다.\n\n8. 기름을 두른 팬에 곱게 간 채소와 고기를 넣고 볶다가 된장, 고추장, 맛술, 간장, 설탕, 물을 넣고 끓여 된장소스를 만든다.\n\n9. 된장소스에 라면수프와 고춧가루, 잘게 썬 청양고추를 넣는다.\n\n10. 튀김옷을 입힌 돈카츠를 기름에 튀긴다.\n\n11. 된장소스에 전분물을 넣는다.\n\n12. 우동 면을 삶는다.\n\n13. 돈카츠를 건져낸 뒤, 적당한 크기로 썬다.\n\n14. 삶은 우동 면을 건져 찬물에 헹군다.\n\n\n15. 그릇에 우동 면을 담은 뒤, 된장소스를 붓는다.\n\n16. 돈카츠를 올려 마무리한다.'),
	(625, '연복풍 탕수육', NULL, 1, '1. 끓는 기름에 적당한 크기로 썬 대파와 으깬 마늘, 고춧가루를 넣고 고추기름을 낸다.\n\n2. 소고기 안심을 두드려 납작하게 만든다.\n\n3. 납작하게 만든 소고기 안심을 적당한 크기로 썬다.\n\n4. 튀김가루, 전분, 물을 섞어 튀김옷을 만든다.\n\n5. 소고기 안심에 튀김옷을 입혀 기름에 튀긴다.\n\n6. 고추기름을 면보에 걸러 건더기를 걸러낸다.\n\n7. 튀긴 소고기 안심을 건진다.\n\n8. 설탕, 식초, 간장, 물을 섞어 탕수소스를 만든다.\n\n9. 양파, 적양배추, 당근, 청양고추를 적당한 크기로 썬다.\n\n10. 튀긴 소고기 안심을 한 번 더 튀겨낸다.\n\n11. 고추기름을 두른 팬에 손질한 채소를 볶다가 케첩을 넣는다.\n\n12. 볶던 재료에 탕수소스를 붓고 끓인다.\n\n13. 튀긴 소고기 안심을 건져 기름기를 뺀다.\n\n14. 탕수소스에 전분물을 넣어 농도를 조절한다.\n\n15. 접시에 튀긴 소고기 안심을 담고 탕수소스를 부어 마무리한다.'),
	(626, '테리우스테이크', NULL, 1, '1. 양파를 잘게 썬다.\n\n2. 파프리카를 직화한다.\n\n3. 마늘은 편으로 썰고, 전복은 손질해 잘게 썬다.\n\n4. 팬에 버터를 두른 뒤, 편마늘과 잘게 썬 양파, 다진 마늘, 발사믹 식초를 넣고 끓인다.\n\n5. 4에 설탕, 잘게 썬 전복을 넣고 볶아 전복발사믹소스를 만든다.\n\n6. 스테이크용 부챗살에 소금, 후추로 간 한 뒤, 칼집을 낸다.\n\n7. 손질한 스테이크용 부챗살을 그릴팬에 굽는다.\n\n8. 냄비에 불린 미역을 볶는다.\n\n9. 볶던 미역에 참기름을 넣고, 뜨거운 물을 부어 미역국을 끓인다.\n\n10. 미역국에 라면수프와 면을 넣고 끓여 미역라면을 만든다.\n\n11. 접시에 전복발사믹소스를 깔고, 부챗살 스테이크를 올린다.\n\n12. 직화한 파프리카의 탄 부분을 벗겨내고, 적당한 크기로 썰어 스테이크에 곁들인다.\n\n13. 스테이크 위에 레몬껍질을 갈아 올린다.\n\n14. 스테이크 위에 올리브오일, 레몬즙을 뿌린다.\n\n15. 그릇에 미역라면을 담고, 잘게 썬 대파를 올려 마무리한다.'),
	(627, '레복만두', NULL, 1, '1. 새우살을 잘게 다진다.\n\n2. 끓는 물에 적당한 크기로 썬 청양고추와 대파, 으깬 마늘을 넣고 끓여 채소육수를 낸다.\n\n3. 핸드블렌더에 부추를 곱게 간다.\n\n4. 잘게 썬 적양배추, 다진 돼지고기, 다진 새우살을 섞어 만두소를 만든다.\n\n5. 밀가루, 소금, 부추즙, 뜨거운 물을 섞어 만두피 반죽을 만든다.\n\n6. 채소육수의 건더기를 걸러낸 뒤, 쯔유로 간 한다.\n\n7. 만두소에 다진 마늘, 생강즙, XO소스, 해선장, 아몬드버터를 섞는다.\n\n8. 7에 후추를 넣는다.\n\n9. 전복을 손질한다.\n\n10. 만두피 반죽을 적당량 떼어낸 뒤, 밀대로 밀어 만두피를 만든다.\n\n11. 기름을 두른 팬에 채 썬 양파를 볶다가 베트남고추, 선드라이 토마토, 바질페스토, 물을 넣고 끓여 바질소스를 만든다.\n\n12. 만두피에 만두소를 넣고 싸 부추만두를 만든다.\n\n13. 기름을 두른 팬에 부추만두를 넣고, 뚜껑을 닫아 굽는다.\n\n14. 채소육수에 바지락살, 새우살, 전복살을 넣고 국물을 끓인다.\n\n15. 마른 팬에 구워먹는 치즈를 굽는다.\n\n16. 만두를 굽던 팬에 뜨거운 물을 부은 뒤, 뚜껑을 닫아 수증기로 익힌다.\n\n17. 끓이던 국물에서 해산물을 건져낸 뒤, 부추만두를 넣고 끓여 만둣국을 만든다.\n\n18. 데친 전복살은 적당한 크기로 썰고, 건져뒀던 해산물과 함께 넣어 해물만둣국을 만든다.\n\n19. 해물만둣국에 맛술과 소금, 잘게 썬 대파를 넣고 끓인다.\n\n20. 접시에 바질소스를 부은 뒤, 구운 치즈를 올리고 트러플오일을 뿌린다.\n\n21. 20 위에 구운 부추만두를 올린다.\n\n22. 해물만둣국을 함께 낸다.'),
	(628, '야관문 장어', NULL, 1, '1. 간장게장의 간장만 걸러낸다.\n\n2. 1에 간장, 청양고추, 으깬 마늘, 전복, 설탕을 넣고 끓여 전복장을 만든다.\n\n3. 간장, 맛술, 청주, 설탕을 끓여 데리야키소스를 만든다.\n\n4. 전복장에 야관문주를 넣는다.\n\n5. 데리야키소스에 야관문주를 넣는다.\n\n6. 달걀노른자, 물, 맛술, 설탕, 식초, 쯔유를 섞은 뒤, 끓는 물 위에서 중탕하며 저어 기미스소스를 만든다.\n\n7. 냄비에 참기름을 끓인다.\n\n8. 적양배추, 청양고추를 가늘게 채 썬다.\n\n9. 채 썬 채소에 끓인 참기름, 설탕을 넣고 버무려 채소샐러드를 만든다.\n\n10. 밀가루, 새우분말, 파슬리가루, 물, 얼음을 섞어 튀김옷을 만든다.\n\n11. 장어에 밀가루를 묻히고 튀김옷을 입힌 뒤, 기름에 튀긴다.\n\n12. 채소샐러드에 적당한 크기로 썬 부추, 채소절임원액, 소금, 식초, 믹스허브, 구운채소절임을 다져 넣고 버무린다.\n\n13. 달걀노른자와 바질페스토를 섞은 뒤, 라임껍질을 갈아 넣어 바질소스를 만든다.\n\n14. 접시에 바질소스와 트러플페스토를 올린다.\n\n15. 접시에 채소샐러드를 담는다.\n\n16. 개복숭아절임과 선드라이토마토를 적당한 크기로 썰어 채소샐러드 위에 올린다.\n\n17. 16 위에 장어튀김을 얹는다.\n\n18. 장어튀김 위에 기미스소스를 얹는다.\n\n19. 전복장을 적당한 크기로 썰어 함께 곁들인다.\n\n20. 데리야키소스를 뿌리고, 블랙올리브를 곁들여 마무리한다.'),
	(629, '봉골레 안 먹고 가시나♬', NULL, 1, '1. 밀가루를 체에 거른다.\n\n2. 체에 거른 밀가루에 호박즙을 넣어 반죽한다.\n\n3. 기름을 두른 냄비에 편으로 썬 마늘, 손질한 새우와 오징어, 잘게 썬 홍고추와 바지락, 물을 넣고 끓여 봉골레스튜를 만든다.\n\n4. 빵반죽을 밀대로 밀어 넓게 편 뒤, 모차렐라치즈를 넣고 접어 빵 모양을 잡아 피타빵을 만든다.\n\n5. 치즈를 넣은 피타빵을 그릴팬에 굽는다.\n\n6. 양파, 마늘종장아찌를 잘게 썬 뒤, 칵테일소스, 올리브오일과 섞어 소스를 만든다.\n\n7. 기름을 두른 팬에 손질한 새우를 굽다가 소금, 후추로 간 한다.\n\n8. 마늘을 잘게 다진 뒤, 버터를 섞어 갈릭버터를 만든다.\n\n9. 피타빵에 갈릭버터를 발라가며 굽는다.\n\n10. 봉골레스튜에 구운 새우와 잘게 썬 대파를 넣는다.\n\n11. 피타빵을 접시에 담아낸다.'),
	(630, '제비~ 복 먹으러 나간다', NULL, 1, '1. 밀가루를 체에 거른다.\n\n2. 핸드블렌더에 부추, 물을 넣고 간 뒤, 건더기를 걸러내 부추즙을 만든다.\n\n3. 밀가루에 부추즙, 식초을 넣고 부추수제비반죽을 만든다.\n\n4. 끓는 물에 북어대가리, 다진 마늘, 청주를 넣고 끓여 육수를 낸다.\n\n5. 대파를 꼬치에 꽂아 직화한다.\n\n6. 완성된 수제비 반죽을 볼에 담고 랩을 씌워 숙성시킨다.\n\n7. 냄비에 인스턴트 사골곰탕과 양지, 물을 넣고 끓인다.\n\n8. 알배추, 양파를 적당한 크기로 썬 뒤, 기름을 두른 팬에 볶는다.\n\n9. 볶던 채소에 대패삼겹살을 넣고 함께 볶는다.\n\n10. 삶던 양지에 구운 대파를 넣고 함께 끓인다.\n\n11. 볶은 채소와 대패삼겹살을 양지를 삶던 냄비에 넣고 끓여 전골을 만든다.\n\n12. 달래와 부추장아찌를 잘게 썬 뒤, 부추장아찌 국물을 섞어 소스를 만든다.\n\n13. 부추달래소스에 잘게 썬 팽이버섯과 곱게 다진 깨, 참기름, 식초, 고춧가루, 설탕을 섞는다.\n\n14. 핸드블렌더에 조미간장, 맛술, 설탕, 식초, 잘게 썬 청양고추, 마요네즈, 깨를 넣고 갈아 매운참깨소스를 만든다.\n\n15. 끓이던 전골에 북어육수, 맛술, 일본간장을 넣고 끓인다.\n\n16. 숙성한 부추수제비반죽을 밀대로 밀어 넓게 편 뒤, 부추를 넣고 접어 다시 한 번 민다.\n\n17. 끓는 물에 적당한 크기로 떼어낸 부추수제비반죽을 넣고 삶는다.\n\n18. 강판에 감자를 간 뒤, 밀가루와 전분을 넣고 섞어 감자전반죽을 만든다.\n\n19. 기름을 두른 팬에 감자전반죽을 넓게 편 뒤, 바지락살을 올려 조개감자전을 굽는다.\n\n20. 갑오징어에 촘촘하게 칼집을 낸 뒤, 적당한 크기로 썰어 전골에 넣는다.\n\n21. 새송이버섯과 대파를 적당한 크기로 썬다.\n\n22. 삶은 양지를 건져내 포 뜬다.\n\n23. 삶은 부추수제비를 얼음물에 담가 식힌다.\n\n24. 끓인 전골을 전골냄비에 옮겨 담은 뒤, 부추수제비와 포 뜬 양지를 얹는다.\n\n25. 조개감자전을 접시에 담아낸다.\n\n26. 전골 위에 손질한 대파와 새송이버섯을 올린다.'),
	(631, '마성의 분짜조', NULL, 1, '1. 쪽파와 양파를 잘게 썬다.\n\n2. 두부를 다진다.\n\n3. 잘게 썬 쪽파와 양파, 다진 두부, 다진 돼지고기, 달걀노른자, 설탕, 소금, 후추, 잘게 썬 씻은 묵은지를 섞어 짜조 속을 만든다.\n\n4. 3에 불린 목이버섯을 다져 넣는다.\n\n5. 라이스페이퍼를 따뜻한 물에 불린 뒤, 짜조 속을 넣고 돌돌 만다.\n\n6. 끓는 물에 불린 쌀국수면을 데친 뒤, 찬물에 헹궈 물기를 뺀다.\n\n7. 물, 설탕, 피시소스, 쌀식초를 4 : 1 : 3/4 : 1/2 비율로 섞은 뒤 끓여 분짜소스를 만든다.\n\n8. 갈비산적을 석쇠에 올려 직화한다.\n\n9. 기름에 짜조를 튀긴다.\n\n10. 튀긴 짜조를 건져내 한 김 식힌 뒤, 다시 튀긴다.\n\n11. 스위트칠리소스와 스리라차소스를 섞는다.\n\n12. 당근을 조각해 꽃모양을 만든다.\n\n13. 접시에 짜조를 담고, 소스를 곁들인다.\n\n14. 그릇에 분짜소스를 담고, 샐러드용 채소와 고수, 당근꽃을 올린다.\n\n15. 분짜소스에 쌀국수면과 갈비산적을 담아낸다.'),
	(632, '칼제비전', NULL, 1, '1. 끓는 물에 된장을 풀고 새우가루를 넣는다.\n\n2. 감자, 애호박을 적당한 크기로 썬다.\n\n3. 청양고추를 적당한 크기로 썬 뒤, 된장국에 넣는다.\n\n4. 된장국에 감자를 넣고 끓인다.\n\n5. 부추를 적당한 길이로 썬다.\n\n6. 부침가루, 물, 소금, 참기름, 손질한 부추와 깻잎, 칵테일새우를 섞어 부추깻잎전 반죽을 만든다.\n\n7. 기름을 두른 팬에 부추깻잎전 반죽을 넓게 펴서 굽는다.\n\n8. 홍고추를 적당한 크기로 썰어 부추깻잎전 위에 올린다.\n\n9. 된장국에 칼국수면과 수제비, 다진 마늘, 손질한 애호박, 후추를 넣고 끓여 된장칼제비를 만든다.\n\n10. 된장칼제비에 팽이버섯, 칵테일새우를 넣고 끓인다.\n\n11. 9에 적당한 크기로 썬 두부를 넣는다.\n\n12. 부추깻잎전을 접시에 담아낸다.\n\n13. 간장, 식초, 고춧가루, 다진 청양고추를 섞어 간장소스를 만든다.\n\n14. 된장칼제비를 그릇에 담아낸다.\n\n15. 된장칼제비 위에 날치알을 올린다.\n\n16. 막걸리를 곁들여낸다.'),
	(633, '아모르 패티', NULL, 1, '1. 핸드블렌더에 소고기 등심을 넣고 간다.\n\n2. 다진 등심, 다진 돼지고기에 잘게 썬 양파와 부추, 올리브오일, 소금, 후추, 모차렐라치즈를 넣고 버무려 패티반죽을 만든다.\n\n3. 강판에 감자를 간 뒤, 즙을 짜낸다.\n\n4. 간 감자에 간 양파, 달걀물, 후추, 소금, 밀가루를 넣고 섞어 감자전반죽을 만든다.\n\n5. 기름을 두른 팬에 감자전반죽을 굽는다.\n\n6. 패티반죽을 치댄 뒤, 동그랗게 모양을 잡는다.\n\n7. 청양고추와 홍고추를 석쇠에 올려 직화한다.\n\n8. 기름을 두른 팬에 편으로 썬 마늘과 잘게 썬 구운 홍고추, 토마토소스, 물을 넣고 끓여 칠리토마토소스를 만든다.\n\n9. 기름을 두른 그릴팬에 패티를 굽는다.\n\n10. 마늘편, 소금, 올리브오일, 구운 청양고추, 식초, 잘게 썬 부추를 섞어 고추절임을 만든다.\n\n11. 레몬을 모양내서 접시에 플레이팅한다.\n\n12. 접시에 감자전, 칠리토마토소스를 담는다.\n\n13. 구운 패티를 접시에 담고, 잘게 썬 부추를 뿌려 마무리한다.'),
	(634, '뜯고 씹고 호로록', NULL, 1, '1. 끓는 기름에 대파, 고춧가루, 마늘을 넣고 끓여 고추기름을 낸다.\n\n2. 당면을 튀긴다.\n\n3. 등갈비를 손질한 뒤, 칼집을 낸다.\n\n4. 손질한 등갈비와 대파에 조미간장을 넣고 버무린다.\n\n5. 양념한 등갈비에 전분을 묻혀 튀긴다.\n\n6. 양파는 채 썰고, 대파, 청양고추, 홍고추를 잘게 썬다.\n\n7. 고추기름을 체에 밭쳐 건더기를 걸러낸다.\n\n8. 대파를 적당한 크기로 썬다.\n\n9. 오징어와 돼지목살을 가늘게 채 썬다.\n\n10. 고추기름을 두른 팬에 적당한 크기로 썬 대파와 채 썬 오징어, 돼지목살, 조미간장, 느타리버섯, 새우가루를 넣고 볶는다.\n\n11. 무를 채 썬다.\n\n12. 볶던 재료에 채 썬 무, 새우젓, 인스턴트 사골곰탕, 물을 넣고 산라탕을 끓인다.\n\n13. 산라탕에 불린 당면과 새우를 넣고 끓인다.\n\n14. 등갈비튀김을 건져 한 김 식힌다.\n\n15. 설탕, 간장, 식초, 맛술을 섞어 무침소스를 만든다.\n\n16. 무침소스에 매실액을 더한다.\n\n17. 잘게 썬 양배추, 사과, 부추에 무침소스, 손질한 양파, 대파, 청양고추, 홍고추를 넣고 버무려 부추무침을 만든다.\n\n18. 등갈비튀김을 다시 한 번 튀긴다.\n\n19. 대파, 청양고추, 홍고추를 잘게 썰어 팬에 넣는다.\n\n20. 손질한 채소에 기름을 두르고, 등갈비튀김과 폰즈소스를 넣어 볶는다.\n\n21. 접시에 부추무침을 깔고, 당면튀김을 올린다.\n\n22. 21 위에 등갈비튀김을 올린 뒤, 부추무침을 얹는다.\n\n23. 손질한 양파, 대파, 청양고추, 홍고추를 무침소스에 버무려 부추무침 위에 올린다.\n\n24. 산라탕면에 전분물을 넣어 농도를 조절한다.\n\n25. 그릇에 산라탕면을 담고, 고추기름을 뿌린다.'),
	(635, '역전만루홍런', NULL, 1, '1. 통오겹살의 비계를 제거한 뒤, 꼬치에 꽂아 칼집을 낸다.\n\n2. 칼집을 낸 통오겹살에 밀가루를 묻힌 뒤, 기름에 튀긴다.\n\n3. 가래떡을 적당한 크기로 썬 뒤, 밀대로 얇게 민다.\n\n4. 얇게 민 떡에 밀가루를 묻혀 기름에 튀긴다.\n\n5. 기름을 두른 팬에 손질된 묵은지를 넣고 볶다가 고추장을 넣는다.\n\n6. 묵은지를 볶던 팬에 대파를 잘게 썰어 넣고 함께 볶는다.\n\n7. 양념불고기를 칼로 두드려 잘게 다진다.\n\n8. 다진 양념불고기에 전분, 잘게 다진 고추장아찌를 넣고 버무린 뒤, 석쇠에 넓게 펴고 직화로 굽는다.\n\n9. 밀가루, 달걀, 물을 섞어 전반죽을 만든다.\n\n10. 기름을 두른 팬에 전반죽을 두른다.\n\n11. 전반죽 위에 구운 양념불고기를 올려 불고기전을 만든다.\n\n12. 튀긴 통오겹살과 떡을 건져 기름기를 뺀다.\n\n13. 볶던 묵은지에 물, 설탕, 식초, 깍두기국물, 고추장아찌국물, 새우젓, 전분물을 넣고 끓여 묵은지소스를 만든다.\n\n14. 튀긴 통오겹살을 다시 한 번 튀긴다.\n\n15. 고춧가루에 뜨거운 물을 부은 뒤, 채 썬 양파와 연겨자, 식초, 매실액, 간장, 피클국물, 고추장아찌국물을 섞어 전 양념을 만든다.\n\n16. 전 양념을 종지에 담는다.\n\n17. 접시에 불고기전을 담는다.\n\n18. 접시에 묵은지소스를 담은 뒤, 통오겹탕수를 올리고 떡튀김을 얹어 마무리한다.'),
	(636, '샌디에이고 짜그리스', NULL, 1, '1. 버터를 두른 팬에 적당한 크기로 썬 베이컨과 삼겹살, 밀가루를 넣고 볶는다.\n\n2. 볶던 재료에 인스턴트 사골곰탕을 붓고 끓인다.\n\n3. 끓이던 재료에 적당한 크기로 썬 무와 앤초비, 표고버섯을 넣고 끓인다.\n\n4. 대파를 두드린 후 적당한 크기로 썬다.\n\n5. 끓이던 재료에 대파와 고춧가루를 넣고 짜글이를 끓인다.\n\n6. 짜글이에 고추장을 푼 뒤, 불린 떡국떡을 넣고 끓인다.\n\n7. 짜글이에 굴소스, 간장, 식초, 다진 마늘, 후추를 넣고 끓인다.\n\n8. 그릇에 밥을 담는다.\n\n9. 짜글이에 설탕을 넣는다.\n\n10. 그릇에 짜글이를 담아낸다.'),
	(637, '월드시리즈 간닭!', NULL, 1, '1. 압력솥에 물을 끓이다가, 손질한 대파, 청양고추, 무, 마늘, 표고버섯을 넣고 끓인다.\n\n2. 가래떡을 적당한 크기로 썰어 채소육수에 넣고 인스턴트 사골곰탕을 부어 함께 끓인다.\n\n3. 손질한 닭에 칼집을 내고 채소육수에 넣은 뒤 압력솥 뚜껑을 닫고 끓인다.\n\n4. 핸드블렌더에 닭가슴살을 넣고 간다.\n\n5. 핸드블렌더에 인스턴트 무뼈닭발을 넣고 간다.\n\n6. 간 닭가슴살과 닭발, 잘게 썬 양파, 달걀노른자, 전분, 소금, 후추, 생강즙을 섞어 닭완자반죽을 만든다.\n\n7. 동그랗게 닭완자를 빚어 전분>달걀물을 순서대로 입힌다.\n\n8. 끓는 물에 인스턴트 사골곰탕을 넣고 끓이다가, 닭완자를 넣고 삶는다.\n\n9. 기름을 두른 팬에 닭완자를 굽는다.\n\n10. 고추장아찌국물, 매실액, 연겨자, 고추장, 된장을 섞어 소스를 만든다.\n\n11. 종지에 소스를 담은 뒤, 들깻가루를 넣는다.\n\n12. 팬에서 구운 닭완자를 석쇠에 넣고 직화한다.\n\n13. 압력솥에서 익힌 닭과 떡을 건져내고, 국물에 맛술과 구운 닭완자를 넣고 끓인다.\n\n14. 14에 삶은 닭완자도 함께 넣고 끓인다.\n\n15. 익은 닭의 살만 발라낸 뒤, 그릇에 떡과 함께 담는다.\n\n16. 그릇에 국물과 닭완자, 채소를 담는다.\n\n17. 잘게 썬 대파를 올려 마무리한다.'),
	(638, '홍~치킨!', NULL, 1, '1. 닭을 손질해 닭다리와 닭가슴살을 분리한다.\n\n2. 끓는 물에 손질하고 남은 닭, 양파, 표고버섯대를 넣고 끓인다.\n\n3. 핸드블렌더에 닭가슴살을 넣고 간다.\n\n4. 핸드블렌더에 표고버섯과 느타리버섯을 넣고 간다.\n\n5. 기름을 두른 팬에 잘게 썬 베이컨을 볶는다.\n\n6. 양파를 한 겹씩 벗겨 기름에 튀긴다.\n\n7. 튀긴 양파를 건져 기름기를 뺀다.\n\n8. 볶던 베이컨에 간 닭가슴살과 버섯을 넣고 함께 볶는다.\n\n9. 기름을 두른 팬에 잘게 썬 파프리카와 양파를 볶는다.\n\n10. 견과류와 건크랜베리를 다진다.\n\n11. 볶은 파프리카와 양파, 다진 견과류와 건크랜베리에 토마토소스를 넣고 섞는다.\n\n12. 11에 간 블랙올리브, 설탕, 레몬즙, 올리브오일을 넣고 섞어 토마토올리브소스를 만든다.\n\n13. 닭다리를 발골해 살만 발라낸 뒤, 기름을 두른 팬에 굽다가 소금, 후추로 간 한다.\n\n14. 볶은 베이컨, 닭가슴살, 버섯의 일부를 핸드블렌더로 간 뒤, 버터와 닭육수를 넣고 끓여 스테이크소스를 만든다.\n\n15. 접시에 스테이크소스를 담은 뒤, 튀긴 양파를 얹고 볶은 베이컨, 닭가슴살과 버섯을 올린 뒤 튀긴 양파로 덮는다.\n\n16. 발사믹드레싱을 뿌린다.\n\n17. 닭다리살 스테이크를 올린 뒤, 토마토올리브소스를 얹는다.\n\n18. 닭육수를 뿌린다.\n\n19. 잘게 썬 대파를 올려 마무리한다.'),
	(639, '사바사바♬', NULL, 1, '1. 적당한 크기로 썬 무를 기름에 튀긴다.\n\n2. 불린 쌀, 표고버섯 불린 물, 간장, 맛술, 밤, 불린 표고버섯을 솥에 넣은 뒤 뚜껑을 닫고 끓여 밥을 짓는다.\n\n3. 고등어는 손질해 가시를 발라내고 적당한 크기로 썰어 등에 칼집을 낸다.\n\n4. 손질한 고등어를 뜨거운 물에 담가둔다.\n\n5. 고등어를 손질해 가시를 발라낸 뒤, 소금을 뿌리고 기름을 두른 팬에 굽는다.\n\n6. 팬에 물, 맛술, 청주, 된장을 넣고 끓이다가 뜨거운 물에 담가뒀던 고등어와 불린 표고버섯을 넣고 조린다.\n\n7. 튀긴 무의 겉면을 제거하고, 적당한 크기로 썰어 접시에 담는다.\n\n8. 구운 고등어를 건져 기름기를 뺀다.\n\n9. 고등어된장조림에 칼집을 낸 청양고추를 넣고 양념을 끼얹어가며 조린다.\n\n10. 무 위에 고등어된장조림과 청양고추, 표고버섯을 얹는다.\n\n11. 솥밥 위에 구운 고등어를 얹고, 적당한 크기로 썬 쪽파를 뿌린 뒤 뚜껑을 닫아 뜸을 들인다.\n\n12. 고등어된장조림과 고등어솥밥을 함께 낸다.'),
	(640, '마무리고등어', NULL, 1, '1. 우유를 끓인다.\n\n2. 레몬껍질, 양파를 채 썬다.\n\n3. 끓이던 우유에 채 썬 레몬껍질과 양파, 생강즙, 맛술을 넣고 끓인다.\n\n4. 고등어는 손질해 적당한 크기로 자른다.\n\n5. 각종 재료를 넣고 끓인 우유에 간장을 붓고 손질한 고등어를 담가둔다.\n\n6. 마른 팬에 양파를 굽는다.\n\n7. 구운 양파 일부를 덜어놓고, 남은 양파에 간장, 표고버섯 불린 물, 잘게 썬 청양고추를 넣고 조린다.\n\n8. 핸드블렌더에 아보카도, 우유, 소금, 설탕을 넣고 갈다가 레몬즙을 더해 아보카도 퓌레를 만든다.\n\n9. 접시에 구운 양파와 아보카도 퓌레를 담는다.\n\n10. 아스파라거스와 당근을 얇게 슬라이스 한 뒤, 버터에 볶는다.\n\n11. 볶던 채소에 마늘종을 넣고 볶는다.\n\n12. 접시에 볶은 채소를 담는다.\n\n13. 우유에 담가뒀던 고등어를 건져 올리브오일을 뿌린 뒤, 토치로 직화한다.\n\n14. 직화한 고등어에 소금을 뿌린 뒤, 접시에 담는다.\n\n15. 양파조림을 체에 밭쳐 국물만 걸러낸 뒤, 접시에 담는다.\n\n16. 믹서기에 육포를 갈아 보푸라기를 만든다.\n\n17. 고등어 위에 육포 보푸라기와 볶은 마늘종을 올려 마무리한다.'),
	(641, '퀵! 차돌박이 볶음 말이', NULL, 1, '1. 알배추, 적양파를 채 썬다.\n\n2. 올리브오일과 참기름을 두른 팬에 채 썬 적양파, 홍고추, 대파를 볶는다.\n\n3. 볶던 채소에 마늘, 청양고춧가루를 넣고 볶는다.\n\n4. 아스파라거스를 적당한 길이로 썰어 기름을 두른 팬에 굽다가 소금으로 간한다.\n\n5. 구운 아스파라거스를 접시에 옮겨 불고기양념과 굴소스를 바른다.\n\n6. 채소를 볶던 팬에 채 썬 알배추와 굴소스를 넣고 볶는다.\n\n7. 채소볶음에 팽이버섯을 넣고 버무린다.\n\n8. 전자레인지에 즉석밥을 넣고 돌린다.\n\n9. 기름을 두른 팬에 밥을 편 뒤, 눌러서 굽는다.\n\n10. 기름을 두른 팬에 차돌박이를 굽다가 소금으로 간 한다.\n\n11. 구운 차돌박이 위에 볶은 채소와 구운 아스파라거스를 올리고 돌돌 말아 차돌박이롤을 만든다.\n\n12. 차돌박이롤에 불고기양념을 바른다.\n\n13. 기름을 두른 팬에 차돌박이롤을 굽는다.\n\n14. 구운 밥을 모양내서 자른 뒤, 접시에 올린다.\n\n15. 구운 밥 위에 차돌박이롤을 올린다.\n\n16. 차돌박이롤 위에 불고기양념을 바르고, 볶은 채소를 얹는다.\n\n17. 잘게 썬 쪽파를 뿌려 마무리한다.'),
	(642, '파이널복스', NULL, 1, '1. 소고기등심을 적당한 크기로 썬 뒤, 간장에 재워둔다.\n\n2. 기름에 적당한 크기로 썬 대파와 청양고춧가루를 넣고 끓여 고추기름을 낸다.\n\n3. 간장에 재워둔 소고기등심에 전분>달걀물을 입힌다.\n\n4. 고추기름을 넉넉하게 두른 팬에 소고기 등심, 아스파라거스, 마늘종, 청양고추를 튀기듯 볶는다.\n\n5. 찜기에 배추와 차돌박이, 쪽파를 넣고 찐다.\n\n6. 핸드블렌더에 간장, 맛술, 유자청, 아보카도, 우유, 굴소스를 넣고 갈아 아보카도소스를 만든다.\n\n7. 잘게 썬 양파와 아보카도에 아보카도소스를 넣고 섞은 뒤, 종지에 담는다.\n\n8. 등심볶음을 체에 밭쳐 기름기를 뺀다.\n\n9. 기름을 두른 팬에 적당한 크기로 썬 대파와 쪽파를 볶다가, 등심볶음과 맛술, 굴소스, 후추를 넣고 볶아 소고기회과육을 만든다.\n\n10. 소고기회과육을 접시에 담는다.\n\n11. 차돌박이배추찜을 낸다.'),
	(643, '승수를 올릴 어향가지', NULL, 1, '1. 가지에 칼집을 넣는다.\n\n2. 기름을 두른 팬에 손질한 가지를 튀기듯 굽는다.\n\n3. 대파를 잘게 썬다.\n\n4. 기름을 넉넉히 두른 팬에 잘게 썬 대파를 넣고 볶아 파기름을 낸다.\n\n5. 파기름에 다진 돼지고기를 넣고 볶는다.\n\n6. 잘게 썬 청양고추, 다진 마늘, 다진 생강을 섞은 뒤, 볶던 고기에 넣는다.\n\n7. 올리고당, 간장, 된장, 물, 고춧가루를 섞어 양념을 만든다.\n\n8. 볶던 재료에 양념을 넣고 볶아 어향소스를 만든다.\n\n9. 어향소스에 튀기듯 구운 가지를 넣고 조린다.\n\n10. 양송이버섯과 파프리카를 적당한 크기로 썰어 어향가지에 넣는다.\n\n11. 전자레인지에 즉석밥을 넣고 돌린다.\n\n12. 기름을 두른 팬에 달걀프라이를 굽다가, 뚜껑을 덮어 익힌다.\n\n13. 그릇에 밥을 담은 뒤, 달걀프라이를 올린다.\n\n14. 동치미 무를 적당한 크기로 썰어 종지에 담는다.\n\n15. 접시에 어향가지를 담는다.'),
	(644, '이태공 스테이크', NULL, 1, '1. 참돔을 손질한다.\n\n2. 끓는 물에 손질한 참돔, 된장, 다진 마늘을 넣고 참돔맑은탕을 끓인다.\n\n3. 양파를 적당한 크기로 썰어 참돔맑은탕에 넣고 끓인다.\n\n4. 무를 적당한 크기로 썬다.\n\n5. 참돔맑은탕에 적당한 크기로 썬 무와 대파를 넣고 끓인다.\n\n6. 참돔맑은탕에 적당한 크기로 썬 홍고추와 청양고추를 넣고 끓인다.\n\n7. 참돔맑은탕에 핑크솔트, 마늘소금, 고춧가루를 넣는다.\n\n8. 손질한 방어에 올리브오일, 후추, 레몬즙을 뿌린다.\n\n9. 래디시, 방울양배추, 엔다이브를 적당한 크기로 썬다.\n\n10. 기름을 두른 팬에 손질한 채소를 볶다가 소금, 후추로 간 한다.\n\n11. 접시에 볶은 채소를 담는다.\n\n12. 기름을 두른 팬에 양념한 방어를 굽는다.\n\n13. 참돔맑은탕에 후추, 간장, 소금으로 간 한다.\n\n14. 접시에 방어스테이크를 담고, 볶은 채소 위에 발사믹드레싱을 뿌린다.\n\n15. 조미간장, 식초, 레몬즙을 섞어 간장소스를 만든다.\n\n16. 고추냉이를 곁들인다.\n\n17. 참돔맑은탕에 미나리를 넣는다.\n\n18. 그릇에 참돔맑은탕을 담아낸다.'),
	(645, '승수나르도 파스타리오', NULL, 1, '1. 적양파, 삼겹살, 베이컨을 채 썰어 팬에 넣고 볶는다.\n\n2. 파프리카, 가지, 새송이버섯을 적당한 크기로 썰어 기름을 두른 그릴팬에 굽다가 소금, 후추로 간한다.\n\n3. 가지를 적당한 두께로 썰어 속을 동그랗게 파내고 밀가루를 입혀 튀긴다.\n\n4. 베이컨을 채 썰어 팬에 볶다가 인스턴트 사골곰탕을 붓고 끓인다.\n\n5. 볶은 재료에 베이컨과 함께 끓인 사골곰탕과 토마토소스를 붓고 끓여 파스타소스를 만든다.\n\n6. 핸드블렌더로 게맛살을 간 뒤, 마요네즈, 올리브오일, 날치알을 넣고 섞는다.\n\n7. 6을 튀긴 가지 속에 넣는다.\n\n8. 속을 채운 가지에 위에 모차렐라치즈를 얹고 오븐에 넣고 익힌다.\n\n9. 끓는 물에 소금을 넣은 뒤, 파스타면을 삶는다.\n\n10. 식초, 간장, 맛술, 설탕, 물을 섞어 초간장소스를 만든다.\n\n11. 접시에 초간장소스와 채 썬 적양파를 담는다.\n\n12. 삶은 파스타면과 면수, 구운 채소를 파스타소스에 넣고 볶는다.\n\n13. 오븐에서 익힌 가지를 초간장소스와 적양파 위에 올린 뒤, 토치로 치즈를 녹인다.\n\n14. 파스타를 접시에 담는다.\n\n15. 파스타 위에 파르메산 치즈가루를 뿌려 완성한다.'),
	(646, '파스타랑깨', NULL, 1, '1. 끓는 물에 소금과 생강즙을 넣고 알배추를 데친다.\n\n2. 냄비에 인스턴트 사골곰탕과 으깬 마늘을 넣고 끓인다.\n\n3. 끓인 사골곰탕에 적당한 크기로 썬 편육과 토마토소스를 넣고 끓여 배추찜소스를 만든다.\n\n4. 3에 간장, 고춧가루, 후추를 넣고 끓인다.\n\n5. 소금물에 데친 알배추를 배추찜소스에 넣고 끓인다.\n\n6. 끓는 물에 파스타면과 굴소스를 넣고 끓인다.\n\n7. 쪽파를 잘게 썬다.\n\n8. 삶은 파스타면의 물을 따라낸 뒤, 들깻가루를 넣고 섞는다.\n\n9. 김을 잘게 썬다.\n\n10. 접시에 배추찜을 담는다.\n\n11. 그릇에 들깨파스타를 담은 뒤, 들기름을 넣고 버무린다.\n\n12. 들깨파스타 위에 잘게 썬 김을 올린다.\n\n13. 배추찜 위에 잘게 썬 쪽파를 올린다.'),
	(647, '벵에돔이시여', NULL, 1, '1. 대파, 청양고추를 적당한 크기로 썬다.\n\n2. 끓는 기름에 손질한 채소와 고춧가루를 넣고 끓여 고추기름을 낸다.\n\n3. 홍합을 삶는다.\n\n4. 무, 브로콜리, 마늘, 대파, 아스파라거스를 손질한다.\n\n5. 긴꼬리벵에돔 양쪽에 깊이 칼집을 낸다.\n\n6. 칼집을 낸 긴꼬리벵에돔에 전분과 전분물을 차례로 바른 뒤, 기름에 튀긴다.\n\n7. 고추기름을 체에 밭쳐 기름만 걸러낸다.\n\n8. 홍고추와 고추를 직화한다.\n\n9. 쯔유, 후추, 식초를 섞어 쯔유소스를 만든다.\n\n10. 삶은 홍합을 찬물에 헹궈 식힌다.\n\n11. 직화한 고추의 탄 부분을 제거하고 적당한 크기로 썰어 쯔유소스에 넣는다.\n\n12. 고추기름을 두른 팬에 손질한 채소들을 넣고 볶는다.\n\n13. 볶던 채소에 간장, 적당한 크기로 썬 그린올리브와 고추를 넣고 볶는다.\n\n14. 14에 홍합육수와 삶은 홍합살을 넣고 끓인다.\n\n15. 끓이던 재료에 맛술, 매실액, 후추, 쯔유를 넣어 마라탕소스를 만든다.\n\n16. 긴꼬리벵에돔 튀김을 건져 기름기를 뺀다.\n\n17. 깻잎을 채 썬다.\n\n18. 접시에 익힌 채소를 담는다.\n\n19. 익힌 채소 위에 긴꼬리벵에돔 튀김을 올린다.\n\n20. 마라탕소스를 붓고, 채 썬 깻잎을 올려 마무리한다.'),
	(648, '레이먼킹돔', NULL, 1, '1. 압력밥솥에 물을 끓이다가, 손질한 대파와 무, 다진 생강, 마늘, 맛술, 청주를 넣고 끓여 채소육수를 낸다.\n\n2. 찜기에 물, 레몬즙, 레몬을 넣고 끓인다.\n\n3. 긴꼬리벵에돔을 손질한다.\n\n4. 긴꼬리벵에돔 뼈에 뜨거운 물을 부어 비린내를 제거한다.\n\n5. 채소육수에 긴꼬리벵에돔 뼈를 넣고 끓인다.\n\n6. 긴꼬리벵에돔 살을 적당한 크기로 손질한 뒤, 소금, 후추로 간 한다.\n\n7. 6에 올리브오일을 바르고 레몬을 올린 뒤, 찜기에 넣어 레몬증기로 찐다.\n\n8. 긴꼬리벵에돔 살을 다지고, 달걀노른자, 즉석밥, 소금, 후추, 전분과 함께 버무린다.\n\n9. 긴꼬리벵에돔 밥을 모양 잡은 뒤, 기름에 튀긴다.\n\n10. 돔육수를 체에 밭쳐 육수만 걸러낸다.\n\n11. 돔육수에 쯔유, 소금으로 간 하고, 전분물을 넣어 농도를 조절한다.\n\n12. 그릇에 긴꼬리벵에돔 밥튀김을 넣고, 돔육수를 붓는다.\n\n13. 긴꼬리벵에돔 밥튀김 위에 긴꼬리벵에돔 스테이크를 올린다.\n\n14. 긴꼬리벵에돔 스테이크 위에 잘게 썬 대파와 우메보시를 올려 마무리한다.'),
	(649, '이건 태곤스타일♬', NULL, 1, '1. 적당한 크기로 썬 무의 껍질을 벗겨내고, 대패삼겹살으로 돌돌 만 뒤 밀가루를 묻힌다.\n\n2. 식빵의 테두리는 잘라내고, 믹서기에 갈아 빵가루를 만든다.\n\n3. 밀가루, 달걀, 우유를 섞어 튀김반죽을 만든다.\n\n4. 밀가루를 묻힌 대패삼겹살에 튀김반죽>빵가루를 순서대로 입혀 기름에 튀긴다.\n\n5. 튀긴 대패삼겹살을 건져낸 뒤, 다시 한 번 튀김반죽>빵가루를 순서대로 입혀 튀긴다.\n\n6. 무늬오징어에 촘촘하게 칼집을 낸 뒤, 적당한 크기로 썰어 끓는 물에 데친다.\n\n7. 홍합을 삶는다.\n\n8. 양파, 마늘, 애호박을 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\n\n9. 삶은 홍합의 살을 발라낸다.\n\n10. 볶던 채소에 새우, 데친 무늬오징어, 소금, 굴소스, 삶은 홍합살, 청주, 버터, 후추를 넣고 볶는다.\n\n11. 대패삼겹살 튀김의 윗면을 자른 뒤, 무를 빼내 그릇모양을 만든다.\n\n12. 대패삼겹살 그릇 속을 토치로 익힌다.\n\n13. 볶던 해산물과 채소에 오징어먹물을 넣고 볶아 오징어먹물버터야키를 만든다.\n\n14. 대패삼겹살그릇에 오징어먹물버터야키를 담는다.\n\n15. 접시에 대패삼겹살그릇과 오징어먹물버터야키를 담아 마무리한다.'),
	(650, '무소유', NULL, 1, '1. 무를 세로로 반을 가른다.\n\n2. 반으로 가른 무의 속을 파내 그릇모양을 만든 뒤, 찜기에 찐다.\n\n3. 무와 양파를 적당한 크기로 썬다.\n\n4. 기름을 두른 팬에 손질한 채소를 볶는다.\n\n5. 볶던 채소에 잘게 썬 대파를 넣고 볶는다.\n\n6. 볶던 채소에 크림소스와 뜨거운 물을 붓고 조린다.\n\n7. 6과 소금, 후추를 핸드블렌더에 넣고 갈아 무크림소스를 만든다.\n\n8. 소금을 넣은 끓는 물에 브로콜리와 애호박을 데친다.\n\n9. 데친 채소를 얼음물에 담가 식힌다.\n\n10. 래디시와 방울양배추를 손질해 얼음물에 담가둔다.\n\n11. 끓는 물에 간장, 설탕, 홍합을 넣고 익힌다.\n\n12. 소갈비살에 핑크솔트로 간 한다.\n\n13. 기름을 두른 팬에 소갈비살을 굽는다.\n\n14. 대파, 양파를 잘게 썬다.\n\n15. 익힌 홍합을 건져낸 뒤, 홍합육수에 발사믹드레싱과 버터를 넣어 홍합발사믹소스를 만든다.\n\n16. 소갈비살을 굽던 팬에 익힌 홍합과 홍합발사믹소스를 넣고 함께 볶는다.\n\n17. 16에 잘게 썬 대파와 양파를 넣고, 후추를 뿌린다.\n\n18. 브로콜리를 손질한 뒤, 적당한 크기로 썬다.\n\n19. 찐 무그릇에 무크림소스를 담은 뒤, 홍합발사믹소스에 볶은 홍합과 소갈비살을 올린다.\n\n20. 그릇에 무크림소스와 손질한 브로콜리, 래디시, 무순을 담은 뒤, 홍합과 소갈비살을 담은 무그릇을 올려 마무리한다.'),
	(651, '홍시 연볶이', NULL, 1, '1. 양파에 칼집을 넣어 연꽃 모양을 만든 뒤, 찬물에 담가둔다.\n\n2. 양파, 마늘, 대파를 손질한다.\n\n3. 기름을 두른 팬에 손질한 채소를 볶는다.\n\n4. 채식어묵을 적당한 크기로 썬다.\n\n5. 불린 떡볶이떡, 적당한 크기로 썬 아스파라거스와 애호박, 채식어묵에 매실청, 올리브오일, 간장, 고추장을 넣고 버무린다.\n\n6. 채소를 볶던 팬에 양념한 떡&채소와 표고버섯가루를 넣고 볶아 떡볶이를 만든다.\n\n7. 찬물에 담가둔 양파연꽃을 끓는 물에 데친다.\n\n8. 데친 양파연꽃에 밀가루>달걀물>밀가루를 순서대로 입혀 튀긴다.\n\n9. 떡볶이에 불린 당면을 넣고 볶는다.\n\n10. 홍시는 껍질을 벗기고 씨를 발라낸 뒤, 핸드블렌더에 간다.\n\n11. 당면 떡볶이에 간 홍시를 넣고 볶는다.\n\n12. 양파연꽃튀김에 소금을 뿌린다.\n\n13. 그릇에 홍시 당면 떡볶이를 담은 뒤, 간 홍시를 붓는다.\n\n14. 13 위에 양파연꽃튀김을 올려 마무리한다.'),
	(652, '완벽한 라볶이에 대한 사랑', NULL, 1, '1. 채식라면 면을 삶는다.\n\n2. 유부를 데친다.\n\n3. 삶은 면을 건져 찬물에 헹군다.\n\n4. 삶은 면에 전분, 모차렐라치즈를 넣고 버무린 뒤, 기름을 두른 팬에 굽는다.\n\n5. 핸드블랜더에 손질한 마, 밀가루, 전분, 달걀흰자, 설탕, 소금, 당근, 대파를 넣고 갈아 어묵반죽을 만든다.\n\n6. 기름을 두른 팬에 어묵반죽을 구워 마 어묵을 만든다.\n\n7. 데친 유부를 건져낸 뒤, 찬물에 담가 식힌다.\n\n8. 애호박, 양파, 불린 표고버섯을 간 뒤, 불린 당면과 조미김, 간장, 들기름을 섞어 만두소를 만든다.\n\n9. 데친 유부 안에 만두소를 채운 뒤, 이쑤시개로 고정해 유부만두를 만든다.\n\n10. 기름을 두른 팬에 손질한 양파와 마늘을 볶는다.\n\n11. 10에 손질한 불린 표고버섯, 양배추, 애호박, 불린 떡볶이떡, 양송이버섯, 표고버섯가루, 표고버섯 불린 물, 뜨거운 물을 넣고 끓여 떡볶이 소스를 만든다.\n\n12. 마 어묵을 적당한 크기로 썬다.\n\n13. 그릇에 구운 라면과 마 어묵을 담는다.\n\n14. 밀가루에 물을 섞어 튀김옷을 만든 뒤, 유부만두에 입혀 튀긴다.\n\n15. 떡볶이 소스에 간장, 맛술, 설탕, 적당한 크기로 썬 홍고추, 부순 조미김, 채식라면 수프를 넣고 끓인다.\n\n16. 떡볶이 소스에 전분물을 넣어 농도를 조절한다.\n\n17. 그릇에 유부만두튀김을 담은 뒤, 떡볶이 소스를 부어 마무리한다.'),
	(653, '승소 파스타', NULL, 1, '1. 믹서기에 물, 도토리묵을 넣고 간다.\n\n2. 밀가루에 소금, 올리브오일, 물과 함께 간 도토리묵을 넣고 섞어 도토리면 반죽을 만든다.\n\n3. 배의 윗면을 잘라내고 속을 파낸다.\n\n4. 볼에 오미자청, 물, 배, 딸기를 넣고 버무린다.\n\n5. 속을 파낸 배 안에 오미자청에 버무린 배와 딸기를 채우고, 압력솥에 넣어 찐다.\n\n6. 불린 표고버섯과 양파를 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\n\n7. 강판에 간 배를 채소를 볶던 팬에 넣는다.\n\n8. 아스파라거스와 동치미 무를 잘게 썰어 채소를 볶던 팬에 넣고 함께 볶는다.\n\n9. 8에 고춧가루, 간장, 들기름, 잘게 썬 연근조림을 넣고 볶아 파스타소스를 만든다.\n\n10. 도토리반죽을 치대고 넓게 민 뒤, 적당한 두께로 썰어 도토리생면을 만든다.\n\n11. 끓는 물에 도토리생면을 삶는다.\n\n12. 파스타소스에 식초, 동치미국물을 넣는다.\n\n13. 삶은 도토리생면을 찬물에 헹군 뒤, 파스타소스에 넣고 볶는다.\n\n14. 파스타에 흑임자드레싱을 넣고 볶는다.\n\n15. 그릇에 도토리생면 파스타를 담은 뒤, 잘게 썬 아스파라거스와 동치미 무를 올린다.\n\n16. 그릇에 배찜을 담고 잘게 썬 대추를 올려 마무리한다.'),
	(654, '머쉬 치윤디~!', NULL, 1, '1. 마른 팬에 아몬드슬라이스를 볶는다.\n\n2. 기름을 두른 냄비에 손질한 양파, 당근, 표고버섯과 표고버섯가루를 넣고 볶다가, 물을 넣고 끓여 채소육수를 낸다.\n\n3. 볶은 아몬드슬라이스를 접시에 담아둔다.\n\n4. 기름을 두른 팬에 잘게 썬 양파와 양송이버섯, 소금을 넣고 볶는다.\n\n5. 마른 팬에 버터와 밀가루를 넣고 볶아 루를 만든다.\n\n6. 루에 우유, 검은콩두유를 넣고 끓여 크림소스를 만든다.\n\n7. 볶던 채소에 채소육수를 넣고 끓이다가 크림소스를 넣어 버섯크림수프를 만든다.\n\n8. 양송이버섯에 올리브오일, 발사믹식초를 넣고 버무린 뒤, 잘게 다진 연근조림으로 속을 채운다.\n\n9. 속을 채운 양송이버섯을 오븐에 넣고 굽는다.\n\n10. 오븐에 구운 양송이버섯 위에 모차렐라치즈를 올린 뒤, 다시 한 번 굽는다.\n\n11. 아스파라거스와 마를 채 썬다.\n\n12. 레몬즙, 올리브오일, 설탕, 발사믹식초를 섞어 만든 드레싱을 채 썬 채소에 넣고 버무린다.\n\n13. 버섯크림수프에 모차렐라치즈를 넣고 핸드블렌더로 간 뒤, 다시 한 번 끓인다.\n\n14. 그릇에 버섯크림수프를 담는다.\n\n15. 버섯크림수프 위에 양송이치즈구이를 올린다.\n\n16. 15 위에 마&아스파라거스샐러드를 올리고, 구운 아몬드슬라이스를 뿌린다.\n\n17. 레몬즙을 뿌려 마무리한다.'),
	(655, '풍카소', NULL, 1, '1. 설탕물을 끓인다.\n\n2. 달걀의 흰자와 노른자를 분리한다.\n\n3. 달걀흰자로 머랭을 친다.\n\n4. 달걀노른자에 화이트와인과 설탕을 넣은 뒤, 거품기로 치며 중탕한다.\n\n5. 4에 소금, 버터, 우유, 연유를 넣어 달걀크림소스를 만든다.\n\n6. 머랭에 끓인 설탕물을 조금씩 넣어가며 섞는다.\n\n7. 팬케이크가루에 달걀, 우유를 섞어 팬케이크반죽을 만든다.\n\n8. 소스병에 팬케이크반죽을 담는다.\n\n9. 팬에 기름을 두른 뒤, 키친타월로 닦아낸다.\n\n10. 기름으로 코팅한 팬에 팬케이크반죽으로 그림을 그린다.\n\n11. 팬케이크반죽으로 그린 그림이 어느 정도 익으면, 위에 팬케이크반죽을 부어 굽는다.\n\n12. 머랭에 달걀크림소스를 넣고 섞어 머랭크림소스를 만든다.\n\n13. 그릇에 머랭크림소스를 담는다.\n\n14. 체리는 씨를 발라내고, 그릇에 담는다.\n\n15. 머랭크림소스 위에 팬케이크를 올려 마무리한다.'),
	(656, '브런치의 유혹', NULL, 1, '1. 사과는 껍질을 깎은 뒤, 잘게 썬다.\n\n2. 마른 팬에 잘게 썬 사과를 볶다가 버터, 설탕, 레몬즙, 건크랜베리를 넣어 캐러멜라이징 한다.\n\n3. 핸드블렌더로 달걀, 연유, 우유, 설탕을 갈아 달걀물을 만든다.\n\n4. 달걀물을 체에 밭쳐 거른다.\n\n5. 식빵에 달걀물을 입힌다.\n\n6. 기름을 두른 팬에 달걀물을 입힌 식빵을 굽다가 우유를 부어 프렌치토스트를 만든다.\n\n7. 마른 팬에 잘게 썬 양송이버섯과 양파, 돈가스소스, 케첩, 맛술, 버터를 넣고 볶아 양송이소스를 만든다.\n\n8. 마른 팬에 채 썬 양파와 당근, 잘게 썬 김밥용 햄, 소금을 넣고 볶는다.\n\n9. 캐러멜라이징한 사과와 햄볶음을 함께 볶는다.\n\n10. 프렌치토스트 사이에 사과햄볶음을 넣고 샌드한다.\n\n11. 프렌치토스트 샌드위치의 테두리를 잘라낸 뒤, 먹기 좋은 크기로 썬다.\n\n12. 접시에 프렌치토스트 샌드위치를 담는다.\n\n13. 달걀물에 소금으로 간 한 뒤, 기름을 두른 팬에 부어 오믈렛을 만든다.\n\n14. 프렌치토스트 샌드위치 위에 오믈렛을 올린다.\n\n15. 오믈렛을 가른 뒤, 전자레인지에 돌려 익힌다.\n\n16. 오믈렛 위에 양송이소스를 부어 마무리한다.'),
	(657, '감자로 유혹하자냐', NULL, 1, '1. 감자를 얇게 슬라이스 한다.\n\n2. 감자를 잘게 썬 뒤, 끓는 물에 넣고 삶는다.\n\n3. 감자를 삶던 냄비에 편마늘을 넣고 함께 삶는다.\n\n4. 마른 팬 3개에 감자슬라이스를 넓게 펼쳐 굽는다.\n\n5. 감자전에 전분을 뿌린다.\n\n6. 오이를 얇게 썬 뒤, 소금을 뿌린다.\n\n7. 청양고추, 홍고추, 양파를 손질한다.\n\n8. 오이와 손질한 채소를 함께 섞은 뒤, 레몬즙, 후추, 핫소스, 소금을 넣고 버무려 오이샐러드를 만든다.\n\n9. 편마늘을 넣고 삶은 감자에 달걀노른자를 넣고 으깨 매시드포테이토를 만든다.\n\n10. 매시드포테이토에 손질 쪽파와 버터를 넣고 섞는다.\n\n11. 감자전에 매시드포테이토를 올린 뒤, 다른 감자전으로 샌드한다.\n\n12. 감자전 위에 슬라이스치즈를 얹는다.\n\n13. 매시드포테이토에 다진 브라질너트를 넣고 섞는다.\n\n14. 슬라이스치즈 위에 브라질너트를 넣은 매시드포테이토를 올리고 감자전으로 덮어 감자라자냐를 만든다.\n\n15. 감자라쟈냐를 적당한 크기로 썬 뒤, 슬라이스치즈를 사이에 넣고 샌드한다.\n\n16. 접시에 감자라자냐를 담고, 올리브오일을 뿌린다.\n\n17. 접시에 오이샐러드를 곁들여 완성한다.'),
	(658, '호감이야', NULL, 1, '1. 감자를 잘게 썬 뒤, 소금을 넣은 끓는 물에 넣고 삶는다.\n\n2. 채 썬 감자와 잘게 썬 양파를 기름을 두른 팬에 넣고 볶는다.\n\n3. 2에 잘게 썬 청양고추를 넣고 함께 볶는다.\n\n4. 달걀을 삶는다.\n\n5. 애호박을 동그랗게 파내 기름을 두른 팬에 굽는다.\n\n6. 애호박의 속을 파내 그릇 모양을 만들고 기름을 두른 팬에 굽는다.\n\n7. 볶던 감자에 레드와인을 넣고 조린다.\n\n8. 7에 소금, 후추, 칠리소스, 고춧가루를 넣고 볶는다.\n\n9. 구운 애호박 그릇 안에 슬라이스치즈를 깐 뒤, 감자볶음을 채워 넣고 슬라이스치즈를 얹는다.\n\n10. 9를 오븐에 굽는다.\n\n11. 삶은 감자를 얼음물에 담가 식힌다.\n\n12. 삶은 달걀은 껍데기를 벗겨낸 뒤, 잘게 썬다.\n\n13. 상추, 오이, 홍고추를 손질한다.\n\n14. 슬라이스한 오이에 손질한 상추와 홍고추를 올리고 돌돌 말아 꽃모양을 만든다.\n\n15. 14와 구운 애호박을 접시에 담는다.\n\n16. 수제 월남쌈소스에 연겨자와 후추를 넣고 섞은 뒤, 삶은 감자를 넣고 버무려 감자샐러드를 만든다.\n\n17. 감자샐러드에 잘게 썬 삶은 달걀을 넣고 버무린다.\n\n18. 원형틀에 감자샐러드를 담아 모양낸 뒤, 삶은 달걀을 얹고 고춧가루를 뿌린다.\n\n19. 오븐에 구운 감자볶음을 채운 애호박을 접시에 담고, 후추를 뿌린다.\n\n20. 올리브오일을 뿌려 마무리한다.'),
	(659, '1파 2일', NULL, 1, '1. 대파를 채 썬다.\n\n2. 끓는 물에 파채를 데친 뒤, 찬물에 담가 식힌다.\n\n3. 삼겹살을 적당한 크기로 썬 뒤 끓는 물에 넣고 삶는다.\n\n4. 3에 커피가루, 맛술을 넣는다.\n\n5. 삶은 삼겹살을 건져 물기를 빼고, 전분을 입혀 기름에 튀긴다.\n\n6. 튀긴 삼겹살을 건져 기름기를 뺀다.\n\n7. 핸드블렌더에 대파, 사과, 생강즙, 청양고추, 마늘, 해선장소스, 간장, 고춧가루를 넣고 갈아 소스를 만든다.\n\n8. 팬에 튀긴 삼겹살과 소스를 넣고 볶아 회과육을 만든다.\n\n9. 회과육에 뜨거운 물을 넣고 볶는다.\n\n10. 찬물에 담가둔 데친 파채의 물기를 뺀 뒤, 접시에 담는다.\n\n11. 데친 파채 위에 회과육을 올린다.\n\n12. 볼에 고춧가루, 간장, 포도즙, 물엿, 들깻가루, 식초를 넣고 섞어 특제소스를 만든다.\n\n13. 종지에 특제소스를 담아낸다.'),
	(660, '스테이콘', NULL, 1, '1. 오징어채볶음을 밀가루와 달걀물에 버무린다.\n\n2. 양파와 대파 흰 부분을 잘게 썬다.\n\n3. 골뱅이를 잘게 썬다.\n\n4. 통조림 닭가슴살과 통조림 참치의 기름을 따라낸다.\n\n5. 어묵을 잘게 썬다.\n\n6. 믹서기에 어육소시지, 통조림 닭가슴살, 통조림 참치를 넣고 간다.\n\n7. 믹서기에 달걀, 물을 넣어 다시 간다.\n\n8. 기름을 두른 팬에 채 썬 양파, 잘게 썬 대파, 인스턴트 오돌뼈볶음, 인스턴트 막창, 맛술을 넣고 볶는다.\n\n9. 간 인스턴트 재료에 잘게 썬 어묵, 양파, 햄, 골뱅이를 넣고 섞어 스테이크 반죽을 만든다.\n\n10. 기름을 두른 팬에 스테이크 반죽을 굽는다.\n\n11. 볶던 재료에 물, 간장, 청주, 레몬즙을 넣고 졸여 오돌뼈막창소스를 만든다.\n\n12. 밀가루반죽을 입힌 오징어채볶음을 기름에 튀긴 뒤, 건져내 기름기를 뺀다.\n\n13. 햄버그 스테이크에 달걀물을 부어 굽는다.\n\n14. 오돌뼈막창소스에 참기름, 잘게 썬 대파를 넣고 볶는다.\n\n15. 접시에 햄버그 스테이크를 담는다.\n\n16. 스테이크 위에 오돌뼈막창소스를 부어 마무리한다.'),
	(661, '골뱅이 에스카르고', NULL, 1, '1. 밀가루, 전분, 달걀, 명이나물장아찌 국물을 섞어 반죽을 만든 뒤, 삼겹살을 버무린다.\n\n2. 통조림 번데기는 국물을 걸러 두고, 번데기만 잘게 썬다.\n\n3. 팬에 다진 마늘과 번데기를 볶는다.\n\n4. 골뱅이는 물에 한 번 헹궈낸 뒤, 번데기와 함께 볶는다.\n\n5. 골뱅이볶음에 후추, 소금으로 간 한다.\n\n6. 옥수수맛 과자를 부숴 가루로 만든다.\n\n7. 골뱅이볶음에 버터를 넣고 버무린다.\n\n8. 과자가루를 묻힌 골뱅이 볶음을 오븐에 구워 골뱅이 에스카르고를 만든다.\n\n9. 핸드블랜더에 청양고추와 홍고추, 통마늘을 넣고 간 뒤 팬에 넣어 볶는다.\n\n10. 볼에 식초, 새우젓소스, 볶은 고추와 마늘, 채 썬 양파를 넣고 버무려 태국식 소스를 만든다.\n\n11. 채 썬 대파를 얼음물에 담가 아린 맛을 뺀다.\n\n12. 태국식 소스에 통조림 번데기 국물과 레몬즙을 넣는다.\n\n13. 반죽에 버무린 삼겹살을 튀긴다.\n\n14. 골뱅이 에스카르고에 인스턴트 대게딱지장을 얹은 뒤, 다시 오븐에 굽는다.\n\n15. 팬에 애호박버섯볶음을 볶는다.\n\n16. 튀긴 삼겹살을 건져 먹기 좋은 크기로 썬다.\n\n17. 접시에 애호박버섯볶음을 담은 뒤, 삼겹살튀김을 올린다.\n\n18. 삼겹살튀김 위에 태국식 소스를 붓고, 골뱅이 에스카르고를 얹어 마무리한다.'),
	(662, '혼술 빠라삐', NULL, 1, '1. 파기름을 낸다.\n\n2. 마른 팬에 잘게 썬 양파와 파기름을 넣고 볶는다.\n\n3. 홍고추와 청양고추를 잘게 썬다.\n\n4. 양파를 볶던 팬에 잘게 썬 고추, 다진 생강을 넣고 함께 볶는다.\n\n5. 채소를 볶던 팬에 설탕과 물, 케첩, 레몬즙을 넣고 끓여 칠리소스를 만든다.\n\n6. 옥돔은 가시를 발라내고 손질한다.\n\n7. 손질한 옥돔에 소금으로 간 한 뒤, 밀가루를 입힌다.\n\n8. 파기름을 두른 팬에 옥돔을 굽는다.\n\n9. 적당한 크기로 썬 대파를 옥돔과 함께 굽는다.\n\n10. 부침가루와 물을 섞어 반죽을 만든 뒤, 대파 위에 부어 굽는다.\n\n11. 반죽 위에 구운 옥돔과 달걀을 올려 옥돔대파전을 만든다.\n\n12. 절구에 잘게 썬 청양고추와 홍고추, 다진 생강을 넣고 찧는다.\n\n13. 골뱅이 통조림 국물, 레몬즙, 찧은 고추와 생강, 설탕, 새우젓소스를 섞어 골뱅이소스를 만든 뒤, 종지에 담아낸다.\n\n14. 기름을 두른 팬에 채 썬 양배추와 양파, 소금, 후추를 넣고 볶는다.\n\n15. 접시에 양배추볶음을 담고 인스턴트 머리고기를 얹는다.\n\n16. 옥돔대파전을 먹기 좋은 크기로 썰어 접시에 담는다.\n\n17. 칠리소스에 참기름과 연겨자를 넣는다.\n\n18. 머리고기 위에 칠리소스를 얹고 파기름을 뿌려 마무리한다.'),
	(663, '레이먼 스테이크', NULL, 1, '1. 부챗살은 물기를 제거하고 고추냉이를 사이에 발라 겹겹이 붙인다.\n\n2. 부챗살에 소금과 후추로 간 한다.\n\n3. 겹겹이 쌓은 부챗살을 고기망치로 두드려 넓게 편다.\n\n4. 두 개의 볼에 전분과 밀가루를 넣은 뒤, 한 쪽에는 강황가루를, 한 쪽에는 고춧가루를 넣고 섞어 강황튀김옷과 고추튀김옷을 만든다.\n\n5. 달걀물에 우유를 넣는다.\n\n6. 부챗살에 고추튀김옷>달걀물>강황튀김옷>달걀물>강황튀김옷>고추튀김옷을 순서대로 입힌다.\n\n7. 튀김옷을 입힌 부챗살을 기름에 튀겨 컨트리 스테이크를 만든다.\n\n8. 양파를 채 썬다.\n\n9. 청양고추를 잘게 썬다.\n\n10. 버터를 녹인 팬에 채 썬 양파와 잘게 썬 청양고추, 맛술, 우유를 넣고 볶아 화이트소스를 만든다.\n\n11. 마른 팬에 방울토마토를 굽다가, 시금치와 으깬 마늘을 넣고 볶는다.\n\n12. 볶던 채소에 아로니아와 호박즙, 발사믹드레싱을 넣고 볶는다.\n\n13. 컨트리 스테이크의 기름기를 뺀다.\n\n14. 기름을 넉넉히 두른 팬에 컨트리 스테이크와 버터를 넣고 굽는다.\n\n15. 접시에 볶은 채소를 담는다.\n\n16. 접시에 컨트리 스테이크를 올린 뒤, 화이트소스를 얹어 마무리한다.'),
	(664, '안질리나 딤섬', NULL, 1, '1. 고춧가루, 대파 흰 부분, 다진 생강에 뜨거운 기름을 넣어 고추기름을 낸다.\n\n2. 끓는 물에 시금치를 넣어 삶는다.\n\n3. 부챗살은 물기를 제거한다.\n\n4. 핸드블렌더에 부챗살과 홍두깨살, 청양고추, 달걀을 넣고 간다.\n\n5. 부추와 양파를 잘게 썬다.\n\n6. 잘게 썬 채소, 간 소고기, 버터를 버무려 딤섬 소를 만든다.\n\n7. 딤섬 소에 고추기름을 넣는다.\n\n8. 밀전분에 시금치 삶은 물을 넣고 반죽한다.\n\n9. 딤섬 소에 참기름을 넣고 버무린다.\n\n10. 딤섬 반죽에 식용유를 넣어가며 반죽한 뒤, 딤섬 피를 만든다.\n\n11. 딤섬 피에 딤섬 소를 넣고 모양을 잡아 찜기에 찐다.\n\n12. 물기를 뺀 부챗살에 설탕을 묻힌다.\n\n13. 설탕을 묻힌 부챗살을 기름에 튀겨 육포를 만든다.\n\n14. 육포를 먹기 좋은 크기로 썬다.\n\n15. 사과를 적당한 크기로 썬 뒤, 설탕을 뿌려 기름에 튀긴다.\n\n16. 접시에 육포와 사과튀김을 담는다.\n\n17. 접시에 딤섬을 담아 마무리한다.'),
	(665, '샨티샨티~ 카레 라자냐♬', NULL, 1, '1. 핸드블렌더에 밀가루, 달걀, 강황가루, 물을 넣고 갈아 강황반죽을 만든다.\n\n2. 핸드블렌더에 우둔살과 부챗살, 닭가슴살소시지를 넣고 간다.\n\n3. 마른 팬에 잘게 썬 양파를 볶는다.\n\n4. 볶던 양파에 편마늘, 잘게 썬 홍고추, 간 소고기와 닭가슴살소시지, 소금, 카레가루, 후추를 넣고 볶는다.\n\n5. 볶던 재료에 적당한 크기로 썬 방울토마토를 넣고 볶는다.\n\n6. 볶던 재료에 시금치를 넣고 볶아 소고기채소카레볶음을 만든다.\n\n7. 강황반죽을 밀대로 넓게 민 뒤, 적당한 크기로 썰어 라자냐 면을 만든다.\n\n8. 끓는 물에 소금을 넣고, 라자냐 면을 삶는다.\n\n9. 마른 팬에 버터와 밀가루를 넣고 볶아 루를 만든 뒤, 우유를 넣어 베샤멜소스를 만든다.\n\n10. 베샤멜소스에 토마토소스를 넣어 로제소스를 만든다.\n\n11. 그릇에 로제소스>라자냐 면>소고기채소카레볶음>로제소스>라자냐 면>소고기채소카레볶음>후추>로제소스를 순서대로 담는다.\n\n12. 카레 라자냐를 오븐에 굽는다.\n\n13. 팬에 버터, 밀가루, 우유를 넣고 베샤멜소스를 만든다.\n\n14. 베샤멜소스에 코코넛밀크스무디를 넣어 코코넛크림소스를 만든다.\n\n15. 카레 라자냐를 오븐에서 꺼내 코코넛크림소스를 올리고, 다시 오븐에 굽는다.\n\n16. 카레 라자냐를 토치로 직화해 마무리한다.'),
	(666, '카레맛~ 궁금해 혀니♬', NULL, 1, '1. 연어는 적당한 크기로 썬 뒤, 소금과 후추로 간 한다.\n\n2. 밀가루와 카레가루를 섞은 뒤, 연어에 묻힌다.\n\n3. 핸드블렌더에 우둔살, 양파, 당근을 넣고 간다.\n\n4. 기름을 두른 팬에 간 우둔살과 채소를 넣고 볶는다.\n\n5. 핸드블렌더에 방울토마토를 넣고 간 뒤, 볶던 재료에 넣고 함께 볶는다.\n\n6. 볶던 재료에 카레가루와 강황가루를 넣고 볶아 토마토드라이카레를 만든다.\n\n7. 양파는 채 썰고, 부추는 적당한 크기로 썬다.\n\n8. 기름을 두른 팬에 카레가루를 입힌 연어를 굽는다.\n\n9. 연어스테이크와 토마토드라이카레에 버터를 넣는다.\n\n10. 토마토드라이카레에 토마토소스, 설탕, 간장을 넣는다.\n\n11. 기름을 두른 팬에 달걀프라이를 만든다.\n\n12. 전자레인지에 즉석밥을 돌린다.\n\n13. 고춧가루, 간장, 식초, 설탕, 참기름, 깨를 섞어 양념을 만든다.\n\n14. 잘게 썬 부추와 채 썬 양파에 양념을 넣고 무쳐 부추무침을 만든 뒤, 그릇에 담는다.\n\n15. 접시에 밥을 담고, 토마토드라이카레와 카레연어스테이크를 담는다.\n\n16. 밥 위에 달걀프라이를 얹어 마무리한다.'),
	(667, '너마늘 위한 치킨', NULL, 1, '1. 토종닭을 해체한다.\n\n2. 해체한 닭에 칼집을 넣는다.\n\n3. 칼집을 낸 닭에 카레가루와 조미료를 넣는다.\n\n4. 3에 다진 마늘, 생강즙을 넣고 버무린다.\n\n5. 양념한 닭에 소금, 달걀, 밀가루, 전분을 넣고 섞어 튀김옷을 입힌다.\n\n6. 튀김옷을 입힌 닭을 기름에 튀긴다.\n\n7. 마른 팬에 다진 마늘, 생강즙, 설탕, 물엿, 뜨거운 물을 넣고 끓여 마늘소스를 만든다.\n\n8. 무를 채 썬다.\n\n9. 끓는 물에 채 썬 무와 식초를 넣고 절인다.\n\n10. 절이던 무에 소금과 설탕을 넣는다.\n\n11. 튀기던 닭을 건진 뒤, 전자레인지에 넣어 속살을 익힌다.\n\n12. 전자레인지에서 익힌 닭을 마늘소스에 버무린다.\n\n13. 절인 무채를 체에 밭쳐 물기를 뺀 뒤, 얼음물에 담가 식힌다.\n\n14. 절인 무채를 종지에 담는다.\n\n15. 마늘치킨을 접시에 담아낸다.'),
	(668, 'Touch 닭 body~♬', NULL, 1, '1. 토종닭을 해체한다.\n\n2. 끓는 물에 닭 몸통을 넣고 끓여 닭육수를 낸다.\n\n3. 닭다리를 발골한다.\n\n4. 닭육수에 닭다리뼈와 표고버섯 우린 물을 넣고 끓인다.\n\n5. 핸드블렌더에 대파, 닭가슴살, 돼지 배받이살, 청란을 넣고 간다.\n\n6. 간 고기에 전분을 넣고 한 번 더 갈아 닭완자반죽을 만든다.\n\n7. 닭완자를 빚은 뒤, 닭육수에 넣고 익힌다.\n\n8. 닭날개에 전분을 묻힌 뒤, 기름에 튀긴다.\n\n9. 닭다리살을 석쇠에 올리고 토치로 직화한다.\n\n10. 팬에 표고버섯과 대파, 양파, 직화한 닭다리살을 적당한 크기로 썰어 넣고 볶는다.\n\n11. 맛술, 간장, 청주를 3:1:1 비율로 섞어 스키야키소스를 만든다.\n\n12. 볶던 재료에 스키야키소스를 넣고 조린다.\n\n13. 닭날개튀김을 건져내 기름기를 뺀다.\n\n14. 닭육수에 익힌 닭완자와 닭날개튀김을 스키야키에 넣는다.\n\n15. 닭스키야키를 전골냄비에 옮겨 담는다.\n\n16. 전자레인지에 돌린 즉석밥을 그릇에 담는다.\n\n17. 청란과 마요네즈를 섞어 달걀소스를 만든 뒤, 종지에 담아낸다.\n\n18. 닭스키야키에 후추를 넣어 마무리한다.'),
	(669, '입맛 잡어', NULL, 1, '1. 잡어를 적당한 크기로 썬다.\n\n2. 기름을 두른 팬에 적당한 크기로 썬 잡어, 다진 마늘, 밀가루를 넣어 볶는다.\n\n3. 볶은 잡어를 토치로 직화한 뒤, 소주를 넣고 한 번 더 직화한다.\n\n4. 볶은 잡어에 뜨거운 물을 부은 뒤, 핸드블렌더로 간다.\n\n5. 간 잡어를 체에 밭쳐 거른다.\n\n6. 돼지 배받이살을 얇게 썬다.\n\n7. 참기름을 두른 냄비에 갈치속젓과 돼지 배받이살을 넣어 볶는다.\n\n8. 볶은 갈치속젓과 돼지 배받이살에 간 잡어, 뜨거운 물을 넣고 끓인다.\n\n9. 불린 무시래기의 잎을 적당한 크기로 썰어 넣고 함께 끓인다.\n\n10. 적당한 크기로 썬 무, 고추, 대파를 넣은 뒤 고추장, 고춧가루를 더해 잡어매운탕을 만든다.\n\n11. 부시리의 껍질만 떼어낸다.\n\n12. 식초와 소금을 넣은 끓는 물에 부시리껍질과 전복을 데친다.\n\n13. 전자레인지에 즉석밥을 데운다.\n\n14. 무말랭이장아찌와 깻잎된장무침을 잘게 썬 뒤 식초, 참기름, 깨를 넣어 소스를 만든다.\n\n15. 양파, 대파, 당근, 청양고추를 채 썬다.\n\n16. 데친 부시리껍질과 전복을 얼음물에 담가 식힌 후, 적당한 크기로 썬다.\n\n17. 부시리껍질과 전복, 채 썬 채소에 소스를 넣고 무쳐 부시리껍질무침을 만든다.\n\n18. 접시에 부시리껍질무침을 담는다.\n\n19. 잡어매운탕에 조미료, 생강즙, 설탕을 넣고 끓인다.\n\n20. 그릇에 밥을 담는다.\n\n21. 잡어매운탕을 뚝배기그릇에 옮겨 담아낸다.'),
	(670, '돔레이더', NULL, 1, '1. 참돔을 해체한다.\n\n2. 참돔살에 격자무늬로 칼집을 낸다.\n\n3. 손질한 참돔살과 아가미에 전분을 묻힌다.\n\n4. 전분을 묻힌 참돔살과 아가미를 기름에 튀긴다.\n\n5. 튀긴 참돔살과 아가미를 건져내 기름기를 뺀다.\n\n6. 무, 대파, 당근을 채 썬 뒤 전분에 버무린다.\n\n7. 전분에 버무린 채소를 기름에 튀긴 뒤 체에 밭쳐 기름기를 뺀다.\n\n8. 기름을 두른 팬에 다진 생강을 넣고 소주로 불을 내 익힌다.\n\n9. 익힌 생강에 고추장아찌국물, 딸기잼을 넣고 졸여 간장소스를 만든다.\n\n10. 팬에 손질된 앵두를 넣고 졸인다.\n\n11. 간장소스에 후추를 넣는다.\n\n12. 졸인 앵두에 소주, 잣, 케첩, 설탕을 넣고 졸여 앵두소스를 만든다.\n\n13. 접시에 참돔살튀김과 아가미튀김을 올린다.\n\n14. 앵두소스에 전분물을 넣어 농도를 조절한다.\n\n15. 참돔살튀김 위에 간장소스와 앵두소스를 각각 붓는다.\n\n16. 참돔튀김에 채소튀김을 곁들인다.'),
	(671, '울금 캐비지', NULL, 1, '1. 군고구마의 껍질을 벗겨낸다.\n\n2. 우둔살과 불린 건표고버섯은 잘게 썰고, 마늘은 으깬다.\n\n3. 기름을 두른 팬에 잘게 썬 우둔살과 건표고버섯, 으깬 마늘을 넣고 볶는다.\n\n4. 볶던 우둔살에 간장, 후추를 넣는다.\n\n5. 군고구마에 볶은 우둔살을 넣은 뒤 함께 으깬다.\n\n6. 5를 삶은 양배추에 올린 뒤 돌돌 말아 양배추롤을 만든다.\n\n7. 양배추롤을 꼬치로 고정시킨다.\n\n8. 기름을 두른 팬에 은행을 굽는다.\n\n9. 버터를 녹인 냄비에 잘게 썬 양파를 넣고 볶는다.\n\n10. 볶던 양파에 밀가루, 두유, 사과즙을 넣고 섞는다.\n\n11. 10에 울금가루를 넣어 울금소스를 만든다.\n\n12. 울금소스에 소금으로 간 한다.\n\n13. 기름을 두른 팬에 양배추롤을 굽는다.\n\n14. 사과와 당근을 채 썬다.\n\n15. 채 썬 사과와 당근에 채 썬 양배추, 올리브오일, 키위식초를 넣고 버무려 샐러드를 만든다.\n\n16. 접시에 울금소스를 담은 뒤, 구운 양배추롤과 은행을 올린다.\n\n17. 샐러드와 울금양배추롤을 함께 낸다.'),
	(672, '시나몬 아로니아 비스킷', NULL, 1, '1. 호박즙과 사과즙을 각각 팬에 넣고 끓인다.\n\n2. 끓이던 사과즙에 씨를 빼고 손질한 사과를 넣고 졸인다.\n\n3. 끓이던 호박즙에 적당한 크기로 썬 레몬을 넣고 졸인다.\n\n4. 졸이던 사과즙에 대추청을 넣는다.\n\n5. 레몬과 함께 졸이던 호박즙에 패션프루트주스를 넣는다.\n\n6. 핸드블렌더에 현미누룽지를 넣고 간다.\n\n7. 간 현미누룽지를 두 개의 볼에 나눠 담는다.\n\n8. 달걀흰자로 머랭을 친다.\n\n9. 현미누룽지가루에 각각 계피가루와 아로니아가루를 넣은 뒤, 흑설탕을 넣는다.\n\n10. 아로니아누룽지가루에 머랭을 섞어 아로니아비스킷반죽을 만든다.\n\n11. 원형틀에 적당한 크기로 썬 군고구마를 넣는다.\n\n12. 원형틀에 아로니아비스킷반죽을 넣는다.\n\n13. 계피누룽지가루에 머랭을 섞어 계피비스킷반죽을 만든다.\n\n14. 원형틀에 계피비스킷반죽을 넣는다.\n\n15. 비스킷반죽 위에 적당한 크기로 썬 군고구마를 올린다.\n\n16. 비스킷반죽을 오븐에 넣고 굽는다.\n\n17. 오븐에 구운 비스킷을 원형틀에서 빼낸 뒤, 접시에 담는다.\n\n18. 계피비스킷 위에 호박소스, 조린 레몬과 패션프루트를 올린다.\n\n19. 사과대추소스에 조린 사과 위에 아로니아 비스킷을 올린다.\n\n20. 사과대추소스를 접시에 플레이팅 해 마무리한다.'),
	(673, '고기말이~ 히트다 히트!', NULL, 1, '1. 기름을 두른 팬에 채 썬 당근과 다진 마늘, 소금, 잘게 썬 홍고추를 넣고 볶는다.\n\n2. 핸드블렌더에 오징어닭가슴살스테이크를 넣고 간다.\n\n3. 볶던 재료에 간 오징어닭가슴살스테이크, 간장, 참기름, 깨를 넣고 볶는다.\n\n4. 우둔살을 얇게 썬다.\n\n5. 얇게 썬 우둔살을 고기망치로 두드려 넓게 편다.\n\n6. 넓게 편 우둔살에 볶은 당근과 군고구마를 넣고 돌돌 만 뒤, 이쑤시개로 고정한다.\n\n7. 기름을 두른 팬에 고기말이를 굽는다.\n\n8. 고기말이에 소금, 후추로 간 한다.\n\n9. 잘게 썬 홍고추, 간장, 식초, 올리브오일, 다진 마늘, 다진 생강, 흑설탕, 참기름, 레몬즙을 섞어 드레싱을 만든다.\n\n10. 고기말이를 토치로 직화한다.\n\n11. 고기말이를 고정하던 이쑤시개를 제거한 뒤, 접시에 담는다.\n\n12. 고기말이 위에 드레싱을 뿌린다.\n\n13. 사과와 대파를 채 썬다.\n\n14. 고기말이에 채 썬 양배추와 사과, 대파를 곁들인다.\n\n15. 잘게 부순 깨를 뿌려 마무리한다.'),
	(674, '버거킴', NULL, 1, '1. 적당한 크기로 썬 우둔살과 오징어닭가슴살스테이크를 핸드블렌더에 넣고 간다.\n\n2. 간 재료에 통밀빵, 잘게 썬 마늘, 후추, 소금을 넣고 치대어 패티반죽을 만든다.\n\n3. 패티반죽의 모양을 잡은 뒤, 기름을 두른 팬에 굽는다.\n\n4. 볼에 군고구마, 통밀빵, 전분, 두유를 넣고 치대어 빵반죽을 만든다.\n\n5. 빵반죽의 모양을 잡은 뒤, 기름을 두른 팬에 굽는다.\n\n6. 채 썬 양배추에 키위식초를 넣는다.\n\n7. 볼에 마요네즈, 고추냉이를 넣고 섞은 뒤, 채 썬 사과와 청양고추를 넣는다.\n\n8. 7에 소금, 키위식초를 섞은 채 썬 양배추를 넣고 버무려 코울슬로를 만든다.\n\n9. 사과즙, 배즙, 오디즙, 키위식초를 섞어 건강주스를 만든다.\n\n10. 기름을 두른 팬에 통으로 썬 양파를 굽는다.\n\n11. 구운 빵을 반으로 가른 뒤, 케첩을 뿌린다.\n\n12. 케첩을 뿌린 빵 위에 패티와 코울슬로를 올린다.\n\n13. 코울슬로 위에 구운 양파, 케첩을 뿌린 빵을 얹어 햄버거를 만든다.\n\n14. 얼음잔에 건강주스를 담아 함께 낸다.'),
	(675, '어서 와~ 이런 바칼라는 처음이지?', NULL, 1, '1. 기름을 두른 팬에 고등어살과 손질가자미를 함께 굽는다.\n\n2. 소금물에 아스파라거스와 그린 빈을 넣고 데친다.\n\n3. 냄비에 다진 소고기와 소금, 뜨거운 물을 넣고 끓여 소고기육수를 만든다.\n\n4. 애호박과 양파, 파프리카를 적당한 크기로 썰어 그릴팬에 굽는다.\n\n5. 핸드블랜더에 마늘과 올리브오일을 넣고 갈아 마늘오일을 만든다.\n\n6. 데친 아스파라거스와 그린 빈을 건져내 식힌다.\n\n7. 그린 빈과 아스파라거스를 데친 물에 그라냐노면과 냉동 감자튀김을 삶는다.\n\n8. 데친 아스파라거스와 그린 빈을 적당한 크기로 썰어 채소를 굽던 그릴팬에 넣고 함께 굽는다.\n\n9. 볼에 안초비 기름을 따라내고, 안초비를 다진다.\n\n10. 볼에 안초비 기름과 다진 안초비, 레몬즙, 마늘오일, 케이퍼, 적당한 크기로 썬 모차렐라 치즈와 방울토마토를 넣고 페코리노 치즈를 갈아 넣어 안초비드레싱을 만든다.\n\n11. 핸드블랜더에 구운 고등어살과 가자미살, 생크림, 간 페코리노 치즈, 삶은 감자튀김을 넣고 갈아 바칼라를 만든다.\n\n12. 기름을 두른 팬에 잘게 썬 당근과 마늘, 양파, 다진 소고기와 불린 포르치니버섯, 소금, 후추, 올리브오일, 마늘오일, 화이트와인, 소고기육수를 넣고 볶아 라구소스를 만든다.\n\n13. 채소를 굽던 그릴팬에 치아바타를 함께 굽는다.\n\n14. 구운 채소를 안초비드레싱에 버무려 안초비샐러드를 만든다.\n\n15. 안초비샐러드와 치아바타를 접시에 담은 뒤, 구운 치아바타 위에 바칼라를 올린다.\n\n16. 15 위에 마늘오일과 후추, 루콜라, 간 페코리노 치즈를 얹는다.\n\n17. 삶은 그라냐노면을 건져 라구소스에 넣고 볶아 라구 그라냐노 파스타를 만든다.\n\n18. 라구 그라냐노 파스타에 구운 애호박과 간 페코리노 치즈를 넣고 볶는다.\n\n19. 접시에 라구 그라냐노 파스타를 담은 뒤, 간 페코리노 치즈를 뿌린다.\n\n20. 라구 그라냐노 파스타와 바칼라, 안초비샐러드에 간 파르미지아노 레지아노 치즈를 뿌려 완성한다.'),
	(676, '알파고', NULL, 1, '1. 양파를 잘게 썬다.\n\n2. 소고기 안심에 이탈리아소금과 후추, 트러플오일로 밑간한다.\n\n3. 핸드블랜더에 각종 허브, 건 포르치니버섯, 물을 넣고 간다.\n\n4. 간 재료를 뭉쳐 훈연칩을 만든 뒤, 찜기에 넣고 가열해 태운다.\n\n5. 볼에 팩 레드와인을 부은 뒤, 면포를 넣어 적신다.\n\n6. 팩 레드와인에 적신 면포로 밑간한 안심을 감싼다.\n\n7. 훈연칩을 태워 둔 찜기에 면포로 감싼 안심을 넣고 훈연한다.\n\n8. 끓는 물에 소금을 넣는다.\n\n9. 소금물에 트러플 탈리아텔레면을 넣고 삶는다.\n\n10. 삶던 면에 얼음물을 넣는다.\n\n11. 삶은 트러플 탈리아텔레면을 체에 밭쳐 물기를 뺀다.\n\n12. 냄비에 버터와 잘게 썬 양파를 넣고 볶는다.\n\n13. 양파를 볶던 팬에 물에 불린 포르치니 버섯, 화이트와인을 넣고 볶는다.\n\n14. 볶던 재료에 삶은 트러플 탈리아텔레면, 포르치니 버섯을 불린 물, 생크림, 아시아고 치즈, 간 페코리노 치즈를 넣고 볶아 크림파스타를 만든다.\n\n15. 훈연한 안심 스테이크를 적당한 크기로 썬다.\n\n16. 크림파스타를 접시에 담는다.\n\n17. 크림파스타를 담은 접시에 안심 스테이크를 올리고 아스파라거스를 올린다.\n\n18. 크림파스타와 안심 스테이크에 트러플오일을 뿌려 마무리한다.'),
	(677, '알짜배기 두부', NULL, 1, '1. 기름에 두부를 넣어 통째로 튀긴다.\n\n2. 애호박, 양파, 알배추를 적당한 크기로 썬다.\n\n3. 적당한 크기로 썬 채소를 끓는 물에 데친다.\n\n4. 대파를 적당한 크기로 썰어 기름에 튀긴 뒤, 고춧가루를 넣어 고추기름을 만든다.\n\n5. 데친 채소를 체에 밭쳐 거른 뒤, 찬물에 헹궈 식힌다.\n\n6. 식힌 채소를 얼음물에 담가 차갑게 만든다.\n\n7. 얼음물에 담갔던 데친 알배추의 물기를 짠 뒤, 접시에 담는다.\n\n8. 얼음물에 담갔던 채소를 체에 밭쳐 물기를 뺀다.\n\n9. 고추기름을 체에 밭쳐 건더기를 걸러낸다.\n\n10. 그린 빈과 대파는 잘게 썰고, 마늘은 으깬다.\n\n11. 고추기름을 두른 팬에 손질한 채소와 다진 소고기, 간장을 넣고 볶는다.\n\n12. 아스파라거스를 적당한 크기로 썬다.\n\n13. 볶던 재료에 적당한 크기로 썬 아스파라거스를 넣고 함께 볶는다.\n\n14. 파스타면을 삶는다.\n\n15. 볼에 스리라차소스, 스위트칠리소스, 굴소스, 식초, 고추기름을 넣고 섞어 비빔소스를 만든다.\n\n16. 튀긴 두부를 건져 윗부분을 편평하게 잘라낸다.\n\n17. 튀긴 두부의 속을 파낸다.\n\n18. 파낸 두부의 속을 으깬다.\n\n19. 볶던 재료에 으깬 두부와 뜨거운 물, 고추기름을 넣고 끓여 두부소스를 만든다.\n\n20. 삶은 파스타면을 건져 찬물에 헹궈 식힌다.\n\n21. 비빔소스에 손질한 아스파라거스와 데친 채소, 차가운 파스타면을 넣고 버무린다.\n\n22. 속을 파낸 튀긴 두부에 완성된 파스타를 넣는다.\n\n23. 데친 알배추가 담긴 접시 위에 두부소스를 얹는다.\n\n24. 두부소스 위에 파스타로 속을 채운 두부튀김을 올린다.\n\n25. 자숙새우를 곁들여 마무리한다.'),
	(678, '두부 맛있또', NULL, 1, '1. 두부를 으깬다.\n\n2. 으깬 두부에 소금을 넣는다.\n\n3. 핸드블랜더에 자숙새우와 완두콩을 넣고 간다.\n\n4. 으깬 두부에 간 자숙새우와 완두콩을 넣고 치대어 만두소를 만든다.\n\n5. 만두소를 동그랗게 빚은 뒤, 전분에 굴린다.\n\n6. 전분에 굴린 만두에 달걀흰자를 입힌 뒤, 다시 전분에 굴린다.\n\n7. 두부굴림만두를 찜기에 넣고 찐다.\n\n8. 파프리카, 애호박, 양파를 적당한 크기로 썬다.\n\n9. 핸드블랜더에 적당한 크기로 썬 채소를 넣고 갈다가 양송이버섯을 더해 간다.\n\n10. 들기름&참기름을 두른 팬에 간 채소와 수제된장, 뜨거운 물을 넣고 볶는다.\n\n11. 볶던 재료에 잘게 다진 루콜라를 넣고 볶아 된장소스를 만든다.\n\n12. 차갑게 만든 볼에 연두부, 설탕, 생크림을 넣고 섞는다.\n\n13. 연두부크림을 냉동실에 넣어둔다.\n\n14. 차갑게 만든 볼에 얼음을 넣는다.\n\n15. 믹서기에 적당한 크기로 썬 냉동 고구마와 두부를 넣고 간다.\n\n16. 얼음 위에 볼을 올린 뒤, 간 고구마와 두부를 넣고 치댄다.\n\n17. 16에 차갑게 만든 연두부크림을 조금씩 넣어가며 계속 치대어 두부젤라또를 만든다.\n\n18. 접시에 된장소스를 담는다.\n\n19. 된장소스 위에 적당한 크기로 썬 루콜라와 들기름&참기름, 들깻가루를 올린다.\n\n20. 판도로를 적당한 크기로 썰어 유리잔에 담는다.\n\n21. 판도로가 담긴 유리잔에 두부젤라또를 담는다.\n\n22. 두부젤라또 위에 꿀을 뿌린다.\n\n23. 두부굴림만두를 접시에 담아 마무리한다.'),
	(679, '생오취리', NULL, 1, '1. 소고기 등심 위에 비닐을 덮은 뒤, 고기망치로 두드려 얇게 편다.\n\n2. 얇게 편 등심에 후추, 올리브 오일을 뿌린다.\n\n3. 볼에 홀그레인 머스터드와 식초를 넣고 섞어 드레싱을 만든다.\n\n4. 얇게 편 등심에 드레싱을 바른다.\n\n5. 드레싱을 바른 등심을 비닐로 덮어 다시 한 번 두드린 뒤, 냉동실에 넣어둔다.\n\n6. 베이컨을 오븐에 넣고 굽는다.\n\n7. 물에 불린 귀리, 가나 땅콩가루, 파르메산 치즈가루, 소금을 섞어 크루통 반죽을 만든다.\n\n8. 기름을 두른 팬에 크루통 반죽을 구워 크루통을 만든다.\n\n9. 토마토와 상추를 적당한 크기로 썬다.\n\n10. 볼에 적당한 크기로 썬 토마토와 상추, 얇게 저민 양파, 올리브오일, 소금을 넣고 버무려 샐러드를 만든다.\n\n11. 접시에 샐러드를 담은 뒤, 얇게 저민 파프리카를 올린다.\n\n12. 냉동실에 넣어둔 등심을 4등분 한 뒤, 샐러드 위에 올린다.\n\n13. 얇게 저민 양파와 파프리카를 얹는다.\n\n14. 구운 크루통을 적당한 크기로 썰어 올린 뒤, 후추를 뿌린다.\n\n15. 볼에 파슬리, 올리브오일, 잘게 썬 마늘을 넣고 버무려 드레싱을 만든 뒤, 소고기샐러드 위에 뿌린다.\n\n16. 구운 베이컨과 얇게 저민 파프리카를 소고기샐러드 위에 올려 마무리한다.'),
	(680, '맛있는 소고기렌당', NULL, 1, '1. 강판에 양파를 간다.\n\n2. 가나 절구에 고춧가루, 다진 생강, 간 양파를 넣고 찧듯이 섞는다.\n\n3. 소고기 등심을 적당한 크기로 썬다.\n\n4. 적당한 크기로 썬 등심에 소금을 뿌린다.\n\n5. 기름을 두른 냄비에 찧은 재료와 다진 마늘, 강황가루, 계피가루를 넣고 볶는다.\n\n6. 볶던 재료에 간 한 등심을 넣고 볶는다.\n\n7. 볶던 재료에 청양고추, 코코넛밀크를 넣고 끓인다.\n\n8. 끓이던 재료에 계피가루와 강황가루, 고춧가루를 넣는다.\n\n9. 끓이던 재료에 레몬껍질을 넣고 졸여 렌당을 만든다.\n\n10. 브로콜리를 적당한 크기로 썬다.\n\n11. 끓는 소금물에 브로콜리를 넣고 데친다.\n\n12. 데친 브로콜리를 건져 올리브오일을 넣고 버무린 뒤, 그릴팬에 굽는다.\n\n13. 냄비에 올리브오일과 다진 마늘을 넣고 끓여 마늘오일을 만든다.\n\n14. 홍고추와 청양고추를 잘게 썬다.\n\n15. 마늘오일에 잘게 썬 고추와 가나 새우가루, 시토를 넣고 섞어 소스를 만든다.\n\n16. 구운 브로콜리에 소스를 넣고 버무려 접시에 담아낸 뒤, 레몬즙을 뿌린다.\n\n17. 렌당에 코코넛밀크를 더해가며 졸인다.\n\n18. 즉석밥을 접시에 담아낸다.\n\n19. 렌당을 미니냄비에 담은 뒤, 미니화로 위에 올려 낸다.'),
	(681, '가나 가즈아~', NULL, 1, '1. 닭다리에 칼집을 넣는다.\n\n2. 칼집을 넣은 닭다리를 기름에 튀긴다.\n\n3. 핸드블랜더에 통마늘과 양파, 다진 생강, 가나 새우가루, 레드팜 오일을 넣는다.\n\n4. 3에 토마토 페이스트를 넣고 간다.\n\n5. 냄비에 간 재료와 물을 넣고 끓여 수프를 만든다.\n\n6. 튀긴 닭다리를 물에 삶는다.\n\n7. 7에 치킨스톡을 넣는다.\n\n8. 달걀흰자와 전분을 섞어 튀김반죽을 만든 뒤, 게맛살을 넣고 버무린다.\n\n9. 튀김반죽에 버무린 게맛살을 기름에 튀긴다.\n\n10. 게맛살튀김을 건져내 기름기를 뺀다.\n\n11. 냄비에 푸푸가루와 물을 넣은 뒤, 끓이면서 저어 푸푸 반죽을 만든다.\n\n12. 수프에 닭육수와 닭다리를 넣고 끓인다.\n\n13. 13에 소금과 게맛살튀김, 후추를 넣는다.\n\n14. 가나 절구에 뜨거운 물을 부은 뒤, 푸푸 반죽을 넣는다.\n\n15. 푸푸 반죽을 찧어 푸푸를 만든다.\n\n16. 수프에 코코넛밀크를 넣고 끓인다.\n\n17. 접시에 푸푸를 담고, 수프를 부어 마무리한다.'),
	(682, '나이스! 라이스~', NULL, 1, '1. 냄비에 인스턴트 사골곰탕, 불린 쌀을 넣고 끓인다.\n\n2. 쌀을 끓이던 냄비에 잘게 썬 양파와 토마토 페이스트를 넣는다.\n\n3. 다른 냄비에 적당한 크기로 썬 양파를 넣은 뒤, 버터스프레이를 뿌린다.\n\n4. 볶던 양파에 레드팜 오일을 넣고 볶는다.\n\n5. 파프리카를 적당한 크기로 썰어 쌀을 끓이던 냄비에 넣는다.\n\n6. 핸드블랜더에 토마토를 넣고 간다.\n\n7. 다른 핸드블랜더에 레드팜 오일에 볶은 양파와 청양고추, 통마늘, 다진 생강을 넣는다.\n\n8. 끓이던 쌀에 다진 생강을 넣는다.\n\n9. 핸드블랜더에 넣은 채소를 간다.\n\n10. 기름을 두른 팬에 적당한 크기로 썬 소고기 등심과 스테이크시즈닝을 넣고 볶는다.\n\n11. 볶던 등심에 간 토마토와 간 채소를 넣고 볶는다.\n\n12. 등심볶음과 끓이던 쌀에 강황가루와 가나 새우가루를 넣는다.\n\n13. 끓이던 쌀에 등심볶음을 넣고 졸여 졸로프 라이스를 만든다.\n\n14. 남은 등심볶음에 인스턴트 사골곰탕과 팜너트 통조림을 넣고 끓여 팜너트 스튜를 만든다.\n\n15. 그릇에 팜너트 스튜를 담는다.\n\n16. 종지에 시토를 담는다.\n\n17. 그릇에 졸로프 라이스를 담고 팜너트 스튜와 시토를 함께 낸다.'),
	(683, '죽빙', NULL, 1, '1. 끓는 물에 잘게 부순 누룽지를 넣고 끓인다.\n\n2. 적당한 크기로 썬 오징어의 뼈를 제거하고 핸드블랜더에 넣어 간다.\n\n3. 마른 팬에 잘게 썬 애호박과 간 오징어를 넣고 볶는다.\n\n4. 볶던 재료에 편마늘과 맛술을 넣는다.\n\n5. 쑥떡을 적당한 크기로 썬다.\n\n6. 핸드블랜더에 쑥떡을 넣고 간 뒤, 끓이던 누룽지에 넣는다.\n\n\n7. 6에 볶은 오징어와 애호박을 넣고 함께 끓인다.\n\n8. 끓이던 재료에 뜨거운 물을 더한 뒤, 핸드블랜더로 갈아 죽을 만든다.\n\n9. 죽에 소금으로 간 한다.\n\n10. 핸드블랜더에 멍게젓과 청양고추를 넣고 간다.\n\n11. 간 멍게젓에 깨와 참기름을 넣고 섞어 양념장을 만든다.\n\n12. 종지에 양념장을 담고 잘게 썬 대파를 올린다.\n\n13. 핸드블랜더에 청포도를 넣고 간다.\n\n14. 간 청포도에 꿀을 넣고 다시 한 번 간다.\n\n15. 간 청포도를 체에 밭쳐 건더기와 즙을 걸러낸다.\n\n16. 수수부꾸미에서 팥만 골라낸다.\n\n17. 팥에 꿀을 넣고 버무린다.\n\n18. 믹서기에 얼음을 넣고 간다.\n\n19. 청포도즙에 레몬즙을 넣은 뒤, 종지에 담아낸다.\n\n20. 그릇에 간 청포도>간 얼음>팥>간 얼음>간 청포도>팥을 순서대로 담아 팥빙수를 만든다.\n\n21. 팥빙수에 잘게 썬 딸기를 얹는다.\n\n22. 팥빙수에 블루베리를 올리고 잘게 썬 레몬껍질을 뿌린다.\n\n23. 그릇에 죽을 담고 잘게 썬 레몬껍질을 뿌린다.\n\n24. 죽 위에 잘게 썬 대파를 올려 마무리한다.'),
	(684, '수비드 레이서', NULL, 1, '1. 연어를 적당한 크기로 썬다.\n\n2. 비닐백에 올리브오일을 붓고, 소금으로 간 한 연어를 넣는다.\n\n3. 볼에 뜨거운 물과 찬물을 섞어 미지근한 물(약 47℃)을 만든 뒤, 연어를 넣은 비닐팩을 담가 수비드한다.\n\n4. 애호박을 슬라이스한 뒤, 소금을 뿌리고 올리브오일에 버무린다.\n\n5. 그릴팬에 애호박을 굽는다.\n\n6. 갈치의 살을 발라낸다.\n\n7. 기름을 두른 팬에 잘게 썬 대파를 볶는다.\n\n8. 대파를 볶던 팬에 적당한 크기로 썬 갈치살을 넣는다.\n\n9. 볶던 갈치살에 달걀과 소금을 넣는다.\n\n10. 갈치살과 애호박에 후추를 뿌린다.\n\n11. 볶은 갈치살에 불린 누룽지를 넣고 볶아 갈치볶음밥을 만든다.\n\n12. 아보카도를 손질한다.\n\n13. 손질한 아보카도를 얇게 썬다.\n\n14. 구운 애호박 위에 얇게 썬 아보카도를 올린다.\n\n15. 수비드한 연어를 체에 밭쳐 기름을 뺀다.\n\n16. 구운 애호박과 얇게 썬 아보카도 위에 수비드한 연어를 올린다.\n\n17. 구운 애호박>얇게 썬 아보카도>수비드한 연어를 차례로 계속해서 쌓아 레이어드한다.\n\n18. 레이어드한 연어와 채소의 가장자리를 잘라낸 뒤, 접시에 담는다.\n\n19. 양파를 채 썬다.\n\n20. 기름을 두른 팬에 채 썬 양파와 통후추, 레몬즙을 넣고 볶는다.\n\n21. 볶던 재료에 레드와인과 화이트와인을 넣고 졸여 와인소스를 만든다.\n\n22. 와인소스를 체에 밭쳐 건더기를 걸러낸다.\n\n23. 와인소스에 레몬즙, 올리브오일, 모과청, 연겨자, 손질한 청포도와 딸기를 넣고 섞는다.\n\n24. 레이어드한 연어&채소 위에 과일소스를 뿌린다.\n\n25. 그릇에 갈치볶음밥을 담아낸다.'),
	(685, '베리베리 맛있게', NULL, 1, '1. 꽃게를 손질해 적당한 크기로 자른다.\n\n2. 달군 찜기에 화이트와인을 부어 끓인다.\n\n3. 끓이는 화이트와인에 귤차 티백과 페퍼민트차 티백을 넣는다.\n\n4. 찜기에 손질한 꽃게와 오징어를 넣고 찐다.\n\n5. 물과 식용유, 밀가루, 잘게 부순 누룽지를 섞어 누룽지튀일반죽을 만든다.\n\n6. 누룽지튀일반죽을 팬에 굽는다.\n\n7. 믹서기에 꼭지를 제거한 딸기와 블루베리를 넣는다.\n\n8. 딸기와 블루베리를 넣은 믹서기에 물과 화이트 와인, 모과청, 얼음을 넣고 갈아 베리베리주스를 만든다.\n\n9. 양파를 잘게 썬다.\n\n10. 핸드블랜더에 손질한 아보카도와 다진 양파, 레몬즙, 마요네즈, 소금을 넣고 갈아 아보카도무스를 만든다.\n\n11. 찐 꽃게를 밀대로 밀어 게살을 발라내고, 찐 오징어는 적당한 크기로 썬다.\n\n12. 게살과 찐 오징어를 잘게 다진다.\n\n13. 볼에 잘게 다진 게살&오징어, 다진 양파, 소금, 연겨자, 레몬즙을 넣는다.\n\n14. 13에 올리브오일과 후추를 넣고 섞어 게살 타르타르를 만든다.\n\n15. 접시에 원형틀을 올린 뒤, 게살 타르타르와 아보카도무스를 담는다.\n\n16. 원형틀을 제거한 뒤, 누룽지튀일을 곁들인다.\n\n17. 베리베리주스를 잔에 따른다.\n\n18. 체리와 청포도를 곁들여 마무리한다.'),
	(686, '골든 크랩', NULL, 1, '1. 양파를 채 썬다.\n\n2. 냄비에 채 썬 양파와 모과청, 간장, 물을 넣고 끓인다.\n\n3. 꽃게의 내장을 발라낸다.\n\n4. 꽃게를 밀대로 밀어 게살을 발라낸다.\n\n5. 오징어를 적당한 크기로 썬다.\n\n6. 핸드블랜더에 누룽지를 넣고 간다.\n\n7. 간 누룽지를 체에 밭쳐 곱게 거른다.\n\n8. 핸드블랜더에 고운 누룽지가루와 게살, 오징어, 통조림 참치를 넣고 갈아 게살반죽을 만든다.\n\n9. 살을 발라낸 게 다리에 전분을 묻힌 뒤, 게살반죽을 뭉쳐 붙인다.\n\n10. 게살반죽 위에 누룽지가루를 입히고 기름에 튀긴다.\n\n11. 꽃게 등딱지를 물에 씻어낸 뒤, 안쪽에 전분을 묻힌다.\n\n12. 남은 게살반죽에 게살을 더하고, 게내장과 즉석밥을 넣는다.\n\n13. 멍게젓을 잘게 다진다.\n\n14. 게내장밥에 잘게 다진 멍게젓을 넣고 섞는다.\n\n15. 게 등딱지에 게내장밥을 담은 뒤, 전분->달걀->전분을 순서대로 묻혀 찜기에 넣고 찐다.\n\n16. 감말랭이를 잘게 썬다.\n\n17. 양파와 모과청을 넣고 끓인 간장소스를 체에 밭쳐 건더기는 걸러낸다.\n\n18. 팬에 간장소스와 잘게 썬 감말랭이, 물, 레몬즙, 설탕을 넣고 졸여 감소스를 만든다.\n\n19. 게튀김을 건져 기름기를 뺀다.\n\n20. 양상추와 얇게 저민 단감, 식초, 잘게 썬 체리, 페퍼민트차 가루, 레몬즙을 섞어 단감샐러드를 만든다.\n\n21. 게 등딱지에 담아서 찐 게내장밥과 게튀김, 감소스를 접시에 담고 단감샐러드를 얹어 마무리한다.'),
	(687, '샘표 한상', NULL, 1, '1. 핸드블랜더에 올리브오일과 통마늘을 넣고 갈아 마늘오일을 만든다.\n\n2. 기름을 두른 냄비에 통마늘과 국거리용 소고기를 넣고 볶는다.\n\n3. 볶던 소고기에 적당한 크기로 썬 무와 소금을 넣는다.\n\n4. 3에 뜨거운 물을 붓고 끓여 소고기육수를 낸다.\n\n5. 양파와 당근을 채 썬 뒤, 기름을 두른 팬에 볶다가 소금으로 간 한다.\n\n6. 볶던 채소에 적당한 크기로 썬 어묵을 넣고 함께 볶는다.\n\n7. 볶던 재료에 적당한 크기로 썬 청양고추와 마늘오일을 넣는다.\n\n8. 볶던 재료에 고춧가루와 간장, 설탕, 참기름을 더해 어묵볶음을 만든다.\n\n9. 볼에 간장과 설탕, 삼겹살, 고춧가루, 고추장, 매실액, 마늘오일을 넣고 버무려 고추장불고기를 만든다.\n\n10. 고추장불고기에 후추, 참기름, 다진 생강, 물엿을 넣고 버무린다.\n\n11. 청양고추와 대파를 썰어 고추장불고기에 넣고 버무린다.\n\n12. 볼에 고추장과 참기름, 적당한 크기로 썬 청양고추, 멸치를 넣고 버무려 멸치고추장무침을 만든다.\n\n13. 청양고추와 홍고추를 적당한 크기로 썰어 고추장불고기에 넣는다.\n\n14. 가지를 길게 썬 뒤, 밀가루를 묻혀 기름에 튀긴다.\n\n15. 팬에 마늘오일과 간 우둔살, 소금, 간장, 맛술을 넣고 볶은 뒤, 참기름을 더해 양념을 만든다.\n\n16. 튀긴 가지와 양념, 잘게 썬 대파와 참기름을 버무려 가지무침을 만든다.\n\n17. 그릴팬에 고추장불고기를 굽는다.\n\n18. 팬에 마늘오일과 참기름, 국거리용 소고기, 소금을 넣고 볶다가 소고기육수를 넣고 끓인다.\n\n19. 끓이던 재료를 체에 밭쳐 건더기를 걸러낸 뒤, 매생이를 넣고 끓여 매생이국을 만든다.\n\n20. 매생이국에 소금과 간장으로 간 한다.\n\n21. 김을 구운 뒤 적당한 크기로 찢는다.\n\n22. 즉석밥을 전자레인지에 돌린다.\n\n23. 구운 김과 멸치고추장무침, 어묵볶음을 각각 접시에 담는다.\n\n24. 매생이국을 그릇에 담는다.\n\n25. 가지무침과 고추장불고기, 밥을 각각 접시에 담는다.\n\n26. 밥과 고추장불고기, 매생이국, 멸치고추장무침, 어묵볶음, 가지무침을 함께 낸다.'),
	(688, '맘스텔라', NULL, 1, '1. 가지를 길게 자른 뒤, 찜기에 넣고 찐다.\n\n2. 무를 적당한 크기로 썰어 냄비에 깐 뒤, 조기를 올린다.\n\n3. 양파와 청양고추를 적당한 크기로 썰어 조기 위에 올린다.\n\n4. 대파를 적당한 크기로 썬다.\n\n5. 해물다시팩에 뜨거운 물을 부어 해물육수를 낸다.\n\n6. 해물육수에 고춧가루, 간장, 다진 마늘, 청주, 소금을 넣어 양념을 만든다.\n\n7. 조기에 양념을 붓고 조린다.\n\n8. 버터를 전자레인지에 돌려 녹인다.\n\n9. 볼에 설탕과 달걀흰자를 넣고 섞어 머랭을 만든다.\n\n10. 머랭에 달걀노른자와 밀가루를 넣고 섞어 카스텔라 반죽을 만든다.\n\n11. 카스텔라 반죽에 우유와 녹인 버터를 넣는다.\n\n12. 달군 팬에 녹인 버터를 넣고 한번 닦아낸 뒤, 카스텔라 반죽을 붓고 뚜껑을 덮어 익힌다.\n\n13. 카스텔라를 돌돌 말아 김발로 고정한다.\n\n14. 조기매운탕에 참치액을 넣는다.\n\n15. 찐 가지를 꺼내 물기를 짠다.\n\n16. 볼에 찐 가지, 적당한 크기로 썬 대파와 홍고추, 깨를 넣는다.\n\n17. 16에 참치액과 참기름, 다진 마늘, 소금을 넣고 무쳐 가지무침을 만든다.\n\n18. 접시에 가지무침을 담는다.\n\n19. 전자레인지에 넣고 돌린 즉석밥을 그릇에 담는다.\n\n20. 카스텔라를 적당한 크기로 썰어 접시에 담는다.\n\n21. 조기매운탕에 잘게 썬 대파를 올린다.\n\n22. 밥과 가지무침, 카스텔라, 조기매운탕을 함께 낸다.'),
	(689, '허니버터치', NULL, 1, '1. 멸치의 대가리와 내장을 제거한다.\n\n2. 끓는 물에 다시마와 무, 국물용 멸치를 넣고 우린다.\n\n3. 버터를 녹이던 팬에 잘게 다진 마늘과 꿀, 화이트와인을 넣어 허니버터를 만든다.\n\n4. 끓는 물에 표고버섯 불린 물과 참치액을 넣은 뒤, 파스타면을 넣고 삶는다.\n\n5. 삶던 파스타면에 소금을 넣는다.\n\n6. 손질한 멸치를 체에 밭친 뒤, 멸치육수에 담가 익힌다.\n\n7. 물에 불린 표고버섯을 적당한 크기로 썬다.\n\n8. 느타리버섯은 적당한 크기로 찢고, 마늘은 잘게 다진다.\n\n9. 팬에 들기름을 두르고, 다진 마늘과 간장, 손질한 버섯을 넣고 볶는다.\n\n10. 볶던 재료에 삶은 파스타면과 멸치육수를 넣고 볶아 멸치파스타를 만든다.\n\n11. 멸치파스타에 잘게 썬 청양고추를 넣는다.\n\n12. 멸치육수에 담가 익힌 멸치를 허니버터에 넣고 버무려 허니버터멸치를 만든다.\n\n13. 접시에 어린잎채소를 담고, 허니버터멸치를 올린다.\n\n14. 멸치파스타에 후추와 참기름을 넣은 뒤, 접시에 담아 마무리한다.'),
	(690, '차이나는 비빔면', NULL, 1, '1. 새송이버섯을 적당한 크기로 썬다.\n\n2. 적당한 크기로 자른 새송이버섯에 파스타면을 꽂는다.\n\n3. 양배추물을 끓이다가 소금을 넣고, 새송이버섯에 꽂은 파스타면을 삶는다.\n\n4. 팬에 기름을 넉넉하게 두른 뒤, 길게 자른 대파를 넣고 튀겨 파기름을 낸다.\n\n5. 파기름에 다진 생강과 새우젓을 넣는다.\n\n6. 통조림옥수수에 전분을 묻힌다.\n\n7. 전분을 묻힌 옥수수알을 기름에 튀긴 뒤, 건져내 기름기를 뺀다.\n\n8. 잔멸치를 기름에 튀긴 뒤, 건져내 기름기를 뺀다.\n\n9. 어묵을 채 썬다.\n\n10. 채 썬 어묵에 전분을 묻힌 뒤, 기름에 튀긴다.\n\n11. 어묵튀김을 건져내 얼음물에 넣고 식힌다.\n\n12. 마늘장아찌를 적당한 크기로 썬다.\n\n13. 파기름에 면수를 넣는다.\n\n14. 끓이던 파기름에 잘게 썬 청양고추를 넣는다.\n\n15. 삶은 파스타면을 건져낸 뒤, 얼음물에 담가 식힌다.\n\n16. 파기름을 체에 밭쳐 건더기를 걸러낸다.\n\n17. 파스타면에 파기름을 넣고 버무린 뒤, 후추를 넣어 파기름비빔면을 만든다.\n\n18. 양상추를 채 썬다.\n\n19. 접시에 채 썬 양상추를 담고, 파기름비빔면을 올린다.\n\n20. 파기름비빔면 위에 적당한 크기로 썬 마늘장아찌와 파기름을 뿌린다.\n\n21. 20 위에 설탕과 후추를 뿌린다.\n\n22. 옥수수알튀김에 소금을 넣고 섞는다.\n\n23. 접시에 옥수수알튀김을 담아낸다.'),
	(691, '참치 언더파~', NULL, 1, '1. 마늘을 잘게 다진다.\n\n2. 소갈비살을 적당한 크기로 썬다.\n\n3. 기름을 두른 팬에 잘게 다진 마늘과 소갈비살을 넣고 볶는다.\n\n4. 볶던 소갈비에 잘게 썬 살라미를 넣고 볶는다.\n\n5. 볶던 재료에 즉석밥을 넣고 볶아 볶음밥을 만든다..\n\n6. 볶음밥에 인스턴트 사골곰탕 국물을 넣는다.\n\n7. 핸드블랜더에 마늘과 올리브오일을 넣고 갈아 마늘오일을 만든다.\n\n8. 참치를 적당한 크기로 썬 뒤, 마늘오일에 버무린다.\n\n9. 쪽파의 적당한 크기로 썬 뒤, 기름을 두른 그릴팬에 굽는다.\n\n10. 굽던 쪽파에 바비큐소스를 바른다.\n\n11. 쪽파를 굽는 그릴팬에 양파를 함께 굽는다.\n\n12. 볶음밥에 파르메산치즈를 갈아 넣는다.\n\n13. 볶음밥에 리코타치즈를 넣고 섞는다.\n\n14. 볶음밥에 후추와 모차렐라치즈를 넣고 식힌다.\n\n15. 핸드블렌더에 통밀빵을 갈아 빵가루를 만든다.\n\n16. 볶음밥을 동그랗게 뭉친 뒤, 전분>달걀물>빵가루를 순서대로 입히고 기름에 튀겨 아란치니를 만든다.\n\n17. 플레인 요거트와 홀스래디시, 소금, 후추, 레몬즙을 섞은 뒤, 라임껍질을 갈아 넣어 요거트소스를 만든다.\n\n18. 기름을 두른 팬에 마늘오일에 버무린 참치를 굽는다.\n\n19. 석쇠에 베트남고추를 굽다가, 구운 참치를 올려 고추향을 입힌다.\n\n20. 구운 쪽파를 접시에 담는다.\n\n21. 아보카도를 적당한 크기로 썰어 쪽파 위에 올린다.\n\n22. 볼에 바비큐소스와 발사믹식초, 레몬즙, 올리브오일을 넣고 섞어 소스를 만든다.\n\n23. 참치타다키를 적당한 크기로 썰어 아보카도 위에 올린다.\n\n24. 참치타다키 위에 소스를 뿌리고 구운 양파를 곁들인다.\n\n25. 그릇에 요거트소스를 담은 뒤, 아란치니를 올린다.\n\n26. 아란치니 위에 라임껍질과 파르메산치즈를 갈아 올린다.\n\n27. 참치타다키 위에 잘게 썬 애플민트와 레몬즙을 뿌려 마무리한다.'),
	(692, '스피드 스퀴드', NULL, 1, '1. 통밀빵을 큐브모양으로 썬다.\n\n2. 큐브모양으로 썬 통밀빵을 오븐에 구워 크루통을 만든다.\n\n3. 오징어는 다리를 잘라낸다.\n\n4. 오징어의 몸통을 잘게 썬다.\n\n5. 팬에 으깬 마늘과 올리브오일을 넣고 굽는다.\n\n6. 쪽파 흰 부분을 잘게 썬다.\n\n7. 마늘을 굽던 팬에 잘게 썬 쪽파 흰 부분과 오징어를 넣고 볶는다.\n\n8. 볶던 재료에 소금과 후추로 간 한다.\n\n9. 볼에 리코타치즈와 볶은 오징어, 잘게 썬 애플민트를 넣고 레몬껍질과 파르메산치즈를 갈아 넣어 소를 만든다.\n\n10. 소에 크루통을 넣는다.\n\n11. 통오징어의 몸통 안에 소를 채워 넣는다.\n\n12. 소을 채운 오징어를 나무꼬치로 고정한 뒤, 올리브오일과 후추를 뿌린다.\n\n13. 마른 그릴팬에 소을 채운 오징어를 굽는다.\n\n14. 굽던 오징어를 오븐에 넣고 굽는다.\n\n15. 적양파를 적당한 크기로 썬다.\n\n16. 끓는 물에 소금을 넣고, 적양파를 넣어 데친다.\n\n17. 16에 레드와인과 식초, 설탕을 넣고 끓인다.\n\n18. 핸드블랜더에 손질한 아보카도와 잘게 썬 쪽파, 라임즙, 후추를 넣는다.\n\n19. 구운 오징어를 그릴팬에 옮겨 담은 뒤, 올리브오일을 뿌리고 뚜껑을 덮어 익힌다.\n\n20. 재료를 넣은 핸드블랜더에 올리브오일을 넣고 갈아 아보카도소스를 만든다.\n\n21. 아보카도소스를 체에 곱게 걸러낸다.\n\n22. 아보카도소스를 짤주머니에 넣고, 접시에 플레이팅 한다.\n\n23. 데친 적양파를 건져낸 뒤, 소금과 올리브오일에 버무린다.\n\n24. 아보카도소스 위에 적양파를 올린다.\n\n25. 아보카도를 적당한 크기로 썬 뒤, 레몬즙과 후추를 뿌리고 접시에 플레이팅한다.\n\n26. 애플민트를 곁들인다.\n\n27. 구운 오징어의 나무꼬치를 제거한다.\n\n28. 접시에 구운 오징어를 담아 마무리한다.'),
	(693, '월드클래스 삼합', NULL, 1, '1. 소갈비는 뼈를 발라내고 반으로 접어 두툼하게 만든 뒤, 두드려 넓게 편다.\n\n2. 반으로 접은 소갈비를 조리용 실로 묶어 고정한다.\n\n3. 소갈비에 블랙트러플소금으로 간 한다.\n\n4. 대파를 적당한 크기로 썬 뒤, 석쇠에 올린다.\n\n5. 석쇠에 올린 대파를 직화한다.\n\n6. 통밀빵을 슬라이스 한 뒤, 마른 팬에 굽는다.\n\n7. 핸드블랜더에 구운 통밀빵과 선드라이드 토마토, 파르메산 치즈, 씻은 김치, 스리라차소스를 넣고 갈아 로메스코소스를 만든다.\n\n8. 애플민트를 적당한 크기로 썬 뒤, 고량주에 재운다.\n\n9. 마른 팬에 통조림 옥수수를 굽는다.\n\n10. 적양파를 잘게 다진다.\n\n11. 핸드블랜더에 손질한 아보카도와 다진 적양파를 넣고 간다.\n\n12. 간 아보카도에 구운 옥수수와 레몬즙, 소금을 넣어 과카몰레를 만든다.\n\n13. 볼에 크림치즈와 블랙트러플오일, 블랙트러플페이스트, 잘게 썬 블랙트러플, 블랙트러플소금을 넣고 섞어 트러플치즈소스를 만든다.\n\n14. 트러플크림소스에 레몬즙을 더한다.\n\n15. 볼에 크림치즈와 고추냉이크림, 소금을 넣고 섞어 고추냉이치즈소스를 만든다.\n\n16. 뜨거운 물에 레몬즙과 레몬, 소금을 넣고 끓인 뒤, 관자를 데친다.\n\n17. 데친 관자를 건져 식힌다.\n\n18. 기름을 두른 팬에 소갈비를 굽다가 버터를 넣는다.\n\n19. 구운 통밀빵 위에 트러플치즈소스와 고추냉이치즈소스를 올린다.\n\n20. 접시에 소스를 올린 통밀빵과 과카몰레, 로메스코소스를 담는다.\n\n21. 대파 칼솟타다의 껍질을 벗겨낸 뒤, 로메스코소스 위에 올린다.\n\n22. 구운 소갈비스테이크의 실을 풀고 반으로 자른다.\n\n23. 트러플치즈소스와 고추냉이치즈소스 위에 각각 소갈비스테이크를 올린다.\n\n24. 구운 소갈비를 반으로 자른 뒤, 트러플크림치즈소스와 고추냉이크림치즈소스 위에 올린다.\n\n25. 과카몰레 위에 데친 관자를 올린다.\n\n26. 접시에 고량주에 재운 애플민트를 담은 뒤, 토치로 불을 붙여 낸다.'),
	(694, '세리볼 나이스샷', NULL, 1, '1. 뜨거운 물에 맛술과 다시마, 소금, 설탕, 식용유를 넣고 끓인다.\n\n2. 끓이던 재료에 참기름을 넣는다.\n\n3. 양파는 채 썰고, 무는 얇게 썬 뒤 끓이던 재료에 넣고 끓인다.\n\n4. 대파를 길게 썰어 마른 그릴팬에 굽는다.\n\n5. 쪽파는 뿌리를 잘라내고 그릴팬에 함께 굽는다.\n\n6. 볼에 잘게 다진 선드라이드 토마토와 통조림 게살을 넣는다.\n\n7. 구운 대파와 쪽파를 잘게 썰어 볼에 함께 담고, 소금을 더해 섞는다.\n\n8. 7에 참기름과 김칫국물을 넣어 소를 만든다.\n\n9. 끓이던 재료를 체에 밭쳐 건더기만 걸러내고, 양파와 무만 꺼내 잘게 다진다.\n\n10. 소에 잘게 다진 양파와 무를 섞는다.\n\n11. 아보카도를 손질한 뒤, 기름을 두른 그릴팬에 굽는다.\n\n12. 구운 아보카도를 적당한 크기로 썰어 소에 넣고 섞는다.\n\n13. 소에 레몬즙을 더한다.\n\n14. 안창살에 소금과 후추로 간한 뒤, 기름을 두른 그릴팬에 굽는다.\n\n15. 배추김치의 잎만 잘라낸다.\n\n16. 랩 위에 김치 잎을 넓게 편다.\n\n17. 넓게 편 김치 위에 소를 올린 뒤, 동그랗게 모양을 잡아 김치볼을 만든다.\n\n18. 김치볼의 랩을 벗겨내고 그릇에 담는다.\n\n19. 굽던 안창살에 김칫국물을 넣는다.\n\n20. 김칫국물을 넣고 구운 안창살을 김치볼과 함께 담는다.\n\n21. 김칫국물과 참기름을 뿌려 마무리한다.'),
	(695, '연복풍 완자', NULL, 1, '1. 끓는 물에 해물다시팩과 적당한 크기로 썬 무, 고추를 넣고 끓여 해물육수를 낸다.\n\n2. 그린 빈을 손질한 뒤, 기름에 튀기듯 볶는다.\n\n3. 양파와 브로콜리를 적당한 크기로 썬다.\n\n4. 양송이버섯과 새송이버섯을 적당한 크기로 썬다.\n\n5. 가지와 소고기 안심을 적당한 크기로 썬 뒤, 기름에 튀긴다.\n\n6. 튀긴 가지와 안심을 건져내 기름기를 뺀다.\n\n7. 믹서기에 돼지고기 어깨살과 간 닭고기, 닭허벅지살, 대구맛후리카케, 통마늘, 다진 생강을 넣고 간다.\n\n8. 간 고기에 전분을 넣고 치댄다.\n\n9. 8에 후추와 대구맛후리카케, 달걀흰자를 넣고 치대 완자반죽을 만든다.\n\n10. 완자반죽을 동그란 모양으로 커다랗게 뭉친 뒤, 기름에 튀긴다.\n\n11. 익힌 그린 빈을 체에 밭쳐 기름기를 뺀다.\n\n12. 대파는 잘게 썰고 마늘은 으깬다.\n\n13. 기름을 두른 팬에 라유와 잘게 썬 대파, 으깬 마늘, 익힌 그린 빈, 야키니쿠소스, 해물육수를 넣고 볶는다.\n\n14. 기름을 두른 팬에 말린 새우를 넣고 볶다가 미소된장, 적당한 크기로 썬 채소, 야키니쿠소스, 명란젓, 튀긴 안심과 가지, 해물육수를 넣고 볶아 완자소를 만든다.\n\n15. 채 썬 양파를 볶던 그린 빈과 함께 볶는다.\n\n16. 대파와 마늘, 돼지고기 어깨살, 무, 표고버섯을 적당한 크기로 썬다.\n\n17. 기름을 두른 팬에 돼지고기 어깨살과 대파, 고춧가루, 간장을 넣고 볶는다.\n\n18. 볶던 재료에 손질한 표고버섯과 무를 넣은 뒤, 해물육수를 더해 끓인다.\n\n\n19. 끓이던 재료에 가리비다시마소스를 넣는다.\n\n20. 끓는 물에 우동면을 넣고 삶는다.\n\n21. 끓이던 재료에 대게다리살과 시금치를 넣는다.\n\n22. 튀긴 완자를 건져 기름기를 뺀다.\n\n23. 완자의 윗부분을 잘라낸 뒤, 속을 파낸다.\n\n24. 속을 파낸 완자를 기름에 다시 한 번 튀긴다.\n\n25. 튀긴 완자의 속에 완자소를 채운다.\n\n26. 접시에 완자를 올린 뒤, 그린 빈 볶음을 담는다.\n\n27. 그릇에 우동탕을 담아 마무리한다.'),
	(696, '추바라시', NULL, 1, '1. 오븐에 식빵을 넣고 굽는다.\n\n2. 끓는 물에 해물다시팩을 넣고 끓여 해물육수를 낸다.\n\n3. 마늘과 양송이버섯은 편 썰고, 브로콜리는 적당한 크기로 썬다.\n\n4. 끓는 올리브오일에 편마늘과 양송이버섯, 브로콜리, 오렌지껍질을 넣고 끓인다.\n\n5. 소고기 안심에 소금과 후추를 뿌린 뒤, 끓이던 올리브오일에 넣고 익힌다.\n\n6. 달걀흰자와 노른자를 분리한다.\n\n7. 달걀노른자와 우유를 섞은 뒤, 체에 곱게 걸러내 달걀찜반죽을 만든다.\n\n8. 잘게 썬 블루치즈와 구운 식빵을 그릇에 담는다.\n\n9. 달걀찜반죽을 그릇에 담은 뒤, 파르미지아노 치즈를 갈아 올린다.\n\n10. 찜기에 달걀찜반죽을 넣고 찐다.\n\n11. 양파를 채 썬다.\n\n12. 볼에 달걀과 달걀껍데기, 간 닭고기, 채 썬 양파를 넣는다.\n\n13. 11에 잘게 썬 당근과 방울토마토, 어니언프라이를 넣고 손으로 으깨듯 섞는다.\n\n14. 끓이던 해물육수에서 해물다시팩을 건져낸 뒤, 섞은 재료를 넣고 끓인다.\n\n15. 올리브오일에 익힌 안심을 건져 마른 팬에 굽는다.\n\n16. 멘쯔유와 우동 쯔유, 마늘간장소스를 섞어 쯔유소스를 만든다.\n\n17. 굽던 안심에 쯔유소스를 부어 졸인 뒤, 쯔유소스는 해물육수에 넣고 안심은 식힌다.\n\n18. 올리브오일에 익힌 재료들을 건져내 잘게 썬다.\n\n19. 볼에 달걀흰자를 넣고 치대어 머랭을 만든다.\n\n20. 끓이던 올리브오일을 체에 곱게 걸러 잔에 담고, 머랭을 올린다.\n\n21. 해물육수에 끓이던 재료를 체에 걸러 국물만 팬에 담는다.\n\n22. 21에 손질한 아보카도와 올리브오일에 익힌 브로콜리, 양송이버섯을 넣어 콩소메수프를 만든다.\n\n23. 콩소메수프에 전분물을 넣어 농도를 조절한다.\n\n24. 달걀찜 위에 콩소메수프를 붓는다.\n\n25. 적당한 크기로 썬 안심을 콩소메수프 위에 얹는다.\n\n26. 콩소메수프 위에 올리브오일에 익힌 오렌지껍질을 뿌린다.\n\n27. 올리브오일과 머랭이 담긴 소스에 금가루를 뿌린다.'),
	(697, '추추테일러', NULL, 1, '1. 마늘을 으깬다.\n\n2. 양파와 대파, 당근을 적당한 크기로 썬다.\n\n3. 냄비에 으깬 마늘과 적당한 크기로 썬 채소, 밀가루, 뜨거운 물, 스키야키소스, 고기소스, 인삼, 대추를 넣고 끓여 꼬리곰탕소스를 만든다.\n\n4. 무를 적당한 크기로 썰어 끓이던 재료에 넣는다.\n\n5. 무를 원통모양으로 썬다.\n\n6. 소고기 안심에 원형틀로 동그란 구멍을 낸다.\n\n7. 구멍을 낸 소고기 안심에 소금과 후추로 밑간한 뒤, 밀가루를 묻힌다.\n\n8. 원통모양 무에 밀가루를 묻히고, 소고기 안심 구멍에 끼워 소꼬리 모양을 만든다.\n\n9. 쿠킹포일로 안심 테두리를 감싼 뒤, 기름에 튀긴다.\n\n10. 찜기에 나무젓가락과 튀긴 안심을 넣는다.\n\n11. 나무젓가락에 토치로 불을 붙인 뒤, 찜기 뚜껑을 덮어 안심을 훈연한다.\n\n12. 기름을 두른 팬에 다진 마늘과 데친 고사리를 넣고 볶는다.\n\n13. 핸드블렌더에 들깻가루와 물을 넣고 간 뒤, 볶던 고사리에 넣는다.\n\n14. 끓는 물에 손질한 양배추와 브로콜리니, 시금치를 넣고 데친 뒤, 얼음물에 담가 식힌다.\n\n15. 꼬리곰탕소스에서 인삼만 건져낸 뒤, 밀가루를 묻혀 튀긴다.\n\n16. 튀긴 인삼을 건져내 기름기를 뺀다.\n\n17. 데친 시금치의 물기를 짠 뒤, 고사리를 볶던 팬에 넣는다.\n\n18. 볶던 재료에 팽이버섯을 넣는다.\n\n19. 데친 브로콜리니와 양배추의 물기를 짠 뒤, 식초와 고춧가루, 가리비다시마소스, 소금을 넣고 버무려 김치를 만든 뒤, 접시에 담아낸다.\n\n20. 훈연한 안심의 쿠킹포일을 벗겨낸다.\n\n21. 안심을 마른 그릴팬에 굽는다.\n\n22. 꼬리곰탕소스를 종지에 담는다.\n\n23. 후머스와 콘수프를 섞어 접시에 플레이팅 한다.\n\n24. 접시에 고사리시금치볶음을 담는다.\n\n25. 접시에 당근드레싱을 뿌리고 안심 스테이크를 담는다.\n\n26. 튀긴 인삼에 금가루를 뿌린 뒤, 접시에 올려 마무리한다'),
	(698, 'K.F.C.(Korean Fried Chicken)', NULL, 1, '1. 양파를 적당한 크기로 썬다.\n\n2. 믹서기에 적당한 크기로 썬 양파와 소금, 된장, 고추장, 달걀흰자, 후추를 넣고 갈아 된장소스를 만든다.\n\n3. 닭허벅지살을 두드려 넓게 편다.\n\n4. 넓게 편 닭허벅지살에 후추와 소금으로 밑간한다.\n\n5. 밑간한 닭허벅지살을 된장소스에 버무려 재운다.\n\n6. 냄비에 설탕과 레드와인식초, 고추장, 스리라차소스, 다진 생강, 다진 마늘을 넣고 끓여 고추장소스를 만든다.\n\n7. 고추장소스에 참기름을 더한다.\n\n8. 무를 잘게 썬다.\n\n9. 잘게 썬 무에 설탕과 청주, 식초를 넣고 절인다.\n\n10. 볼에 팬케이크가루와 우유, 고추맛후리카케를 넣고 섞어 팬케이크반죽을 만든다.\n\n11. 다른 볼에 밀가루와 시치미, 유자향시치미, 소금을 섞어 튀김옷을 만든다.\n\n12. 된장소스에 재운 닭허벅지살에 튀김옷을 입힌 뒤, 기름에 튀긴다.\n\n13. 기름을 두른 팬에 팬케이크반죽을 굽는다.\n\n14. 치킨을 건져내 기름기를 뺀다.\n\n15. 치킨을 다시 한 번 기름에 튀긴 뒤, 건져내 기름기를 뺀다.\n\n16. 기름을 두른 팬에 손질한 아보카도를 넣고 굽는다.\n\n17. 빵가루를 튀긴다.\n\n18. 튀긴 빵가루와 대파를 잘게 썬다.\n\n19. 치킨에 고추장소스를 바른다.\n\n20. 19에 튀긴 빵가루와 대파, 고추맛후리카케를 뿌린다.\n\n21. 팬케이크 위에 치킨을 올린다.\n\n22. 치킨 위에 구운 아보카도를 올린 뒤, 고춧가루를 뿌려 접시에 담아낸다.\n\n23. 치킨 위에 절인 무를 올려 마무리한다.'),
	(699, '투가스', NULL, 1, '1. 끓는 물에 양파를 데친다.\n\n2. 금태의 살을 발라낸다.\n\n3. 양파를 데치던 냄비에 데친 시금치와 완두콩, 물을 넣고 끓인다.\n\n4. 끓이던 채소의 물을 모두 따라낸 뒤, 인스턴트 설렁탕을 넣고 끓인다.\n\n5. 소고기 안심에 소금과 후추로 밑간한다.\n\n6. 밑간한 소고기 안심에 디종 머스터드를 발라 겹겹이 쌓는다.\n\n7. 금태살에 소금으로 밑간한 뒤, 고추냉이를 바르고 다른 금태살을 붙여 샌드한다.\n\n8. 겹겹이 쌓은 소고기 안심과 금태살에 밀가루를 입힌다.\n\n9. 8에 우유를 넣은 달걀물 > 밀가루 > 달걀물 > 빵가루를 순서대로 입힌다.\n\n10. 설렁탕과 함께 끓이던 채소를 핸드블렌더로 곱게 갈아 채소수프를 만든다.\n\n11. 팬에 버터를 두른 뒤, 밀가루를 넣고 볶아 루를 만든다.\n\n12. 채소수프에 루와 우유를 넣고 끓인다.\n\n13. 기름을 넉넉히 두른 팬에 통마늘을 넣어 마늘향을 낸 뒤, 튀김옷을 입힌 소고기 안심과 금태살을 넣고 튀기듯 굽는다.\n\n14. 루에 스테이크소스와 미네스트로네수프, 케첩, 물을 넣고 섞어 데미글라스소스를 만든다.\n\n15. 잘게 썬 청양고추와 마요네즈, 타르타르소스, 레몬즙, 소금을 넣고 섞어 수제타르타르소스를 만든다.\n16. 굽던 안심가스와 금태가스에 버터를 넣는다.\n\n17. 접시에 안심가스소스와 금태가스소스를 담는다.\n\n18. 소스 위에 각각 안심가스와 금태가스를 올려 마무리한다.'),
	(700, '프리하새우', NULL, 1, '1. 끓는 물에 달걀을 넣고 삶는다.\n\n2. 냄비에 다진 마늘과 식용유, 불고기용 돼지고기를 넣고 볶는다.\n\n3. 볶던 재료에 적당한 크기로 썬 고구마를 넣는다.\n\n4. 핸드블랜더에 토마토즙과 적당한 크기로 썬 토마토를 넣고 간다.\n\n5. 볶던 재료에 간 토마토와 물, 스리라차소스, 잘게 썬 청양고추, 고추장을 넣고 끓여 나베를 만든다.\n\n6. 끓이던 나베에 참치액을 넣는다.\n\n7. 두부를 깍둑썰기 한 뒤, 전분을 묻혀 기름에 튀긴다.\n\n8. 튀긴 두부를 나베에 넣고 끓이다가 설탕을 더한다.\n\n9. 테두리를 잘라낸 식빵을 믹서기에 넣고 갈아 빵가루를 만든다.\n\n10. 밀가루와 달걀, 우유, 간 마를 섞어 튀김반죽을 만든다.\n\n11. 손질한 새우에 칼집을 넣고, 다섯 마리를 꼬치에 끼워 고정한다.\n\n12. 새우에 튀김반죽을 묻히고 빵가루를 입힌 뒤, 기름에 튀긴다.\n\n13. 나베에 고춧가루를 넣는다.\n\n14. 간 사과와 레몬즙, 간 레몬껍질, 플레인요거트, 마요네즈, 설탕, 삶은 달걀을 섞어 수제타르타르소스를 만든다.\n\n15. 나베를 뚝배기에 옮겨 담고 끓인다.\n\n16. 접시에 채 썬 양배추와 타르타르소스를 담은 뒤, 꼬치를 제거한 새우가스를 올려 마무리한다.'),
	(701, '폼나는 조식', NULL, 1, '1. 달걀의 끝부분만 깨서 껍데기 원형을 살리고 달걀속은 빼낸다.\n\n2. 달걀물을 체에 밭쳐 곱게 거른다.\n\n3. 달걀물에 통조림연어를 넣고 섞는다.\n\n4. 달걀컵에 달걀껍데기를 담고, 속에 연어달걀물을 채운다.\n\n5. 4를 찜기에 넣은 뒤, 키친타월로 덮고 찐다.\n\n6. 마른 팬에 손질한 토마토를 굽는다.\n\n7. 물과 전분, 밀가루, 소금을 섞어 라이스페이퍼반죽을 만든다.\n\n8. 기름을 바른 접시에 라이스페이퍼반죽을 동그랗게 붓고, 랩으로 덮는다.\n\n9. 라이스페이퍼반죽을 전자레인지에 넣고 돌린다.\n\n10. 전자레인지에서 익힌 라이스페이퍼를 꺼내 얼음물에 담가 식힌다.\n\n11. 양배추와 상추, 깻잎을 채 썬다.\n\n12. 핸드블랜더에 토마토즙과 설탕을 넣고 갈아 토마토폼소스를 만든다.\n\n13. 뜨거운 물에 새우와 완두콩을 넣어 데친 뒤, 얼음물에 넣어 식힌다.\n\n14. 라이스페이퍼에 채 썬 채소와 타르타르소스, 데친 새우를 넣고 돌돌 말아 새우스프링롤을 만든다.\n\n15. 접시에 타르타르소스로 플레이팅한 뒤, 구운 토마토와 새우스프링롤을 올린다.\n\n16. 잘게 간 볶은땅콩과 오리엔탈드레싱을 뿌린다.\n\n17. 딸기의 꼭지를 제거한 뒤, 곁들인다.\n\n18. 새우스프링롤 위에 채 썬 채소와 토마토폼소스를 올린다.\n\n19. 연어달걀찜을 곁들여 마무리한다.'),
	(702, '수플레가 모닝?', NULL, 1, '1. 달걀흰자와 노른자를 분리한다.\n\n2. 달걀노른자를 휘저어 푼 뒤, 밀가루를 섞는다.\n\n3. 달걀흰자에 설탕을 넣어가며 쳐서 머랭을 만든다.\n\n4. 밀가루를 넣은 달걀노른자에 머랭을 조금씩 넣어가며 섞어 수플레팬케이크반죽을 만든다.\n\n5. 기름을 두른 팬에 수플레팬케이크반죽을 올린 뒤, 뚜껑을 덮고 약불에서 서서히 익힌다.\n\n6. 우유를 끓인 뒤, 레몬즙을 넣는다.\n\n7. 끓인 우유를 체에 밭쳐 리코타치즈를 만든다.\n\n8. 수플레팬케이크 위에 반죽을 한 번 더 올리고, 다시 익힌다.\n\n9. 딸기를 적당한 크기로 썬 뒤, 설탕과 레몬즙을 섞는다.\n\n10. 프랑크소시지에 칼집을 넣은 뒤, 뜨거운 물에 넣고 데친다.\n\n11. 볼에 달걀을 푼다.\n\n12. 양상추를 채 썬다.\n\n13. 팬에 메이플시럽을 넣고 끓인다.\n\n14. 한 팬에는 달걀물을 넣어 에그스크램블을 만들고, 한 팬에는 식빵을 넣어 굽는다.\n\n15. 사과를 적당한 크기로 썬다.\n\n16. 핸드블랜더에 사과와 식혜를 넣는다.\n\n17. 16에 당근과 다진 생강을 넣고 갈아 채소주스를 만든다.\n\n18. 에그스크램블에 버터와 채 썬 양상추를 넣고, 소금으로 간 한다.\n\n19. 접시에 수플레팬케이크와 설탕에 절인 딸기, 에그스크램블을 담는다.\n\n20. 구운 식빵을 썰어 접시에 올린다.\n\n21. 끓인 메이플시럽을 수플레팬케이크 위에 뿌린다.\n\n22. 채소주스를 체에 거른다.\n\n23. 데친 프랑크소시지를 마른 팬에 굽는다.\n\n24. 구운 프랑크소시지를 접시에 담아낸다.\n\n25. 리코타치즈에 소금과 꿀을 넣고 섞은 뒤, 접시에 담아낸다.\n\n26. 채소주스를 곁들인다.'),
	(703, '나도 어디서 꿀리진 않魚', NULL, 1, '1. 기름을 두른 냄비에 잘게 썬 대파와 으깬 마늘, 토마토즙을 넣고 끓여 토마토탕을 만든다.\n\n2. 핸드블렌더에 마늘과 잣, 그린올리브, 크림치즈, 콩테치즈, 깻잎, 올리브오일을 넣고 갈아 깻잎페스토를 만든다.\n\n3. 핸드블렌더에 차돌박이와 오겹살, 통마늘, 후추, 맛술을 넣고 간다.\n\n4. 마른 팬에 잘게 썬 대파와 간 고기, 소금을 넣고 볶는다.\n\n5. 볶던 재료에 밥과 깻잎페스토를 넣고 볶아 볶음밥을 만든다.\n\n6. 볶음밥과 쌈무, 김, 간 당근으로 물고기초밥을 만든다.\n\n7. 쌈무로 만든 물고기 꼬리와 몸통을 얹어 물고기 초밥을 만든다.\n\n8. 접시에 볶음밥을 담아 마무리한다.'),
	(704, '치즈 쇼타임~마!', NULL, 1, '1. 기름을 두른 그릴팬에 손질한 양파와 오이고추를 굽는다.\n\n2. 전분과 달걀, 물을 섞어 튀김반죽을 만든다.\n\n3. 양파를 적당한 크기로 썰어 튀김반죽을 입힌 뒤, 소금과 후추로 간 한다.\n\n4. 튀김반죽을 입힌 양파를 기름에 튀긴다.\n\n5. 기름을 두른 팬에 잘게 썬 양파를 볶는다.\n\n6. 핸드블렌더에 오겹살과 차돌박이, 인스턴트 고기산적, 슬라이스 훈제오리를 넣고 갈아 패티반죽을 만든다.\n\n7. 패티반죽에 볶은 양파를 넣고 소금과 후추로 간 한다.\n\n8. 기름을 두른 팬에 적당한 크기로 떼어낸 패티반죽을 굽는다.\n\n9. 양파튀김을 건져 기름기를 뺀다.\n\n10. 양파와 오이 껍질을 잘게 썬다.\n\n11. 볼에 잘게 썬 양파와 오이, 플레인 요거트, 레몬즙, 소금, 후추를 넣고 섞는다.\n\n12. 굽던 패티 위에 슬라이스 체더치즈를 올린다.\n\n13. 발사믹드레싱과 간 배, 올리브오일, 레몬즙, 잘게 썬 오이와 구운 오이고추, 후추를 넣고 섞어 소스를 만든다.\n\n14. 접시에 양파튀김과 생양파를 담는다.\n\n15. 생양파 위에 구운 양파 > 치즈를 올린 패티 > 구운 양파 > 치즈를 올린 패티 순으로 쌓는다.\n\n16. 16에 쇠꼬치를 꽂아 고정한다.\n\n17. 종지에 소스를 담는다.\n\n18. 고춧가루와 파슬리를 뿌려 마무리한다.'),
	(705, '곤짜장', NULL, 1, '1. 냄비에 통조림간장골뱅이 국물을 따라낸다.\n\n2. 통조림간장골뱅이 국물에 곤약면과 레몬즙을 넣고 조린다.\n\n3. 기름을 두른 팬에 적당한 크기로 썬 오겹살을 넣고 볶는다.\n\n4. 양파와 애호박을 적당한 크기로 썬다.\n\n5. 볶던 오겹살에 양파를 넣고 볶는다.\n\n6. 밀대로 민 식빵의 테두리를 잘라낸 뒤, 정사각형 모양으로 자른다.\n\n7. 핸드블렌더에 슬라이스 닭가슴살과 달걀, 전분, 버터를 넣고 갈아 닭가슴살반죽을 만든다.\n\n8. 닭가슴살반죽에 잣을 넣고 섞는다.\n\n9. 슬라이스 체더치즈를 뭉친 뒤, 닭가슴살반죽으로 감싼다.\n\n10. 식빵 사이에 닭가슴살반죽을 넣어 샌드한 뒤, 기름에 튀긴다.\n\n11. 멘보샤를 건져 기름기를 뺀다.\n\n12. 간장골뱅이국물에 조린 곤약면을 체에 밭쳐 물기를 뺀다.\n\n13. 볶던 재료에 간장과 인스턴트 짜장가루를 넣고 볶아 짜장양념을 만든다.\n\n14. 짜장양념에 전분물을 넣는다.\n\n15. 양배추를 채 썬 뒤, 접시에 담는다.\n\n16. 멘보샤를 건져낸 뒤, 접시에 담는다.\n\n17. 채 썬 양배추 위에 케첩과 마요네즈를 뿌린다.\n\n18. 오이를 채 썬다.\n\n19. 접시에 곤약면을 담는다.\n\n20. 곤약면 위에 짜장양념을 올린 후, 채 썬 오이를 얹어 마무리한다.'),
	(706, '치킨 치즈 스마일', NULL, 1, '1. 소금을 넣은 끓는 물에 달걀을 삶는다.\n\n2. 끓는 물에 잘게 썬 당근을 넣고 삶는다.\n\n3. 밀가루를 체에 걸러 곱게 친다.\n\n4. 밀가루에 달걀노른자와 올리브오일, 맥주를 넣고 섞어 튀김반죽을 만든다.\n\n5. 달걀흰자에 소금을 넣고 쳐서 머랭을 만든다.\n\n6. 튀김반죽에 머랭을 넣고 섞는다.\n\n7. 냉동한 크림치즈를 적당한 크기로 썰어 밀가루를 묻힌다.\n\n8. 큐브닭가슴살에 레몬즙을 넣은 뒤, 밀가루를 묻힌다.\n\n9. 밀가루를 묻힌 크림치즈와 큐브닭가슴살에 튀김반죽을 입혀 기름에 튀긴다.\n\n10. 다이어트도시락에서 완두콩과 옥수수만 골라낸다.\n\n11. 닭가슴살과 닭가슴살 소시지, 인스턴트 감자튀김을 적당한 크기로 썰어 볼에 담는다.\n\n12. 볼에 완두콩과 옥수수, 잘게 썬 그린올리브, 소금, 후추를 넣는다.\n\n13. 12에 마요네즈와 잘게 썬 사과를 넣고 섞어 샐러드를 만든다.\n\n14. 삶은 달걀의 껍데기를 깐다.\n\n15. 샐러드에 삶은 당근을 넣는다.\n\n16. 다이어트도시락의 각종 채소와 잘게 썬 양파, 칠리소스를 섞은 뒤, 원형틀 안에 넣는다.\n\n17. 칠리소스와 라즈베리잼을 섞어 소스를 만든다.\n\n18. 치즈튀김과 닭가슴살튀김을 건져 기름기를 뺀다.\n\n19. 접시에 상추와 치즈튀김, 닭가슴살튀김을 담는다.\n\n20. 샐러드에 삶은 달걀을 으깨 넣고 섞은 뒤, 접시에 담는다.\n\n21. 오이를 슬라이스한 뒤, 플레이팅한다.\n\n22. 오렌지를 적당한 크기로 썰어 플레이팅한다.\n\n23. 그린올리브로 토끼모양을 만든 뒤, 샐러드 위에 올려 마무리한다.'),
	(707, '연볶면', NULL, 1, '1. 대파 흰 부분을 잘게 썬다.\n\n2. 마른 찜기에 잘게 썬 대파를 넣고 뚜껑을 덮은 뒤 가열한다.\n\n3. 밀가루와 물을 섞어 면반죽을 만든 뒤, 젖은 행주를 덮어 숙성한다.\n\n4. 끓는 기름에 손질한 대파와 고춧가루를 넣고 끓여 고추기름을 낸다.\n\n5. 고추기름에 청양고추를 넣는다.\n\n6. 손질새우와 낙지를 적당한 크기로 썬다.\n\n7. 적당한 크기로 썬 새우와 낙지를 데친다.\n\n8. 데친 새우와 낙지를 체에 밭쳐 물기를 뺀다.\n\n9. 양파, 표고버섯, 당근, 차돌박이, 목살, 쪽파를 적당한 크기로 썬다.\n\n10. 고추기름을 체에 밭쳐 건더기를 걸러낸다.\n\n11. 기름을 두른 팬에 고추기름을 붓는다.\n\n12. 면반죽을 밀대로 얇게 민다.\n\n13. 면반죽을 썰어 면을 뽑은 뒤, 기름에 튀긴다.\n\n14. 고추기름을 두른 팬에 손질한 차돌박이와 목살, 쪽파를 넣고 볶다가 화이트와인을 넣는다.\n\n15. 14에 손질한 채소들, 돼지불고기 양념, 고추장을 넣고 볶아 볶음짬뽕소스을 만든다.\n\n16. 대파를 태우는 찜기에 볶음짬뽕소스를 담은 그릇을 얹은 뒤, 데친 새우와 낙지를 넣고 섞는다.\n\n17. 찜기의 뚜껑을 닫아 볶음짬뽕소스에 불향을 입힌다.\n\n18. 튀긴 면을 건져 기름기를 뺀다.\n\n19. 깻잎을 채 썬다.\n\n20. 종지에 마요네즈를 담는다.\n\n21. 그릇에 튀긴 면을 담는다.\n\n22. 훈연한 볶음짬뽕소스 위에 채 썬 깻잎을 얹어 낸다.'),
	(708, '볶출어람', NULL, 1, '1. 즉석밥을 물에 헹궈 전분기를 뺀 뒤, 체에 밭쳐 물기를 뺀다.\n\n2. 마른 팬에 설탕을 녹인 뒤, 뜨거운 물을 섞어 캐러멜소스를 만든다.\n\n3. 캐러멜소스에 다진 생강과 소금을 섞는다.\n\n4. 목살과 인스턴트 떡갈비를 잘게 썬다.\n\n5. 대파, 마늘, 청양고추를 잘게 썬다.\n\n6. 표고버섯과 깻잎을 적당한 크기로 썬다.\n\n7. 오이를 적당한 크기로 썬 뒤, 속을 파낸다.\n\n8. 씻어서 물기를 뺀 밥을 체에 밭쳐 기름에 튀긴다.\n\n9. 밥튀김의 기름기를 뺀다.\n\n10. 김에 캐러멜소스를 묻힌 뒤, 기름에 튀긴다.\n\n11. 손질한 표고버섯에 캐러멜소스를 묻힌 뒤, 기름에 튀긴다.\n\n12. 기름을 두른 팬에 손질한 대파와 마늘, 청양고추, 멸치액젓을 넣고 볶는다.\n\n13. 12에 손질한 목살과 인스턴트 떡갈비, 낙지볶음소스, 후추, 청주를 넣고 볶는다.\n\n14. 접시에 밥튀김을 깔고 김튀김과 표고버섯튀김을 얹는다.\n\n15. 접시에 속을 파낸 오이를 담은 뒤, 속에 깻잎을 깐다.\n\n16. 속을 파낸 오이와 접시에 고기볶음을 담아 마무리한다.'),
	(709, '안심하고 튀겼새우', NULL, 1, '1. 끓는 물에 다시마와 국물용 멸치를 넣고 끓여 육수를 낸다.\n\n2. 마늘을 편 썬 뒤, 찬물에 담가 아린 맛을 뺀다.\n\n3. 스테이크용 안심을 고기망치로 두드려 넓게 편 후, 소금과 후추로 밑간한다.\n\n4. 통조림햄을 적당한 크기로 썬다.\n\n5. 표고버섯에 칼집을 넣어 모양을 낸다.\n\n6. 차돌박이에 손질새우를 올린 뒤, 돌돌 만다.\n\n7. 손질한 안심과 통조림햄, 표고버섯, 차돌박이로 만 새우에 밀가루를 묻힌다.\n\n8. 전자레인지에 즉석밥을 넣고 돌린다.\n\n9. 팬에 육수와 맛술, 청주, 간장, 설탕을 넣어 데리야키소스를 만든다.\n\n10. 설탕과 식초, 케첩, 마요네즈를 섞어 마요소스를 만든다.\n\n11. 편마늘을 튀긴 뒤, 기름기를 뺀다.\n\n12. 밀가루와 튀김가루, 맥주, 얼음물을 섞어 튀김반죽을 만든다.\n\n13. 안심에 튀김반죽을 묻힌 뒤, 튀긴다.\n\n14. 튀김반죽을 튀겨 덴카스를 만든다.\n\n15. 차돌박이로 만 새우와 통조림햄에 튀김반죽을 묻힌 뒤, 튀긴다.\n\n16. 튀김과 덴카스를 건져 기름기를 뺀다.\n\n17. 즉석밥을 그릇에 담은 뒤, 데리야키소스를 뿌린다.\n\n18. 차돌박이새우튀김과 햄튀김을 한 번 더 튀긴 뒤, 건져 기름기를 뺀다.\n\n19. 안심튀김을 한 번 더 튀긴다.\n\n20. 차돌박이새우튀김을 적당한 크기로 썬 뒤, 마요소스에 버무려 접시에 담아낸다.\n\n21. 안심튀김을 건져 적당한 크기로 썬 뒤, 밥 위에 얹고 덴카스를 뿌린다.\n\n22. 달걀을 기름에 튀긴다.\n\n23. 달걀튀김을 건져 안심튀김 위에 얹는다.\n\n24. 달걀튀김 위에 잘게 썬 청양고추를 올린 뒤, 데리야키소스를 뿌려 마무리한다.'),
	(710, '두고보샤', NULL, 1, '1. 기름을 두른 팬에 달걀과 조미료를 넣고 휘저어 스크램블을 만든다.\n\n2. 끓는 기름에 대파와 통마늘, 고춧가루를 넣어 고추기름을 만든다.\n\n3. 두부를 가로로 썬 뒤, 기름에 튀긴다.\n\n4. 볼에 스크램블을 담아 식힌다.\n\n5. 핸드블렌더에 차돌박이와 달걀흰자, 전분, 소금, 후추를 넣고 간다.\n\n6. 간 재료에 손질새우를 넣고 다시 한 번 간다.\n\n7. 스크램블과 간 재료를 섞어 새우반죽을 만든다.\n\n8. 튀긴 두부를 건져 정사각형 모양으로 자른다.\n\n9. 튀긴 두부 사이에 새우반죽을 넣고 샌드한 뒤, 이쑤시개로 고정해 튀긴다.\n\n10. 표고버섯 사이에 새우반죽을 넣고 샌드한 뒤, 튀긴다.\n\n11. 뜨거운 물에 콩나물을 넣고 데친다.\n\n12. 간장과 고춧가루, 식초, 설탕, 잘게 썬 쪽파를 섞어 양념장을 만든다.\n\n13. 양념장에 다진 마늘을 넣는다.\n\n14. 지퍼백에 오이를 넣고 두드린 뒤, 잘게 썰어 양념장에 넣는다.\n\n15. 데친 콩나물을 접시에 담는다.\n\n16. 두부멘보샤를 건져 이쑤시개를 제거한 뒤, 접시에 담는다.\n\n17. 표고버섯멘보샤를 건져 접시에 담는다.\n\n18. 오이를 넣은 양념장을 얹고, 고추기름을 뿌려 마무리한다.'),
	(711, '파스타가 캐비아', NULL, 1, '1. 옥돔의 살만 발라낸다.\n\n2. 채칼로 애호박을 슬라이스한다.\n\n3. 옥돔살 위에 슬라이스한 애호박을 얹은 뒤, 끝부분을 잘라낸다.\n\n4. 애호박을 얹은 옥돔살을 찜기에 넣고 찐다.\n\n5. 양파를 채 썬 뒤, 기름을 두른 팬에 넣고 볶는다.\n\n6. 감자를 채 썬 뒤, 볶던 양파와 함께 볶는다.\n\n7. 볶던 양파와 감자에 소금으로 간 하고, 수제양파와인과 잘게 썬 베이컨을 넣는다.\n\n8. 7에 트러플페스토와 후추를 넣고 볶아 트러플양파볶음을 만든다.\n\n9. 끓는 물에 디포리와 가츠오부시를 넣고 끓여 해물육수를 낸 뒤, 건더기만 걸러낸다.\n\n10. 육수에 고다치즈를 넣고 끓여 해물치즈육수를 만든다.\n\n11. 10을 핸드블렌더로 간다.\n\n12. 끓는 물에 사발라면의 면을 넣고 익힌다.\n\n13. 익힌 사발라면의 면을 찬물에 헹궈 식힌다.\n\n14. 해물치즈육수에 생크림을 넣고 끓인다.\n\n15. 사발라면 면을 해물치즈육수에 버무린다.\n\n16. 접시에 트러플양파볶음을 담는다.\n\n17. 캐비아를 절구로 빻은 뒤, 면에 넣고 섞는다.\n\n18. 트러플양파볶음 위에 해물치즈육수와 캐비아에 버무린 면을 얹는다.\n\n19. 면 위에 애호박옥돔찜을 얹는다.\n\n20. 해물치즈육수에 캐비아를 넣고 끓인 뒤, 소금 간을 해 캐비아소스를 만든다.\n\n21. 접시에 캐비아소스를 부어 낸다.'),
	(712, '돈 코스', NULL, 1, '1. 핸드블렌더에 호두와 아몬드, 잣을 넣고 간다.\n\n2. 간 견과류를 체에 밭친 뒤, 끓는 물에 데쳐 기름을 뺀다.\n\n3. 데친 견과류를 설탕에 버무린 뒤, 오븐에 굽는다.\n\n4. 볼에 푸아그라와 생크림을 넣고, 끓는 물 위에서 중탕하며 섞는다.\n\n5. 발사믹식초에 설탕을 넣고 졸여 발사믹크림을 만든 뒤, 식혀둔다.\n\n6. 생크림을 얼음물 위에 얹고 설탕을 넣은 뒤 거품기로 젓는다.\n\n7. 기름을 두른 팬에 손질한 파프리카와 애호박을 굽는다.\n\n8. 양파를 반으로 썬 뒤, 모양을 살려 기름에 튀긴다.\n\n9. 대파 흰 부분을 채 썬 뒤, 기름에 튀긴다.\n\n10. 튀긴 양파와 대파를 건져둔다.\n\n11. 핸드블렌더에 마늘과 올리브오일을 넣고 갈아 마늘오일을 만든다.\n\n12. 기름을 두른 팬에 마늘오일과 손질한 송화버섯을 넣고 볶는다.\n\n13. 송화버섯에는 소금과 후추로, 굽던 파프리카와 애호박에는 소금으로 간 한다.\n\n14. 볶던 송화버섯에 식초를 넣고 볶는다.\n\n15. 이베리코 목살에 소금으로 간 한 뒤, 기름을 두른 팬에 굽는다.\n\n16. 이베리코 목살스테이크에 후추를 뿌린다.\n\n17. 팬에 굴소스, 마늘오일, 올리브오일, 발사믹식초, 잘게 썬 구운 파프리카를 넣고 볶아 발사믹소스를 만든다.\n\n18. 아보카도를 손질한 뒤, 적당한 크기로 썰어 접시에 깔고 소금과 후추, 올리브오일을 뿌린다.\n\n19. 아보카도 위에 레몬즙을 뿌린다.\n\n20. 끓는 기름에 생바질을 넣고 튀긴다.\n\n21. 바질기름에 적당한 크기로 썬 관자를 넣어 익힌다.\n\n22. 바질기름에 익한 관자를 아보카도 위에 얹고 후추를 뿌린다.\n\n23. 송화버섯볶음에 버터를 넣고 녹인다.\n\n24. 관자 위에 송화버섯소스를 뿌리고 튀긴 대파와 캐비아를 얹는다.\n\n25. 발사믹크림에 파프리카를 넣은 발사믹소스를 섞은 뒤 뿌린다.\n\n26. 차게 굳힌 생크림에 푸아그라를 녹인 생크림을 섞어 푸아그라무스를 만든다.\n\n27. 잔에 설탕에 버무려 구운 견과류를 담고, 푸아그라무스를 얹는다.\n\n28. 푸아그라무스 위에 잘게 썬 배를 얹는다.\n\n29. 접시에 튀긴 양파와 튀긴 대파, 이베리코 목살스테이크, 송화버섯볶음과 발사믹크림을 섞은 발사믹소스를 담아낸다.'),
	(713, '타조가 조타', NULL, 1, '1. 양파를 채 썬다.\n\n2. 두 개의 팬에 버터를 두른 뒤, 채 썬 양파를 넣고 볶는다.\n\n3. 볶던 양파에 소금과 후추로 간 한다.\n\n4. 송화버섯과 만가닥버섯, 대파 흰 부분을 잘게 썬다.\n\n5. 볶던 양파에 각각 손질한 채소들을 넣고 볶는다.\n\n6. 볶던 채소에 적당한 크기로 썬 베이컨을 넣고 볶는다.\n\n7. 밀가루와 파프리카 파우더를 섞는다.\n\n8. 타조고기를 고기망치로 두드려 얇게 편다.\n\n9. 타조고기에 소금과 후추로 간을 한 뒤 길게 채 썬다.\n\n10. 타조고기에 파프리카 파우더를 섞은 밀가루를 묻힌 뒤, 볶던 채소에 넣고 버터를 더한다.\n\n11. 10에 생크림을 넣고 끓여 타조 스트로가노프를 만든다.\n\n12. 타조 스트로가노프에 소금과 간장으로 간 한다.\n\n13. 핸드블렌더에 달걀, 우유, 레몬즙, 밀가루를 넣고 섞어 스패츨 반죽을 만든다.\n\n14. 스패츨 반죽을 체에 내려 스패츨을 만든 뒤, 끓는 물에 익힌다.\n\n15. 볶던 채소와 베이컨에 스패츨과 다진 쪽파를 넣고 볶는다.\n\n16. 그릇에 볶은 스패츨을 담고 고다치즈를 갈아서 뿌린다.\n\n17. 스패츨 위에 타조 스트로가노프를 얹고, 다진 쪽파를 뿌린다.\n\n18. 사워크림과 다진 쪽파를 한 번 더 올려 마무리한다.'),
	(714, '돈스테이크', NULL, 1, '1. 끓는 기름에 대파를 넣어 파기름을 낸다.\n\n2. 양파를 채 썬다.\n\n3. 기름을 두른 냄비에 채 썬 양파를 넣고 볶는다.\n\n4. 냄비에 두유를 넣고 끓인다.\n\n5. 감자와 당근, 아스파라거스를 적당한 크기로 썬 뒤 끓는 두유에 넣고 익힌다.\n\n6. 타조고기를 적당한 크기로 손질한 뒤, 소금과 후추로 간 한다.\n\n7. 끓는 파기름을 불에서 내린 뒤, 타조고기를 넣어 튀긴다.\n\n8. 파기름에 튀긴 타조고기를 기름을 두른 팬에 굽는다.\n\n9. 가열하던 찜기에 바질과 로즈메리를 넣은 뒤 타조스테이크를 올려 훈연한다.\n\n10. 적당한 크기로 썬 베이컨을 볶던 양파에 넣고 함께 볶는다.\n\n11. 적당한 크기로 썬 타조고기를 밀가루에 묻힌 뒤, 볶던 양파와 베이컨에 넣고 함께 볶는다.\n\n12. 11에 파프리카 파우더를 넣는다.\n\n13. 12에 편마늘과 토마토소스, 손질한 송화버섯과 만가닥버섯, 수제된장, 흑맥주와 물을 넣고 끓인다.\n\n14. 끓이던 각종 재료를 체에 밭쳐 건더기를 걸러낸 뒤 국물만 끓인다.\n\n15. 팬에 버터와 밀가루를 넣고 볶아 루를 만든다.\n\n16. 끓이던 국물에 루를 넣어 타조스튜소스를 만든다.\n\n17. 타조스튜소스에 소금으로 간 한다.\n\n18. 두유에 익힌 채소를 건져낸 뒤 물에 헹군다.\n\n19. 접시에 두유에 익힌 감자>슬라이스 치즈>아스파라거스>슬라이스 치즈>당근>슬라이스 치즈 순으로 켜켜이 쌓는다.\n\n20. 치즈와 채소를 토치로 직화한다.\n\n21. 훈연한 타조스테이크를 적당한 크기로 썰어 올린 뒤, 타조스튜소스를 붓는다.\n\n22. 타조스튜에서 건져낸 송화버섯을 얹어 마무리한다.'),
	(715, '비먹부먹', NULL, 1, '1. 끓는 기름에 대파와 고춧가루를 넣어 고추기름을 낸다.\n\n2. 팬에 식용유와 들기름을 넣는다.\n\n3. 2에 으깬 마늘을 넣고 볶는다.\n\n4. 볶던 마늘에 불린 황태채를 넣고 볶는다.\n\n5. 볶던 황태채에 멸치액젓과 건새우, 뜨거운 물, 다시마를 넣고 끓여 육수를 낸다.\n\n6. 대패삼겹살을 적당한 크기로 썬다.\n\n7. 고추기름을 두른 팬에 대패삼겹살을 볶는다.\n\n8. 볶던 대패삼겹살에 으깬 마늘과 맛술을 넣는다.\n\n9. 8에 굴소스, 간장, 쯔유, 후추, 잘게 썬 대파, 식초를 넣고 볶아 비빔양념을 만든다.\n\n10. 육수에 건표고버섯 불린 물을 넣는다.\n\n11. 육수에 식초와 쯔유를 넣는다.\n\n12. 마른 팬에 불린 건표고버섯과 굴소스, 간장, 맛술, 식초, 소금, 후추를 넣고 볶는다.\n\n13. 볶은 건표고버섯에 고추기름을 넣는다.\n\n14. 끓는 물에 짬뽕면을 삶는다.\n\n15. 육수는 체에 밭쳐 국물만 뚝배기에 옮겨 담는다.\n\n16. 그릇에 익힌 면을 담고 쯔유를 넣어 섞는다.\n\n17. 면 위에 비빔양념과 건표고버섯볶음을 얹는다.\n\n18. 레몬슬라이스를 함께 낸다.'),
	(716, '팟타이가 맛있돔', NULL, 1, '1. 소금을 넣은 물에 파스타면을 삶는다.\n\n2. 끓는 기름에 편마늘과 건새우를 넣고 끓여 새우기름을 낸다.\n\n3. 팬에 새우기름을 두른 뒤, 손질한 옥돔살을 넣고 굽는다.\n\n4. 굽던 옥돔살에 적당한 크기로 썬 표고버섯을 넣고 볶는다.\n\n5. 달걀로 에그스크램블을 만든다.\n\n6. 볶던 옥돔과 버섯에 에그스크램블과 대패삼겹살을 넣고 볶다가 소금과 후추로 간 한다.\n\n7. 6에 맛술과 다진 마늘을 넣고 볶는다.\n\n8. 굴소스와 간장, 발사믹식초, 메이플시럽을 섞어 소스를 만든다.\n\n9. 삶은 파스타면을 건져 물기를 뺀다.\n\n10. 볶던 재료에 파스타면과 소스, 통고추를 넣어 함께 볶는다.\n\n11. 채 썬 대파 흰 부분과 잘게 썬 홍고추를 넣고 함께 볶아 옥돔팟타이를 만든다.\n\n12. 통고추는 빼내고, 접시에 옥돔팟타이를 담아낸다.\n\n13. 옥돔팟타이에 고춧가루를 뿌려 마무리한다.'),
	(717, '고기의 품격', NULL, 1, '1. 애호박을 슬라이스 한 뒤, 기름을 두른 그릴팬에 굽는다.\n\n2. 감자를 슬라이스 한 뒤, 냄비에 넣고 뜨거운 물을 부어 끓인다.\n\n3. 익히던 감자에 소금을 넣는다.\n\n4. 팬에 버터를 녹인 뒤, 밀가루를 넣고 섞어 루를 만든다.\n\n5. 루에 우유를 넣어 베샤멜소스를 만든다.\n\n6. 감자에 베샤멜소스를 넣고 끓여 감자수프를 만든다.\n\n7. 핸드블렌더에 적당한 크기로 썬 표고버섯과 새송이버섯을 넣고 간다.\n\n8. 기름을 두른 팬에 간 버섯을 볶는다.\n\n9. 소고기등심에 올리브오일과 소금, 후추로 간 한다.\n\n10. 그릴팬에 밑간 한 소고기등심을 굽는다.\n\n11. 양파를 채 썬 뒤, 기름에 튀긴다.\n\n12. 튀긴 양파를 건져낸 뒤, 통마늘을 튀긴다.\n\n13. 튀긴 양파채와 마늘에 발사믹식초, 후추를 넣고 섞는다.\n\n14. 갈아서 볶은 버섯을 감자수프에 넣고 끓여 버섯감자수프를 만든다.\n\n15. 갈아서 볶은 버섯의 나머지에 버터를 넣는다.\n\n16. 핸드블렌더에 아몬드와 호두를 넣고 간다.\n\n17. 버터를 넣은 볶은 버섯에 간 견과류를 넣는다.\n\n18. 랩 위에 구운 애호박을 깔고, 발사믹식초에 버무린 튀긴 양파와 마늘을 올린다.\n\n19. 19 위에 등심스테이크를 올린 뒤 홀그레인머스터드를 바르고 간 버섯볶음을 얹어 돌돌 만다.\n\n20. 애호박으로 만 등심스테이크를 오븐에 굽는다.\n\n21. 버섯감자수프에 파르메산치즈가루를 넣고 섞은 뒤, 그릇에 담아내고 간 견과류를 뿌린다.\n\n22. 화이트식초와 올리브오일, 발사믹식초, 설탕, 방울토마토, 레몬즙, 홀그레인머스터드를 섞어 드레싱을 만든다.\n\n23. 접시에 드레싱을 담고, 발사믹식초에 버무린 튀긴 양파와 마늘을 올린다.\n\n24. 발사믹식초와 올리브오일을 섞어 발사믹오일을 만든다.\n\n25. 드레싱을 담은 접시에 구운 애호박으로 만 등심스테이크를 올린다.\n\n26. 발사믹오일과 간 견과류를 뿌려 마무리한다.'),
	(718, '파이팅롤', NULL, 1, '1. 감자와 당근을 동그랗게 모양 낸 뒤, 끓는 물에 삶는다.\n\n2. 표고버섯을 슬라이스 한 뒤, 기름을 두른 팬에 볶는다.\n\n3. 볶던 표고버섯에 채 썬 양파, 편마늘, 버터, 소금, 후추를 넣고 볶는다.\n\n4. 채끝살을 고기망치로 두드려 얇게 편다.\n\n5. 얇게 편 채끝살에 소금과 후추로 간 한 뒤, 고추장을 바른다.\n\n6. 5에 표고버섯볶음을 올려 돌돌 만 뒤, 이쑤시개로 고정한다.\n\n7. 기름을 두른 팬에 채끝버섯롤을 굽는다.\n\n8. 삶은 감자와 당근을 건져내 마른 팬에 굽는다.\n\n9. 애호박을 동그랗게 모양낸 뒤, 함께 굽는다.\n\n10. 구운 채소에 버터와 후추, 소금을 넣고 볶는다.\n\n11. 상추를 적당한 크기로 썬 뒤, 강판에 간 총각김치와 채 썬 양파, 소금, 올리브오일을 넣고 버무려 총각김치샐러드를 만든다.\n\n12. 총각김치샐러드에 발사믹식초를 넣는다.\n\n13. 총각김치샐러드에 화이트식초를 넣는다.\n\n14. 접시에 총각김치샐러드를 담는다.\n\n15. 구운 채끝버섯롤을 전자레인지에 넣고 익힌다.\n\n16. 채끝버섯롤을 적당한 크기로 썬다.\n\n17. 마른 팬에 채끝버섯롤을 한 번 더 굽는다.\n\n18. 접시에 구운 채소와 어린잎 채소를 담는다.\n\n19. 채끝버섯롤을 담아낸다.'),
	(719, '푸딩이 최고당', NULL, 1, '1. 볼에 카카오 70% 초콜릿을 넣고 중탕한다.\n\n2. 냄비에 설탕과 버터를 넣고 녹이다가 우유와 땅콩버터를 넣어 땅콩 캐러멜 소스를 만든다.\n\n3. 식빵은 테두리를 잘라내고 잘게 썬다.\n\n4. 달걀물을 만든다.\n\n5. 달걀물에 설탕과 우유를 섞는다.\n\n6. 초콜릿과 건무화과, 건살구를 잘게 썬다.\n\n7. 설탕과 우유를 섞은 달걀물에 잘게 썬 식빵을 넣는다.\n\n8. 볼에 올리브오일을 바른 뒤, 달걀물에 적신 식빵>잘게 썬 초콜릿과 건과일>달걀물에 적신 식빵을 켜켜이 담는다.\n\n9. 8을 전자레인지에 넣고 돌린다.\n\n10. 귤을 슬라이스 한 뒤, 껍질을 벗겨낸다.\n\n11. 청포도를 잘게 썬다.\n\n12. 손질한 귤과 청포도에 설탕을 뿌리고 토치로 직화해 캐러멜라이즈한다.\n\n13. 전자레인지에서 브레드푸딩을 꺼내 랩으로 싸둔다.\n\n14. 냄비에 두유와 우유, 칼집을 낸 청양고추를 넣고 끓인다.\n\n15. 14에 녹인 초콜릿을 넣고 끓여 멕시코식 핫초콜릿을 만든다.\n\n16. 잔에 멕시코식 핫초콜릿을 담는다.\n\n17. 카카오 70% 초콜릿을 갈아 가루를 낸다.\n\n18. 브레드푸딩에 초콜릿가루를 묻혀 그릇에 담는다.\n\n19. 브레드푸딩과 멕시코식 핫초콜릿 위에 캐러멜라이즈한 귤을 올린다.\n\n20. 브레드푸딩이 담긴 그릇에 땅콩 캐러멜 소스를 담고, 캐러멜라이즈한 청포도를 곁들인다.\n\n21. 참외를 채 썬다.\n\n22. 채 썬 참외에 설탕을 뿌리고 토치로 직화해 캐러멜라이즈한다.\n\n23. 브레드푸딩 위에 캐러멜라이즈한 참외를 올려 마무리한다.'),
	(720, '티라미수코리아', NULL, 1, '1. 절구에 카카오 70% 초콜릿을 넣고 빻는다.\n\n2. 다른 절구에 코코넛과자를 넣고 빻는다.\n\n3. 핸드블렌더에 빻은 코코넛과자를 넣고 곱게 간다.\n\n4. 마른 팬에 설탕과 뜨거운 물을 넣고 끓이다가 버터, 빻은 초콜릿을 넣고 녹인다.\n\n5. 녹인 초콜릿에 우유와 청주, 달걀노른자를 넣고 섞는다.\n\n6. 달걀흰자로 머랭을 치다가 설탕을 넣고 계속해서 친다.\n\n7. 녹인 초콜릿에 머랭을 섞어 초콜릿무스를 만든다.\n\n8. 컵에 코코넛과자가루>초콜릿무스>코코넛과자가루>초콜릿무스를 켜켜이 담아 초코티라미수를 만든다.\n\n9. 초코티라미수 위에 초콜릿을 갈아 올린다.\n\n10. 초코티라미수를 냉동실에 넣어둔다.\n\n11. 화이트와인과 자몽청, 레몬즙, 얇게 썬 사과를 섞어 디저트와인을 만든 뒤, 잔에 담아낸다.\n\n12. 초코티라미수와 디저트와인을 함께 낸다.'),
	(721, '나이스~튜', NULL, 1, '1. 갈치를 손질한 뒤, 소금과 후추로 간 한다.\n\n2. 손질한 갈치에 전분을 입히고 기름을 두른 팬에 굽는다.\n\n3. 감자와 양파, 아스파라거스, 청양고추, 대파, 마늘을 잘게 썬다.\n\n4. 냄비에 손질한 채소와 우유, 땅콩버터, 슬라이스 체더치즈를 넣고 끓여 스튜를 만든다.\n\n5. 적당한 크기로 썬 방울토마토를 스튜에 넣고 끓인다.\n\n6. 명란젓을 잘게 다진다.\n\n7. 스튜에 잘게 다진 명란젓을 넣는다.\n\n8. 금태의 살을 발라낸 뒤, 적당한 크기로 썬다.\n\n9. 금태살에 전분을 묻혀 기름에 튀긴다.\n\n10. 마른 팬에 케첩, 물, 레몬즙, 매실청, 샹라장, 채 썬 양파와 당근, 아스파라거스, 방울토마토를 넣고 끓여 탕수소스를 만든다.\n\n11. 끓이던 스튜에 구운 갈치를 넣는다.\n\n12. 시금치를 적당한 크기로 썰어 스튜에 넣는다.\n\n13. 접시에 스튜를 담는다.\n\n14. 그릇에 탕수소스를 담는다.\n\n15. 금태튀김을 탕수소스 위에 올려 마무리한다.'),
	(722, '라돌체비타', NULL, 1, '1. 닭을 손질해 닭가슴살만 발라낸다.\n\n2. 핸드블렌더에 닭가슴살과 소금, 마요네즈를 넣고 간다.\n\n3. 아스파라거스를 얇게 슬라이스한 뒤, 얼음물에 담가둔다.\n\n4. 삶은 달걀의 노른자와 슬라이스 체더치즈를 섞는다.\n\n5. 동그란 그릇에 랩을 덮은 뒤, 슬라이스한 아스파라거스를 촘촘히 올린다.\n\n6. 5 위에 간 닭가슴살을 올리고 치즈를 섞은 노른자로 속을 채운다.\n\n7. 랩을 말아 알 모양을 만든 뒤, 끓는 물에 삶는다.\n\n8. 마른 팬에 잘게 썬 양파와 적당한 크기로 썬 방울토마토를 넣고 볶다가 자몽청을 넣는다.\n\n9. 냄비에 버터, 밀가루, 된장, 카카오 100% 초콜릿을 넣고 섞어 초코된장소스를 만든다.\n\n10. 초코된장소스에 두유와 설탕을 넣고 섞는다.\n\n11. 믹서기에 얼음, 청포도, 속을 파낸 참외, 방울토마토를 넣고 갈아 참외가스파초를 만든다.\n\n12. 초코된장소스를 체에 밭쳐 곱게 거른 뒤, 짜장수프를 넣고 섞는다.\n\n13. 접시에 초코된장소스와 자몽토마토소스를 담는다.\n\n14. 슬라이스한 참외와 아스파라거스에 식초, 자몽청, 소금을 넣는다.\n\n15. 14에 잘게 썬 청포도와 두릅을 넣어 샐러드를 만든다.\n\n16. 소스가 담긴 접시에 참외두릅샐러드를 올린다.\n\n17. 삶은 닭고기알의 랩을 벗겨낸 뒤, 참외두릅샐러드 위에 얹는다.\n\n18. 참외가스파초를 잔에 담는다.\n\n19. 블랙올리브를 곁들여 마무리한다.'),
	(723, '술 당길 만두 하지', NULL, 1, '1. 두부를 적당한 크기로 썬 뒤, 전자레인지에 넣고 돌린다.\n\n2. 양파를 잘게 다진다.\n\n3. 칼집을 낸 대파와 당근을 냄비에 넣고, 기름을 두른 뒤 새우를 넣는다.\n\n4. 3에 으깬 마늘과 맛술, 물을 넣고 끓여 새우육수를 낸다.\n\n5. 냄비에 채 썬 양파를 볶다가 버터와 밀가루를 넣어 루를 만든다.\n\n6. 루에 우유를 넣어 베샤멜소스를 만든다.\n\n7. 삼겹살을 적당한 크기로 썬 뒤, 핸드블렌더에 넣고 간다.\n\n8. 다른 핸드블렌더에 소고기안심과 삼겹살을 함께 넣고 간다.\n\n9. 소고기와 함께 간 삼겹살을 다진 양파와 함께 섞는다.\n\n10. 전자레인지에서 익힌 두부를 삼겹살소와 양파를 섞은 소고기삼겹살소에 각각 담는다.\n\n11. 부추를 새우육수에 데친다.\n\n12. 데친 부추를 잘게 썬다.\n\n13. 삼겹살소와 소고기삼겹살소에 데친 부추와 다진 마늘을 각각 넣는다.\n\n14. 삼겹살소에 쌈장과 후추를 넣고 치댄다.\n\n15. 만두피에 삼겹살소를 넣어 만두를 빚는다.\n\n16. 기름을 두른 팬에 삼겹살만두를 굽다가, 뚜껑을 덮어 속까지 익힌다.\n\n17. 끓이던 베샤멜소스에 데친 시금치를 잘라 넣는다.\n\n18. 새우는 껍데기를 벗겨내고 잘게 다진 뒤, 소고기삼겹살소에 넣고 굴소스로 양념한다.\n\n19. 청양고추를 잘게 썬다.\n\n20. 청양고추에 칼집을 낸 뒤, 새우육수에 넣는다.\n\n21. 잘게 썬 청양고추를 새우고기소에 넣고 섞는다.\n\n22. 만두피에 새우고기소를 넣고 빚은 뒤, 새우육수에 넣고 삶는다.\n\n23. 베샤멜소스에 새우육수를 넣는다.\n\n24. 베샤멜소스에 소금으로 간 한다.\n\n25. 베샤멜소스에 파르메산치즈가루를 넣는다.\n\n26. 새우육수에서 새우를 건져내 껍데기를 벗겨낸다.\n\n27. 간장, 식초, 연겨자를 섞어 양념간장을 만든 뒤, 종지에 담는다.\n\n28. 새우육수에서 삶은 새우만두를 꺼내 그릇에 담고, 베샤멜소스와 모차렐라치즈를 올린다.\n\n29. 삼겹살군만두를 양념간장과 함께 그릇에 담아낸다.\n\n30. 토치로 모차렐라치즈를 직화해 새우만두 그라탕을 완성한다.'),
	(724, '불금 가즈아!', NULL, 1, '1. 양파를 채 썬다.\n\n2. 마늘을 편으로 썬다.\n\n3. 팬에 버터를 두른 뒤, 채 썬 양파와 편마늘을 넣고 볶는다.\n\n4. 소갈빗살에 소금과 후추로 간한 뒤, 기름을 두른 그릴팬에 굽는다.\n\n5. 볶던 양파와 마늘에 밀가루를 넣어 루를 만든다.\n\n6. 루에 우유를 조금씩 넣어가며 익혀 베샤멜소스를 만든다.\n\n7. 데친 시금치를 잘게 다진다.\n\n8. 잘게 썬 시금치를 베샤멜소스에 넣고, 우유를 부어가며 익히다가 소금으로 간한다.\n\n9. 잘게 썬 청양고추와 우유, 파르메산치즈가루를 더한다.\n\n10. 구운 소갈빗살을 오븐용 그릇에 담는다.\n\n11. 소갈빗살 위에 시금치크림볶음을 올린다.\n\n12. 11에 달걀을 올린 뒤 오븐에 넣어 익힌다.\n\n13. 부추를 잘게 썬다.\n\n14. 요거트드레싱, 레몬즙, 잘게 썬 부추, 다진 마늘을 섞어 요거트소스를 만든다.\n\n15. 오븐에서 소갈빗살과 함께 익힌 시금치크림볶음을 꺼낸 뒤 요거트소스를 올리고, 후추를 뿌려 마무리한다.'),
	(725, '냉쌈온쌈', NULL, 1, '1. 양파를 채 썬다.\n\n2. 채 썬 양파를 얼음물에 담가 아린 맛을 뺀다.\n\n3. 고춧가루, 멸치액젓, 간장, 설탕, 식초, 참기름, 다진 마늘, 후추를 섞어 양념장을 만든다.\n\n4. 양념장에 물엿을 넣는다.\n\n5. 소고기안심을 적당한 크기로 썬다.\n\n6. 소고기안심에 참기름, 간장, 설탕을 넣고 버무린다.\n\n7. 골뱅이의 표면을 거칠게 만든다.\n\n8. 밀가루와 전분, 물을 섞어 만든 반죽에 다진 마늘과 소금을 넣어 간한다.\n\n9. 골뱅이에 반죽을 묻혀 기름에 튀긴다.\n\n10. 튀긴 골뱅이를 건져 기름기를 뺀다.\n\n11. 달래를 적당한 크기로 썬다.\n\n12. 달래와 아린 맛을 뺀 양파에 양념장을 섞어 달래양념장을 만든다.\n\n13. 접시에 양념한 소고기육회를 담고 달걀노른자를 얹는다.\n\n14. 접시에 골뱅이튀김을 담는다.\n\n15. 밥에 소금과 참기름으로 간한 뒤 그릇에 담는다.\n\n16. 밥과 육회에 깨를 뿌린다.\n\n17. 쌈채소를 곁들인다.'),
	(726, '오윤기 쌈밥', NULL, 1, '1. 전자레인지에 즉석밥을 넣고 돌린다.\n\n2. 인스턴트 사골곰탕을 끓인다.\n\n3. 두부, 부추, 맛타리버섯을 적당한 크기로 썰어 사골곰탕에 넣고 끓인다.\n\n4. 오이를 얇게 슬라이스 한 뒤, 소금에 절인다.\n\n5. 두부를 으깬다.\n\n6. 으깬 두부에 소고기고추장볶음을 올리고 동그랗게 뭉친 뒤, 전분을 묻혀 기름에 튀긴다.\n\n7. 밥에 으깬 명란, 마요네즈, 파르메산치즈가루를 섞는다.\n\n8. 7을 동그랗게 뭉친 뒤, 전분을 묻힌다.\n\n9. 전분을 묻힌 명란밥을 채 썬 감자로 싼 뒤, 기름에 튀긴다.\n\n10. 골뱅이를 적당한 크기로 썬다.\n\n11. 마른 팬에 적당한 크기로 썬 골뱅이와 된장을 넣고 볶는다.\n\n12. 소금에 절인 오이를 물에 헹군 뒤, 물기를 짠다.\n\n13. 잘게 다진 양파를 팬에 볶는다.\n\n14. 볶던 양파에 밥과 춘장을 넣고 볶는다.\n\n15. 접시에 적겨자잎과 알배추잎을 깔고, 그 위에 춘장쌈밥과 감자명란쌈밥을 올린다.\n\n16. 밥에 된장에 볶은 골뱅이를 넣고 동그랗게 뭉친 뒤, 절인 오이로 감싼다.\n\n17. 소고기안심을 고기망치로 두드려 얇게 펴고, 소금으로 간 한다.\n\n18. 육포를 믹서기에 갈아 육포보푸라기를 만든다.\n\n19. 밥에 소고기고추장볶음을 넣고 동그랗게 뭉친 뒤, 얇게 편 소고기로 감싼다.\n\n20. 소고기쌈밥을 토치로 직화한 뒤 접시에 담고, 육포보푸라기를 올린다.\n\n21. 잘게 썬 부추와 꽈리고추에 물엿과 깨를 넣고 버무린다.\n\n22. 두부쌈 위에 양념한 부추, 파, 꽈리고추를 올린다.\n\n23. 사골된장국을 그릇에 담아낸다.'),
	(727, '복칼로리 피자', NULL, 1, '1. 밀가루에 올리브오일, 뜨거운 물, 소금을 넣고 익반죽 한다.\n\n2. 비닐을 넓게 펼친 뒤 밀가루, 소금을 뿌려둔다.\n\n3. 닭가슴살을 얇게 포 뜬 뒤, 밀가루와 소금을 묻힌다.\n\n4. 닭가슴살을 비닐로 덮은 뒤 밀대로 밀어 납작하게 편다.\n\n5. 반죽을 넓게 펴 도우를 만든다.\n\n6. 기름을 두른 팬에 도우를 굽는다.\n\n7. 밀가루를 묻힌 닭가슴살을 기름을 두른 팬에 굽는다.\n\n8. 적당한 크기로 썬 돼지고기 목살에 다진 마늘, 대파, 양파를 썰어 넣는다.\n\n9. 7에 설탕, 맛술, 간장을 넣고 양념한 뒤, 팬에 볶다가 후추를 넣는다.\n\n10. 팬에 버터, 다진 양파를 넣고 볶다가 인스턴트 양송이 수프를 넣는다.\n\n11. 7에 브리치즈, 스트링치즈, 밀가루를 넣어 볶아 크림소스를 만든다.\n\n12. 구운 도우를 그릇에 담는다.\n\n13. 도우 위에 케첩을 뿌린다.\n\n14. 도우 위에 구운 닭가슴살과 돼지목살볶음을 올린다.\n\n15. 돼지목살 위에 크림소스를 올린 뒤 잘게 찢은 게맛살을 얹는다.\n\n16. 피자 위에 브리치즈, 스트링치즈를 올린다.\n\n17. 피자를 오븐에 넣어 치즈를 녹인다.\n\n18. 샐러드 채소, 딸기, 블루베리를 접시에 담는다.\n\n19. 올리브오일, 메이플시럽, 레몬즙을 섞어 드레싱을 만든 뒤 샐러드에 뿌린다.\n\n20. 오븐에서 꺼낸 피자에 아보카도를 올린다.\n\n21. 아보카도를 올린 피자를 토치로 직화해 마무리한다.'),
	(728, '도리아랑', NULL, 1, '1. 달걀노른자에 밀가루, 설탕, 바나나우유를 섞어 커스터드크림을 만든다.\n\n2. 커스터드크림을 전자레인지에 넣고 돌린다.\n\n3. 기름을 두른 팬에 달걀을 넣고 에그 스크램블을 만들어 따로 담아놓는다.\n\n4. 대파, 양파를 잘게 다져 팬에 볶는다.\n\n5. 돼지목살을 적당한 크기로 썬다.\n\n6. 재료를 볶던 팬에 돼지목살, 에그 스크램블, 밥, 소금, 버터를 넣는다.\n\n7. 볼에 달걀노른자와 생초콜릿, 밀가루를 넣고 섞는다.\n\n8. 달걀흰자를 섞어 거품을 낸 뒤 달걀과 밀가루를 섞은 초콜릿을 섞어 초콜릿 반죽을 만든다.\n\n9. 초콜릿 반죽에 식용유를 넣은 뒤 컵에 담는다.\n\n10. 전자레인지에 돌린 커스터드크림을 얼음물에 올려 차게 식힌다.\n\n11. 초콜릿반죽을 전자레인지에 넣고 돌린다.\n\n12. 커스터드크림에 꿀을 넣고 섞는다.\n\n13. 전자레인지에 돌린 초콜릿 케이크를 꺼낸다.\n\n14. 초콜릿 케이크를 적당한 크기로 나눈다.\n\n15. 냄비에 밀가루, 버터를 넣고 볶아 루를 만든다.\n\n16. 루에 양송이수프, 우유, 게맛살을 넣고 끓여 크림소스를 만든다.\n\n17. 딸기를 적당한 크기로 썬다.\n\n18. 유리컵에 커스터드크림>딸기>초콜릿 케이크>블루베리 순으로 층층이 쌓는다.\n\n19. 커스터드크림에 살구잼을 넣어 초콜릿 케이크 위에 올린다.\n\n20. 믹서기에 설탕을 넣고 갈아 슈가파우더를 만든 뒤 초콜릿 컵케이크 위에 뿌린다.\n\n21. 무쇠팬에 크림소스를 깔고 볶음밥>크림소스>잘게 찢은 스트링치즈를 올린다.\n\n22. 무쇠팬 위에 다른 무쇠팬을 달궈 올린 뒤 토치로 가열해 치즈를 녹인다.'),
	(729, '전주행', NULL, 1, '1. 돼지목살을 적당한 크기로 썬다.\n\n2. 기름을 두른 팬에 목살과 설탕을 넣고 볶는다.\n\n3. 대파, 양파, 홍고추를 팬에 넣고 함께 볶는다.\n\n4. 3에 고춧가루를 넣는다.\n\n5. 볶던 재료에 고추장, 간장, 후추, 참기름, 다진 마늘, 맛술, 물엿을 넣는다.\n\n6. 기름을 두른 냄비에 돼지목살, 된장, 설탕, 양파를 넣는다.\n\n7. 6에 물을 부은 뒤 채 썬 홍고추, 고춧가루, 간장을 넣어 끓인다.\n\n8. 볼에 달걀을 넣고 부드럽게 풀어준다.\n\n9. 달걀물에 대파를 잘게 썰어 넣는다.\n\n10. 기름을 두른 팬에 달걀물을 부어 굽는다.\n\n11. 된장찌개에 파를 썰어 넣는다.\n\n12. 달걀을 말아준 뒤, 달걀물을 더 부어 익힌다.\n\n13. 전자레인지에 즉석밥을 넣고 돌린다.\n\n14. 달걀을 한 번 더 말아준다.\n\n15. 달걀말이를 썰어 접시에 담는다.\n\n16. 종지에 케첩을 따로 낸다.\n\n17. 제육볶음을 접시에 담는다.\n\n18. 된장찌개를 양은냄비에 담은 뒤 연두부를 썰어 넣는다.\n\n19. 즉석밥을 그릇에 따로 담아낸다.'),
	(730, '집밥 정선생', NULL, 1, '1. 뚝배기에 불린 쌀, 물을 넣고 밥을 짓는다.\n\n2. 돼지목살을 고기망치로 두드린 뒤 적당한 크기로 썬다.\n\n3. 고추장, 고춧가루, 간장, 물엿, 설탕을 넣고 섞는다.\n\n4. 4에 다진 마늘, 맛술을 넣은 뒤 돼지목살을 넣어 재워둔다.\n\n5. 양파, 대파를 썰어 기름을 두른 팬에 볶는다.\n\n6. 채소를 볶던 팬에 제육볶음양념에 재운 목살을 넣고 볶는다.\n\n7. 석쇠에 대파를 올린 뒤 제육볶음을 올려 굽는다.\n\n8. 석쇠에 구운 제육볶음을 팬에 넣고 한 번 더 볶는다.\n\n9. 냄비에 물, 황태국밥 수프를 넣고 끓여 황태육수를 만든다.\n\n10. 달걀물에 설탕, 소금, 잘게 찢은 게맛살을 넣고 섞는다.\n\n11. 달걀물에 황태육수를 넣고, 잘게 썬 대파를 넣는다.\n\n12. 뚝배기에 달걀물을 넣고 익힌다.\n\n13. 뚝배기에 뚜껑을 덮어 익힌다.\n\n14. 볼에 게맛살, 시리얼, 블루베리, 마요네즈, 샐러드 채소를 넣고 버무려 게살샐러드를 만든다.\n\n15. 제육볶음을 접시에 담고, 잘게 썬 홍고추, 참기름, 깨를 뿌린다.\n\n16. 달걀찜에 잘게 썬 대파, 홍고추, 깨, 참기름을 뿌려 완성한다.'),
	(731, '이츠 대파타임', NULL, 1, '1. 핸드블렌더에 밀가루, 달걀을 넣고 갈아 면반죽을 만든다.\n\n2. 그릴 팬에 청양고추를 넣고 올리브오일을 뿌려 굽는다.\n\n3. 소고기등심을 먹기 좋은 크기로 잘라, 전분을 묻힌다.\n\n4. 기름을 두른 팬에 전분을 묻힌 소고기등심을 굽는다.\n\n5. 마늘, 양파, 대파를 썬 뒤, 기름을 두른 팬에 볶는다.\n\n6. 볶던 채소에 소금, 후추로 간한다.\n\n7. 적당한 크기로 썬 대파를 끓는 기름에 넣어 파기름을 낸다.\n\n8. 볶던 채소에 파기름을 넣고, 파기름을 낸 대파는 건져낸다.\n\n9. 파기름에 고춧가루를 넣어 고추기름을 낸다.\n\n10. 면반죽을 밀대로 민 뒤, 적당한 크기로 썰어 면을 뽑는다.\n\n11. 볶던 채소에 구운 소고기등심을 넣고, 간장, 맛술, 설탕, 고춧가루, 파기름을 냈던 대파를 넣고 볶는다.\n\n12. 소금을 넣은 물에 면을 넣고 삶는다.\n\n13. 삶은 면을 볶던 재료와 함께 볶아 볶음면을 만든다.\n\n14. 구운 청양고추를 적당한 크기로 썬 뒤, 올리브오일과 후추, 핫소스, 레몬즙을 넣고 절인다.\n\n15. 대파를 채 썬다.\n\n16. 접시에 볶음면을 담고, 채 썬 대파를 올린다.\n\n17. 그릇에 구운 청양고추 절임을 담아낸다.\n\n18. 볶음면에 깨를 뿌리고, 레몬을 곁들여 마무리 한다.'),
	(732, '갓다니엘 정식', NULL, 1, '1. 김을 뜨거운 물에 불린다.\n\n2. 핸드블렌더에 즉석밥, 뜨거운 물, 소금을 넣고 갈아 떡반죽을 만든다.\n\n3. 대파를 적당한 크기로 썬 뒤, 원형 모양을 살려 한 겹씩 벗겨낸다.\n\n4. 대파를 소금물에 데친다.\n\n5. 데친 대파를 얼음물에 담가둔다.\n\n6. 지퍼백에 떡반죽을 넣고, 모서리를 잘라 짤주머니를 만든다.\n\n7. 떡반죽으로 대파의 속을 채워 대파떡을 만든다.\n\n8. 지퍼백에 통후추를 넣고 고기망치로 두드려 부순다.\n\n9. 마른 팬에 소고기등심을 굽다가 소금으로 간 한다.\n\n10. 채 썬 양파와 청양고추를 팬에 볶다가 후춧가루, 우유를 넣어 후추크림소스를 만든다.\n\n11. 끓는 물에 라면을 삶는다.\n\n12. 후추크림소스에 슬라이스 모차렐라치즈를 넣는다.\n\n13. 12에 불린 김을 넣는다.\n\n14. 육포를 잘게 다져 후추크림소스에 넣는다.\n\n15. 구운 소고기등심을 적당한 크기로 썬다.\n\n16. 꼬치에 소고기등심>대파떡>소고기등심>대파떡 순서로 꽂는다.\n\n17. 기름을 두른 팬에 소고기대파떡꼬치를 굽는다.\n\n18. 불닭소스와 짜장소스를 섞어 불닭짜장소스를 만든다.\n\n19. 불닭짜장소스를 소고기대파떡꼬치에 발라가며 굽는다.\n\n20. 삶은 라면을 얼음물에 담가 식힌다.\n\n21. 후추크림소스에 삶은 라면과 버터를 넣고 볶아 후추김파스타를 만든다.\n\n22. 접시에 후추김파스타를 담는다.\n\n23. 소고기대파떡꼬치에 불닭라면플레이크, 후추를 뿌린다.'),
	(733, '레옹 정식', NULL, 1, '1. 즉석밥을 전자레인지에 넣고 돌린다.\n\n2. 양파를 잘게 다진다.\n\n3. 삼겹살, 소고기등심을 썰어 핸드블렌더에 넣고 간다.\n\n4. 핸드블렌더에 옥수수과자를 넣고 간다.\n\n5. 간 삼겹살, 소고기등심에 간 옥수수과자와 소금, 후추를 넣는다.\n\n6. 5에 달걀물을 넣고 섞어 고기반죽을 만든다.\n\n7. 밥에 소금과 후추, 깨로 간 한 뒤 삼각형 모양으로 뭉쳐 기름을 두른 팬에 굽는다.\n\n8. 넓게 편 고기반죽 위에 구운 밥, 슬라이스 모차렐라치즈를 올린 뒤 고기반죽으로 감싼다.\n\n9. 기름을 두른 팬에 삼각고기밥을 굽는다.\n\n10. 식초를 넣은 물에 라면 면을 삶는다.\n\n11. 삶은 라면을 얼음물에 담가둔다.\n\n12. 양파, 대파, 청양고추를 썰어 기름을 두른 팬에 볶는다.\n\n13. 볶던 채소에 라면수프, 족발을 넣고 볶는다.\n\n14. 볶던 재료에 물을 넣고 끓여 족발국물을 만든다.\n\n15. 면수에 달걀을 넣어 수란을 만든다.\n\n16. 끓이던 족발국물에 건더기수프를 넣는다.\n\n17. 삼각고기밥 위에 슬라이스 모차렐라치즈를 올려 굽는다.\n\n18. 족발국물에 삶은 라면 면을 넣고 끓인다.\n\n19. 족발라면을 그릇에 담는다.\n\n20. 족발라면 위에 수란을 올린다.\n\n21. 상추를 올린 접시에 삼각고기밥을 담아낸다.'),
	(734, '카르옹나라', NULL, 1, '1. 전분과 밀가루, 소금에 뜨거운 물을 넣고 익반죽을 한다.\n\n2. 반죽을 동그랗게 빚어 옹심이를 만든다.\n\n3. 삼겹살을 잘게 썬다.\n\n4. 삼겹살에 쌍화음료를 넣고 버무린다.\n\n5. 4에 간장, 맛술, 전분을 넣고 섞은 뒤 기름에 튀기듯이 볶는다.\n\n6. 채 썬 양파와 으깬 마늘을 팬에 넣는다.\n\n7. 6에 버터, 후추, 밀가루, 간장, 소금을 넣고 볶다가 물을 넣고 끓인다.\n\n8. 끓이던 채소에 우유와 라면수프를 넣어 카르보나라 소스를 만든다.\n\n9. 카르보나라 소스에 옹심이를 넣고 익힌다.\n\n10. 청양고추를 핸드블렌더에 간 뒤, 옹심이 카르보나라에 넣는다.\n\n11. 팬에 슬라이스 모차렐라치즈, 우유를 넣고 끓여 치즈소스를 만든다.\n\n12. 옹심이 카르보나라를 그릇에 담고, 볶은 삼겹살과 치즈소스를 올린다.'),
	(735, '게 섞어라', NULL, 1, '1. 끓는 물에 멸치볶음을 데친다.\n\n2. 찐 꽃게의 살을 발라낸다.\n\n3. 기름을 두른 팬에 잘게 썬 총각김치와 밥을 넣고 볶는다.\n\n4. 통조림햄, 소고기안심을 잘게 썰어 기름을 두른 팬에 볶는다.\n\n5. 두 개의 팬에 버터를 녹인다.\n\n6. 버터를 녹인 팬에 밀가루와 뜨거운 물을 넣고 끓여 루를 만든다.\n\n7. 버터를 녹인 다른 팬에 삶은 옥수수알과 설탕을 넣고 볶는다.\n\n8. 볶던 밥에 데친 멸치볶음과 고추장, 고춧가루, 굴소스를 넣는다.\n\n9. 8에 잘게 썬 대파, 볶은 햄과 소고기를 넣고 함께 볶는다.\n\n10. 루에 게살을 넣고 소금, 후추로 간한 뒤, 모차렐라치즈를 넣고 끓여 게살크림소스를 만든다.\n\n11. 총각김치볶음밥을 밥그릇에 눌러 담아 모양을 잡는다.\n\n12. 마른 팬에 총각김치볶음밥을 그릇째 뒤집어 올려 누룽지를 만든다.\n\n13. 달걀흰자에 설탕을 넣고 머랭을 친다.\n\n14. 달걀노른자에 머랭을 넣고 섞어 달걀반죽을 만든다.\n\n15. 버터를 녹인 팬에 달걀반죽을 넣고, 볶은 햄과 소고기, 머랭을 올려 굽는다.\n\n16. 15를 반으로 접어 익힌다.\n\n17. 그릇에 총각김치볶음밥을 담는다.\n\n18. 총각김치볶음밥에 게살크림소스를 담는다.\n\n19. 총각김치볶음밥 위에 달걀 디저트를 올린다.\n\n20. 버터에 볶은 삶은 옥수수알을 뿌려 마무리한다.'),
	(736, '꽃게를 든 남자', NULL, 1, '1. 삶은 옥수수의 알을 발라낸다.\n\n2. 비닐팩에 찐 꽃게와 적당한 크기로 썬 양파, 대파, 당근과 마늘을 넣고 고기망치로 두드려 으깬다.\n\n3. 기름을 두른 냄비에 으깬 꽃게와 채소, 물을 넣고 끓여 육수를 낸다.\n\n4. 육수에 된장을 넣고 끓인다.\n\n5. 4에 토마토소스, 칼국수라면 면을 넣고 끓여 비스크소스를 만든다.\n\n6. 달걀흰자를 분리한다.\n\n7. 달걀흰자로 머랭을 친 뒤, 소금, 후추, 검은콩두유를 넣고 섞는다.\n\n8. 핸드블렌더에 삶은 약밤, 검은콩두유를 넣고 갈아 밤페이스트를 만든다.\n\n9. 머그잔으로 비닐백의 모양을 잡은 뒤, 올리브오일을 바른다.\n\n10. 비닐백에 밤페이스트를 담은 뒤, 머랭을 넣고 밀봉한다.\n\n11. 10을 끓는 물에 넣고 익힌다.\n\n12. 양파, 통조림햄, 김치를 잘게 썰어 기름을 두른 팬에 넣고 볶는다.\n\n13. 볶던 재료에 밥을 넣고 볶다가 굴소스, 소금, 후추로 간 한다.\n\n14. 비스크소스를 믹서기에 넣고 간다.\n\n15. 믹서기에 간 비스크소스를 체에 걸러 한 번 더 끓인다.\n\n16. 비스크소스에 버터, 삶은 옥수수알을 넣고 끓인다.\n\n17. 끓는 물에 익힌 몽블랑달걀찜을 전자레인지에 넣고 한 번 더 익힌다.\n\n18. 그릇에 김치볶음밥을 담는다.\n\n19. 김치볶음밥 위에 몽블랑달걀찜을 올린다.\n\n20. 비스크소스를 따로 담아낸다.'),
	(737, '꽃게길만 걷자', NULL, 1, '1. 끓는 기름에 적당한 크기로 썬 대파, 고춧가루를 넣어 고추기름을 낸다.\n\n2. 압력솥에 김치, 물을 넣고 끓인다.\n\n3. 찐 꽃게의 살을 발라낸다.\n\n4. 으깬 마늘, 잘게 썬 대파를 기름을 두른 팬에 볶는다.\n\n\n5. 소고기안심과 불린 수제육포를 적당한 크기로 썬다.\n\n6. 적당한 크기로 썬 소고기안심과 수제육포를 핸드블렌더에 넣고 간다.\n\n7. 볶던 채소에 간 소고기와 육포, 간장, 청주를 넣고 볶은 뒤 김치를 끓이던 압력솥에 넣는다.\n\n8. 7에 인스턴트 만두를 넣고 뚜껑을 닫아 푹 끓인다.\n\n9. 불린 수제육포와 닭갈비, 대파를 잘게 썬 뒤 기름을 두른 팬에 볶는다.\n\n10. 볶던 재료에 굴소스를 넣고 볶는다.\n\n11. 삶은 약밤을 잘게 썬다.\n\n12. 잘게 썬 삶은 약밤에 밥, 참기름, 볶은 재료를 넣고 비빈다.\n\n13. 비빈 밥을 그릇에 담고 게살을 올린 뒤, 찜기에 넣고 찐다.\n\n14. 고추기름을 체에 거른다.\n\n15. 잘게 썬 대파, 다진 마늘, 간장, 맛술, 레몬즙, 고추기름을 넣어 간장소스를 만든다.\n\n16. 간장소스를 종지에 담는다.\n\n17. 찐 밥을 뚝배기에 옮겨 담은 뒤, 한 번 더 익힌다.\n\n18. 기름을 두른 팬에 쑥개떡을 튀기듯 굽는다.\n\n19. 압력솥에서 끓인 김치전골을 뚝배기에 옮겨 담는다.\n\n20. 김치전골에 구운 쑥개떡을 올려 마무리한다.'),
	(738, '구구닭', NULL, 1, '1. 인스턴트 만두소를 분리한다.\n\n2. 으깬 마늘, 적당한 크기로 썬 대파와 양파, 만두소에 청주, 물을 넣고 끓여 육수를 낸다.\n\n3. 팬에 닭갈비, 청주, 자른 대파와 양파를 넣고 볶는다.\n\n4. 쑥개떡을 잘게 썰어 닭갈비에 넣고 볶는다.\n\n5. 닭갈비에 끓인 육수를 넣어 물닭갈비를 만든다.\n\n6. 물닭갈비에 잘게 썬 청양고추, 홍고추를 넣는다.\n\n7. 믹서기에 수제육포를 넣고 간다.\n\n8. 밀가루, 튀김가루, 물을 섞어 반죽을 만들고, 부추겉절이를 잘라 넣는다.\n\n9. 반죽에 간 육포를 넣고 섞는다.\n\n10. 기름을 두른 팬에 부추육포반죽을 올려 부친다.\n\n11. 청양고추, 홍고추, 양파를 잘게 다진다.\n\n12. 11에 설탕, 간장, 식초, 고춧가루를 넣고 섞어 간장소스를 만든 뒤, 종지에 담는다.\n\n13. 끓는 물에 칼국수라면 면을 삶는다.\n\n14. 다진 마늘과 고춧가루를 섞어 양념을 만든 뒤, 물닭갈비에 넣는다.\n\n15. 삶은 칼국수면을 물닭갈비에 넣고 완성한다.'),
	(739, '최용수테이크', NULL, 1, '1. 도미 머리를 잘라 기름을 두른 압력솥에 넣고 굽는다.\n\n2. 구운 도미 머리에 적당한 크기로 썬 양파, 으깬 마늘, 청양고추, 송이토마토를 넣는다.\n\n3. 2에 청주와 맛술, 끓는 물을 붓고 끓이다가 수제된장, 수제고추장, 토마토소스를 넣은 뒤 압력솥 뚜껑을 닫고 푹 끓인다.\n\n4. 도미의 뼈를 발라낸 뒤, 도미 껍질에 칼집을 낸다.\n\n5. 칼집을 낸 도미에 뜨거운 물을 부어 데친다.\n\n6. 데친 도미를 얼음물에 담가둔다.\n\n7. 도미의 물기를 제거하고 소금으로 간 한 뒤, 전분을 묻힌다.\n\n8. 기름을 두른 팬에 전분을 묻힌 도미를 굽는다.\n\n9. 적당한 크기로 썬 감자와 아스파라거스에 소금, 후추로 간한 뒤 도미를 굽던 팬에 함께 굽는다.\n\n10. 끓는 물에 칼국수면을 삶는다.\n\n11. 찜기에 물, 레몬즙과 레몬을 넣고 끓인다.\n\n12. 찜기에 구운 도미를 넣고 찐다.\n\n13. 마른 팬에 버터, 밀가루를 넣어 루를 만든다.\n\n14. 압력솥에서 도미 머리를 건져 내고 육수를 핸드블렌더로 간 뒤, 루를 넣고 섞어 토마토고추장소스를 만든다.\n\n15. 삶은 칼국수면에 올리브오일을 넣고 버무린 뒤 그릇에 담는다.\n\n16. 구운 감자 위에 스트링치즈를 올린 뒤, 토치로 직화해 치즈를 녹인다.\n\n17. 16을 구운 도미 배 사이에 넣는다.\n\n18. 토마토고추장소스에 설탕을 더한 뒤, 칼국수면 위에 뿌린다.\n\n19. 18에 도미스테이크를 올린 뒤 구운 아스파라거스를 곁들인다.\n\n20. 도미스테이크에 올리브오일을 뿌린다.\n\n21. 도미스테이크에 잘게 썬 대파를 올려 마무리한다.'),
	(740, '테린우스', NULL, 1, '1. 자반고등어를 적당한 크기로 썬다.\n\n2. 끓는 물에 올리브오일과 식초, 버터를 넣은 뒤 손질한 고등어를 넣고 데친다.\n\n3. 고등어를 데치는 물에 소금을 넣는다.\n\n4. 전복의 살과 내장을 분리한다.\n\n5. 고등어를 데치는 냄비에 전복살을 넣은 뒤, 레몬즙을 더해 삶는다.\n\n6. 데친 고등어의 뼈를 발라낸다.\n\n7. 마른 팬에 마늘, 전복내장을 넣은 뒤, 올리브오일을 넣고 볶는다.\n\n8. 볶던 재료에 적당한 크기로 썬 양파, 간장, 맛술, 설탕, 물, 대파를 넣고 끓여 간장소스를 만든다.\n\n9. 올리브오일과 식초, 버터를 넣은 끓는 물에 적당한 크기로 썬 아스파라거스와 대파를 데친다.\n\n10. 카망베르치즈와 크림치즈를 섞는다.\n\n11. 고등어살, 달걀물, 트러플페스토를 핸드블렌더에 넣고 간다.\n\n12. 말린 채소칩을 핸드블렌더에 넣고 간다.\n\n13. 원형틀에 명이나물을 올리고, 트러플페스토를 더해 간 고등어와 데친 아스파라거스, 대파를 얹는다.\n\n14. 원형틀에 채 썬 파프리카를 얹는다.\n\n15. 14 위에 삶은 전복을 올리고 트러플페스토를 더해 간 고등어를 얹은 뒤, 명이나물로 감싸 찜기에 넣고 찐다.\n\n16. 무를 오각형 막대모양으로 손질한다.\n\n17. 접시에 체에 곱게 거른 초록색 채소칩가루를 뿌린다.\n\n18. 섞은 치즈를 아이스크림 스쿱으로 동그랗게 모양낸 뒤, 채소칩가루 위에 얹는다.\n\n19. 모양을 낸 무에 초록색 채소칩가루와 갈색 채소칩가루를 묻힌 뒤, 치즈볼에 축구공 무늬를 찍는다.\n\n20. 찜기에 익힌 고등어테린을 반으로 자른다.\n\n21. 끓이던 간장소스를 체에 밭쳐 국물만 걸러낸 뒤, 한 번 더 끓인다.\n\n22. 간장소스에 물을 넣는다.\n\n23. 접시에 고등어테린을 올린다.\n\n24. 간장소스에 버터를 넣고 녹인 뒤, 고등어테린 위에 뿌린다.\n\n25. 고등어테린에 갈색 채소칩가루와 트러플오일을 뿌려 완성한다.'),
	(741, '독수리 세트', NULL, 1, '1. 끓는 기름에 통마늘을 튀긴다.\n\n2. 대파와 삼겹살을 적당한 크기로 썰어 기름을 두른 팬에 볶는다.\n\n3. 낙지 다리를 적당한 크기로 잘라 볶던 재료와 함께 볶는다.\n\n4. 3에 채 썬 양파, 홍고추, 청양고추, 다진 마늘, 간장, 고춧가루, 굴소스, 설탕, 맛술, 채 썬 깻잎을 넣고 볶아 낙지삼겹살볶음을 만든다.\n\n5. 소고기를 적당한 크기로 썰어 핸드블렌더에 넣고 간다.\n\n6. 기름을 두른 팬에 다진 마늘, 간 소고기, 고추장, 간장을 넣고 볶는다.\n\n7. 소고기고추장볶음을 종지에 담고, 다진 쪽파를 올린다.\n\n8. 기름을 두른 팬에 간 소고기, 다진 마늘, 다진 양파, 콩나물을 넣고 볶는다.\n\n9. 9에 굴소스, 다진 홍고추, 밥을 넣고 볶다가 적당한 크기로 썬 전복, 달걀, 다진 쪽파를 넣어 전복소고기볶음밥을 만든다.\n\n10. 전복소고기볶음밥을 뚝배기에 옮겨 담은 뒤, 한 번 더 익힌다.\n\n11. 칼국수면을 삶은 뒤, 소고기고추장볶음을 넣고 볶는다.\n\n12. 접시에 낙지삼겹살볶음을 담은 뒤, 볶은 칼국수면을 곁들인다.\n\n13. 12에 다진 쪽파를 올린다.\n\n14. 전복소고기볶음밥에 튀긴 통마늘을 올려 마무리한다.'),
	(742, '오복쟁반', NULL, 1, '1. 삼겹살을 적당한 크기로 썰어 냄비에 볶는다.\n\n2. 볶던 삼겹살에 인스턴트 들깨삼계탕 국물만 넣고 끓인다.\n\n3. 끓는 물에 전복을 데친다.\n\n4. 데친 전복의 살과 내장을 발라낸다.\n\n5. 적당한 크기로 썬 쪽파를 전골냄비에 담는다.\n\n6. 데친 전복살에 칼집을 낸 뒤 적당한 크기로 썰어 전골냄비에 담는다.\n\n7. 끓는 물에 달걀을 삶는다.\n\n8. 기름을 두른 팬에 데친 전복내장을 볶는다.\n\n9. 볶던 전복내장에 다진 양파, 절인 복분자, 청주, 고추장을 넣고 끓여 전복내장고추장을 만든다.\n\n10. 전골냄비에 적당한 크기로 썬 표고버섯을 담는다.\n\n11. 끓이던 육수에 양파즙, 간장, 굴소스를 넣는다.\n\n12. 소금으로 간 한 소고기를 그릴팬에 구운 뒤, 적당한 크기로 썰어 전골냄비에 담는다.\n\n13. 낙지다리를 고기망치로 두드려 부드럽게 만든 뒤, 전골냄비에 담는다.\n\n14. 홍고추와 깻잎을 적당한 크기로 썰어 전골냄비에 담는다.\n\n15. 볼에 적당한 크기로 썬 대파와 식용유, 고춧가루를 담고 전자레인지에 돌려 고추기름을 낸다.\n\n16. 끓인 전복내장고추장을 핸드블렌더에 넣고 간 뒤, 참기름을 더한다.\n\n17. 전복내장고추장에 고추기름을 넣는다.\n\n18. 끓이던 육수를 전골냄비에 붓고 끓인다.\n\n19. 우동면에 끓는 물을 부어 데친 후 전골냄비에 담는다.\n\n20. 삶은 달걀의 껍데기를 벗겨 전골냄비에 담는다.\n\n21. 전복내장고추장을 종지에 담는다.\n\n22. 잘게 썬 청양고추를 전복내장고추장 위에 올려 마무리한다.'),
	(743, '불닭 카리스마', NULL, 1, '1. 기름에 고춧가루와 적당한 크기로 썬 대파를 넣고 끓여 고추기름을 낸다.\n\n2. 밀가루, 전분, 소금, 물, 달걀물, 식용유를 섞어 튀김반죽을 만든다.\n\n3. 닭은 뼈를 발라낸 뒤, 튀김반죽을 입혀 기름에 튀긴다.\n\n4. 달걀, 우유, 물, 컵우동소스를 섞어 달걀물을 만든다.\n\n5. 큰 그릇 안에 작은 그릇을 넣은 뒤, 달걀물을 붓는다.\n\n6. 달걀물이 담긴 그릇을 전자레인지에 돌려 달걀찜을 만든다.\n\n7. 양파는 채 썰고, 양배추, 청양고추, 대파는 잘게 썬다.\n\n8. 고추기름을 체에 밭쳐 거른다.\n\n9. 팬에 고추기름을 두른 뒤, 손질한 채소를 넣고 볶는다.\n\n10. 볶던 채소에 고추장, 고춧가루, 설탕, 간장, 물, 삶은 메추리알, 적당한 크기로 썬 아스파라거스를 넣고 볶는다.\n\n11. 연두부에 십자(十)로 칼집을 낸 뒤, 기름에 튀긴다.\n\n12. 뜨거운 물에 데친 우동면과 적당한 크기로 썬 차돌박이를 볶던 채소에 넣고 함께 볶는다.\n\n13. 닭튀김을 체에 밭쳐 기름을 뺀다.\n\n14. 볶던 재료에 닭튀김을 넣고 볶아 불닭을 만든다.\n\n15. 간장, 물엿, 식초, 고추기름, 잘게 다진 쪽파를 섞어 소스를 만든다.\n\n16. 양배추를 채 썰어 그릇에 담는다.\n\n17. 채 썬 양배추 위에 연두부튀김을 올리고 소스를 뿌린다.\n\n18. 전자레인지에 익힌 달걀찜을 꺼내 작은 그릇을 빼낸 뒤, 닭튀김을 올린다.\n\n19. 닭튀김이 담긴 달걀찜그릇에 불닭도 함께 담아낸다.'),
	(744, '오~ 야식 코리아♬', NULL, 1, '1. 끓는 물에 우동면을 삶는다.\n\n2. 청양고추를 석쇠에 올려 직화한다.\n\n3. 소고기등심과 양념돼지목살을 핸드블렌더에 넣고 간다.\n\n4. 간 고기에 밀가루와 후추를 넣고 치대어 패티 반죽을 만든다.\n\n5. 삶은 우동면에 컵우동소스로 간한 뒤, 전분을 섞는다.\n\n6. 우동면을 원형틀에 넣고 구워 우동번을 만든다.\n\n7. 패티 반죽을 빚어 모양 잡은 뒤, 기름을 두른 팬에 굽는다.\n\n8. 기름을 두른 팬에 슬라이스한 양파를 굽는다.\n\n9. 구운 청양고추와 방울토마토를 핸드블렌더에 넣고 간다.\n\n10. 기름을 두른 팬에 다진 마늘, 고춧가루, 케첩, 뜨거운 물, 간 청양고추와 방울토마토, 간장, 물엿, 설탕, 고추장을 넣고 끓여 소스를 만든다.\n\n11. 소스에 구운 패티와 물을 넣고 조린다.\n\n12. 씻어서 불린 진미채에 컵우동소스로 간한 뒤, 튀김가루를 묻혀 기름에 튀긴다.\n\n13. 양배추를 채 썰어 물에 담가둔다.\n\n14. 우동번 위에 샐러드채소>방울토마토>패티>구운 양파>패티 순으로 올린다.\n\n15. 달걀프라이를 만든다.\n\n16. 패티 위에 채 썬 양배추를 올리고, 진미채튀김과 우동번을 곁들인다.\n\n17. 버거 위에 달걀프라이를 올려 완성한다.'),
	(745, '입에 불나게', NULL, 1, '1. 핸드블렌더에 청양고추, 홍고추, 마늘, 적당한 크기로 썬 셀러리를 넣고 간다.\n\n2. 기름을 두른 팬에 간 채소를 넣고 볶는다.\n\n3. 볶던 재료에 설탕을 넣고 볶는다.\n\n4. 볶던 재료에 컵우동소스로 간한 뒤 느타리버섯을 넣는다.\n\n5. 소금을 넣은 뜨거운 물에 게맛살을 담가둔다.\n\n6. 방울토마토를 반으로 자른 뒤, 볶던 재료에 넣고 볶아 칠리소스를 만든다.\n\n7. 게맛살을 게 집게 모양으로 자른다.\n\n8. 게맛살에 전분을 입혀 기름에 튀긴다.\n\n9. 즉석밥을 전자레인지에 넣고 돌린다.\n\n10. 볶던 칠리소스에 게맛살튀김을 넣고 볶아 칠리크랩을 만든다.\n\n11. 즉석밥을 그릇에 따로 담아낸다.\n\n12. 칠리크랩을 그릇에 담는다.\n\n13. 잘게 썬 쪽파를 칠리크랩 위에 올려 마무리한다.'),
	(746, '청양냉면', NULL, 1, '1. 끓는 물에 소고기안심과 달걀을 넣고, 소금을 더해 삶는다.\n\n2. 청양고추에 십자(十)로 길게 칼집을 낸다.\n\n3. 칼집을 낸 청양고추 안에 인스턴트 떡갈비를 넣어 청양고추소박이를 만든다.\n\n4. 청양고추소박이에 밀가루와 달걀물을 묻혀 기름에 튀긴다.\n\n5. 적당한 크기로 썬 청양고추를 믹서기에 넣은 뒤, 뜨거운 물을 더한다.\n\n6. 5에 적당한 크기로 썬 부추를 넣고 간다.\n\n7. 간 청양고추와 부추를 체에 밭쳐 즙만 걸러낸다.\n\n8. 밀가루, 전분, 소금, 청양고추즙을 섞어 반죽한 뒤, 면포로 덮어 숙성시킨다.\n\n9. 오이, 양배추, 씻은 총각김치를 채 썬다.\n\n10. 열무김치를 적당한 크기로 썬다.\n\n11. 열무김치국물에 물, 얼음, 식초, 설탕, 총각김치국물을 넣어 냉면육수를 만든다.\n\n12. 사과와 오이를 강판에 간다.\n\n13. 간 사과와 오이를 면포에 넣고 즙을 낸 뒤, 냉면육수에 넣는다.\n\n14. 냉면육수에 간 청양고추와 부추를 넣는다.\n\n15. 숙성시킨 청양고추반죽을 밀대로 얇게 민다.\n\n16. 얇게 민 청양고추반죽을 가늘게 썰어 청양고추면을 만든다.\n\n17. 청양고추면을 끓는 물에 삶는다.\n\n18. 청양고추소박이튀김을 접시에 담아낸다.\n\n19. 삶은 청양고추면을 얼음물에 담가 차게 만든다.\n\n20. 삶은 소고기안심을 적당한 크기로 썬다.\n\n21. 그릇에 채 썬 채소와 김치를 넣은 뒤, 삶은 달걀과 소고기안심을 얹는다.\n\n22. 그릇에 청양고추면을 담은 뒤 냉면육수를 부어 마무리한다.'),
	(747, '대박나새우', NULL, 1, '1. 뚝배기에 물과 알배추를 넣고 끓인다.\n\n2. 적당한 크기로 썬 양송이버섯, 팽이버섯, 다진 소고기를 뚝배기에 넣는다.\n\n3. 2에 적당한 크기로 썬 듀록 삼겹살, 쯔유, 인스턴트 가츠오육수, 물을 넣고 끓인다.\n\n4. 새우살을 으깬다.\n\n5. 게맛살을 잘게 다진다.\n\n6. 다진 새우와 게맛살에 다진 생강, 청주, 전분, 녹인 버터를 넣고 섞어 딤섬소를 만든다.\n\n7. 달걀흰자에 빨간색 식용 색소를 넣고 핸드블렌더로 섞는다.\n\n8. 달걀흰자에 검정색 식용 색소를 넣고 핸드블렌더로 섞는다.\n\n9. 색을 입힌 달걀흰자로 지단을 구워 빨간색, 검정색 달걀피를 만든다.\n\n10. 달걀노른자로 지단을 구워 노란색 달걀피를 만든다.\n\n11. 색색깔의 달걀피에 다진 딤섬소을 넣고 빚어 찜기에 찐다.\n\n12. 어란을 잘게 썰어 딤섬 위에 올린다.\n\n13. 뚝배기에 끓이던 재료에 떡볶이 떡을 넣고 끓인다.\n\n14. 13에 잘게 썬 청양고추와 홍고추를 올린다.\n\n15. 칠리소스, 케첩, 레몬즙, 설탕을 섞어 소스를 만든 뒤 종지에 담아낸다.'),
	(748, '우리집에 와규~', NULL, 1, '1. 생강을 잘게 다진다.\n\n2. 다진 생강을 물에 담가둔다.\n\n3. 대파 흰 부분을 채 썬다.\n\n4. 채 썬 대파의 일부분을 얼음물에 담가둔다.\n\n5. 남은 채 썬 대파에 전분을 묻힌 뒤 기름에 튀긴다.\n\n6. 튀긴 대파를 체에 밭쳐 기름기를 뺀다.\n\n7. 와규등심에 후추과 소금으로 간한 뒤 석쇠에 올려 직화한다.\n\n8. 감귤의 껍질을 벗긴다.\n\n9. 간장, 식초, 물, 감귤즙, 감귤을 섞는다.\n\n10. 냄비에 청주와 맛술을 넣고 끓인다.\n\n11. 감귤간장소스에 끓인 청주와 맛술을 섞어 감귤폰즈소스를 만든다.\n\n12. 석쇠에 구운 와규등심을 얇게 포 뜬다.\n\n13. 포를 뜬 와규등심에 다진마늘을 바른다.\n\n14. 와규등심에 팽이버섯을 올리고 돌돌 만다.\n\n15. 와규등심에 채 썬 파프리카와 얼음물에 담가둔 파채를 각각 올리고 돌돌 만다.\n\n16. 팬에 올리브오일과 참기름을 넣고 끓인다.\n\n17. 와규채소말이를 접시에 담고 감귤폰즈소스를 뿌린다.\n\n18. 깨를 으깬 뒤, 와규채소말이 위에 뿌린다.\n\n19. 다진 생강과 잘게 썬 시금치를 와규채소말이 위에 뿌린다.\n\n20. 기름을 두른 팬에 시금치를 볶다가 소금, 후추로 간 한다.\n\n21. 끓인 올리브오일&참기름을 와규채소말이 위에 뿌리고, 볶은 시금치와 파채튀김을 곁들인다.\n\n22. 와규채소말이에 후추를 뿌려 마무리한다.'),
	(749, '최고양', NULL, 1, '1. 양갈비는 막을 벗겨내고 적당한 크기로 썬다.\n\n2. 절구에 불린 레몬그라스와 마늘, 올리브오일을 넣고 빻는다.\n\n3. 지퍼백에 손질한 양갈비와 빻은 재료, 커민, 파프리카가루, 고수가루를 넣은 뒤 주물러 재워둔다.\n\n4. 핸드블렌더에 딸기를 넣고 간다.\n\n5. 기름을 두른 팬에 간 딸기와 녹차 아이스크림을 넣고 끓인다.\n\n6. 핸드블렌더에 끓인 재료와 민트젤리, 레몬 껍질을 넣고 간 뒤, 팬에 한 번 더 끓여 녹차민트딸기소스를 만든다.\n\n7. 절구에 새우젓, 케이퍼, 잘게 썬 청양고추를 넣고 빻는다.\n\n8. 7에 올리브와 올리브오일을 넣고 빻아 올리브 타프나드를 만든다.\n\n9. 올리브 타프나드에 소금을 더한다.\n\n10. 양파는 채 썰고 대파는 잘게 썬다.\n\n11. 재워둔 양갈비에 손질한 채소를 넣고 주무른다.\n\n12. 기름을 두른 팬에 양갈비와 채소를 굽는다.\n\n13. 구운 양갈비를 먹기 좋게 손질한 뒤 토치로 직화한다.\n\n14. 접시에 구운 양갈비를 올린 뒤 녹차민트딸기소스를 곁들인다.\n\n15. 올리브 타프나드에 새우젓을 더해 빻는다.\n\n16. 녹차민트딸기소스 위에 올리브 타프나드를 올린 뒤 레몬즙, 소금, 올리브오일을 뿌려 완성한다.'),
	(750, '오케이 마초샌드', NULL, 1, '1. 끓는 기름에 적당한 크기로 썬 대파, 고춧가루, 페페론치노를 넣고 고추기름을 낸다.\n\n2. 고추기름에 마늘을 넣고 끓인다.\n\n3. 끓는 물에 라면을 삶는다.\n\n4. 듀록 삼겹살을 적당한 크기로 썬다.\n\n5. 듀록 삼겹살에 쯔유, 커민, 파프리카가루, 맛술, 후추를 넣고 버무린다.\n\n6. 고추기름을 체에 밭쳐 거른다.\n\n7. 삶은 라면을 팬에 납작하게 깔고 끓는 기름을 부어 튀긴다.\n\n8. 양념한 삼겹살에 전분을 묻힌다.\n\n9. 냄비에 삶은 라면을 납작하게 깔고 전분을 묻힌 삼겹살을 올린 뒤, 끓는 기름을 부어 튀긴다.\n\n10. 양파를 채 썬다.\n\n11. 고추기름을 두른 팬에 채 썬 양파를 볶다가 오렌지주스, 케이퍼, 버터, 레몬즙을 넣고 끓여 오렌지케이퍼소스를 만든다.\n\n12. 튀긴 초면을 건져내 기름기를 뺀다.\n\n13. 코코아가루에 뜨거운 물을 부어 녹인 뒤 설탕을 더해 끓인다.\n\n14. 13에 마요네즈를 섞어 마요네즈초콜릿소스를 만든다.\n\n15. 삼겹살을 올린 초면에 에멘탈치즈를 갈아 올린다.\n\n16. 15 위에 초면을 덮어 샌드한다.\n\n17. 오렌지케이퍼소스와 마요네즈초콜릿소스를 각각 담아낸다.'),
	(751, '타코 판타지아♬', NULL, 1, '1. 핸드블렌더에 할리페뇨라임원액, 고수, 커민, 설탕, 고춧가루, 다진 마늘, 간장,\n적당한 크기로 썬 대파를 넣고 간다.\n\n2. 간 재료에 라임즙을 넣는다.\n\n3. 양파를 잘게 썬다.\n\n4. 간 재료에 잘게 썬 양파를 넣고 한 번 더 갈아 할라페뇨고수소스를 만든다.\n\n5. 기름을 두른 그릴팬에 슬라이스한 양파와 청양고추를 굽는다.\n\n6. 굽던 양파와 청양고추, 할라페뇨고수소스에 각각 소금을 더한다.\n\n7. 사워크림에 레몬즙, 렐리시, 다진 케이퍼, 올리브오일, 잘게 썬 양파를 넣고 섞어 사워크림소스를 만든다.\n\n8. 토마토와 양파, 고수를 잘게 썰어 섞는다.\n\n9. 8에 잘게 썬 아보카도를 넣는다.\n\n10. 밀가루에 달걀, 물, 파프리카가루를 넣고 섞어 튀김반죽을 만든다.\n\n11. 틸라피아를 적당한 크기로 썬 뒤, 튀김반죽을 입힌다.\n\n12. 핸드블렌더에 블랙올리브를 넣고 간다.\n\n13. 간 블랙올리브와 구운 양파, 잘게 썬 샬롯, 레몬즙, 소금, 올리브오일, 후추를 섞어 양파절임을 만든다.\n\n14. 튀김반죽을 입힌 틸라피아를 기름에 튀긴다.\n\n15. 틸라피아튀김을 건져낸 뒤, 소금과 커민을 넣고 버무린다.\n\n16. 슬라이스한 토마토를 그릴팬에 굽는다.\n\n17. 구운 청양고추를 적당한 크기로 썬 뒤 토마토, 양파, 고수, 아보카도와 섞는다.\n\n18. 17에 올리브오일, 후추, 레몬즙을 더해 토마토살사를 만든다.\n\n19. 양상추를 잘게 썬다.\n\n20. 마른 팬에 토르티야를 굽는다.\n\n21. 접시에 구운 토마토와 양파절임을 담고, 루콜라를 올린다.\n\n22. 구운 토르티야 위에 토마토살사>틸라피아튀김>사워크림소스>할라페뇨고수소스를 올린다.\n\n23. 타코에 잘게 썬 고수, 양상추를 올린다.\n\n24. 적당한 크기로 썬 라임을 곁들여 마무리한다.'),
	(752, '살몬 판타지아♬', NULL, 1, '1. 연어의 살과 껍질을 분리한다.\n\n2. 연어 껍질에 소금, 후추로 간한 뒤 오븐에 넣고 굽는다.\n\n3. 냄비에 화이트와인, 오미자원액, 간 너트메그, 사프란을 넣는다.\n\n4. 3에 월계수잎, 샬롯, 소금, 오레가노홀, 후추를 넣고 끓인다.\n\n5. 끓이던 재료에 적당한 크기로 자른 연어를 넣고 약한 불에서 천천히 익힌다.\n\n6. 마른 팬에 냉동 콜리플라워, 냉동 아스파라거스, 냉동 그린빈,\n냉동 브로콜리와 적당한 크기로 썬 당근, 버터를 넣고 볶는다.\n\n7. 볶던 채소에 뜨거운 물을 더한 뒤, 잘게 썬 마늘과 후추를 넣는다.\n\n8. 익히던 연어에 너트매그홀을 넣는다.\n\n9. 슬라이스한 래디시와 애플민트를 얼음물에 담가둔다.\n\n10. 핸드블렌더에 아보카도, 케이퍼, 소금, 크림치즈를 넣고 갈아 아보카도무스를 만든다.\n\n11. 화이트와인에 수비드한 연어를 건져낸다.\n\n12. 오븐에 구운 연어 껍질을 꺼내 남은 살을 모두 발라낸 뒤, 한 번 더 굽는다.\n\n13. 접시에 엔다이브를 올린 뒤, 아보카도무스와 수비드 연어를 담고 소금을 뿌린다.\n\n14. 볶은 채소에 사워크림을 섞은 뒤 접시에 담는다.\n\n15. 화이트와인에 익힌 샬롯과 슬라이스한 래디시, 애플민트와 라임 껍질을 갈아 올린다.\n\n16. 오븐에 구운 연어 껍질을 곁들인다.'),
	(753, '캡틴 아메리카', NULL, 1, '1. 압력솥에 기름을 두른다.\n\n2. 양파를 채 썬다.\n\n3. 채 썬 양파를 압력솥에 넣는다.\n\n4. 지퍼백에 적당한 크기로 썬 당근, 닭고기를 넣고 고기망치로 두드려 으깬다.\n\n5. 압력솥에 으깬 닭고기와 당근, 셀러리, 우유, 맛술, 월계수잎, 오레가노홀, 파슬리를 넣는다.\n\n6. 5에 뜨거운 물을 넣은 뒤, 뚜껑을 덮어 끓여 닭육수를 낸다.\n\n7. 스파게티면을 지퍼백에 넣고 잘게 부순다.\n\n8. 끓는 물에 부순 스파게티면을 삶는다.\n\n9. 밀가루에 베이킹파우더와 소금을 섞는다.\n\n10. 달걀물에 식용유, 설탕, 버터를 넣고 섞는다.\n\n11. 베이킹파우더와 소금을 넣은 밀가루에 달걀물을 섞어 케이크반죽을 만든다.\n\n12. 케이크반죽에 라임 껍질을 갈아 넣은 뒤, 머그컵에 넣는다.\n\n13. 케이크반죽을 전자레인지에 넣고 돌린다.\n\n14. 믹서기에 설탕, 옥수수전분, 라임즙을 넣고 갈아 라임아이싱을 만든다.\n\n15. 청양고추를 석쇠에 올려 직화한다.\n\n16. 팬에 냉동 애플망고, 라임즙, 링곤베리잼, 잘게 썬 애플민트를 넣고 볶는다.\n\n17. 핸드블렌더에 볶은 재료를 넣고 갈아 애플망고소스를 만든다.\n\n18. 전자레인지에서 라임케이크를 꺼내 랩으로 싸둔다.\n\n19. 구운 청양고추를 잘게 썰어 종지에 담는다.\n\n20. 애플망고소스를 그릇에 담는다.\n\n21. 애플망고소스 위에 라임케이크를 올리고, 라임아이싱을 바른다.\n\n22. 스파게티면을 삶던 냄비에서 면수를 덜어낸다.\n\n23. 22에 닭육수를 넣고 끓여 치킨누들수프를 만든다.\n\n24. 그릇에 치킨누들수프를 담아낸다.'),
	(754, '부산행', NULL, 1, '1. 양파를 채 썬다.\n\n2. 맛술을 넣어 끓인 물에 손질한 조기를 넣고 데친다.\n\n3. 기름을 두른 팬에 채 썬 양파, 백합을 넣고 볶다가 뜨거운 물, 소금,\n불린 건고사리, 다시마를 넣고 끓여 백합육수를 낸다.\n\n4. 무를 얇게 슬라이스한 뒤, 채 썬다.\n\n5. 데친 조기의 살을 발라낸다.\n\n6. 백합육수를 체에 밭쳐 육수만 걸러낸 뒤, 채 썬 무, 조기살, 고추장,\n다진 마늘, 고춧가루, 불린 건표고버섯을 넣고 끓인다.\n\n7. 끓이던 재료에 아프리카고추양념장, 맛술, 멸치액젓을 넣는다.\n\n8. 백합의 살을 발라낸다.\n\n9. 밀가루에 달걀노른자, 잘게 썬 고수를 넣는다.\n\n10. 9에 잘게 썬 셀러리, 산초가루, 백합살, 아프리카고추양념장을 넣고 섞어 반죽을 만든다.\n\n11. 기름을 두른 팬에 반죽을 적당한 크기로 부친다.\n\n12. 아프리카고추양념장, 간장, 식초를 넣어 양념장을 만든다.\n\n13. 접시에 물엿을 바른다.\n\n14. 물엿을 바른 접시에 조기뼈를 올린 뒤, 고춧가루를 뿌린다.\n\n15. 믹서기에 건새우를 간다.\n\n16. 조기뼈 위에 체에 거른 건새우가루를 뿌린다.\n\n17. 건새우가루 위에 아사이베리가루를 뿌린다.\n\n18. 조기무조림을 그릇에 담고 건새우가루를 뿌린다.\n\n19. 즉석밥을 그릇에 담는다.\n\n20. 접시에 백합고수전을 담고, 레몬 슬라이스를 곁들인다.'),
	(755, '풍식 두비두밥♬', NULL, 1, '1. 핸드블렌더에 홍고추, 청양고추, 마늘, 대파를 넣고 간다.\n\n2. 기름을 끓이던 팬에 간 채소를 넣고 볶는다.\n\n3. 냄비에 간장, 해물육수팩을 넣는다.\n\n4. 끓이던 간장소스에 뜨거운 물, 설탕을 넣고 끓인다.\n\n5. 핸드블렌더에 돼지목살을 넣고 간다.\n\n6. 볶던 채소에 간 돼지목살, 고춧가루, 굴소스를 넣고 볶는다.\n\n7. 볶던 재료에 설탕과 간장소스를 넣는다.\n\n8. 두부를 적당한 크기로 썬 뒤, 기름에 튀긴다.\n\n9. 끓는 물에 해물육수팩, 건새우를 넣고 해물육수를 낸다.\n\n10. 오이를 얇게 슬라이스 한다.\n\n11. 볶던 재료에 두부튀김, 식초를 넣고 두부덮밥소스를 만든다.\n\n12. 해물육수를 체에 밭쳐 거른 뒤, 소금을 넣고 끓인다.\n\n13. 해물육수를 그릇에 담고, 후추, 슬라이스한 오이를 넣어 중국식 오이탕을 만든다.\n\n14. 접시에 즉석밥과 두부덮밥소스를 담아낸다.'),
	(756, '한현민 Go! 현민 Go!', NULL, 1, '1. 끓는 기름에 적당한 크기로 썬 대파를 넣는다.\n\n2. 1에 고춧가루를 넣고 끓여 고추기름을 낸다.\n\n3. 고사리무침을 지퍼백에 넣고 고기망치로 두드려 으깬다.\n\n4. 물을 끓이던 압력솥에 으깬 고사리무침을 넣고 끓인다.\n\n5. 고사리무침에 적당한 크기로 썬 돼지 뒷다리살, 다진 마늘, 조미료, 으깬 만두를 넣고 익힌다.\n\n6. 고추기름을 체에 밭쳐 거른다.\n\n7. 고등어의 가시를 발라낸다.\n\n8. 기름을 두른 팬에 고등어를 넣고, 소금으로 간 한 뒤 굽는다.\n\n9. 구운 고등어에 청주를 넣고 불을 붙여 비린내를 날린다.\n\n10. 대파, 애호박, 양파를 잘게 썬다.\n\n11. 기름을 두른 팬에 손질한 채소와 밥을 넣고 볶는다.\n\n12. 굽던 고등어를 으깬 뒤, 볶음밥에 넣고 함께 볶는다.\n\n13. 고등어볶음밥에 소금과 잘게 썬 청양고추를 넣고 볶는다.\n\n14. 압력솥에 익힌 재료를 팬에 옮겨 담고 뜨거운 물, 간장, 고춧가루, 고추기름을 더해 끓여 고사리육개장을 만든다.\n\n15. 핸드블렌더에 고사리육개장을 넣고 간 뒤, 팬에 넣고 한 번 더 끓인다.\n\n16. 고사리육개장에 조미료를 더한다.\n\n17. 고등어볶음밥에 굴소스, 달걀을 넣고 볶는다.\n\n18. 고등어볶음밥에 고춧가루, 고추기름을 더한다.\n\n19. 고사리육개장에 전분물을 넣어 농도를 조절한 뒤, 뚝배기에 옮겨 담는다.\n\n20. 고사리육개장에 잘게 썬 청양고추, 고춧가루를 올린다.\n\n21. 접시에 고등어볶음밥을 담아낸다.'),
	(757, '한국인의 밥상', NULL, 1, '1. 냄비에 김치, 설탕, 식초, 물을 넣고 끓인다.\n\n2. 끓이던 김치에 양파즙을 더해 묵은지를 만든다.\n\n3. 즉석밥을 전자레인지에 넣고 돌린다.\n\n4. 냄비에 물, 해물육수팩, 간장, 설탕을 넣고 끓인다.\n\n5. 간장소스에 적당한 크기로 썬 청양고추를 넣는다.\n\n6. 기름을 두른 팬에 적당한 크기로 썬 대파, 으깬 마늘, 고추장아찌, 조미료, 고춧가루를 넣고 볶는다.\n\n7. 돼지목살, 닭가슴살, 훈제오리, 인스턴트 동그랑땡을 적당한 크기로 썬다.\n\n8. 7을 볶던 채소에 넣고 함께 볶는다.\n\n9. 볶던 재료에 뜨거운 물을 넣고 끓인다.\n\n10. 9에 묵은지를 넣고 끓여 묵은지얼큰탕을 만든다.\n\n11. 낙지젓을 물에 씻는다.\n\n12. 핸드블렌더에 씻은 낙지젓, 잘게 썬 어묵을 넣고 간다.\n\n13. 라이스페이퍼를 뜨거운 물에 불린 뒤, 적당한 크기로 썬 어묵과 간 낙지&어묵을 넣고 감싼다.\n\n14. 13을 라이스페이퍼로 한 번 더 감싼 뒤, 튀김가루를 묻혀 기름에 튀긴다.\n\n15. 묵은지얼큰탕에 다진 마늘, 고춧가루, 잘게 썬 홍고추, 청양고추를 넣는다.\n\n16. 묵은지얼큰탕을 뚝배기에 옮겨 담고, 고춧가루를 더한다.\n\n17. 핸드블렌더에 홍고추와 청양고추를 넣고 간 뒤, 종지에 담는다.\n\n18. 간장소스를 체에 밭쳐 건더기를 걸러낸다.\n\n19. 종지에 간장소스를 더해 양념장을 만든다.\n\n20. 즉석밥을 그릇에 담는다.\n\n21. 낙지어묵튀김을 건져 기름을 뺀다.\n\n22. 간장소스를 그릇에 담고, 잘게 썬 홍고추와 청양고추를 넣는다.\n\n23. 22 위에 낙지어묵튀김을 담아 간장낙지장을 만든다.'),
	(758, '아재볼', NULL, 1, '1. 기름을 두른 팬에 잘게 썬 느타리버섯, 양파, 양송이수프 분말을 넣고 볶는다.\n\n2. 볼에 잘게 썬 양파, 다진 돼지고기, 다진 소고기, 달걀, 잘게 썬 청양고추를 넣는다.\n\n3. 2에 물을 더한 양송이수프 분말, 후추, 소금을 넣고 치댄다.\n\n4. 고기반죽을 동그랗게 뭉쳐 미트볼을 만든다.\n\n5. 미트볼에 튀김가루를 묻힌 뒤, 기름에 튀긴다.\n\n6. 양파즙, 물, 소금을 섞는다.\n\n7. 애호박을 길게 슬라이스한 뒤, 양파즙>튀김가루>양파즙을 순서대로 묻힌다.\n\n8. 애호박을 기름에 튀긴다.\n\n9. 핸드블렌더에 토마토소스, 소금, 후추, 고춧가루를 넣고 간다.\n\n10. 볶던 채소에 토마토소스와 고추장을 넣고 끓여 매운 토마토소스를 만든다.\n\n11. 매운 토마토소스를 그릇에 담고, 튀긴 미트볼을 올린다.\n\n12. 고추장아찌를 잘게 썬 뒤, 미트볼 위에 뿌린다.\n\n13. 접시에 애호박칩을 담고, 고춧가루를 뿌린다.'),
	(759, '원 펀치 쓰리 코스', NULL, 1, '1. 슬라이스한 가지에 밀가루를 묻혀 기름에 튀기듯 굽는다.\n\n2. 적당한 크기로 썬 당근, 양파에 뜨거운 물을 넣고 끓여 채소육수를 낸다.\n\n3. 구운 가지에 토마토소스를 바른 뒤, 소금, 오레가노를 뿌린다.\n\n4. 가지 위에 잘게 썬 생모차렐라치즈를 올린 뒤, 돌돌 만다.\n\n5. 가지치즈말이 위에 토마토소스를 올리고 파르메산치즈를 갈아 뿌린 뒤, 오븐에 넣고 굽는다.\n\n6. 기름을 두른 팬에 잘게 썬 방울토마토를 넣고 불을 붙여 익힌다.\n\n7. 6에 잘게 썬 마늘과 통마늘, 채소육수, 토마토소스를 넣고 끓여 소스를 만든다.\n\n8. 기름을 두른 팬에 잘게 썬 양파, 소금, 식초, 설탕을 넣고 볶는다.\n\n9. 대파와 가지를 채 썬 뒤, 전분을 묻혀 기름에 튀긴다.\n\n10. 파채&가지채튀김을 체에 밭쳐 기름기를 뺀다.\n\n11. 마른 팬에 즉석밥과 채소육수를 넣는다.\n\n12. 기름을 두른 팬에 적당한 크기로 썬 칵테일새우를 넣고 볶는다.\n\n13. 칵테일새우를 볶던 팬에 채소육수와 섞은 즉석밥, 바질페스토, 파르메산치즈를 넣고 끓여 새우바질리소토를 만든다.\n\n14. 소고기채끝을 직사각형 모양으로 자른 뒤, 소금과 후추로 간 한다.\n\n15. 기름을 두른 팬에 소고기채끝을 구워 스테이크를 만든다.\n\n16. 핸드블렌더에 껍질을 벗긴 아보카도를 넣고 간다.\n\n17. 오븐에 구운 가지치즈말이를 접시에 담고, 파채&가지채튀김을 올린다.\n\n18. 새우바질리소토를 그릇에 담고, 파채&가지채튀김을 올린다.\n\n19. 그릇에 채끝스테이크, 볶은 양파, 간 아보카도, 채 썬 블랙트러플을 올린 뒤, 트러플오일을 뿌린다.'),
	(760, '아임 파인다이닝, 앤드 유?', NULL, 1, '1. 달걀노른자에 레몬즙과 소금을 넣는다.\n\n2. 1에 버터를 넣은 뒤, 끓는 물이 담긴 냄비 위에서 중탕해 홀랜다이즈소스를 만든다.\n\n3. 홀랜다이즈소스에 바질페스토와 피스타치오페스토를 넣고 섞어 피스타치오퓨레를 만든다.\n\n4. 코코넛워터를 끓이다가 강판에 간 당근과 소금을 넣고 익힌다.\n\n5. 핸드블랜더에 코코넛워터에 익힌 당근과 버터, 코코넛워터를 넣고 간 뒤, 즉석밥을 넣고 다시 갈아 당근 퓨레를 만든다.\n\n6. 기름을 두른 팬에 채 썬 양파와 잘게 썬 소고기등심을 넣고 볶는다.\n\n7. 볶던 재료에 레드와인, 맛술, 연겨자, 소금을 넣고 볶아 레드와인소스를 만든 뒤, 체에 밭쳐 건더기를 걸러낸다.\n\n8. 레드와인소스에 버터를 녹여가며 섞은 뒤, 블루베리잼을 더한다.\n\n9. 소고기등심에 소금, 후추로 간 한 뒤, 기름을 두른 팬에 굽는다.\n\n10. 잘게 썬 아보카도를 체에 밭쳐 으깬다.\n\n11. 잘게 썬 양파, 으깬 아보카도, 레몬즙, 잘게 썬 방울토마토, 소금, 스리라차소스를 섞어 과카몰레를 만든다.\n\n12. 과카몰레에 커민과 코리앤더를 넣는다.\n\n13. 통밀식빵을 직사각형 모양으로 자른 뒤, 마른 팬에 굽는다.\n\n14. 접시에 피스타치오퓨레를 담는다.\n\n15. 구운 식빵 사이에 과카몰레를 넣고 샌드한 뒤, 접시에 올린다.\n\n16. 접시에 구운 양파와 등심스테이크를 올린다.\n\n17. 등심스테이크 위에 블랙트러플을 갈아 올린다.\n\n18. 셀러리를 슬라이스해서 얼음물에 담갔다 뺀 뒤, 등심스테이크에 곁들인다.\n\n19. 당근퓨레를 체에 밭쳐 내린다.\n\n20. 등심스테이크 위에 레드와인 소스를 뿌린다.\n\n21. 접시에 당근퓨레를 담아 마무리한다.'),
	(761, '으라차차찜', NULL, 1, '1. 끓는 기름에 대파와 고춧가루를 넣고 끓여 고추기름을 낸다.\n\n2. 기름을 두른 팬에 적당한 크기로 썬 대파와 으깬 마늘을 넣고 볶는다.\n\n3. 소고기등심에 촘촘하게 칼집을 낸 뒤, 적당한 크기로 썬다.\n\n4. 볶던 채소에 칼집을 낸 소고기등심을 넣고 함께 볶는다.\n\n5. 볶던 재료에 적당한 크기로 썬 양파, 간장, 스리라차소스, 굴소스, 설탕, 화이트와인을 넣는다.\n\n6. 물이 자작하게 담긴 압력솥에 방울토마토를 넣고 끓이다가 볶은 소고기&채소를 넣는다.\n\n7. 6에 잘게 썬 청양고추를 넣고 압력솥 뚜껑을 닫아 찐다.\n\n8. 가지를 적당한 크기로 썬다.\n\n9. 밀가루, 전분, 물, 달걀물, 식용유을 섞어 튀김옷을 만든다.\n\n10. 가지에 튀김옷을 묻힌 뒤, 기름에 튀긴다.\n\n11. 고추기름을 체에 밭쳐 걸러낸다.\n\n12. 스리라차소스와 굴소스, 설탕, 레몬즙, 고추기름을 섞어 튀김소스를 만든다.\n\n13. 튀김소스를 종지에 담아낸다.\n\n14. 가지튀김을 체에 밭쳐 기름기를 뺀다.\n\n15. 채 썬 대파에 밀가루를 묻혀 기름에 튀긴다.\n\n16. 그릇에 가지튀김>스리라차 소고기찜>파채튀김 순서대로 담는다.\n\n17. 16에 소고기찜 국물을 듬뿍 담아 마무리한다.'),
	(762, '맛있스리라~', NULL, 1, '1. 소고기채끝에 깊은 칼집을 넣어 고기를 넓게 편 뒤, 고기망치로 두드린다.\n\n2. 소고기채끝에 갈릭솔트와 머스터드파우더를 뿌리고, 전분을 묻힌다.\n\n3. 소고기채끝 위에 칵테일새우를 올린 뒤, 돌돌 만다.\n\n4. 4에 밀가루를 묻힌다.\n\n5. 달걀물에 우유와 스리라차소스를 넣고 섞는다.\n\n6. 새우를 넣은 소고기에 스리라차달걀물>밀가루를 순서대로 입힌 뒤, 기름에 튀기듯이 구워 프라이드 스테이크를 만든다.\n\n7. 기름을 두른 팬에 채 썬 양파와 살라미를 넣고 볶는다.\n\n8. 7에 버터와 밀가루를 넣고 볶다가 스리라차소스를 넣어 스리라차루를 만든다.\n\n9. 스리라차루에 뜨거운 물을 넣고 조린다.\n\n10. 스리라차루에 우유를 넣는다.\n\n11. 10에 양송이버섯, 어니언파우더, 후추, 방울토마토를 넣고 볶아 스리라차 크림소스를 만든다.\n\n12. 스리라차 크림소스에 소금을 더한다.\n\n13. 오렌지에 칼집을 낸 뒤, 즙을 짜고 과육을 파낸다.\n\n14. 칵테일 잔 입에 설탕을 묻힌 뒤, 얼음을 담는다.\n\n15. 칵테일 셰이커에 얼음, 오렌지즙&과육, 유자음료, 스리라차소스, 설탕, 진을 넣고 흔든다.\n\n16. 오렌지 스리라차 칵테일을 설탕이 묻은 잔에 담은 뒤, 레몬 슬라이스를 곁들인다.\n\n17. 프라이드 스테이크를 굽던 팬에 버터와 스리라차소스를 넣고 조린다.\n\n18. 접시에 스리라차크림소스를 담은 뒤, 프라이드 스테이크를 올린다.\n\n19. 프라이드 스테이크 위에 잘게 썬 대파를 뿌려 마무리한다.'),
	(763, '요~으리의 고수', NULL, 1, '1. 곤약면을 끓는 물에 삶은 뒤, 얼음물에 담가둔다.\n\n2. 파스타면을 끓는 물에 삶는다.\n\n3. 기름을 두른 팬에 잘게 썬 대파, 홍고추, 태국고추, 홍새우껍질을 넣고 볶은 뒤 홍새우껍질만 건져낸다.\n\n4. 볶던 채소에 손질한 새우와 홍새우, 다진 돼지고기를 넣은 뒤, 소금, 고춧가루, 후추를 넣고 볶는다.\n\n5. 굴소스, 피시소스, 설탕, 물을 섞어 소스를 만든다.\n\n6. 차돌박이에 소스를 바른 뒤, 석쇠에 직화한다.\n\n7. 석쇠에 굽던 차돌박이에 후추, 소금으로 간 한다.\n\n8. 파스타면을 삶는 면수에 새우를 데친다.\n\n9. 데친 새우를 잘게 썰어 오이냉국에 담가둔다.\n\n10. 간장, 식초, 고춧가루, 설탕을 섞는다.\n\n11. 10에 참기름을 더해 간장소스를 만든다.\n\n12. 채 썬 양파, 단무지, 오이, 곤약면을 간장소스에 넣어 버무린 뒤 인스턴트 냉면육수, 잘게 썬 고수를 넣고 섞는다.\n\n13. 볶던 재료에 숙주, 굴소스, 채 썬 양파, 피시소스, 팟타이소스, 깨를 넣는다.\n\n14. 볶던 재료에 삶은 파스타면, 고춧가루를 넣고 볶아 팟타이를 만든다.\n\n15. 곤약면 무침에 깨를 뿌린 뒤, 데친 새우를 넣고 버무린다.\n\n16. 그릇에 곤약면 무침을 담은 뒤, 그릇을 얼음물 위에 올려 차게 만든다.\n\n17. 팟타이를 그릇에 담은 뒤, 구운 차돌박이를 올린다.\n\n18. 팟타이에 고수를 얹는다.\n\n19. 팟타이에 레몬슬라이스를 곁들인다.\n\n20. 곤약면 무침에 데친 새우와 오이냉국을 부어 마무리한다.'),
	(764, '으리으리한 항아~으리!', NULL, 1, '1. 소갈비에 촘촘하게 칼집을 낸 뒤, 고기망치로 두드려 부드럽게 만든다.\n\n2. 손질한 소갈비에 전분을 묻혀 기름에 튀긴다.\n\n3. 튀긴 갈비를 끓는 물에 넣은 뒤, 설탕, 전복을 넣는다.\n\n4. 3에 다진 마늘, 대추청을 넣은 뒤, 양지육수를 넣고 끓여 갈비찜을 만든다.\n\n5. 갈비찜에 누룽지, 쌍화탕, 소고기장조림을 넣는다.\n\n6. 갈비찜에 청양고춧가루, 적당한 크기로 썬 알배추를 넣은 뒤, 뚜껑을 닫고 끓인다.\n\n7. 소면을 끓는 물에 삶는다.\n\n8. 기름을 두른 팬에 저염명란젓, 청양고추를 굽는다.\n\n9. 뱅어포를 기름에 튀긴다.\n\n10. 밀가루, 소금, 물을 섞어 반죽을 만든다.\n\n11. 반죽을 밀대로 넓게 편 뒤, 항아리 입구 크기에 맞게 자른다.\n\n12. 항아리에 갈비찜을 담은 뒤, 반죽으로 항아리 입구를 덮는다.반죽에 달걀물을 바른 뒤,\n항아리갈비찜을 오븐에 넣어 익힌다.\n\n13. 삶은 소면을 찬물에 헹군 뒤, 그릇에 담는다.\n\n14. 소면 위에 매운 냉면소스, 인스턴트 냉면육수, 잘게 썬 쪽파, 구운 저염명란젓,\n달걀노른자장, 뱅어포튀김을 올려 명란비빔국수를 만든다.\n\n15. 명란비빔국수에 잘게 썬 깻잎, 청양고추, 얼음을 올린다.\n\n16. 항아리갈비찜을 오븐에서 꺼낸 뒤, 밀가루 반죽을 토치로 직화한다.'),
	(765, '사나이 가는 길에 기죽지 마라♬', NULL, 1, '1. 기름을 넉넉하게 두른 팬에 버터를 넣는다.\n\n2. 버터를 녹인 기름에 으깬 마늘과 청양고추를 넣고 볶는다.\n\n3. 대파 흰 부분을 칼등으로 으깬 뒤 적당한 크기로 썬다.\n\n4. 볶던 채소에 대파, 태국고추, 청양고춧가루, 다진 생강, 중국고춧가루를 넣는다.\n\n5. 볶던 재료에 식초, 쯔유, 간장으로 간 한다.\n\n6. 볶던 재료에 된장을 넣는다.\n\n7. 볶던 재료에 멸치와 뜨거운 물을 넣고 끓여 마라탕을 만든다.\n\n8. 마라탕에 태국고추를 더해 끓인다.\n\n9. 적당한 크기로 썬 삼겹살을 체에 밭친 뒤, 마라탕에 넣어 데친다.\n\n10. 마라탕에 적당한 크기로 썬 토마토와 쌍화탕을 넣는다.\n\n11. 마라탕에 월계수잎, 굴소스를 넣는다.\n\n12. 마라탕에 적당한 크기로 썬 알배추, 양송이버섯을 넣는다.\n\n13. 마라탕에 통후추를 넣는다.\n\n14. 기름을 두른 팬에 잘게 썬 태국고추와 으깬 마늘, 굴소스, 중국고춧가루를 넣고 볶아 고추기름을 낸다.\n\n15. 즉석밥을 전자레인지에 넣고 돌린다.\n\n16. 감자를 채 썬 뒤, 물에 담가 전분기를 뺀다.\n\n17. 채 썬 감자를 고추기름에 넣고 볶는다.\n\n18. 감자채볶음에 식초, 쯔유를 넣는다.\n\n19. 마라탕에 불린 쌀국수면, 청경채, 고수를 넣고 끓인다.\n\n20. 마라탕을 그릇에 담는다.\n\n21. 즉석밥을 그릇에 따로 담는다.\n\n22. 감자채볶음을 그릇에 담아 마무리 한다.'),
	(766, '불짬뽕 드새우', NULL, 1, '1. 전복을 끓는 물에 삶는다.\n\n2. 믹서기에 홍고추, 청양고추, 태국고추, 청양고춧가루, 뜨거운 물을 넣고 간다.\n\n3. 기름을 두른 팬에 차돌박이, 채 썬 양파와 대파를 넣고 볶다가 고추기름을 넣는다.\n\n4. 3에 간 고추, 적당한 크기로 썬 알배추와 청경채, 홍새우, 숙주, 조갯살을 넣고 볶다가 간장, 굴소스로 간 한다.\n\n5. 칼국수면을 끓는 물에 삶는다.\n\n6. 손질한 새우를 끓는 물에 삶는다.\n\n7. 삶은 전복은 껍데기를 떼어내고 살만 적당한 크기로 썬다.\n\n8. 마른 팬에 삶은 전복살, 삶은 새우, 채 썬 양파를 넣어둔다.\n\n9. 볶던 재료에 삶은 전복살을 넣고 함께 볶다가 양지육수, 다진 마늘,\n굴소스, 후추, 소금, 맛술을 넣고 끓여 차돌박이짬뽕을 만든다.\n\n10. 차돌박이짬뽕에 목이버섯을 넣는다.\n\n11. 전복과 새우, 양파를 넣어둔 팬에 고추기름을 두르고 볶은 뒤, 잘게 썬 대파와 뜨거운 물,\n케첩, 매운 냉면소스, 설탕, 청양고춧가루를 넣고 조려 칠리새우를 만든다.\n\n12. 칠리새우에 잘게 썬 청양고추와 쪽파를 넣는다.\n\n13. 그릇에 양상추를 올린 뒤, 칠리새우를 담는다.\n\n14. 삶은 칼국수면을 찬물에 헹궈 그릇에 담은 뒤, 차돌박이짬뽕을 부어 낸다.'),
	(767, '원조 테리우스테이크', NULL, 1, '1. 양파를 채 썬다.\n\n2. 표고버섯, 베이컨을 적당한 크기로 썬다.\n\n3. 기름을 두른 팬에 양파, 표고버섯을 볶는다.\n\n4. 청양고추를 석쇠에 올려 직화한다.\n\n5. 적당한 크기로 썬 소고기등심, 베이컨을 핸드블렌더에 넣고 간다.\n\n6. 적당한 크기로 썬 빵을 우유에 적신 뒤, 간 고기와 섞는다.\n\n7. 6에 홀그레인머스터드, 우스터소스, 카옌페퍼, 달걀노른자, 밀가루를 넣고 섞은 뒤 동그랗게 뭉쳐 패티를 만든다.\n\n8. 기름을 두른 팬에 패티를 굽는다.\n\n9. 액상치킨스톡을 뜨거운 물에 풀어 치킨육수를 만든다.\n\n10. 볶던 양파, 표고버섯에 토마토소스, 버터, 치킨육수를 넣고 끓여 그레이비소스를 만든다.\n\n11. 굽던 패티에 그레이비소스를 부어 조린다.\n\n12. 석쇠에 구운 청양고추를 잘게 썬 뒤, 마스카포네치즈, 후추와 함께 섞어 치즈소스를 만든다.\n\n13. 나라즈케에 치즈소스를 올린다.\n\n14. 접시에 치즈소스를 올린 나라즈케를 담은 뒤, 올리브오일, 후추를 뿌린다.\n\n15. 이탈리안파슬리를 잘게 다진다.\n\n16. 접시에 찹스테이크를 담은 뒤, 이탈리안파슬리, 선드라이드토마토를 올려 완성한다.'),
	(768, '다이어트는 잊그라', NULL, 1, '1. 적당한 크기로 썬 소고기등심을 기름을 두른 팬에 넣고, 버터를 더해 볶는다.\n\n2. 베이컨, 양배추를 적당한 크기로 썬다.\n\n3. 베이컨을 볶는다.\n\n4. 식초를 더한 끓는 물에 양배추를 넣고 데친다.\n\n5. 볶던 소고기에 밀가루, 소금, 후추를 넣고 볶다가 페타치즈, 크림치즈를 넣는다.\n\n6. 볶던 재료에 우유, 브로콜리, 후추를 넣고 끓인다.\n\n7. 6를 그라탱 그릇에 담고 선드라이드토마토, 모차렐라치즈를 올린 뒤, 오븐에 넣고 굽는다.\n\n8. 볶은 베이컨을 건져 기름을 뺀다.\n\n9. 데치던 양배추에 당근을 넣고 함께 데친 뒤, 건져내 얼음물에 담가둔다.\n\n10. 믹서기에 홍고추, 마늘, 물을 넣고 간다.\n\n11. 10에 고춧가루, 새우젓, 다진 생강즙, 소금, 다진 마늘을 넣어 김치양념을 만든다.\n\n12. 김치양념에 데친 양배추와 당근을 넣고 버무린 뒤, 그릇에 담는다.\n\n13. 양배추김치에 잘게 썬 청양고추를 올린다.\n\n14. 오븐에서 꺼낸 그라탱 위에 잘게 썬 청양고추와 구운 베이컨, 파르메지아노치즈를 갈아 올려 마무리한다.'),
	(769, '복덩이', NULL, 1, '1. 전복을 끓는 물에 삶는다.\n\n2. 오징어 몸통에 격자로 촘촘하게 칼집을 낸다.\n\n3. 칼집을 낸 오징어와 새우를 끓는 물에 삶는다.\n\n4. 병어의 양쪽에 깊게 칼집을 낸다.\n\n5. 칼집을 낸 병어에 뜨거운 물을 부은 뒤, 맛술을 뿌린다.\n\n6. 손질한 병어에 소금을 뿌린다.\n\n7. 잘게 썬 대파를 병어 위에 올린 뒤, 찜기에 넣고 찐다.\n\n8. 가지를 적당한 크기로 썬 뒤, 기름에 튀긴다.\n\n9. 삶은 전복의 껍데기를 떼어내고 전복살에 칼집을 낸다.\n\n10. 칼집을 낸 전복에 달걀간장, 물, 맛술을 넣고 조린다.\n\n11. 잘게 썬 양파, 파프리카, 대파, 으깬 마늘과 청양고추를 기름을 두른 팬에 넣은 뒤, 두반장을 넣고 볶는다.\n\n12. 소고기등심을 핸드블렌더에 넣고 간다.\n\n13. 볶던 채소에 간 소고기, 고춧가루를 넣고 함께 볶다가 뜨거운 물을 넣고 끓여 소스를 만든다.\n\n14. 기름을 두른 팬에 달걀, 즉석밥을 넣고 볶는다.\n\n15. 삶은 전복을 잘게 썬다.\n\n16. 삶은 오징어와 새우를 잘게 썬다.\n\n17. 잘게 썬 해산물을 달걀볶음밥에 넣고 볶는다.\n\n18. 간장소스에 조린 전복을 적당한 크기로 썬다.\n\n19. 적당한 크기로 썬 공심채를 팬에 볶는다.\n\n20. 잘게 썬 대파와 공심채를 전복볶음밥에 넣고 후추로 간 한다.\n\n21. 전복볶음밥을 그릇에 담는다.\n\n22. 끓이던 소스에 튀긴 가지를 넣은 뒤, 전분물로 농도를 조절한다.\n\n23. 소스를 접시에 담는다.\n\n24. 전복볶음밥 위에 볶은 공심채와 조린 전복을 올린다.\n\n25. 소스 위에 병어찜을 담아낸다.'),
	(770, '한방탕', NULL, 1, '1. 소금을 더한 끓는 물에 적당한 크기로 썬 미나리를 데친 뒤, 얼음물에 담가둔다.\n\n2. 끓는 물에 채 썬 양파, 으깬 마늘, 적당한 크기로 썬 당근, 레몬 슬라이스, 화이트와인을 넣는다.\n\n3. 바닷가재에 꼬치를 끼워 모양을 고정한 뒤, 채소를 끓이던 물에 넣고 데친다.\n\n4. 데친 전복의 껍데기를 떼어내고 살에 칼집을 낸다.\n\n5. 관자를 얇게 썬 뒤, 칼집을 낸다.\n\n6. 칼집을 낸 관자, 전복을 끓는 물에 데친 뒤, 얼음물에 담가둔다.\n\n7. 데친 바닷가재를 얼음물에 담가둔다.\n\n8. 마른 팬에 잣과 피스타치오를 볶는다.\n\n9. 참외의 속을 파낸 뒤, 적당한 크기로 썬다.\n\n10. 믹서기에 볶은 잣과 피스타치오, 참외, 우유, 유자청, 쯔유를 넣고 갈아 잣소스를 만든다.\n\n11. 데친 미나리의 물기를 짜낸 뒤, 참기름과 깨를 넣고 버무려 그릇에 담는다.\n\n12. 데친 바닷가재살을 먹기 좋은 크기로 썬 뒤, 그릇에 담는다.\n\n13. 데친 전복, 관자에 쯔유, 유자청을 넣고 버무린 뒤, 그릇에 담는다.\n\n14. 식초를 더한 끓는 물에 달걀을 넣어 수란을 만든다.\n\n15. 소금을 더한 끓는 물에 라이스페이퍼를 데친 뒤, 얼음물에 담가둔다.\n\n16. 라이스페이퍼에 참기름을 버무린 뒤, 그릇에 담는다.\n\n17. 수란을 그릇에 담고 후추를 뿌린다.\n\n18. 잣소스를 그릇에 따로 담아낸다.'),
	(771, '낫토 먹고 crab! crab! crab! crab!', NULL, 1, '1. 양파를 채 썬다.\n\n2. 마늘은 얇게 편 썰고 청양고추, 홍고추는 잘게 썬다.\n\n3. 볼에 간장, 식초, 설탕, 청주, 잘게 썬 채소를 넣어 간장소스를 만든다.\n\n4. 대파를 채 썬다.\n\n5. 마른 팬에 베이컨을 굽는다.\n\n6. 살치살을 비닐로 덮고 고기망치로 두드려 얇게 편 뒤, 채 썬다.\n\n7. 간장, 다진 마늘, 참기름을 섞어 불고기 양념을 만든 뒤 살치살과 파채에 넣고 버무린다.\n\n8. 뚝배기에 불린 건표고버섯, 양념한 살치살과 파채를 넣고 끓여 파불고기를 만든다.\n\n9. 믹서기에 메밀면을 부숴 넣고 간다.\n\n10. 간 메밀면에 물을 넣어 반죽을 만든 뒤, 기름을 두른 팬에 넣고 부친다.\n\n11. 낫토를 저어준다.\n\n12. 잘게 썬 구운 베이컨과 김치를 낫토에 넣은 뒤 간장소스, 연겨자를 넣고 섞는다.\n\n13. 냉동 간장게장을 밀대로 밀어 살만 발라낸다.\n\n14. 즉석밥을 전자레인지에 넣고 돌린다.\n\n15. 메밀전 위에 낫토김치베이컨을 넣은 뒤, 사각형 모양으로 접는다.\n\n16. 접시 위에 메밀전병을 올린 뒤, 간장게장살과 달걀노른자를 올린다.\n\n17. 16에 간장소스를 뿌린다.\n\n18. 잘게 썬 대파를 메밀전병 위에 올린다.\n\n19. 즉석밥을 따로 담아낸다.'),
	(772, 'I\'m so sorry 보말 love you♬', NULL, 1, '1. 압력솥에 적당한 크기로 썬 당근을 깐 뒤 통삼겹살을 넣는다.\n\n2. 1에 맥주, 커피가루를 넣은 뒤 뚜껑을 닫고 익힌다.\n\n3. 대파를 채 썬다.\n\n4. 파채를 맛술을 더한 얼음물에 담가둔다.\n\n5. 끓는 물에 멸치육수팩을 넣어 멸치육수를 낸다.\n\n6. 전복을 손질해 내장을 분리한다.\n\n7. 팬에 들기름, 참기름, 밀가루를 넣어 볶은 뒤 전복내장, 보말을 넣어 함께 볶는다.\n\n8. 7에 맛술을 넣는다.\n\n9. 볶은 전복내장과 보말을 핸드블렌더에 넣고 간다.\n\n10. 간 전복내장과 보말을 팬에 넣는다.\n\n11. 볼에 멜젓소스, 고춧가루를 넣는다.\n\n12. 11에 식초, 다진 마늘, 맛술, 파채를 넣고 섞어 멜젓파무침을 만든다.\n\n13. 간 전복내장과 보말을 볶던 팬에 불린 건표고버섯과 물, 멸치육수를 넣는다.\n\n14. 13에 간장, 액상조미료를 넣고 끓여 국물을 만든다.\n\n15. 국물에 소금 간을 한 뒤, 삶은 소면을 넣고 끓인다.\n\n16. 멸치육수팩을 건져낸 뒤, 전분물을 넣어 농도를 조절한다.\n\n17. 보말국수를 그릇에 담는다.\n\n18. 압력솥에 익힌 돔베고기를 꺼내 적당한 크기로 썬다.\n\n19. 돔베고기를 끓이던 보말국수에 넣고 한 번 더 익힌다.\n\n20. 보말국수 위에 다진 청양고추, 돔베고기를 올린다.\n\n21. 돔베고기 위에 멜젓파무침을 올려 완성한다.'),
